INSERT INTO public.attack_groups VALUES (1, '2021-07-16 00:44:47.388342+00', '2021-07-16 00:44:47.388342+00', NULL, 'intrusion-set--c4d50cdf-87ce-407d-86d8-862883485842', '[{"url": "https://attack.mitre.org/groups/G0099", "external_id": "G0099", "source_name": "mitre-attack"}, {"description": "(Citation: QiAnXin APT-C-36 Feb2019)", "source_name": "Blind Eagle"}, {"url": "https://web.archive.org/web/20190625182633if_/https://ti.360.net/blog/articles/apt-c-36-continuous-attacks-targeting-colombian-government-institutions-and-corporations-en/", "description": "QiAnXin Threat Intelligence Center. (2019, February 18). APT-C-36: Continuous Attacks Targeting Colombian Government Institutions and Corporations. Retrieved May 5, 2020.", "source_name": "QiAnXin APT-C-36 Feb2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1685}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jose Luis Sánchez Martinez\"\n    ]", "Str": "", "Type": 5, "Index": 1617}}', 'G0099', '[APT-C-36](https://attack.mitre.org/groups/G0099) is a suspected South America espionage group that has been active since at least 2018. The group mainly targets Colombian government institutions as well as important corporations in the financial sector, petroleum industry, and professional manufacturing.(Citation: QiAnXin APT-C-36 Feb2019)', 'APT-C-36');
