INSERT INTO public.attack_groups VALUES (4, '2021-07-16 00:44:47.388342+00', '2021-07-16 00:44:47.388342+00', NULL, 'intrusion-set--d6e88e18-81e8-4709-82d8-973095da1e70', '[{"url": "https://attack.mitre.org/groups/G0023", "external_id": "G0023", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye EPS Awakens Part 2)", "source_name": "APT16"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/12/the-eps-awakens-part-two.html", "description": "Winters, R.. (2015, December 20). The EPS Awakens - Part 2. Retrieved January 22, 2016.", "source_name": "FireEye EPS Awakens Part 2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1274}}', 'G0023', '[APT16](https://attack.mitre.org/groups/G0023) is a China-based threat group that has launched spearphishing campaigns targeting Japanese and Taiwanese organizations. (Citation: FireEye EPS Awakens Part 2)', 'APT16');
