INSERT INTO public.attack_mitigations VALUES (1, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--f9f9e6ef-bc0a-41ad-ba11-0924e5e84c4c', '[{"url": "https://attack.mitre.org/mitigations/M1036", "external_id": "M1036", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 760}}', 'M1036', 'Configure features related to account use like login attempt lockouts, specific login times, etc.', 'Account Use Policies');
INSERT INTO public.attack_mitigations VALUES (2, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--e3388c78-2a8d-47c2-8422-c1398b324462', '[{"url": "https://attack.mitre.org/mitigations/M1015", "external_id": "M1015", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 761}}', 'M1015', 'Configure Active Directory to prevent use of certain techniques; use SID Filtering, etc.', 'Active Directory Configuration');
INSERT INTO public.attack_mitigations VALUES (3, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--a6a47a06-08fc-4ec4-bdc3-20373375ebb9', '[{"url": "https://attack.mitre.org/mitigations/M1049", "external_id": "M1049", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 722}}', 'M1049', 'Use signatures or heuristics to detect malicious software.', 'Antivirus/Antimalware');
INSERT INTO public.attack_mitigations VALUES (4, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--25dc1ce8-eb55-4333-ae30-a7cb4f5894a1', '[{"url": "https://attack.mitre.org/mitigations/M1013", "external_id": "M1013", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 894}, "x_mitre_old_attack_id": {"Num": 0, "Raw": "\"MOB-M1013\"", "Str": "MOB-M1013", "Type": 3, "Index": 858}}', 'M1013', 'This mitigation describes any guidance or training given to developers of applications to avoid introducing security weaknesses that an adversary may be able to take advantage of.', 'Application Developer Guidance');
INSERT INTO public.attack_mitigations VALUES (5, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--b9f0c069-abbe-4a07-a245-2481219a1463', '[{"url": "https://attack.mitre.org/mitigations/M1048", "external_id": "M1048", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 770}}', 'M1048', 'Restrict execution of code to a virtual environment on or in transit to an endpoint system.', 'Application Isolation and Sandboxing');
INSERT INTO public.attack_mitigations VALUES (6, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--cc2399fd-3cd3-4319-8d0a-fbd6420cdaf8', '[{"url": "https://attack.mitre.org/mitigations/M1047", "external_id": "M1047", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 779}}', 'M1047', 'Perform audits or scans of systems, permissions, insecure software, insecure configurations, etc. to identify potential weaknesses.', 'Audit');
INSERT INTO public.attack_mitigations VALUES (7, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--90f39ee1-d5a3-4aaa-9f28-3b42815b0d46', '[{"url": "https://attack.mitre.org/mitigations/M1040", "external_id": "M1040", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 836}}', 'M1040', 'Use capabilities to prevent suspicious behavior patterns from occurring on endpoint systems. This could include suspicious process, file, API call, etc. behavior.', 'Behavior Prevention on Endpoint');
INSERT INTO public.attack_mitigations VALUES (8, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--7da0387c-ba92-4553-b291-b636ee42b2eb', '[{"url": "https://attack.mitre.org/mitigations/M1046", "external_id": "M1046", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 765}}', 'M1046', 'Use secure methods to boot a system and verify the integrity of the operating system and loading mechanisms.', 'Boot Integrity');
INSERT INTO public.attack_mitigations VALUES (9, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--590777b3-b475-4c7c-aaf8-f4a73b140312', '[{"url": "https://attack.mitre.org/mitigations/M1045", "external_id": "M1045", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 773}}', 'M1045', 'Enforce binary and application integrity with digital signature verification to prevent untrusted code from executing.', 'Code Signing');
INSERT INTO public.attack_mitigations VALUES (10, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--49c06d54-9002-491d-9147-8efb537fbd26', '[{"url": "https://attack.mitre.org/mitigations/M1043", "external_id": "M1043", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 791}}', 'M1043', 'Use capabilities to prevent successful credential access by adversaries; including blocking forms of credential dumping.', 'Credential Access Protection');
INSERT INTO public.attack_mitigations VALUES (11, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--3efe43d1-6f3f-4fcb-ab39-4a730971f70b', '[{"url": "https://attack.mitre.org/mitigations/M1053", "external_id": "M1053", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 840}}', 'M1053', 'Take and store data backups from end user systems and critical servers. Ensure backup and storage systems are hardened and kept separate from the corporate network to prevent compromise.', 'Data Backup');
INSERT INTO public.attack_mitigations VALUES (12, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--eb88d97c-32f1-40be-80f0-d61a4b0b4b31', '[{"url": "https://attack.mitre.org/mitigations/M1042", "external_id": "M1042", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 784}}', 'M1042', 'Remove or deny access to unnecessary and potentially vulnerable software to prevent abuse by adversaries.', 'Disable or Remove Feature or Program');
INSERT INTO public.attack_mitigations VALUES (13, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--787fb64d-c87b-4ee5-a341-0ef17ec4c15c', '[{"url": "https://attack.mitre.org/mitigations/M1055", "external_id": "M1055", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 793}}', 'M1055', 'This category is to associate techniques that mitigation might increase risk of compromise and therefore mitigation is not recommended.', 'Do Not Mitigate');
INSERT INTO public.attack_mitigations VALUES (14, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--feff9142-e8c2-46f4-842b-bd6fb3d41157', '[{"url": "https://attack.mitre.org/mitigations/M1041", "external_id": "M1041", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 725}}', 'M1041', 'Protect sensitive information with strong encryption.', 'Encrypt Sensitive Information');
INSERT INTO public.attack_mitigations VALUES (15, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--609191bf-7d06-40e4-b1f8-9e11eb3ff8a6', '[{"url": "https://attack.mitre.org/mitigations/M1039", "external_id": "M1039", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 754}}', 'M1039', 'Prevent modification of environment variables by unauthorized users and groups.', 'Environment Variable Permissions');
INSERT INTO public.attack_mitigations VALUES (16, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--47e0e9fe-96ce-4f65-8bb1-8be1feacb5db', '[{"url": "https://attack.mitre.org/mitigations/M1038", "external_id": "M1038", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 751}}', 'M1038', 'Block execution of code on a system through application control, and/or script blocking.', 'Execution Prevention');
INSERT INTO public.attack_mitigations VALUES (17, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--d2a24649-9694-4c97-9c62-ce7b270bf6a3', '[{"url": "https://attack.mitre.org/mitigations/M1050", "external_id": "M1050", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 775}}', 'M1050', 'Use capabilities to detect and block conditions that may lead to or be indicative of a software exploit occurring.', 'Exploit Protection');
INSERT INTO public.attack_mitigations VALUES (18, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--20f6a9df-37c4-4e20-9e47-025983b1b39d', '[{"url": "https://attack.mitre.org/mitigations/M1037", "external_id": "M1037", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 820}}', 'M1037', 'Use network appliances to filter ingress or egress traffic and perform protocol-based filtering. Configure software on endpoints to filter network traffic.', 'Filter Network Traffic');
INSERT INTO public.attack_mitigations VALUES (19, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--1dcaeb21-9348-42ea-950a-f842aaf1ae1f', '[{"url": "https://attack.mitre.org/mitigations/M1035", "external_id": "M1035", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 846}}', 'M1035', 'Prevent access to file shares, remote access to systems, unnecessary services. Mechanisms to limit access may include use of network concentrators, RDP gateways, etc.', 'Limit Access to Resource Over Network');
INSERT INTO public.attack_mitigations VALUES (20, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--2995bc22-2851-4345-ad19-4e7e295be264', '[{"url": "https://attack.mitre.org/mitigations/M1034", "external_id": "M1034", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 771}}', 'M1034', 'Block users or groups from installing or using unapproved hardware on systems, including USB devices.', 'Limit Hardware Installation');
INSERT INTO public.attack_mitigations VALUES (21, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--23843cff-f7b9-4659-a7b7-713ef347f547', '[{"url": "https://attack.mitre.org/mitigations/M1033", "external_id": "M1033", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 728}}', 'M1033', 'Block users or groups from installing unapproved software.', 'Limit Software Installation');
INSERT INTO public.attack_mitigations VALUES (22, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--b045d015-6bed-4490-bd38-56b41ece59a0', '[{"url": "https://attack.mitre.org/mitigations/M1032", "external_id": "M1032", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 833}}', 'M1032', 'Use two or more pieces of evidence to authenticate to a system; such as username and password in addition to a token from a physical smart card or token generator.', 'Multi-factor Authentication');
INSERT INTO public.attack_mitigations VALUES (23, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--12241367-a8b7-49b4-b86e-2236901ba50c', '[{"url": "https://attack.mitre.org/mitigations/M1031", "external_id": "M1031", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 745}}', 'M1031', 'Use intrusion detection signatures to block traffic at network boundaries.', 'Network Intrusion Prevention');
INSERT INTO public.attack_mitigations VALUES (24, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--86598de0-b347-4928-9eb0-0acbfc21908c', '[{"url": "https://attack.mitre.org/mitigations/M1030", "external_id": "M1030", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1052}}', 'M1030', 'Architect sections of the network to isolate critical systems, functions, or resources. Use physical and logical segmentation to prevent access to potentially sensitive systems and information. Use a DMZ to contain any internet-facing services that should not be exposed from the internal network. Configure separate virtual private cloud (VPC) instances to isolate critical cloud systems.', 'Network Segmentation');
INSERT INTO public.attack_mitigations VALUES (25, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--2f316f6c-ae42-44fe-adf8-150989e0f6d3', '[{"url": "https://attack.mitre.org/mitigations/M1028", "external_id": "M1028", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 827}}', 'M1028', 'Make configuration changes related to the operating system or a common feature of the operating system that result in system hardening against techniques.', 'Operating System Configuration');
INSERT INTO public.attack_mitigations VALUES (26, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--90c218c3-fbf8-4830-98a7-e8cfb7eaa485', '[{"url": "https://attack.mitre.org/mitigations/M1027", "external_id": "M1027", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 714}}', 'M1027', 'Set and enforce secure password policies for accounts.', 'Password Policies');
INSERT INTO public.attack_mitigations VALUES (27, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--78bb71be-92b4-46de-acd6-5f998fedf1cc', '[{"url": "https://attack.mitre.org/mitigations/M1056", "external_id": "M1056", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 856}}', 'M1056', 'This category is used for any applicable mitigation activities that apply to techniques occurring before an adversary gains Initial Access, such as Reconnaissance and Resource Development techniques.', 'Pre-compromise');
INSERT INTO public.attack_mitigations VALUES (28, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--9bb9e696-bff8-4ae1-9454-961fc7d91d5f', '[{"url": "https://attack.mitre.org/mitigations/M1026", "external_id": "M1026", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 789}}', 'M1026', 'Manage the creation, modification, use, and permissions associated to privileged accounts, including SYSTEM and root.', 'Privileged Account Management');
INSERT INTO public.attack_mitigations VALUES (29, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--72dade3e-1cba-4182-b3b3-a77ca52f02a1', '[{"url": "https://attack.mitre.org/mitigations/M1025", "external_id": "M1025", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 892}}', 'M1025', 'Protect processes with high privileges that can be used to interact with critical system components through use of protected process light, anti-process injection defenses, or other process integrity enforcement measures.', 'Privileged Process Integrity');
INSERT INTO public.attack_mitigations VALUES (30, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--20a2baeb-98c2-4901-bad7-dc62d0a03dea', '[{"url": "https://attack.mitre.org/mitigations/M1029", "external_id": "M1029", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 828}}', 'M1029', 'Use remote security log and sensitive file storage where access can be controlled better to prevent exposure of intrusion detection log data or sensitive information.', 'Remote Data Storage');
INSERT INTO public.attack_mitigations VALUES (31, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--987988f0-cf86-4680-a875-2f6456ab2448', '[{"url": "https://attack.mitre.org/mitigations/M1022", "external_id": "M1022", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 794}}', 'M1022', 'Restrict access by setting directory and file permissions that are not specific to users or privileged accounts.', 'Restrict File and Directory Permissions');
INSERT INTO public.attack_mitigations VALUES (32, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--e8242a33-481c-4891-af63-4cf3e4cf6aff', '[{"url": "https://attack.mitre.org/mitigations/M1044", "external_id": "M1044", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 873}}', 'M1044', 'Prevent abuse of library loading mechanisms in the operating system and software to load untrusted code by configuring appropriate library loading mechanisms and investigating potential vulnerable software.', 'Restrict Library Loading');
INSERT INTO public.attack_mitigations VALUES (33, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--a2c36a5d-4058-475e-8e77-fff75e50d3b9', '[{"url": "https://attack.mitre.org/mitigations/M1024", "external_id": "M1024", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 749}}', 'M1024', 'Restrict the ability to modify certain hives or keys in the Windows Registry.', 'Restrict Registry Permissions');
INSERT INTO public.attack_mitigations VALUES (34, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--21da4fd4-27ad-4e9c-b93d-0b9b14d02c96', '[{"url": "https://attack.mitre.org/mitigations/M1021", "external_id": "M1021", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 783}}', 'M1021', 'Restrict use of certain websites, block downloads/attachments, block Javascript, restrict browser extensions, etc.', 'Restrict Web-Based Content');
INSERT INTO public.attack_mitigations VALUES (35, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--7bb5fae9-53ad-4424-866b-f0ea2a8b731d', '[{"url": "https://attack.mitre.org/mitigations/M1020", "external_id": "M1020", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 752}}', 'M1020', 'Break and inspect SSL/TLS sessions to look at encrypted web traffic for adversary activity.', 'SSL/TLS Inspection');
INSERT INTO public.attack_mitigations VALUES (36, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--b5dbb4c5-b0b1-40b1-80b6-e9e84ab90067', '[{"url": "https://attack.mitre.org/mitigations/M1054", "external_id": "M1054", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 810}}', 'M1054', 'Implement configuration changes to software (other than the operating system) to mitigate security risks associated to how the software operates.', 'Software Configuration');
INSERT INTO public.attack_mitigations VALUES (37, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--874c0166-e407-45c2-a1d9-e4e3a6570fd8', '[{"url": "https://attack.mitre.org/mitigations/M1019", "external_id": "M1019", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 838}}', 'M1019', 'A threat intelligence program helps an organization generate their own threat intelligence information and track trends to inform defensive priorities to mitigate risk.', 'Threat Intelligence Program');
INSERT INTO public.attack_mitigations VALUES (38, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--e5d930e9-775a-40ad-9bdb-b941d8dfe86b', '[{"url": "https://attack.mitre.org/mitigations/M1051", "external_id": "M1051", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 721}}', 'M1051', 'Perform regular software updates to mitigate exploitation risk.', 'Update Software');
INSERT INTO public.attack_mitigations VALUES (39, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--2c2ad92a-d710-41ab-a996-1db143bb4808', '[{"url": "https://attack.mitre.org/mitigations/M1052", "external_id": "M1052", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 768}}', 'M1052', 'Configure Windows User Account Control to mitigate risk of adversaries obtaining elevated process access.', 'User Account Control');
INSERT INTO public.attack_mitigations VALUES (40, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--93e7968a-9074-4eac-8ae9-9f5200ec3317', '[{"url": "https://attack.mitre.org/mitigations/M1018", "external_id": "M1018", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 750}}', 'M1018', 'Manage the creation, modification, use, and permissions associated to user accounts.', 'User Account Management');
INSERT INTO public.attack_mitigations VALUES (41, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--2a4f6c11-a4a7-4cb9-b0ef-6ae1bb3a718a', '[{"url": "https://attack.mitre.org/mitigations/M1017", "external_id": "M1017", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 850}}', 'M1017', 'Train users to be aware of access or manipulation attempts by an adversary to reduce the risk of successful spearphishing, social engineering, and other techniques that involve user interaction.', 'User Training');
INSERT INTO public.attack_mitigations VALUES (42, '2021-07-16 00:44:47.153666+00', '2021-07-16 00:44:47.153666+00', NULL, 'course-of-action--15437c6d-b998-4a36-be41-4ace3d54d266', '[{"url": "https://attack.mitre.org/mitigations/M1016", "external_id": "M1016", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 771}}', 'M1016', 'Vulnerability scanning is used to find potentially exploitable software vulnerabilities to remediate them.', 'Vulnerability Scanning');
INSERT INTO public.attack_patterns VALUES (2, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.382433+00', NULL, 'attack-pattern--cabe189c-a0e3-4965-a473-dcff00f17213', '[{"url": "https://attack.mitre.org/techniques/T1557/002", "external_id": "T1557.002", "source_name": "mitre-attack"}, {"url": "https://tools.ietf.org/html/rfc826", "description": "Plummer, D. (1982, November). An Ethernet Address Resolution Protocol. Retrieved October 15, 2020.", "source_name": "RFC826 ARP"}, {"url": "https://pen-testing.sans.org/resources/papers/gcih/real-world-arp-spoofing-105411", "description": "Siles, R. (2003, August). Real World ARP Spoofing. Retrieved October 15, 2020.", "source_name": "Sans ARP Spoofing Aug 2003"}, {"url": "https://web.archive.org/web/20200302085133/https://www.cylance.com/content/dam/cylance/pages/operation-cleaver/Cylance_Operation_Cleaver_Report.pdf", "description": "Cylance. (2014, December). Operation Cleaver. Retrieved September 14, 2017.", "source_name": "Cylance Cleaver"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4565}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor network traffic for unusual ARP traffic, gratuitous ARP replies may be suspicious. \\n\\nConsider collecting changes to ARP caches across endpoints for signs of ARP poisoning. For example, if multiple IP addresses map to a single MAC address, this could be an indicator that the ARP cache has been poisoned.\"", "Str": "Monitor network traffic for unusual ARP traffic, gratuitous ARP replies may be suspicious. \n\nConsider collecting changes to ARP caches across endpoints for signs of ARP poisoning. For example, if multiple IP addresses map to a single MAC address, this could be an indicator that the ARP cache has been poisoned.", "Type": 3, "Index": 4050}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4487}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jon Sternstein, Stern Security\"\n    ]", "Str": "", "Type": 5, "Index": 3844}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3920}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4398}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4440}}', 'T1557.002', 'Adversaries may poison Address Resolution Protocol (ARP) caches to position themselves between the communication of two or more networked devices. This activity may be used to enable follow-on behaviors such as [Network Sniffing](https://attack.mitre.org/techniques/T1040) or [Transmitted Data Manipulation](https://attack.mitre.org/techniques/T1565/002).
INSERT INTO public.attack_patterns VALUES (3, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.417497+00', NULL, 'attack-pattern--3986e7fd-a8e9-4ecb-bfc6-55920855912b', '[{"url": "https://attack.mitre.org/techniques/T1558/004", "external_id": "T1558.004", "source_name": "mitre-attack"}, {"url": "http://www.harmj0y.net/blog/activedirectory/roasting-as-reps/", "description": "HarmJ0y. (2017, January 17). Roasting AS-REPs. Retrieved August 24, 2020.", "source_name": "Harmj0y Roasting AS-REPs Jan 2017"}, {"url": "https://social.technet.microsoft.com/wiki/contents/articles/23559.kerberos-pre-authentication-why-it-should-not-be-disabled.aspx", "description": "Sanyal, M.. (2014, March 18). Kerberos Pre-Authentication: Why It Should Not Be Disabled. Retrieved August 25, 2020.", "source_name": "Microsoft Kerberos Preauth 2014"}, {"url": "https://blog.stealthbits.com/cracking-active-directory-passwords-with-as-rep-roasting/", "description": "Jeff Warren. (2019, June 27). Cracking Active Directory Passwords with AS-REP Roasting. Retrieved August 24, 2020.", "source_name": "Stealthbits Cracking AS-REP Roasting Jun 2019"}, {"url": "https://redsiege.com/kerberoast-slides", "description": "Medin, T. (2014, November). Attacking Kerberos - Kicking the Guard Dog of Hades. Retrieved March 22, 2018.", "source_name": "SANS Attacking Kerberos Nov 2014"}, {"url": "https://adsecurity.org/?p=2293", "description": "Metcalf, S. (2015, December 31). Cracking Kerberos TGS Tickets Using Kerberoast – Exploiting Kerberos to Compromise the Active Directory Domain. Retrieved March 22, 2018.", "source_name": "AdSecurity Cracking Kerberos Dec 2015"}, {"url": "https://blogs.technet.microsoft.com/motiba/2018/02/23/detecting-kerberoasting-activity-using-azure-security-center/", "description": "Bani, M. (2018, February 23). Detecting Kerberoasting activity using Azure Security Center. Retrieved March 23, 2018.", "source_name": "Microsoft Detecting Kerberoasting Feb 2018"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/event-4768", "description": "Microsoft. (2017, April 19). 4768(S, F): A Kerberos authentication ticket (TGT) was requested. Retrieved August 24, 2020.", "source_name": "Microsoft 4768 TGT 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6504}, "x_mitre_detection": {"Num": 0, "Raw": "\"Enable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4768 and 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17], pre-authentication not required [Type: 0x0]).(Citation: AdSecurity Cracking Kerberos Dec 2015)(Citation: Microsoft Detecting Kerberoasting Feb 2018)(Citation: Microsoft 4768 TGT 2017)\"", "Str": "Enable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4768 and 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17], pre-authentication not required [Type: 0x0]).(Citation: AdSecurity Cracking Kerberos Dec 2015)(Citation: Microsoft Detecting Kerberoasting Feb 2018)(Citation: Microsoft 4768 TGT 2017)", "Type": 3, "Index": 5776}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6383}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"James Dunn, @jamdunnDFW, EY\",\n      \"Swapnil Kumbhar\",\n      \"Jacques Pluviose, @Jacqueswildy_IT\",\n      \"Dan Nutting, @KerberToast\"\n    ]", "Str": "", "Type": 5, "Index": 5503}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Credential Request\"\n    ]", "Str": "", "Type": 5, "Index": 5680}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6294}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Valid domain account\"\n    ]", "Str": "", "Type": 5, "Index": 6443}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6336}}', 'T1558.004', 'Adversaries may reveal credentials of accounts that have disabled Kerberos preauthentication by [Password Cracking](https://attack.mitre.org/techniques/T1110/002) Kerberos messages.(Citation: Harmj0y Roasting AS-REPs Jan 2017) 
INSERT INTO public.attack_patterns VALUES (4, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.467424+00', NULL, 'attack-pattern--67720091-eee3-4d2d-ae16-8264567f6f5b', '[{"url": "https://attack.mitre.org/techniques/T1548", "external_id": "T1548", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3774}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the file system for files that have the setuid or setgid bits set. Also look for any process API calls for behavior that may be indicative of [Process Injection](https://attack.mitre.org/techniques/T1055) and unusual loaded DLLs through [DLL Search Order Hijacking](https://attack.mitre.org/techniques/T1574/001), which indicate attempts to gain access to higher privileged processes. On Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo).\\n\\nConsider monitoring for <code>/usr/libexec/security_authtrampoline</code> executions which may indicate that AuthorizationExecuteWithPrivileges is being executed. MacOS system logs may also indicate when AuthorizationExecuteWithPrivileges is being called. Monitoring OS API callbacks for the execution can also be a way to detect this behavior but requires specialized security tooling.\\n\\nOn Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo). This technique is abusing normal functionality in macOS and Linux systems, but sudo has the ability to log all input and output based on the <code>LOG_INPUT</code> and <code>LOG_OUTPUT</code> directives in the <code>/etc/sudoers</code> file.\\n\\nThere are many ways to perform UAC bypasses when a user is in the local administrator group on a system, so it may be difficult to target detection on all variations. Efforts should likely be placed on mitigation and collecting enough information on process launches and actions that could be performed before and after a UAC bypass is performed. Some UAC bypass methods rely on modifying specific, user-accessible Registry settings. Analysts should monitor Registry settings for unauthorized changes.\"", "Str": "Monitor the file system for files that have the setuid or setgid bits set. Also look for any process API calls for behavior that may be indicative of [Process Injection](https://attack.mitre.org/techniques/T1055) and unusual loaded DLLs through [DLL Search Order Hijacking](https://attack.mitre.org/techniques/T1574/001), which indicate attempts to gain access to higher privileged processes. On Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo).\n\nConsider monitoring for <code>/usr/libexec/security_authtrampoline</code> executions which may indicate that AuthorizationExecuteWithPrivileges is being executed. MacOS system logs may also indicate when AuthorizationExecuteWithPrivileges is being called. Monitoring OS API callbacks for the execution can also be a way to detect this behavior but requires specialized security tooling.\n\nOn Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo). This technique is abusing normal functionality in macOS and Linux systems, but sudo has the ability to log all input and output based on the <code>LOG_INPUT</code> and <code>LOG_OUTPUT</code> directives in the <code>/etc/sudoers</code> file.\n\nThere are many ways to perform UAC bypasses when a user is in the local administrator group on a system, so it may be difficult to target detection on all variations. Efforts should likely be placed on mitigation and collecting enough information on process launches and actions that could be performed before and after a UAC bypass is performed. Some UAC bypass methods rely on modifying specific, user-accessible Registry settings. Analysts should monitor Registry settings for unauthorized changes.", "Type": 3, "Index": 1764}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3696}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Metadata\",\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\",\n      \"File: File Modification\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1467}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3583}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3626}}', 'T1548', 'Adversaries may circumvent mechanisms designed to control elevate privileges to gain higher-level permissions. Most modern systems contain native elevation control mechanisms that are intended to limit privileges that a user can perform on a machine. Authorization has to be granted to specific users in order to perform tasks that can be considered of higher risk. An adversary can perform several methods to take advantage of built-in control mechanisms in order to escalate privileges on a system.', 'Abuse Elevation Control Mechanism', 0);
INSERT INTO public.attack_patterns VALUES (5, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.521216+00', NULL, 'attack-pattern--dcaa092b-7de9-4a21-977f-7fcb77e89c48', '[{"url": "https://attack.mitre.org/techniques/T1134", "external_id": "T1134", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/633.html", "external_id": "CAPEC-633", "source_name": "capec"}, {"url": "https://pentestlab.blog/2017/04/03/token-manipulation/", "description": "netbiosX. (2017, April 3). Token Manipulation. Retrieved April 21, 2017.", "source_name": "Pentestlab Token Manipulation"}, {"url": "https://technet.microsoft.com/en-us/windows-server-docs/identity/ad-ds/manage/component-updates/command-line-process-auditing", "description": "Mathers, B. (2017, March 7). Command line process auditing. Retrieved April 21, 2017.", "source_name": "Microsoft Command-line Logging"}, {"url": "https://msdn.microsoft.com/en-us/library/windows/desktop/aa378184(v=vs.85).aspx", "description": "Microsoft TechNet. (n.d.). Retrieved April 25, 2017.", "source_name": "Microsoft LogonUser"}, {"url": "https://msdn.microsoft.com/en-us/library/windows/desktop/aa446617(v=vs.85).aspx", "description": "Microsoft TechNet. (n.d.). Retrieved April 25, 2017.", "source_name": "Microsoft DuplicateTokenEx"}, {"url": "https://msdn.microsoft.com/en-us/library/windows/desktop/aa378612(v=vs.85).aspx", "description": "Microsoft TechNet. (n.d.). Retrieved April 25, 2017.", "source_name": "Microsoft ImpersonateLoggedOnUser"}, {"url": "https://www.blackhat.com/docs/eu-17/materials/eu-17-Atkinson-A-Process-Is-No-One-Hunting-For-Token-Manipulation.pdf", "description": "Atkinson, J., Winchester, R. (2017, December 7). A Process is No One: Hunting for Token Manipulation. Retrieved December 21, 2017.", "source_name": "BlackHat Atkinson Winchester Token Manipulation"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 6967}, "x_mitre_detection": {"Num": 0, "Raw": "\"If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\\n\\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior. \\n\\nThere are many Windows API calls a payload can take advantage of to manipulate access tokens (e.g., <code>LogonUser</code> (Citation: Microsoft LogonUser), <code>DuplicateTokenEx</code>(Citation: Microsoft DuplicateTokenEx), and <code>ImpersonateLoggedOnUser</code>(Citation: Microsoft ImpersonateLoggedOnUser)). Please see the referenced Windows API pages for more information.\\n\\nQuery systems for process and thread token information and look for inconsistencies such as user owns processes impersonating the local SYSTEM account.(Citation: BlackHat Atkinson Winchester Token Manipulation)\\n\\nLook for inconsistencies between the various fields that store PPID information, such as the EventHeader ProcessId from data collected via Event Tracing for Windows (ETW), Creator Process ID/Name from Windows event logs, and the ProcessID and ParentProcessID (which are also produced from ETW and other utilities such as Task Manager and Process Explorer). The ETW provided EventHeader ProcessId identifies the actual parent process.\"", "Str": "If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\n\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior. \n\nThere are many Windows API calls a payload can take advantage of to manipulate access tokens (e.g., <code>LogonUser</code> (Citation: Microsoft LogonUser), <code>DuplicateTokenEx</code>(Citation: Microsoft DuplicateTokenEx), and <code>ImpersonateLoggedOnUser</code>(Citation: Microsoft ImpersonateLoggedOnUser)). Please see the referenced Windows API pages for more information.\n\nQuery systems for process and thread token information and look for inconsistencies such as user owns processes impersonating the local SYSTEM account.(Citation: BlackHat Atkinson Winchester Token Manipulation)\n\nLook for inconsistencies between the various fields that store PPID information, such as the EventHeader ProcessId from data collected via Event Tracing for Windows (ETW), Creator Process ID/Name from Windows event logs, and the ProcessID and ParentProcessID (which are also produced from ETW and other utilities such as Task Manager and Process Explorer). The ETW provided EventHeader ProcessId identifies the actual parent process.", "Type": 3, "Index": 5090}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6919}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tom Ueltschi @c_APT_ure\",\n      \"Travis Smith, Tripwire\",\n      \"Robby Winchester, @robwinchester3\",\n      \"Jared Atkinson, @jaredcatkinson\"\n    ]", "Str": "", "Type": 5, "Index": 4414}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Process: Process Metadata\",\n      \"Process: OS API Execution\",\n      \"User Account: User Account Metadata\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4599}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6806}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\",\n      \"System access controls\",\n      \"File system access controls\",\n      \"Heuristic Detection\",\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 4889}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6849}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 6751}}', 'T1134', 'Adversaries may modify access tokens to operate under a different user or system security context to perform actions and bypass access controls. Windows uses access tokens to determine the ownership of a running process. A user can manipulate access tokens to make a running process appear as though it is the child of a different process or belongs to someone other than the user that started the process. When this occurs, the process also takes on the security context associated with the new token.
INSERT INTO public.attack_patterns VALUES (6, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.956039+00', NULL, 'attack-pattern--70e52b04-2a0c-4cea-9d18-7149f1df9dc5', '[{"url": "https://attack.mitre.org/techniques/T1546/008", "external_id": "T1546.008", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/558.html", "external_id": "CAPEC-558", "source_name": "capec"}, {"url": "https://www.fireeye.com/blog/threat-research/2012/08/hikit-rootkit-advanced-persistent-attack-techniques-part-1.html", "description": "Glyer, C., Kazanciyan, R. (2012, August 20). The “Hikit” Rootkit: Advanced and Persistent Attack Techniques (Part 1). Retrieved June 6, 2016.", "source_name": "FireEye Hikit Rootkit"}, {"url": "https://www.slideshare.net/DennisMaldonado5/sticky-keys-to-the-kingdom", "description": "Maldonado, D., McGuffin, T. (2016, August 6). Sticky Keys to the Kingdom. Retrieved July 5, 2017.", "source_name": "DEFCON2016 Sticky Keys"}, {"url": "http://blog.crowdstrike.com/registry-analysis-with-crowdresponse/", "description": "Tilbury, C. (2014, August 28). Registry Analysis with CrowdResponse. Retrieved November 12, 2014.", "source_name": "Tilbury 2014"}, {"url": "https://giuliocomi.blogspot.com/2019/10/abusing-windows-10-narrators-feedback.html", "description": "Comi, G. (2019, October 19). Abusing Windows 10 Narrator''s ''Feedback-Hub'' URI for Fileless Persistence. Retrieved April 28, 2020.", "source_name": "Narrator Accessibility Abuse"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6080}, "x_mitre_detection": {"Num": 0, "Raw": "\"Changes to accessibility utility binaries or binary paths that do not correlate with known software, patch cycles, etc., are suspicious. Command line invocation of tools capable of modifying the Registry for associated keys are also suspicious. Utility arguments and the binaries themselves should be monitored for changes. Monitor Registry keys within <code>HKEY_LOCAL_MACHINE\\\\SOFTWARE\\\\Microsoft\\\\Windows NT\\\\CurrentVersion\\\\Image File Execution Options</code>.\"", "Str": "Changes to accessibility utility binaries or binary paths that do not correlate with known software, patch cycles, etc., are suspicious. Command line invocation of tools capable of modifying the Registry for associated keys are also suspicious. Utility arguments and the binaries themselves should be monitored for changes. Monitor Registry keys within <code>HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\Windows NT\\CurrentVersion\\Image File Execution Options</code>.", "Type": 3, "Index": 5374}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6032}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Paul Speulstra, AECOM Global Security Operations Center\"\n    ]", "Str": "", "Type": 5, "Index": 5046}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 5147}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5934}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5976}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5879}}', 'T1546.008', 'Adversaries may establish persistence and/or elevate privileges by executing malicious content triggered by accessibility features. Windows contains accessibility features that may be launched with a key combination before a user has logged in (ex: when the user is on the Windows logon screen). An adversary can modify the way these programs are launched to get a command prompt or backdoor without logging in to the system.
INSERT INTO public.attack_patterns VALUES (8, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.045967+00', NULL, 'attack-pattern--72b74d71-8169-42aa-92e0-e7b04b9f5a08', '[{"url": "https://attack.mitre.org/techniques/T1087", "external_id": "T1087", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/575.html", "external_id": "CAPEC-575", "source_name": "capec"}, {"url": "https://www.elastic.co/blog/embracing-offensive-tooling-building-detections-against-koadic-using-eql", "description": "Stepanic, D.. (2020, January 13). Embracing offensive tooling: Building detections against Koadic using EQL. Retrieved November 30, 2020.", "source_name": "Elastic - Koadiac Detection with EQL"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.3\"", "Str": "2.3", "Type": 3, "Index": 3133}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nMonitor for processes that can be used to enumerate user accounts, such as <code>net.exe</code> and <code>net1.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nMonitor for processes that can be used to enumerate user accounts, such as <code>net.exe</code> and <code>net1.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)", "Type": 3, "Index": 1859}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2963}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniel Stepanic, Elastic\",\n      \"Microsoft Threat Intelligence Center (MSTIC)\",\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 1527}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Metadata\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 1683}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2873}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2916}}', 'T1087', 'Adversaries may attempt to get a listing of accounts on a system or within an environment. This information can help adversaries determine which accounts exist to aid in follow-on behavior.', 'Account Discovery', 0);
INSERT INTO public.attack_patterns VALUES (10, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.140961+00', NULL, 'attack-pattern--0458aab9-ad42-4eac-9e22-706a95bafee2', '[{"url": "https://attack.mitre.org/techniques/T1583", "external_id": "T1583", "source_name": "mitre-attack"}, {"url": "https://documents.trendmicro.com/assets/wp/wp-criminal-hideouts-for-lease.pdf", "description": "Max Goncharov. (2015, July 15). Criminal Hideouts for Lease: Bulletproof Hosting Services. Retrieved March 6, 2017.", "source_name": "TrendmicroHideoutsLease"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2467}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider use of services that may aid in tracking of newly acquired infrastructure, such as WHOIS databases for domain registration information. Much of this activity may take place outside the visibility of the target organization, making detection of this behavior difficult.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Consider use of services that may aid in tracking of newly acquired infrastructure, such as WHOIS databases for domain registration information. Much of this activity may take place outside the visibility of the target organization, making detection of this behavior difficult.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 1961}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2423}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2391}}', 'T1583', 'Adversaries may buy, lease, or rent infrastructure that can be used during targeting. A wide variety of infrastructure exists for hosting and orchestrating adversary operations. Infrastructure solutions include physical or cloud servers, domains, and third-party web services.(Citation: TrendmicroHideoutsLease) Additionally, botnets are available for rent or purchase.
INSERT INTO public.attack_patterns VALUES (11, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.186901+00', NULL, 'attack-pattern--67073dde-d720-45ae-83da-b12d5e73ca3b', '[{"url": "https://attack.mitre.org/techniques/T1595", "external_id": "T1595", "source_name": "mitre-attack"}, {"url": "https://www.caida.org/publications/papers/2012/analysis_slash_zero/analysis_slash_zero.pdf", "description": "Dainotti, A. et al. (2012). Analysis of a “/0” Stealth Scan from a Botnet. Retrieved October 20, 2020.", "source_name": "Botnet Scan"}, {"url": "https://wiki.owasp.org/index.php/OAT-004_Fingerprinting", "description": "OWASP Wiki. (2018, February 16). OAT-004 Fingerprinting. Retrieved October 20, 2020.", "source_name": "OWASP Fingerprinting"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3496}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.\\n\\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.\n\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2678}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3452}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2548}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3420}}', 'T1595', 'Adversaries may execute active reconnaissance scans to gather information that can be used during targeting. Active scans are those where the adversary probes victim infrastructure via network traffic, as opposed to other forms of reconnaissance that do not involve direct interaction.
INSERT INTO public.attack_patterns VALUES (12, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.234015+00', NULL, 'attack-pattern--22522668-ddf6-470b-a027-9d6866679f67', '[{"url": "https://attack.mitre.org/techniques/T1547/014", "external_id": "T1547.014", "source_name": "mitre-attack"}, {"url": "https://helgeklein.com/blog/2010/04/active-setup-explained/", "description": "Klein, H. (2010, April 22). Active Setup Explained. Retrieved December 18, 2020.", "source_name": "Klein Active Setup 2010"}, {"url": "https://digital-forensics.sans.org/summit-archives/2010/35-glyer-apt-persistence-mechanisms.pdf", "description": "Glyer, C. (2010). Examples of Recent APT Persitence Mechanism. Retrieved December 18, 2020.", "source_name": "Mandiant Glyer APT 2010"}, {"url": "https://citizenlab.ca/2015/12/packrat-report/", "description": "Scott-Railton, J., et al. (2015, December 8). Packrat. Retrieved December 18, 2020.", "source_name": "Citizenlab Packrat 2015"}, {"url": "https://www.fireeye.com/blog/threat-research/2012/12/council-foreign-relations-water-hole-attack-details.html", "description": "Kindlund, D. (2012, December 30). CFR Watering Hole Attack Details. Retrieved December 18, 2020.", "source_name": "FireEye CFR Watering Hole 2012"}, {"url": "https://securelist.com/whos-really-spreading-through-the-bright-star/68978/", "description": "Baumgartner, K., Guerrero-Saade, J. (2015, March 4). Who’s Really Spreading through the Bright Star?. Retrieved December 18, 2020.", "source_name": "SECURELIST Bright Star 2015"}, {"url": "https://unit42.paloaltonetworks.com/unit42-tropic-trooper-targets-taiwanese-government-and-fossil-fuel-provider-with-poison-ivy/", "description": "Ray, V., et al. (2016, November 22). Tropic Trooper Targets Taiwanese Government and Fossil Fuel Provider With Poison Ivy. Retrieved December 18, 2020.", "source_name": "paloalto Tropic Trooper 2016"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5296}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor Registry key additions and/or modifications to <code>HKEY_LOCAL_MACHINE\\\\SOFTWARE\\\\Microsoft\\\\Active Setup\\\\Installed Components\\\\</code>.\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing the Active Setup Registry locations and startup folders.(Citation: TechNet Autoruns) Suspicious program execution as startup programs may show up as outlier processes that have not been seen before when compared against historical data.\"", "Str": "Monitor Registry key additions and/or modifications to <code>HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\Active Setup\\Installed Components\\</code>.\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing the Active Setup Registry locations and startup folders.(Citation: TechNet Autoruns) Suspicious program execution as startup programs may show up as outlier processes that have not been seen before when compared against historical data.", "Type": 3, "Index": 4594}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5248}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bencherchali Nasreddine, @nas_bench, ELIT Security Team (DSSD)\"\n    ]", "Str": "", "Type": 5, "Index": 4264}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4372}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5150}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5192}}', 'T1547.014', 'Adversaries may achieve persistence by adding a Registry key to the Active Setup of the local machine. Active Setup is a Windows mechanism that is used to execute programs when a user logs in. The value stored in the Registry key will be executed after a user logs into the computer.(Citation: Klein Active Setup 2010) These programs will be executed under the context of the user and will have the account''s associated permissions level.
INSERT INTO public.attack_patterns VALUES (15, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.701472+00', NULL, 'attack-pattern--8a2f40cf-8325-47f9-96e4-b1ca4c7389bd', '[{"url": "https://attack.mitre.org/techniques/T1098/001", "external_id": "T1098.001", "source_name": "mitre-attack"}, {"url": "https://msrc-blog.microsoft.com/2020/12/13/customer-guidance-on-recent-nation-state-cyber-attacks/", "description": "MSRC. (2020, December 13). Customer Guidance on Recent Nation-State Cyber Attacks. Retrieved December 17, 2020.", "source_name": "Microsoft SolarWinds Customer Guidance"}, {"url": "https://speakerdeck.com/tweekfawkes/blue-cloud-of-death-red-teaming-azure-1", "description": "Kunz, Bryce. (2018, May 11). Blue Cloud of Death: Red Teaming Azure. Retrieved October 23, 2019.", "source_name": "Blue Cloud of Death"}, {"url": "https://www.youtube.com/watch?v=wQ1CuAPnrLM&feature=youtu.be&t=2815", "description": "Kunz, Bruce. (2018, October 14). Blue Cloud of Death: Red Teaming Azure. Retrieved November 21, 2019.", "source_name": "Blue Cloud of Death Video"}, {"url": "https://nedinthecloud.com/2019/07/16/demystifying-azure-ad-service-principals/", "description": "Bellavance, Ned. (2019, July 16). Demystifying Azure AD Service Principals. Retrieved January 19, 2020.", "source_name": "Demystifying Azure AD Service Principals"}, {"url": "https://cloud.google.com/sdk/gcloud/reference/compute/os-login/ssh-keys/add", "description": "Google. (n.d.). gcloud compute os-login ssh-keys add. Retrieved October 1, 2020.", "source_name": "GCP SSH Key Add"}, {"url": "https://expel.io/blog/finding-evil-in-aws/", "description": "A. Randazzo, B. Manahan and S. Lipton. (2020, April 28). Finding Evil in AWS. Retrieved June 25, 2020.", "source_name": "Expel IO Evil in AWS"}, {"url": "https://expel.io/blog/behind-the-scenes-expel-soc-alert-aws/", "description": "S. Lipton, L. Easterly, A. Randazzo and J. Hencinski. (2020, July 28). Behind the scenes in the Expel SOC: Alert-to-fix in AWS. Retrieved October 1, 2020.", "source_name": "Expel Behind the Scenes"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 5084}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor Azure Activity Logs for Service Principal and Application modifications. Monitor for the usage of APIs that create or import SSH keys, particularly by unexpected users or accounts such as the root account.\\n\\nMonitor for use of credentials at unusual times or to unusual systems or services. This may also correlate with other suspicious activity.\"", "Str": "Monitor Azure Activity Logs for Service Principal and Application modifications. Monitor for the usage of APIs that create or import SSH keys, particularly by unexpected users or accounts such as the root account.\n\nMonitor for use of credentials at unusual times or to unusual systems or services. This may also correlate with other suspicious activity.", "Type": 3, "Index": 4519}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\",\n      \"Azure AD\"\n    ]", "Str": "", "Type": 5, "Index": 5021}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Expel\",\n      \"Oleg Kolesnikov, Securonix\",\n      \"Jannie Li, Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 4217}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Modification\",\n      \"Active Directory: Active Directory Object Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4373}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4909}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4951}}', 'T1098.001', 'Adversaries may add adversary-controlled credentials to a cloud account to maintain persistent access to victim accounts and instances within the environment.
INSERT INTO public.attack_patterns VALUES (52, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.772557+00', NULL, 'attack-pattern--91177e6d-b616-4a03-ba4b-f3b32f7dda75', '[{"url": "https://attack.mitre.org/techniques/T1596/004", "external_id": "T1596.004", "source_name": "mitre-attack"}, {"url": "https://www.digitalshadows.com/blog-and-research/content-delivery-networks-cdns-can-leave-you-exposed-how-you-might-be-affected-and-what-you-can-do-about-it/", "description": "Swisscom & Digital Shadows. (2017, September 6). Content Delivery Networks (CDNs) Can Leave You Exposed – How You Might Be Affected And What You Can Do About It. Retrieved October 20, 2020.", "source_name": "DigitalShadows CDN"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3004}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2563}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2960}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2929}}', 'T1596.004', 'Adversaries may search content delivery network (CDN) data about victims that can be used during targeting. CDNs allow an organization to host content from a distributed, load balanced array of servers. CDNs may also allow organizations to customize content delivery based on the requestor’s geographical region.
INSERT INTO public.attack_patterns VALUES (18, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.860534+00', NULL, 'attack-pattern--37b11151-1776-4f8f-b328-30939fbf2ceb', '[{"url": "https://attack.mitre.org/techniques/T1059/002", "external_id": "T1059.002", "source_name": "mitre-attack"}, {"url": "https://developer.apple.com/library/archive/documentation/AppleScript/Conceptual/AppleScriptLangGuide/introduction/ASLR_intro.html", "description": "Apple. (2016, January 25). Introduction to AppleScript Language Guide. Retrieved March 28, 2020.", "source_name": "Apple AppleScript"}, {"url": "https://www.sentinelone.com/blog/how-offensive-actors-use-applescript-for-attacking-macos/", "description": "Phil Stokes. (2020, March 16). How Offensive Actors Use AppleScript For Attacking macOS. Retrieved July 17, 2020.", "source_name": "SentinelOne AppleScript"}, {"url": "https://www.sentinelone.com/blog/macos-red-team-calling-apple-apis-without-building-binaries/", "description": "Phil Stokes. (2019, December 5). macOS Red Team: Calling Apple APIs Without Building Binaries. Retrieved July 17, 2020.", "source_name": "SentinelOne macOS Red Team"}, {"url": "https://www.mcafee.com/blogs/other-blogs/mcafee-labs/macro-malware-targets-macs/", "description": "Yerko Grbic. (2017, February 14). Macro Malware Targets Macs. Retrieved July 8, 2017.", "source_name": "Macro Malware Targets Macs"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5750}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for execution of AppleScript through <code>osascript</code> and usage of the <code>NSAppleScript</code> and <code>OSAScript</code> APIs that may be related to other suspicious behavior occurring on the system. Scripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.\\n\\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\"", "Str": "Monitor for execution of AppleScript through <code>osascript</code> and usage of the <code>NSAppleScript</code> and <code>OSAScript</code> APIs that may be related to other suspicious behavior occurring on the system. Scripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.\n\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.", "Type": 3, "Index": 4358}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 5704}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Phil Stokes, SentinelOne\"\n    ]", "Str": "", "Type": 5, "Index": 4149}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4219}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5615}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5657}}', 'T1059.002', 'Adversaries may abuse AppleScript for execution. AppleScript is a macOS scripting language designed to control applications and parts of the OS via inter-application messages called AppleEvents.(Citation: Apple AppleScript) These AppleEvent messages can be sent independently or easily scripted with AppleScript. These events can locate open windows, send keystrokes, and interact with almost any open application locally or remotely.
INSERT INTO public.attack_patterns VALUES (20, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.950327+00', NULL, 'attack-pattern--18cffc21-3260-437e-80e4-4ab8bf2ba5e9', '[{"url": "https://attack.mitre.org/techniques/T1499/003", "external_id": "T1499.003", "source_name": "mitre-attack"}, {"url": "https://pages.arbornetworks.com/rs/082-KNA-087/images/13th_Worldwide_Infrastructure_Security_Report.pdf", "description": "Philippe Alcoy, Steinthor Bjarnason, Paul Bowen, C.F. Chui, Kirill Kasavchnko, and Gary Sockrider of Netscout Arbor. (2018, January). Insight into the Global Threat Landscape - Netscout Arbor''s 13th Annual Worldwide Infrastructure Security Report. Retrieved April 22, 2019.", "source_name": "Arbor AnnualDoSreport Jan 2018"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3417}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\\n\\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.\"", "Str": "Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\n\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.", "Type": 3, "Index": 2230}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3247}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 3155}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2014}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3216}}', 'T1499.003', 'Adversaries may target resource intensive features of web applications to cause a denial of service (DoS). Specific features in web applications may be highly resource intensive. Repeated requests to those features may be able to exhaust system resources and deny access to the application or the server itself. (Citation: Arbor AnnualDoSreport Jan 2018)', 'Application Exhaustion Flood', 182);
INSERT INTO public.attack_patterns VALUES (21, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.008174+00', NULL, 'attack-pattern--355be19c-ffc9-46d5-8d50-d6a036c675b6', '[{"url": "https://attack.mitre.org/techniques/T1071", "external_id": "T1071", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2553}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1867}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2475}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1737}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2401}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2444}}', 'T1071', 'Adversaries may communicate using application layer protocols to avoid detection/network filtering by blending in with existing traffic. Commands to the remote system, and often the results of those commands, will be embedded within the protocol traffic between the client and server. 
INSERT INTO public.attack_patterns VALUES (23, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.07973+00', NULL, 'attack-pattern--4ae4f953-fe58-4cc8-a327-33257e30a830', '[{"url": "https://attack.mitre.org/techniques/T1010", "external_id": "T1010", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2090}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1170}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2027}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1031}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1937}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 1980}}', 'T1010', 'Adversaries may attempt to get a listing of open application windows. Window listings could convey information about how the system is used or give context to information collected by a keylogger.', 'Application Window Discovery', 0);
INSERT INTO public.attack_patterns VALUES (24, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.117568+00', NULL, 'attack-pattern--2bee5ffb-7a7a-4119-b1f2-158151b19ac0', '[{"url": "https://attack.mitre.org/techniques/T1499/004", "external_id": "T1499.004", "source_name": "mitre-attack"}, {"url": "https://blog.sucuri.net/2015/08/bind9-denial-of-service-exploit-in-the-wild.html", "description": "Cid, D.. (2015, August 2). BIND9 – Denial of Service Exploit in the Wild. Retrieved April 26, 2019.", "source_name": "Sucuri BIND9 August 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2238}, "x_mitre_detection": {"Num": 0, "Raw": "\"Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack. Externally monitor the availability of services that may be targeted by an Endpoint DoS.\"", "Str": "Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack. Externally monitor the availability of services that may be targeted by an Endpoint DoS.", "Type": 3, "Index": 1693}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2068}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 1976}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 1477}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2037}}', 'T1499.004', 'Adversaries may exploit software vulnerabilities that can cause an application or system to crash and deny availability to users. (Citation: Sucuri BIND9 August 2015) Some systems may automatically restart critical applications and services when crashes occur, but they can likely be re-exploited to cause a persistent DoS condition.', 'Application or System Exploitation', 182);
INSERT INTO public.attack_patterns VALUES (25, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.151689+00', NULL, 'attack-pattern--53ac20cd-aca3-406e-9aa0-9fc7fdc60a5a', '[{"url": "https://attack.mitre.org/techniques/T1560", "external_id": "T1560", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/List_of_file_signatures", "description": "Wikipedia. (2016, March 31). List of file signatures. Retrieved April 22, 2016.", "source_name": "Wikipedia File Header Signatures"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2793}, "x_mitre_detection": {"Num": 0, "Raw": "\"Archival software and archived files can be detected in many ways. Common utilities that may be present on the system or brought in by an adversary may be detectable through process monitoring and monitoring for command-line arguments for known archival utilities. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\\n\\nA process that loads the Windows DLL crypt32.dll may be used to perform encryption, decryption, or verification of file signatures.\\n\\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)\"", "Str": "Archival software and archived files can be detected in many ways. Common utilities that may be present on the system or brought in by an adversary may be detectable through process monitoring and monitoring for command-line arguments for known archival utilities. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\n\nA process that loads the Windows DLL crypt32.dll may be used to perform encryption, decryption, or verification of file signatures.\n\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)", "Type": 3, "Index": 1748}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2715}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1581}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2683}}', 'T1560', 'An adversary may compress and/or encrypt data that is collected prior to exfiltration. Compressing the data can help to obfuscate the collected data and minimize the amount of data sent over the network. Encryption can be used to hide information that is being exfiltrated from detection or make exfiltration less conspicuous upon inspection by a defender.
INSERT INTO public.attack_patterns VALUES (28, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.290723+00', NULL, 'attack-pattern--00f90846-cbd1-4fc5-9233-df5c2bf2a662', '[{"url": "https://attack.mitre.org/techniques/T1560/001", "external_id": "T1560.001", "source_name": "mitre-attack"}, {"url": "https://www.7-zip.org/", "description": "I. Pavlov. (2019). 7-Zip. Retrieved February 20, 2020.", "source_name": "7zip Homepage"}, {"url": "https://www.rarlab.com/", "description": "A. Roshal. (2020). RARLAB. Retrieved February 20, 2020.", "source_name": "WinRAR Homepage"}, {"url": "https://www.winzip.com/win/en/", "description": "Corel Corporation. (2020). WinZip. Retrieved February 20, 2020.", "source_name": "WinZip Homepage"}, {"url": "https://en.wikipedia.org/wiki/List_of_file_signatures", "description": "Wikipedia. (2016, March 31). List of file signatures. Retrieved April 22, 2016.", "source_name": "Wikipedia File Header Signatures"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3073}, "x_mitre_detection": {"Num": 0, "Raw": "\"Common utilities that may be present on the system or brought in by an adversary may be detectable through process monitoring and monitoring for command-line arguments for known archival utilities. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\\n\\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)\"", "Str": "Common utilities that may be present on the system or brought in by an adversary may be detectable through process monitoring and monitoring for command-line arguments for known archival utilities. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\n\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)", "Type": 3, "Index": 2231}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2995}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2098}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2964}}', 'T1560.001', 'An adversary may compress or encrypt data that is collected prior to exfiltration using 3rd party utilities. Many utilities exist that can archive data, including 7-Zip(Citation: 7zip Homepage), WinRAR(Citation: WinRAR Homepage), and WinZip(Citation: WinZip Homepage). Most utilities include functionality to encrypt and/or compress data.
INSERT INTO public.attack_patterns VALUES (30, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.356944+00', NULL, 'attack-pattern--7c0f17c9-1af6-4628-9cbd-9e45482dd605', '[{"url": "https://attack.mitre.org/techniques/T1055/004", "external_id": "T1055.004", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms681951.aspx", "description": "Microsoft. (n.d.). Asynchronous Procedure Calls. Retrieved December 8, 2017.", "source_name": "Microsoft APC"}, {"url": "https://www.cyberbit.com/blog/endpoint-security/new-early-bird-code-injection-technique-discovered/", "description": "Gavriel, H. & Erbesfeld, B. (2018, April 11). New ‘Early Bird’ Code Injection Technique Discovered. Retrieved May 24, 2018.", "source_name": "CyberBit Early Bird Apr 2018"}, {"url": "https://blog.ensilo.com/atombombing-brand-new-code-injection-for-windows", "description": "Liberman, T. (2016, October 27). ATOMBOMBING: BRAND NEW CODE INJECTION FOR WINDOWS. Retrieved December 8, 2017.", "source_name": "ENSIL AtomBombing Oct 2016"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms649053.aspx", "description": "Microsoft. (n.d.). About Atom Tables. Retrieved December 8, 2017.", "source_name": "Microsoft Atom Table"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5305}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, <code>QueueUserAPC</code>/<code>NtQueueApcThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, <code>QueueUserAPC</code>/<code>NtQueueApcThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 4238}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5257}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 4048}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5226}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 4156}}', 'T1055.004', 'Adversaries may inject malicious code into processes via the asynchronous procedure call (APC) queue in order to evade process-based defenses as well as possibly elevate privileges. APC injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (31, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.395244+00', NULL, 'attack-pattern--6636bc83-0611-45a6-b74f-1f3daf635b8e', '[{"url": "https://attack.mitre.org/techniques/T1053/001", "external_id": "T1053.001", "source_name": "mitre-attack"}, {"url": "https://kifarunix.com/scheduling-tasks-using-at-command-in-linux/", "description": "Koromicha. (2019, September 7). Scheduling tasks using at command in Linux. Retrieved December 3, 2019.", "source_name": "Kifarunix - Task Scheduling in Linux"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3044}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor scheduled task creation using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \\n\\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor scheduled task creation using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \n\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 2203}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2962}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2052}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3015}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2931}}', 'T1053.001', 'Adversaries may abuse the [at](https://attack.mitre.org/software/S0110) utility to perform task scheduling for initial or recurring execution of malicious code. The [at](https://attack.mitre.org/software/S0110) command within Linux operating systems enables administrators to schedule tasks.(Citation: Kifarunix - Task Scheduling in Linux)
INSERT INTO public.attack_patterns VALUES (33, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.496961+00', NULL, 'attack-pattern--1035cdf2-3e5f-446f-a7a7-e8f6d7925967', '[{"url": "https://attack.mitre.org/techniques/T1123", "external_id": "T1123", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/634.html", "external_id": "CAPEC-634", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2301}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of this technique may be difficult due to the various APIs that may be used. Telemetry data regarding API use may not be useful depending on how a system is normally used, but may provide context to other potentially malicious activity occurring on a system.\\n\\nBehavior that could indicate technique use include an unknown or unusual process accessing APIs associated with devices or software that interact with the microphone, recording devices, or recording software, and a process periodically writing files to disk that contain audio data.\"", "Str": "Detection of this technique may be difficult due to the various APIs that may be used. Telemetry data regarding API use may not be useful depending on how a system is normally used, but may provide context to other potentially malicious activity occurring on a system.\n\nBehavior that could indicate technique use include an unknown or unusual process accessing APIs associated with devices or software that interact with the microphone, recording devices, or recording software, and a process periodically writing files to disk that contain audio data.", "Type": 3, "Index": 1544}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2223}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1440}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2133}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2176}}', 'T1123', 'An adversary can leverage a computer''s peripheral devices (e.g., microphones and webcams) or applications (e.g., voice and video call services) to capture audio recordings for the purpose of listening into sensitive conversations to gather information.
INSERT INTO public.attack_patterns VALUES (35, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.576995+00', NULL, 'attack-pattern--30208d3e-0d6b-43c8-883e-44462a514619', '[{"url": "https://attack.mitre.org/techniques/T1119", "external_id": "T1119", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2903}, "x_mitre_detection": {"Num": 0, "Raw": "\"Depending on the method used, actions could include common file system commands and parameters on the command-line interface within batch files or scripts. A sequence of actions like this may be unusual, depending on the system and network environment. Automated collection may occur along with other techniques such as [Data Staged](https://attack.mitre.org/techniques/T1074). As such, file access monitoring that shows an unusual process performing sequential file opens and potentially copy actions to another location on the file system for many files at once may indicate automated collection behavior. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Depending on the method used, actions could include common file system commands and parameters on the command-line interface within batch files or scripts. A sequence of actions like this may be unusual, depending on the system and network environment. Automated collection may occur along with other techniques such as [Data Staged](https://attack.mitre.org/techniques/T1074). As such, file access monitoring that shows an unusual process performing sequential file opens and potentially copy actions to another location on the file system for many files at once may indicate automated collection behavior. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1640}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2693}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1510}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2603}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Permissions to access directories and files that store information of interest.\"\n    ]", "Str": "", "Type": 5, "Index": 2783}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2646}}', 'T1119', 'Once established within a system or network, an adversary may use automated techniques for collecting internal data. Methods for performing this technique could include use of a [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059) to search for and copy information fitting set criteria such as file type, location, or name at specific time intervals. This functionality could also be built into remote access tools. 
INSERT INTO public.attack_patterns VALUES (36, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.626148+00', NULL, 'attack-pattern--774a3188-6ba9-4dc4-879d-d54ee48a5ce9', '[{"url": "https://attack.mitre.org/techniques/T1020", "external_id": "T1020", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2004}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process file access patterns and network behavior. Unrecognized processes or scripts that appear to be traversing file systems and sending network traffic may be suspicious.\"", "Str": "Monitor process file access patterns and network behavior. Unrecognized processes or scripts that appear to be traversing file systems and sending network traffic may be suspicious.", "Type": 3, "Index": 1619}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 1909}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ExtraHop\"\n    ]", "Str": "", "Type": 5, "Index": 1284}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Script: Script Execution\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 1338}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1835}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1878}}', 'T1020', 'Adversaries may exfiltrate data, such as sensitive documents, through the use of automated processing after being gathered during Collection. 
INSERT INTO public.attack_patterns VALUES (39, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.714592+00', NULL, 'attack-pattern--be055942-6e63-49d7-9fa1-9cb7d8a8f3f4', '[{"url": "https://attack.mitre.org/techniques/T1102/002", "external_id": "T1102.002", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3284}, "x_mitre_detection": {"Num": 0, "Raw": "\"Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)\"", "Str": "Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2503}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3206}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2319}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3117}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3159}}', 'T1102.002', 'Adversaries may use an existing, legitimate external Web service as a means for sending commands to and receiving output from a compromised system over the Web service channel. Compromised systems may leverage popular websites and social media to host command and control (C2) instructions. Those infected systems can then send the output from those commands back over that Web service channel. The return traffic may occur in a variety of ways, depending on the Web service being utilized. For example, the return traffic may take the form of the compromised system posting a comment on a forum, issuing a pull request to development project, updating a document hosted on a Web service, or by sending a Tweet. 
INSERT INTO public.attack_patterns VALUES (40, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.759589+00', NULL, 'attack-pattern--5bfccc3f-2326-4112-86cc-c1ece9d8a2b5', '[{"url": "https://attack.mitre.org/techniques/T1027/001", "external_id": "T1027.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/572.html", "external_id": "CAPEC-572", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/655.html", "external_id": "CAPEC-655", "source_name": "capec"}, {"url": "https://www.welivesecurity.com/2018/03/13/oceanlotus-ships-new-backdoor/", "description": "Foltýn, T. (2018, March 13). OceanLotus ships new backdoor using old tricks. Retrieved May 22, 2018.", "source_name": "ESET OceanLotus"}, {"url": "https://securelist.com/old-malware-tricks-to-bypass-detection-in-the-age-of-big-data/78010/", "description": "Ishimaru, S.. (2017, April 13). Old Malware Tricks To Bypass Detection in the Age of Big Data. Retrieved May 30, 2019.", "source_name": "Securelist Malware Tricks April 2017"}, {"url": "https://www.virustotal.com/en/faq/", "description": "VirusTotal. (n.d.). VirusTotal FAQ. Retrieved May 23, 2019.", "source_name": "VirusTotal FAQ"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3802}, "x_mitre_detection": {"Num": 0, "Raw": "\"Depending on the method used to pad files, a file-based signature may be capable of detecting padding using a scanning or on-access based tool.  When executed, the resulting process from padded files may also exhibit other behavior characteristics of being used to conduct an intrusion such as system and network information Discovery or Lateral Movement, which could be used as event indicators that point to the source file. \"", "Str": "Depending on the method used to pad files, a file-based signature may be capable of detecting padding using a scanning or on-access based tool.  When executed, the resulting process from padded files may also exhibit other behavior characteristics of being used to conduct an intrusion such as system and network information Discovery or Lateral Movement, which could be used as event indicators that point to the source file. ", "Type": 3, "Index": 3231}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3724}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Martin Jirkal, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2981}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\",\n      \"File: File Content\"\n    ]", "Str": "", "Type": 5, "Index": 3046}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3693}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Signature-based detection\"\n    ]", "Str": "", "Type": 5, "Index": 3143}}', 'T1027.001', 'Adversaries may use binary padding to add junk data and change the on-disk representation of malware. This can be done without affecting the functionality or behavior of a binary, but can increase the size of the binary beyond what some security tools are capable of handling due to file size limitations. 
INSERT INTO public.attack_patterns VALUES (41, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.812262+00', NULL, 'attack-pattern--1ecb2399-e8ba-4f6b-8ba7-5c27d49405cf', '[{"url": "https://attack.mitre.org/techniques/T1547", "external_id": "T1547", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/564.html", "external_id": "CAPEC-564", "source_name": "capec"}, {"url": "http://msdn.microsoft.com/en-us/library/aa376977", "description": "Microsoft. (n.d.). Run and RunOnce Registry Keys. Retrieved November 12, 2014.", "source_name": "Microsoft Run Key"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/aa374733.aspx", "description": "Microsoft. (n.d.). Authentication Packages. Retrieved March 1, 2017.", "source_name": "MSDN Authentication Packages"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms725475.aspx", "description": "Microsoft. (n.d.). Time Provider. Retrieved March 26, 2018.", "source_name": "Microsoft TimeProvider"}, {"url": "https://blog.cylance.com/windows-registry-persistence-part-2-the-run-keys-and-search-order", "description": "Langendorf, S. (2013, September 24). Windows Registry Persistence, Part 2: The Run Keys and Search-Order. Retrieved April 11, 2018.", "source_name": "Cylance Reg Persistence Sept 2013"}, {"url": "https://www.tldp.org/LDP/lkmpg/2.4/lkmpg.pdf", "description": "Pomerantz, O., Salzman, P.. (2003, April 4). The Linux Kernel Module Programming Guide. Retrieved April 6, 2018.", "source_name": "Linux Kernel Programming"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5616}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for additions or modifications of mechanisms that could be used to trigger autostart execution, such as relevant additions to the Registry. Look for changes that are not correlated with known updates, patches, or other planned administrative activity. Tools such as Sysinternals Autoruns may also be used to detect system autostart configuration changes that could be attempts at persistence.(Citation: TechNet Autoruns)  Changes to some autostart configuration settings may happen under normal conditions when legitimate software is installed. \\n\\nSuspicious program execution as autostart programs may show up as outlier processes that have not been seen before when compared against historical data.To increase confidence of malicious activity, data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\\n\\nMonitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Look for abnormal process behavior that may be due to a process loading a malicious DLL.\\n\\nMonitor for abnormal usage of utilities and command-line parameters involved in kernel modification or driver installation.\"", "Str": "Monitor for additions or modifications of mechanisms that could be used to trigger autostart execution, such as relevant additions to the Registry. Look for changes that are not correlated with known updates, patches, or other planned administrative activity. Tools such as Sysinternals Autoruns may also be used to detect system autostart configuration changes that could be attempts at persistence.(Citation: TechNet Autoruns)  Changes to some autostart configuration settings may happen under normal conditions when legitimate software is installed. \n\nSuspicious program execution as autostart programs may show up as outlier processes that have not been seen before when compared against historical data.To increase confidence of malicious activity, data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\n\nMonitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Look for abnormal process behavior that may be due to a process loading a malicious DLL.\n\nMonitor for abnormal usage of utilities and command-line parameters involved in kernel modification or driver installation.", "Type": 3, "Index": 4016}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5538}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Modification\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Kernel: Kernel Module Load\",\n      \"Driver: Driver Load\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3603}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5411}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 5454}}', 'T1547', 'Adversaries may configure system settings to automatically execute a program during system boot or logon to maintain persistence or gain higher-level privileges on compromised systems. Operating systems may have mechanisms for automatically running a program on system boot or account logon.(Citation: Microsoft Run Key)(Citation: MSDN Authentication Packages)(Citation: Microsoft TimeProvider)(Citation: Cylance Reg Persistence Sept 2013)(Citation: Linux Kernel Programming)  These mechanisms may include automatically executing programs that are placed in specially designated directories or are referenced by repositories that store configuration information, such as the Windows Registry. An adversary may achieve the same goal by modifying or extending features of the kernel.
INSERT INTO public.attack_patterns VALUES (168, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.902138+00', NULL, 'attack-pattern--232a7e42-cd6e-4902-8fe9-2960f529dd4d', '[{"url": "https://attack.mitre.org/techniques/T1559/002", "external_id": "T1559.002", "source_name": "mitre-attack"}, {"url": "https://www.bleepingcomputer.com/news/microsoft/microsoft-disables-dde-feature-in-word-to-prevent-further-malware-attacks/", "description": "Cimpanu, C. (2017, December 15). Microsoft Disables DDE Feature in Word to Prevent Further Malware Attacks. Retrieved December 19, 2017.", "source_name": "BleepingComputer DDE Disabled in Word Dec 2017"}, {"url": "https://portal.msrc.microsoft.com/security-guidance/advisory/ADV170021", "description": "Microsoft. (2017, December 12). ADV170021 - Microsoft Office Defense in Depth Update. Retrieved February 3, 2018.", "source_name": "Microsoft ADV170021 Dec 2017"}, {"url": "https://technet.microsoft.com/library/security/4053440", "description": "Microsoft. (2017, November 8). Microsoft Security Advisory 4053440 - Securely opening Microsoft Office documents that contain Dynamic Data Exchange (DDE) fields. Retrieved November 21, 2017.", "source_name": "Microsoft DDE Advisory Nov 2017"}, {"url": "https://sensepost.com/blog/2016/powershell-c-sharp-and-dde-the-power-within/", "description": "El-Sherei, S. (2016, May 20). PowerShell, C-Sharp and DDE The Power Within. Retrieved November 22, 2017.", "source_name": "SensePost PS DDE May 2016"}, {"url": "https://www.contextis.com/blog/comma-separated-vulnerabilities", "description": "Kettle, J. (2014, August 29). Comma Separated Vulnerabilities. Retrieved November 22, 2017.", "source_name": "Kettle CSV DDE Aug 2014"}, {"url": "https://posts.specterops.io/reviving-dde-using-onenote-and-excel-for-code-execution-d7226864caee", "description": "Nelson, M. (2018, January 29). Reviving DDE: Using OneNote and Excel for Code Execution. Retrieved February 3, 2018.", "source_name": "Enigma Reviving DDE Jan 2018"}, {"url": "https://sensepost.com/blog/2017/macro-less-code-exec-in-msword/", "description": "Stalmans, E., El-Sherei, S. (2017, October 9). Macro-less Code Exec in MSWord. Retrieved November 21, 2017.", "source_name": "SensePost MacroLess DDE Oct 2017"}, {"url": "https://blog.nviso.be/2017/10/11/detecting-dde-in-ms-office-documents/", "description": "NVISO Labs. (2017, October 11). Detecting DDE in MS Office documents. Retrieved November 21, 2017.", "source_name": "NVisio Labs DDE Detection Oct 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5644}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for abnormal behavior indicative of DDE abuse, such as Microsoft Office applications loading DLLs and other modules not typically associated with the application or these applications spawning unusual processes (such as cmd.exe).\\n\\nOLE and Office Open XML files can be scanned for ‘DDEAUTO'', ‘DDE’, and other strings indicative of DDE execution.(Citation: NVisio Labs DDE Detection Oct 2017)\"", "Str": "Monitor processes for abnormal behavior indicative of DDE abuse, such as Microsoft Office applications loading DLLs and other modules not typically associated with the application or these applications spawning unusual processes (such as cmd.exe).\n\nOLE and Office Open XML files can be scanned for ‘DDEAUTO'', ‘DDE’, and other strings indicative of DDE execution.(Citation: NVisio Labs DDE Detection Oct 2017)", "Type": 3, "Index": 5056}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5596}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4925}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5507}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5549}}', 'T1559.002', 'Adversaries may use Windows Dynamic Data Exchange (DDE) to execute arbitrary commands. DDE is a client-server protocol for one-time and/or continuous inter-process communication (IPC) between applications. Once a link is established, applications can autonomously exchange transactions consisting of strings, warm data links (notifications when a data item changes), hot data links (duplications of changes to a data item), and requests for command execution.
INSERT INTO public.attack_patterns VALUES (43, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.004009+00', NULL, 'attack-pattern--1b7b1806-7746-41a1-a35d-e48dae25ddba', '[{"url": "https://attack.mitre.org/techniques/T1542/003", "external_id": "T1542.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/552.html", "external_id": "CAPEC-552", "source_name": "capec"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/current-threats/pdfs/rpt-mtrends-2016.pdf", "description": "Mandiant. (2016, February 25). Mandiant M-Trends 2016. Retrieved March 5, 2019.", "source_name": "Mandiant M Trends 2016"}, {"url": "http://www.symantec.com/connect/blogs/are-mbr-infections-back-fashion", "description": "Lau, H. (2011, August 8). Are MBR Infections Back in Fashion? (Infographic). Retrieved November 13, 2014.", "source_name": "Lau 2011"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3220}, "x_mitre_detection": {"Num": 0, "Raw": "\"Perform integrity checking on MBR and VBR. Take snapshots of MBR and VBR and compare against known good samples. Report changes to MBR and VBR as they occur for indicators of suspicious activity and further analysis.\"", "Str": "Perform integrity checking on MBR and VBR. Take snapshots of MBR and VBR and compare against known good samples. Report changes to MBR and VBR as they occur for indicators of suspicious activity and further analysis.", "Type": 3, "Index": 2792}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3157}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Drive: Drive Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2596}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3043}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host intrusion prevention systems\",\n      \"Anti-virus\",\n      \"File monitoring\"\n    ]", "Str": "", "Type": 5, "Index": 2671}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3085}}', 'T1542.003', 'Adversaries may use bootkits to persist on systems. Bootkits reside at a layer below the operating system and may make it difficult to perform full remediation unless an organization suspects one was used and can act accordingly.
INSERT INTO public.attack_patterns VALUES (44, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.042992+00', NULL, 'attack-pattern--31225cd3-cd46-4575-b287-c2c14011c074', '[{"url": "https://attack.mitre.org/techniques/T1583/005", "external_id": "T1583.005", "source_name": "mitre-attack"}, {"url": "https://us.norton.com/internetsecurity-malware-what-is-a-botnet.html", "description": "Norton. (n.d.). What is a botnet?. Retrieved October 4, 2020.", "source_name": "Norton Botnet"}, {"url": "https://www.imperva.com/learn/ddos/booters-stressers-ddosers/", "description": "Imperva. (n.d.). Booters, Stressers and DDoSers. Retrieved October 4, 2020.", "source_name": "Imperva DDoS for Hire"}, {"url": "https://krebsonsecurity.com/2017/01/who-is-anna-senpai-the-mirai-worm-author/", "description": "Brian Krebs. (2017, January 18). Who is Anna-Senpai, the Mirai Worm Author?. Retrieved May 15, 2017.", "source_name": "Krebs-Anna"}, {"url": "https://krebsonsecurity.com/2016/10/hackforums-shutters-booter-service-bazaar/", "description": "Brian Krebs. (2016, October 31). Hackforums Shutters Booter Service Bazaar. Retrieved May 15, 2017.", "source_name": "Krebs-Bazaar"}, {"url": "https://krebsonsecurity.com/2016/10/are-the-days-of-booter-services-numbered/", "description": "Brian Krebs. (2016, October 27). Are the Days of “Booter” Services Numbered?. Retrieved May 15, 2017.", "source_name": "Krebs-Booter"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3306}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during [Phishing](https://attack.mitre.org/techniques/T1566), [Endpoint Denial of Service](https://attack.mitre.org/techniques/T1499), or [Network Denial of Service](https://attack.mitre.org/techniques/T1498).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during [Phishing](https://attack.mitre.org/techniques/T1566), [Endpoint Denial of Service](https://attack.mitre.org/techniques/T1499), or [Network Denial of Service](https://attack.mitre.org/techniques/T1498).", "Type": 3, "Index": 2766}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3262}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3231}}', 'T1583.005', 'Adversaries may buy, lease, or rent a network of compromised systems that can be used during targeting. A botnet is a network of compromised systems that can be instructed to perform coordinated tasks.(Citation: Norton Botnet) Adversaries may purchase a subscription to use an existing botnet from a booter/stresser service. With a botnet at their disposal, adversaries may perform follow-on activity such as large-scale [Phishing](https://attack.mitre.org/techniques/T1566) or Distributed Denial of Service (DDoS).(Citation: Imperva DDoS for Hire)(Citation: Krebs-Anna)(Citation: Krebs-Bazaar)(Citation: Krebs-Booter)', 'Botnet', 10);
INSERT INTO public.attack_patterns VALUES (45, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.082146+00', NULL, 'attack-pattern--810d8072-afb6-4a56-9ee7-86379ac4a6f3', '[{"url": "https://attack.mitre.org/techniques/T1584/005", "external_id": "T1584.005", "source_name": "mitre-attack"}, {"url": "https://us.norton.com/internetsecurity-malware-what-is-a-botnet.html", "description": "Norton. (n.d.). What is a botnet?. Retrieved October 4, 2020.", "source_name": "Norton Botnet"}, {"url": "https://www.imperva.com/learn/ddos/booters-stressers-ddosers/", "description": "Imperva. (n.d.). Booters, Stressers and DDoSers. Retrieved October 4, 2020.", "source_name": "Imperva DDoS for Hire"}, {"url": "https://www.secureworks.com/research/dridex-bugat-v5-botnet-takeover-operation", "description": "Dell SecureWorks Counter Threat Unit Threat Intelligence. (2015, October 13). Dridex (Bugat v5) Botnet Takeover Operation. Retrieved May 31, 2019.", "source_name": "Dell Dridex Oct 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2950}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during [Phishing](https://attack.mitre.org/techniques/T1566), [Endpoint Denial of Service](https://attack.mitre.org/techniques/T1499), or [Network Denial of Service](https://attack.mitre.org/techniques/T1498).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during [Phishing](https://attack.mitre.org/techniques/T1566), [Endpoint Denial of Service](https://attack.mitre.org/techniques/T1499), or [Network Denial of Service](https://attack.mitre.org/techniques/T1498).", "Type": 3, "Index": 2410}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2906}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2875}}', 'T1584.005', 'Adversaries may compromise numerous third-party systems to form a botnet that can be used during targeting. A botnet is a network of compromised systems that can be instructed to perform coordinated tasks.(Citation: Norton Botnet) Instead of purchasing/renting a botnet from a booter/stresser service(Citation: Imperva DDoS for Hire), adversaries may build their own botnet by compromising numerous third-party systems. Adversaries may also conduct a takeover of an existing botnet, such as redirecting bots to adversary-controlled C2 servers.(Citation: Dell Dridex Oct 2015) With a botnet at their disposal, adversaries may perform follow-on activity such as large-scale [Phishing](https://attack.mitre.org/techniques/T1566) or Distributed Denial of Service (DDoS).', 'Botnet', 84);
INSERT INTO public.attack_patterns VALUES (46, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.118039+00', NULL, 'attack-pattern--5e4a2073-9643-44cb-a0b5-e7f4048446c7', '[{"url": "https://attack.mitre.org/techniques/T1217", "external_id": "T1217", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2669}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to gather browser bookmark information. Remote access tools with built-in features may interact directly using APIs to gather information. Information may also be acquired through system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nSystem and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Collection and Exfiltration, based on the information obtained.\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to gather browser bookmark information. Remote access tools with built-in features may interact directly using APIs to gather information. Information may also be acquired through system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nSystem and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Collection and Exfiltration, based on the information obtained.", "Type": 3, "Index": 1716}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2591}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Mike Kemmerer\"\n    ]", "Str": "", "Type": 5, "Index": 1526}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 1585}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2501}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2544}}', 'T1217', 'Adversaries may enumerate browser bookmarks to learn more about compromised hosts. Browser bookmarks may reveal personal information about users (ex: banking sites, interests, social media, etc.) as well as details about internal network resources such as servers, tools/dashboards, or other related infrastructure.
INSERT INTO public.attack_patterns VALUES (49, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.302693+00', NULL, 'attack-pattern--800f9819-7007-4540-a520-40e655876800', '[{"url": "https://attack.mitre.org/techniques/T1612", "external_id": "T1612", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/engine/api/v1.41/#operation/ImageBuild", "description": "Docker. ( null). Docker Engine API v1.41 Reference - Build an Image. Retrieved March 30, 2021.", "source_name": "Docker Build Image"}, {"url": "https://blog.aquasec.com/malicious-container-image-docker-container-host", "description": "Assaf Morag. (2020, July 15). Threat Alert: Attackers Building Malicious Images on Your Hosts. Retrieved March 29, 2021.", "source_name": "Aqua Build Images on Hosts"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3342}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unexpected Docker image build requests to the Docker daemon on hosts in the environment. Additionally monitor for subsequent network communication with anomalous IPs that have never been seen before in the environment that indicate the download of malicious code.\"", "Str": "Monitor for unexpected Docker image build requests to the Docker daemon on hosts in the environment. Additionally monitor for subsequent network communication with anomalous IPs that have never been seen before in the environment that indicate the download of malicious code.", "Type": 3, "Index": 2877}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3291}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Assaf Morag, @MoragAssaf, Team Nautilus Aqua Security\",\n      \"Roi Kol, @roykol1, Team Nautilus Aqua Security\",\n      \"Michael Katchinskiy, @michael64194968, Team Nautilus Aqua Security\",\n      \"Vishwas Manral, McAfee\"\n    ]", "Str": "", "Type": 5, "Index": 2399}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Image: Image Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2662}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3187}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3230}}', 'T1612', 'Adversaries may build a container image directly on a host to bypass defenses that monitor for the retrieval of malicious images from a public registry. A remote <code>build</code> request may be sent to the Docker API that includes a Dockerfile that pulls a vanilla base image, such as alpine, from a public or local registry and then builds a custom image upon it.(Citation: Docker Build Image)
INSERT INTO public.attack_patterns VALUES (50, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.344467+00', NULL, 'attack-pattern--6ee2dc99-91ad-4534-a7d8-a649358c331f', '[{"url": "https://attack.mitre.org/techniques/T1591/002", "external_id": "T1591.002", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/broadvoice-leaks-350m-records-voicemail-transcripts/160158/", "description": "Seals, T. (2020, October 15). Broadvoice Leak Exposes 350M Records, Personal Voicemail Transcripts. Retrieved October 20, 2020.", "source_name": "ThreatPost Broadvoice Leak"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3211}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2770}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3167}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3136}}', 'T1591.002', 'Adversaries may gather information about the victim''s business relationships that can be used during targeting. Information about an organization’s business relationships may include a variety of details, including second or third-party organizations/domains (ex: managed service providers, contractors, etc.) that have connected (and potentially elevated) network access. This information may also reveal supply chains and shipment paths for the victim’s hardware and software resources.
INSERT INTO public.attack_patterns VALUES (54, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.459982+00', NULL, 'attack-pattern--ffeb0780-356e-4261-b036-cfb6bd234335', '[{"url": "https://attack.mitre.org/techniques/T1574/012", "external_id": "T1574.012", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/dotnet/framework/unmanaged-api/profiling/profiling-overview", "description": "Microsoft. (2017, March 30). Profiling Overview. Retrieved June 24, 2020.", "source_name": "Microsoft Profiling Mar 2017"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/dotnet/netframework-4.0/ee471451(v=vs.100)", "description": "Microsoft. (2013, February 4). Registry-Free Profiler Startup and Attach. Retrieved June 24, 2020.", "source_name": "Microsoft COR_PROFILER Feb 2013"}, {"url": "https://redcanary.com/blog/blue-mockingbird-cryptominer/", "description": "Lambert, T. (2020, May 7). Introducing Blue Mockingbird. Retrieved May 26, 2020.", "source_name": "RedCanary Mockingbird May 2020"}, {"url": "https://redcanary.com/blog/cor_profiler-for-persistence/", "description": "Brown, J. (2020, May 7). Detecting COR_PROFILER manipulation for persistence. Retrieved June 24, 2020.", "source_name": "Red Canary COR_PROFILER May 2020"}, {"url": "https://offsec.almond.consulting/UAC-bypass-dotnet.html", "description": "Almond. (2019, April 30). UAC bypass via elevated .NET applications. Retrieved June 24, 2020.", "source_name": "Almond COR_PROFILER Apr 2019"}, {"url": "https://github.com/OmerYa/Invisi-Shell", "description": "Yair, O. (2019, August 19). Invisi-Shell. Retrieved June 24, 2020.", "source_name": "GitHub OmerYa Invisi-Shell"}, {"url": "https://web.archive.org/web/20170720041203/http://subt0x10.blogspot.com/2017/05/subvert-clr-process-listing-with-net.html", "description": "Smith, C. (2017, May 18). Subvert CLR Process Listing With .NET Profilers. Retrieved June 24, 2020.", "source_name": "subTee .NET Profilers May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6236}, "x_mitre_detection": {"Num": 0, "Raw": "\"For detecting system and user scope abuse of the COR_PROFILER, monitor the Registry for changes to COR_ENABLE_PROFILING, COR_PROFILER, and COR_PROFILER_PATH that correspond to system and user environment variables that do not correlate to known developer tools. Extra scrutiny should be placed on suspicious modification of these Registry keys by command line tools like wmic.exe, setx.exe, and [Reg](https://attack.mitre.org/software/S0075), monitoring for command-line arguments indicating a change to COR_PROFILER variables may aid in detection. For system, user, and process scope abuse of the COR_PROFILER, monitor for new suspicious unmanaged profiling DLLs loading into .NET processes shortly after the CLR causing abnormal process behavior.(Citation: Red Canary COR_PROFILER May 2020) Consider monitoring for DLL files that are associated with COR_PROFILER environment variables.\"", "Str": "For detecting system and user scope abuse of the COR_PROFILER, monitor the Registry for changes to COR_ENABLE_PROFILING, COR_PROFILER, and COR_PROFILER_PATH that correspond to system and user environment variables that do not correlate to known developer tools. Extra scrutiny should be placed on suspicious modification of these Registry keys by command line tools like wmic.exe, setx.exe, and [Reg](https://attack.mitre.org/software/S0075), monitoring for command-line arguments indicating a change to COR_PROFILER variables may aid in detection. For system, user, and process scope abuse of the COR_PROFILER, monitor for new suspicious unmanaged profiling DLLs loading into .NET processes shortly after the CLR causing abnormal process behavior.(Citation: Red Canary COR_PROFILER May 2020) Consider monitoring for DLL files that are associated with COR_PROFILER environment variables.", "Type": 3, "Index": 5154}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6188}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jesse Brown, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 4891}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4960}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6076}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6118}}', 'T1574.012', 'Adversaries may leverage the COR_PROFILER environment variable to hijack the execution flow of programs that load the .NET CLR. The COR_PROFILER is a .NET Framework feature which allows developers to specify an unmanaged (or external of .NET) profiling DLL to be loaded into each .NET process that loads the Common Language Runtime (CLR). These profiliers are designed to monitor, troubleshoot, and debug managed code executed by the .NET CLR.(Citation: Microsoft Profiling Mar 2017)(Citation: Microsoft COR_PROFILER Feb 2013)
INSERT INTO public.attack_patterns VALUES (56, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.550208+00', NULL, 'attack-pattern--98034fef-d9fb-4667-8dc4-2eab6231724c', '[{"url": "https://attack.mitre.org/techniques/T1546/001", "external_id": "T1546.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/556.html", "external_id": "CAPEC-556", "source_name": "capec"}, {"url": "https://support.microsoft.com/en-us/help/18539/windows-7-change-default-programs", "description": "Microsoft. (n.d.). Change which programs Windows 7 uses by default. Retrieved July 26, 2016.", "source_name": "Microsoft Change Default Programs"}, {"url": "http://msdn.microsoft.com/en-us/library/bb166549.aspx", "description": "Microsoft. (n.d.). Specifying File Handlers for File Name Extensions. Retrieved November 13, 2014.", "source_name": "Microsoft File Handlers"}, {"url": "https://docs.microsoft.com/windows-server/administration/windows-commands/assoc", "description": "Plett, C. et al.. (2017, October 15). assoc. Retrieved August 7, 2018.", "source_name": "Microsoft Assoc Oct 2017"}, {"url": "https://www.trendmicro.com/vinfo/us/threat-encyclopedia/malware/troj_fakeav.gzd", "description": "Sioting, S. (2012, October 8). TROJ_FAKEAV.GZD. Retrieved August 8, 2018.", "source_name": "TrendMicro TROJ-FAKEAV OCT 2012"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4856}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect and analyze changes to Registry keys that associate file extensions to default applications for execution and correlate with unknown process launch activity or unusual file types for that process.\\n\\nUser file association preferences are stored under <code> [HKEY_CURRENT_USER]\\\\Software\\\\Microsoft\\\\Windows\\\\CurrentVersion\\\\Explorer\\\\FileExts</code> and override associations configured under <code>[HKEY_CLASSES_ROOT]</code>. Changes to a user''s preference will occur under this entry''s subkeys.\\n\\nAlso look for abnormal process call trees for execution of other commands that could relate to Discovery actions or other techniques.\"", "Str": "Collect and analyze changes to Registry keys that associate file extensions to default applications for execution and correlate with unknown process launch activity or unusual file types for that process.\n\nUser file association preferences are stored under <code> [HKEY_CURRENT_USER]\\Software\\Microsoft\\Windows\\CurrentVersion\\Explorer\\FileExts</code> and override associations configured under <code>[HKEY_CLASSES_ROOT]</code>. Changes to a user''s preference will occur under this entry''s subkeys.\n\nAlso look for abnormal process call trees for execution of other commands that could relate to Discovery actions or other techniques.", "Type": 3, "Index": 4003}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4808}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 3746}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3838}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4680}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4722}}', 'T1546.001', 'Adversaries may establish persistence by executing malicious content triggered by a file type association. When a file is opened, the default program used to open the file (also called the file association or handler) is checked. File association selections are stored in the Windows Registry and can be edited by users, administrators, or programs that have Registry access (Citation: Microsoft Change Default Programs) (Citation: Microsoft File Handlers) or by administrators using the built-in assoc utility. (Citation: Microsoft Assoc Oct 2017) Applications can modify the file association for a given file extension to call an arbitrary program when a file with the given extension is opened.
INSERT INTO public.attack_patterns VALUES (169, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.588221+00', NULL, 'attack-pattern--633a100c-b2c9-41bf-9be5-905c1b16c825', '[{"url": "https://attack.mitre.org/techniques/T1574/006", "external_id": "T1574.006", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/13.html", "external_id": "CAPEC-13", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/640.html", "external_id": "CAPEC-640", "source_name": "capec"}, {"url": "https://www.man7.org/linux/man-pages/man8/ld.so.8.html", "description": "Kerrisk, M. (2020, June 13). Linux Programmer''s Manual. Retrieved June 15, 2020.", "source_name": "Man LD.SO"}, {"url": "https://www.tldp.org/HOWTO/Program-Library-HOWTO/shared-libraries.html", "description": "The Linux Documentation Project. (n.d.). Shared Libraries. Retrieved January 31, 2020.", "source_name": "TLDP Shared Libraries"}, {"url": "https://developer.apple.com/library/archive/documentation/DeveloperTools/Conceptual/DynamicLibraries/100-Articles/OverviewOfDynamicLibraries.html", "description": "Apple Inc.. (2012, July 23). Overview of Dynamic Libraries. Retrieved March 24, 2021.", "source_name": "Apple Doco Archive Dynamic Libraries"}, {"url": "https://www.baeldung.com/linux/ld_preload-trick-what-is", "description": "baeldung. (2020, August 9). What Is the LD_PRELOAD Trick?. Retrieved March 24, 2021.", "source_name": "Baeldung LD_PRELOAD"}, {"url": "https://www.datawire.io/code-injection-on-linux-and-macos/", "description": "Itamar Turner-Trauring. (2017, April 18). “This will only hurt for a moment”: code injection on Linux and macOS with LD_PRELOAD. Retrieved December 20, 2017.", "source_name": "Code Injection on Linux and macOS"}, {"url": "http://hick.org/code/skape/papers/needle.txt", "description": "skape. (2003, January 19). Linux x86 run-time process manipulation. Retrieved December 20, 2017.", "source_name": "Uninformed Needle"}, {"url": "http://phrack.org/issues/51/8.html", "description": "halflife. (1997, September 1). Shared Library Redirection Techniques. Retrieved December 20, 2017.", "source_name": "Phrack halfdead 1997"}, {"url": "http://www.nth-dimension.org.uk/pub/BTL.pdf", "description": "Tim Brown. (2011, June 29). Breaking the links: Exploiting the linker. Retrieved March 29, 2021.", "source_name": "Brown Exploiting Linkers"}, {"url": "https://theevilbit.github.io/posts/dyld_insert_libraries_dylib_injection_in_macos_osx_deep_dive/", "description": "Fitzl, C. (2019, July 9). DYLD_INSERT_LIBRARIES DYLIB injection in macOS / OSX. Retrieved March 26, 2020.", "source_name": "TheEvilBit DYLD_INSERT_LIBRARIES"}, {"url": "https://blog.timac.org/2012/1218-simple-code-injection-using-dyld_insert_libraries/", "description": "Timac. (2012, December 18). Simple code injection using DYLD_INSERT_LIBRARIES. Retrieved March 26, 2020.", "source_name": "Timac DYLD_INSERT_LIBRARIES"}, {"url": "https://jon-gabilondo-angulo-7635.medium.com/how-to-inject-code-into-mach-o-apps-part-ii-ddb13ebc8191", "description": "Jon Gabilondo. (2019, September 22). How to Inject Code into Mach-O Apps. Part II.. Retrieved March 24, 2021.", "source_name": "Gabilondo DYLD_INSERT_LIBRARIES Catalina Bypass"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 8192}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for changes to environment variables and files associated with loading shared libraries such as <code>LD_PRELOAD</code> and <code>DYLD_INSERT_LIBRARIES</code>, as well as the commands to implement these changes.\\n\\nMonitor processes for unusual activity (e.g., a process that does not use the network begins to do so). Track library metadata, such as a hash, and compare libraries that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.\"", "Str": "Monitor for changes to environment variables and files associated with loading shared libraries such as <code>LD_PRELOAD</code> and <code>DYLD_INSERT_LIBRARIES</code>, as well as the commands to implement these changes.\n\nMonitor processes for unusual activity (e.g., a process that does not use the network begins to do so). Track library metadata, such as a hash, and compare libraries that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.", "Type": 3, "Index": 7479}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 8131}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 7284}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 8042}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 8084}}', 'T1574.006', 'Adversaries may execute their own malicious payloads by hijacking environment variables the dynamic linker uses to load shared libraries. During the execution preparation phase of a program, the dynamic linker loads specified absolute paths of shared libraries from environment variables and files, such as <code>LD_PRELOAD</code> on Linux or <code>DYLD_INSERT_LIBRARIES</code> on macOS. Libraries specified in environment variables are loaded first, taking precedence over system libraries with the same function name.(Citation: Man LD.SO)(Citation: TLDP Shared Libraries)(Citation: Apple Doco Archive Dynamic Libraries) These variables are often used by developers to debug binaries without needing to recompile, deconflict mapped symbols, and implement custom functions without changing the original library.(Citation: Baeldung LD_PRELOAD)
INSERT INTO public.attack_patterns VALUES (58, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.656435+00', NULL, 'attack-pattern--2bce5b30-7014-4a5d-ade7-12913fe6ac36', '[{"url": "https://attack.mitre.org/techniques/T1070/002", "external_id": "T1070.002", "source_name": "mitre-attack"}, {"url": "https://www.eurovps.com/blog/important-linux-log-files-you-must-be-monitoring/", "description": "Marcel. (2018, April 19). 12 Critical Linux Log Files You Must be Monitoring. Retrieved March 29, 2020.", "source_name": "Linux Logs"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2353}, "x_mitre_detection": {"Num": 0, "Raw": "\"File system monitoring may be used to detect improper deletion or modification of indicator files. Also monitor for suspicious processes interacting with log files.\"", "Str": "File system monitoring may be used to detect improper deletion or modification of indicator files. Also monitor for suspicious processes interacting with log files.", "Type": 3, "Index": 2062}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2292}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Deletion\",\n      \"File: File Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1931}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2261}}', 'T1070.002', 'Adversaries may clear system logs to hide evidence of an intrusion. macOS and Linux both keep track of system or user-initiated actions via system logs. The majority of native system logging is stored under the <code>/var/log/</code> directory. Subfolders in this directory categorize logs by their related functions, such as:(Citation: Linux Logs)
INSERT INTO public.attack_patterns VALUES (59, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.694531+00', NULL, 'attack-pattern--6495ae23-3ab4-43c5-a94f-5638a2c31fd2', '[{"url": "https://attack.mitre.org/techniques/T1070/001", "external_id": "T1070.001", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/windows-server/administration/windows-commands/wevtutil", "description": "Plett, C. et al.. (2017, October 16). wevtutil. Retrieved July 2, 2018.", "source_name": "Microsoft wevtutil Oct 2017"}, {"url": "https://msdn.microsoft.com/library/system.diagnostics.eventlog.clear.aspx", "description": "Microsoft. (n.d.). EventLog.Clear Method (). Retrieved July 2, 2018.", "source_name": "Microsoft EventLog.Clear"}, {"url": "https://docs.microsoft.com/powershell/module/microsoft.powershell.management/clear-eventlog", "description": "Microsoft. (n.d.). Clear-EventLog. Retrieved July 2, 2018.", "source_name": "Microsoft Clear-EventLog"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3150}, "x_mitre_detection": {"Num": 0, "Raw": "\"Deleting Windows event logs (via native binaries (Citation: Microsoft wevtutil Oct 2017), API functions (Citation: Microsoft EventLog.Clear), or [PowerShell](https://attack.mitre.org/techniques/T1059/001) (Citation: Microsoft Clear-EventLog)) may also generate an alterable event (Event ID 1102: \\\"The audit log was cleared\\\").\"", "Str": "Deleting Windows event logs (via native binaries (Citation: Microsoft wevtutil Oct 2017), API functions (Citation: Microsoft EventLog.Clear), or [PowerShell](https://attack.mitre.org/techniques/T1059/001) (Citation: Microsoft Clear-EventLog)) may also generate an alterable event (Event ID 1102: \"The audit log was cleared\").", "Type": 3, "Index": 2523}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2983}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2294}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2885}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti Virus\",\n      \"Host Intrusion Prevention Systems\",\n      \"Log Analysis\"\n    ]", "Str": "", "Type": 5, "Index": 2405}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Clearing the Windows event logs requires Administrator permissions\"\n    ]", "Str": "", "Type": 5, "Index": 3043}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2927}}', 'T1070.001', 'Adversaries may clear Windows Event Logs to hide the activity of an intrusion. Windows Event Logs are a record of a computer''s alerts and notifications. There are three system-defined sources of events: System, Application, and Security, with five event types: Error, Warning, Information, Success Audit, and Failure Audit.
INSERT INTO public.attack_patterns VALUES (60, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.757345+00', NULL, 'attack-pattern--774ad5bb-2366-4c13-a8a9-65e50b292e7c', '[{"url": "https://attack.mitre.org/techniques/T1592/004", "external_id": "T1592.004", "source_name": "mitre-attack"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3167}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2726}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3123}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3092}}', 'T1592.004', 'Adversaries may gather information about the victim''s client configurations that can be used during targeting. Information about client configurations may include a variety of details and settings, including operating system/version, virtualization, architecture (ex: 32 or 64 bit), language, and/or time zone.
INSERT INTO public.attack_patterns VALUES (61, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.800451+00', NULL, 'attack-pattern--30973a08-aed9-4edf-8604-9084ce1b5c4f', '[{"url": "https://attack.mitre.org/techniques/T1115", "external_id": "T1115", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/637.html", "external_id": "CAPEC-637", "source_name": "capec"}, {"url": "https://msdn.microsoft.com/en-us/library/ms649012", "description": "Microsoft. (n.d.). About the Clipboard. Retrieved March 29, 2016.", "source_name": "MSDN Clipboard"}, {"url": "https://medium.com/rvrsh3ll/operating-with-empyre-ea764eda3363", "description": "rvrsh3ll. (2016, May 18). Operating with EmPyre. Retrieved July 12, 2017.", "source_name": "Operating with EmPyre"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2273}, "x_mitre_detection": {"Num": 0, "Raw": "\"Access to the clipboard is a legitimate function of many applications on an operating system. If an organization chooses to monitor for this behavior, then the data will likely need to be correlated against other suspicious or non-user-driven activity.\"", "Str": "Access to the clipboard is a legitimate function of many applications on an operating system. If an organization chooses to monitor for this behavior, then the data will likely need to be correlated against other suspicious or non-user-driven activity.", "Type": 3, "Index": 1876}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2195}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1772}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2163}}', 'T1115', 'Adversaries may collect data stored in the clipboard from users copying information within or between applications. 
INSERT INTO public.attack_patterns VALUES (64, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.932235+00', NULL, 'attack-pattern--f232fa7a-025c-4d43-abc7-318e81a73d65', '[{"url": "https://attack.mitre.org/techniques/T1078/004", "external_id": "T1078.004", "source_name": "mitre-attack"}, {"url": "https://aws.amazon.com/identity/federation/", "description": "Amazon. (n.d.). Identity Federation in AWS. Retrieved March 13, 2020.", "source_name": "AWS Identity Federation"}, {"url": "https://cloud.google.com/solutions/federating-gcp-with-active-directory-introduction", "description": "Google. (n.d.). Federating Google Cloud with Active Directory. Retrieved March 13, 2020.", "source_name": "Google Federating GC"}, {"url": "https://docs.microsoft.com/en-us/windows-server/identity/ad-fs/deployment/how-to-connect-fed-azure-adfs", "description": "Microsoft. (n.d.). Deploying Active Directory Federation Services in Azure. Retrieved March 13, 2020.", "source_name": "Microsoft Deploying AD Federation"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3683}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the activity of cloud accounts to detect abnormal or malicious behavior, such as accessing information outside of the normal function of the account or account usage at atypical hours.\"", "Str": "Monitor the activity of cloud accounts to detect abnormal or malicious behavior, such as accessing information outside of the normal function of the account or account usage at atypical hours.", "Type": 3, "Index": 3221}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3560}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3090}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3448}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3490}}', 'T1078.004', 'Adversaries may obtain and abuse credentials of a cloud account as a means of gaining Initial Access, Persistence, Privilege Escalation, or Defense Evasion. Cloud accounts are those created and configured by an organization for use by users, remote support, services, or for administration of resources within a cloud service provider or SaaS application. In some cases, cloud accounts may be federated with traditional identity management system, such as Window Active Directory. (Citation: AWS Identity Federation)(Citation: Google Federating GC)(Citation: Microsoft Deploying AD Federation)
INSERT INTO public.attack_patterns VALUES (65, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.979709+00', NULL, 'attack-pattern--16e94db9-b5b1-4cd0-b851-f38fbd0a70f2', '[{"url": "https://attack.mitre.org/techniques/T1069/003", "external_id": "T1069.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/msonline/get-msolrole?view=azureadps-1.0", "description": "Microsoft. (n.d.). Get-MsolRole. Retrieved October 6, 2019.", "source_name": "Microsoft Msolrole"}, {"url": "https://github.com/True-Demon/raindance", "description": "Stringer, M.. (2018, November 21). RainDance. Retrieved October 6, 2019.", "source_name": "GitHub Raindance"}, {"url": "https://docs.microsoft.com/en-us/cli/azure/ad/user?view=azure-cli-latest", "description": "Microsoft. (n.d.). az ad user. Retrieved October 6, 2019.", "source_name": "Microsoft AZ CLI"}, {"url": "https://www.blackhillsinfosec.com/red-teaming-microsoft-part-1-active-directory-leaks-via-azure/", "description": "Felch, M.. (2018, August 31). Red Teaming Microsoft Part 1 Active Directory Leaks via Azure. Retrieved October 6, 2019.", "source_name": "Black Hills Red Teaming MS AD Azure, 2018"}, {"url": "https://cloud.google.com/identity/docs/reference/rest", "description": "Google. (n.d.). Retrieved March 16, 2021.", "source_name": "Google Cloud Identity API Documentation"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4215}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Activity and account logs for the cloud services can also be monitored for suspicious commands that are anomalous compared to a baseline of normal activity.\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Activity and account logs for the cloud services can also be monitored for suspicious commands that are anomalous compared to a baseline of normal activity.", "Type": 3, "Index": 3387}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4092}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Group: Group Enumeration\",\n      \"Group: Group Metadata\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3168}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4003}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4045}}', 'T1069.003', 'Adversaries may attempt to find cloud groups and permission settings. The knowledge of cloud permission groups can help adversaries determine the particular roles of users and groups within an environment, as well as which users are associated with a particular group.
INSERT INTO public.attack_patterns VALUES (70, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.015407+00', NULL, 'attack-pattern--32901740-b42c-4fdd-bc02-345b5dc57082', '[{"url": "https://attack.mitre.org/techniques/T1553/002", "external_id": "T1553.002", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Code_signing", "description": "Wikipedia. (2015, November 10). Code Signing. Retrieved March 31, 2016.", "source_name": "Wikipedia Code Signing"}, {"url": "https://securelist.com/why-you-shouldnt-completely-trust-files-signed-with-digital-certificates/68593/", "description": "Ladikov, A. (2015, January 29). Why You Shouldn’t Completely Trust Files Signed with Digital Certificates. Retrieved March 31, 2016.", "source_name": "Securelist Digital Certificates"}, {"url": "http://www.symantec.com/connect/blogs/how-attackers-steal-private-keys-digital-certificates", "description": "Shinotsuka, H. (2013, February 22). How Attackers Steal Private Keys from Digital Certificates. Retrieved March 31, 2016.", "source_name": "Symantec Digital Certificates"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3083}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect and analyze signing certificate metadata on software that executes within the environment to look for unusual certificate characteristics and outliers.\"", "Str": "Collect and analyze signing certificate metadata on software that executes within the environment to look for unusual certificate characteristics and outliers.", "Type": 3, "Index": 2795}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3020}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2655}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2989}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\"\n    ]", "Str": "", "Type": 5, "Index": 2724}}', 'T1553.002', 'Adversaries may create, acquire, or steal code signing materials to sign their malware or tools. Code signing provides a level of authenticity on a binary from the developer and a guarantee that the binary has not been tampered with. (Citation: Wikipedia Code Signing) The certificates used during an operation may be created, acquired, or stolen by the adversary. (Citation: Securelist Digital Certificates) (Citation: Symantec Digital Certificates) Unlike [Invalid Code Signature](https://attack.mitre.org/techniques/T1036/001), this activity will result in a valid signature.
INSERT INTO public.attack_patterns VALUES (67, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.097623+00', NULL, 'attack-pattern--19bf235b-8620-4997-b5b4-94e0659ed7c3', '[{"url": "https://attack.mitre.org/techniques/T1552/005", "external_id": "T1552.005", "source_name": "mitre-attack"}, {"url": "https://docs.aws.amazon.com/AWSEC2/latest/UserGuide/ec2-instance-metadata.html", "description": "AWS. (n.d.). Instance Metadata and User Data. Retrieved July 18, 2019.", "source_name": "AWS Instance Metadata API"}, {"url": "https://krebsonsecurity.com/2019/08/what-we-can-learn-from-the-capital-one-hack/", "description": "Krebs, B.. (2019, August 19). What We Can Learn from the Capital One Hack. Retrieved March 25, 2020.", "source_name": "Krebs Capital One August 2019"}, {"url": "https://redlock.io/blog/instance-metadata-api-a-modern-day-trojan-horse", "description": "Higashi, Michael. (2018, May 15). Instance Metadata API: A Modern Day Trojan Horse. Retrieved July 16, 2019.", "source_name": "RedLock Instance Metadata API 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3605}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor access to the Instance Metadata API and look for anomalous queries.\\n\\nIt may be possible to detect adversary use of credentials they have obtained such as in [Valid Accounts](https://attack.mitre.org/techniques/T1078).\"", "Str": "Monitor access to the Instance Metadata API and look for anomalous queries.\n\nIt may be possible to detect adversary use of credentials they have obtained such as in [Valid Accounts](https://attack.mitre.org/techniques/T1078).", "Type": 3, "Index": 3267}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3560}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 3127}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\"\n    ]", "Str": "", "Type": 5, "Index": 3183}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3529}}', 'T1552.005', 'Adversaries may attempt to access the Cloud Instance Metadata API to collect credentials and other sensitive data.
INSERT INTO public.attack_patterns VALUES (68, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.134375+00', NULL, 'attack-pattern--e49920b0-6c54-40c1-9571-73723653205f', '[{"url": "https://attack.mitre.org/techniques/T1538", "external_id": "T1538", "source_name": "mitre-attack"}, {"url": "https://cloud.google.com/security-command-center/docs/quickstart-scc-dashboard", "description": "Google. (2019, October 3). Quickstart: Using the dashboard. Retrieved October 8, 2019.", "source_name": "Google Command Center Dashboard"}, {"url": "https://docs.aws.amazon.com/awscloudtrail/latest/userguide/cloudtrail-event-reference-aws-console-sign-in-events.html", "description": "Amazon. (n.d.). AWS Console Sign-in Events. Retrieved October 23, 2019.", "source_name": "AWS Console Sign-in Events"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2759}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor account activity logs to see actions performed and activity associated with the cloud service management console. Some cloud providers, such as AWS, provide distinct log events for login attempts to the management console.(Citation: AWS Console Sign-in Events)\"", "Str": "Monitor account activity logs to see actions performed and activity associated with the cloud service management console. Some cloud providers, such as AWS, provide distinct log events for login attempts to the management console.(Citation: AWS Console Sign-in Events)", "Type": 3, "Index": 2257}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2650}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2070}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2126}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2560}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2603}}', 'T1538', 'An adversary may use a cloud service dashboard GUI with stolen credentials to gain useful information from an operational cloud environment, such as specific services, resources, and features. For example, the GCP Command Center can be used to view all assets, findings of potential security risks, and to run additional queries, such as finding public IP addresses and open ports.(Citation: Google Command Center Dashboard)
INSERT INTO public.attack_patterns VALUES (69, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.174822+00', NULL, 'attack-pattern--e24fcba8-2557-4442-a139-1ee2f2e784db', '[{"url": "https://attack.mitre.org/techniques/T1526", "external_id": "T1526", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/rest/api/resources/", "description": "Microsoft. (2019, May 20). Azure Resource Manager. Retrieved June 17, 2020.", "source_name": "Azure - Resource Manager API"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/azure/ad/graph/howto/azure-ad-graph-api-operations-overview", "description": "Microsoft. (2016, March 26). Operations overview | Graph API concepts. Retrieved June 18, 2020.", "source_name": "Azure AD Graph API"}, {"url": "https://github.com/Azure/Stormspotter", "description": "Microsoft. (2020). Azure Stormspotter GitHub. Retrieved June 17, 2020.", "source_name": "Azure - Stormspotter"}, {"url": "https://github.com/RhinoSecurityLabs/pacu", "description": "Rhino Security Labs. (2019, August 22). Pacu. Retrieved October 17, 2019.", "source_name": "GitHub Pacu"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3983}, "x_mitre_detection": {"Num": 0, "Raw": "\"Cloud service discovery techniques will likely occur throughout an operation where an adversary is targeting cloud-based systems and services. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\\n\\nNormal, benign system and network events that look like cloud service discovery may be uncommon, depending on the environment and how they are used. Monitor cloud service usage for anomalous behavior that may indicate adversarial presence within the environment.\"", "Str": "Cloud service discovery techniques will likely occur throughout an operation where an adversary is targeting cloud-based systems and services. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\n\nNormal, benign system and network events that look like cloud service discovery may be uncommon, depending on the environment and how they are used. Monitor cloud service usage for anomalous behavior that may indicate adversarial presence within the environment.", "Type": 3, "Index": 3170}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3860}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Suzy Schapperle - Microsoft Azure Red Team\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2932}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Cloud Service: Cloud Service Metadata\",\n      \"Cloud Service: Cloud Service Enumeration\"\n    ]", "Str": "", "Type": 5, "Index": 3040}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3770}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3813}}', 'T1526', 'An adversary may attempt to enumerate the cloud services running on a system after gaining access. These methods can differ from platform-as-a-service (PaaS), to infrastructure-as-a-service (IaaS), or software-as-a-service (SaaS). Many services exist throughout the various cloud providers and can include Continuous Integration and Continuous Delivery (CI/CD), Lambda Functions, Azure AD, etc. 
INSERT INTO public.attack_patterns VALUES (183, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.21828+00', NULL, 'attack-pattern--f244b8dd-af6c-4391-a497-fc03627ce995', '[{"url": "https://attack.mitre.org/techniques/T1480/001", "external_id": "T1480.001", "source_name": "mitre-attack"}, {"url": "https://www.schneier.com/academic/paperfiles/paper-clueless-agents.pdf", "description": "Riordan, J., Schneier, B. (1998, June 18). Environmental Key Generation towards Clueless Agents. Retrieved January 18, 2019.", "source_name": "EK Clueless Agents"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/20134940/kaspersky-lab-gauss.pdf", "description": "Kaspersky Lab. (2012, August). Gauss: Abnormal Distribution. Retrieved January 17, 2019.", "source_name": "Kaspersky Gauss Whitepaper"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/home-routers-under-attack-malvertising-windows-android-devices", "description": "Kafeine. (2016, December 13). Home Routers Under Attack via Malvertising on Windows, Android Devices. Retrieved January 16, 2019.", "source_name": "Proofpoint Router Malvertising"}, {"url": "https://pdfs.semanticscholar.org/2721/3d206bc3c1e8c229fb4820b6af09e7f975da.pdf", "description": "Song, C., et al. (2012, August 7). Impeding Automated Malware Analysis with Environment-sensitive Malware. Retrieved January 18, 2019.", "source_name": "EK Impeding Malware Analysis"}, {"url": "https://research.nccgroup.com/2017/08/08/smuggling-hta-files-in-internet-explorer-edge/", "description": "Warren, R. (2017, August 8). Smuggling HTA files in Internet Explorer/Edge. Retrieved January 16, 2019.", "source_name": "Environmental Keyed HTA"}, {"url": "https://github.com/Genetic-Malware/Ebowla/blob/master/Eko_2016_Morrow_Pitts_Master.pdf", "description": "Morrow, T., Pitts, J. (2016, October 28). Genetic Malware: Designing Payloads for Specific Targets. Retrieved January 18, 2019.", "source_name": "Ebowla: Genetic Malware"}, {"url": "https://github.com/nccgroup/demiguise/blob/master/examples/virginkey.js", "description": "Warren, R. (2017, August 2). Demiguise: virginkey.js. Retrieved January 17, 2019.", "source_name": "Demiguise Guardrail Router Logo"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 7159}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the use of environmental keying may be difficult depending on the implementation. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of [Discovery](https://attack.mitre.org/tactics/TA0007), especially in a short period of time, may aid in detection.\"", "Str": "Detecting the use of environmental keying may be difficult depending on the implementation. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of [Discovery](https://attack.mitre.org/tactics/TA0007), especially in a short period of time, may aid in detection.", "Type": 3, "Index": 6632}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7081}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Nick Carr, FireEye\"\n    ]", "Str": "", "Type": 5, "Index": 6307}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 6371}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6992}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host forensic analysis\",\n      \"Signature-based detection\",\n      \"Static file analysis\"\n    ]", "Str": "", "Type": 5, "Index": 6482}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7034}}', 'T1480.001', 'Adversaries may environmentally key payloads or other features of malware to evade defenses and constraint execution to a specific target environment. Environmental keying uses cryptography to constrain execution or actions based on adversary supplied environment specific conditions that are expected to be present on the target. Environmental keying is an implementation of [Execution Guardrails](https://attack.mitre.org/techniques/T1480) that utilizes cryptographic techniques for deriving encryption/decryption keys from specific types of values in a given computing environment.(Citation: EK Clueless Agents)
INSERT INTO public.attack_patterns VALUES (72, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.287542+00', NULL, 'attack-pattern--e7cbc1de-1f79-48ee-abfd-da1241c65a15', '[{"url": "https://attack.mitre.org/techniques/T1588/003", "external_id": "T1588.003", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Code_signing", "description": "Wikipedia. (2015, November 10). Code Signing. Retrieved March 31, 2016.", "source_name": "Wikipedia Code Signing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2610}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related follow-on behavior, such as [Code Signing](https://attack.mitre.org/techniques/T1553/002) or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related follow-on behavior, such as [Code Signing](https://attack.mitre.org/techniques/T1553/002) or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).", "Type": 3, "Index": 2155}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2566}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2535}}', 'T1588.003', 'Adversaries may buy and/or steal code signing certificates that can be used during targeting. Code signing is the process of digitally signing executables and scripts to confirm the software author and guarantee that the code has not been altered or corrupted. Code signing provides a level of authenticity for a program from the developer and a guarantee that the program has not been tampered with.(Citation: Wikipedia Code Signing) Users and/or security tools may trust a signed piece of code more than an unsigned piece of code even if they don''t know who issued the certificate or who the author is.
INSERT INTO public.attack_patterns VALUES (73, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.328286+00', NULL, 'attack-pattern--565275d5-fcc3-4b66-b4e7-928e4cac6b8c', '[{"url": "https://attack.mitre.org/techniques/T1553/006", "external_id": "T1553.006", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/hardware/design/dn653559(v=vs.85)?redirectedfrom=MSDN", "description": "Microsoft. (2017, June 1). Digital Signatures for Kernel Modules on Windows. Retrieved April 22, 2021.", "source_name": "Microsoft DSE June 2017"}, {"url": "https://developer.apple.com/documentation/security/disabling_and_enabling_system_integrity_protection", "description": "Apple. (n.d.). Disabling and Enabling System Integrity Protection. Retrieved April 22, 2021.", "source_name": "Apple Disable SIP"}, {"url": "https://docs.microsoft.com/en-us/windows-hardware/drivers/install/installing-an-unsigned-driver-during-development-and-test", "description": "Microsoft. (2017, April 20). Installing an Unsigned Driver during Development and Test. Retrieved April 22, 2021.", "source_name": "Microsoft Unsigned Driver Apr 2017"}, {"url": "https://docs.microsoft.com/en-us/windows-hardware/drivers/install/the-testsigning-boot-configuration-option", "description": "Microsoft. (2021, February 15). Enable Loading of Test Signed Drivers. Retrieved April 22, 2021.", "source_name": "Microsoft TESTSIGNING Feb 2021"}, {"url": "https://www.fireeye.com/blog/threat-research/2012/08/hikit-rootkit-advanced-persistent-attack-techniques-part-2.html", "description": "Glyer, C., Kazanciyan, R. (2012, August 22). The “Hikit” Rootkit: Advanced and Persistent Attack Techniques (Part 2). Retrieved May 4, 2020.", "source_name": "FireEye HIKIT Rootkit Part 2"}, {"url": "https://github.com/hfiref0x/TDL", "description": "TDL Project. (2016, February 4). TDL (Turla Driver Loader). Retrieved April 22, 2021.", "source_name": "GitHub Turla Driver Loader"}, {"url": "https://blog-assets.f-secure.com/wp-content/uploads/2019/10/15163408/BlackEnergy_Quedagh.pdf", "description": "F-Secure Labs. (2014). BlackEnergy & Quedagh: The convergence of crimeware and APT attacks. Retrieved March 24, 2016.", "source_name": "F-Secure BlackEnergy 2014"}, {"url": "https://unit42.paloaltonetworks.com/acidbox-rare-malware/", "description": "Reichel, D. and Idrizovic, E. (2020, June 17). AcidBox: Rare Malware Repurposing Turla Group Exploit Targeted Russian Organizations. Retrieved March 16, 2021.", "source_name": "Unit42 AcidBox June 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6986}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to modify the code signing policy of a system, such as <code>bcdedit.exe -set TESTSIGNING ON</code>.(Citation: Microsoft TESTSIGNING Feb 2021) Consider monitoring for modifications made to Registry keys associated with code signing policies, such as <code>HKCU\\\\Software\\\\Policies\\\\Microsoft\\\\Windows NT\\\\Driver Signing</code>. Modifications to the code signing policy of a system are likely to be rare.\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to modify the code signing policy of a system, such as <code>bcdedit.exe -set TESTSIGNING ON</code>.(Citation: Microsoft TESTSIGNING Feb 2021) Consider monitoring for modifications made to Registry keys associated with code signing policies, such as <code>HKCU\\Software\\Policies\\Microsoft\\Windows NT\\Driver Signing</code>. Modifications to the code signing policy of a system are likely to be rare.", "Type": 3, "Index": 6310}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 6923}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Abel Morales, Exabeam\"\n    ]", "Str": "", "Type": 5, "Index": 5929}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 5996}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6825}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"User Mode Signature Validation\",\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 6168}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6867}}', 'T1553.006', 'Adversaries may modify code signing policies to enable execution of unsigned or self-signed code. Code signing provides a level of authenticity on a program from a developer and a guarantee that the program has not been tampered with. Security controls can include enforcement mechanisms to ensure that only valid, signed code can be run on an operating system. 
INSERT INTO public.attack_patterns VALUES (75, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.442926+00', NULL, 'attack-pattern--64196062-5210-42c3-9a02-563a0d1797ef', '[{"url": "https://attack.mitre.org/techniques/T1092", "external_id": "T1092", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1793}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file access on removable media. Detect processes that execute when removable media is mounted.\"", "Str": "Monitor file access on removable media. Detect processes that execute when removable media is mounted.", "Type": 3, "Index": 1503}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1715}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Drive: Drive Creation\",\n      \"Drive: Drive Access\"\n    ]", "Str": "", "Type": 5, "Index": 1410}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1640}, "x_mitre_network_requirements": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1683}}', 'T1092', 'Adversaries can perform command and control between compromised hosts on potentially disconnected networks using removable media to transfer commands from system to system. Both systems would need to be compromised, with the likelihood that an Internet-connected system was compromised first and the second through lateral movement by [Replication Through Removable Media](https://attack.mitre.org/techniques/T1091). Commands and files would be relayed from the disconnected system to the Internet-connected system to which the adversary has direct access.', 'Communication Through Removable Media', 0);
INSERT INTO public.attack_patterns VALUES (77, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.504821+00', NULL, 'attack-pattern--a6937325-9321-4e2e-bb2b-3ed2d40b2a9d', '[{"url": "https://attack.mitre.org/techniques/T1218/001", "external_id": "T1218.001", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/previous-versions/windows/desktop/htmlhelp/microsoft-html-help-1-4-sdk", "description": "Microsoft. (2018, May 30). Microsoft HTML Help 1.4. Retrieved October 3, 2018.", "source_name": "Microsoft HTML Help May 2018"}, {"url": "https://msdn.microsoft.com/windows/desktop/ms644670", "description": "Microsoft. (n.d.). HTML Help ActiveX Control Overview. Retrieved October 3, 2018.", "source_name": "Microsoft HTML Help ActiveX"}, {"url": "https://msdn.microsoft.com/windows/desktop/ms524405", "description": "Microsoft. (n.d.). About the HTML Help Executable Program. Retrieved October 3, 2018.", "source_name": "Microsoft HTML Help Executable Program"}, {"url": "https://msitpros.com/?p=3909", "description": "Moe, O. (2017, August 13). Bypassing Device guard UMCI using CHM – CVE-2017-8625. Retrieved October 3, 2018.", "source_name": "MsitPros CHM Aug 2017"}, {"url": "https://portal.msrc.microsoft.com/en-US/security-guidance/advisory/CVE-2017-8625", "description": "Microsoft. (2017, August 8). CVE-2017-8625 - Internet Explorer Security Feature Bypass Vulnerability. Retrieved October 3, 2018.", "source_name": "Microsoft CVE-2017-8625 Aug 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4327}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and analyze the execution and arguments of hh.exe. (Citation: MsitPros CHM Aug 2017) Compare recent invocations of hh.exe with prior history of known good arguments to determine anomalous and potentially adversarial activity (ex: obfuscated and/or malicious commands). Non-standard process execution trees may also indicate suspicious or malicious behavior, such as if hh.exe is the parent process for suspicious processes and activity relating to other adversarial techniques.\\n\\nMonitor presence and use of CHM files, especially if they are not typically used within an environment.\"", "Str": "Monitor and analyze the execution and arguments of hh.exe. (Citation: MsitPros CHM Aug 2017) Compare recent invocations of hh.exe with prior history of known good arguments to determine anomalous and potentially adversarial activity (ex: obfuscated and/or malicious commands). Non-standard process execution trees may also indicate suspicious or malicious behavior, such as if hh.exe is the parent process for suspicious processes and activity relating to other adversarial techniques.\n\nMonitor presence and use of CHM files, especially if they are not typically used within an environment.", "Type": 3, "Index": 3563}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4279}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Rahmat Nurfauzi, @infosecn1nja, PT Xynexis International\"\n    ]", "Str": "", "Type": 5, "Index": 3219}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3321}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4190}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3461}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4232}}', 'T1218.001', 'Adversaries may abuse Compiled HTML files (.chm) to conceal malicious code. CHM files are commonly distributed as part of the Microsoft HTML Help system. CHM files are compressed compilations of various content such as HTML documents, images, and scripting/web related programming languages such VBA, JScript, Java, and ActiveX. (Citation: Microsoft HTML Help May 2018) CHM content is displayed using underlying components of the Internet Explorer browser (Citation: Microsoft HTML Help ActiveX) loaded by the HTML Help executable program (hh.exe). (Citation: Microsoft HTML Help Executable Program)
INSERT INTO public.attack_patterns VALUES (78, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.532249+00', NULL, 'attack-pattern--791481f8-e96a-41be-b089-a088763083d4', '[{"url": "https://attack.mitre.org/techniques/T1542/002", "external_id": "T1542.002", "source_name": "mitre-attack"}, {"description": "SanDisk. (n.d.). Self-Monitoring, Analysis and Reporting Technology (S.M.A.R.T.). Retrieved October 2, 2018.", "source_name": "SanDisk SMART"}, {"url": "https://www.smartmontools.org/", "description": "smartmontools. (n.d.). smartmontools. Retrieved October 2, 2018.", "source_name": "SmartMontools"}, {"url": "https://www.itworld.com/article/2853992/3-tools-to-check-your-hard-drives-health-and-make-sure-its-not-already-dying-on-you.html", "description": "Pinola, M. (2014, December 14). 3 tools to check your hard drive''s health and make sure it''s not already dying on you. Retrieved October 2, 2018.", "source_name": "ITWorld Hard Disk Health Dec 2014"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3882}, "x_mitre_detection": {"Num": 0, "Raw": "\"Data and telemetry from use of device drivers (i.e. processes and API calls) and/or provided by SMART (Self-Monitoring, Analysis and Reporting Technology) (Citation: SanDisk SMART) (Citation: SmartMontools) disk monitoring may reveal malicious manipulations of components. Otherwise, this technique may be difficult to detect since malicious activity is taking place on system components possibly outside the purview of OS security and integrity mechanisms.\\n\\nDisk check and forensic utilities (Citation: ITWorld Hard Disk Health Dec 2014) may reveal indicators of malicious firmware such as strings, unexpected disk partition table entries, or blocks of otherwise unusual memory that warrant deeper investigation. Also consider comparing components, including hashes of component firmware and behavior, against known good images.\"", "Str": "Data and telemetry from use of device drivers (i.e. processes and API calls) and/or provided by SMART (Self-Monitoring, Analysis and Reporting Technology) (Citation: SanDisk SMART) (Citation: SmartMontools) disk monitoring may reveal malicious manipulations of components. Otherwise, this technique may be difficult to detect since malicious activity is taking place on system components possibly outside the purview of OS security and integrity mechanisms.\n\nDisk check and forensic utilities (Citation: ITWorld Hard Disk Health Dec 2014) may reveal indicators of malicious firmware such as strings, unexpected disk partition table entries, or blocks of otherwise unusual memory that warrant deeper investigation. Also consider comparing components, including hashes of component firmware and behavior, against known good images.", "Type": 3, "Index": 2749}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3706}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Driver: Driver Metadata\",\n      \"Firmware: Firmware Modification\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2479}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3615}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host intrusion prevention systems\",\n      \"File monitoring\"\n    ]", "Str": "", "Type": 5, "Index": 2628}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Ability to update component device firmware from the host operating system.\"\n    ]", "Str": "", "Type": 5, "Index": 3766}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3657}}', 'T1542.002', 'Adversaries may modify component firmware to persist on systems. Some adversaries may employ sophisticated means to compromise computer components and install malicious firmware that will execute adversary code outside of the operating system and main system firmware or BIOS. This technique may be similar to [System Firmware](https://attack.mitre.org/techniques/T1542/001) but conducted upon other system components/devices that may not have the same capability or level of integrity checking.
INSERT INTO public.attack_patterns VALUES (81, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.631758+00', NULL, 'attack-pattern--81033c3b-16a4-46e4-8fed-9b030dd03c4a', '[{"url": "https://attack.mitre.org/techniques/T1586", "external_id": "T1586", "source_name": "mitre-attack"}, {"url": "https://arstechnica.com/tech-policy/2011/02/anonymous-speaks-the-inside-story-of-the-hbgary-hack/", "description": "Bright, P. (2011, February 15). Anonymous speaks: the inside story of the HBGary hack. Retrieved March 9, 2017.", "source_name": "AnonHBGary"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3289}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently modified accounts making numerous connection requests to accounts affiliated with your organization.\\n\\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).\"", "Str": "Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently modified accounts making numerous connection requests to accounts affiliated with your organization.\n\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).", "Type": 3, "Index": 2607}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3245}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3213}}', 'T1586', 'Adversaries may compromise accounts with services that can be used during targeting. For operations incorporating social engineering, the utilization of an online persona may be important. Rather than creating and cultivating accounts (i.e. [Establish Accounts](https://attack.mitre.org/techniques/T1585)), adversaries may compromise existing accounts. Utilizing an existing persona may engender a level of trust in a potential victim if they have a relationship, or knowledge of, the compromised persona. 
INSERT INTO public.attack_patterns VALUES (82, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.675496+00', NULL, 'attack-pattern--960c3c86-1480-4d72-b4e0-8c242e84a5c5', '[{"url": "https://attack.mitre.org/techniques/T1554", "external_id": "T1554", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2300}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect and analyze signing certificate metadata and check signature validity on software that executes within the environment. Look for changes to client software that do not correlate with known software or patch cycles. \\n\\nConsider monitoring for anomalous behavior from client applications, such as atypical module loads, file reads/writes, or network connections.\"", "Str": "Collect and analyze signing certificate metadata and check signature validity on software that executes within the environment. Look for changes to client software that do not correlate with known software or patch cycles. \n\nConsider monitoring for anomalous behavior from client applications, such as atypical module loads, file reads/writes, or network connections.", "Type": 3, "Index": 1786}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2222}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"CrowdStrike Falcon OverWatch\"\n    ]", "Str": "", "Type": 5, "Index": 1559}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"File: File Creation\",\n      \"File: File Deletion\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 1633}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2190}}', 'T1554', 'Adversaries may modify client software binaries to establish persistent access to systems. Client software enables users to access services provided by a server. Common client software types are SSH clients, FTP clients, email clients, and web browsers.
INSERT INTO public.attack_patterns VALUES (83, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.717958+00', NULL, 'attack-pattern--39131305-9282-45e4-ac3b-591d2d4fc3ef', '[{"url": "https://attack.mitre.org/techniques/T1195/003", "external_id": "T1195.003", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1648}, "x_mitre_detection": {"Num": 0, "Raw": "\"Perform physical inspection of hardware to look for potential tampering. Perform integrity checking on pre-OS boot mechanisms that can be manipulated for malicious purposes.\"", "Str": "Perform physical inspection of hardware to look for potential tampering. Perform integrity checking on pre-OS boot mechanisms that can be manipulated for malicious purposes.", "Type": 3, "Index": 1331}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1570}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1539}}', 'T1195.003', 'Adversaries may manipulate hardware components in products prior to receipt by a final consumer for the purpose of data or system compromise. By modifying hardware or firmware in the supply chain, adversaries can insert a backdoor into consumer networks that may be difficult to detect and give the adversary a high degree of control over the system. Hardware backdoors may be inserted into various devices, such as servers, workstations, network infrastructure, or peripherals.', 'Compromise Hardware Supply Chain', 477);
INSERT INTO public.attack_patterns VALUES (84, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.747201+00', NULL, 'attack-pattern--7e3beebd-8bfe-4e7b-a892-e44ab06a75f9', '[{"url": "https://attack.mitre.org/techniques/T1584", "external_id": "T1584", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}, {"url": "https://www.icann.org/groups/ssac/documents/sac-007-en", "description": "ICANN Security and Stability Advisory Committee. (2005, July 12). Domain Name Hijacking: Incidents, Threats, Risks and Remediation. Retrieved March 6, 2017.", "source_name": "ICANNDomainNameHijacking"}, {"url": "https://blog.talosintelligence.com/2018/11/dnspionage-campaign-targets-middle-east.html", "description": "Mercer, W., Rascagneres, P. (2018, November 27). DNSpionage Campaign Targets Middle East. Retrieved October 9, 2020.", "source_name": "Talos DNSpionage Nov 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/12/the-eps-awakens-part-two.html", "description": "Winters, R.. (2015, December 20). The EPS Awakens - Part 2. Retrieved January 22, 2016.", "source_name": "FireEye EPS Awakens Part 2"}, {"url": "https://media.defense.gov/2019/Oct/18/2002197242/-1/-1/0/NSA_CSA_Turla_20191021%20ver%204%20-%20nsa.gov.pdf", "description": "NSA/NCSC. (2019, October 21). Cybersecurity Advisory: Turla Group Exploits Iranian APT To Expand Coverage Of Victims. Retrieved October 16, 2020.", "source_name": "NSA NCSC Turla OilRig"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3826}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection difficult for defenders. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection difficult for defenders. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 3470}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3782}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3750}}', 'T1584', 'Adversaries may compromise third-party infrastructure that can be used during targeting. Infrastructure solutions include physical or cloud servers, domains, and third-party web services. Instead of buying, leasing, or renting infrastructure an adversary may compromise infrastructure and use it during other phases of the adversary lifecycle.(Citation: Mandiant APT1)(Citation: ICANNDomainNameHijacking)(Citation: Talos DNSpionage Nov 2018)(Citation: FireEye EPS Awakens Part 2) Additionally, adversaries may compromise numerous machines to form a botnet they can leverage.
INSERT INTO public.attack_patterns VALUES (86, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.825781+00', NULL, 'attack-pattern--bd369cd9-abb8-41ce-b5bb-fff23ee86c00', '[{"url": "https://attack.mitre.org/techniques/T1195/002", "external_id": "T1195.002", "source_name": "mitre-attack"}, {"url": "https://blog.avast.com/new-investigations-in-ccleaner-incident-point-to-a-possible-third-stage-that-had-keylogger-capacities", "description": "Avast Threat Intelligence Team. (2018, March 8). New investigations into the CCleaner incident point to a possible third stage that had keylogger capacities. Retrieved March 15, 2018.", "source_name": "Avast CCleaner3 2018"}, {"url": "https://www.commandfive.com/papers/C5_APT_SKHack.pdf", "description": "Command Five Pty Ltd. (2011, September). SK Hack by an Advanced Persistent Threat. Retrieved April 6, 2018.", "source_name": "Command Five SK 2011"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2549}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use verification of distributed binaries through hash checking or other integrity checking mechanisms. Scan downloads for malicious signatures and attempt to test software and updates prior to deployment while taking note of potential suspicious activity. \"", "Str": "Use verification of distributed binaries through hash checking or other integrity checking mechanisms. Scan downloads for malicious signatures and attempt to test software and updates prior to deployment while taking note of potential suspicious activity. ", "Type": 3, "Index": 2149}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2471}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2440}}', 'T1195.002', 'Adversaries may manipulate application software prior to receipt by a final consumer for the purpose of data or system compromise. Supply chain compromise of software can take place in a number of ways, including manipulation of the application source code, manipulation of the update/distribution mechanism for that software, or replacing compiled releases with a modified version.
INSERT INTO public.attack_patterns VALUES (87, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.862745+00', NULL, 'attack-pattern--7ad38ef1-381a-406d-872a-38b136eb5ecc', '[{"url": "https://attack.mitre.org/techniques/T1213/001", "external_id": "T1213.001", "source_name": "mitre-attack"}, {"url": "https://confluence.atlassian.com/confkb/how-to-enable-user-access-logging-182943.html", "description": "Atlassian. (2018, January 9). How to Enable User Access Logging. Retrieved April 4, 2018.", "source_name": "Atlassian Confluence Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2966}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor access to Confluence repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies.\\n\\nUser access logging within Atlassian''s Confluence can be configured to report access to certain pages and documents through AccessLogFilter. (Citation: Atlassian Confluence Logging) Additional log storage and analysis infrastructure will likely be required for more robust detection capabilities.\"", "Str": "Monitor access to Confluence repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies.\n\nUser access logging within Atlassian''s Confluence can be configured to report access to certain pages and documents through AccessLogFilter. (Citation: Atlassian Confluence Logging) Additional log storage and analysis infrastructure will likely be required for more robust detection capabilities.", "Type": 3, "Index": 1851}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"SaaS\"\n    ]", "Str": "", "Type": 5, "Index": 2921}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1721}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2832}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2874}}', 'T1213.001', '
INSERT INTO public.attack_patterns VALUES (89, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.930014+00', NULL, 'attack-pattern--7b50a1d3-4ca7-45d1-989d-a6503f04bfe1', '[{"url": "https://attack.mitre.org/techniques/T1609", "external_id": "T1609", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/engine/reference/commandline/dockerd/", "description": "Docker. (n.d.). DockerD CLI. Retrieved March 29, 2021.", "source_name": "Docker Daemon CLI"}, {"url": "https://kubernetes.io/docs/concepts/overview/kubernetes-api/", "description": "The Kubernetes Authors. (n.d.). The Kubernetes API. Retrieved March 29, 2021.", "source_name": "Kubernetes API"}, {"url": "https://kubernetes.io/docs/reference/command-line-tools-reference/kubelet/", "description": "The Kubernetes Authors. (n.d.). Kubelet. Retrieved March 29, 2021.", "source_name": "Kubernetes Kubelet"}, {"url": "https://docs.docker.com/engine/reference/run/#entrypoint-default-command-to-execute-at-runtime", "description": "Docker. (n.d.). Docker run reference. Retrieved March 29, 2021.", "source_name": "Docker Entrypoint"}, {"url": "https://docs.docker.com/engine/reference/commandline/exec/", "description": "Docker. (n.d.). Docker Exec. Retrieved March 29, 2021.", "source_name": "Docker Exec"}, {"url": "https://kubernetes.io/docs/tasks/debug-application-cluster/get-shell-running-container/", "description": "The Kubernetes Authors. (n.d.). Get a Shell to a Running Container. Retrieved March 29, 2021.", "source_name": "Kubectl Exec Get Shell"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4170}, "x_mitre_detection": {"Num": 0, "Raw": "\"Container administration service activities and executed commands can be captured through logging of process execution with command-line arguments on the container and the underlying host. In Docker, the daemon log provides insight into events at the daemon and container service level. Kubernetes system component logs may also detect activities running in and out of containers in the cluster. \"", "Str": "Container administration service activities and executed commands can be captured through logging of process execution with command-line arguments on the container and the underlying host. In Docker, the daemon log provides insight into events at the daemon and container service level. Kubernetes system component logs may also detect activities running in and out of containers in the cluster. ", "Type": 3, "Index": 3620}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4083}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Alfredo Oliveira, Trend Micro\",\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Brad Geesaman, @bradgeesaman\",\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Vishwas Manral, McAfee\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 3212}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3551}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4141}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4051}}', 'T1609', 'Adversaries may abuse a container administration service to execute commands within a container. A container administration service such as the Docker daemon, the Kubernetes API server, or the kubelet may allow remote management of containers within an environment.(Citation: Docker Daemon CLI)(Citation: Kubernetes API)(Citation: Kubernetes Kubelet)
INSERT INTO public.attack_patterns VALUES (206, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.972229+00', NULL, 'attack-pattern--b21c3b2d-02e6-45b1-980b-e69051040839', '[{"url": "https://attack.mitre.org/techniques/T1068", "external_id": "T1068", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2020/06/ESET_InvisiMole.pdf", "description": "Hromcova, Z. and Cherpanov, A. (2020, June). INVISIMOLE: THE HIDDEN PART OF THE STORY. Retrieved July 16, 2020.", "source_name": "ESET InvisiMole June 2020"}, {"url": "https://unit42.paloaltonetworks.com/acidbox-rare-malware/", "description": "Reichel, D. and Idrizovic, E. (2020, June 17). AcidBox: Rare Malware Repurposing Turla Group Exploit Targeted Russian Organizations. Retrieved March 16, 2021.", "source_name": "Unit42 AcidBox June 2020"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/windows-defender-application-control/microsoft-recommended-driver-block-rules", "description": "Microsoft. (2020, October 15). Microsoft recommended driver block rules. Retrieved March 16, 2021.", "source_name": "Microsoft Driver Block Rules"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 5348}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution or evidence of Discovery. Consider monitoring for the presence or loading (ex: Sysmon Event ID 6) of known vulnerable drivers that adversaries may drop and exploit to execute code in kernel mode.(Citation: Microsoft Driver Block Rules)\\n\\nHigher privileges are often necessary to perform additional actions such as some methods of [OS Credential Dumping](https://attack.mitre.org/techniques/T1003). Look for additional activity that may indicate an adversary has gained higher privileges.\"", "Str": "Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution or evidence of Discovery. Consider monitoring for the presence or loading (ex: Sysmon Event ID 6) of known vulnerable drivers that adversaries may drop and exploit to execute code in kernel mode.(Citation: Microsoft Driver Block Rules)\n\nHigher privileges are often necessary to perform additional actions such as some methods of [OS Credential Dumping](https://attack.mitre.org/techniques/T1003). Look for additional activity that may indicate an adversary has gained higher privileges.", "Type": 3, "Index": 4098}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 5250}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Joas Antonio dos Santos, @C0d3Cr4zy, Inmetrics\",\n      \"Yaniv Agman, @AgmanYaniv, Team Nautilus Aqua Security\",\n      \"Idan Revivo, @idanr86, Team Nautilus Aqua Security\"\n    ]", "Str": "", "Type": 5, "Index": 3821}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Driver: Driver Load\"\n    ]", "Str": "", "Type": 5, "Index": 4036}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5160}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5203}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5107}}', 'T1068', 'Adversaries may exploit software vulnerabilities in an attempt to elevate privileges. Exploitation of a software vulnerability occurs when an adversary takes advantage of a programming error in a program, service, or within the operating system software or kernel itself to execute adversary-controlled code. Security constructs such as permission levels will often hinder access to information and use of certain techniques, so adversaries will likely need to perform privilege escalation to include use of software exploitation to circumvent those restrictions.
INSERT INTO public.attack_patterns VALUES (91, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.046982+00', NULL, 'attack-pattern--0470e792-32f8-46b0-a351-652bc35e9336', '[{"url": "https://attack.mitre.org/techniques/T1613", "external_id": "T1613", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/engine/api/v1.41/", "description": "Docker. (n.d.). Docker Engine API v1.41 Reference. Retrieved March 31, 2021.", "source_name": "Docker API"}, {"url": "https://kubernetes.io/docs/concepts/overview/kubernetes-api/", "description": "The Kubernetes Authors. (n.d.). The Kubernetes API. Retrieved March 29, 2021.", "source_name": "Kubernetes API"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3270}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish centralized logging for the activity of container and Kubernetes cluster components. This can be done by deploying logging agents on Kubernetes nodes and retrieving logs from sidecar proxies for application pods to detect malicious activity at the cluster level.\\n\\nMonitor logs for actions that could be taken to gather information about container infrastructure, including the use of discovery API calls by new or unexpected users. Monitor account activity logs to see actions performed and activity associated with the Kubernetes dashboard and other web applications. \"", "Str": "Establish centralized logging for the activity of container and Kubernetes cluster components. This can be done by deploying logging agents on Kubernetes nodes and retrieving logs from sidecar proxies for application pods to detect malicious activity at the cluster level.\n\nMonitor logs for actions that could be taken to gather information about container infrastructure, including the use of discovery API calls by new or unexpected users. Monitor account activity logs to see actions performed and activity associated with the Kubernetes dashboard and other web applications. ", "Type": 3, "Index": 2513}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3219}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vishwas Manral, McAfee\",\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 2085}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Cluster: Cluster Metadata\",\n      \"Container: Container Enumeration\",\n      \"Container: Container Metadata\",\n      \"Pod: Pod Enumeration\",\n      \"Pod: Pod Metadata\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2257}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3129}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3172}}', 'T1613', 'Adversaries may attempt to discover containers and other resources that are available within a containers environment. Other resources may include images, deployments, pods, nodes, and other information such as the status of a cluster.
INSERT INTO public.attack_patterns VALUES (93, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.119183+00', NULL, 'attack-pattern--e01be9c5-e763-4caf-aeb7-000b416aef67', '[{"url": "https://attack.mitre.org/techniques/T1136", "external_id": "T1136", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/event-4720", "description": "Lich, B., Miroshnikov, A. (2017, April 5). 4720(S): A user account was created. Retrieved June 30, 2017.", "source_name": "Microsoft User Creation Event"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 2890}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for processes and command-line parameters associated with account creation, such as <code>net user</code> or <code>useradd</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows system and domain controller. (Citation: Microsoft User Creation Event) Perform regular audits of domain and local system accounts to detect suspicious accounts that may have been created by an adversary.\\n\\nCollect usage logs from cloud administrator accounts to identify unusual activity in the creation of new accounts and assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins.\"", "Str": "Monitor for processes and command-line parameters associated with account creation, such as <code>net user</code> or <code>useradd</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows system and domain controller. (Citation: Microsoft User Creation Event) Perform regular audits of domain and local system accounts to detect suspicious accounts that may have been created by an adversary.\n\nCollect usage logs from cloud administrator accounts to identify unusual activity in the creation of new accounts and assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins.", "Type": 3, "Index": 1880}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2734}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 1621}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1731}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2635}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2678}}', 'T1136', 'Adversaries may create an account to maintain access to victim systems. With a sufficient level of access, creating such accounts may be used to establish secondary credentialed access that do not require persistent remote access tools to be deployed on the system.
INSERT INTO public.attack_patterns VALUES (94, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.165582+00', NULL, 'attack-pattern--cf1c2504-433f-4c4e-a1f8-91de45a0318c', '[{"url": "https://attack.mitre.org/techniques/T1578/002", "external_id": "T1578.002", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}, {"url": "https://aws.amazon.com/premiumsupport/knowledge-center/cloudtrail-search-api-calls/", "description": "Amazon. (n.d.). Search CloudTrail logs for API calls to EC2 Instances. Retrieved June 17, 2020.", "source_name": "AWS CloudTrail Search"}, {"url": "https://docs.microsoft.com/en-us/azure/azure-resource-manager/management/view-activity-logs", "description": "Microsoft. (n.d.). View Azure activity logs. Retrieved June 17, 2020.", "source_name": "Azure Activity Logs"}, {"url": "https://cloud.google.com/logging/docs/audit#admin-activity", "description": "Google. (n.d.). Audit Logs. Retrieved June 1, 2020.", "source_name": "Cloud Audit Logs"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3777}, "x_mitre_detection": {"Num": 0, "Raw": "\"The creation of a new instance or VM is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities. For example, the creation of an instance by a new user account or the unexpected creation of one or more snapshots followed by the creation of an instance may indicate suspicious activity.\\n\\nIn AWS, CloudTrail logs capture the creation of an instance in the <code>RunInstances</code> event, and in Azure the creation of a VM may be captured in Azure activity logs.(Citation: AWS CloudTrail Search)(Citation: Azure Activity Logs) Google''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of <code>gcloud compute instances create</code> to create a VM.(Citation: Cloud Audit Logs)\"", "Str": "The creation of a new instance or VM is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities. For example, the creation of an instance by a new user account or the unexpected creation of one or more snapshots followed by the creation of an instance may indicate suspicious activity.\n\nIn AWS, CloudTrail logs capture the creation of an instance in the <code>RunInstances</code> event, and in Azure the creation of a VM may be captured in Azure activity logs.(Citation: AWS CloudTrail Search)(Citation: Azure Activity Logs) Google''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of <code>gcloud compute instances create</code> to create a VM.(Citation: Cloud Audit Logs)", "Type": 3, "Index": 2771}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3732}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2701}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3643}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3685}}', 'T1578.002', 'An adversary may create a new instance or virtual machine (VM) within the compute service of a cloud account to evade defenses. Creating a new instance may allow an adversary to bypass firewall rules and permissions that exist on instances currently residing within an account. An adversary may [Create Snapshot](https://attack.mitre.org/techniques/T1578/001) of one or more volumes in an account, create a new instance, mount the snapshots, and then apply a less restrictive security policy to collect [Data from Local System](https://attack.mitre.org/techniques/T1005) or for [Remote Data Staging](https://attack.mitre.org/techniques/T1074/002).(Citation: Mandiant M-Trends 2020)
INSERT INTO public.attack_patterns VALUES (220, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.200813+00', NULL, 'attack-pattern--b85f6ce5-81e8-4f36-aff2-3df9d02a9c9d', '[{"url": "https://attack.mitre.org/techniques/T1592/003", "external_id": "T1592.003", "source_name": "mitre-attack"}, {"url": "https://arstechnica.com/information-technology/2020/08/intel-is-investigating-the-leak-of-20gb-of-its-source-code-and-private-data/", "description": "Goodin, D. & Salter, J. (2020, August 6). More than 20GB of Intel source code and proprietary data dumped online. Retrieved October 20, 2020.", "source_name": "ArsTechnica Intel"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2975}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2534}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2931}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2900}}', 'T1592.003', 'Adversaries may gather information about the victim''s host firmware that can be used during targeting. Information about host firmware may include a variety of details such as type and versions on specific hosts, which may be used to infer more information about hosts in the environment (ex: configuration, purpose, age/patch level, etc.).
INSERT INTO public.attack_patterns VALUES (96, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.27713+00', NULL, 'attack-pattern--ed2e45f9-d338-4eb2-8ce5-3a2e03323bc1', '[{"url": "https://attack.mitre.org/techniques/T1578/001", "external_id": "T1578.001", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}, {"url": "https://docs.aws.amazon.com/aws-backup/latest/devguide/logging-using-cloudtrail.html", "description": "Amazon. (2020). Logging AWS Backup API Calls with AWS CloudTrail. Retrieved April 27, 2020.", "source_name": "AWS Cloud Trail Backup API"}, {"url": "https://docs.microsoft.com/en-us/azure/backup/backup-azure-monitoring-use-azuremonitor", "description": "Microsoft. (2019, June 4). Monitor at scale by using Azure Monitor. Retrieved May 1, 2020.", "source_name": "Azure - Monitor Logs"}, {"url": "https://cloud.google.com/logging/docs/audit#admin-activity", "description": "Google. (n.d.). Audit Logs. Retrieved June 1, 2020.", "source_name": "Cloud Audit Logs"}, {"url": "https://cloud.google.com/compute/docs/instances/create-start-instance#api_2", "description": "Google. (2020, April 23). Creating and Starting a VM instance. Retrieved May 1, 2020.", "source_name": "GCP - Creating and Starting a VM"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4702}, "x_mitre_detection": {"Num": 0, "Raw": "\"The creation of a snapshot is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities such as the creation of one or more snapshots and the restoration of these snapshots by a new user account.\\n\\nIn AWS, CloudTrail logs capture the creation of snapshots and all API calls for AWS Backup as events. Using the information collected by CloudTrail, you can determine the request that was made, the IP address from which the request was made, which user made the request, when it was made, and additional details.(Citation: AWS Cloud Trail Backup API).\\n\\nIn Azure, the creation of a snapshot may be captured in Azure activity logs. Backup restoration events can also be detected through Azure Monitor Log Data by creating a custom alert for completed restore jobs.(Citation: Azure - Monitor Logs)\\n\\nGoogle''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of the <code>gcloud compute instances create</code> command to create a new VM disk from a snapshot.(Citation: Cloud Audit Logs) It is also possible to detect the usage of the GCP API with the <code>\\\"sourceSnapshot\\\":</code> parameter pointed to <code>\\\"global/snapshots/[BOOT_SNAPSHOT_NAME]</code>.(Citation: GCP - Creating and Starting a VM)\"", "Str": "The creation of a snapshot is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities such as the creation of one or more snapshots and the restoration of these snapshots by a new user account.\n\nIn AWS, CloudTrail logs capture the creation of snapshots and all API calls for AWS Backup as events. Using the information collected by CloudTrail, you can determine the request that was made, the IP address from which the request was made, which user made the request, when it was made, and additional details.(Citation: AWS Cloud Trail Backup API).\n\nIn Azure, the creation of a snapshot may be captured in Azure activity logs. Backup restoration events can also be detected through Azure Monitor Log Data by creating a custom alert for completed restore jobs.(Citation: Azure - Monitor Logs)\n\nGoogle''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of the <code>gcloud compute instances create</code> command to create a new VM disk from a snapshot.(Citation: Cloud Audit Logs) It is also possible to detect the usage of the GCP API with the <code>\"sourceSnapshot\":</code> parameter pointed to <code>\"global/snapshots/[BOOT_SNAPSHOT_NAME]</code>.(Citation: GCP - Creating and Starting a VM)", "Type": 3, "Index": 3173}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 4657}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 3047}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Snapshot: Snapshot Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3103}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4568}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4610}}', 'T1578.001', 'An adversary may create a snapshot or data backup within a cloud account to evade defenses. A snapshot is a point-in-time copy of an existing cloud compute component such as a virtual machine (VM), virtual hard drive, or volume. An adversary may leverage permissions to create a snapshot in order to bypass restrictions that prevent access to existing compute service infrastructure, unlike in [Revert Cloud Instance](https://attack.mitre.org/techniques/T1578/004) where an adversary may revert to a snapshot to evade detection and remove evidence of their presence.
INSERT INTO public.attack_patterns VALUES (97, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.308263+00', NULL, 'attack-pattern--106c0cf6-bf73-4601-9aa8-0945c2715ec5', '[{"url": "https://attack.mitre.org/techniques/T1543", "external_id": "T1543", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/library/cc772408.aspx", "description": "Microsoft. (n.d.). Services. Retrieved June 7, 2016.", "source_name": "TechNet Services"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLaunchdJobs.html", "description": "Apple. (n.d.). Creating Launch Daemons and Agents. Retrieved July 10, 2017.", "source_name": "AppleDocs Launch Agent Daemons"}, {"url": "https://www.synack.com/wp-content/uploads/2016/03/RSA_OSX_Malware.pdf", "description": "Patrick Wardle. (2016, February 29). Let''s Play Doctor: Practical OS X Malware Detection & Analysis. Retrieved July 10, 2017.", "source_name": "OSX Malware Detection"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4375}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for changes to system processes that do not correlate with known software, patch cycles, etc., including by comparing results against a trusted system baseline. New, benign system processes may be created during installation of new software. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.  \\n\\nCommand-line invocation of tools capable of modifying services may be unusual, depending on how systems are typically used in a particular environment. Look for abnormal process call trees from known services and for execution of other commands that could relate to Discovery or other adversary techniques. \\n\\nMonitor for changes to files associated with system-level processes.\"", "Str": "Monitor for changes to system processes that do not correlate with known software, patch cycles, etc., including by comparing results against a trusted system baseline. New, benign system processes may be created during installation of new software. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.  \n\nCommand-line invocation of tools capable of modifying services may be unusual, depending on how systems are typically used in a particular environment. Look for abnormal process call trees from known services and for execution of other commands that could relate to Discovery or other adversary techniques. \n\nMonitor for changes to files associated with system-level processes.", "Type": 3, "Index": 3335}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4297}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Service: Service Creation\",\n      \"Service: Service Modification\",\n      \"Process: Process Creation\",\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2942}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4265}}', 'T1543', 'Adversaries may create or modify system-level processes to repeatedly execute malicious payloads as part of persistence. When operating systems boot up, they can start processes that perform background system functions. On Windows and Linux, these system processes are referred to as services. (Citation: TechNet Services) On macOS, launchd processes known as [Launch Daemon](https://attack.mitre.org/techniques/T1543/004) and [Launch Agent](https://attack.mitre.org/techniques/T1543/001) are run to finish system initialization and load user specific parameters.(Citation: AppleDocs Launch Agent Daemons) 
INSERT INTO public.attack_patterns VALUES (98, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.35582+00', NULL, 'attack-pattern--f5946b5e-9408-485f-a7f7-b5efc88909b6', '[{"url": "https://attack.mitre.org/techniques/T1056/004", "external_id": "T1056.004", "source_name": "mitre-attack"}, {"url": "https://www.microsoft.com/en-us/wdsi/threats/malware-encyclopedia-description?Name=TrojanSpy:Win32/Ursnif.gen!I&threatId=-2147336918", "description": "Microsoft. (2017, September 15). TrojanSpy:Win32/Ursnif.gen!I. Retrieved December 18, 2017.", "source_name": "Microsoft TrojanSpy:Win32/Ursnif.gen!I Sept 2017"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms644959.aspx", "description": "Microsoft. (n.d.). Hooks Overview. Retrieved December 12, 2017.", "source_name": "Microsoft Hook Overview"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://www.adlice.com/userland-rootkits-part-1-iat-hooks/", "description": "Tigzy. (2014, October 15). Userland Rootkits: Part 1, IAT hooks. Retrieved December 12, 2017.", "source_name": "Adlice Software IAT Hooks Oct 2014"}, {"url": "https://www.mwrinfosecurity.com/our-thinking/dynamic-hooking-techniques-user-mode/", "description": "Hillman, M. (2015, August 8). Dynamic Hooking Techniques: User Mode. Retrieved December 20, 2017.", "source_name": "MWRInfoSecurity Dynamic Hooking 2015"}, {"url": "https://www.exploit-db.com/docs/17802.pdf", "description": "Mariani, B. (2011, September 6). Inline Hooking in Windows. Retrieved December 12, 2017.", "source_name": "HighTech Bridge Inline Hooking Sept 2011"}, {"url": "https://volatility-labs.blogspot.com/2012/09/movp-31-detecting-malware-hooks-in.html", "description": "Volatility Labs. (2012, September 24). MoVP 3.1 Detecting Malware Hooks in the Windows GUI Subsystem. Retrieved December 12, 2017.", "source_name": "Volatility Detecting Hooks Sept 2012"}, {"url": "https://github.com/prekageo/winhook", "description": "Prekas, G. (2011, July 11). Winhook. Retrieved December 12, 2017.", "source_name": "PreKageo Winhook Jul 2011"}, {"url": "https://github.com/jay/gethooks", "description": "Satiro, J. (2011, September 14). GetHooks. Retrieved December 12, 2017.", "source_name": "Jay GetHooks Sept 2011"}, {"url": "https://zairon.wordpress.com/2006/12/06/any-application-defined-hook-procedure-on-my-machine/", "description": "Felici, M. (2006, December 6). Any application-defined hook procedure on my machine?. Retrieved December 12, 2017.", "source_name": "Zairon Hooking Dec 2006"}, {"url": "https://eyeofrablog.wordpress.com/2017/06/27/windows-keylogger-part-2-defense-against-user-land/", "description": "Eye of Ra. (2017, June 27). Windows Keylogger Part 2: Defense against user-land. Retrieved December 12, 2017.", "source_name": "EyeofRa Detecting Hooking June 2017"}, {"url": "http://www.gmer.net/", "description": "GMER. (n.d.). GMER. Retrieved December 12, 2017.", "source_name": "GMER Rootkits"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms686701.aspx", "description": "Microsoft. (n.d.). Taking a Snapshot and Viewing Processes. Retrieved December 12, 2017.", "source_name": "Microsoft Process Snapshot"}, {"url": "https://security.stackexchange.com/questions/17904/what-are-the-methods-to-find-hooked-functions-and-apis", "description": "Stack Exchange - Security. (2012, July 31). What are the methods to find hooked functions and APIs?. Retrieved December 12, 2017.", "source_name": "StackExchange Hooks Jul 2012"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 7736}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for calls to the `SetWindowsHookEx` and `SetWinEventHook` functions, which install a hook procedure.(Citation: Microsoft Hook Overview)(Citation: Volatility Detecting Hooks Sept 2012) Also consider analyzing hook chains (which hold pointers to hook procedures for each type of hook) using tools(Citation: Volatility Detecting Hooks Sept 2012)(Citation: PreKageo Winhook Jul 2011)(Citation: Jay GetHooks Sept 2011) or by programmatically examining internal kernel structures.(Citation: Zairon Hooking Dec 2006)(Citation: EyeofRa Detecting Hooking June 2017)\\n\\nRootkits detectors(Citation: GMER Rootkits) can also be used to monitor for various types of hooking activity.\\n\\nVerify integrity of live processes by comparing code in memory to that of corresponding static binaries, specifically checking for jumps and other instructions that redirect code flow. Also consider taking snapshots of newly started processes(Citation: Microsoft Process Snapshot) to compare the in-memory IAT to the real addresses of the referenced functions.(Citation: StackExchange Hooks Jul 2012)(Citation: Adlice Software IAT Hooks Oct 2014)\"", "Str": "Monitor for calls to the `SetWindowsHookEx` and `SetWinEventHook` functions, which install a hook procedure.(Citation: Microsoft Hook Overview)(Citation: Volatility Detecting Hooks Sept 2012) Also consider analyzing hook chains (which hold pointers to hook procedures for each type of hook) using tools(Citation: Volatility Detecting Hooks Sept 2012)(Citation: PreKageo Winhook Jul 2011)(Citation: Jay GetHooks Sept 2011) or by programmatically examining internal kernel structures.(Citation: Zairon Hooking Dec 2006)(Citation: EyeofRa Detecting Hooking June 2017)\n\nRootkits detectors(Citation: GMER Rootkits) can also be used to monitor for various types of hooking activity.\n\nVerify integrity of live processes by comparing code in memory to that of corresponding static binaries, specifically checking for jumps and other instructions that redirect code flow. Also consider taking snapshots of newly started processes(Citation: Microsoft Process Snapshot) to compare the in-memory IAT to the real addresses of the referenced functions.(Citation: StackExchange Hooks Jul 2012)(Citation: Adlice Software IAT Hooks Oct 2014)", "Type": 3, "Index": 6411}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7688}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 6308}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7574}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 7616}}', 'T1056.004', 'Adversaries may hook into Windows application programming interface (API) functions to collect user credentials. Malicious hooking mechanisms may capture API calls that include parameters that reveal user authentication credentials.(Citation: Microsoft TrojanSpy:Win32/Ursnif.gen!I Sept 2017) Unlike [Keylogging](https://attack.mitre.org/techniques/T1056/001),  this technique focuses specifically on API functions that include parameters that reveal user credentials. Hooking involves redirecting calls to these functions and can be implemented via:
INSERT INTO public.attack_patterns VALUES (101, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.459796+00', NULL, 'attack-pattern--837f9164-50af-4ac0-8219-379d8a74cefc', '[{"url": "https://attack.mitre.org/techniques/T1552/001", "external_id": "T1552.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/639.html", "external_id": "CAPEC-639", "source_name": "capec"}, {"url": "http://carnal0wnage.attackresearch.com/2014/05/mimikatz-against-virtual-machine-memory.html", "description": "CG. (2014, May 20). Mimikatz Against Virtual Machine Memory Part 1. Retrieved November 12, 2014.", "source_name": "CG 2014"}, {"url": "http://blogs.technet.com/b/srd/archive/2014/05/13/ms14-025-an-update-for-group-policy-preferences.aspx", "description": "Security Research and Defense. (2014, May 13). MS14-025: An Update for Group Policy Preferences. Retrieved January 28, 2015.", "source_name": "SRD GPP"}, {"url": "https://unit42.paloaltonetworks.com/hildegard-malware-teamtnt/", "description": "Chen, J. et al. (2021, February 3). Hildegard: New TeamTNT Cryptojacking Malware Targeting Kubernetes. Retrieved April 5, 2021.", "source_name": "Unit 42 Hildegard Malware"}, {"url": "https://unit42.paloaltonetworks.com/attackers-tactics-and-techniques-in-unsecured-docker-daemons-revealed/", "description": "Chen, J.. (2020, January 29). Attacker''s Tactics and Techniques in Unsecured Docker Daemons Revealed. Retrieved March 31, 2021.", "source_name": "Unit 42 Unsecured Docker Daemons"}, {"url": "https://posts.specterops.io/head-in-the-clouds-bd038bb69e48", "description": "Maddalena, C.. (2018, September 12). Head in the Clouds. Retrieved October 4, 2019.", "source_name": "Specter Ops - Cloud Credential Storage"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4893}, "x_mitre_detection": {"Num": 0, "Raw": "\"While detecting adversaries accessing these files may be difficult without knowing they exist in the first place, it may be possible to detect adversary use of credentials they have obtained. Monitor the command-line arguments of executing processes for suspicious words or regular expressions that may indicate searching for a password (for example: password, pwd, login, secure, or credentials). See [Valid Accounts](https://attack.mitre.org/techniques/T1078) for more information.\"", "Str": "While detecting adversaries accessing these files may be difficult without knowing they exist in the first place, it may be possible to detect adversary use of credentials they have obtained. Monitor the command-line arguments of executing processes for suspicious words or regular expressions that may indicate searching for a password (for example: password, pwd, login, secure, or credentials). See [Valid Accounts](https://attack.mitre.org/techniques/T1078) for more information.", "Type": 3, "Index": 4067}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4713}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Rory McCune, Aqua Security\",\n      \"Jay Chen, Palo Alto Networks\",\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\",\n      \"Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 3722}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3971}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4585}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Access to files\"\n    ]", "Str": "", "Type": 5, "Index": 4837}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4627}}', 'T1552.001', 'Adversaries may search local file systems and remote file shares for files containing insecurely stored credentials. These can be files created by users to store their own credentials, shared credential stores for a group of individuals, configuration files containing passwords for a system or service, or source code/binary files containing embedded passwords.
INSERT INTO public.attack_patterns VALUES (103, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.555367+00', NULL, 'attack-pattern--58a3e6aa-4453-4cc8-a51f-4befe80b31a8', '[{"url": "https://attack.mitre.org/techniques/T1555/003", "external_id": "T1555.003", "source_name": "mitre-attack"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}, {"url": "https://docs.microsoft.com/en-us/windows/desktop/api/dpapi/nf-dpapi-cryptunprotectdata", "description": "Microsoft. (2018, April 12). CryptUnprotectData function. Retrieved June 18, 2019.", "source_name": "Microsoft CryptUnprotectData April 2018"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/new-vega-stealer-shines-brightly-targeted-campaign", "description": "Proofpoint. (2018, May 10). New Vega Stealer shines brightly in targeted campaign . Retrieved June 18, 2019.", "source_name": "Proofpoint Vega Credential Stealer May 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/07/hawkeye-malware-distributed-in-phishing-campaign.html", "description": "Swapnil Patil, Yogesh Londhe. (2017, July 25). HawkEye Credential Theft Malware Distributed in Recent Phishing Campaign. Retrieved June 18, 2019.", "source_name": "FireEye HawkEye Malware July 2017"}, {"url": "https://github.com/putterpanda/mimikittenz", "description": "Jamieson O''Reilly (putterpanda). (2016, July 4). mimikittenz. Retrieved June 20, 2019.", "source_name": "GitHub Mimikittenz July 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5514}, "x_mitre_detection": {"Num": 0, "Raw": "\"Identify web browser files that contain credentials such as Google Chrome’s Login Data database file: <code>AppData\\\\Local\\\\Google\\\\Chrome\\\\User Data\\\\Default\\\\Login Data</code>. Monitor file read events of web browser files that contain credentials, especially when the reading process is unrelated to the subject web browser. Monitor process execution logs to include PowerShell Transcription focusing on those that perform a combination of behaviors including reading web browser process memory, utilizing regular expressions, and those that contain numerous keywords for common web applications (Gmail, Twitter, Office365, etc.).\"", "Str": "Identify web browser files that contain credentials such as Google Chrome’s Login Data database file: <code>AppData\\Local\\Google\\Chrome\\User Data\\Default\\Login Data</code>. Monitor file read events of web browser files that contain credentials, especially when the reading process is unrelated to the subject web browser. Monitor process execution logs to include PowerShell Transcription focusing on those that perform a combination of behaviors including reading web browser process memory, utilizing regular expressions, and those that contain numerous keywords for common web applications (Gmail, Twitter, Office365, etc.).", "Type": 3, "Index": 4677}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5436}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ryan Benson, Exabeam\",\n      \"Barry Shteiman, Exabeam\",\n      \"Sylvain Gil, Exabeam\",\n      \"RedHuntLabs, @redhuntlabs\"\n    ]", "Str": "", "Type": 5, "Index": 4349}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 4513}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5347}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5389}}', 'T1555.003', 'Adversaries may acquire credentials from web browsers by reading files specific to the target browser.(Citation: Talos Olympic Destroyer 2018) Web browsers commonly save credentials such as website usernames and passwords so that they do not need to be entered manually in the future. Web browsers typically store the credentials in an encrypted format within a credential store; however, methods exist to extract plaintext credentials from web browsers.
INSERT INTO public.attack_patterns VALUES (106, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.660936+00', NULL, 'attack-pattern--f303a39a-6255-4b89-aecc-18c4d8ca7163', '[{"url": "https://attack.mitre.org/techniques/T1003/006", "external_id": "T1003.006", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/cc228086.aspx", "description": "Microsoft. (2017, December 1). MS-DRSR Directory Replication Service (DRS) Remote Protocol. Retrieved December 4, 2017.", "source_name": "Microsoft DRSR Dec 2017"}, {"url": "https://msdn.microsoft.com/library/dd207691.aspx", "description": "Microsoft. (n.d.). IDL_DRSGetNCChanges (Opnum 3). Retrieved December 4, 2017.", "source_name": "Microsoft GetNCCChanges"}, {"url": "https://wiki.samba.org/index.php/DRSUAPI", "description": "SambaWiki. (n.d.). DRSUAPI. Retrieved December 4, 2017.", "source_name": "Samba DRSUAPI"}, {"url": "https://source.winehq.org/WineAPI/samlib.html", "description": "Wine API. (n.d.). samlib.dll. Retrieved December 4, 2017.", "source_name": "Wine API samlib.dll"}, {"url": "https://adsecurity.org/?p=1729", "description": "Metcalf, S. (2015, September 25). Mimikatz DCSync Usage, Exploitation, and Detection. Retrieved August 7, 2017.", "source_name": "ADSecurity Mimikatz DCSync"}, {"url": "http://www.harmj0y.net/blog/redteaming/mimikatz-and-dcsync-and-extrasids-oh-my/", "description": "Schroeder, W. (2015, September 22). Mimikatz and DCSync and ExtraSids, Oh My. Retrieved August 7, 2017.", "source_name": "Harmj0y Mimikatz and DCSync"}, {"url": "https://blog.stealthbits.com/manipulating-user-passwords-with-mimikatz-SetNTLM-ChangeNTLM", "description": "Warren, J. (2017, July 11). Manipulating User Passwords with Mimikatz. Retrieved December 4, 2017.", "source_name": "InsiderThreat ChangeNTLM July 2017"}, {"url": "https://github.com/gentilkiwi/mimikatz/wiki/module-~-lsadump", "description": "Deply, B., Le Toux, V. (2016, June 5). module ~ lsadump. Retrieved August 7, 2017.", "source_name": "GitHub Mimikatz lsadump Module"}, {"url": "https://msdn.microsoft.com/library/cc237008.aspx", "description": "Microsoft. (2017, December 1). MS-NRPC - Netlogon Remote Protocol. Retrieved December 6, 2017.", "source_name": "Microsoft NRPC Dec 2017"}, {"url": "https://msdn.microsoft.com/library/cc245496.aspx", "description": "Microsoft. (n.d.). MS-SAMR Security Account Manager (SAM) Remote Protocol (Client-to-Server) - Transport. Retrieved December 4, 2017.", "source_name": "Microsoft SAMR"}, {"url": "https://adsecurity.org/?p=1729", "description": "Metcalf, S. (2015, September 25). Mimikatz DCSync Usage, Exploitation, and Detection. Retrieved December 4, 2017.", "source_name": "AdSecurity DCSync Sept 2015"}, {"url": "http://www.harmj0y.net/blog/redteaming/mimikatz-and-dcsync-and-extrasids-oh-my/", "description": "Schroeder, W. (2015, September 22). Mimikatz and DCSync and ExtraSids, Oh My. Retrieved December 4, 2017.", "source_name": "Harmj0y DCSync Sept 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6453}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor domain controller logs for replication requests and other unscheduled activity possibly associated with DCSync.(Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft GetNCCChanges) (Citation: Samba DRSUAPI) Also monitor for network protocols(Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft NRPC Dec 2017) and other replication requests(Citation: Microsoft SAMR) from IPs not associated with known domain controllers.(Citation: AdSecurity DCSync Sept 2015)\\n\\nNote: Domain controllers may not log replication requests originating from the default domain controller account.(Citation: Harmj0y DCSync Sept 2015)\"", "Str": "Monitor domain controller logs for replication requests and other unscheduled activity possibly associated with DCSync.(Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft GetNCCChanges) (Citation: Samba DRSUAPI) Also monitor for network protocols(Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft NRPC Dec 2017) and other replication requests(Citation: Microsoft SAMR) from IPs not associated with known domain controllers.(Citation: AdSecurity DCSync Sept 2015)\n\nNote: Domain controllers may not log replication requests originating from the default domain controller account.(Citation: Harmj0y DCSync Sept 2015)", "Type": 3, "Index": 5646}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6405}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ExtraHop\",\n      \"Vincent Le Toux\"\n    ]", "Str": "", "Type": 5, "Index": 5379}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Object Access\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 5458}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6307}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6349}}', 'T1003.006', 'Adversaries may attempt to access credentials and other sensitive information by abusing a Windows Domain Controller''s application programming interface (API)(Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft GetNCCChanges) (Citation: Samba DRSUAPI) (Citation: Wine API samlib.dll) to simulate the replication process from a remote domain controller using a technique called DCSync.
INSERT INTO public.attack_patterns VALUES (107, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.700079+00', NULL, 'attack-pattern--2fee9321-3e71-4cf4-af24-d4d40d355b34', '[{"url": "https://attack.mitre.org/techniques/T1574/001", "external_id": "T1574.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/471.html", "external_id": "CAPEC-471", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/dlls/dynamic-link-library-search-order?redirectedfrom=MSDN", "description": "Microsoft. (2018, May 31). Dynamic-Link Library Search Order. Retrieved November 30, 2014.", "source_name": "Microsoft Dynamic Link Library Search Order"}, {"url": "https://www.fireeye.com/blog/threat-research/2010/07/malware-persistence-windows-registry.html", "description": "Harbour, N. (2010, July 15). Malware Persistence without the Windows Registry. Retrieved November 17, 2020.", "source_name": "FireEye Hijacking July 2010"}, {"url": "https://www.owasp.org/index.php/Binary_planting", "description": "OWASP. (2013, January 30). Binary planting. Retrieved June 7, 2016.", "source_name": "OWASP Binary Planting"}, {"url": "https://www.fireeye.com/blog/threat-research/2011/06/fxsst.html", "description": "Harbour, N. (2011, June 3). What the fxsst?. Retrieved November 17, 2020.", "source_name": "FireEye fxsst June 2011"}, {"url": "https://docs.microsoft.com/en-us/security-updates/securityadvisories/2010/2269637", "description": "Microsoft. (, May 23). Microsoft Security Advisory 2269637. Retrieved March 13, 2020.", "source_name": "Microsoft Security Advisory 2269637"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/dlls/dynamic-link-library-redirection?redirectedfrom=MSDN", "description": "Microsoft. (2018, May 31). Dynamic-Link Library Redirection. Retrieved March 13, 2020.", "source_name": "Microsoft Dynamic-Link Library Redirection"}, {"url": "https://msdn.microsoft.com/en-US/library/aa375365", "description": "Microsoft. (n.d.). Manifests. Retrieved December 5, 2014.", "source_name": "Microsoft Manifests"}, {"url": "https://www.fireeye.com/blog/threat-research/2010/08/dll-search-order-hijacking-revisited.html", "description": "Nick Harbour. (2010, September 1). DLL Search Order Hijacking Revisited. Retrieved March 13, 2020.", "source_name": "FireEye DLL Search Order Hijacking"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 6457}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file systems for moving, renaming, replacing, or modifying DLLs. Changes in the set of DLLs that are loaded by a process (compared with past behavior) that do not correlate with known software, patches, etc., are suspicious. Monitor DLLs loaded into a process and detect DLLs that have the same file name but abnormal paths. Modifications to or creation of `.manifest` and `.local` redirection files that do not correlate with software updates are suspicious.\"", "Str": "Monitor file systems for moving, renaming, replacing, or modifying DLLs. Changes in the set of DLLs that are loaded by a process (compared with past behavior) that do not correlate with known software, patches, etc., are suspicious. Monitor DLLs loaded into a process and detect DLLs that have the same file name but abnormal paths. Modifications to or creation of `.manifest` and `.local` redirection files that do not correlate with software updates are suspicious.", "Type": 3, "Index": 5876}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6409}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 5660}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 5752}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6378}}', 'T1574.001', 'Adversaries may execute their own malicious payloads by hijacking the search order used to load DLLs. Windows systems use a common method to look for required DLLs to load into a program. (Citation: Microsoft Dynamic Link Library Search Order)(Citation: FireEye Hijacking July 2010) Hijacking DLL loads may be for the purpose of establishing persistence as well as elevating privileges and/or evading restrictions on file execution.
INSERT INTO public.attack_patterns VALUES (113, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.774246+00', NULL, 'attack-pattern--c2f59d25-87fe-44aa-8f83-e8e59d077bf5', '[{"url": "https://attack.mitre.org/techniques/T1584/002", "external_id": "T1584.002", "source_name": "mitre-attack"}, {"url": "https://blog.talosintelligence.com/2018/11/dnspionage-campaign-targets-middle-east.html", "description": "Mercer, W., Rascagneres, P. (2018, November 27). DNSpionage Campaign Targets Middle East. Retrieved October 9, 2020.", "source_name": "Talos DNSpionage Nov 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/01/global-dns-hijacking-campaign-dns-record-manipulation-at-scale.html", "description": "Hirani, M., Jones, S., Read, B. (2019, January 10). Global DNS Hijacking Campaign: DNS Record Manipulation at Scale. Retrieved October 9, 2020.", "source_name": "FireEye DNS Hijack 2019"}, {"url": "https://blogs.cisco.com/security/talos/angler-domain-shadowing", "description": "Nick Biasini. (2015, March 3). Threat Spotlight: Angler Lurking in the Domain Shadows. Retrieved March 6, 2017.", "source_name": "CiscoAngler"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/The-Shadow-Knows", "description": "Proofpoint Staff. (2015, December 15). The shadow knows: Malvertising campaigns use domain shadowing to pull in Angler EK. Retrieved October 16, 2020.", "source_name": "Proofpoint Domain Shadowing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3387}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 3029}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3343}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3312}}', 'T1584.002', 'Adversaries may compromise third-party DNS servers that can be used during targeting. During post-compromise activity, adversaries may utilize DNS traffic for various tasks, including for Command and Control (ex: [Application Layer Protocol](https://attack.mitre.org/techniques/T1071)). Instead of setting up their own DNS servers, adversaries may compromise third-party DNS servers in support of operations.
INSERT INTO public.attack_patterns VALUES (110, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.854056+00', NULL, 'attack-pattern--0ff59227-8aa8-4c09-bf1f-925605bd07ea', '[{"url": "https://attack.mitre.org/techniques/T1590/002", "external_id": "T1590.002", "source_name": "mitre-attack"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2971}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2530}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2927}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2896}}', 'T1590.002', 'Adversaries may gather information about the victim''s DNS that can be used during targeting. DNS information may include a variety of details, including registered name servers as well as records that outline addressing for a target’s subdomains, mail servers, and other hosts.
INSERT INTO public.attack_patterns VALUES (112, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.928507+00', NULL, 'attack-pattern--197ef1b9-e764-46c3-b96c-23f77985dc81', '[{"url": "https://attack.mitre.org/techniques/T1583/002", "external_id": "T1583.002", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/dns-tunneling-how-dns-can-be-abused-by-malicious-actors/", "description": "Hinchliffe, A. (2019, March 15). DNS Tunneling: how DNS can be (ab)used by malicious actors. Retrieved October 3, 2020.", "source_name": "Unit42 DNS Mar 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2360}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 2002}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2316}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2285}}', 'T1583.002', 'Adversaries may set up their own Domain Name System (DNS) servers that can be used during targeting. During post-compromise activity, adversaries may utilize DNS traffic for various tasks, including for Command and Control (ex: [Application Layer Protocol](https://attack.mitre.org/techniques/T1071)). Instead of hijacking existing DNS servers, adversaries may opt to configure and run their own DNS servers in support of operations.
INSERT INTO public.attack_patterns VALUES (115, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.997267+00', NULL, 'attack-pattern--d45a3d09-b3cf-48f4-9f0f-f521ee5cb05c', '[{"url": "https://attack.mitre.org/techniques/T1485", "external_id": "T1485", "source_name": "mitre-attack"}, {"url": "https://www.symantec.com/connect/blogs/shamoon-attacks", "description": "Symantec. (2012, August 16). The Shamoon Attacks. Retrieved March 14, 2019.", "source_name": "Symantec Shamoon 2012"}, {"url": "https://www.fireeye.com/blog/threat-research/2016/11/fireeye_respondsto.html", "description": "FireEye. (2016, November 30). FireEye Responds to Wave of Destructive Cyber Attacks in Gulf Region. Retrieved January 11, 2017.", "source_name": "FireEye Shamoon Nov 2016"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/11/unit42-shamoon-2-return-disttrack-wiper/", "description": "Falcone, R.. (2016, November 30). Shamoon 2: Return of the Disttrack Wiper. Retrieved January 11, 2017.", "source_name": "Palo Alto Shamoon Nov 2016"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/07180722/Report_Shamoon_StoneDrill_final.pdf", "description": "Kaspersky Lab. (2017, March 7). From Shamoon to StoneDrill: Wipers attacking Saudi organizations and beyond. Retrieved March 14, 2019.", "source_name": "Kaspersky StoneDrill 2017"}, {"url": "https://unit42.paloaltonetworks.com/shamoon-3-targets-oil-gas-organization/", "description": "Falcone, R. (2018, December 13). Shamoon 3 Targets Oil and Gas Organization. Retrieved March 14, 2019.", "source_name": "Unit 42 Shamoon3 2018"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}, {"url": "https://threatpost.com/hacker-puts-hosting-service-code-spaces-out-of-business/106761/", "description": "Mimoso, M.. (2014, June 18). Hacker Puts Hosting Service Code Spaces Out of Business. Retrieved December 15, 2020.", "source_name": "Data Destruction - Threat Post"}, {"url": "https://www.justice.gov/usao-ndca/pr/san-jose-man-pleads-guilty-damaging-cisco-s-network", "description": "DOJ. (2020, August 26). San Jose Man Pleads Guilty To Damaging Cisco’s Network. Retrieved December 15, 2020.", "source_name": "DOJ  - Cisco Insider"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 7299}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and command-line parameters of binaries that could be involved in data destruction activity, such as [SDelete](https://attack.mitre.org/software/S0195). Monitor for the creation of suspicious files as well as high unusual file modification activity. In particular, look for large quantities of file modifications in user directories and under <code>C:\\\\Windows\\\\System32\\\\</code>.\\n\\nIn cloud environments, the occurrence of anomalous high-volume deletion events, such as the <code>DeleteDBCluster</code> and <code>DeleteGlobalCluster</code> events in AWS, or a high quantity of data deletion events, such as <code>DeleteBucket</code>, within a short period of time may indicate suspicious activity.\"", "Str": "Use process monitoring to monitor the execution and command-line parameters of binaries that could be involved in data destruction activity, such as [SDelete](https://attack.mitre.org/software/S0195). Monitor for the creation of suspicious files as well as high unusual file modification activity. In particular, look for large quantities of file modifications in user directories and under <code>C:\\Windows\\System32\\</code>.\n\nIn cloud environments, the occurrence of anomalous high-volume deletion events, such as the <code>DeleteDBCluster</code> and <code>DeleteGlobalCluster</code> events in AWS, or a high quantity of data deletion events, such as <code>DeleteBucket</code>, within a short period of time may indicate suspicious activity.", "Type": 3, "Index": 6225}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 7207}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 7003}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brent Murphy, Elastic\",\n      \"David French, Elastic\",\n      \"Syed Ummar Farooqh, McAfee\",\n      \"Prasad Somasamudram, McAfee\",\n      \"Sekhar Sarukkai, McAfee \",\n      \"Varonis Threat Labs\"\n    ]", "Str": "", "Type": 5, "Index": 5640}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"File: File Deletion\",\n      \"Image: Image Deletion\",\n      \"Instance: Instance Deletion\",\n      \"Snapshot: Snapshot Deletion\",\n      \"Cloud Storage: Cloud Storage Deletion\",\n      \"Volume: Volume Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 5874}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7064}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 7107}}', 'T1485', 'Adversaries may destroy data and files on specific systems or in large numbers on a network to interrupt availability to systems, services, and network resources. Data destruction is likely to render stored data irrecoverable by forensic techniques through overwriting files or data on local and remote drives.(Citation: Symantec Shamoon 2012)(Citation: FireEye Shamoon Nov 2016)(Citation: Palo Alto Shamoon Nov 2016)(Citation: Kaspersky StoneDrill 2017)(Citation: Unit 42 Shamoon3 2018)(Citation: Talos Olympic Destroyer 2018) Common operating system file deletion commands such as <code>del</code> and <code>rm</code> often only remove pointers to files without wiping the contents of the files themselves, making the files recoverable by proper forensic methodology. This behavior is distinct from [Disk Content Wipe](https://attack.mitre.org/techniques/T1561/001) and [Disk Structure Wipe](https://attack.mitre.org/techniques/T1561/002) because individual files are destroyed rather than sections of a storage disk or the disk''s logical structure.
INSERT INTO public.attack_patterns VALUES (116, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.04026+00', NULL, 'attack-pattern--cc7b8c4e-9be0-47ca-b0bb-83915ec3ee2f', '[{"url": "https://attack.mitre.org/techniques/T1132", "external_id": "T1132", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Binary-to-text_encoding", "description": "Wikipedia. (2016, December 26). Binary-to-text encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Binary-to-text Encoding"}, {"url": "https://en.wikipedia.org/wiki/Character_encoding", "description": "Wikipedia. (2017, February 19). Character Encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Character Encoding"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2988}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 2319}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2910}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Itzik Kotler, SafeBreach\"\n    ]", "Str": "", "Type": 5, "Index": 2166}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2236}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2778}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2821}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2863}}', 'T1132', 'Adversaries may encode data to make the content of command and control traffic more difficult to detect. Command and control (C2) information can be encoded using a standard data encoding system. Use of data encoding may adhere to existing protocol specifications and includes use of ASCII, Unicode, Base64, MIME, or other binary-to-text and character encoding systems.(Citation: Wikipedia Binary-to-text Encoding) (Citation: Wikipedia Character Encoding) Some data encoding systems may also result in data compression, such as gzip.', 'Data Encoding', 0);
INSERT INTO public.attack_patterns VALUES (117, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.084303+00', NULL, 'attack-pattern--b80d107d-fa0d-4b60-9684-b0433e8bdba0', '[{"url": "https://attack.mitre.org/techniques/T1486", "external_id": "T1486", "source_name": "mitre-attack"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA16-091A", "description": "US-CERT. (2016, March 31). Alert (TA16-091A): Ransomware and Recent Variants. Retrieved March 15, 2019.", "source_name": "US-CERT Ransomware 2016"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/05/wannacry-malware-profile.html", "description": "Berry, A., Homan, J., and Eitzman, R. (2017, May 23). WannaCry Malware Profile. Retrieved March 15, 2019.", "source_name": "FireEye WannaCry 2017"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA17-181A", "description": "US-CERT. (2017, July 1). Alert (TA17-181A): Petya Ransomware. Retrieved March 15, 2019.", "source_name": "US-CERT NotPetya 2017"}, {"url": "https://www.us-cert.gov/ncas/alerts/AA18-337A", "description": "US-CERT. (2018, December 3). Alert (AA18-337A): SamSam Ransomware. Retrieved March 15, 2019.", "source_name": "US-CERT SamSam 2018"}, {"url": "https://rhinosecuritylabs.com/aws/s3-ransomware-part-1-attack-vector/", "description": "Gietzen, S. (n.d.). S3 Ransomware Part 1: Attack Vector. Retrieved April 14, 2021.", "source_name": "Rhino S3 Ransomware Part 1"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4901}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and command line parameters of binaries involved in data destruction activity, such as vssadmin, wbadmin, and bcdedit. Monitor for the creation of suspicious files as well as unusual file modification activity. In particular, look for large quantities of file modifications in user directories.\\n\\nIn some cases, monitoring for unusual kernel driver installation activity can aid in detection.\\n\\nIn cloud environments, monitor for events that indicate storage objects have been anomalously replaced by copies.\"", "Str": "Use process monitoring to monitor the execution and command line parameters of binaries involved in data destruction activity, such as vssadmin, wbadmin, and bcdedit. Monitor for the creation of suspicious files as well as unusual file modification activity. In particular, look for large quantities of file modifications in user directories.\n\nIn some cases, monitoring for unusual kernel driver installation activity can aid in detection.\n\nIn cloud environments, monitor for events that indicate storage objects have been anomalously replaced by copies.", "Type": 3, "Index": 4016}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 4809}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4605}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oleg Kolesnikov, Securonix\"\n    ]", "Str": "", "Type": 5, "Index": 3680}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Cloud Storage: Cloud Storage Metadata\",\n      \"Cloud Storage: Cloud Storage Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3752}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4666}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4709}}', 'T1486', 'Adversaries may encrypt data on target systems or on large numbers of systems in a network to interrupt availability to system and network resources. They can attempt to render stored data inaccessible by encrypting files or data on local and remote drives and withholding access to a decryption key. This may be done in order to extract monetary compensation from a victim in exchange for decryption or a decryption key (ransomware) or to render data permanently inaccessible in cases where the key is not saved or transmitted.(Citation: US-CERT Ransomware 2016)(Citation: FireEye WannaCry 2017)(Citation: US-CERT NotPetya 2017)(Citation: US-CERT SamSam 2018) In the case of ransomware, it is typical that common user files like Office documents, PDFs, images, videos, audio, text, and source code files will be encrypted. In some cases, adversaries may encrypt critical system files, disk partitions, and the MBR.(Citation: US-CERT NotPetya 2017)
INSERT INTO public.attack_patterns VALUES (131, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.125323+00', NULL, 'attack-pattern--70857657-bd0b-4695-ad3e-b13f92cac1b4', '[{"url": "https://attack.mitre.org/techniques/T1578/003", "external_id": "T1578.003", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}, {"url": "https://aws.amazon.com/premiumsupport/knowledge-center/cloudtrail-search-api-calls/", "description": "Amazon. (n.d.). Search CloudTrail logs for API calls to EC2 Instances. Retrieved June 17, 2020.", "source_name": "AWS CloudTrail Search"}, {"url": "https://docs.microsoft.com/en-us/azure/azure-resource-manager/management/view-activity-logs", "description": "Microsoft. (n.d.). View Azure activity logs. Retrieved June 17, 2020.", "source_name": "Azure Activity Logs"}, {"url": "https://cloud.google.com/logging/docs/audit#admin-activity", "description": "Google. (n.d.). Audit Logs. Retrieved June 1, 2020.", "source_name": "Cloud Audit Logs"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3477}, "x_mitre_detection": {"Num": 0, "Raw": "\"The deletion of a new instance or virtual machine is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities. For example, detecting a sequence of events such as the creation of an instance, mounting of a snapshot to that instance, and deletion of that instance by a new user account may indicate suspicious activity.\\n\\nIn AWS, CloudTrail logs capture the deletion of an instance in the <code>TerminateInstances</code> event, and in Azure the deletion of a VM may be captured in Azure activity logs.(Citation: AWS CloudTrail Search)(Citation: Azure Activity Logs) Google''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of <code>gcloud compute instances delete</code> to delete a VM.(Citation: Cloud Audit Logs)\"", "Str": "The deletion of a new instance or virtual machine is a common part of operations within many cloud environments. Events should then not be viewed in isolation, but as part of a chain of behavior that could lead to other activities. For example, detecting a sequence of events such as the creation of an instance, mounting of a snapshot to that instance, and deletion of that instance by a new user account may indicate suspicious activity.\n\nIn AWS, CloudTrail logs capture the deletion of an instance in the <code>TerminateInstances</code> event, and in Azure the deletion of a VM may be captured in Azure activity logs.(Citation: AWS CloudTrail Search)(Citation: Azure Activity Logs) Google''s Admin Activity audit logs within their Cloud Audit logs can be used to detect the usage of <code>gcloud compute instances delete</code> to delete a VM.(Citation: Cloud Audit Logs)", "Type": 3, "Index": 2433}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3432}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 2363}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3343}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3385}}', 'T1578.003', 'An adversary may delete a cloud instance after they have performed malicious activities in an attempt to evade detection and remove evidence of their presence.  Deleting an instance or virtual machine can remove valuable forensic artifacts and other evidence of suspicious behavior if the instance is not recoverable.
INSERT INTO public.attack_patterns VALUES (119, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.209536+00', NULL, 'attack-pattern--ad255bfe-a9e6-4b52-a258-8d3462abe842', '[{"url": "https://attack.mitre.org/techniques/T1001", "external_id": "T1001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2299}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 1688}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2221}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1605}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2147}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2190}}', 'T1001', 'Adversaries may obfuscate command and control traffic to make it more difficult to detect. Command and control (C2) communications are hidden (but not necessarily encrypted) in an attempt to make the content more difficult to discover or decipher and to make the communication less conspicuous and hide commands from being seen. This encompasses many methods, such as adding junk data to protocol traffic, using steganography, or impersonating legitimate protocols. ', 'Data Obfuscation', 0);
INSERT INTO public.attack_patterns VALUES (120, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.253603+00', NULL, 'attack-pattern--7dd95ff6-712e-4056-9626-312ea4ab4c5e', '[{"url": "https://attack.mitre.org/techniques/T1074", "external_id": "T1074", "source_name": "mitre-attack"}, {"url": "https://www.pwc.co.uk/cyber-security/pdf/cloud-hopper-report-final-v4.pdf", "description": "PwC and BAE Systems. (2017, April). Operation Cloud Hopper. Retrieved April 5, 2017.", "source_name": "PWC Cloud Hopper April 2017"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 3601}, "x_mitre_detection": {"Num": 0, "Raw": "\"Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\\n\\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\n\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2488}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3509}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\",\n      \"Shane Tully, @securitygypsy\"\n    ]", "Str": "", "Type": 5, "Index": 2270}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"File: File Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2363}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3477}}', 'T1074', 'Adversaries may stage collected data in a central location or directory prior to Exfiltration. Data may be kept in separate files or combined into one file through techniques such as [Archive Collected Data](https://attack.mitre.org/techniques/T1560). Interactive command shells may be used, and common functionality within [cmd](https://attack.mitre.org/software/S0106) and bash may be used to copy data into a staging location.(Citation: PWC Cloud Hopper April 2017)
INSERT INTO public.attack_patterns VALUES (121, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.291556+00', NULL, 'attack-pattern--c3888c54-775d-4b2f-b759-75a2ececcbfd', '[{"url": "https://attack.mitre.org/techniques/T1030", "external_id": "T1030", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2333}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). If a process maintains a long connection during which it consistently sends fixed size data packets or a process opens connections and sends fixed sized data packets at regular intervals, it may be performing an aggregate data transfer. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). If a process maintains a long connection during which it consistently sends fixed size data packets or a process opens connections and sends fixed sized data packets at regular intervals, it may be performing an aggregate data transfer. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 1485}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2255}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 1351}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2181}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2224}}', 'T1030', 'An adversary may exfiltrate data in fixed size chunks instead of whole files or limit packet sizes below certain thresholds. This approach may be used to avoid triggering network data transfer threshold alerts.', 'Data Transfer Size Limits', 0);
INSERT INTO public.attack_patterns VALUES (132, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.324879+00', NULL, 'attack-pattern--3ccef7ae-cb5e-48f6-8302-897105fbf55c', '[{"url": "https://attack.mitre.org/techniques/T1140", "external_id": "T1140", "source_name": "mitre-attack"}, {"url": "https://blog.malwarebytes.com/cybercrime/social-engineering-cybercrime/2017/03/new-targeted-attack-saudi-arabia-government/", "description": "Malwarebytes Labs. (2017, March 27). New targeted attack against Saudi Arabia Government. Retrieved July 3, 2017.", "source_name": "Malwarebytes Targeted Attack against Saudi Arabia"}, {"url": "https://www.carbonblack.com/2016/09/23/security-advisory-variants-well-known-adware-families-discovered-include-sophisticated-obfuscation-techniques-previously-associated-nation-state-attacks/", "description": "Tedesco, B. (2016, September 23). Security Alert Summary. Retrieved February 12, 2018.", "source_name": "Carbon Black Obfuscation Sept 2016"}, {"url": "https://www.volexity.com/blog/2016/11/09/powerduke-post-election-spear-phishing-campaigns-targeting-think-tanks-and-ngos/", "description": "Adair, S.. (2016, November 9). PowerDuke: Widespread Post-Election Spear Phishing Campaigns Targeting Think Tanks and NGOs. Retrieved January 11, 2017.", "source_name": "Volexity PowerDuke November 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4678}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the action of deobfuscating or decoding files or information may be difficult depending on the implementation. If the functionality is contained within malware and uses the Windows API, then attempting to detect malicious behavior before or after the action may yield better results than attempting to perform analysis on loaded libraries or API calls. If scripts are used, then collecting the scripts for analysis may be necessary. Perform process and command-line monitoring to detect potentially malicious behavior related to scripts and system utilities such as [certutil](https://attack.mitre.org/software/S0160).\\n\\nMonitor the execution file paths and command-line arguments for common archive file applications and extensions, such as those for Zip and RAR archive tools, and correlate with other suspicious behavior to reduce false positives from normal user and administrator behavior.\"", "Str": "Detecting the action of deobfuscating or decoding files or information may be difficult depending on the implementation. If the functionality is contained within malware and uses the Windows API, then attempting to detect malicious behavior before or after the action may yield better results than attempting to perform analysis on loaded libraries or API calls. If scripts are used, then collecting the scripts for analysis may be necessary. Perform process and command-line monitoring to detect potentially malicious behavior related to scripts and system utilities such as [certutil](https://attack.mitre.org/software/S0160).\n\nMonitor the execution file paths and command-line arguments for common archive file applications and extensions, such as those for Zip and RAR archive tools, and correlate with other suspicious behavior to reduce false positives from normal user and administrator behavior.", "Type": 3, "Index": 3570}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4600}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthew Demaske, Adaptforward\",\n      \"Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 3158}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Modification\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3253}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4510}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host intrusion prevention systems\",\n      \"Signature-based detection\",\n      \"Network intrusion detection system\"\n    ]", "Str": "", "Type": 5, "Index": 3395}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4553}}', 'T1140', 'Adversaries may use [Obfuscated Files or Information](https://attack.mitre.org/techniques/T1027) to hide artifacts of an intrusion from analysis. They may require separate mechanisms to decode or deobfuscate that information depending on how they intend to use it. Methods for doing that include built-in functionality of malware or by using utilities present on the system.
INSERT INTO public.attack_patterns VALUES (124, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.452163+00', NULL, 'attack-pattern--d28ef391-8ed4-45dc-bc4a-2f43abf54416', '[{"url": "https://attack.mitre.org/techniques/T1213", "external_id": "T1213", "source_name": "mitre-attack"}, {"url": "https://support.office.com/en-us/article/configure-audit-settings-for-a-site-collection-a9920c97-38c0-44f2-8bcb-4cf1e2ae22d2", "description": "Microsoft. (2017, July 19). Configure audit settings for a site collection. Retrieved April 4, 2018.", "source_name": "Microsoft SharePoint Logging"}, {"url": "https://confluence.atlassian.com/confkb/how-to-enable-user-access-logging-182943.html", "description": "Atlassian. (2018, January 9). How to Enable User Access Logging. Retrieved April 4, 2018.", "source_name": "Atlassian Confluence Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"3.1\"", "Str": "3.1", "Type": 3, "Index": 4354}, "x_mitre_detection": {"Num": 0, "Raw": "\"As information repositories generally have a considerably large user base, detection of malicious use can be non-trivial. At minimum, access to information repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) should be closely monitored and alerted upon, as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies.\\n\\nThe user access logging within Microsoft''s SharePoint can be configured to report access to certain pages and documents. (Citation: Microsoft SharePoint Logging) The user access logging within Atlassian''s Confluence can also be configured to report access to certain pages and documents through AccessLogFilter. (Citation: Atlassian Confluence Logging) Additional log storage and analysis infrastructure will likely be required for more robust detection capabilities.\"", "Str": "As information repositories generally have a considerably large user base, detection of malicious use can be non-trivial. At minimum, access to information repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) should be closely monitored and alerted upon, as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies.\n\nThe user access logging within Microsoft''s SharePoint can be configured to report access to certain pages and documents. (Citation: Microsoft SharePoint Logging) The user access logging within Atlassian''s Confluence can also be configured to report access to certain pages and documents through AccessLogFilter. (Citation: Atlassian Confluence Logging) Additional log storage and analysis infrastructure will likely be required for more robust detection capabilities.", "Type": 3, "Index": 2801}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\",\n      \"SaaS\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4216}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\",\n      \"Milos Stojadinovic\"\n    ]", "Str": "", "Type": 5, "Index": 2587}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2671}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4126}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4169}}', 'T1213', 'Adversaries may leverage information repositories to mine valuable information. Information repositories are tools that allow for storage of information, typically to facilitate collaboration or information sharing between users, and can store a wide variety of data that may aid adversaries in further objectives, or direct access to the target information.
INSERT INTO public.attack_patterns VALUES (125, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.49431+00', NULL, 'attack-pattern--3c4a2599-71ee-4405-ba1e-0e28414b4bc5', '[{"url": "https://attack.mitre.org/techniques/T1005", "external_id": "T1005", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2126}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to collect files from a system. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to collect files from a system. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1449}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1945}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1353}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1913}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Privileges to access certain files and directories\"\n    ]", "Str": "", "Type": 5, "Index": 2035}}', 'T1005', 'Adversaries may search local system sources, such as file systems or local databases, to find files of interest and sensitive data prior to Exfiltration.
INSERT INTO public.attack_patterns VALUES (520, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.534041+00', NULL, 'attack-pattern--8c32eb4d-805f-4fc5-bf60-c4d476c131b5', '[{"url": "https://attack.mitre.org/techniques/T1204", "external_id": "T1204", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 3015}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the execution of and command-line arguments for applications that may be used by an adversary to gain Initial Access that require user interaction. This includes compression applications, such as those for zip files, that can be used to [Deobfuscate/Decode Files or Information](https://attack.mitre.org/techniques/T1140) in payloads.\\n\\nAnti-virus can potentially detect malicious documents and files that are downloaded and executed on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning powershell.exe).\"", "Str": "Monitor the execution of and command-line arguments for applications that may be used by an adversary to gain Initial Access that require user interaction. This includes compression applications, such as those for zip files, that can be used to [Deobfuscate/Decode Files or Information](https://attack.mitre.org/techniques/T1140) in payloads.\n\nAnti-virus can potentially detect malicious documents and files that are downloaded and executed on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning powershell.exe).", "Type": 3, "Index": 2112}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\",\n      \"IaaS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2903}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oleg Skulkin, Group-IB\"\n    ]", "Str": "", "Type": 5, "Index": 1580}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Instance: Instance Start\",\n      \"Instance: Instance Creation\",\n      \"Image: Image Creation\",\n      \"Command: Command Execution\",\n      \"Container: Container Start\",\n      \"Container: Container Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Creation\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1648}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2813}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2856}}', 'T1204', 'An adversary may rely upon specific actions by a user in order to gain execution. Users may be subjected to social engineering to get them to execute malicious code by, for example, opening a malicious document file or link. These user actions will typically be observed as follow-on behavior from forms of [Phishing](https://attack.mitre.org/techniques/T1566).
INSERT INTO public.attack_patterns VALUES (127, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.60805+00', NULL, 'attack-pattern--1b7ba276-eedc-4951-a762-0ceea2c030ec', '[{"url": "https://attack.mitre.org/techniques/T1025", "external_id": "T1025", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2177}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to collect files from a system''s connected removable media. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to collect files from a system''s connected removable media. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1470}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1994}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1374}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1962}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Privileges to access removable media drive and files\"\n    ]", "Str": "", "Type": 5, "Index": 2084}}', 'T1025', 'Adversaries may search connected removable media on computers they have compromised to find files of interest. Sensitive data can be collected from any removable media (optical disk drive, USB memory, etc.) connected to the compromised system prior to Exfiltration. Interactive command shells may be in use, and common functionality within [cmd](https://attack.mitre.org/software/S0106) may be used to gather information. 
INSERT INTO public.attack_patterns VALUES (128, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.646568+00', NULL, 'attack-pattern--f7827069-0bf2-4764-af4f-23fae0d181b7', '[{"url": "https://attack.mitre.org/techniques/T1102/001", "external_id": "T1102.001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2980}, "x_mitre_detection": {"Num": 0, "Raw": "\"Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)\"", "Str": "Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2325}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2902}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2195}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2813}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2855}}', 'T1102.001', 'Adversaries may use an existing, legitimate external Web service to host information that points to additional command and control (C2) infrastructure. Adversaries may post content, known as a dead drop resolver, on Web services with embedded (and often obfuscated/encoded) domains or IP addresses. Once infected, victims will reach out to and be redirected by these resolvers.
INSERT INTO public.attack_patterns VALUES (129, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.678315+00', NULL, 'attack-pattern--5909f20f-3c39-4795-be06-ef1ea40d350b', '[{"url": "https://attack.mitre.org/techniques/T1491", "external_id": "T1491", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2043}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor internal and external websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.\\n\\n\"", "Str": "Monitor internal and external websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.\n\n", "Type": 3, "Index": 1477}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1951}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 1861}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"File: File Creation\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1282}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1919}}', 'T1491', 'Adversaries may modify visual content available internally or externally to an enterprise network. Reasons for [Defacement](https://attack.mitre.org/techniques/T1491) include delivering messaging, intimidation, or claiming (possibly false) credit for an intrusion. Disturbing or offensive images may be used as a part of [Defacement](https://attack.mitre.org/techniques/T1491) in order to cause user discomfort, or to pressure compliance with accompanying messages. 
INSERT INTO public.attack_patterns VALUES (130, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.728284+00', NULL, 'attack-pattern--6151cbea-819b-455a-9fa6-99a1cc58797d', '[{"url": "https://attack.mitre.org/techniques/T1078/001", "external_id": "T1078.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/70.html", "external_id": "CAPEC-70", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/windows/security/identity-protection/access-control/local-accounts", "description": "Microsoft. (2018, December 9). Local Accounts. Retrieved February 11, 2019.", "source_name": "Microsoft Local Accounts Feb 2019"}, {"url": "https://docs.aws.amazon.com/IAM/latest/UserGuide/id_root-user.html", "description": "Amazon. (n.d.). AWS Account Root User. Retrieved April 5, 2021.", "source_name": "AWS Root User"}, {"url": "https://www.microsoft.com/security/blog/2020/04/02/attack-matrix-kubernetes/", "description": "Weizman, Y. (2020, April 2). Threat Matrix for Kubernetes. Retrieved March 30, 2021.", "source_name": "Threat Matrix for Kubernetes"}, {"url": "https://github.com/rapid7/metasploit-framework/tree/master/modules/exploits/linux/ssh", "description": "Metasploit. (n.d.). Retrieved April 12, 2019.", "source_name": "Metasploit SSH Module"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4396}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor whether default accounts have been activated or logged into. These audits should also include checks on any appliances and applications for default credentials or SSH keys, and if any are discovered, they should be updated immediately.\"", "Str": "Monitor whether default accounts have been activated or logged into. These audits should also include checks on any appliances and applications for default credentials or SSH keys, and if any are discovered, they should be updated immediately.", "Type": 3, "Index": 3816}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4206}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3685}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4094}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4136}}', 'T1078.001', 'Adversaries may obtain and abuse credentials of a default account as a means of gaining Initial Access, Persistence, Privilege Escalation, or Defense Evasion. Default accounts are those that are built-into an OS, such as the Guest or Administrator accounts on Windows systems. Default accounts also include default factory/provider set accounts on other types of systems, software, or devices, including the root user account in AWS and the default service account in Kubernetes.(Citation: Microsoft Local Accounts Feb 2019)(Citation: AWS Root User)(Citation: Threat Matrix for Kubernetes)
INSERT INTO public.attack_patterns VALUES (134, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.857634+00', NULL, 'attack-pattern--ed730f20-0e44-48b9-85f8-0e2adeb76867', '[{"url": "https://attack.mitre.org/techniques/T1591/001", "external_id": "T1591.001", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/broadvoice-leaks-350m-records-voicemail-transcripts/160158/", "description": "Seals, T. (2020, October 15). Broadvoice Leak Exposes 350M Records, Personal Voicemail Transcripts. Retrieved October 20, 2020.", "source_name": "ThreatPost Broadvoice Leak"}, {"url": "https://www.dobsearch.com/business-lookup/", "description": "Concert Technologies . (n.d.). Business Lookup - Company Name Search. Retrieved October 20, 2020.", "source_name": "DOB Business Lookup"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3280}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2839}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3236}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3205}}', 'T1591.001', 'Adversaries may gather the victim''s physical location(s) that can be used during targeting. Information about physical locations of a target organization may include a variety of details, including where key resources and infrastructure are housed. Physical locations may also indicate what legal jurisdiction and/or authorities the victim operates within.
INSERT INTO public.attack_patterns VALUES (136, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.936661+00', NULL, 'attack-pattern--1cec9319-743b-4840-bb65-431547bce82a', '[{"url": "https://attack.mitre.org/techniques/T1587/003", "external_id": "T1587.003", "source_name": "mitre-attack"}, {"url": "https://www.splunk.com/en_us/blog/security/tall-tales-of-hunting-with-tls-ssl-certificates.html", "description": "Kovar, R. (2017, December 11). Tall Tales of Hunting with TLS/SSL Certificates. Retrieved October 16, 2020.", "source_name": "Splunk Kovar Certificates 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3154}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider use of services that may aid in the tracking of certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017)\\n\\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001), [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002), and/or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).\"", "Str": "Consider use of services that may aid in the tracking of certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017)\n\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001), [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002), and/or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).", "Type": 3, "Index": 2482}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3110}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3079}}', 'T1587.003', 'Adversaries may create self-signed SSL/TLS certificates that can be used during targeting. SSL/TLS certificates are designed to instill trust. They include information about the key, information about its owner''s identity, and the digital signature of an entity that has verified the certificate''s contents are correct. If the signature is valid, and the person examining the certificate trusts the signer, then they know they can use that key to communicate with its owner. In the case of self-signing, digital certificates will lack the element of trust associated with the signature of a third-party certificate authority (CA).
INSERT INTO public.attack_patterns VALUES (139, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.048558+00', NULL, 'attack-pattern--0bda01d5-4c1d-4062-8ee2-6872334383c3', '[{"url": "https://attack.mitre.org/techniques/T1498/001", "external_id": "T1498.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/125.html", "external_id": "CAPEC-125", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/486.html", "external_id": "CAPEC-486", "source_name": "capec"}, {"url": "https://www.justice.gov/opa/pr/seven-iranians-working-islamic-revolutionary-guard-corps-affiliated-entities-charged", "description": "Preet Bharara, US Attorney. (2016, March 24). Retrieved April 23, 2019.", "source_name": "USNYAG IranianBotnet March 2016"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4428}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of a network flood can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect a network flood event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.\"", "Str": "Detection of a network flood can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect a network flood event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.", "Type": 3, "Index": 3237}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4258}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4166}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3121}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4227}}', 'T1498.001', 'Adversaries may attempt to cause a denial of service (DoS) by directly sending a high-volume of network traffic to a target. [Direct Network Flood](https://attack.mitre.org/techniques/T1498/001) are when one or more systems are used to send a high-volume of network packets towards the targeted service''s network. Almost any network protocol may be used for flooding. Stateless protocols such as UDP or ICMP are commonly used but stateful protocols such as TCP can be used as well.
INSERT INTO public.attack_patterns VALUES (140, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.081151+00', NULL, 'attack-pattern--0c8ab3eb-df48-4b9c-ace7-beacaac81cc5', '[{"url": "https://attack.mitre.org/techniques/T1006", "external_id": "T1006", "source_name": "mitre-attack"}, {"url": "http://www.codeproject.com/Articles/32169/FDump-Dumping-File-Sectors-Directly-from-Disk-usin", "description": "Hakobyan, A. (2009, January 8). FDump - Dumping File Sectors Directly from Disk using Logical Offsets. Retrieved November 12, 2014.", "source_name": "Hakobyan 2009"}, {"url": "https://github.com/PowerShellMafia/PowerSploit/blob/master/Exfiltration/Invoke-NinjaCopy.ps1", "description": "Bialek, J. (2015, December 16). Invoke-NinjaCopy.ps1. Retrieved June 2, 2016.", "source_name": "Github PowerSploit Ninjacopy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2837}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor handle opens on drive volumes that are made by processes to determine when they may directly access logical drives. (Citation: Github PowerSploit Ninjacopy)\\n\\nMonitor processes and command-line arguments for actions that could be taken to copy files from the logical drive and evade common file system protections. Since this technique may also be used through [PowerShell](https://attack.mitre.org/techniques/T1059/001), additional logging of PowerShell scripts is recommended.\"", "Str": "Monitor handle opens on drive volumes that are made by processes to determine when they may directly access logical drives. (Citation: Github PowerSploit Ninjacopy)\n\nMonitor processes and command-line arguments for actions that could be taken to copy files from the logical drive and evade common file system protections. Since this technique may also be used through [PowerShell](https://attack.mitre.org/techniques/T1059/001), additional logging of PowerShell scripts is recommended.", "Type": 3, "Index": 2168}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2789}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Drive: Drive Access\"\n    ]", "Str": "", "Type": 5, "Index": 1968}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2690}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"File monitoring\",\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 2073}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2733}}', 'T1006', 'Adversaries may directly access a volume to bypass file access controls and file system monitoring. Windows allows programs to have direct access to logical volumes. Programs with direct access may read and write files directly from the drive by analyzing file system data structures. This technique bypasses Windows file access controls as well as file system monitoring tools. (Citation: Hakobyan 2009)
INSERT INTO public.attack_patterns VALUES (141, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.121777+00', NULL, 'attack-pattern--cacc40da-4c9e-462c-80d5-fd70a178b12d', '[{"url": "https://attack.mitre.org/techniques/T1562/008", "external_id": "T1562.008", "source_name": "mitre-attack"}, {"url": "https://expel.io/blog/following-cloudtrail-generating-aws-security-signals-sumo-logic/", "description": "Dan Whalen. (2019, September 10). Following the CloudTrail: Generating strong AWS security signals with Sumo Logic. Retrieved October 16, 2020.", "source_name": "Following the CloudTrail: Generating strong AWS security signals with Sumo Logic"}, {"url": "https://docs.aws.amazon.com/awscloudtrail/latest/userguide/stop-cloudtrail-from-sending-events-to-cloudwatch-logs.html", "description": "Amazon Web Services. (n.d.). Stopping CloudTrail from Sending Events to CloudWatch Logs. Retrieved October 16, 2020.", "source_name": "Stopping CloudTrail from Sending Events to CloudWatch Logs"}, {"url": "https://cloud.google.com/logging/docs/audit/configure-data-access", "description": "Google. (n.d.). Configuring Data Access audit logs. Retrieved October 16, 2020.", "source_name": "Configuring Data Access audit logs"}, {"url": "https://docs.microsoft.com/en-us/cli/azure/monitor/diagnostic-settings?view=azure-cli-latest#az_monitor_diagnostic_settings_delete", "description": "Microsoft. (n.d.). az monitor diagnostic-settings. Retrieved October 16, 2020.", "source_name": "az monitor diagnostic-settings"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3925}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor logs for API calls to disable logging. In AWS, monitor for: <code>StopLogging</code> and <code>DeleteTrail</code>.(Citation: Stopping CloudTrail from Sending Events to CloudWatch Logs) In GCP, monitor for: <code>google.logging.v2.ConfigServiceV2.UpdateSink</code>.(Citation: Configuring Data Access audit logs)  In Azure, monitor for <code>az monitor diagnostic-settings delete</code>.(Citation: az monitor diagnostic-settings) Additionally, a sudden loss of a log source may indicate that it has been disabled.\"", "Str": "Monitor logs for API calls to disable logging. In AWS, monitor for: <code>StopLogging</code> and <code>DeleteTrail</code>.(Citation: Stopping CloudTrail from Sending Events to CloudWatch Logs) In GCP, monitor for: <code>google.logging.v2.ConfigServiceV2.UpdateSink</code>.(Citation: Configuring Data Access audit logs)  In Azure, monitor for <code>az monitor diagnostic-settings delete</code>.(Citation: az monitor diagnostic-settings) Additionally, a sudden loss of a log source may indicate that it has been disabled.", "Type": 3, "Index": 3237}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3880}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Syed Ummar Farooqh, McAfee\",\n      \"Prasad Somasamudram, McAfee\",\n      \"Sekhar Sarukkai, McAfee \",\n      \"Ibrahim Ali Khan\",\n      \"Alex Soler, AttackIQ\",\n      \"Janantha Marasinghe\",\n      \"Matt Snyder, VMware\"\n    ]", "Str": "", "Type": 5, "Index": 2850}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Cloud Service: Cloud Service Modification\",\n      \"Cloud Service: Cloud Service Disable\"\n    ]", "Str": "", "Type": 5, "Index": 3107}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3791}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3833}}', 'T1562.008', 'An adversary may disable cloud logging capabilities and integrations to limit what data is collected on their activities and avoid detection. 
INSERT INTO public.attack_patterns VALUES (142, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.150651+00', NULL, 'attack-pattern--7efba77e-3bc4-4ca5-8292-d8201dcd64b5', '[{"url": "https://attack.mitre.org/techniques/T1600/002", "external_id": "T1600.002", "source_name": "mitre-attack"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2865}, "x_mitre_detection": {"Num": 0, "Raw": "\"There is no documented method for defenders to directly identify behaviors that disable cryptographic hardware. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601) and [Network Device CLI](https://attack.mitre.org/techniques/T1059/008). Some detection methods require vendor support to aid in investigation.\"", "Str": "There is no documented method for defenders to directly identify behaviors that disable cryptographic hardware. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601) and [Network Device CLI](https://attack.mitre.org/techniques/T1059/008). Some detection methods require vendor support to aid in investigation.", "Type": 3, "Index": 2283}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2817}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2217}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2719}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2761}}', 'T1600.002', 'Adversaries disable a network device’s dedicated hardware encryption, which may enable them to leverage weaknesses in software encryption in order to reduce the effort involved in collecting, manipulating, and exfiltrating transmitted data.
INSERT INTO public.attack_patterns VALUES (145, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.280194+00', NULL, 'attack-pattern--5372c5fe-f424-4def-bcd5-d3a8e770f07b', '[{"url": "https://attack.mitre.org/techniques/T1562/004", "external_id": "T1562.004", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1943}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments to see if firewalls are disabled or modified. Monitor Registry edits to keys that manage firewalls.\"", "Str": "Monitor processes and command-line arguments to see if firewalls are disabled or modified. Monitor Registry edits to keys that manage firewalls.", "Type": 3, "Index": 1655}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1865}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Firewall: Firewall Disable\",\n      \"Firewall: Firewall Rule Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1385}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1834}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Firewall\"\n    ]", "Str": "", "Type": 5, "Index": 1604}}', 'T1562.004', 'Adversaries may disable or modify system firewalls in order to bypass controls limiting network usage. Changes could be disabling the entire mechanism as well as adding, deleting, or modifying particular rules. This can be done numerous ways depending on the operating system, including via command-line, editing Windows Registry keys, and Windows Control Panel.
INSERT INTO public.attack_patterns VALUES (146, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.336879+00', NULL, 'attack-pattern--ac08589e-ee59-4935-8667-d845e38fe579', '[{"url": "https://attack.mitre.org/techniques/T1562/001", "external_id": "T1562.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/578.html", "external_id": "CAPEC-578", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2513}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments to see if security tools are killed or stop running. Monitor Registry edits for modifications to services and startup programs that correspond to security tools. Lack of log events may be suspicious.\"", "Str": "Monitor processes and command-line arguments to see if security tools are killed or stop running. Monitor Registry edits for modifications to services and startup programs that correspond to security tools. Lack of log events may be suspicious.", "Type": 3, "Index": 2010}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\",\n      \"Containers\",\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 2401}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ziv Karliner, @ziv_kr, Team Nautilus Aqua Security\",\n      \"Nathaniel Quist, Palo Alto Networks\",\n      \"Gal Singer, @galsinger29, Team Nautilus Aqua Security\"\n    ]", "Str": "", "Type": 5, "Index": 1325}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Termination\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Windows Registry: Windows Registry Key Deletion\",\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"Sensor Health: Host Status\"\n    ]", "Str": "", "Type": 5, "Index": 1529}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2289}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Log analysis\",\n      \"Signature-based detection\",\n      \"Host intrusion prevention systems\",\n      \"File monitoring\"\n    ]", "Str": "", "Type": 5, "Index": 1832}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2331}}', 'T1562.001', 'Adversaries may disable security tools to avoid possible detection of their tools and activities. This can take the form of killing security software or event logging processes, deleting Registry keys so that tools do not start at run time, or other methods to interfere with security tools scanning or reporting information.', 'Disable or Modify Tools', 246);
INSERT INTO public.attack_patterns VALUES (156, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.378389+00', NULL, 'attack-pattern--118f61a5-eb3e-4fb6-931f-2096647f4ecd', '[{"url": "https://attack.mitre.org/techniques/T1568/002", "external_id": "T1568.002", "source_name": "mitre-attack"}, {"url": "http://go.cybereason.com/rs/996-YZT-709/images/Cybereason-Lab-Analysis-Dissecting-DGAs-Eight-Real-World-DGA-Variants.pdf", "description": "Sternfeld, U. (2016). Dissecting Domain Generation Algorithms: Eight Real World DGA Variants. Retrieved February 18, 2019.", "source_name": "Cybereason Dissecting DGAs"}, {"url": "https://umbrella.cisco.com/blog/2016/10/10/domain-generation-algorithms-effective/", "description": "Scarfo, A. (2016, October 10). Domain Generation Algorithms – Why so effective?. Retrieved February 18, 2019.", "source_name": "Cisco Umbrella DGA"}, {"url": "https://unit42.paloaltonetworks.com/threat-brief-understanding-domain-generation-algorithms-dga/", "description": "Unit 42. (2019, February 7). Threat Brief: Understanding Domain Generation Algorithms (DGA). Retrieved February 19, 2019.", "source_name": "Unit 42 DGA Feb 2019"}, {"url": "http://blog.talosintelligence.com/2017/09/avast-distributes-malware.html", "description": "Brumaghin, E. et al. (2017, September 18). CCleanup: A Vast Number of Machines at Risk. Retrieved March 9, 2018.", "source_name": "Talos CCleanup 2017"}, {"url": "https://blogs.akamai.com/2018/01/a-death-match-of-domain-generation-algorithms.html", "description": "Liu, H. and Yuzifovich, Y. (2018, January 9). A Death Match of Domain Generation Algorithms. Retrieved February 18, 2019.", "source_name": "Akamai DGA Mitigation"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/03/dissecting_one_ofap.html", "description": "Dunwoody, M.. (2017, April 3). Dissecting One of APT29’s Fileless WMI and PowerShell Backdoors (POSHSPY). Retrieved April 5, 2017.", "source_name": "FireEye POSHSPY April 2017"}, {"url": "https://www.welivesecurity.com/2017/12/21/sednit-update-fancy-bear-spent-year/", "description": "ESET. (2017, December 21). Sednit update: How Fancy Bear Spent the Year. Retrieved February 18, 2019.", "source_name": "ESET Sednit 2017 Activity"}, {"url": "https://datadrivensecurity.info/blog/posts/2014/Oct/dga-part2/", "description": "Jacobs, J. (2014, October 2). Building a DGA Classifier: Part 2, Feature Engineering. Retrieved February 18, 2019.", "source_name": "Data Driven Security DGA"}, {"url": "http://csis.pace.edu/~ctappert/srd2017/2017PDF/d4.pdf", "description": "Chen, L., Wang, T.. (2017, May 5). Detecting Algorithmically Generated Domains Using Data Visualization and N-Grams Methods . Retrieved April 26, 2019.", "source_name": "Pace University Detecting DGA May 2017"}, {"url": "https://arxiv.org/pdf/1611.00791.pdf", "description": "Ahuja, A., Anderson, H., Grant, D., Woodbridge, J.. (2016, November 2). Predicting Domain Generation Algorithms with Long Short-Term Memory Networks. Retrieved April 26, 2019.", "source_name": "Elastic Predicting DGA"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 7255}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting dynamically generated domains can be challenging due to the number of different DGA algorithms, constantly evolving malware families, and the increasing complexity of the algorithms. There is a myriad of approaches for detecting a pseudo-randomly generated domain name, including using frequency analysis, Markov chains, entropy, proportion of dictionary words, ratio of vowels to other characters, and more.(Citation: Data Driven Security DGA) CDN domains may trigger these detections due to the format of their domain names. In addition to detecting a DGA domain based on the name, another more general approach for detecting a suspicious domain is to check for recently registered names or for rarely visited domains.\\n\\nMachine learning approaches to detecting DGA domains have been developed and have seen success in applications. One approach is to use N-Gram methods to determine a randomness score for strings used in the domain name. If the randomness score is high, and the domains are not whitelisted (CDN, etc), then it may be determined if a domain is related to a legitimate host or DGA.(Citation: Pace University Detecting DGA May 2017) Another approach is to use deep learning to classify domains as DGA-generated.(Citation: Elastic Predicting DGA)\"", "Str": "Detecting dynamically generated domains can be challenging due to the number of different DGA algorithms, constantly evolving malware families, and the increasing complexity of the algorithms. There is a myriad of approaches for detecting a pseudo-randomly generated domain name, including using frequency analysis, Markov chains, entropy, proportion of dictionary words, ratio of vowels to other characters, and more.(Citation: Data Driven Security DGA) CDN domains may trigger these detections due to the format of their domain names. In addition to detecting a DGA domain based on the name, another more general approach for detecting a suspicious domain is to check for recently registered names or for rarely visited domains.\n\nMachine learning approaches to detecting DGA domains have been developed and have seen success in applications. One approach is to use N-Gram methods to determine a randomness score for strings used in the domain name. If the randomness score is high, and the domains are not whitelisted (CDN, etc), then it may be determined if a domain is related to a legitimate host or DGA.(Citation: Pace University Detecting DGA May 2017) Another approach is to use deep learning to classify domains as DGA-generated.(Citation: Elastic Predicting DGA)", "Type": 3, "Index": 5779}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7177}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ryan Benson, Exabeam\",\n      \"Barry Shteiman, Exabeam\",\n      \"Sylvain Gil, Exabeam\"\n    ]", "Str": "", "Type": 5, "Index": 5516}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 5645}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7088}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7130}}', 'T1568.002', 'Adversaries may make use of Domain Generation Algorithms (DGAs) to dynamically identify a destination domain for command and control traffic rather than relying on a list of static IP addresses or domains. This has the advantage of making it much harder for defenders block, track, or take over the command and control channel, as there potentially could be thousands of domains that malware can check for instructions.(Citation: Cybereason Dissecting DGAs)(Citation: Cisco Umbrella DGA)(Citation: Unit 42 DGA Feb 2019)
INSERT INTO public.attack_patterns VALUES (148, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.451903+00', NULL, 'attack-pattern--0af0ca99-357d-4ba1-805f-674fdfb7bef9', '[{"url": "https://attack.mitre.org/techniques/T1561/002", "external_id": "T1561.002", "source_name": "mitre-attack"}, {"url": "https://www.symantec.com/connect/blogs/shamoon-attacks", "description": "Symantec. (2012, August 16). The Shamoon Attacks. Retrieved March 14, 2019.", "source_name": "Symantec Shamoon 2012"}, {"url": "https://www.fireeye.com/blog/threat-research/2016/11/fireeye_respondsto.html", "description": "FireEye. (2016, November 30). FireEye Responds to Wave of Destructive Cyber Attacks in Gulf Region. Retrieved January 11, 2017.", "source_name": "FireEye Shamoon Nov 2016"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/11/unit42-shamoon-2-return-disttrack-wiper/", "description": "Falcone, R.. (2016, November 30). Shamoon 2: Return of the Disttrack Wiper. Retrieved January 11, 2017.", "source_name": "Palo Alto Shamoon Nov 2016"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/07180722/Report_Shamoon_StoneDrill_final.pdf", "description": "Kaspersky Lab. (2017, March 7). From Shamoon to StoneDrill: Wipers attacking Saudi organizations and beyond. Retrieved March 14, 2019.", "source_name": "Kaspersky StoneDrill 2017"}, {"url": "https://unit42.paloaltonetworks.com/shamoon-3-targets-oil-gas-organization/", "description": "Falcone, R. (2018, December 13). Shamoon 3 Targets Oil and Gas Organization. Retrieved March 14, 2019.", "source_name": "Unit 42 Shamoon3 2018"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/sysmon", "description": "Russinovich, M. & Garnier, T. (2017, May 22). Sysmon v6.20. Retrieved December 13, 2017.", "source_name": "Microsoft Sysmon v6 May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5080}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for attempts to read/write to sensitive locations like the master boot record and the disk partition table. Monitor for direct access read/write attempts using the <code>\\\\\\\\\\\\\\\\.\\\\\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.\"", "Str": "Look for attempts to read/write to sensitive locations like the master boot record and the disk partition table. Monitor for direct access read/write attempts using the <code>\\\\\\\\.\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.", "Type": 3, "Index": 4466}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5002}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4799}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Driver: Driver Load\",\n      \"Drive: Drive Access\",\n      \"Drive: Drive Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4269}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4860}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4902}}', 'T1561.002', 'Adversaries may corrupt or wipe the disk data structures on a hard drive necessary to boot a system; targeting specific critical systems or in large numbers in a network to interrupt availability to system and network resources. 
INSERT INTO public.attack_patterns VALUES (149, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.489758+00', NULL, 'attack-pattern--1988cc35-ced8-4dad-b2d1-7628488fa967', '[{"url": "https://attack.mitre.org/techniques/T1561", "external_id": "T1561", "source_name": "mitre-attack"}, {"url": "https://operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Destructive-Malware-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Destructive Malware Report. Retrieved March 2, 2016.", "source_name": "Novetta Blockbuster Destructive Malware"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/sysmon", "description": "Russinovich, M. & Garnier, T. (2017, May 22). Sysmon v6.20. Retrieved December 13, 2017.", "source_name": "Microsoft Sysmon v6 May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3244}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for attempts to read/write to sensitive locations like the partition boot sector, master boot record, disk partition table, or BIOS parameter block/superblock. Monitor for direct access read/write attempts using the <code>\\\\\\\\\\\\\\\\.\\\\\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.\"", "Str": "Look for attempts to read/write to sensitive locations like the partition boot sector, master boot record, disk partition table, or BIOS parameter block/superblock. Monitor for direct access read/write attempts using the <code>\\\\\\\\.\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.", "Type": 3, "Index": 2577}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3166}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 2962}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Driver: Driver Load\",\n      \"Drive: Drive Access\",\n      \"Drive: Drive Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2380}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3023}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\",\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3066}}', 'T1561', 'Adversaries may wipe or corrupt raw disk data on specific systems or in large numbers in a network to interrupt availability to system and network resources. With direct write access to a disk, adversaries may attempt to overwrite portions of disk data. Adversaries may opt to wipe arbitrary portions of disk data and/or wipe disk structures like the master boot record (MBR). A complete wipe of all disk sectors may be attempted.
INSERT INTO public.attack_patterns VALUES (162, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.580601+00', NULL, 'attack-pattern--40f5caa0-4cb7-4117-89fc-d421bb493df3', '[{"url": "https://attack.mitre.org/techniques/T1583/001", "external_id": "T1583.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/630.html", "external_id": "CAPEC-630", "source_name": "capec"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/aa20-258a", "description": "CISA. (2020, September 14). Alert (AA20-258A): Chinese Ministry of State Security-Affiliated Cyber Threat Actor Activity. Retrieved October 1, 2020.", "source_name": "CISA MSS Sep 2020"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/rpt-apt28.pdf", "description": "FireEye. (2015). APT28: A WINDOW INTO RUSSIA’S CYBER ESPIONAGE OPERATIONS?. Retrieved August 19, 2015.", "source_name": "FireEye APT28"}, {"url": "https://www.zdnet.com/article/paypal-alert-beware-the-paypai-scam-5000109103/", "description": "Bob Sullivan. (2000, July 24). PayPal alert! Beware the ''PaypaI'' scam. Retrieved March 2, 2017.", "source_name": "PaypalScam"}, {"url": "https://us-cert.cisa.gov/ncas/tips/ST05-016", "description": "CISA. (2019, September 27). Security Tip (ST05-016): Understanding Internationalized Domain Names. Retrieved October 20, 2020.", "source_name": "CISA IDN ST05-016"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4982}, "x_mitre_detection": {"Num": 0, "Raw": "\"Domain registration information is, by design, captured in public registration logs. Consider use of services that may aid in tracking of newly acquired domains, such as WHOIS databases and/or passive DNS. In some cases it may be possible to pivot on known pieces of domain registration information to uncover other infrastructure purchased by the adversary. Consider monitoring for domains created with a similar structure to your own, including under a different TLD. Though various tools and services exist to track, query, and monitor domain name registration information, tracking across multiple DNS infrastructures can require multiple tools/services or more advanced analytics.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access and Command and Control.\"", "Str": "Domain registration information is, by design, captured in public registration logs. Consider use of services that may aid in tracking of newly acquired domains, such as WHOIS databases and/or passive DNS. In some cases it may be possible to pivot on known pieces of domain registration information to uncover other infrastructure purchased by the adversary. Consider monitoring for domains created with a similar structure to your own, including under a different TLD. Though various tools and services exist to track, query, and monitor domain name registration information, tracking across multiple DNS infrastructures can require multiple tools/services or more advanced analytics.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access and Command and Control.", "Type": 3, "Index": 4050}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4938}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Wes Hurd\",\n      \"Vinayak Wadhwa, Lucideus\",\n      \"Deloitte Threat Library Team\"\n    ]", "Str": "", "Type": 5, "Index": 3927}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4907}}', 'T1583.001', 'Adversaries may purchase domains that can be used during targeting. Domain names are the human readable names used to represent one or more IP addresses. They can be purchased or, in some cases, acquired for free.
INSERT INTO public.attack_patterns VALUES (152, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.670559+00', NULL, 'attack-pattern--21875073-b0ee-49e3-9077-1e2a885359af', '[{"url": "https://attack.mitre.org/techniques/T1087/002", "external_id": "T1087.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/575.html", "external_id": "CAPEC-575", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2478}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n", "Type": 3, "Index": 1517}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2400}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1413}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2311}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2353}}', 'T1087.002', 'Adversaries may attempt to get a listing of domain accounts. This information can help adversaries determine which domain accounts exist to aid in follow-on behavior.
INSERT INTO public.attack_patterns VALUES (153, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.709499+00', NULL, 'attack-pattern--c3d4bdd9-2cfe-4a80-9d0c-07a29ecdce8f', '[{"url": "https://attack.mitre.org/techniques/T1078/002", "external_id": "T1078.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/560.html", "external_id": "CAPEC-560", "source_name": "capec"}, {"url": "https://technet.microsoft.com/en-us/library/dn535501.aspx", "description": "Microsoft. (2016, April 15). Attractive Accounts for Credential Theft. Retrieved June 3, 2016.", "source_name": "TechNet Credential Theft"}, {"url": "https://docs.microsoft.com/en-us/windows/security/identity-protection/access-control/active-directory-accounts", "description": "Microsoft. (2019, August 23). Active Directory Accounts. Retrieved March 13, 2020.", "source_name": "Microsoft AD Accounts"}, {"url": "https://technet.microsoft.com/en-us/library/dn487457.aspx", "description": "Microsoft. (2016, April 15). Audit Policy Recommendations. Retrieved June 3, 2016.", "source_name": "TechNet Audit Policy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4132}, "x_mitre_detection": {"Num": 0, "Raw": "\"Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\\n\\nPerform regular audits of domain accounts to detect accounts that may have been created by an adversary for persistence.\"", "Str": "Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\n\nPerform regular audits of domain accounts to detect accounts that may have been created by an adversary for persistence.", "Type": 3, "Index": 3003}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4054}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2872}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3942}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3984}}', 'T1078.002', 'Adversaries may obtain and abuse credentials of a domain account as a means of gaining Initial Access, Persistence, Privilege Escalation, or Defense Evasion. (Citation: TechNet Credential Theft) Domain accounts are those managed by Active Directory Domain Services where access and permissions are configured across systems and services that are part of that domain. Domain accounts can cover users, administrators, and services.(Citation: Microsoft AD Accounts)
INSERT INTO public.attack_patterns VALUES (154, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.749718+00', NULL, 'attack-pattern--d4b96d2c-1032-4b22-9235-2b5b649d0605', '[{"url": "https://attack.mitre.org/techniques/T1556/001", "external_id": "T1556.001", "source_name": "mitre-attack"}, {"url": "https://www.secureworks.com/research/skeleton-key-malware-analysis", "description": "Dell SecureWorks. (2015, January 12). Skeleton Key Malware Analysis. Retrieved April 8, 2019.", "source_name": "Dell Skeleton"}, {"url": "https://technet.microsoft.com/en-us/library/dn487457.aspx", "description": "Microsoft. (2016, April 15). Audit Policy Recommendations. Retrieved June 3, 2016.", "source_name": "TechNet Audit Policy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3980}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for calls to <code>OpenProcess</code> that can be used to manipulate lsass.exe running on a domain controller as well as for malicious modifications to functions exported from authentication-related system DLLs (such as cryptdll.dll and samsrv.dll).(Citation: Dell Skeleton)\\n\\nConfigure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g. a user has an active login session but has not entered the building or does not have VPN access). \"", "Str": "Monitor for calls to <code>OpenProcess</code> that can be used to manipulate lsass.exe running on a domain controller as well as for malicious modifications to functions exported from authentication-related system DLLs (such as cryptdll.dll and samsrv.dll).(Citation: Dell Skeleton)\n\nConfigure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g. a user has an active login session but has not entered the building or does not have VPN access). ", "Type": 3, "Index": 2733}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3932}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2552}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3834}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3876}}', 'T1556.001', 'Adversaries may patch the authentication process on a domain controller to bypass the typical authentication mechanisms and enable access to accounts. 
INSERT INTO public.attack_patterns VALUES (155, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.78771+00', NULL, 'attack-pattern--ca9d3402-ada3-484d-876a-d717bd6e05f2', '[{"url": "https://attack.mitre.org/techniques/T1090/004", "external_id": "T1090.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/481.html", "external_id": "CAPEC-481", "source_name": "capec"}, {"url": "http://www.icir.org/vern/papers/meek-PETS-2015.pdf", "description": "David Fifield, Chang Lan, Rod Hynes, Percy Wegmann, and Vern Paxson. (2015). Blocking-resistant communication through domain fronting. Retrieved November 20, 2017.", "source_name": "Fifield Blocking Resistent Communication through domain fronting 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3013}, "x_mitre_detection": {"Num": 0, "Raw": "\"If SSL inspection is in place or the traffic is not encrypted, the Host field of the HTTP header can be checked if it matches the HTTPS SNI or against a blocklist or allowlist of domain names. (Citation: Fifield Blocking Resistent Communication through domain fronting 2015)\"", "Str": "If SSL inspection is in place or the traffic is not encrypted, the Host field of the HTTP header can be checked if it matches the HTTPS SNI or against a blocklist or allowlist of domain names. (Citation: Fifield Blocking Resistent Communication through domain fronting 2015)", "Type": 3, "Index": 2595}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2935}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matt Kelly, @breakersall\"\n    ]", "Str": "", "Type": 5, "Index": 2442}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2512}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2904}}', 'T1090.004', 'Adversaries may take advantage of routing schemes in Content Delivery Networks (CDNs) and other services which host multiple domains to obfuscate the intended destination of HTTPS traffic or traffic tunneled through HTTPS. (Citation: Fifield Blocking Resistent Communication through domain fronting 2015) Domain fronting involves using different domain names in the SNI field of the TLS header and the Host field of the HTTP header. If both domains are served from the same CDN, then the CDN may route to the address specified in the HTTP header after unwrapping the TLS header. A variation of the the technique, "domainless" fronting, utilizes a SNI field that is left blank; this may allow the fronting to work even when the CDN attempts to validate that the SNI and HTTP Host fields match (if the blank SNI fields are ignored).
INSERT INTO public.attack_patterns VALUES (158, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.86744+00', NULL, 'attack-pattern--ebb42bbe-62d7-47d7-a55f-3b08b61d792d', '[{"url": "https://attack.mitre.org/techniques/T1484", "external_id": "T1484", "source_name": "mitre-attack"}, {"url": "https://adsecurity.org/?p=2716", "description": "Metcalf, S. (2016, March 14). Sneaky Active Directory Persistence #17: Group Policy. Retrieved March 5, 2019.", "source_name": "ADSecurity GPO Persistence 2016"}, {"url": "https://wald0.com/?p=179", "description": "Robbins, A. (2018, April 2). A Red Teamer’s Guide to GPOs and OUs. Retrieved March 5, 2019.", "source_name": "Wald0 Guide to GPOs"}, {"url": "http://www.harmj0y.net/blog/redteaming/abusing-gpo-permissions/", "description": "Schroeder, W. (2016, March 17). Abusing GPO Permissions. Retrieved March 5, 2019.", "source_name": "Harmj0y Abusing GPO Permissions"}, {"url": "https://msrc-blog.microsoft.com/2020/12/13/customer-guidance-on-recent-nation-state-cyber-attacks/", "description": "MSRC. (2020, December 13). Customer Guidance on Recent Nation-State Cyber Attacks. Retrieved December 30, 2020.", "source_name": "Microsoft - Customer Guidance on Recent Nation-State Cyber Attacks"}, {"url": "https://github.com/Azure/Azure-Sentinel/blob/master/Detections/AuditLogs/ADFSDomainTrustMods.yaml", "description": "Microsoft. (2020, December). Azure Sentinel Detections. Retrieved December 30, 2020.", "source_name": "Microsoft - Azure Sentinel ADFSDomainTrustMods"}, {"url": "https://www.microsoft.com/security/blog/2020/12/28/using-microsoft-365-defender-to-coordinate-protection-against-solorigate/", "description": "Microsoft 365 Defender Team. (2020, December 28). Using Microsoft 365 Defender to protect against Solorigate. Retrieved January 7, 2021.", "source_name": "Microsoft 365 Defender Solorigate"}, {"url": "https://www.sygnia.co/golden-saml-advisory", "description": "Sygnia. (2020, December). Detection and Hunting of Golden SAML Attack. Retrieved January 6, 2021.", "source_name": "Sygnia Golden SAML"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/aa21-008a", "description": "CISA. (2021, January 8). Detecting Post-Compromise Threat Activity in Microsoft Cloud Environments. Retrieved January 8, 2021.", "source_name": "CISA SolarWinds Cloud Detection"}, {"url": "https://docs.microsoft.com/en-us/office365/troubleshoot/active-directory/update-federated-domain-office-365", "description": "Microsoft. (2020, September 14). Update or repair the settings of a federated domain in Office 365, Azure, or Intune. Retrieved December 30, 2020.", "source_name": "Microsoft - Update or Repair Federated domain"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 7224}, "x_mitre_detection": {"Num": 0, "Raw": "\"It may be possible to detect domain policy modifications using Windows event logs. Group policy modifications, for example, may be logged under a variety of Windows event IDs for modifying, creating, undeleting, moving, and deleting directory service objects (Event ID 5136, 5137, 5138, 5139, 5141 respectively). Monitor for modifications to domain trust settings, such as when a user or application modifies the federation settings on the domain or updates domain authentication from Managed to Federated via ActionTypes <code>Set federation settings on domain</code> and <code>Set domain authentication</code>.(Citation: Microsoft - Azure Sentinel ADFSDomainTrustMods)(Citation: Microsoft 365 Defender Solorigate) This may also include monitoring for Event ID 307 which can be correlated to relevant Event ID 510 with the same Instance ID for change details.(Citation: Sygnia Golden SAML)(Citation: CISA SolarWinds Cloud Detection)\\n\\nConsider monitoring for commands/cmdlets and command-line arguments that may be leveraged to modify domain policy settings.(Citation: Microsoft - Update or Repair Federated domain) Some domain policy modifications, such as changes to federation settings, are likely to be rare.(Citation: Microsoft 365 Defender Solorigate)\"", "Str": "It may be possible to detect domain policy modifications using Windows event logs. Group policy modifications, for example, may be logged under a variety of Windows event IDs for modifying, creating, undeleting, moving, and deleting directory service objects (Event ID 5136, 5137, 5138, 5139, 5141 respectively). Monitor for modifications to domain trust settings, such as when a user or application modifies the federation settings on the domain or updates domain authentication from Managed to Federated via ActionTypes <code>Set federation settings on domain</code> and <code>Set domain authentication</code>.(Citation: Microsoft - Azure Sentinel ADFSDomainTrustMods)(Citation: Microsoft 365 Defender Solorigate) This may also include monitoring for Event ID 307 which can be correlated to relevant Event ID 510 with the same Instance ID for change details.(Citation: Sygnia Golden SAML)(Citation: CISA SolarWinds Cloud Detection)\n\nConsider monitoring for commands/cmdlets and command-line arguments that may be leveraged to modify domain policy settings.(Citation: Microsoft - Update or Repair Federated domain) Some domain policy modifications, such as changes to federation settings, are likely to be rare.(Citation: Microsoft 365 Defender Solorigate)", "Type": 3, "Index": 5751}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\"\n    ]", "Str": "", "Type": 5, "Index": 7158}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Object Creation\",\n      \"Active Directory: Active Directory Object Deletion\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5389}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7045}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"System access controls\",\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 5649}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7088}}', 'T1484', 'Adversaries may modify the configuration settings of a domain to evade defenses and/or escalate privileges in domain environments. Domains provide a centralized means of managing how computer resources (ex: computers, user accounts) can act, and interact with each other, on a network. The policy of the domain also includes configuration settings that may apply between domains in a multi-domain/forest environment. Modifications to domain settings may include altering domain Group Policy Objects (GPOs) or changing trust settings for domains, including federation trusts.
INSERT INTO public.attack_patterns VALUES (159, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.92567+00', NULL, 'attack-pattern--e3b168bd-fcd7-439e-9382-2e6c2f63514d', '[{"url": "https://attack.mitre.org/techniques/T1590/001", "external_id": "T1590.001", "source_name": "mitre-attack"}, {"url": "https://www.whois.net/", "description": "NTT America. (n.d.). Whois Lookup. Retrieved October 20, 2020.", "source_name": "WHOIS"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3384}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2943}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3340}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3309}}', 'T1590.001', 'Adversaries may gather information about the victim''s network domain(s) that can be used during targeting. Information about domains and their properties may include a variety of details, including what domain(s) the victim owns as well as administrative data (ex: name, registrar, etc.) and more directly actionable information such as contacts (email addresses and phone numbers), business addresses, and name servers.
INSERT INTO public.attack_patterns VALUES (161, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.021896+00', NULL, 'attack-pattern--24769ab5-14bd-4f4e-a752-cfb185da53ee', '[{"url": "https://attack.mitre.org/techniques/T1484/002", "external_id": "T1484.002", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/hybrid/whatis-fed", "description": "Microsoft. (2018, November 28). What is federation with Azure AD?. Retrieved December 30, 2020.", "source_name": "Microsoft - Azure AD Federation"}, {"url": "https://github.com/Azure/Azure-Sentinel/blob/master/Detections/AuditLogs/ADFSDomainTrustMods.yaml", "description": "Microsoft. (2020, December). Azure Sentinel Detections. Retrieved December 30, 2020.", "source_name": "Microsoft - Azure Sentinel ADFSDomainTrustMods"}, {"url": "https://www.sygnia.co/golden-saml-advisory", "description": "Sygnia. (2020, December). Detection and Hunting of Golden SAML Attack. Retrieved January 6, 2021.", "source_name": "Sygnia Golden SAML"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/aa21-008a", "description": "CISA. (2021, January 8). Detecting Post-Compromise Threat Activity in Microsoft Cloud Environments. Retrieved January 8, 2021.", "source_name": "CISA SolarWinds Cloud Detection"}, {"url": "https://docs.microsoft.com/en-us/office365/troubleshoot/active-directory/update-federated-domain-office-365", "description": "Microsoft. (2020, September 14). Update or repair the settings of a federated domain in Office 365, Azure, or Intune. Retrieved December 30, 2020.", "source_name": "Microsoft - Update or Repair Federated domain"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4767}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for modifications to domain trust settings, such as when a user or application modifies the federation settings on the domain or updates domain authentication from Managed to Federated via ActionTypes <code>Set federation settings on domain</code> and <code>Set domain authentication</code>.(Citation: Microsoft - Azure Sentinel ADFSDomainTrustMods) This may also include monitoring for Event ID 307 which can be correlated to relevant Event ID 510 with the same Instance ID for change details.(Citation: Sygnia Golden SAML)(Citation: CISA SolarWinds Cloud Detection)\\n\\nMonitor for PowerShell commands such as: <code>Update-MSOLFederatedDomain –DomainName: \\\"Federated Domain Name\\\"</code>, or <code>Update-MSOLFederatedDomain –DomainName: \\\"Federated Domain Name\\\" –supportmultipledomain</code>.(Citation: Microsoft - Update or Repair Federated domain)\"", "Str": "Monitor for modifications to domain trust settings, such as when a user or application modifies the federation settings on the domain or updates domain authentication from Managed to Federated via ActionTypes <code>Set federation settings on domain</code> and <code>Set domain authentication</code>.(Citation: Microsoft - Azure Sentinel ADFSDomainTrustMods) This may also include monitoring for Event ID 307 which can be correlated to relevant Event ID 510 with the same Instance ID for change details.(Citation: Sygnia Golden SAML)(Citation: CISA SolarWinds Cloud Detection)\n\nMonitor for PowerShell commands such as: <code>Update-MSOLFederatedDomain –DomainName: \"Federated Domain Name\"</code>, or <code>Update-MSOLFederatedDomain –DomainName: \"Federated Domain Name\" –supportmultipledomain</code>.(Citation: Microsoft - Update or Repair Federated domain)", "Type": 3, "Index": 3700}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\"\n    ]", "Str": "", "Type": 5, "Index": 4701}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Blake Strom, Microsoft 365 Defender\"\n    ]", "Str": "", "Type": 5, "Index": 3426}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Object Creation\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3507}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4603}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4645}}', 'T1484.002', 'Adversaries may add new domain trusts or modify the properties of existing domain trusts to evade defenses and/or elevate privileges. Domain trust details, such as whether or not a domain is federated, allow authentication and authorization properties to apply between domains for the purpose of accessing shared resources.(Citation: Microsoft - Azure AD Federation) These trust objects may include accounts, credentials, and other authentication material applied to servers, tokens, and domains.
INSERT INTO public.attack_patterns VALUES (164, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.104849+00', NULL, 'attack-pattern--fc74ba38-dc98-461f-8611-b3dbf9978e3d', '[{"url": "https://attack.mitre.org/techniques/T1601/002", "external_id": "T1601.002", "source_name": "mitre-attack"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2967}, "x_mitre_detection": {"Num": 0, "Raw": "\"Many embedded network devices provide a command to print the version of the currently running operating system.  Use this command to query the operating system for its version number and compare it to what is expected for the device in question.  Because image downgrade may be used in conjunction with [Patch System Image](https://attack.mitre.org/techniques/T1601/001), it may be appropriate to also verify the integrity of the vendor provided operating system image file. \"", "Str": "Many embedded network devices provide a command to print the version of the currently running operating system.  Use this command to query the operating system for its version number and compare it to what is expected for the device in question.  Because image downgrade may be used in conjunction with [Patch System Image](https://attack.mitre.org/techniques/T1601/001), it may be appropriate to also verify the integrity of the vendor provided operating system image file. ", "Type": 3, "Index": 2311}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2919}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2245}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2821}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2863}}', 'T1601.002', 'Adversaries may install an older version of the operating system of a network device to weaken security.  Older operating system versions on network devices often have weaker encryption ciphers and, in general, fewer/less updated defensive features. (Citation: Cisco Synful Knock Evolution)
INSERT INTO public.attack_patterns VALUES (165, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.135261+00', NULL, 'attack-pattern--d742a578-d70e-4d0e-96a6-02a9c30204e6', '[{"url": "https://attack.mitre.org/techniques/T1189", "external_id": "T1189", "source_name": "mitre-attack"}, {"url": "http://blog.shadowserver.org/2012/05/15/cyber-espionage-strategic-web-compromises-trusted-websites-serving-dangerous-results/", "description": "Adair, S., Moran, N. (2012, May 15). Cyber Espionage & Strategic Web Compromises – Trusted Websites Serving Dangerous Results. Retrieved March 13, 2018.", "source_name": "Shadowserver Strategic Web Compromise"}, {"url": "https://www.volexity.com/blog/2017/11/06/oceanlotus-blossoms-mass-digital-surveillance-and-exploitation-of-asean-nations-the-media-human-rights-and-civil-society/", "description": "Lassalle, D., et al. (2017, November 6). OceanLotus Blossoms: Mass Digital Surveillance and Attacks Targeting ASEAN, Asian Nations, the Media, Human Rights Groups, and Civil Society. Retrieved November 6, 2017.", "source_name": "Volexity OceanLotus Nov 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6274}, "x_mitre_detection": {"Num": 0, "Raw": "\"Firewalls and proxies can inspect URLs for potentially known-bad domains or parameters. They can also do reputation-based analytics on websites and their requested resources such as how old a domain is, who it''s registered to, if it''s on a known bad list, or how many other users have connected to it before.\\n\\nNetwork intrusion detection systems, sometimes with SSL/TLS MITM inspection, can be used to look for known malicious scripts (recon, heap spray, and browser identification scripts have been frequently reused), common script obfuscation, and exploit code.\\n\\nDetecting compromise based on the drive-by exploit from a legitimate website may be difficult. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of browser processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.\"", "Str": "Firewalls and proxies can inspect URLs for potentially known-bad domains or parameters. They can also do reputation-based analytics on websites and their requested resources such as how old a domain is, who it''s registered to, if it''s on a known bad list, or how many other users have connected to it before.\n\nNetwork intrusion detection systems, sometimes with SSL/TLS MITM inspection, can be used to look for known malicious scripts (recon, heap spray, and browser identification scripts have been frequently reused), common script obfuscation, and exploit code.\n\nDetecting compromise based on the drive-by exploit from a legitimate website may be difficult. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of browser processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.", "Type": 3, "Index": 4978}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\",\n      \"SaaS\"\n    ]", "Str": "", "Type": 5, "Index": 6182}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jeff Sakowicz, Microsoft Identity Developer Platform Services (IDPM Services)\",\n      \"Saisha Agrawal, Microsoft Threat Intelligent Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 4535}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 4727}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6092}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6135}}', 'T1189', 'Adversaries may gain access to a system through a user visiting a website over the normal course of browsing. With this technique, the user''s web browser is typically targeted for exploitation, but adversaries may also use compromised websites for non-exploitation behavior such as acquiring [Application Access Token](https://attack.mitre.org/techniques/T1550/001).
INSERT INTO public.attack_patterns VALUES (166, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.177947+00', NULL, 'attack-pattern--31fe0ba2-62fd-4fd9-9293-4043d84f7fe9', '[{"url": "https://attack.mitre.org/techniques/T1608/004", "external_id": "T1608.004", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2012/12/council-foreign-relations-water-hole-attack-details.html", "description": "Kindlund, D. (2012, December 30). CFR Watering Hole Attack Details. Retrieved December 18, 2020.", "source_name": "FireEye CFR Watering Hole 2012"}, {"url": "http://arstechnica.com/security/2015/08/newly-discovered-chinese-hacking-group-hacked-100-websites-to-use-as-watering-holes/", "description": "Gallagher, S.. (2015, August 5). Newly discovered Chinese hacking group hacked 100+ websites to use as “watering holes”. Retrieved January 25, 2016.", "source_name": "Gallagher 2015"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4864}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on other phases of the adversary lifecycle, such as [Drive-by Compromise](https://attack.mitre.org/techniques/T1189) or [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on other phases of the adversary lifecycle, such as [Drive-by Compromise](https://attack.mitre.org/techniques/T1189) or [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203).", "Type": 3, "Index": 4388}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4820}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4789}}', 'T1608.004', 'Adversaries may prepare an operational environment to infect systems that visit a website over the normal course of browsing. Endpoint systems may be compromised through browsing to adversary controlled sites, as in [Drive-by Compromise](https://attack.mitre.org/techniques/T1189). In such cases, the user''s web browser is typically targeted for exploitation (often not requiring any extra user interaction once landing on the site), but adversaries may also set up websites for non-exploitation behavior such as [Application Access Token](https://attack.mitre.org/techniques/T1550/001). Prior to [Drive-by Compromise](https://attack.mitre.org/techniques/T1189), adversaries must stage resources needed to deliver that exploit to users who browse to an adversary controlled site. Drive-by content can be staged on adversary controlled infrastructure that has been acquired ([Acquire Infrastructure](https://attack.mitre.org/techniques/T1583)) or previously compromised ([Compromise Infrastructure](https://attack.mitre.org/techniques/T1584)).
INSERT INTO public.attack_patterns VALUES (167, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.214016+00', NULL, 'attack-pattern--fc742192-19e3-466c-9eb5-964a97b29490', '[{"url": "https://attack.mitre.org/techniques/T1574/004", "external_id": "T1574.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/471.html", "external_id": "CAPEC-471", "source_name": "capec"}, {"url": "https://objective-see.com/blog/blog_0x46.html", "description": "Patrick Wardle. (2019, July 2). Getting Root with Benign AppStore Apps. Retrieved March 31, 2021.", "source_name": "Wardle Dylib Hijack Vulnerable Apps"}, {"url": "https://www.virusbulletin.com/uploads/pdf/magazine/2015/vb201503-dylib-hijacking.pdf", "description": "Patrick Wardle. (2015, March 1). Dylib Hijacking on OS X. Retrieved March 29, 2021.", "source_name": "Wardle Dylib Hijacking OSX 2015"}, {"url": "https://github.com/EmpireProject/Empire/blob/master/lib/modules/python/situational_awareness/host/osx/HijackScanner.py", "description": "Wardle, P., Ross, C. (2017, September 21). Empire Project Dylib Hijack Vulnerability Scanner. Retrieved April 1, 2021.", "source_name": "Github EmpireProject HijackScanner"}, {"url": "https://github.com/EmpireProject/Empire/blob/08cbd274bef78243d7a8ed6443b8364acd1fc48b/lib/modules/python/persistence/osx/CreateHijacker.py", "description": "Wardle, P., Ross, C. (2018, April 8). EmpireProject Create Dylib Hijacker. Retrieved April 1, 2021.", "source_name": "Github EmpireProject CreateHijacker Dylib"}, {"url": "https://www.blackhat.com/docs/us-15/materials/us-15-Wardle-Writing-Bad-A-Malware-For-OS-X.pdf", "description": "Patrick Wardle. (2015). Writing Bad @$$ Malware for OS X. Retrieved July 10, 2017.", "source_name": "Writing Bad Malware for OSX"}, {"url": "https://taomm.org/vol1/pdfs.html", "description": "Patrick Wardle. (2020, August 5). The Art of Mac Malware Volume 0x1: Analysis. Retrieved March 19, 2021.", "source_name": "wardle artofmalware volume1"}, {"url": "https://malwareunicorn.org/workshops/macos_dylib_injection.html#5", "description": "Amanda Rousseau. (2020, April 4). MacOS Dylib Injection Workshop. Retrieved March 29, 2021.", "source_name": "MalwareUnicorn macOS Dylib Injection MachO"}, {"url": "https://developer.apple.com/library/archive/documentation/DeveloperTools/Conceptual/DynamicLibraries/100-Articles/RunpathDependentLibraries.html", "description": "Apple Inc.. (2012, July 7). Run-Path Dependent Libraries. Retrieved March 31, 2021.", "source_name": "Apple Developer Doco Archive Run-Path"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 6541}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file systems for moving, renaming, replacing, or modifying dylibs. Changes in the set of dylibs that are loaded by a process (compared to past behavior) that do not correlate with known software, patches, etc., are suspicious. Check the system for multiple dylibs with the same name and monitor which versions have historically been loaded into a process. \\n\\nRun path dependent libraries can include <code>LC_LOAD_DYLIB</code>, <code>LC_LOAD_WEAK_DYLIB</code>, and <code>LC_RPATH</code>. Other special keywords are recognized by the macOS loader are <code>@rpath</code>, <code>@loader_path</code>, and <code>@executable_path</code>.(Citation: Apple Developer Doco Archive Run-Path) These loader instructions can be examined for individual binaries or frameworks using the <code>otool -l</code> command. Objective-See''s Dylib Hijacking Scanner can be used to identify applications vulnerable to dylib hijacking.(Citation: Wardle Dylib Hijack Vulnerable Apps)(Citation: Github EmpireProject HijackScanner)\"", "Str": "Monitor file systems for moving, renaming, replacing, or modifying dylibs. Changes in the set of dylibs that are loaded by a process (compared to past behavior) that do not correlate with known software, patches, etc., are suspicious. Check the system for multiple dylibs with the same name and monitor which versions have historically been loaded into a process. \n\nRun path dependent libraries can include <code>LC_LOAD_DYLIB</code>, <code>LC_LOAD_WEAK_DYLIB</code>, and <code>LC_RPATH</code>. Other special keywords are recognized by the macOS loader are <code>@rpath</code>, <code>@loader_path</code>, and <code>@executable_path</code>.(Citation: Apple Developer Doco Archive Run-Path) These loader instructions can be examined for individual binaries or frameworks using the <code>otool -l</code> command. Objective-See''s Dylib Hijacking Scanner can be used to identify applications vulnerable to dylib hijacking.(Citation: Wardle Dylib Hijack Vulnerable Apps)(Citation: Github EmpireProject HijackScanner)", "Type": 3, "Index": 5417}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 6495}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 5224}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6464}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 5355}}', 'T1574.004', 'Adversaries may execute their own payloads by placing a malicious dynamic library (dylib) with an expected name in a path a victim application searches at runtime. The dynamic loader will try to find the dylibs based on the sequential order of the search paths. Paths to dylibs may be prefixed with <code>@rpath</code>, which allows developers to use relative paths to specify an array of search paths used at runtime based on the location of the executable.  Additionally, if weak linking is used, such as the <code>LC_LOAD_WEAK_DYLIB</code> function, an application will still execute even if an expected dylib is not present. Weak linking enables developers to run an application on multiple macOS versions as new APIs are added.
INSERT INTO public.attack_patterns VALUES (171, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.314346+00', NULL, 'attack-pattern--f4599aa0-4f85-4a32-80ea-fc39dc965945', '[{"url": "https://attack.mitre.org/techniques/T1055/001", "external_id": "T1055.001", "source_name": "mitre-attack"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://www.endgame.com/blog/technical-blog/hunting-memory", "description": "Desimone, J. (2017, June 13). Hunting in Memory. Retrieved December 7, 2017.", "source_name": "Elastic HuntingNMemory June 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4556}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code> and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\\n\\nMonitor DLL/PE file events, specifically creation of these binary files as well as the loading of DLLs into processes. Look for DLLs that are not recognized or not normally loaded into a process. \\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code> and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\n\nMonitor DLL/PE file events, specifically creation of these binary files as well as the loading of DLLs into processes. Look for DLLs that are not recognized or not normally loaded into a process. \n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 3340}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4508}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 3121}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4419}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 3258}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4461}}', 'T1055.001', 'Adversaries may inject dynamic-link libraries (DLLs) into processes in order to evade process-based defenses as well as possibly elevate privileges. DLL injection is a method of executing arbitrary code in the address space of a separate live process.  
INSERT INTO public.attack_patterns VALUES (172, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.362569+00', NULL, 'attack-pattern--b84903f0-c7d5-435d-a69e-de47cc3578c0', '[{"url": "https://attack.mitre.org/techniques/T1548/004", "external_id": "T1548.004", "source_name": "mitre-attack"}, {"url": "https://developer.apple.com/documentation/security/1540038-authorizationexecutewithprivileg", "description": "Apple. (n.d.). Apple Developer Documentation - AuthorizationExecuteWithPrivileges. Retrieved August 8, 2019.", "source_name": "AppleDocs AuthorizationExecuteWithPrivileges"}, {"url": "https://speakerdeck.com/patrickwardle/defcon-2017-death-by-1000-installers-its-all-broken?slide=8", "description": "Patrick Wardle. (2017). Death by 1000 installers; it''s all broken!. Retrieved August 8, 2019.", "source_name": "Death by 1000 installers; it''s all broken!"}, {"url": "https://www.carbonblack.com/2019/02/12/tau-threat-intelligence-notification-new-macos-malware-variant-of-shlayer-osx-discovered/", "description": "Carbon Black Threat Analysis Unit. (2019, February 12). New macOS Malware Variant of Shlayer (OSX) Discovered. Retrieved August 8, 2019.", "source_name": "Carbon Black Shlayer Feb 2019"}, {"url": "https://objective-see.com/blog/blog_0x2A.html", "description": "Patrick Wardle. (2018, February 17). Tearing Apart the Undetected (OSX)Coldroot RAT. Retrieved August 8, 2019.", "source_name": "OSX Coldroot RAT"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4800}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring for <code>/usr/libexec/security_authtrampoline</code> executions which may indicate that <code>AuthorizationExecuteWithPrivileges</code> is being executed. MacOS system logs may also indicate when <code>AuthorizationExecuteWithPrivileges</code> is being called. Monitoring OS API callbacks for the execution can also be a way to detect this behavior but requires specialized security tooling.\"", "Str": "Consider monitoring for <code>/usr/libexec/security_authtrampoline</code> executions which may indicate that <code>AuthorizationExecuteWithPrivileges</code> is being executed. MacOS system logs may also indicate when <code>AuthorizationExecuteWithPrivileges</code> is being called. Monitoring OS API callbacks for the execution can also be a way to detect this behavior but requires specialized security tooling.", "Type": 3, "Index": 4136}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4754}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jimmy Astle, @AstleJimmy, Carbon Black\",\n      \"Erika Noerenberg, @gutterchurl, Carbon Black\"\n    ]", "Str": "", "Type": 5, "Index": 3895}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 4033}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4642}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4684}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4589}}', 'T1548.004', 'Adversaries may leverage the <code>AuthorizationExecuteWithPrivileges</code> API to escalate privileges by prompting the user for credentials.(Citation: AppleDocs AuthorizationExecuteWithPrivileges) The purpose of this API is to give application developers an easy way to perform operations with root privileges, such as for application installation or updating. This API does not validate that the program requesting root privileges comes from a reputable source or has been maliciously modified. 
INSERT INTO public.attack_patterns VALUES (173, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.406527+00', NULL, 'attack-pattern--4bc31b94-045b-4752-8920-aebaebdb6470', '[{"url": "https://attack.mitre.org/techniques/T1087/003", "external_id": "T1087.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/exchange/email-addresses-and-address-books/address-lists/address-lists?view=exchserver-2019", "description": "Microsoft. (2020, February 7). Address lists in Exchange Server. Retrieved March 26, 2020.", "source_name": "Microsoft Exchange Address Lists"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/exchange/email-addresses-and-address-books/get-globaladdresslist", "description": "Microsoft. (n.d.). Get-GlobalAddressList. Retrieved October 6, 2019.", "source_name": "Microsoft getglobaladdresslist"}, {"url": "https://www.blackhillsinfosec.com/attacking-exchange-with-mailsniper/", "description": "Bullock, B.. (2016, October 3). Attacking Exchange with MailSniper. Retrieved October 6, 2019.", "source_name": "Black Hills Attacking Exchange MailSniper, 2016"}, {"url": "https://support.google.com/a/answer/166870?hl=en", "description": "Google. (n.d.). Retrieved March 16, 2021.", "source_name": "Google Workspace Global Access List"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3889}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2912}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3795}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Metadata\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2798}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3706}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3748}}', 'T1087.003', 'Adversaries may attempt to get a listing of email addresses and accounts. Adversaries may try to dump Exchange address lists such as global address lists (GALs).(Citation: Microsoft Exchange Address Lists)
INSERT INTO public.attack_patterns VALUES (271, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.478733+00', NULL, 'attack-pattern--650c784b-7504-4df7-ab2c-4ea882384d1e', '[{"url": "https://attack.mitre.org/techniques/T1557/001", "external_id": "T1557.001", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Link-Local_Multicast_Name_Resolution", "description": "Wikipedia. (2016, July 7). Link-Local Multicast Name Resolution. Retrieved November 17, 2017.", "source_name": "Wikipedia LLMNR"}, {"url": "https://technet.microsoft.com/library/cc958811.aspx", "description": "Microsoft. (n.d.). NetBIOS Name Resolution. Retrieved November 17, 2017.", "source_name": "TechNet NetBIOS"}, {"url": "https://byt3bl33d3r.github.io/practical-guide-to-ntlm-relaying-in-2017-aka-getting-a-foothold-in-under-5-minutes.html", "description": "Salvati, M. (2017, June 2). Practical guide to NTLM Relaying in 2017 (A.K.A getting a foothold in under 5 minutes). Retrieved February 7, 2019.", "source_name": "byt3bl33d3r NTLM Relaying"}, {"url": "https://blog.secureideas.com/2018/04/ever-run-a-relay-why-smb-relays-should-be-on-your-mind.html", "description": "Kuehn, E. (2018, April 11). Ever Run a Relay? Why SMB Relays Should Be On Your Mind. Retrieved February 7, 2019.", "source_name": "Secure Ideas SMB Relay"}, {"url": "https://github.com/nomex/nbnspoof", "description": "Nomex. (2014, February 7). NBNSpoof. Retrieved November 17, 2017.", "source_name": "GitHub NBNSpoof"}, {"url": "https://www.rapid7.com/db/modules/auxiliary/spoof/llmnr/llmnr_response", "description": "Francois, R. (n.d.). LLMNR Spoofer. Retrieved November 17, 2017.", "source_name": "Rapid7 LLMNR Spoofer"}, {"url": "https://github.com/SpiderLabs/Responder", "description": "Gaffie, L. (2016, August 25). Responder. Retrieved November 17, 2017.", "source_name": "GitHub Responder"}, {"url": "https://www.sternsecurity.com/blog/local-network-attacks-llmnr-and-nbt-ns-poisoning", "description": "Sternstein, J. (2013, November). Local Network Attacks: LLMNR and NBT-NS Poisoning. Retrieved November 17, 2017.", "source_name": "Sternsecurity LLMNR-NBTNS"}, {"url": "https://github.com/Kevin-Robertson/Conveigh", "description": "Robertson, K. (2016, August 28). Conveigh. Retrieved November 17, 2017.", "source_name": "GitHub Conveigh"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6611}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor <code>HKLM\\\\Software\\\\Policies\\\\Microsoft\\\\Windows NT\\\\DNSClient</code> for changes to the \\\"EnableMulticast\\\" DWORD value. A value of “0” indicates LLMNR is disabled. (Citation: Sternsecurity LLMNR-NBTNS)\\n\\nMonitor for traffic on ports UDP 5355 and UDP 137 if LLMNR/NetBIOS is disabled by security policy.\\n\\nDeploy an LLMNR/NBT-NS spoofing detection tool.(Citation: GitHub Conveigh) Monitoring of Windows event logs for event IDs 4697 and 7045 may help in detecting successful relay techniques.(Citation: Secure Ideas SMB Relay)\"", "Str": "Monitor <code>HKLM\\Software\\Policies\\Microsoft\\Windows NT\\DNSClient</code> for changes to the \"EnableMulticast\" DWORD value. A value of “0” indicates LLMNR is disabled. (Citation: Sternsecurity LLMNR-NBTNS)\n\nMonitor for traffic on ports UDP 5355 and UDP 137 if LLMNR/NetBIOS is disabled by security policy.\n\nDeploy an LLMNR/NBT-NS spoofing detection tool.(Citation: GitHub Conveigh) Monitoring of Windows event logs for event IDs 4697 and 7045 may help in detecting successful relay techniques.(Citation: Secure Ideas SMB Relay)", "Type": 3, "Index": 5896}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6563}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Eric Kuehn, Secure Ideas\",\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 5564}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Service: Service Creation\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 5673}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6474}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6516}}', 'T1557.001', 'By responding to LLMNR/NBT-NS network traffic, adversaries may spoof an authoritative source for name resolution to force communication with an adversary controlled system. This activity may be used to collect or relay authentication materials. 
INSERT INTO public.attack_patterns VALUES (175, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.558073+00', NULL, 'attack-pattern--3dc8c101-d4db-4f4d-8150-1b5a76ca5f1b', '[{"url": "https://attack.mitre.org/techniques/T1586/002", "external_id": "T1586.002", "source_name": "mitre-attack"}, {"url": "https://arstechnica.com/tech-policy/2011/02/anonymous-speaks-the-inside-story-of-the-hbgary-hack/", "description": "Bright, P. (2011, February 15). Anonymous speaks: the inside story of the HBGary hack. Retrieved March 9, 2017.", "source_name": "AnonHBGary"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2797}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).", "Type": 3, "Index": 2384}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2753}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2722}}', 'T1586.002', 'Adversaries may compromise email accounts that can be used during targeting. Adversaries can use compromised email accounts to further their operations, such as leveraging them to conduct [Phishing for Information](https://attack.mitre.org/techniques/T1598) or [Phishing](https://attack.mitre.org/techniques/T1566). Utilizing an existing persona with a compromised email account may engender a level of trust in a potential victim if they have a relationship, or knowledge of, the compromised persona. Compromised email accounts can also be used in the acquisition of infrastructure (ex: [Domains](https://attack.mitre.org/techniques/T1583/001)).
INSERT INTO public.attack_patterns VALUES (176, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.599318+00', NULL, 'attack-pattern--69f897fd-12a9-4c89-ad6a-46d2f3c38262', '[{"url": "https://attack.mitre.org/techniques/T1589/002", "external_id": "T1589.002", "source_name": "mitre-attack"}, {"url": "https://www.hackers-arise.com/email-scraping-and-maltego", "description": "Hackers Arise. (n.d.). Email Scraping and Maltego. Retrieved October 20, 2020.", "source_name": "HackersArise Email"}, {"url": "https://www.cnet.com/news/massive-breach-leaks-773-million-emails-21-million-passwords/", "description": "Ng, A. (2019, January 17). Massive breach leaks 773 million email addresses, 21 million passwords. Retrieved October 20, 2020.", "source_name": "CNET Leaks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2790}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2349}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2746}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2715}}', 'T1589.002', 'Adversaries may gather email addresses that can be used during targeting. Even if internal instances exist, organizations may have public-facing email infrastructure and addresses for employees.
INSERT INTO public.attack_patterns VALUES (177, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.642955+00', NULL, 'attack-pattern--1608f3e1-598a-42f4-a01a-2e252e81728f', '[{"url": "https://attack.mitre.org/techniques/T1114", "external_id": "T1114", "source_name": "mitre-attack"}, {"url": "https://blogs.technet.microsoft.com/timmcmic/2015/06/08/exchange-and-office-365-mail-forwarding-2/", "description": "McMichael, T.. (2015, June 8). Exchange and Office 365 Mail Forwarding. Retrieved October 8, 2019.", "source_name": "Microsoft Tim McMichael Exchange Mail Forwarding 2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 4308}, "x_mitre_detection": {"Num": 0, "Raw": "\"There are likely a variety of ways an adversary could collect email from a target, each with a different mechanism for detection.\\n\\nFile access of local system email files for Exfiltration, unusual processes connecting to an email server within a network, or unusual access patterns or authentication attempts on a public-facing webmail server may all be indicators of malicious activity.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather local email files. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nDetection is challenging because all messages forwarded because of an auto-forwarding rule have the same presentation as a manually forwarded message. It is also possible for the user to not be aware of the addition of such an auto-forwarding rule and not suspect that their account has been compromised; email-forwarding rules alone will not affect the normal usage patterns or operations of the email account.\\n\\nAuto-forwarded messages generally contain specific detectable artifacts that may be present in the header; such artifacts would be platform-specific. Examples include <code>X-MS-Exchange-Organization-AutoForwarded</code> set to true, <code>X-MailFwdBy</code> and <code>X-Forwarded-To</code>. The <code>forwardingSMTPAddress</code> parameter used in a forwarding process that is managed by administrators and not by user actions. All messages for the mailbox are forwarded to the specified SMTP address. However, unlike typical client-side rules, the message does not appear as forwarded in the mailbox; it appears as if it were sent directly to the specified destination mailbox.(Citation: Microsoft Tim McMichael Exchange Mail Forwarding 2) High volumes of emails that bear the <code>X-MS-Exchange-Organization-AutoForwarded</code> header (indicating auto-forwarding) without a corresponding number of emails that match the appearance of a forwarded message may indicate that further investigation is needed at the administrator level rather than user-level.\"", "Str": "There are likely a variety of ways an adversary could collect email from a target, each with a different mechanism for detection.\n\nFile access of local system email files for Exfiltration, unusual processes connecting to an email server within a network, or unusual access patterns or authentication attempts on a public-facing webmail server may all be indicators of malicious activity.\n\nMonitor processes and command-line arguments for actions that could be taken to gather local email files. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nDetection is challenging because all messages forwarded because of an auto-forwarding rule have the same presentation as a manually forwarded message. It is also possible for the user to not be aware of the addition of such an auto-forwarding rule and not suspect that their account has been compromised; email-forwarding rules alone will not affect the normal usage patterns or operations of the email account.\n\nAuto-forwarded messages generally contain specific detectable artifacts that may be present in the header; such artifacts would be platform-specific. Examples include <code>X-MS-Exchange-Organization-AutoForwarded</code> set to true, <code>X-MailFwdBy</code> and <code>X-Forwarded-To</code>. The <code>forwardingSMTPAddress</code> parameter used in a forwarding process that is managed by administrators and not by user actions. All messages for the mailbox are forwarded to the specified SMTP address. However, unlike typical client-side rules, the message does not appear as forwarded in the mailbox; it appears as if it were sent directly to the specified destination mailbox.(Citation: Microsoft Tim McMichael Exchange Mail Forwarding 2) High volumes of emails that bear the <code>X-MS-Exchange-Organization-AutoForwarded</code> header (indicating auto-forwarding) without a corresponding number of emails that match the appearance of a forwarded message may indicate that further investigation is needed at the administrator level rather than user-level.", "Type": 3, "Index": 1778}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4214}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Swetha Prabakaran, Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 1422}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1531}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4124}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4167}}', 'T1114', 'Adversaries may target user email to collect sensitive information. Emails may contain sensitive data, including trade secrets or personal information, that can prove valuable to adversaries. Adversaries can collect or forward email from mail servers or clients. ', 'Email Collection', 0);
INSERT INTO public.attack_patterns VALUES (292, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.699108+00', NULL, 'attack-pattern--8cdeb020-e31e-4f88-a582-f53dcfbda819', '[{"url": "https://attack.mitre.org/techniques/T1134/003", "external_id": "T1134.003", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/windows-server-docs/identity/ad-ds/manage/component-updates/command-line-process-auditing", "description": "Mathers, B. (2017, March 7). Command line process auditing. Retrieved April 21, 2017.", "source_name": "Microsoft Command-line Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3094}, "x_mitre_detection": {"Num": 0, "Raw": "\"If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\\n\\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior.\\n\\nAnalysts can also monitor for use of Windows APIs such as <code>LogonUser</code> and <code> SetThreadToken</code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.\"", "Str": "If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\n\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior.\n\nAnalysts can also monitor for use of Windows APIs such as <code>LogonUser</code> and <code> SetThreadToken</code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.", "Type": 3, "Index": 1989}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3046}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1738}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2934}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\",\n      \"System access controls\",\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 1849}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2976}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2879}}', 'T1134.003', 'Adversaries may make and impersonate tokens to escalate privileges and bypass access controls. If an adversary has a username and password but the user is not logged onto the system, the adversary can then create a logon session for the user using the <code>LogonUser</code> function. The function will return a copy of the new session''s access token and the adversary can use <code>SetThreadToken</code> to assign the token to a thread.', 'Make and Impersonate Token', 5);
INSERT INTO public.attack_patterns VALUES (179, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.77215+00', NULL, 'attack-pattern--9c45eaa3-8604-4780-8988-b5074dbb9ecd', '[{"url": "https://attack.mitre.org/techniques/T1546/014", "external_id": "T1546.014", "source_name": "mitre-attack"}, {"url": "https://www.xorrior.com/emond-persistence/", "description": "Ross, Chris. (2018, January 17). Leveraging Emond on macOS For Persistence. Retrieved September 10, 2019.", "source_name": "xorrior emond Jan 2018"}, {"url": "http://www.magnusviri.com/Mac/what-is-emond.html", "description": "Reynolds, James. (2016, April 7). What is emond?. Retrieved September 10, 2019.", "source_name": "magnusviri emond Apr 2016"}, {"url": "https://www.sentinelone.com/blog/how-malware-persists-on-macos/", "description": "Stokes, Phil. (2019, June 17). HOW MALWARE PERSISTS ON MACOS. Retrieved September 10, 2019.", "source_name": "sentinelone macos persist Jun 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3906}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor emond rules creation by checking for files created or modified in <code>/etc/emond.d/rules/</code> and <code>/private/var/db/emondClients</code>.\"", "Str": "Monitor emond rules creation by checking for files created or modified in <code>/etc/emond.d/rules/</code> and <code>/private/var/db/emondClients</code>.", "Type": 3, "Index": 3574}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3860}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ivan Sinyakov\"\n    ]", "Str": "", "Type": 5, "Index": 3349}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3408}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3762}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3804}}', 'T1546.014', 'Adversaries may gain persistence and elevate privileges by executing malicious content triggered by the Event Monitor Daemon (emond). Emond is a [Launch Daemon](https://attack.mitre.org/techniques/T1543/004) that accepts events from various services, runs them through a simple rules engine, and takes action. The emond binary at <code>/sbin/emond</code> will load any rules from the <code>/etc/emond.d/rules/</code> directory and take action once an explicitly defined event takes place.
INSERT INTO public.attack_patterns VALUES (180, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.812549+00', NULL, 'attack-pattern--76551c52-b111-4884-bc47-ff3e728f0156', '[{"url": "https://attack.mitre.org/techniques/T1589/003", "external_id": "T1589.003", "source_name": "mitre-attack"}, {"url": "https://www.opm.gov/cybersecurity/cybersecurity-incidents/", "description": "Cybersecurity Resource Center. (n.d.). CYBERSECURITY INCIDENTS. Retrieved October 20, 2020.", "source_name": "OPM Leak"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2530}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2089}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2486}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2455}}', 'T1589.003', 'Adversaries may gather employee names that can be used during targeting. Employee names be used to derive email addresses as well as to help guide other reconnaissance efforts and/or craft more-believable lures.
INSERT INTO public.attack_patterns VALUES (181, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.844847+00', NULL, 'attack-pattern--b8902400-e6c5-4ba2-95aa-2d35b442b118', '[{"url": "https://attack.mitre.org/techniques/T1573", "external_id": "T1573", "source_name": "mitre-attack"}, {"url": "http://www.sans.org/reading-room/whitepapers/analyst/finding-hidden-threats-decrypting-ssl-34840", "description": "Butler, M. (2013, November). Finding Hidden Threats by Decrypting SSL. Retrieved April 5, 2016.", "source_name": "SANS Decrypting SSL"}, {"url": "https://insights.sei.cmu.edu/cert/2015/03/the-risks-of-ssl-inspection.html", "description": "Dormann, W. (2015, March 13). The Risks of SSL Inspection. Retrieved April 5, 2016.", "source_name": "SEI SSL Inspection Risks"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3097}, "x_mitre_detection": {"Num": 0, "Raw": "\"SSL/TLS inspection is one way of detecting command and control traffic within some encrypted communication channels.(Citation: SANS Decrypting SSL) SSL/TLS inspection does come with certain risks that should be considered before implementing to avoid potential security issues such as incomplete certificate validation.(Citation: SEI SSL Inspection Risks)\\n\\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "SSL/TLS inspection is one way of detecting command and control traffic within some encrypted communication channels.(Citation: SANS Decrypting SSL) SSL/TLS inspection does come with certain risks that should be considered before implementing to avoid potential security issues such as incomplete certificate validation.(Citation: SEI SSL Inspection Risks)\n\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2158}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3019}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2075}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2987}}', 'T1573', 'Adversaries may employ a known encryption algorithm to conceal command and control traffic rather than relying on any inherent protections provided by a communication protocol. Despite the use of a secure algorithm, these implementations may be vulnerable to reverse engineering if secret keys are encoded and/or generated within malware samples/configuration files.', 'Encrypted Channel', 0);
INSERT INTO public.attack_patterns VALUES (182, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.888283+00', NULL, 'attack-pattern--c675646d-e204-4aa8-978d-e3d6d65885c4', '[{"url": "https://attack.mitre.org/techniques/T1499", "external_id": "T1499", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/227.html", "external_id": "CAPEC-227", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/131.html", "external_id": "CAPEC-131", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/130.html", "external_id": "CAPEC-130", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/125.html", "external_id": "CAPEC-125", "source_name": "capec"}, {"url": "https://www.fireeye.com/blog/threat-research/2014/11/operation-poisoned-handover-unveiling-ties-between-apt-activity-in-hong-kongs-pro-democracy-movement.html", "description": "Ned Moran, Mike Scott, Mike Oppenheim of FireEye. (2014, November 3). Operation Poisoned Handover: Unveiling Ties Between APT Activity in Hong Kong’s Pro-Democracy Movement. Retrieved April 18, 2019.", "source_name": "FireEye OpPoisonedHandover February 2016"}, {"url": "https://www.ic3.gov/media/2012/FraudAlertFinancialInstitutionEmployeeCredentialsTargeted.pdf", "description": "FS-ISAC. (2012, September 17). Fraud Alert – Cyber Criminals Targeting Financial Institution Employee Credentials to Conduct Wire Transfer Fraud. Retrieved April 18, 2019.", "source_name": "FSISAC FraudNetDoS September 2012"}, {"url": "https://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-continued-rise-of-ddos-attacks.pdf", "description": "Wueest, C.. (2014, October 21). The continued rise of DDoS attacks. Retrieved April 24, 2019.", "source_name": "Symantec DDoS October 2014"}, {"url": "https://www.justice.gov/opa/pr/seven-iranians-working-islamic-revolutionary-guard-corps-affiliated-entities-charged", "description": "Preet Bharara, US Attorney. (2016, March 24). Retrieved April 23, 2019.", "source_name": "USNYAG IranianBotnet March 2016"}, {"url": "https://arstechnica.com/information-technology/2015/03/massive-denial-of-service-attack-on-github-tied-to-chinese-government/", "description": "Goodin, D.. (2015, March 31). Massive denial-of-service attack on GitHub tied to Chinese government. Retrieved April 19, 2019.", "source_name": "ArsTechnica Great Firewall of China"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 8842}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\\n\\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.\\n\\nExternally monitor the availability of services that may be targeted by an Endpoint DoS.\"", "Str": "Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\n\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.\n\nExternally monitor the availability of services that may be targeted by an Endpoint DoS.", "Type": 3, "Index": 7542}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 8652}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 8559}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Alfredo Oliveira, Trend Micro\",\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Vishwas Manral, McAfee\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 7076}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 7326}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 8620}}', 'T1499', 'Adversaries may perform Endpoint Denial of Service (DoS) attacks to degrade or block the availability of services to users. Endpoint DoS can be performed by exhausting the system resources those services are hosted on or exploiting the system to cause a persistent crash condition. Example services include websites, email services, DNS, and web-based applications. Adversaries have been observed conducting DoS attacks for political purposes(Citation: FireEye OpPoisonedHandover February 2016) and to support other malicious activities, including distraction(Citation: FSISAC FraudNetDoS September 2012), hacktivism, and extortion.(Citation: Symantec DDoS October 2014)
INSERT INTO public.attack_patterns VALUES (185, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.980157+00', NULL, 'attack-pattern--cdfc5f0a-9bb9-4352-b896-553cfa2d8fd8', '[{"url": "https://attack.mitre.org/techniques/T1585", "external_id": "T1585", "source_name": "mitre-attack"}, {"url": "https://www.securityweek.com/iranian-hackers-targeted-us-officials-elaborate-social-media-attack-operation", "description": "Lennon, M. (2014, May 29). Iranian Hackers Targeted US Officials in Elaborate Social Media Attack Operation. Retrieved March 1, 2017.", "source_name": "NEWSCASTER2014"}, {"url": "http://media.blackhat.com/bh-us-10/whitepapers/Ryan/BlackHat-USA-2010-Ryan-Getting-In-Bed-With-Robin-Sage-v1.0.pdf", "description": "Ryan, T. (2010). “Getting In Bed with Robin Sage.”. Retrieved March 6, 2017.", "source_name": "BlackHatRobinSage"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3866}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently created/modified accounts making numerous connection requests to accounts affiliated with your organization.\\n\\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).\"", "Str": "Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently created/modified accounts making numerous connection requests to accounts affiliated with your organization.\n\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).", "Type": 3, "Index": 3176}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3822}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3790}}', 'T1585', 'Adversaries may create and cultivate accounts with services that can be used during targeting. Adversaries can create accounts that can be used to build a persona to further operations. Persona development consists of the development of public information, presence, history and appropriate affiliations. This development could be applied to social media, website, or other publicly available information that could be referenced and scrutinized for legitimacy over the course of an operation using that persona or identity.(Citation: NEWSCASTER2014)(Citation: BlackHatRobinSage)
INSERT INTO public.attack_patterns VALUES (186, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.04706+00', NULL, 'attack-pattern--b6301b64-ef57-4cce-bb0b-77026f14a8db', '[{"url": "https://attack.mitre.org/techniques/T1546", "external_id": "T1546", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/wp-windows-management-instrumentation.pdf", "description": "Ballenthin, W., et al. (2015). Windows Management Instrumentation (WMI) Offense, Defense, and Forensics. Retrieved March 30, 2016.", "source_name": "FireEye WMI 2015"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2015). Malware Persistence on OS X Yosemite. Retrieved July 10, 2017.", "source_name": "Malware Persistence on OS X"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/04/unit42-new-iotlinux-malware-targets-dvrs-forms-botnet/", "description": "Claud Xiao, Cong Zheng, Yanhui Jia. (2017, April 6). New IoT/Linux Malware Targets DVRs, Forms Botnet. Retrieved February 19, 2018.", "source_name": "amnesia malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4816}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring for additions or modifications of mechanisms that could be used to trigger event-based execution, especially the addition of abnormal commands such as execution of unknown programs, opening network sockets, or reaching out across the network. Also look for changes that do not line up with updates, patches, or other planned administrative activity. \\n\\nThese mechanisms may vary by OS, but are typically stored in central repositories that store configuration information such as the Windows Registry, Common Information Model (CIM), and/or specific named files, the last of which can be hashed and compared to known good values. \\n\\nMonitor for processes, API/System calls, and other common ways of manipulating these event repositories. \\n\\nTools such as Sysinternals Autoruns can be used to detect changes to execution triggers that could be attempts at persistence. Also look for abnormal process call trees for execution of other commands that could relate to Discovery actions or other techniques.  \\n\\nMonitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Look for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement. \"", "Str": "Monitoring for additions or modifications of mechanisms that could be used to trigger event-based execution, especially the addition of abnormal commands such as execution of unknown programs, opening network sockets, or reaching out across the network. Also look for changes that do not line up with updates, patches, or other planned administrative activity. \n\nThese mechanisms may vary by OS, but are typically stored in central repositories that store configuration information such as the Windows Registry, Common Information Model (CIM), and/or specific named files, the last of which can be hashed and compared to known good values. \n\nMonitor for processes, API/System calls, and other common ways of manipulating these event repositories. \n\nTools such as Sysinternals Autoruns can be used to detect changes to execution triggers that could be attempts at persistence. Also look for abnormal process call trees for execution of other commands that could relate to Discovery actions or other techniques.  \n\nMonitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Look for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement. ", "Type": 3, "Index": 3162}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4738}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"WMI: WMI Creation\",\n      \"File: File Metadata\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2850}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4706}}', 'T1546', 'Adversaries may establish persistence and/or elevate privileges using system mechanisms that trigger execution based on specific events. Various operating systems have means to monitor and subscribe to events such as logons or other user activity such as running specific applications/binaries. 
INSERT INTO public.attack_patterns VALUES (187, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.103261+00', NULL, 'attack-pattern--e74de37c-a829-446c-937d-56a44f0e9306', '[{"url": "https://attack.mitre.org/techniques/T1098/002", "external_id": "T1098.002", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/exchange/mailboxes/add-mailboxpermission?view=exchange-ps", "description": "Microsoft. (n.d.). Add-Mailbox Permission. Retrieved September 13, 2019.", "source_name": "Microsoft - Add-MailboxPermission"}, {"url": "https://www.fireeye.com/content/dam/collateral/en/mtrends-2018.pdf", "description": "Mandiant. (2018). Mandiant M-Trends 2018. Retrieved July 9, 2018.", "source_name": "FireEye APT35 2018"}, {"url": "https://www.crowdstrike.com/blog/hiding-in-plain-sight-using-the-office-365-activities-api-to-investigate-business-email-compromises/", "description": "Crowdstrike. (2018, July 18). Hiding in Plain Sight: Using the Office 365 Activities API to Investigate Business Email Compromises. Retrieved January 19, 2020.", "source_name": "Crowdstrike Hiding in Plain Sight 2018"}, {"url": "https://www.slideshare.net/DouglasBienstock/shmoocon-2019-becs-and-beyond-investigating-and-defending-office-365", "description": "Bienstock, D.. (2019). BECS and Beyond: Investigating and Defending O365. Retrieved September 13, 2019.", "source_name": "Bienstock, D. - Defending O365 - 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4120}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unusual Exchange and Office 365 email account permissions changes that may indicate excessively broad permissions being granted to compromised accounts.\\n\\nA larger than normal volume of emails sent from an account and similar phishing emails sent from  real accounts within a network may be a sign that an account was compromised and attempts to leverage access with modified email permissions is occurring.\"", "Str": "Monitor for unusual Exchange and Office 365 email account permissions changes that may indicate excessively broad permissions being granted to compromised accounts.\n\nA larger than normal volume of emails sent from an account and similar phishing emails sent from  real accounts within a network may be a sign that an account was compromised and attempts to leverage access with modified email permissions is occurring.", "Type": 3, "Index": 3497}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 4052}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jannie Li, Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 3275}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Group: Group Modification\",\n      \"User Account: User Account Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3380}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3954}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3996}}', 'T1098.002', 'Adversaries may grant additional permission levels, such as ReadPermission or FullAccess, to maintain persistent access to an adversary-controlled email account. The <code>Add-MailboxPermission</code> [PowerShell](https://attack.mitre.org/techniques/T1059/001) cmdlet, available in on-premises Exchange and in the cloud-based service Office 365, adds permissions to a mailbox.(Citation: Microsoft - Add-MailboxPermission)(Citation: FireEye APT35 2018)(Citation: Crowdstrike Hiding in Plain Sight 2018)
INSERT INTO public.attack_patterns VALUES (189, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.172947+00', NULL, 'attack-pattern--853c4192-4311-43e1-bfbb-b11b14911852', '[{"url": "https://attack.mitre.org/techniques/T1480", "external_id": "T1480", "source_name": "mitre-attack"}, {"url": "https://www.cyberscoop.com/kevin-mandia-fireeye-u-s-malware-nice/", "description": "Shoorbajee, Z. (2018, June 1). Playing nice? FireEye CEO says U.S. malware is more restrained than adversaries''. Retrieved January 17, 2019.", "source_name": "FireEye Kevin Mandia Guardrails"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/12/breaking-the-rules-tough-outlook-for-home-page-attacks.html", "description": "McWhirt, M., Carr, N., Bienstock, D. (2019, December 4). Breaking the Rules: A Tough Outlook for Home Page Attacks (CVE-2017-11774). Retrieved June 23, 2020.", "source_name": "FireEye Outlook Dec 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3618}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the use of guardrails may be difficult depending on the implementation. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of [Discovery](https://attack.mitre.org/tactics/TA0007), especially in a short period of time, may aid in detection.\"", "Str": "Detecting the use of guardrails may be difficult depending on the implementation. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of [Discovery](https://attack.mitre.org/tactics/TA0007), especially in a short period of time, may aid in detection.", "Type": 3, "Index": 3100}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3540}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Nick Carr, FireEye\"\n    ]", "Str": "", "Type": 5, "Index": 2775}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2839}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3450}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host forensic analysis\",\n      \"Signature-based detection\",\n      \"Static file analysis\"\n    ]", "Str": "", "Type": 5, "Index": 2950}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3493}}', 'T1480', 'Adversaries may use execution guardrails to constrain execution or actions based on adversary supplied and environment specific conditions that are expected to be present on the target. Guardrails ensure that a payload only executes against an intended target and reduces collateral damage from an adversary’s campaign.(Citation: FireEye Kevin Mandia Guardrails) Values an adversary can provide about a target system or environment to use as guardrails may include specific network share names, attached physical devices, files, joined Active Directory (AD) domains, and local/external IP addresses.(Citation: FireEye Outlook Dec 2019)
INSERT INTO public.attack_patterns VALUES (191, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.261568+00', NULL, 'attack-pattern--8e350c1d-ac79-4b5c-bd4e-7476d7e84ec5', '[{"url": "https://attack.mitre.org/techniques/T1048/002", "external_id": "T1048.002", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2852}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2) \"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2) ", "Type": 3, "Index": 2374}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2774}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2127}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2701}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2743}}', 'T1048.002', 'Adversaries may steal data by exfiltrating it over an asymmetrically encrypted network protocol other than that of the existing command and control channel. The data may also be sent to an alternate network location from the main command and control server. 
INSERT INTO public.attack_patterns VALUES (193, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.342695+00', NULL, 'attack-pattern--92d7da27-2d91-488e-a00c-059dc162766d', '[{"url": "https://attack.mitre.org/techniques/T1041", "external_id": "T1041", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2218}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 1607}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2140}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1360}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2066}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2109}}', 'T1041', 'Adversaries may steal data by exfiltrating it over an existing command and control channel. Stolen data is encoded into the normal communications channel using the same protocol as command and control communications.', 'Exfiltration Over C2 Channel', 0);
INSERT INTO public.attack_patterns VALUES (194, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.38308+00', NULL, 'attack-pattern--51ea26b1-ff1e-4faa-b1a0-1114cd298c87', '[{"url": "https://attack.mitre.org/techniques/T1011", "external_id": "T1011", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2375}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for processes utilizing the network that do not normally have network communication or have never been seen before. Processes that normally require user-driven events to access the network (for example, a web browser opening with a mouse click or key press) but access the network without such may be malicious.\\n\\nMonitor for and investigate changes to host adapter settings, such as addition and/or replication of communication interfaces.\"", "Str": "Monitor for processes utilizing the network that do not normally have network communication or have never been seen before. Processes that normally require user-driven events to access the network (for example, a web browser opening with a mouse click or key press) but access the network without such may be malicious.\n\nMonitor for and investigate changes to host adapter settings, such as addition and/or replication of communication interfaces.", "Type": 3, "Index": 1739}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2297}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Itzik Kotler, SafeBreach\"\n    ]", "Str": "", "Type": 5, "Index": 1422}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1492}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2223}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2266}}', 'T1011', 'Adversaries may attempt to exfiltrate data over a different network medium than the command and control channel. If the command and control network is a wired Internet connection, the exfiltration may occur, for example, over a WiFi connection, modem, cellular data connection, Bluetooth, or another radio frequency (RF) channel.
INSERT INTO public.attack_patterns VALUES (195, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.417269+00', NULL, 'attack-pattern--e6415f09-df0e-48de-9aba-928c902b7549', '[{"url": "https://attack.mitre.org/techniques/T1052", "external_id": "T1052", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1884}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file access on removable media. Detect processes that execute when removable media are mounted.\"", "Str": "Monitor file access on removable media. Detect processes that execute when removable media are mounted.", "Type": 3, "Index": 1503}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1716}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Access\",\n      \"Drive: Drive Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1341}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1641}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Presence of physical medium or device\"\n    ]", "Str": "", "Type": 5, "Index": 1806}, "x_mitre_network_requirements": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1684}}', 'T1052', 'Adversaries may attempt to exfiltrate data via a physical medium, such as a removable drive. In certain circumstances, such as an air-gapped network compromise, exfiltration could occur via a physical medium or device introduced by a user. Such media could be an external hard drive, USB drive, cellular phone, MP3 player, or other removable storage and processing device. The physical medium or device could be used as the final exfiltration point or to hop between otherwise disconnected systems.', 'Exfiltration Over Physical Medium', 0);
INSERT INTO public.attack_patterns VALUES (196, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.472701+00', NULL, 'attack-pattern--79a4052e-1a89-4b09-aea6-51f1d11fe19c', '[{"url": "https://attack.mitre.org/techniques/T1048/001", "external_id": "T1048.001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3096}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2) \\n\\nArtifacts and evidence of symmetric key exchange may be recoverable by analyzing network traffic or looking for hard-coded values within malware. If recovered, these keys can be used to decrypt network data from command and control channels. \"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2) \n\nArtifacts and evidence of symmetric key exchange may be recoverable by analyzing network traffic or looking for hard-coded values within malware. If recovered, these keys can be used to decrypt network data from command and control channels. ", "Type": 3, "Index": 2372}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3018}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2125}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2945}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2987}}', 'T1048.001', 'Adversaries may steal data by exfiltrating it over a symmetrically encrypted network protocol other than that of the existing command and control channel. The data may also be sent to an alternate network location from the main command and control server. 
INSERT INTO public.attack_patterns VALUES (198, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.539204+00', NULL, 'attack-pattern--40597f16-0963-4249-bf4c-ac93b7fb9807', '[{"url": "https://attack.mitre.org/techniques/T1567", "external_id": "T1567", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2074}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.", "Type": 3, "Index": 1560}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1996}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1367}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1922}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1965}}', 'T1567', 'Adversaries may use an existing, legitimate external Web service to exfiltrate data rather than their primary command and control channel. Popular Web services acting as an exfiltration mechanism may give a significant amount of cover due to the likelihood that hosts within a network are already communicating with them prior to compromise. Firewall rules may also already exist to permit traffic to these services.
INSERT INTO public.attack_patterns VALUES (199, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.59032+00', NULL, 'attack-pattern--a3e1e6c5-9c74-4fc0-a16c-a9d228c17829', '[{"url": "https://attack.mitre.org/techniques/T1052/001", "external_id": "T1052.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1665}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file access on removable media. Detect processes that execute when removable media are mounted.\"", "Str": "Monitor file access on removable media. Detect processes that execute when removable media are mounted.", "Type": 3, "Index": 1328}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1497}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Access\",\n      \"Drive: Drive Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1166}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1466}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Presence of physical medium or device\"\n    ]", "Str": "", "Type": 5, "Index": 1587}}', 'T1052.001', 'Adversaries may attempt to exfiltrate data over a USB connected physical device. In certain circumstances, such as an air-gapped network compromise, exfiltration could occur via a USB device introduced by a user. The USB device could be used as the final exfiltration point or to hop between otherwise disconnected systems.', 'Exfiltration over USB', 195);
INSERT INTO public.attack_patterns VALUES (200, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.630138+00', NULL, 'attack-pattern--bf1b6176-597c-4600-bfcd-ac989670f96b', '[{"url": "https://attack.mitre.org/techniques/T1567/002", "external_id": "T1567.002", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2083}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server) to known cloud storage services. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server) to known cloud storage services. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.", "Type": 3, "Index": 1538}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2005}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1345}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1932}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1974}}', 'T1567.002', 'Adversaries may exfiltrate data to a cloud storage service rather than over their primary command and control channel. Cloud storage services allow for the storage, edit, and retrieval of data from a remote cloud storage server over the Internet.
INSERT INTO public.attack_patterns VALUES (307, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.678602+00', NULL, 'attack-pattern--ae7f3575-0a5e-427e-991b-fe03ad44c754', '[{"url": "https://attack.mitre.org/techniques/T1601", "external_id": "T1601", "source_name": "mitre-attack"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#7", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Image File Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Image File Verification"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#13", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Run-Time Memory Integrity Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Run-Time Memory Verification"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3815}, "x_mitre_detection": {"Num": 0, "Raw": "\"Most embedded network devices provide a command to print the version of the currently running operating system.  Use this command to query the operating system for its version number and compare it to what is expected for the device in question.  Because this method may be used in conjunction with [Patch System Image](https://attack.mitre.org/techniques/T1601/001), it may be appropriate to also verify the integrity of the vendor provided operating system image file. \\n\\nCompare the checksum of the operating system file with the checksum of a known good copy from a trusted source.  Some embedded network device platforms may have the capability to calculate the checksum of the file, while others may not.  Even for those platforms that have the capability, it is recommended to download a copy of the file to a trusted computer to calculate the checksum with software that is not compromised.  (Citation: Cisco IOS Software Integrity Assurance - Image File Verification)\\n\\nMany vendors of embedded network devices can provide advanced debugging support that will allow them to work with device owners to validate the integrity of the operating system running in memory.  If a compromise of the operating system is suspected, contact the vendor technical support and seek such services for a more thorough inspection of the current running system.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\"", "Str": "Most embedded network devices provide a command to print the version of the currently running operating system.  Use this command to query the operating system for its version number and compare it to what is expected for the device in question.  Because this method may be used in conjunction with [Patch System Image](https://attack.mitre.org/techniques/T1601/001), it may be appropriate to also verify the integrity of the vendor provided operating system image file. \n\nCompare the checksum of the operating system file with the checksum of a known good copy from a trusted source.  Some embedded network device platforms may have the capability to calculate the checksum of the file, while others may not.  Even for those platforms that have the capability, it is recommended to download a copy of the file to a trusted computer to calculate the checksum with software that is not compromised.  (Citation: Cisco IOS Software Integrity Assurance - Image File Verification)\n\nMany vendors of embedded network devices can provide advanced debugging support that will allow them to work with device owners to validate the integrity of the operating system running in memory.  If a compromise of the operating system is suspected, contact the vendor technical support and seek such services for a more thorough inspection of the current running system.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)", "Type": 3, "Index": 2196}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3767}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2130}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3668}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3711}}', 'T1601', 'Adversaries may make changes to the operating system of embedded network devices to weaken defenses and provide new capabilities for themselves.  On such devices, the operating systems are typically monolithic and most of the device functionality and capabilities are contained within a single file.
INSERT INTO public.attack_patterns VALUES (202, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.759136+00', NULL, 'attack-pattern--3f886f2a-874f-4333-b794-aa6075009b1c', '[{"url": "https://attack.mitre.org/techniques/T1190", "external_id": "T1190", "source_name": "mitre-attack"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2016-6662", "description": "National Vulnerability Database. (2017, February 2). CVE-2016-6662 Detail. Retrieved April 3, 2018.", "source_name": "NVD CVE-2016-6662"}, {"url": "https://www.cisecurity.org/advisory/multiple-vulnerabilities-in-microsoft-windows-smb-server-could-allow-for-remote-code-execution/", "description": "CIS. (2017, May 15). Multiple Vulnerabilities in Microsoft Windows SMB Server Could Allow for Remote Code Execution. Retrieved April 3, 2018.", "source_name": "CIS Multiple SMB Vulnerabilities"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/TA18-106A", "description": "US-CERT. (2018, April 20). Russian State-Sponsored Cyber Actors Targeting Network Infrastructure Devices. Retrieved October 19, 2020.", "source_name": "US-CERT TA18-106A Network Infrastructure Devices 2018"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2014-7169", "description": "National Vulnerability Database. (2017, September 24). CVE-2014-7169 Detail. Retrieved April 3, 2018.", "source_name": "NVD CVE-2014-7169"}, {"url": "https://www.owasp.org/index.php/Category:OWASP_Top_Ten_Project", "description": "OWASP. (2018, February 23). OWASP Top Ten Project. Retrieved April 3, 2018.", "source_name": "OWASP Top 10"}, {"url": "https://cwe.mitre.org/top25/index.html", "description": "Christey, S., Brown, M., Kirby, D., Martin, B., Paller, A.. (2011, September 13). 2011 CWE/SANS Top 25 Most Dangerous Software Errors. Retrieved April 10, 2019.", "source_name": "CWE top 25"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.3\"", "Str": "2.3", "Type": 3, "Index": 5129}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.\"", "Str": "Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.", "Type": 3, "Index": 4654}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Network\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 5000}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 4412}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 4521}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4968}}', 'T1190', 'Adversaries may attempt to take advantage of a weakness in an Internet-facing computer or program using software, data, or commands in order to cause unintended or unanticipated behavior. The weakness in the system can be a bug, a glitch, or a design vulnerability. These applications are often websites, but can include databases (like SQL)(Citation: NVD CVE-2016-6662), standard services (like SMB(Citation: CIS Multiple SMB Vulnerabilities) or SSH), network device administration and management protocols (like SNMP and Smart Install(Citation: US-CERT TA18-106A Network Infrastructure Devices 2018)(Citation: Cisco Blog Legacy Device Attacks)), and any other applications with Internet accessible open sockets, such as web servers and related services.(Citation: NVD CVE-2014-7169) Depending on the flaw being exploited this may include [Exploitation for Defense Evasion](https://attack.mitre.org/techniques/T1211). 
INSERT INTO public.attack_patterns VALUES (203, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.804584+00', NULL, 'attack-pattern--be2dcee9-a7a7-4e38-afd6-21b31ecc3d63', '[{"url": "https://attack.mitre.org/techniques/T1203", "external_id": "T1203", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3910}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting software exploitation may be difficult depending on the tools available. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the browser or Office processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.\"", "Str": "Detecting software exploitation may be difficult depending on the tools available. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the browser or Office processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.", "Type": 3, "Index": 2994}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3572}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3657}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3540}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Remote exploitation for execution requires a remotely accessible service reachable over the network or other vector of access such as spearphishing or drive-by compromise.\"\n    ]", "Str": "", "Type": 5, "Index": 3698}}', 'T1203', 'Adversaries may exploit software vulnerabilities in client applications to execute code. Vulnerabilities can exist in software due to unsecure coding practices that can lead to unanticipated behavior. Adversaries can take advantage of certain vulnerabilities through targeted exploitation for the purpose of arbitrary code execution. Oftentimes the most valuable exploits to an offensive toolkit are those that can be used to obtain code execution on a remote system because they can be used to gain access to that system. Users will expect to see files related to the applications they commonly used to do work, so they are a useful target for exploit research and development because of their high utility.
INSERT INTO public.attack_patterns VALUES (205, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.896949+00', NULL, 'attack-pattern--fe926152-f431-4baf-956c-4ad3cb0bf23b', '[{"url": "https://attack.mitre.org/techniques/T1211", "external_id": "T1211", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2785}, "x_mitre_detection": {"Num": 0, "Raw": "\"Exploitation for defense evasion may happen shortly after the system has been compromised to prevent detection during later actions for for additional tools that may be brought in and used. Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the system that might indicate successful compromise, such as abnormal behavior of processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution or evidence of Discovery.\"", "Str": "Exploitation for defense evasion may happen shortly after the system has been compromised to prevent detection during later actions for for additional tools that may be brought in and used. Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the system that might indicate successful compromise, such as abnormal behavior of processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution or evidence of Discovery.", "Type": 3, "Index": 1900}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2707}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"John Lambert, Microsoft Threat Intelligence Center\"\n    ]", "Str": "", "Type": 5, "Index": 1715}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2617}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"System access controls\"\n    ]", "Str": "", "Type": 5, "Index": 1815}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2660}}', 'T1211', 'Adversaries may exploit a system or application vulnerability to bypass security features. Exploitation of a software vulnerability occurs when an adversary takes advantage of a programming error in a program, service, or within the operating system software or kernel itself to execute adversary-controlled code. Vulnerabilities may exist in defensive security software that can be used to disable or circumvent them.
INSERT INTO public.attack_patterns VALUES (208, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.989533+00', NULL, 'attack-pattern--bbc3cba7-84ae-410d-b18b-16750731dfa2', '[{"url": "https://attack.mitre.org/techniques/T1587/004", "external_id": "T1587.004", "source_name": "mitre-attack"}, {"url": "https://www.nytimes.com/2011/01/16/world/middleeast/16stuxnet.html", "description": "William J. Broad, John Markoff, and David E. Sanger. (2011, January 15). Israeli Test on Worm Called Crucial in Iran Nuclear Delay. Retrieved March 1, 2017.", "source_name": "NYTStuxnet"}, {"url": "https://www.irongeek.com/i.php?page=videos/bsidescharm2017/bsidescharm-2017-t111-microsoft-patch-analysis-for-exploitation-stephen-sims", "description": "Stephen Sims. (2017, April 30). Microsoft Patch Analysis for Exploitation. Retrieved October 16, 2020.", "source_name": "Irongeek Sims BSides 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4185}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on behaviors relating to the use of exploits (i.e. [Exploit Public-Facing Application](https://attack.mitre.org/techniques/T1190), [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203), [Exploitation for Privilege Escalation](https://attack.mitre.org/techniques/T1068), [Exploitation for Defense Evasion](https://attack.mitre.org/techniques/T1211), [Exploitation for Credential Access](https://attack.mitre.org/techniques/T1212), [Exploitation of Remote Services](https://attack.mitre.org/techniques/T1210), and [Application or System Exploitation](https://attack.mitre.org/techniques/T1499/004)).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on behaviors relating to the use of exploits (i.e. [Exploit Public-Facing Application](https://attack.mitre.org/techniques/T1190), [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203), [Exploitation for Privilege Escalation](https://attack.mitre.org/techniques/T1068), [Exploitation for Defense Evasion](https://attack.mitre.org/techniques/T1211), [Exploitation for Credential Access](https://attack.mitre.org/techniques/T1212), [Exploitation of Remote Services](https://attack.mitre.org/techniques/T1210), and [Application or System Exploitation](https://attack.mitre.org/techniques/T1499/004)).", "Type": 3, "Index": 3286}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4141}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4110}}', 'T1587.004', 'Adversaries may develop exploits that can be used during targeting. An exploit takes advantage of a bug or vulnerability in order to cause unintended or unanticipated behavior to occur on computer hardware or software. Rather than finding/modifying exploits from online or purchasing them from exploit vendors, an adversary may develop their own exploits.(Citation: NYTStuxnet) Adversaries may use information acquired via [Vulnerabilities](https://attack.mitre.org/techniques/T1588/006) to focus exploit development efforts. As part of the exploit development process, adversaries may uncover exploitable vulnerabilities through methods such as fuzzing and patch analysis.(Citation: Irongeek Sims BSides 2017)
INSERT INTO public.attack_patterns VALUES (210, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.050679+00', NULL, 'attack-pattern--0cfe31a7-81fc-472c-bc45-e2808d1066a3', '[{"url": "https://attack.mitre.org/techniques/T1491/002", "external_id": "T1491.002", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/current-threats/pdfs/ib-entertainment.pdf", "description": "FireEye. (n.d.). Retrieved April 19, 2019.", "source_name": "FireEye Cyber Threats to Media Industries"}, {"url": "https://www.intelligence.senate.gov/sites/default/files/documents/os-kmandia-033017.pdf", "description": "Kevin Mandia. (2017, March 30). Prepared Statement of Kevin Mandia, CEO of FireEye, Inc. before the United States Senate Select Committee on Intelligence. Retrieved April 19, 2019.", "source_name": "Kevin Mandia Statement to US Senate Committee on Intelligence"}, {"url": "https://torrentfreak.com/anonymous-hackers-deface-russian-govt-site-to-protest-web-blocking-nsfw-180512/", "description": "Andy. (2018, May 12). ‘Anonymous’ Hackers Deface Russian Govt. Site to Protest Web-Blocking (NSFW). Retrieved April 19, 2019.", "source_name": "Anonymous Hackers Deface Russian Govt Site"}, {"url": "https://documents.trendmicro.com/assets/white_papers/wp-a-deep-dive-into-defacement.pdf", "description": "Marco Balduzzi, Ryan Flores, Lion Gu, Federico Maggi, Vincenzo Ciancaglini, Roel Reyes, Akira Urano. (n.d.). A Deep Dive into Defacement: How Geopolitical Events Trigger Web Attacks. Retrieved April 19, 2019.", "source_name": "Trend Micro Deep Dive Into Defacement"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3943}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor external websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.\"", "Str": "Monitor external websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.", "Type": 3, "Index": 3395}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3851}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 3762}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"File: File Creation\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3200}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3820}}', 'T1491.002', 'An adversary may deface systems external to an organization in an attempt to deliver messaging, intimidate, or otherwise mislead an organization or users. Externally-facing websites are a common victim of defacement; often targeted by adversary and hacktivist groups in order to push a political message or spread propaganda.(Citation: FireEye Cyber Threats to Media Industries)(Citation: Kevin Mandia Statement to US Senate Committee on Intelligence)(Citation: Anonymous Hackers Deface Russian Govt Site) [External Defacement](https://attack.mitre.org/techniques/T1491/002) may be used as a catalyst to trigger events, or as a response to actions taken by an organization or government. Similarly, website defacement may also be used as setup, or a precursor, for future attacks such as [Drive-by Compromise](https://attack.mitre.org/techniques/T1189).(Citation: Trend Micro Deep Dive Into Defacement)', 'External Defacement', 129);
INSERT INTO public.attack_patterns VALUES (213, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.157371+00', NULL, 'attack-pattern--0042a9f5-f053-4769-b3ef-9ad018dfa298', '[{"url": "https://attack.mitre.org/techniques/T1055/011", "external_id": "T1055.011", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms633574.aspx", "description": "Microsoft. (n.d.). About Window Classes. Retrieved December 16, 2017.", "source_name": "Microsoft Window Classes"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms633584.aspx", "description": "Microsoft. (n.d.). GetWindowLong function. Retrieved December 16, 2017.", "source_name": "Microsoft GetWindowLong function"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms633591.aspx", "description": "Microsoft. (n.d.). SetWindowLong function. Retrieved December 16, 2017.", "source_name": "Microsoft SetWindowLong function"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://www.malwaretech.com/2013/08/powerloader-injection-something-truly.html", "description": "MalwareTech. (2013, August 13). PowerLoader Injection – Something truly amazing. Retrieved December 16, 2017.", "source_name": "MalwareTech Power Loader Aug 2013"}, {"url": "https://www.welivesecurity.com/2013/03/19/gapz-and-redyms-droppers-based-on-power-loader-code/", "description": "Matrosov, A. (2013, March 19). Gapz and Redyms droppers based on Power Loader code. Retrieved December 16, 2017.", "source_name": "WeLiveSecurity Gapz and Redyms Mar 2013"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms644953.aspx", "description": "Microsoft. (n.d.). SendNotifyMessage function. Retrieved December 16, 2017.", "source_name": "Microsoft SendNotifyMessage function"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6113}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for API calls related to enumerating and manipulating EWM such as GetWindowLong (Citation: Microsoft GetWindowLong function) and SetWindowLong (Citation: Microsoft SetWindowLong function). Malware associated with this technique have also used SendNotifyMessage (Citation: Microsoft SendNotifyMessage function) to trigger the associated window procedure and eventual malicious injection. (Citation: Elastic Process Injection July 2017)\"", "Str": "Monitor for API calls related to enumerating and manipulating EWM such as GetWindowLong (Citation: Microsoft GetWindowLong function) and SetWindowLong (Citation: Microsoft SetWindowLong function). Malware associated with this technique have also used SendNotifyMessage (Citation: Microsoft SendNotifyMessage function) to trigger the associated window procedure and eventual malicious injection. (Citation: Elastic Process Injection July 2017)", "Type": 3, "Index": 5557}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6065}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5400}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6034}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 5475}}', 'T1055.011', 'Adversaries may inject malicious code into process via Extra Window Memory (EWM) in order to evade process-based defenses as well as possibly elevate privileges. EWM injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (214, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.192377+00', NULL, 'attack-pattern--f24faf46-3b26-4dbb-98f2-63460498e433', '[{"url": "https://attack.mitre.org/techniques/T1008", "external_id": "T1008", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2092}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 1481}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2014}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1347}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1940}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1983}}', 'T1008', 'Adversaries may use fallback or alternate communication channels if the primary channel is compromised or inaccessible in order to maintain reliable command and control and to avoid data transfer thresholds.', 'Fallback Channels', 0);
INSERT INTO public.attack_patterns VALUES (308, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.264845+00', NULL, 'attack-pattern--840a987a-99bd-4a80-a5c9-0cb2baa6cade', '[{"url": "https://attack.mitre.org/techniques/T1218/005", "external_id": "T1218.005", "source_name": "mitre-attack"}, {"url": "https://www.cylance.com/content/dam/cylance/pdfs/reports/Op_Dust_Storm_Report.pdf", "description": "Gross, J. (2016, February 23). Operation Dust Storm. Retrieved September 19, 2017.", "source_name": "Cylance Dust Storm"}, {"url": "https://www.redcanary.com/blog/microsoft-html-application-hta-abuse-part-deux/", "description": "McCammon, K. (2015, August 14). Microsoft HTML Application (HTA) Abuse, Part Deux. Retrieved October 27, 2017.", "source_name": "Red Canary HTA Abuse Part Deux"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/04/cve-2017-0199-hta-handler.html", "description": "Berry, A., Galang, L., Jiang, G., Leathery, J., Mohandas, R. (2017, April 11). CVE-2017-0199: In the Wild Attacks Leveraging HTA Handler. Retrieved October 27, 2017.", "source_name": "FireEye Attacks Leveraging HTA"}, {"url": "https://airbus-cyber-security.com/fileless-malware-behavioural-analysis-kovter-persistence/", "description": "Dove, A. (2016, March 23). Fileless Malware – A Behavioural Analysis Of Kovter Persistence. Retrieved December 5, 2017.", "source_name": "Airbus Security Kovter Analysis"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/04/fin7-phishing-lnk.html", "description": "Carr, N., et al. (2017, April 24). FIN7 Evolution and the Phishing LNK. Retrieved April 24, 2017.", "source_name": "FireEye FIN7 April 2017"}, {"url": "https://en.wikipedia.org/wiki/HTML_Application", "description": "Wikipedia. (2017, October 14). HTML Application. Retrieved October 27, 2017.", "source_name": "Wikipedia HTML Application"}, {"url": "https://msdn.microsoft.com/library/ms536471.aspx", "description": "Microsoft. (n.d.). HTML Applications. Retrieved October 27, 2017.", "source_name": "MSDN HTML Applications"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Mshta/", "description": "LOLBAS. (n.d.). Mshta.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Mshta"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5531}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of mshta.exe. Look for mshta.exe executing raw or obfuscated script within the command-line. Compare recent invocations of mshta.exe with prior history of known good arguments and executed .hta files to determine anomalous and potentially adversarial activity. Command arguments used before and after the mshta.exe invocation may also be useful in determining the origin and purpose of the .hta file being executed.\\n\\nMonitor use of HTA files. If they are not typically used within an environment then execution of them may be suspicious\"", "Str": "Use process monitoring to monitor the execution and arguments of mshta.exe. Look for mshta.exe executing raw or obfuscated script within the command-line. Compare recent invocations of mshta.exe with prior history of known good arguments and executed .hta files to determine anomalous and potentially adversarial activity. Command arguments used before and after the mshta.exe invocation may also be useful in determining the origin and purpose of the .hta file being executed.\n\nMonitor use of HTA files. If they are not typically used within an environment then execution of them may be suspicious", "Type": 3, "Index": 4759}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5483}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ye Yint Min Thu Htut, Offensive Security Team, DBS Bank\",\n      \"Ricardo Dias\"\n    ]", "Str": "", "Type": 5, "Index": 4340}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 4463}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5394}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 4657}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5436}}', 'T1218.005', 'Adversaries may abuse mshta.exe to proxy execution of malicious .hta files and Javascript or VBScript through a trusted Windows utility. There are several examples of different types of threats leveraging mshta.exe during initial compromise and for execution of code (Citation: Cylance Dust Storm) (Citation: Red Canary HTA Abuse Part Deux) (Citation: FireEye Attacks Leveraging HTA) (Citation: Airbus Security Kovter Analysis) (Citation: FireEye FIN7 April 2017) 
INSERT INTO public.attack_patterns VALUES (216, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.33314+00', NULL, 'attack-pattern--d63a3fb8-9452-4e9d-a60a-54be68d5998c', '[{"url": "https://attack.mitre.org/techniques/T1070/004", "external_id": "T1070.004", "source_name": "mitre-attack"}, {"url": "http://blog.trendmicro.com/trendlabs-security-intelligence/in-depth-look-apt-attack-tools-of-the-trade/", "description": "Wilhoit, K. (2013, March 4). In-Depth Look: APT Attack Tools of the Trade. Retrieved December 2, 2015.", "source_name": "Trend Micro APT Attack Tools"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3028}, "x_mitre_detection": {"Num": 0, "Raw": "\"It may be uncommon for events related to benign command-line functions such as DEL or third-party utilities or tools to be found in an environment, depending on the user base and how systems are typically used. Monitoring for command-line deletion functions to correlate with binaries or other files that an adversary may drop and remove may lead to detection of malicious activity. Another good practice is monitoring for known deletion and secure deletion tools that are not already on systems within an enterprise network that an adversary could introduce. Some monitoring tools may collect command-line arguments, but may not capture DEL commands since DEL is a native function within cmd.exe.\"", "Str": "It may be uncommon for events related to benign command-line functions such as DEL or third-party utilities or tools to be found in an environment, depending on the user base and how systems are typically used. Monitoring for command-line deletion functions to correlate with binaries or other files that an adversary may drop and remove may lead to detection of malicious activity. Another good practice is monitoring for known deletion and secure deletion tools that are not already on systems within an enterprise network that an adversary could introduce. Some monitoring tools may collect command-line arguments, but may not capture DEL commands since DEL is a native function within cmd.exe.", "Type": 3, "Index": 2129}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2950}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Walker Johnson\"\n    ]", "Str": "", "Type": 5, "Index": 1899}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Deletion\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1959}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2861}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 2064}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2903}}', 'T1070.004', 'Adversaries may delete files left behind by the actions of their intrusion activity. Malware, tools, or other non-native files dropped or created on a system by an adversary may leave traces to indicate to what was done within a network and how. Removal of these files can occur during an intrusion, or as part of a post-intrusion process to minimize the adversary''s footprint.
INSERT INTO public.attack_patterns VALUES (217, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.369372+00', NULL, 'attack-pattern--9a60a291-8960-4387-8a4a-2ab5c18bb50b', '[{"url": "https://attack.mitre.org/techniques/T1071/002", "external_id": "T1071.002", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2598}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2027}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2520}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1897}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2489}}', 'T1071.002', 'Adversaries may communicate using application layer protocols associated with transferring files to avoid detection/network filtering by blending in with existing traffic. Commands to the remote system, and often the results of those commands, will be embedded within the protocol traffic between the client and server. 
INSERT INTO public.attack_patterns VALUES (218, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.409862+00', NULL, 'attack-pattern--7bc57495-ea59-4380-be31-a64af124ef18', '[{"url": "https://attack.mitre.org/techniques/T1083", "external_id": "T1083", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/127.html", "external_id": "CAPEC-127", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/497.html", "external_id": "CAPEC-497", "source_name": "capec"}, {"url": "http://blog.jpcert.or.jp/2016/01/windows-commands-abused-by-attackers.html", "description": "Tomonaga, S. (2016, January 26). Windows Commands Abused by Attackers. Retrieved February 2, 2016.", "source_name": "Windows Commands JPCERT"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 3540}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Collection and Exfiltration, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Collection and Exfiltration, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2361}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3295}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2222}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3166}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Some folders may require Administrator, SYSTEM or specific user depending on permission levels and access controls\"\n    ]", "Str": "", "Type": 5, "Index": 3385}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3209}}', 'T1083', 'Adversaries may enumerate files and directories or may search in specific locations of a host or network share for certain information within a file system. Adversaries may use the information from [File and Directory Discovery](https://attack.mitre.org/techniques/T1083) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.
INSERT INTO public.attack_patterns VALUES (325, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.486645+00', NULL, 'attack-pattern--a750a9f6-0bde-4bb3-9aae-1e2786e9780c', '[{"url": "https://attack.mitre.org/techniques/T1070/005", "external_id": "T1070.005", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/bb490717.aspx", "description": "Microsoft. (n.d.). Net Use. Retrieved November 25, 2016.", "source_name": "Technet Net Use"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2894}, "x_mitre_detection": {"Num": 0, "Raw": "\"Network share connections may be common depending on how an network environment is used. Monitor command-line invocation of <code>net use</code> commands associated with establishing and removing remote shares over SMB, including following best practices for detection of [Windows Admin Shares](https://attack.mitre.org/techniques/T1077). SMB traffic between systems may also be captured and decoded to look for related network share session and file transfer activity. Windows authentication logs are also useful in determining when authenticated network shares are established and by which account, and can be used to correlate network share activity to other events to investigate potentially malicious activity.\"", "Str": "Network share connections may be common depending on how an network environment is used. Monitor command-line invocation of <code>net use</code> commands associated with establishing and removing remote shares over SMB, including following best practices for detection of [Windows Admin Shares](https://attack.mitre.org/techniques/T1077). SMB traffic between systems may also be captured and decoded to look for related network share session and file transfer activity. Windows authentication logs are also useful in determining when authenticated network shares are established and by which account, and can be used to correlate network share activity to other events to investigate potentially malicious activity.", "Type": 3, "Index": 1815}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2677}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Traffic Content\",\n      \"User Account: User Account Authentication\"\n    ]", "Str": "", "Type": 5, "Index": 1538}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2565}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 1750}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Established network share connection to a remote system. Level of access depends on permissions of the account used.\"\n    ]", "Str": "", "Type": 5, "Index": 2737}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2607}}', 'T1070.005', 'Adversaries may remove share connections that are no longer useful in order to clean up traces of their operation. Windows shared drive and [SMB/Windows Admin Shares](https://attack.mitre.org/techniques/T1021/002) connections can be removed when no longer needed. [Net](https://attack.mitre.org/software/S0039) is an example utility that can be used to remove network share connections with the <code>net use \\system\share /delete</code> command. (Citation: Technet Net Use)', 'Network Share Connection Removal', 250);
INSERT INTO public.attack_patterns VALUES (222, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.555565+00', NULL, 'attack-pattern--b77cf5f3-6060-475d-bd60-40ccbf28fdc2', '[{"url": "https://attack.mitre.org/techniques/T1187", "external_id": "T1187", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Server_Message_Block", "description": "Wikipedia. (2017, December 16). Server Message Block. Retrieved December 21, 2017.", "source_name": "Wikipedia Server Message Block"}, {"url": "https://blog.didierstevens.com/2017/11/13/webdav-traffic-to-malicious-sites/", "description": "Stevens, D. (2017, November 13). WebDAV Traffic To Malicious Sites. Retrieved December 21, 2017.", "source_name": "Didier Stevens WebDAV Traffic"}, {"url": "https://www.microsoft.com/technet/prodtechnol/WindowsServer2003/Library/IIS/4beddb35-0cba-424c-8b9b-a5832ad8e208.mspx", "description": "Microsoft. (n.d.). Managing WebDAV Security (IIS 6.0). Retrieved December 21, 2017.", "source_name": "Microsoft Managing WebDAV Security"}, {"url": "https://github.com/hob0/hashjacking", "description": "Dunning, J. (2016, August 1). Hashjacking. Retrieved December 21, 2017.", "source_name": "GitHub Hashjacking"}, {"url": "https://www.cylance.com/content/dam/cylance/pdfs/white_papers/RedirectToSMB.pdf", "description": "Cylance. (2015, April 13). Redirect to SMB. Retrieved December 21, 2017.", "source_name": "Cylance Redirect to SMB"}, {"url": "https://osandamalith.com/2017/03/24/places-of-interest-in-stealing-netntlm-hashes/", "description": "Osanda Malith Jayathissa. (2017, March 24). Places of Interest in Stealing NetNTLM Hashes. Retrieved January 26, 2018.", "source_name": "Osanda Stealing NetNTLM Hashes"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA17-293A", "description": "US-CERT. (2017, October 20). Alert (TA17-293A): Advanced Persistent Threat Activity Targeting Energy and Other Critical Infrastructure Sectors. Retrieved November 2, 2017.", "source_name": "US-CERT APT Energy Oct 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6867}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for SMB traffic on TCP ports 139, 445 and UDP port 137 and WebDAV traffic attempting to exit the network to unknown external systems. If attempts are detected, then investigate endpoint data sources to find the root cause. For internal traffic, monitor the workstation-to-workstation unusual (vs. baseline) SMB traffic. For many networks there should not be any, but it depends on how systems on the network are configured and where resources are located.\\n\\nMonitor creation and modification of .LNK, .SCF, or any other files on systems and within virtual environments that contain resources that point to external network resources as these could be used to gather credentials when the files are rendered. (Citation: US-CERT APT Energy Oct 2017)\"", "Str": "Monitor for SMB traffic on TCP ports 139, 445 and UDP port 137 and WebDAV traffic attempting to exit the network to unknown external systems. If attempts are detected, then investigate endpoint data sources to find the root cause. For internal traffic, monitor the workstation-to-workstation unusual (vs. baseline) SMB traffic. For many networks there should not be any, but it depends on how systems on the network are configured and where resources are located.\n\nMonitor creation and modification of .LNK, .SCF, or any other files on systems and within virtual environments that contain resources that point to external network resources as these could be used to gather credentials when the files are rendered. (Citation: US-CERT APT Energy Oct 2017)", "Type": 3, "Index": 5939}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6819}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Teodor Cimpoesu\",\n      \"Sudhanshu Chauhan, @Sudhanshu_C\"\n    ]", "Str": "", "Type": 5, "Index": 5618}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 5720}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6729}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6772}}', 'T1187', 'Adversaries may gather credential material by invoking or forcing a user to automatically provide authentication information through a mechanism in which they can intercept.
INSERT INTO public.attack_patterns VALUES (223, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.592347+00', NULL, 'attack-pattern--94cb00a4-b295-4d06-aa2b-5653b9c1be9c', '[{"url": "https://attack.mitre.org/techniques/T1606", "external_id": "T1606", "source_name": "mitre-attack"}, {"url": "https://github.com/damianh/aws-adfs-credential-generator", "description": "Damian Hickey. (2017, January 28). AWS-ADFS-Credential-Generator. Retrieved December 16, 2020.", "source_name": "GitHub AWS-ADFS-Credential-Generator"}, {"url": "https://wunderwuzzi23.github.io/blog/passthecookie.html", "description": "Rehberger, J. (2018, December). Pivot to the Cloud using Pass the Cookie. Retrieved April 5, 2019.", "source_name": "Pass The Cookie"}, {"url": "https://unit42.paloaltonetworks.com/mac-malware-steals-cryptocurrency-exchanges-cookies/", "description": "Chen, Y., Hu, W., Xu, Z., et. al. (2019, January 31). Mac Malware Steals Cryptocurrency Exchanges’ Cookies. Retrieved October 14, 2019.", "source_name": "Unit 42 Mac Crypto Cookies January 2019"}, {"url": "https://msrc-blog.microsoft.com/2020/12/13/customer-guidance-on-recent-nation-state-cyber-attacks/", "description": "MSRC. (2020, December 13). Customer Guidance on Recent Nation-State Cyber Attacks. Retrieved December 17, 2020.", "source_name": "Microsoft SolarWinds Customer Guidance"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4117}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for anomalous authentication activity, such as logons or other user session activity associated with unknown accounts. Monitor for unexpected and abnormal access to resources, including access of websites and cloud-based applications by the same user in different locations or by different systems that do not match expected configurations.\"", "Str": "Monitor for anomalous authentication activity, such as logons or other user session activity associated with unknown accounts. Monitor for unexpected and abnormal access to resources, including access of websites and cloud-based applications by the same user in different locations or by different systems that do not match expected configurations.", "Type": 3, "Index": 3546}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"SaaS\",\n      \"Windows\",\n      \"macOS\",\n      \"Linux\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3961}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Web Credential: Web Credential Creation\",\n      \"Web Credential: Web Credential Usage\"\n    ]", "Str": "", "Type": 5, "Index": 3371}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3929}}', 'T1606', 'Adversaries may forge credential materials that can be used to gain access to web applications or Internet services. Web applications and services (hosted in cloud SaaS environments or on-premise servers) often use session cookies, tokens, or other materials to authenticate and authorize user access.
INSERT INTO public.attack_patterns VALUES (224, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.640145+00', NULL, 'attack-pattern--a2029942-0a85-4947-b23c-ca434698171d', '[{"url": "https://attack.mitre.org/techniques/T1056/002", "external_id": "T1056.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/659.html", "external_id": "CAPEC-659", "source_name": "capec"}, {"url": "https://baesystemsai.blogspot.com/2015/06/new-mac-os-malware-exploits-mackeeper.html", "description": "Sergei Shevchenko. (2015, June 4). New Mac OS Malware Exploits Mackeeper. Retrieved July 3, 2017.", "source_name": "OSX Malware Exploits MacKeeper"}, {"url": "https://logrhythm.com/blog/do-you-trust-your-computer/", "description": "Foss, G. (2014, October 3). Do You Trust Your Computer?. Retrieved December 17, 2018.", "source_name": "LogRhythm Do You Trust Oct 2014"}, {"url": "https://www.welivesecurity.com/2016/07/06/new-osxkeydnap-malware-hungry-credentials/", "description": "Marc-Etienne M.Leveille. (2016, July 6). New OSX/Keydnap malware is hungry for credentials. Retrieved July 3, 2017.", "source_name": "OSX Keydnap malware"}, {"url": "https://enigma0x3.net/2015/01/21/phishing-for-credentials-if-you-want-it-just-ask/", "description": "Nelson, M. (2015, January 21). Phishing for Credentials: If you want it, just ask!. Retrieved December 17, 2018.", "source_name": "Enigma Phishing for Credentials Jan 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3997}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process execution for unusual programs as well as malicious instances of [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059) that could be used to prompt users for credentials.\\n\\nInspect and scrutinize input prompts for indicators of illegitimacy, such as non-traditional banners, text, timing, and/or sources.\"", "Str": "Monitor process execution for unusual programs as well as malicious instances of [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059) that could be used to prompt users for credentials.\n\nInspect and scrutinize input prompts for indicators of illegitimacy, such as non-traditional banners, text, timing, and/or sources.", "Type": 3, "Index": 3464}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3934}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthew Molyett, @s1air, Cisco Talos\"\n    ]", "Str": "", "Type": 5, "Index": 3314}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3396}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3845}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3887}}', 'T1056.002', 'Adversaries may mimic common operating system GUI components to prompt users for credentials with a seemingly legitimate prompt. When programs are executed that need additional privileges than are present in the current user context, it is common for the operating system to prompt the user for proper credentials to authorize the elevated privileges for the task (ex: [Bypass User Account Control](https://attack.mitre.org/techniques/T1548/002)).
INSERT INTO public.attack_patterns VALUES (234, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.674126+00', NULL, 'attack-pattern--d40239b3-05ff-46d8-9bdd-b46d13463ef9', '[{"url": "https://attack.mitre.org/techniques/T1200", "external_id": "T1200", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/440.html", "external_id": "CAPEC-440", "source_name": "capec"}, {"url": "https://ossmann.blogspot.com/2011/02/throwing-star-lan-tap.html", "description": "Michael Ossmann. (2011, February 17). Throwing Star LAN Tap. Retrieved March 30, 2018.", "source_name": "Ossmann Star Feb 2011"}, {"url": "https://www.youtube.com/watch?v=lDvf4ScWbcQ", "description": "Nick Aleks. (2015, November 7). Weapons of a Pentester - Understanding the virtual & physical tools used by white/black hat hackers. Retrieved March 30, 2018.", "source_name": "Aleks Weapons Nov 2015"}, {"url": "https://www.hak5.org/blog/main-blog/stealing-files-with-the-usb-rubber-ducky-usb-exfiltration-explained", "description": "Hak5. (2016, December 7). Stealing Files with the USB Rubber Ducky – USB Exfiltration Explained. Retrieved March 30, 2018.", "source_name": "Hak5 RubberDuck Dec 2016"}, {"url": "https://www.youtube.com/watch?v=fXthwl6ShOg", "description": "Ulf Frisk. (2016, August 5). Direct Memory Attack the Kernel. Retrieved March 30, 2018.", "source_name": "Frisk DMA August 2016"}, {"url": "https://arstechnica.com/information-technology/2012/03/the-pwn-plug-is-a-little-white-box-that-can-hack-your-network/", "description": "Robert McMillan. (2012, March 3). The Pwn Plug is a little white box that can hack your network. Retrieved March 30, 2018.", "source_name": "McMillan Pwn March 2012"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3604}, "x_mitre_detection": {"Num": 0, "Raw": "\"Asset management systems may help with the detection of computer systems or network devices that should not exist on a network. \\n\\nEndpoint sensors may be able to detect the addition of hardware via USB, Thunderbolt, and other external device communication ports.\"", "Str": "Asset management systems may help with the detection of computer systems or network devices that should not exist on a network. \n\nEndpoint sensors may be able to detect the addition of hardware via USB, Thunderbolt, and other external device communication ports.", "Type": 3, "Index": 3195}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3526}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3494}}', 'T1200', 'Adversaries may introduce computer accessories, computers, or networking hardware into a system or network that can be used as a vector to gain access. While public references of usage by APT groups are scarce, many penetration testers leverage hardware additions for initial access. Commercial and open source products are leveraged with capabilities such as passive network tapping (Citation: Ossmann Star Feb 2011), man-in-the middle encryption breaking (Citation: Aleks Weapons Nov 2015), keystroke injection (Citation: Hak5 RubberDuck Dec 2016), kernel memory reading via DMA (Citation: Frisk DMA August 2016), adding new wireless access to an existing network (Citation: McMillan Pwn March 2012), and others.', 'Hardware Additions', 0);
INSERT INTO public.attack_patterns VALUES (226, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.747975+00', NULL, 'attack-pattern--09312b1a-c3c6-4b45-9844-3ccc78e5d82f', '[{"url": "https://attack.mitre.org/techniques/T1592", "external_id": "T1592", "source_name": "mitre-attack"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3154}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2712}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3110}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3078}}', 'T1592', 'Adversaries may gather information about the victim''s hosts that can be used during targeting. Information about hosts may include a variety of details, including administrative data (ex: name, assigned IP, functionality, etc.) as well as specifics regarding its configuration (ex: operating system, language, etc.).
INSERT INTO public.attack_patterns VALUES (228, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.869141+00', NULL, 'attack-pattern--9d48cab2-7929-4812-ad22-f536665f0109', '[{"url": "https://attack.mitre.org/techniques/T1590", "external_id": "T1590", "source_name": "mitre-attack"}, {"url": "https://www.whois.net/", "description": "NTT America. (n.d.). Whois Lookup. Retrieved October 20, 2020.", "source_name": "WHOIS"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3172}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2730}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3128}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3096}}', 'T1590', 'Adversaries may gather information about the victim''s networks that can be used during targeting. Information about networks may include a variety of details, including administrative data (ex: IP ranges, domain names, etc.) as well as specifics regarding its topology and operations.
INSERT INTO public.attack_patterns VALUES (244, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.921246+00', NULL, 'attack-pattern--6d4a7fb3-5a24-42be-ae61-6728a2b581f6', '[{"url": "https://attack.mitre.org/techniques/T1546/012", "external_id": "T1546.012", "source_name": "mitre-attack"}, {"url": "https://blogs.msdn.microsoft.com/mithuns/2010/03/24/image-file-execution-options-ifeo/", "description": "Shanbhag, M. (2010, March 24). Image File Execution Options (IFEO). Retrieved December 18, 2017.", "source_name": "Microsoft Dev Blog IFEO Mar 2010"}, {"url": "https://docs.microsoft.com/windows-hardware/drivers/debugger/gflags-overview", "description": "Microsoft. (2017, May 23). GFlags Overview. Retrieved December 18, 2017.", "source_name": "Microsoft GFlags Mar 2017"}, {"url": "https://docs.microsoft.com/windows-hardware/drivers/debugger/registry-entries-for-silent-process-exit", "description": "Marshall, D. & Griffin, S. (2017, November 28). Monitoring Silent Process Exit. Retrieved June 27, 2018.", "source_name": "Microsoft Silent Process Exit NOV 2017"}, {"url": "https://oddvar.moe/2018/04/10/persistence-using-globalflags-in-image-file-execution-options-hidden-from-autoruns-exe/", "description": "Moe, O. (2018, April 10). Persistence using GlobalFlags in Image File Execution Options - Hidden from Autoruns.exe. Retrieved June 27, 2018.", "source_name": "Oddvar Moe IFEO APR 2018"}, {"url": "http://blog.crowdstrike.com/registry-analysis-with-crowdresponse/", "description": "Tilbury, C. (2014, August 28). Registry Analysis with CrowdResponse. Retrieved November 12, 2014.", "source_name": "Tilbury 2014"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://www.f-secure.com/v-descs/backdoor_w32_hupigon_emv.shtml", "description": "FSecure. (n.d.). Backdoor - W32/Hupigon.EMV - Threat Description. Retrieved December 18, 2017.", "source_name": "FSecure Hupigon"}, {"url": "https://www.symantec.com/security_response/writeup.jsp?docid=2008-062807-2501-99&tabid=2", "description": "Symantec. (2008, June 28). Trojan.Ushedix. Retrieved December 18, 2017.", "source_name": "Symantec Ushedix June 2008"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 7436}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for abnormal usage of the GFlags tool as well as common processes spawned under abnormal parents and/or with creation flags indicative of debugging such as <code>DEBUG_PROCESS</code> and <code>DEBUG_ONLY_THIS_PROCESS</code>. (Citation: Microsoft Dev Blog IFEO Mar 2010)\\n\\nMonitor Registry values associated with IFEOs, as well as silent process exit monitoring, for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code>. (Citation: Elastic Process Injection July 2017)\"", "Str": "Monitor for abnormal usage of the GFlags tool as well as common processes spawned under abnormal parents and/or with creation flags indicative of debugging such as <code>DEBUG_PROCESS</code> and <code>DEBUG_ONLY_THIS_PROCESS</code>. (Citation: Microsoft Dev Blog IFEO Mar 2010)\n\nMonitor Registry values associated with IFEOs, as well as silent process exit monitoring, for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code>. (Citation: Elastic Process Injection July 2017)", "Type": 3, "Index": 6569}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7388}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oddvar Moe, @oddvarmoe\"\n    ]", "Str": "", "Type": 5, "Index": 6336}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 6404}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7274}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 7316}}', 'T1546.012', 'Adversaries may establish persistence and/or elevate privileges by executing malicious content triggered by Image File Execution Options (IFEO) debuggers. IFEOs enable a developer to attach a debugger to an application. When a process is created, a debugger present in an application’s IFEO will be prepended to the application’s name, effectively launching the new process under the debugger (e.g., <code>C:\dbg\ntsd.exe -g  notepad.exe</code>). (Citation: Microsoft Dev Blog IFEO Mar 2010)
INSERT INTO public.attack_patterns VALUES (230, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.046068+00', NULL, 'attack-pattern--768dce68-8d0d-477a-b01d-0eea98b963a1', '[{"url": "https://attack.mitre.org/techniques/T1558/001", "external_id": "T1558.001", "source_name": "mitre-attack"}, {"url": "https://adsecurity.org/?p=1640", "description": "Metcalf, S. (2015, August 7). Kerberos Golden Tickets are Now More Golden. Retrieved December 1, 2017.", "source_name": "AdSecurity Kerberos GT Aug 2015"}, {"url": "https://cert.europa.eu/static/WhitePapers/UPDATED%20-%20CERT-EU_Security_Whitepaper_2014-007_Kerberos_Golden_Ticket_Protection_v1_4.pdf", "description": "Abolins, D., Boldea, C., Socha, K., Soria-Machado, M. (2016, April 26). Kerberos Golden Ticket Protection. Retrieved July 13, 2017.", "source_name": "CERT-EU Golden Ticket Protection"}, {"url": "https://adsecurity.org/?p=1515", "description": "Metcalf, S. (2015, May 03). Detecting Forged Kerberos Ticket (Golden Ticket & Silver Ticket) Use in Active Directory. Retrieved December 23, 2015.", "source_name": "ADSecurity Detecting Forged Tickets"}, {"url": "https://adsecurity.org/?p=483", "description": "Sean Metcalf. (2014, November 10). Kerberos & KRBTGT: Active Directory’s Domain Kerberos Service Account. Retrieved January 30, 2020.", "source_name": "ADSecurity Kerberos and KRBTGT"}, {"url": "https://blog.stealthbits.com/detect-pass-the-ticket-attacks", "description": "Jeff Warren. (2019, February 19). How to Detect Pass-the-Ticket Attacks. Retrieved February 27, 2020.", "source_name": "Stealthbits Detect PtT 2019"}, {"url": "https://gallery.technet.microsoft.com/scriptcenter/Kerberos-Golden-Ticket-b4814285", "description": "Microsoft. (2015, March 24). Kerberos Golden Ticket Check (Updated). Retrieved February 27, 2020.", "source_name": "Microsoft Kerberos Golden Ticket"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4691}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4672, 4634), RC4 encryption within TGTs, and TGS requests without preceding TGT requests.(Citation: ADSecurity Kerberos and KRBTGT)(Citation: CERT-EU Golden Ticket Protection)(Citation: Stealthbits Detect PtT 2019)\\n\\nMonitor the lifetime of TGT tickets for values that differ from the default domain duration.(Citation: Microsoft Kerberos Golden Ticket)\\n\\nMonitor for indications of [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003) being used to move laterally. \\n\"", "Str": "Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4672, 4634), RC4 encryption within TGTs, and TGS requests without preceding TGT requests.(Citation: ADSecurity Kerberos and KRBTGT)(Citation: CERT-EU Golden Ticket Protection)(Citation: Stealthbits Detect PtT 2019)\n\nMonitor the lifetime of TGT tickets for values that differ from the default domain duration.(Citation: Microsoft Kerberos Golden Ticket)\n\nMonitor for indications of [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003) being used to move laterally. \n", "Type": 3, "Index": 3915}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4643}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Itamar Mizrahi, Cymptom\"\n    ]", "Str": "", "Type": 5, "Index": 3703}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Credential Request\",\n      \"Logon Session: Logon Session Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3772}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4554}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4596}}', 'T1558.001', 'Adversaries who have the KRBTGT account password hash may forge Kerberos ticket-granting tickets (TGT), also known as a golden ticket.(Citation: AdSecurity Kerberos GT Aug 2015) Golden tickets enable adversaries to generate authentication material for any account in Active Directory.(Citation: CERT-EU Golden Ticket Protection) 
INSERT INTO public.attack_patterns VALUES (231, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.072425+00', NULL, 'attack-pattern--5d2be8b9-d24c-4e98-83bf-2f5f79477163', '[{"url": "https://attack.mitre.org/techniques/T1484/001", "external_id": "T1484.001", "source_name": "mitre-attack"}, {"url": "https://blogs.technet.microsoft.com/musings_of_a_technical_tam/2012/02/13/group-policy-basics-part-1-understanding-the-structure-of-a-group-policy-object/", "description": "srachui. (2012, February 13). Group Policy Basics – Part 1: Understanding the Structure of a Group Policy Object. Retrieved March 5, 2019.", "source_name": "TechNet Group Policy Basics"}, {"url": "https://adsecurity.org/?p=2716", "description": "Metcalf, S. (2016, March 14). Sneaky Active Directory Persistence #17: Group Policy. Retrieved March 5, 2019.", "source_name": "ADSecurity GPO Persistence 2016"}, {"url": "https://wald0.com/?p=179", "description": "Robbins, A. (2018, April 2). A Red Teamer’s Guide to GPOs and OUs. Retrieved March 5, 2019.", "source_name": "Wald0 Guide to GPOs"}, {"url": "http://www.harmj0y.net/blog/redteaming/abusing-gpo-permissions/", "description": "Schroeder, W. (2016, March 17). Abusing GPO Permissions. Retrieved March 5, 2019.", "source_name": "Harmj0y Abusing GPO Permissions"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/current-threats/pdfs/rpt-mtrends-2016.pdf", "description": "Mandiant. (2016, February 25). Mandiant M-Trends 2016. Retrieved March 5, 2019.", "source_name": "Mandiant M Trends 2016"}, {"url": "https://www.microsoft.com/security/blog/2016/06/01/hacking-team-breach-a-cyber-jurassic-park/", "description": "Microsoft Secure Team. (2016, June 1). Hacking Team Breach: A Cyber Jurassic Park. Retrieved March 5, 2019.", "source_name": "Microsoft Hacking Team Breach"}, {"url": "http://www.harmj0y.net/blog/activedirectory/the-most-dangerous-user-right-you-probably-have-never-heard-of/", "description": "Schroeder, W. (2017, January 10). The Most Dangerous User Right You (Probably) Have Never Heard Of. Retrieved March 5, 2019.", "source_name": "Harmj0y SeEnableDelegationPrivilege Right"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 7002}, "x_mitre_detection": {"Num": 0, "Raw": "\"It is possible to detect GPO modifications by monitoring directory service changes using Windows event logs. Several events may be logged for such GPO modifications, including:\\n\\n* Event ID 5136 - A directory service object was modified\\n* Event ID 5137 - A directory service object was created\\n* Event ID 5138 - A directory service object was undeleted\\n* Event ID 5139 - A directory service object was moved\\n* Event ID 5141 - A directory service object was deleted\\n\\n\\nGPO abuse will often be accompanied by some other behavior such as [Scheduled Task/Job](https://attack.mitre.org/techniques/T1053), which will have events associated with it to detect. Subsequent permission value modifications, like those to SeEnableDelegationPrivilege, can also be searched for in events associated with privileges assigned to new logons (Event ID 4672) and assignment of user rights (Event ID 4704).\"", "Str": "It is possible to detect GPO modifications by monitoring directory service changes using Windows event logs. Several events may be logged for such GPO modifications, including:\n\n* Event ID 5136 - A directory service object was modified\n* Event ID 5137 - A directory service object was created\n* Event ID 5138 - A directory service object was undeleted\n* Event ID 5139 - A directory service object was moved\n* Event ID 5141 - A directory service object was deleted\n\n\nGPO abuse will often be accompanied by some other behavior such as [Scheduled Task/Job](https://attack.mitre.org/techniques/T1053), which will have events associated with it to detect. Subsequent permission value modifications, like those to SeEnableDelegationPrivilege, can also be searched for in events associated with privileges assigned to new logons (Event ID 4672) and assignment of user rights (Event ID 4704).", "Type": 3, "Index": 5914}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6954}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Itamar Mizrahi, Cymptom\",\n      \"Tristan Bennett, Seamless Intelligence\"\n    ]", "Str": "", "Type": 5, "Index": 5544}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Object Creation\",\n      \"Active Directory: Active Directory Object Deletion\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5661}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6842}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6884}}', 'T1484.001', 'Adversaries may modify Group Policy Objects (GPOs) to subvert the intended discretionary access controls for a domain, usually with the intention of escalating privileges on the domain. Group policy allows for centralized management of user and computer settings in Active Directory (AD). GPOs are containers for group policy settings made up of files stored within a predicable network path <code>\\&lt;DOMAIN&gt;\SYSVOL\&lt;DOMAIN&gt;\Policies\</code>.(Citation: TechNet Group Policy Basics)(Citation: ADSecurity GPO Persistence 2016) 
INSERT INTO public.attack_patterns VALUES (233, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.135205+00', NULL, 'attack-pattern--24286c33-d4a4-4419-85c2-1d094a896c26', '[{"url": "https://attack.mitre.org/techniques/T1592/001", "external_id": "T1592.001", "source_name": "mitre-attack"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3242}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2801}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3198}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3167}}', 'T1592.001', 'Adversaries may gather information about the victim''s host hardware that can be used during targeting. Information about hardware infrastructure may include a variety of details such as types and versions on specific hosts, as well as the presence of additional components that might be indicative of added defensive protections (ex: card/biometric readers, dedicated encryption hardware, etc.).
INSERT INTO public.attack_patterns VALUES (236, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.200053+00', NULL, 'attack-pattern--ec8fc7e2-b356-455c-8db5-2e37be158e7d', '[{"url": "https://attack.mitre.org/techniques/T1564/001", "external_id": "T1564.001", "source_name": "mitre-attack"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/01/new-mac-backdoor-using-antiquated-code/", "description": "Thomas Reed. (2017, January 18). New Mac backdoor using antiquated code. Retrieved July 5, 2017.", "source_name": "Antiquated Mac Malware"}, {"url": "https://www.paloaltonetworks.com/content/dam/pan/en_US/assets/pdf/reports/Unit_42/unit42-wirelurker.pdf", "description": "Claud Xiao. (n.d.). WireLurker: A New Era in iOS and OS X Malware. Retrieved July 10, 2017.", "source_name": "WireLurker"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4101}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the file system and shell commands for files being created with a leading \\\".\\\" and the Windows command-line use of attrib.exe to add the hidden attribute.\"", "Str": "Monitor the file system and shell commands for files being created with a leading \".\" and the Windows command-line use of attrib.exe to add the hidden attribute.", "Type": 3, "Index": 3736}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4023}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3502}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3934}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 3671}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3976}}', 'T1564.001', 'Adversaries may set files and directories to be hidden to evade detection mechanisms. To prevent normal users from accidentally changing special files on a system, most operating systems have the concept of a ‘hidden’ file. These files don’t show up when a user browses the file system with a GUI or when using normal commands on the command line. Users must explicitly ask to show the hidden files either via a series of Graphical User Interface (GUI) prompts or with command line switches (<code>dir /a</code> for Windows and <code>ls –a</code> for Linux and macOS).
INSERT INTO public.attack_patterns VALUES (238, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.286554+00', NULL, 'attack-pattern--cbb66055-0325-4111-aca0-40547b6ad5b0', '[{"url": "https://attack.mitre.org/techniques/T1564/003", "external_id": "T1564.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/Microsoft.PowerShell.Core/About/about_PowerShell_exe?view=powershell-5.1", "description": "Wheeler, S. et al.. (2019, May 1). About PowerShell.exe. Retrieved October 11, 2019.", "source_name": "PowerShell About 2019"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/01/new-mac-backdoor-using-antiquated-code/", "description": "Thomas Reed. (2017, January 18). New Mac backdoor using antiquated code. Retrieved July 5, 2017.", "source_name": "Antiquated Mac Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3589}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions indicative of hidden windows. In Windows, enable and configure event logging and PowerShell logging to check for the hidden window style. In MacOS, plist files are ASCII text files with a specific format, so they''re relatively easy to parse. File monitoring can check for the <code>apple.awt.UIElement</code> or any other suspicious plist tag in plist files and flag them.\"", "Str": "Monitor processes and command-line arguments for actions indicative of hidden windows. In Windows, enable and configure event logging and PowerShell logging to check for the hidden window style. In MacOS, plist files are ASCII text files with a specific format, so they''re relatively easy to parse. File monitoring can check for the <code>apple.awt.UIElement</code> or any other suspicious plist tag in plist files and flag them.", "Type": 3, "Index": 2973}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3526}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 2734}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Script: Script Execution\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2802}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3437}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3479}}', 'T1564.003', 'Adversaries may use hidden windows to conceal malicious activity from the plain sight of users. In some cases, windows that would typically be displayed when an application carries out an operation can be hidden. This may be utilized by system administrators to avoid disrupting user work environments when carrying out administrative tasks. 
INSERT INTO public.attack_patterns VALUES (240, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.39534+00', NULL, 'attack-pattern--aedfca76-3b30-4866-b2aa-0f1d7fd1e4b6', '[{"url": "https://attack.mitre.org/techniques/T1574", "external_id": "T1574", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/sysinternals/downloads/autoruns", "description": "Mark Russinovich. (2019, June 28). Autoruns for Windows v13.96. Retrieved March 13, 2020.", "source_name": "Autoruns for Windows"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4847}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file systems for moving, renaming, replacing, or modifying DLLs. Changes in the set of DLLs that are loaded by a process (compared with past behavior) that do not correlate with known software, patches, etc., are suspicious. Monitor DLLs loaded into a process and detect DLLs that have the same file name but abnormal paths. Modifications to or creation of .manifest and .local redirection files that do not correlate with software updates are suspicious.\\n\\nLook for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\\n\\nMonitor for changes to environment variables, as well as the commands to implement these changes.\\n\\nMonitor processes for unusual activity (e.g., a process that does not use the network begins to do so, abnormal process call trees). Track library metadata, such as a hash, and compare libraries that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.\\n\\nService changes are reflected in the Registry. Modification to existing services should not occur frequently. If a service binary path or failure parameters are changed to values that are not typical for that service and does not correlate with software updates, then it may be due to malicious activity. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current service information. (Citation: Autoruns for Windows) Suspicious program execution through services may show up as outlier processes that have not been seen before when compared against historical data.\"", "Str": "Monitor file systems for moving, renaming, replacing, or modifying DLLs. Changes in the set of DLLs that are loaded by a process (compared with past behavior) that do not correlate with known software, patches, etc., are suspicious. Monitor DLLs loaded into a process and detect DLLs that have the same file name but abnormal paths. Modifications to or creation of .manifest and .local redirection files that do not correlate with software updates are suspicious.\n\nLook for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\n\nMonitor for changes to environment variables, as well as the commands to implement these changes.\n\nMonitor processes for unusual activity (e.g., a process that does not use the network begins to do so, abnormal process call trees). Track library metadata, such as a hash, and compare libraries that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.\n\nService changes are reflected in the Registry. Modification to existing services should not occur frequently. If a service binary path or failure parameters are changed to values that are not typical for that service and does not correlate with software updates, then it may be due to malicious activity. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current service information. (Citation: Autoruns for Windows) Suspicious program execution through services may show up as outlier processes that have not been seen before when compared against historical data.", "Type": 3, "Index": 2512}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4769}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2132}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4737}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2430}}', 'T1574', 'Adversaries may execute their own malicious payloads by hijacking the way operating systems run programs. Hijacking execution flow can be for the purposes of persistence, since this hijacked execution may reoccur over time. Adversaries may also use these mechanisms to elevate privileges or evade defenses, such as application control or other restrictions on execution.
INSERT INTO public.attack_patterns VALUES (242, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.50958+00', NULL, 'attack-pattern--2339cf19-8f1e-48f7-8a91-0262ba547b6f', '[{"url": "https://attack.mitre.org/techniques/T1591/003", "external_id": "T1591.003", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/broadvoice-leaks-350m-records-voicemail-transcripts/160158/", "description": "Seals, T. (2020, October 15). Broadvoice Leak Exposes 350M Records, Personal Voicemail Transcripts. Retrieved October 20, 2020.", "source_name": "ThreatPost Broadvoice Leak"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3003}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2562}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2959}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2928}}', 'T1591.003', 'Adversaries may gather information about the victim''s business tempo that can be used during targeting. Information about an organization’s business tempo may include a variety of details, including operational hours/days of the week. This information may also reveal times/dates of purchases and shipments of the victim’s hardware and software resources.
INSERT INTO public.attack_patterns VALUES (246, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.62165+00', NULL, 'attack-pattern--3d333250-30e4-4a82-9edc-756c68afc529', '[{"url": "https://attack.mitre.org/techniques/T1562", "external_id": "T1562", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2835}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments to see if security tools or logging services are killed or stop running. Monitor Registry edits for modifications to services and startup programs that correspond to security tools.  Lack of log events may be suspicious.\\n\\nMonitor environment variables and APIs that can be leveraged to disable security measures.\"", "Str": "Monitor processes and command-line arguments to see if security tools or logging services are killed or stop running. Monitor Registry edits for modifications to services and startup programs that correspond to security tools.  Lack of log events may be suspicious.\n\nMonitor environment variables and APIs that can be leveraged to disable security measures.", "Type": 3, "Index": 2196}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2703}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Termination\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Windows Registry: Windows Registry Key Deletion\",\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"Sensor Health: Host Status\",\n      \"Script: Script Execution\",\n      \"Firewall: Firewall Disable\",\n      \"Firewall: Firewall Rule Modification\",\n      \"Cloud Service: Cloud Service Modification\",\n      \"Cloud Service: Cloud Service Disable\"\n    ]", "Str": "", "Type": 5, "Index": 1412}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2590}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Signature-based detection\",\n      \"Host intrusion prevention systems\",\n      \"File monitoring\",\n      \"Digital Certificate Validation\",\n      \"Host forensic analysis\",\n      \"Log analysis\",\n      \"Firewall\"\n    ]", "Str": "", "Type": 5, "Index": 1928}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2633}}', 'T1562', 'Adversaries may maliciously modify components of a victim environment in order to hinder or disable defensive mechanisms. This not only involves impairing preventative defenses, such as firewalls and anti-virus, but also detection capabilities that defenders can use to audit activity and identify malicious behavior. This may also span both native defenses as well as supplemental capabilities installed by users and administrators.
INSERT INTO public.attack_patterns VALUES (248, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.696872+00', NULL, 'attack-pattern--74d2a63f-3c7b-4852-92da-02d8fbab16da', '[{"url": "https://attack.mitre.org/techniques/T1562/006", "external_id": "T1562.006", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/571.html", "external_id": "CAPEC-571", "source_name": "capec"}, {"url": "https://www.microsoft.com/en-us/wdsi/threats/malware-encyclopedia-description?name=Backdoor:Win32/Lamin.A", "description": "Microsoft. (2009, May 17). Backdoor:Win32/Lamin.A. Retrieved September 6, 2018.", "source_name": "Microsoft Lamin Sept 2017"}, {"url": "https://docs.microsoft.com/en-us/windows/desktop/etw/consuming-events", "description": "Microsoft. (2018, May 30). About Event Tracing. Retrieved June 7, 2019.", "source_name": "Microsoft About Event Tracing 2018"}, {"url": "https://medium.com/palantir/tampering-with-windows-event-tracing-background-offense-and-defense-4be7ac62ac63", "description": "Palantir. (2018, December 24). Tampering with Windows Event Tracing: Background, Offense, and Defense. Retrieved June 7, 2019.", "source_name": "Medium Event Tracing Tampering 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4709}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detect lack of reported activity from a host sensor. Different methods of blocking may cause different disruptions in reporting. Systems may suddenly stop reporting all data or only certain kinds of data.\\n\\nDepending on the types of host information collected, an analyst may be able to detect the event that triggered a process to stop or connection to be blocked. For example, Sysmon will log when its configuration state has changed (Event ID 16) and Windows Management Instrumentation (WMI) may be used to subscribe ETW providers that log any provider removal from a specific trace session. (Citation: Medium Event Tracing Tampering 2018) To detect changes in ETW you can also monitor the registry key which contains configurations for all ETW event providers: <code>HKLM\\\\SYSTEM\\\\CurrentControlSet\\\\Control\\\\WMI\\\\Autologger\\\\AUTOLOGGER_NAME\\\\{PROVIDER_GUID}</code>\"", "Str": "Detect lack of reported activity from a host sensor. Different methods of blocking may cause different disruptions in reporting. Systems may suddenly stop reporting all data or only certain kinds of data.\n\nDepending on the types of host information collected, an analyst may be able to detect the event that triggered a process to stop or connection to be blocked. For example, Sysmon will log when its configuration state has changed (Event ID 16) and Windows Management Instrumentation (WMI) may be used to subscribe ETW providers that log any provider removal from a specific trace session. (Citation: Medium Event Tracing Tampering 2018) To detect changes in ETW you can also monitor the registry key which contains configurations for all ETW event providers: <code>HKLM\\SYSTEM\\CurrentControlSet\\Control\\WMI\\Autologger\\AUTOLOGGER_NAME\\{PROVIDER_GUID}</code>", "Type": 3, "Index": 3695}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4631}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Rob Smith\"\n    ]", "Str": "", "Type": 5, "Index": 3371}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Sensor Health: Host Status\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3426}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4600}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host intrusion prevention systems\"\n    ]", "Str": "", "Type": 5, "Index": 3599}}', 'T1562.006', 'An adversary may attempt to block indicators or events typically captured by sensors from being gathered and analyzed. This could include maliciously redirecting (Citation: Microsoft Lamin Sept 2017) or even disabling host-based sensors, such as Event Tracing for Windows (ETW),(Citation: Microsoft About Event Tracing 2018) by tampering settings that control the collection and flow of event telemetry. (Citation: Medium Event Tracing Tampering 2018) These settings may be stored on the system in configuration files and/or in the Registry as well as being accessible via administrative utilities such as [PowerShell](https://attack.mitre.org/techniques/T1059/001) or [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047).
INSERT INTO public.attack_patterns VALUES (250, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.760274+00', NULL, 'attack-pattern--799ace7f-e227-4411-baa0-8868704f2a69', '[{"url": "https://attack.mitre.org/techniques/T1070", "external_id": "T1070", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/93.html", "external_id": "CAPEC-93", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2754}, "x_mitre_detection": {"Num": 0, "Raw": "\"File system monitoring may be used to detect improper deletion or modification of indicator files.  Events not stored on the file system may require different detection mechanisms.\"", "Str": "File system monitoring may be used to detect improper deletion or modification of indicator files.  Events not stored on the file system may require different detection mechanisms.", "Type": 3, "Index": 2409}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2656}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brad Geesaman, @bradgeesaman\",\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 1717}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Deletion\",\n      \"File: File Modification\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Windows Registry: Windows Registry Key Deletion\",\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Traffic Content\",\n      \"User Account: User Account Authentication\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 1835}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2624}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Log analysis\",\n      \"Host intrusion prevention systems\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 2291}}', 'T1070', 'Adversaries may delete or alter generated artifacts on a host system, including logs or captured files such as quarantined malware. Locations and format of logs are platform or product-specific, however standard operating system logs are captured as Windows events or Linux/macOS files such as [Bash History](https://attack.mitre.org/techniques/T1552/003) and /var/log/*.
INSERT INTO public.attack_patterns VALUES (251, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.798147+00', NULL, 'attack-pattern--3b0e52ce-517a-4614-a523-1bd5deef6c5e', '[{"url": "https://attack.mitre.org/techniques/T1202", "external_id": "T1202", "source_name": "mitre-attack"}, {"url": "https://twitter.com/vector_sec/status/896049052642533376", "description": "vector_sec. (2017, August 11). Defenders watching launches of cmd? What about forfiles?. Retrieved January 22, 2018.", "source_name": "VectorSec ForFiles Aug 2017"}, {"url": "https://twitter.com/Evi1cg/status/935027922397573120", "description": "Evi1cg. (2017, November 26). block cmd.exe ? try this :. Retrieved January 22, 2018.", "source_name": "Evi1cg Forfiles Nov 2017"}, {"url": "https://community.rsa.com/community/products/netwitness/blog/2017/08/14/are-you-looking-out-for-forfilesexe-if-you-are-watching-for-cmdexe", "description": "Partington, E. (2017, August 14). Are you looking out for forfiles.exe (if you are watching for cmd.exe). Retrieved January 22, 2018.", "source_name": "RSA Forfiles Aug 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3645}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and analyze logs from host-based detection mechanisms, such as Sysmon, for events such as process creations that include or are resulting from parameters associated with invoking programs/commands/files and/or spawning child processes/network connections. (Citation: RSA Forfiles Aug 2017)\"", "Str": "Monitor and analyze logs from host-based detection mechanisms, such as Sysmon, for events such as process creations that include or are resulting from parameters associated with invoking programs/commands/files and/or spawning child processes/network connections. (Citation: RSA Forfiles Aug 2017)", "Type": 3, "Index": 3175}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3597}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 2847}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2922}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3507}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Static File Analysis\",\n      \"Application control\",\n      \"Application control by file name or path\"\n    ]", "Str": "", "Type": 5, "Index": 3033}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3550}}', 'T1202', 'Adversaries may abuse utilities that allow for command execution to bypass security restrictions that limit the use of command-line interpreters. Various Windows utilities may be used to execute commands, possibly without invoking [cmd](https://attack.mitre.org/software/S0106). For example, [Forfiles](https://attack.mitre.org/software/S0193), the Program Compatibility Assistant (pcalua.exe), components of the Windows Subsystem for Linux (WSL), as well as other utilities may invoke the execution of programs and commands from a [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059), Run window, or via scripts. (Citation: VectorSec ForFiles Aug 2017) (Citation: Evi1cg Forfiles Nov 2017)
INSERT INTO public.attack_patterns VALUES (254, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.90248+00', NULL, 'attack-pattern--bb5a00de-e086-4859-a231-fa793f6797e2', '[{"url": "https://attack.mitre.org/techniques/T1056", "external_id": "T1056", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/569.html", "external_id": "CAPEC-569", "source_name": "capec"}, {"url": "http://opensecuritytraining.info/Keylogging_files/The%20Adventures%20of%20a%20Keystroke.pdf", "description": "Tinaztepe,  E. (n.d.). The Adventures of a Keystroke:  An in-depth look into keyloggers on Windows. Retrieved April 27, 2016.", "source_name": "Adventures of a Keystroke"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3044}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection may vary depending on how input is captured but may include monitoring for certain Windows API calls (e.g. `SetWindowsHook`, `GetKeyState`, and `GetAsyncKeyState`)(Citation: Adventures of a Keystroke), monitoring for malicious instances of [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059), and ensuring no unauthorized drivers or kernel modules that could indicate keylogging or API hooking are present.\"", "Str": "Detection may vary depending on how input is captured but may include monitoring for certain Windows API calls (e.g. `SetWindowsHook`, `GetKeyState`, and `GetAsyncKeyState`)(Citation: Adventures of a Keystroke), monitoring for malicious instances of [Command and Scripting Interpreter](https://attack.mitre.org/techniques/T1059), and ensuring no unauthorized drivers or kernel modules that could indicate keylogging or API hooking are present.", "Type": 3, "Index": 2328}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2949}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"John Lambert, Microsoft Threat Intelligence Center\"\n    ]", "Str": "", "Type": 5, "Index": 1971}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Driver: Driver Load\",\n      \"Process: OS API Execution\",\n      \"Process: Process Creation\",\n      \"File: File Modification\",\n      \"Process: Process Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2067}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2806}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2849}}', 'T1056', 'Adversaries may use methods of capturing user input to obtain credentials or collect information. During normal system usage, users often provide credentials to various different locations, such as login pages/portals or system dialog boxes. Input capture mechanisms may be transparent to the user (e.g. [Credential API Hooking](https://attack.mitre.org/techniques/T1056/004)) or rely on deceiving the user into providing input into what they believe to be a genuine service (e.g. [Web Portal Capture](https://attack.mitre.org/techniques/T1056/003)).', 'Input Capture', 0);
INSERT INTO public.attack_patterns VALUES (256, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.969583+00', NULL, 'attack-pattern--c615231b-f253-4f58-9d47-d5b4cbdb6839', '[{"url": "https://attack.mitre.org/techniques/T1553/004", "external_id": "T1553.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/479.html", "external_id": "CAPEC-479", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Root_certificate", "description": "Wikipedia. (2016, December 6). Root certificate. Retrieved February 20, 2017.", "source_name": "Wikipedia Root Certificate"}, {"url": "http://www.trendmicro.com/cloud-content/us/pdfs/security-intelligence/white-papers/wp-finding-holes-operation-emmental.pdf", "description": "Sancho, D., Hacquebord, F., Link, R. (2014, July 22). Finding Holes Operation Emmental. Retrieved February 9, 2016.", "source_name": "Operation Emmental"}, {"url": "https://www.kaspersky.com/blog/lenovo-pc-with-adware-superfish-preinstalled/7712/", "description": "Onuma. (2015, February 24). Superfish: Adware Preinstalled on Lenovo Laptops. Retrieved February 20, 2017.", "source_name": "Kaspersky Superfish"}, {"url": "https://posts.specterops.io/code-signing-certificate-cloning-attacks-and-defenses-6f98657fc6ec", "description": "Graeber, M. (2017, December 22). Code Signing Certificate Cloning Attacks and Defenses. Retrieved April 3, 2018.", "source_name": "SpectorOps Code Signing Dec 2017"}, {"url": "https://objective-see.com/blog/blog_0x26.html", "description": "Patrick Wardle. (2018, January 11). Ay MaMi. Retrieved March 19, 2018.", "source_name": "objective-see ay mami 2018"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/sigcheck", "description": "Russinovich, M. et al.. (2017, May 22). Sigcheck. Retrieved April 3, 2018.", "source_name": "Microsoft Sigcheck May 2017"}, {"url": "https://www.tripwire.com/state-of-security/off-topic/appunblocker-bypassing-applocker/", "description": "Smith, T. (2016, October 27). AppUNBlocker: Bypassing AppLocker. Retrieved December 19, 2017.", "source_name": "Tripwire AppUNBlocker"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 7375}, "x_mitre_detection": {"Num": 0, "Raw": "\"A system''s root certificates are unlikely to change frequently. Monitor new certificates installed on a system that could be due to malicious activity. (Citation: SpectorOps Code Signing Dec 2017) Check pre-installed certificates on new systems to ensure unnecessary or suspicious certificates are not present. Microsoft provides a list of trustworthy root certificates online and through authroot.stl. (Citation: SpectorOps Code Signing Dec 2017) The Sysinternals Sigcheck utility can also be used (<code>sigcheck[64].exe -tuv</code>) to dump the contents of the certificate store and list valid certificates not rooted to the Microsoft Certificate Trust List. (Citation: Microsoft Sigcheck May 2017)\\n\\nInstalled root certificates are located in the Registry under <code>HKLM\\\\SOFTWARE\\\\Microsoft\\\\EnterpriseCertificates\\\\Root\\\\Certificates\\\\</code> and <code>[HKLM or HKCU]\\\\Software[\\\\Policies\\\\]\\\\Microsoft\\\\SystemCertificates\\\\Root\\\\Certificates\\\\</code>. There are a subset of root certificates that are consistent across Windows systems and can be used for comparison: (Citation: Tripwire AppUNBlocker)\\n\\n* 18F7C1FCC3090203FD5BAA2F861A754976C8DD25\\n* 245C97DF7514E7CF2DF8BE72AE957B9E04741E85\\n* 3B1EFD3A66EA28B16697394703A72CA340A05BD5\\n* 7F88CD7223F3C813818C994614A89C99FA3B5247\\n* 8F43288AD272F3103B6FB1428485EA3014C0BCFE\\n* A43489159A520F0D93D032CCAF37E7FE20A8B419\\n* BE36A4562FB2EE05DBB3D32323ADF445084ED656\\n* CDD4EEAE6000AC7F40C3802C171E30148030C072\"", "Str": "A system''s root certificates are unlikely to change frequently. Monitor new certificates installed on a system that could be due to malicious activity. (Citation: SpectorOps Code Signing Dec 2017) Check pre-installed certificates on new systems to ensure unnecessary or suspicious certificates are not present. Microsoft provides a list of trustworthy root certificates online and through authroot.stl. (Citation: SpectorOps Code Signing Dec 2017) The Sysinternals Sigcheck utility can also be used (<code>sigcheck[64].exe -tuv</code>) to dump the contents of the certificate store and list valid certificates not rooted to the Microsoft Certificate Trust List. (Citation: Microsoft Sigcheck May 2017)\n\nInstalled root certificates are located in the Registry under <code>HKLM\\SOFTWARE\\Microsoft\\EnterpriseCertificates\\Root\\Certificates\\</code> and <code>[HKLM or HKCU]\\Software[\\Policies\\]\\Microsoft\\SystemCertificates\\Root\\Certificates\\</code>. There are a subset of root certificates that are consistent across Windows systems and can be used for comparison: (Citation: Tripwire AppUNBlocker)\n\n* 18F7C1FCC3090203FD5BAA2F861A754976C8DD25\n* 245C97DF7514E7CF2DF8BE72AE957B9E04741E85\n* 3B1EFD3A66EA28B16697394703A72CA340A05BD5\n* 7F88CD7223F3C813818C994614A89C99FA3B5247\n* 8F43288AD272F3103B6FB1428485EA3014C0BCFE\n* A43489159A520F0D93D032CCAF37E7FE20A8B419\n* BE36A4562FB2EE05DBB3D32323ADF445084ED656\n* CDD4EEAE6000AC7F40C3802C171E30148030C072", "Type": 3, "Index": 5686}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7297}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matt Graeber, @mattifestation, SpecterOps\",\n      \"Red Canary\",\n      \"Travis Smith, Tripwire\",\n      \"Itzik Kotler, SafeBreach\"\n    ]", "Str": "", "Type": 5, "Index": 5211}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5384}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7185}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 5613}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7227}}', 'T1553.004', 'Adversaries may install a root certificate on a compromised system to avoid warnings when connecting to adversary controlled web servers. Root certificates are used in public key cryptography to identify a root certificate authority (CA). When a root certificate is installed, the system or application will trust certificates in the root''s chain of trust that have been signed by the root certificate. (Citation: Wikipedia Root Certificate) Certificates are commonly used for establishing secure TLS/SSL communications within a web browser. When a user attempts to browse a website that presents a certificate that is not trusted an error message will be displayed to warn the user of the security risk. Depending on the security settings, the browser may not allow the user to establish a connection to the website.
INSERT INTO public.attack_patterns VALUES (257, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.005004+00', NULL, 'attack-pattern--2cd950a6-16c4-404a-aa01-044322395107', '[{"url": "https://attack.mitre.org/techniques/T1218/004", "external_id": "T1218.004", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/en-us/library/50614e95.aspx", "description": "Microsoft. (n.d.). Installutil.exe (Installer Tool). Retrieved July 1, 2016.", "source_name": "MSDN InstallUtil"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Installutil/", "description": "LOLBAS. (n.d.). Installutil.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Installutil"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2988}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of InstallUtil.exe. Compare recent invocations of InstallUtil.exe with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after the InstallUtil.exe invocation may also be useful in determining the origin and purpose of the binary being executed.\"", "Str": "Use process monitoring to monitor the execution and arguments of InstallUtil.exe. Compare recent invocations of InstallUtil.exe with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after the InstallUtil.exe invocation may also be useful in determining the origin and purpose of the binary being executed.", "Type": 3, "Index": 2405}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2940}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Casey Smith\"\n    ]", "Str": "", "Type": 5, "Index": 2103}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2192}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2851}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2303}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2893}}', 'T1218.004', 'Adversaries may use InstallUtil to proxy execution of code through a trusted Windows utility. InstallUtil is a command-line utility that allows for installation and uninstallation of resources by executing specific installer components specified in .NET binaries. (Citation: MSDN InstallUtil) InstallUtil is digitally signed by Microsoft and located in the .NET directories on a Windows system: <code>C:\Windows\Microsoft.NET\Framework\v<version>\InstallUtil.exe</code> and <code>C:\Windows\Microsoft.NET\Framework64\v<version>\InstallUtil.exe</code>.
INSERT INTO public.attack_patterns VALUES (260, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.109541+00', NULL, 'attack-pattern--f6dacc85-b37d-458e-b58d-74fc4bbf5755', '[{"url": "https://attack.mitre.org/techniques/T1090/001", "external_id": "T1090.001", "source_name": "mitre-attack"}, {"url": "http://blog.trendmicro.com/trendlabs-security-intelligence/in-depth-look-apt-attack-tools-of-the-trade/", "description": "Wilhoit, K. (2013, March 4). In-Depth Look: APT Attack Tools of the Trade. Retrieved December 2, 2015.", "source_name": "Trend Micro APT Attack Tools"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3215}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows between clients that should not or often do not communicate with one another. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows between clients that should not or often do not communicate with one another. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2651}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3137}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2467}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3106}}', 'T1090.001', 'Adversaries may use an internal proxy to direct command and control traffic between two or more systems in a compromised environment. Many tools exist that enable traffic redirection through proxies or port redirection, including [HTRAN](https://attack.mitre.org/software/S0040), ZXProxy, and ZXPortMap. (Citation: Trend Micro APT Attack Tools) Adversaries use internal proxies to manage command and control communications inside a compromised environment, to reduce the number of simultaneous outbound network connections, to provide resiliency in the face of connection loss, or to ride over existing trusted communications paths between infected systems to avoid suspicion. Internal proxy connections may use common peer-to-peer (p2p) networking protocols, such as SMB, to better blend in with the environment.
INSERT INTO public.attack_patterns VALUES (261, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.139019+00', NULL, 'attack-pattern--9e7452df-5144-4b6e-b04a-b66dd4016747', '[{"url": "https://attack.mitre.org/techniques/T1534", "external_id": "T1534", "source_name": "mitre-attack"}, {"url": "https://blog.trendmicro.com/phishing-starts-inside/", "description": "Chris Taylor. (2017, October 5). When Phishing Starts from the Inside. Retrieved October 8, 2019.", "source_name": "Trend Micro When Phishing Starts from the Inside 2017"}, {"url": "https://labs.ft.com/2013/05/a-sobering-day/?mhq5j=e6", "description": "THE FINANCIAL TIMES. (2019, September 2). A sobering day. Retrieved October 8, 2019.", "source_name": "THE FINANCIAL TIMES LTD 2019."}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4092}, "x_mitre_detection": {"Num": 0, "Raw": "\"Network intrusion detection systems and email gateways usually do not scan internal email, but an organization can leverage the journaling-based solution which sends a copy of emails to a security service for offline analysis or incorporate service-integrated solutions using on-premise or API-based integrations to help detect internal spearphishing attacks.(Citation: Trend Micro When Phishing Starts from the Inside 2017)\"", "Str": "Network intrusion detection systems and email gateways usually do not scan internal email, but an organization can leverage the journaling-based solution which sends a copy of emails to a security service for offline analysis or incorporate service-integrated solutions using on-premise or API-based integrations to help detect internal spearphishing attacks.(Citation: Trend Micro When Phishing Starts from the Inside 2017)", "Type": 3, "Index": 3405}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\",\n      \"Office 365\",\n      \"SaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3954}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tim MalcomVetter\",\n      \"Swetha Prabakaran, Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 3090}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3225}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3864}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3907}}', 'T1534', 'Adversaries may use internal spearphishing to gain access to additional information or exploit other users within the same organization after they already have access to accounts or systems within the environment. Internal spearphishing is multi-staged attack where an email account is owned either by controlling the user''s device with previously installed malware or by compromising the account credentials of the user. Adversaries attempt to take advantage of a trusted internal account to increase the likelihood of tricking the target into falling for the phish attempt.(Citation: Trend Micro When Phishing Starts from the Inside 2017)
INSERT INTO public.attack_patterns VALUES (521, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.179746+00', NULL, 'attack-pattern--c898c4b5-bf36-4e6e-a4ad-5b8c4c13e35b', '[{"url": "https://attack.mitre.org/techniques/T1564/007", "external_id": "T1564.007", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2020/01/stomp-2-dis-brilliance-in-the-visual-basics.html", "description": "Cole, R., Moore, A., Stark, G., Stancill, B. (2020, February 5). STOMP 2 DIS: Brilliance in the (Visual) Basics. Retrieved September 17, 2020.", "source_name": "FireEye VBA stomp Feb 2020"}, {"url": "https://outflank.nl/blog/2019/05/05/evil-clippy-ms-office-maldoc-assistant/", "description": "Hegt, S. (2019, May 5). Evil Clippy: MS Office maldoc assistant. Retrieved September 17, 2020.", "source_name": "Evil Clippy May 2019"}, {"url": "https://docs.microsoft.com/en-us/openspecs/office_file_formats/ms-ovba/ef7087ac-3974-4452-aab2-7dba2214d239", "description": "Microsoft. (2020, February 19). 2.3.4.1 _VBA_PROJECT Stream: Version Dependent Project Information. Retrieved September 18, 2020.", "source_name": "Microsoft _VBA_PROJECT Stream"}, {"url": "https://medium.com/walmartglobaltech/vba-stomping-advanced-maldoc-techniques-612c484ab278", "description": "Sayre, K., Ogden, H., Roberts, C. (2018, October 10). VBA Stomping — Advanced Maldoc Techniques. Retrieved September 17, 2020.", "source_name": "Walmart Roberts Oct 2018"}, {"url": "https://github.com/bontchev/pcodedmp", "description": "Bontchev, V. (2019, July 30). pcodedmp.py - A VBA p-code disassembler. Retrieved September 17, 2020.", "source_name": "pcodedmp Bontchev"}, {"url": "https://github.com/decalage2/oletools", "description": "decalage2. (2019, December 3). python-oletools. Retrieved September 18, 2020.", "source_name": "oletools toolkit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5542}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection efforts should be placed finding differences between VBA source code and p-code.(Citation: Walmart Roberts Oct 2018) VBA code can be extracted from p-code before execution with tools such as the pcodedmp disassembler. The oletools toolkit leverages the pcodedmp disassembler to detect VBA stomping by comparing keywords present in the VBA source code and p-code.(Citation: pcodedmp Bontchev)(Citation: oletools toolkit)\\n\\nIf the document is opened with a Graphical User Interface (GUI) the malicious p-code is decompiled and may be viewed. However, if the <code>PROJECT</code> stream, which specifies the project properties, is modified in a specific way the decompiled VBA code will not be displayed. For example, adding a module name that is undefined to the <code>PROJECT</code> stream will inhibit attempts of reading the VBA source code through the GUI.(Citation: FireEye VBA stomp Feb 2020)\"", "Str": "Detection efforts should be placed finding differences between VBA source code and p-code.(Citation: Walmart Roberts Oct 2018) VBA code can be extracted from p-code before execution with tools such as the pcodedmp disassembler. The oletools toolkit leverages the pcodedmp disassembler to detect VBA stomping by comparing keywords present in the VBA source code and p-code.(Citation: pcodedmp Bontchev)(Citation: oletools toolkit)\n\nIf the document is opened with a Graphical User Interface (GUI) the malicious p-code is decompiled and may be viewed. However, if the <code>PROJECT</code> stream, which specifies the project properties, is modified in a specific way the decompiled VBA code will not be displayed. For example, adding a module name that is undefined to the <code>PROJECT</code> stream will inhibit attempts of reading the VBA source code through the GUI.(Citation: FireEye VBA stomp Feb 2020)", "Type": 3, "Index": 4301}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 5332}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Rick Cole, FireEye\"\n    ]", "Str": "", "Type": 5, "Index": 4142}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Script: Script Execution\",\n      \"File: File Content\"\n    ]", "Str": "", "Type": 5, "Index": 4206}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5243}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"MS Office version specified in <code>_VBA_PROJECT</code> stream must match host\"\n    ]", "Str": "", "Type": 5, "Index": 5422}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5285}}', 'T1564.007', 'Adversaries may hide malicious Visual Basic for Applications (VBA) payloads embedded within MS Office documents by replacing the VBA source code with benign data.(Citation: FireEye VBA stomp Feb 2020)
INSERT INTO public.attack_patterns VALUES (263, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.24269+00', NULL, 'attack-pattern--b4b7458f-81f2-4d38-84be-1c5ba0167a52', '[{"url": "https://attack.mitre.org/techniques/T1036/001", "external_id": "T1036.001", "source_name": "mitre-attack"}, {"url": "https://threatexpress.com/blogs/2017/metatwin-borrowing-microsoft-metadata-and-digital-signatures-to-hide-binaries/", "description": "Vest, J. (2017, October 9). Borrowing Microsoft MetaData and Signatures to Hide Binary Payloads. Retrieved September 10, 2019.", "source_name": "Threatexpress MetaTwin 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2355}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect and analyze signing certificate metadata and check signature validity on software that executes within the environment, look for invalid signatures as well as unusual certificate characteristics and outliers.\"", "Str": "Collect and analyze signing certificate metadata and check signature validity on software that executes within the environment, look for invalid signatures as well as unusual certificate characteristics and outliers.", "Type": 3, "Index": 2010}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2292}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 1948}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2261}}', 'T1036.001', 'Adversaries may attempt to mimic features of valid code signatures to increase the chance of deceiving a user, analyst, or tool. Code signing provides a level of authenticity on a binary from the developer and a guarantee that the binary has not been tampered with. Adversaries can copy the metadata and signature information from a signed program, then use it as a template for an unsigned program. Files with invalid code signatures will fail digital signature validation checks, but they may appear more legitimate to users and security tools may improperly handle these files.(Citation: Threatexpress MetaTwin 2017)
INSERT INTO public.attack_patterns VALUES (264, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.274659+00', NULL, 'attack-pattern--0f4a0c76-ab2d-4cb0-85d3-3f0efb8cba0d', '[{"url": "https://attack.mitre.org/techniques/T1059/007", "external_id": "T1059.007", "source_name": "mitre-attack"}, {"url": "https://nodejs.org/", "description": "OpenJS Foundation. (n.d.). Node.js. Retrieved June 23, 2020.", "source_name": "NodeJS"}, {"url": "https://docs.microsoft.com/windows/win32/com/translating-to-jscript", "description": "Microsoft. (2018, May 31). Translating to JScript. Retrieved June 23, 2020.", "source_name": "JScrip May 2018"}, {"url": "https://docs.microsoft.com/archive/blogs/gauravseth/the-world-of-jscript-javascript-ecmascript", "description": "Microsoft. (2007, August 15). The World of JScript, JavaScript, ECMAScript …. Retrieved June 23, 2020.", "source_name": "Microsoft JScript 2007"}, {"url": "https://docs.microsoft.com/scripting/winscript/windows-script-interfaces", "description": "Microsoft. (2017, January 18). Windows Script Interfaces. Retrieved June 23, 2020.", "source_name": "Microsoft Windows Scripts"}, {"url": "https://developer.apple.com/library/archive/documentation/LanguagesUtilities/Conceptual/MacAutomationScriptingGuide/index.html", "description": "Apple. (2016, June 13). About Mac Scripting. Retrieved April 14, 2021.", "source_name": "Apple About Mac Scripting 2016"}, {"url": "https://posts.specterops.io/persistent-jxa-66e1c3cd1cf5", "description": "Pitt, L. (2020, August 6). Persistent JXA. Retrieved April 14, 2021.", "source_name": "SpecterOps JXA 2020"}, {"url": "https://www.sentinelone.com/blog/macos-red-team-calling-apple-apis-without-building-binaries/", "description": "Phil Stokes. (2019, December 5). macOS Red Team: Calling Apple APIs Without Building Binaries. Retrieved July 17, 2020.", "source_name": "SentinelOne macOS Red Team"}, {"url": "https://redcanary.com/blog/clipping-silver-sparrows-wings/", "description": "Tony Lambert. (2021, February 18). Clipping Silver Sparrow’s wings: Outing macOS malware before it takes flight. Retrieved April 20, 2021.", "source_name": "Red Canary Silver Sparrow Feb2021"}, {"url": "https://www.mdsec.co.uk/2021/01/macos-post-exploitation-shenanigans-with-vscode-extensions/", "description": "Dominic Chell. (2021, January 1). macOS Post-Exploitation Shenanigans with VSCode Extensions. Retrieved April 20, 2021.", "source_name": "MDSec macOS JXA and VSCode"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 7420}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for events associated with scripting execution, such as process activity, usage of the Windows Script Host (typically cscript.exe or wscript.exe), file activity involving scripts, or loading of modules associated with scripting languages (ex: JScript.dll). Scripting execution is likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other programmable post-compromise behaviors and could be used as indicators of detection leading back to the source.\\n\\nMonitor for execution of JXA through <code>osascript</code> and usage of <code>OSAScript</code> API that may be related to other suspicious behavior occurring on the system.\\n\\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable related components running on a system would be considered suspicious. If scripting is not commonly used on a system, but enabled, execution running out of cycle from patching or other administrator functions is suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\"", "Str": "Monitor for events associated with scripting execution, such as process activity, usage of the Windows Script Host (typically cscript.exe or wscript.exe), file activity involving scripts, or loading of modules associated with scripting languages (ex: JScript.dll). Scripting execution is likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other programmable post-compromise behaviors and could be used as indicators of detection leading back to the source.\n\nMonitor for execution of JXA through <code>osascript</code> and usage of <code>OSAScript</code> API that may be related to other suspicious behavior occurring on the system.\n\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable related components running on a system would be considered suspicious. If scripting is not commonly used on a system, but enabled, execution running out of cycle from patching or other administrator functions is suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.", "Type": 3, "Index": 5721}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 7342}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Cody Thomas, SpecterOps\"\n    ]", "Str": "", "Type": 5, "Index": 5485}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5554}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7214}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 7256}}', 'T1059.007', 'Adversaries may abuse various implementations of JavaScript for execution. JavaScript (JS) is a platform-independent scripting language (compiled just-in-time at runtime) commonly associated with scripts in webpages, though JS can be executed in runtime environments outside the browser.(Citation: NodeJS)
INSERT INTO public.attack_patterns VALUES (265, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.312752+00', NULL, 'attack-pattern--f7c0689c-4dbd-489b-81be-7cb7c7079ade', '[{"url": "https://attack.mitre.org/techniques/T1001/001", "external_id": "T1001.001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2214}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1647}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2136}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1564}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2105}}', 'T1001.001', 'Adversaries may add junk data to protocols used for command and control to make detection more difficult. By adding random or meaningless data to the protocols used for command and control, adversaries can prevent trivial methods for decoding, deciphering, or otherwise analyzing the traffic. Examples may include appending/prepending data with junk characters or writing junk characters between significant characters. ', 'Junk Data', 119);
INSERT INTO public.attack_patterns VALUES (266, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.346961+00', NULL, 'attack-pattern--f2877f7f-9a4c-4251-879f-1224e3006bee', '[{"url": "https://attack.mitre.org/techniques/T1558/003", "external_id": "T1558.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/509.html", "external_id": "CAPEC-509", "source_name": "capec"}, {"url": "https://github.com/EmpireProject/Empire/blob/master/data/module_source/credentials/Invoke-Kerberoast.ps1", "description": "EmpireProject. (2016, October 31). Invoke-Kerberoast.ps1. Retrieved March 22, 2018.", "source_name": "Empire InvokeKerberoast Oct 2016"}, {"url": "https://adsecurity.org/?p=2293", "description": "Metcalf, S. (2015, December 31). Cracking Kerberos TGS Tickets Using Kerberoast – Exploiting Kerberos to Compromise the Active Directory Domain. Retrieved March 22, 2018.", "source_name": "AdSecurity Cracking Kerberos Dec 2015"}, {"url": "https://blogs.technet.microsoft.com/motiba/2018/02/23/detecting-kerberoasting-activity-using-azure-security-center/", "description": "Bani, M. (2018, February 23). Detecting Kerberoasting activity using Azure Security Center. Retrieved March 23, 2018.", "source_name": "Microsoft Detecting Kerberoasting Feb 2018"}, {"url": "https://msdn.microsoft.com/library/ms677949.aspx", "description": "Microsoft. (n.d.). Service Principal Names. Retrieved March 22, 2018.", "source_name": "Microsoft SPN"}, {"url": "https://social.technet.microsoft.com/wiki/contents/articles/717.service-principal-names-spns-setspn-syntax-setspn-exe.aspx", "description": "Microsoft. (2010, April 13). Service Principal Names (SPNs) SetSPN Syntax (Setspn.exe). Retrieved March 22, 2018.", "source_name": "Microsoft SetSPN"}, {"url": "https://redsiege.com/kerberoast-slides", "description": "Medin, T. (2014, November). Attacking Kerberos - Kicking the Guard Dog of Hades. Retrieved March 22, 2018.", "source_name": "SANS Attacking Kerberos Nov 2014"}, {"url": "https://www.harmj0y.net/blog/powershell/kerberoasting-without-mimikatz/", "description": "Schroeder, W. (2016, November 1). Kerberoasting Without Mimikatz. Retrieved March 23, 2018.", "source_name": "Harmj0y Kerberoast Nov 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5865}, "x_mitre_detection": {"Num": 0, "Raw": "\"Enable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17]).(Citation: Microsoft Detecting Kerberoasting Feb 2018)(Citation: AdSecurity Cracking Kerberos Dec 2015)\"", "Str": "Enable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17]).(Citation: Microsoft Detecting Kerberoasting Feb 2018)(Citation: AdSecurity Cracking Kerberos Dec 2015)", "Type": 3, "Index": 5236}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5696}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 5084}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Credential Request\"\n    ]", "Str": "", "Type": 5, "Index": 5140}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5665}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Valid domain account or the ability to sniff traffic within a domain\"\n    ]", "Str": "", "Type": 5, "Index": 5756}}', 'T1558.003', 'Adversaries may abuse a valid Kerberos ticket-granting ticket (TGT) or sniff network traffic to obtain a ticket-granting service (TGS) ticket that may be vulnerable to [Brute Force](https://attack.mitre.org/techniques/T1110).(Citation: Empire InvokeKerberoast Oct 2016)(Citation: AdSecurity Cracking Kerberos Dec 2015) 
INSERT INTO public.attack_patterns VALUES (268, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.413427+00', NULL, 'attack-pattern--1eaebf46-e361-4437-bc23-d5d65a3b92e3', '[{"url": "https://attack.mitre.org/techniques/T1555/001", "external_id": "T1555.001", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Keychain_(software)", "description": "Wikipedia. (n.d.). Keychain (software). Retrieved July 5, 2017.", "source_name": "Wikipedia keychain"}, {"url": "http://www.slideshare.net/StephanBorosh/external-to-da-the-os-x-way", "description": "Alex Rymdeko-Harvey, Steve Borosh. (2016, May 14). External to DA, the OS X Way. Retrieved July 3, 2017.", "source_name": "External to DA, the OS X Way"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2866}, "x_mitre_detection": {"Num": 0, "Raw": "\"Unlocking the keychain and using passwords from it is a very common process, so there is likely to be a lot of noise in any detection technique. Monitoring of system calls to the keychain can help determine if there is a suspicious process trying to access it.\"", "Str": "Unlocking the keychain and using passwords from it is a very common process, so there is likely to be a lot of noise in any detection technique. Monitoring of system calls to the keychain can help determine if there is a suspicious process trying to access it.", "Type": 3, "Index": 2427}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2820}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 2296}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2722}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2764}}', 'T1555.001', 'Adversaries may collect the keychain storage data from a system to acquire credentials. Keychains are the built-in way for macOS to keep track of users'' passwords and credentials for many services and features such as WiFi passwords, websites, secure notes, certificates, and Kerberos. Keychain files are located in <code>~/Library/Keychains/</code>,<code>/Library/Keychains/</code>, and <code>/Network/Library/Keychains/</code>. (Citation: Wikipedia keychain) The <code>security</code> command-line utility, which is built into macOS by default, provides a useful way to manage these credentials.
INSERT INTO public.attack_patterns VALUES (269, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.445892+00', NULL, 'attack-pattern--09a60ea3-a8d1-4ae5-976e-5783248b72a4', '[{"url": "https://attack.mitre.org/techniques/T1056/001", "external_id": "T1056.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/568.html", "external_id": "CAPEC-568", "source_name": "capec"}, {"url": "http://opensecuritytraining.info/Keylogging_files/The%20Adventures%20of%20a%20Keystroke.pdf", "description": "Tinaztepe,  E. (n.d.). The Adventures of a Keystroke:  An in-depth look into keyloggers on Windows. Retrieved April 27, 2016.", "source_name": "Adventures of a Keystroke"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3937}, "x_mitre_detection": {"Num": 0, "Raw": "\"Keyloggers may take many forms, possibly involving modification to the Registry and installation of a driver, setting a hook, or polling to intercept keystrokes. Commonly used API calls include `SetWindowsHook`, `GetKeyState`, and `GetAsyncKeyState`.(Citation: Adventures of a Keystroke) Monitor the Registry and file system for such changes, monitor driver installs, and look for common keylogging API calls. API calls alone are not an indicator of keylogging, but may provide behavioral data that is useful when combined with other information such as new files written to disk and unusual processes.\"", "Str": "Keyloggers may take many forms, possibly involving modification to the Registry and installation of a driver, setting a hook, or polling to intercept keystrokes. Commonly used API calls include `SetWindowsHook`, `GetKeyState`, and `GetAsyncKeyState`.(Citation: Adventures of a Keystroke) Monitor the Registry and file system for such changes, monitor driver installs, and look for common keylogging API calls. API calls alone are not an indicator of keylogging, but may provide behavioral data that is useful when combined with other information such as new files written to disk and unusual processes.", "Type": 3, "Index": 3063}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3842}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Driver: Driver Load\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2905}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3700}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3742}}', 'T1056.001', 'Adversaries may log user keystrokes to intercept credentials as the user types them. Keylogging is likely to be used to acquire credentials for new access opportunities when [OS Credential Dumping](https://attack.mitre.org/techniques/T1003) efforts are not effective, and may require an adversary to intercept keystrokes on a system for a substantial period of time before credentials can be successfully captured.
INSERT INTO public.attack_patterns VALUES (270, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.481119+00', NULL, 'attack-pattern--10ff21b9-5a01-4268-a1b5-3b55015f1847', '[{"url": "https://attack.mitre.org/techniques/T1546/006", "external_id": "T1546.006", "source_name": "mitre-attack"}, {"url": "https://www.blackhat.com/docs/us-15/materials/us-15-Wardle-Writing-Bad-A-Malware-For-OS-X.pdf", "description": "Patrick Wardle. (2015). Writing Bad @$$ Malware for OS X. Retrieved July 10, 2017.", "source_name": "Writing Bad Malware for OSX"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2015). Malware Persistence on OS X Yosemite. Retrieved July 10, 2017.", "source_name": "Malware Persistence on OS X"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3145}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for those that may be used to modify binary headers. Monitor file systems for changes to application binaries and invalid checksums/signatures. Changes to binaries that do not line up with application updates or patches are also extremely suspicious.\"", "Str": "Monitor processes for those that may be used to modify binary headers. Monitor file systems for changes to application binaries and invalid checksums/signatures. Changes to binaries that do not line up with application updates or patches are also extremely suspicious.", "Type": 3, "Index": 2707}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3099}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\",\n      \"File: File Modification\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2512}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3010}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3052}}', 'T1546.006', 'Adversaries may establish persistence by executing malicious content triggered by the execution of tainted binaries. Mach-O binaries have a series of headers that are used to perform certain operations when a binary is loaded. The LC_LOAD_DYLIB header in a Mach-O binary tells macOS and OS X which dynamic libraries (dylibs) to load during execution time. These can be added ad-hoc to the compiled binary as long as adjustments are made to the rest of the fields and dependencies. (Citation: Writing Bad Malware for OSX) There are tools available to perform these changes.
INSERT INTO public.attack_patterns VALUES (273, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.549211+00', NULL, 'attack-pattern--f0589bc3-a6ae-425a-a3d5-5659bfee07f4', '[{"url": "https://attack.mitre.org/techniques/T1547/008", "external_id": "T1547.008", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/library/cc961760.aspx", "description": "Microsoft. (n.d.). Security Subsystem Architecture. Retrieved November 27, 2017.", "source_name": "Microsoft Security Subsystem"}, {"url": "https://technet.microsoft.com/library/dn408187.aspx", "description": "Microsoft. (2014, March 12). Configuring Additional LSA Protection. Retrieved November 27, 2017.", "source_name": "Microsoft LSA Protection Mar 2014"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ff919712.aspx", "description": "Microsoft. (n.d.). Dynamic-Link Library Security. Retrieved November 27, 2017.", "source_name": "Microsoft DLL Security"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3614}, "x_mitre_detection": {"Num": 0, "Raw": "\"With LSA Protection enabled, monitor the event logs (Events 3033 and 3063) for failed attempts to load LSA plug-ins and drivers. (Citation: Microsoft LSA Protection Mar 2014) Also monitor DLL load operations in lsass.exe. (Citation: Microsoft DLL Security)\\n\\nUtilize the Sysinternals Autoruns/Autorunsc utility (Citation: TechNet Autoruns) to examine loaded drivers associated with the LSA. \"", "Str": "With LSA Protection enabled, monitor the event logs (Events 3033 and 3063) for failed attempts to load LSA plug-ins and drivers. (Citation: Microsoft LSA Protection Mar 2014) Also monitor DLL load operations in lsass.exe. (Citation: Microsoft DLL Security)\n\nUtilize the Sysinternals Autoruns/Autorunsc utility (Citation: TechNet Autoruns) to examine loaded drivers associated with the LSA. ", "Type": 3, "Index": 3025}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3566}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vincent Le Toux\"\n    ]", "Str": "", "Type": 5, "Index": 2811}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Driver: Driver Load\",\n      \"File: File Modification\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2872}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3452}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3494}}', 'T1547.008', 'Adversaries may modify or add LSASS drivers to obtain persistence on compromised systems. The Windows security subsystem is a set of components that manage and enforce the security policy for a computer or domain. The Local Security Authority (LSA) is the main component responsible for local security policy and user authentication. The LSA includes multiple dynamic link libraries (DLLs) associated with various other security functions, all of which run in the context of the LSA Subsystem Service (LSASS) lsass.exe process. (Citation: Microsoft Security Subsystem)
INSERT INTO public.attack_patterns VALUES (274, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.584748+00', NULL, 'attack-pattern--65f2d882-3f41-4d48-8a06-29af77ec9f90', '[{"url": "https://attack.mitre.org/techniques/T1003/001", "external_id": "T1003.001", "source_name": "mitre-attack"}, {"url": "http://docplayer.net/20839173-Analysis-of-malicious-security-support-provider-dlls.html", "description": "Graeber, M. (2014, October). Analysis of Malicious Security Support Provider DLLs. Retrieved March 1, 2017.", "source_name": "Graeber 2014"}, {"url": "https://blogs.technet.microsoft.com/askpfeplat/2016/04/18/the-importance-of-kb2871997-and-kb2928120-for-credential-protection/", "description": "Wilson, B. (2016, April 18). The Importance of KB2871997 and KB2928120 for Credential Protection. Retrieved April 11, 2018.", "source_name": "TechNet Blogs Credential Protection"}, {"url": "https://medium.com/threatpunter/detecting-attempts-to-steal-passwords-from-memory-558f16dce4ea", "description": "French, D. (2018, October 2). Detecting Attempts to Steal Passwords from Memory. Retrieved October 11, 2019.", "source_name": "Medium Detecting Attempts to Steal Passwords from Memory"}, {"url": "https://github.com/mattifestation/PowerSploit", "description": "PowerSploit. (n.d.). Retrieved December 4, 2014.", "source_name": "Powersploit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5799}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unexpected processes interacting with LSASS.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as Mimikatz access LSASS.exe by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details are stored. Credential dumpers may also use methods for reflective [Process Injection](https://attack.mitre.org/techniques/T1055) to reduce potential indicators of malicious activity.\\n\\nOn Windows 8.1 and Windows Server 2012 R2, monitor Windows Logs for LSASS.exe creation to verify that LSASS started as a protected process.\\n\\nMonitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\"", "Str": "Monitor for unexpected processes interacting with LSASS.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as Mimikatz access LSASS.exe by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details are stored. Credential dumpers may also use methods for reflective [Process Injection](https://attack.mitre.org/techniques/T1055) to reduce potential indicators of malicious activity.\n\nOn Windows 8.1 and Windows Server 2012 R2, monitor Windows Logs for LSASS.exe creation to verify that LSASS started as a protected process.\n\nMonitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.", "Type": 3, "Index": 4479}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5751}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 4227}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Process: Process Access\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4307}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5637}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5679}}', 'T1003.001', 'Adversaries may attempt to access credential material stored in the process memory of the Local Security Authority Subsystem Service (LSASS). After a user logs on, the system generates and stores a variety of credential materials in LSASS process memory. These credential materials can be harvested by an administrative user or SYSTEM and used to conduct [Lateral Movement](https://attack.mitre.org/tactics/TA0008) using [Use Alternate Authentication Material](https://attack.mitre.org/techniques/T1550).
INSERT INTO public.attack_patterns VALUES (276, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.657417+00', NULL, 'attack-pattern--d10cbd34-42e3-45c0-84d2-535a09849584', '[{"url": "https://attack.mitre.org/techniques/T1543/001", "external_id": "T1543.001", "source_name": "mitre-attack"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLaunchdJobs.html", "description": "Apple. (n.d.). Creating Launch Daemons and Agents. Retrieved July 10, 2017.", "source_name": "AppleDocs Launch Agent Daemons"}, {"url": "https://www.welivesecurity.com/2016/07/06/new-osxkeydnap-malware-hungry-credentials/", "description": "Marc-Etienne M.Leveille. (2016, July 6). New OSX/Keydnap malware is hungry for credentials. Retrieved July 3, 2017.", "source_name": "OSX Keydnap malware"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/01/new-mac-backdoor-using-antiquated-code/", "description": "Thomas Reed. (2017, January 18). New Mac backdoor using antiquated code. Retrieved July 5, 2017.", "source_name": "Antiquated Mac Malware"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/04/new-osx-dok-malware-intercepts-web-traffic/", "description": "Thomas Reed. (2017, July 7). New OSX.Dok malware intercepts web traffic. Retrieved July 10, 2017.", "source_name": "OSX.Dok Malware"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}, {"url": "https://www.synack.com/wp-content/uploads/2016/03/RSA_OSX_Malware.pdf", "description": "Patrick Wardle. (2016, February 29). Let''s Play Doctor: Practical OS X Malware Detection & Analysis. Retrieved July 10, 2017.", "source_name": "OSX Malware Detection"}, {"url": "https://www.alienvault.com/blogs/labs-research/oceanlotus-for-os-x-an-application-bundle-pretending-to-be-an-adobe-flash-update", "description": "Eddie Lee. (2016, February 17). OceanLotus for OS X - an Application Bundle Pretending to be an Adobe Flash Update. Retrieved July 5, 2017.", "source_name": "OceanLotus for OS X"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5516}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor Launch Agent creation through additional plist files and utilities such as Objective-See’s  KnockKnock application. Launch Agents also require files on disk for persistence which can also be monitored via other file monitoring applications.\"", "Str": "Monitor Launch Agent creation through additional plist files and utilities such as Objective-See’s  KnockKnock application. Launch Agents also require files on disk for persistence which can also be monitored via other file monitoring applications.", "Type": 3, "Index": 5073}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 5470}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Command: Command Execution\",\n      \"Service: Service Creation\",\n      \"Service: Service Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4868}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5358}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5400}}', 'T1543.001', 'Adversaries may create or modify launch agents to repeatedly execute malicious payloads as part of persistence. Per Apple’s developer documentation, when a user logs in, a per-user launchd process is started which loads the parameters for each launch-on-demand user agent from the property list (plist) files found in <code>/System/Library/LaunchAgents</code>, <code>/Library/LaunchAgents</code>, and <code>$HOME/Library/LaunchAgents</code> (Citation: AppleDocs Launch Agent Daemons) (Citation: OSX Keydnap malware) (Citation: Antiquated Mac Malware). These launch agents have property list files which point to the executables that will be launched (Citation: OSX.Dok Malware).
INSERT INTO public.attack_patterns VALUES (278, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.724618+00', NULL, 'attack-pattern--810aa4ad-61c9-49cb-993f-daa06199421d', '[{"url": "https://attack.mitre.org/techniques/T1569/001", "external_id": "T1569.001", "source_name": "mitre-attack"}, {"url": "https://ss64.com/osx/launchctl.html", "description": "SS64. (n.d.). launchctl. Retrieved March 28, 2020.", "source_name": "Launchctl Man"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3010}, "x_mitre_detection": {"Num": 0, "Raw": "\"KnockKnock can be used to detect persistent programs such as those installed via launchctl as launch agents or launch daemons. Additionally, every launch agent or launch daemon must have a corresponding plist file on disk which can be monitored. Monitor process execution from launchctl/launchd for unusual or unknown processes.\"", "Str": "KnockKnock can be used to detect persistent programs such as those installed via launchctl as launch agents or launch daemons. Additionally, every launch agent or launch daemon must have a corresponding plist file on disk which can be monitored. Monitor process execution from launchctl/launchd for unusual or unknown processes.", "Type": 3, "Index": 2498}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2964}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Service: Service Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2326}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2861}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2903}}', 'T1569.001', 'Adversaries may abuse launchctl to execute commands or programs. Launchctl controls the macOS launchd process, which handles things like [Launch Agent](https://attack.mitre.org/techniques/T1543/001)s and [Launch Daemon](https://attack.mitre.org/techniques/T1543/004)s, but can execute other commands or programs itself. Launchctl supports taking subcommands on the command-line, interactively, or even redirected from standard input.(Citation: Launchctl Man)
INSERT INTO public.attack_patterns VALUES (280, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.794537+00', NULL, 'attack-pattern--84ae8255-b4f4-4237-b5c5-e717405a9701', '[{"url": "https://attack.mitre.org/techniques/T1608/005", "external_id": "T1608.005", "source_name": "mitre-attack"}, {"url": "https://blog.malwarebytes.com/malwarebytes-news/2020/10/silent-librarian-apt-phishing-attack/", "description": "Malwarebytes Threat Intelligence Team. (2020, October 14). Silent Librarian APT right on schedule for 20/21 academic year. Retrieved February 3, 2021.", "source_name": "Malwarebytes Silent Librarian October 2020"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/threat-actor-profile-ta407-silent-librarian", "description": "Proofpoint Threat Insight Team. (2019, September 5). Threat Actor Profile: TA407, the Silent Librarian. Retrieved February 3, 2021.", "source_name": "Proofpoint TA407 September 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4018}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on other phases of the adversary lifecycle, such as during [Spearphishing Link](https://attack.mitre.org/techniques/T1598/003), [Spearphishing Link](https://attack.mitre.org/techniques/T1566/002), or [Malicious Link](https://attack.mitre.org/techniques/T1204/001).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on other phases of the adversary lifecycle, such as during [Spearphishing Link](https://attack.mitre.org/techniques/T1598/003), [Spearphishing Link](https://attack.mitre.org/techniques/T1566/002), or [Malicious Link](https://attack.mitre.org/techniques/T1204/001).", "Type": 3, "Index": 3477}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3974}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3943}}', 'T1608.005', 'Adversaries may put in place resources that are referenced by a link that can be used during targeting. An adversary may rely upon a user clicking a malicious link in order to divulge information (including credentials) or to gain execution, as in [Malicious Link](https://attack.mitre.org/techniques/T1204/001). Links can be used for spearphishing, such as sending an email accompanied by social engineering text to coax the user to actively click or copy and paste a URL into a browser. Prior to a phish for information (as in [Spearphishing Link](https://attack.mitre.org/techniques/T1598/003)) or a phish to gain initial access to a system (as in [Spearphishing Link](https://attack.mitre.org/techniques/T1566/002)), an adversary must set up the resources for a link target for the spearphishing link. 
INSERT INTO public.attack_patterns VALUES (281, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.829837+00', NULL, 'attack-pattern--09b130a2-a77e-4af0-a361-f46f9aad1345', '[{"url": "https://attack.mitre.org/techniques/T1222/002", "external_id": "T1222.002", "source_name": "mitre-attack"}, {"url": "https://www.hybrid-analysis.com/sample/ef0d2628823e8e0a0de3b08b8eacaf41cf284c086a948bdfd67f4e4373c14e4d?environmentId=100", "description": "Hybrid Analysis. (2018, June 12). c9b65b764985dfd7a11d3faf599c56b8.exe. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls1 June 2018"}, {"url": "https://www.hybrid-analysis.com/sample/22dab012c3e20e3d9291bce14a2bfc448036d3b966c6e78167f4626f5f9e38d6?environmentId=110", "description": "Hybrid Analysis. (2018, May 30). 2a8efbfadd798f6111340f7c1c956bee.dll. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls2 May 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3949}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and investigate attempts to modify ACLs and file/directory ownership. Many of the commands used to modify ACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\\n\\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files.\"", "Str": "Monitor and investigate attempts to modify ACLs and file/directory ownership. Many of the commands used to modify ACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\n\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files.", "Type": 3, "Index": 3237}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 3888}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3104}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3785}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3827}}', 'T1222.002', 'Adversaries may modify file or directory permissions/attributes to evade access control lists (ACLs) and access protected files.(Citation: Hybrid Analysis Icacls1 June 2018)(Citation: Hybrid Analysis Icacls2 May 2018) File and directory permissions are commonly managed by ACLs configured by the file or directory owner, or users with the appropriate permissions. File and directory ACL implementations vary by platform, but generally explicitly designate which users or groups can perform which actions (read, write, execute, etc.).
INSERT INTO public.attack_patterns VALUES (283, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.902967+00', NULL, 'attack-pattern--25659dd6-ea12-45c4-97e6-381e3e4b593e', '[{"url": "https://attack.mitre.org/techniques/T1087/001", "external_id": "T1087.001", "source_name": "mitre-attack"}, {"url": "https://www.elastic.co/blog/embracing-offensive-tooling-building-detections-against-koadic-using-eql", "description": "Stepanic, D.. (2020, January 13). Embracing offensive tooling: Building detections against Koadic using EQL. Retrieved November 30, 2020.", "source_name": "Elastic - Koadiac Detection with EQL"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3089}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nMonitor for processes that can be used to enumerate user accounts, such as <code>net.exe</code> and <code>net1.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nMonitor for processes that can be used to enumerate user accounts, such as <code>net.exe</code> and <code>net1.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)", "Type": 3, "Index": 1908}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3011}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniel Stepanic, Elastic\"\n    ]", "Str": "", "Type": 5, "Index": 1707}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 1777}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2922}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2964}}', 'T1087.001', 'Adversaries may attempt to get a listing of local system accounts. This information can help adversaries determine which local accounts exist on a system to aid in follow-on behavior.
INSERT INTO public.attack_patterns VALUES (285, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.976586+00', NULL, 'attack-pattern--1c34f7aa-9341-4a48-bfab-af22e51aca6c', '[{"url": "https://attack.mitre.org/techniques/T1074/001", "external_id": "T1074.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2510}, "x_mitre_detection": {"Num": 0, "Raw": "\"Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\\n\\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\n\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1412}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2432}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"File: File Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1287}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2401}}', 'T1074.001', 'Adversaries may stage collected data in a central location or directory on the local system prior to Exfiltration. Data may be kept in separate files or combined into one file through techniques such as [Archive Collected Data](https://attack.mitre.org/techniques/T1560). Interactive command shells may be used, and common functionality within [cmd](https://attack.mitre.org/software/S0106) and bash may be used to copy data into a staging location.', 'Local Data Staging', 120);
INSERT INTO public.attack_patterns VALUES (286, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.012039+00', NULL, 'attack-pattern--1e9eb839-294b-48cc-b0d3-c45555a2a004', '[{"url": "https://attack.mitre.org/techniques/T1114/001", "external_id": "T1114.001", "source_name": "mitre-attack"}, {"url": "https://practical365.com/clients/office-365-proplus/outlook-cached-mode-ost-file-sizes/", "description": "N. O''Bryan. (2018, May 30). Managing Outlook Cached Mode and OST File Sizes. Retrieved February 19, 2020.", "source_name": "Outlook File Sizes"}, {"url": "https://support.office.com/en-us/article/introduction-to-outlook-data-files-pst-and-ost-222eaf92-a995-45d9-bde2-f331f60e2790", "description": "Microsoft. (n.d.). Introduction to Outlook Data Files (.pst and .ost). Retrieved February 19, 2020.", "source_name": "Microsoft Outlook Files"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3048}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to gather local email files. Monitor for unusual processes accessing local email files. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to gather local email files. Monitor for unusual processes accessing local email files. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2377}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3000}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2281}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2911}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2953}}', 'T1114.001', 'Adversaries may target user email on local systems to collect sensitive information. Files containing email data can be acquired from a user’s local system, such as Outlook storage or cache files.
INSERT INTO public.attack_patterns VALUES (288, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.082201+00', NULL, 'attack-pattern--43ba2b05-cf72-4b6c-8243-03a4aba41ee0', '[{"url": "https://attack.mitre.org/techniques/T1037/002", "external_id": "T1037.002", "source_name": "mitre-attack"}, {"url": "https://support.apple.com/de-at/HT2420", "description": "Apple. (2011, June 1). Mac OS X: Creating a login hook. Retrieved July 17, 2017.", "source_name": "creating login hook"}, {"url": "https://www.sentinelone.com/blog/how-malware-persists-on-macos/", "description": "Stokes, P. (2019, July 17). How Malware Persists on macOS. Retrieved March 27, 2020.", "source_name": "S1 macOs Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2849}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.\"", "Str": "Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.", "Type": 3, "Index": 2446}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2803}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2280}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2772}}', 'T1037.002', 'Adversaries may use macOS logon scripts automatically executed at logon initialization to establish persistence. macOS allows logon scripts (known as login hooks) to be executed whenever a specific user logs into a system. A login hook tells Mac OS X to execute a certain script when a user logs in, but unlike [Startup Items](https://attack.mitre.org/techniques/T1037/005), a login hook executes as the elevated root user.(Citation: creating login hook)
INSERT INTO public.attack_patterns VALUES (289, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.115885+00', NULL, 'attack-pattern--eb125d40-0b2d-41ac-a71a-3229241c2cd3', '[{"url": "https://attack.mitre.org/techniques/T1037/001", "external_id": "T1037.001", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/library/cc758918(v=ws.10).aspx", "description": "Microsoft. (2005, January 21). Creating logon scripts. Retrieved April 27, 2016.", "source_name": "TechNet Logon Scripts"}, {"url": "http://www.hexacorn.com/blog/2014/11/14/beyond-good-ol-run-key-part-18/", "description": "Hexacorn. (2014, November 14). Beyond good ol’ Run key, Part 18. Retrieved November 15, 2019.", "source_name": "Hexacorn Logon Scripts"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2646}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for changes to Registry values associated with Windows logon scrips, nameley <code>HKCU\\\\Environment\\\\UserInitMprLogonScript</code>.\\n\\nMonitor running process for actions that could be indicative of abnormal programs or executables running upon logon.\"", "Str": "Monitor for changes to Registry values associated with Windows logon scrips, nameley <code>HKCU\\Environment\\UserInitMprLogonScript</code>.\n\nMonitor running process for actions that could be indicative of abnormal programs or executables running upon logon.", "Type": 3, "Index": 2272}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2598}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2111}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2567}}', 'T1037.001', 'Adversaries may use Windows logon scripts automatically executed at logon initialization to establish persistence. Windows allows logon scripts to be run whenever a specific user or group of users log into a system.(Citation: TechNet Logon Scripts) This is done via adding a path to a script to the <code>HKCU\Environment\UserInitMprLogonScript</code> Registry key.(Citation: Hexacorn Logon Scripts)
INSERT INTO public.attack_patterns VALUES (290, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.15079+00', NULL, 'attack-pattern--c92e3d68-2349-49e4-a341-7edca2deff96', '[{"url": "https://attack.mitre.org/techniques/T1127/001", "external_id": "T1127.001", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/dd393574.aspx", "description": "Microsoft. (n.d.). MSBuild1. Retrieved November 30, 2016.", "source_name": "MSDN MSBuild"}, {"url": "https://docs.microsoft.com/en-us/visualstudio/msbuild/msbuild-inline-tasks?view=vs-2019#code-element", "description": "Microsoft. (2017, September 21). MSBuild inline tasks. Retrieved March 5, 2021.", "source_name": "Microsoft MSBuild Inline Tasks 2017"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Msbuild/", "description": "LOLBAS. (n.d.). Msbuild.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Msbuild"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3231}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of MSBuild.exe. Compare recent invocations of those binaries with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after invocation of the utilities may also be useful in determining the origin and purpose of the binary being executed.\"", "Str": "Use process monitoring to monitor the execution and arguments of MSBuild.exe. Compare recent invocations of those binaries with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after invocation of the utilities may also be useful in determining the origin and purpose of the binary being executed.", "Type": 3, "Index": 2626}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3095}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Carrie Roberts, @OrOneEqualsOne\"\n    ]", "Str": "", "Type": 5, "Index": 2445}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2522}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3064}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \" .NET Framework version 4 or higher\"\n    ]", "Str": "", "Type": 5, "Index": 3155}}', 'T1127.001', 'Adversaries may use MSBuild to proxy execution of code through a trusted Windows utility. MSBuild.exe (Microsoft Build Engine) is a software build platform used by Visual Studio. It handles XML formatted project files that define requirements for loading and building various platforms and configurations.(Citation: MSDN MSBuild)
INSERT INTO public.attack_patterns VALUES (294, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.281509+00', NULL, 'attack-pattern--b0c74ef9-c61e-4986-88cb-78da98a355ec', '[{"url": "https://attack.mitre.org/techniques/T1204/003", "external_id": "T1204.003", "source_name": "mitre-attack"}, {"url": "https://summitroute.com/blog/2018/09/24/investigating_malicious_amis/", "description": "Piper, S.. (2018, September 24). Investigating Malicious AMIs. Retrieved March 30, 2021.", "source_name": "Summit Route Malicious AMIs"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2994}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the local image registry to make sure malicious images are not added. Track the deployment of new containers, especially from newly built images. Monitor the behavior of containers within the environment to detect anomalous behavior or malicious activity after users deploy from malicious images.\"", "Str": "Monitor the local image registry to make sure malicious images are not added. Track the deployment of new containers, especially from newly built images. Monitor the behavior of containers within the environment to detect anomalous behavior or malicious activity after users deploy from malicious images.", "Type": 3, "Index": 2501}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2929}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Vishwas Manral, McAfee\"\n    ]", "Str": "", "Type": 5, "Index": 2086}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Container: Container Creation\",\n      \"Container: Container Start\",\n      \"Command: Command Execution\",\n      \"Image: Image Creation\",\n      \"Instance: Instance Creation\",\n      \"Instance: Instance Start\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2205}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2840}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2882}}', 'T1204.003', 'Adversaries may rely on a user running a malicious image to facilitate execution. Amazon Web Services (AWS) Amazon Machine Images (AMIs), Google Cloud Platform (GCP) Images, and Azure Images as well as popular container runtimes such as Docker can be backdoored. Backdoored images may be uploaded to a public repository via [Upload Malware](https://attack.mitre.org/techniques/T1608/001), and users may then download and deploy an instance or container from the image without realizing the image is malicious, thus bypassing techniques that specifically achieve Initial Access. This can lead to the execution of malicious code, such as code that executes cryptocurrency mining, in the instance or container.(Citation: Summit Route Malicious AMIs)
INSERT INTO public.attack_patterns VALUES (295, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.314589+00', NULL, 'attack-pattern--ef67e13e-5598-4adc-bdb2-998225874fa9', '[{"url": "https://attack.mitre.org/techniques/T1204/001", "external_id": "T1204.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2173}, "x_mitre_detection": {"Num": 0, "Raw": "\"Inspect network traffic for indications that a user visited a malicious site, such as links included in phishing campaigns directed at your organization.\\n\\nAnti-virus can potentially detect malicious documents and files that are downloaded from a link and executed on the user''s computer.\"", "Str": "Inspect network traffic for indications that a user visited a malicious site, such as links included in phishing campaigns directed at your organization.\n\nAnti-virus can potentially detect malicious documents and files that are downloaded from a link and executed on the user''s computer.", "Type": 3, "Index": 1682}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2095}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1516}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2006}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2048}}', 'T1204.001', 'An adversary may rely upon a user clicking a malicious link in order to gain execution. Users may be subjected to social engineering to get them to click on a link that will lead to code execution. This user action will typically be observed as follow-on behavior from [Spearphishing Link](https://attack.mitre.org/techniques/T1566/002). Clicking on a link may also lead to other execution techniques such as exploitation of a browser or application vulnerability via [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203). Links may also lead users to download files that require execution via [Malicious File](https://attack.mitre.org/techniques/T1204/002).', 'Malicious Link', 520);
INSERT INTO public.attack_patterns VALUES (296, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.354159+00', NULL, 'attack-pattern--212306d8-efa4-44c9-8c2d-ed3d2e224aa0', '[{"url": "https://attack.mitre.org/techniques/T1587/001", "external_id": "T1587.001", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}, {"url": "https://securelist.com/sofacy-apt-hits-high-profile-targets-with-updated-toolset/72924/", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, December 4). Sofacy APT hits high profile targets with updated toolset. Retrieved December 10, 2015.", "source_name": "Kaspersky Sofacy"}, {"url": "https://arstechnica.com/information-technology/2014/06/active-malware-operation-let-attackers-sabotage-us-energy-industry/", "description": "Dan Goodin. (2014, June 30). Active malware operation let attackers sabotage US energy industry. Retrieved March 9, 2017.", "source_name": "ActiveMalwareEnergy"}, {"url": "https://www.losangeles.va.gov/documents/MI-000120-MW.pdf", "description": "Federal Bureau of Investigation, Cyber Division. (2020, March 26). FIN7 Cyber Actors Targeting US Businesses Through USB Keystroke Injection Attacks. Retrieved October 14, 2020.", "source_name": "FBI Flash FIN7 USB"}, {"url": "https://www2.fireeye.com/rs/848-DID-242/images/rpt-apt29-hammertoss.pdf", "description": "FireEye Labs. (2015, July). HAMMERTOSS: Stealthy Tactics Define a Russian Cyber Threat Group. Retrieved September 17, 2015.", "source_name": "FireEye APT29"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4096}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle.", "Type": 3, "Index": 3766}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4052}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4021}}', 'T1587.001', 'Adversaries may develop malware and malware components that can be used during targeting. Building malicious software can include the development of payloads, droppers, post-compromise tools, backdoors (including backdoored images), packers, C2 protocols, and the creation of infected removable media. Adversaries may develop malware to support their operations, creating a means for maintaining control of remote machines, evading defenses, and executing post-compromise behaviors.(Citation: Mandiant APT1)(Citation: Kaspersky Sofacy)(Citation: ActiveMalwareEnergy)(Citation: FBI Flash FIN7 USB)
INSERT INTO public.attack_patterns VALUES (301, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.389436+00', NULL, 'attack-pattern--7bdca9d5-d500-4d7d-8c52-5fd47baf4c0c', '[{"url": "https://attack.mitre.org/techniques/T1036/004", "external_id": "T1036.004", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/library/bb490996.aspx", "description": "Microsoft. (n.d.). Schtasks. Retrieved April 28, 2016.", "source_name": "TechNet Schtasks"}, {"url": "https://www.freedesktop.org/software/systemd/man/systemd.service.html", "description": "Freedesktop.org. (n.d.). systemd.service — Service unit configuration. Retrieved March 16, 2020.", "source_name": "Systemd Service Units"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/11/unit42-shamoon-2-return-disttrack-wiper/", "description": "Falcone, R.. (2016, November 30). Shamoon 2: Return of the Disttrack Wiper. Retrieved January 11, 2017.", "source_name": "Palo Alto Shamoon Nov 2016"}, {"url": "https://vms.drweb.com/virus/?i=4276269", "description": "Doctor Web. (2014, November 21). Linux.BackDoor.Fysbis.1. Retrieved December 7, 2017.", "source_name": "Fysbis Dr Web Analysis"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3702}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for changes to tasks and services that do not correlate with known software, patch cycles, etc. Suspicious program execution through scheduled tasks or services may show up as outlier processes that have not been seen before when compared against historical data. Monitor processes and command-line arguments for actions that could be taken to create tasks or services. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Look for changes to tasks and services that do not correlate with known software, patch cycles, etc. Suspicious program execution through scheduled tasks or services may show up as outlier processes that have not been seen before when compared against historical data. Monitor processes and command-line arguments for actions that could be taken to create tasks or services. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 2841}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 3639}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"Service: Service Creation\",\n      \"Scheduled Job: Scheduled Job Metadata\",\n      \"Scheduled Job: Scheduled Job Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2604}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3511}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3553}}', 'T1036.004', 'Adversaries may attempt to manipulate the name of a task or service to make it appear legitimate or benign. Tasks/services executed by the Task Scheduler or systemd will typically be given a name and/or description.(Citation: TechNet Schtasks)(Citation: Systemd Service Units) Windows services will have a service name as well as a display name. Many benign tasks and services exist that have commonly associated names. Adversaries may give tasks or services names that are similar or identical to those of legitimate ones.
INSERT INTO public.attack_patterns VALUES (299, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.495205+00', NULL, 'attack-pattern--035bb001-ab69-4a0b-9f6c-2de8b09e1b9d', '[{"url": "https://attack.mitre.org/techniques/T1557", "external_id": "T1557", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/94.html", "external_id": "CAPEC-94", "source_name": "capec"}, {"url": "https://www.rapid7.com/fundamentals/man-in-the-middle-attacks/", "description": "Rapid7. (n.d.). Man-in-the-Middle (MITM) Attacks. Retrieved March 2, 2020.", "source_name": "Rapid7 MiTM Basics"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2948}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor network traffic for anomalies associated with known MiTM behavior. Consider monitoring for modifications to system configuration files involved in shaping network traffic flow.\"", "Str": "Monitor network traffic for anomalies associated with known MiTM behavior. Consider monitoring for modifications to system configuration files involved in shaping network traffic flow.", "Type": 3, "Index": 2561}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2870}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniil Yugoslavskiy, @yugoslavskiy, Atomic Threat Coverage project\"\n    ]", "Str": "", "Type": 5, "Index": 2226}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Service: Service Creation\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2338}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2780}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2823}}', 'T1557', 'Adversaries may attempt to position themselves between two or more networked devices using a man-in-the-middle (MiTM) technique to support follow-on behaviors such as [Network Sniffing](https://attack.mitre.org/techniques/T1040) or [Transmitted Data Manipulation](https://attack.mitre.org/techniques/T1565/002). By abusing features of common networking protocols that can determine the flow of network traffic (e.g. ARP, DNS, LLMNR, etc.), adversaries may force a device to communicate through an adversary controlled system so they can collect information or perform additional actions.(Citation: Rapid7 MiTM Basics)
INSERT INTO public.attack_patterns VALUES (547, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.56554+00', NULL, 'attack-pattern--910906dd-8c0a-475a-9cc1-5e029e2fad58', '[{"url": "https://attack.mitre.org/techniques/T1546/003", "external_id": "T1546.003", "source_name": "mitre-attack"}, {"url": "https://www2.fireeye.com/rs/fireye/images/rpt-m-trends-2015.pdf", "description": "Mandiant. (2015, February 24). M-Trends 2015: A View from the Front Lines. Retrieved May 18, 2016.", "source_name": "Mandiant M-Trends 2015"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/sans-dfir-2015.pdf", "description": "Devon Kerr. (2015). There''s Something About WMI. Retrieved May 4, 2020.", "source_name": "FireEye WMI SANS 2015"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/wp-windows-management-instrumentation.pdf", "description": "Ballenthin, W., et al. (2015). Windows Management Instrumentation (WMI) Offense, Defense, and Forensics. Retrieved March 30, 2016.", "source_name": "FireEye WMI 2015"}, {"url": "https://www.secureworks.com/blog/wmi-persistence", "description": "Dell SecureWorks Counter Threat Unit™ (CTU) Research Team. (2016, March 28). A Novel WMI Persistence Implementation. Retrieved March 30, 2016.", "source_name": "Dell WMI Persistence"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/wmisdk/managed-object-format--mof-", "description": "Satran, M. (2018, May 30). Managed Object Format (MOF). Retrieved January 24, 2020.", "source_name": "Microsoft MOF May 2018"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}, {"url": "https://medium.com/threatpunter/detecting-removing-wmi-persistence-60ccbb7dff96", "description": "French, D. (2018, October 9). Detecting & Removing an Attacker’s WMI Persistence. Retrieved October 11, 2019.", "source_name": "Medium Detecting WMI Persistence"}, {"url": "https://www.elastic.co/blog/hunting-for-persistence-using-elastic-security-part-1", "description": "French, D., Murphy, B. (2020, March 24). Adversary tradecraft 101: Hunting for persistence using Elastic Security (Part 1). Retrieved December 21, 2020.", "source_name": "Elastic - Hunting for Persistence Part 1"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/microsoft.powershell.management/register-wmievent?view=powershell-5.1", "description": "Microsoft. (n.d.). Retrieved January 24, 2020.", "source_name": "Microsoft Register-WmiEvent"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 6103}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor WMI event subscription entries, comparing current WMI event subscriptions to known good subscriptions for each host. Tools such as Sysinternals Autoruns may also be used to detect WMI changes that could be attempts at persistence. (Citation: TechNet Autoruns) (Citation: Medium Detecting WMI Persistence) Monitor for the creation of new WMI <code>EventFilter</code>, <code>EventConsumer</code>, and <code>FilterToConsumerBinding</code> events. Event ID 5861 is logged on Windows 10 systems when new <code>EventFilterToConsumerBinding</code> events are created.(Citation: Elastic - Hunting for Persistence Part 1)\\n\\nMonitor processes and command-line arguments that can be used to register WMI persistence, such as the <code> Register-WmiEvent</code> [PowerShell](https://attack.mitre.org/techniques/T1059/001) cmdlet (Citation: Microsoft Register-WmiEvent), as well as those that result from the execution of subscriptions (i.e. spawning from the WmiPrvSe.exe WMI Provider Host process).\"", "Str": "Monitor WMI event subscription entries, comparing current WMI event subscriptions to known good subscriptions for each host. Tools such as Sysinternals Autoruns may also be used to detect WMI changes that could be attempts at persistence. (Citation: TechNet Autoruns) (Citation: Medium Detecting WMI Persistence) Monitor for the creation of new WMI <code>EventFilter</code>, <code>EventConsumer</code>, and <code>FilterToConsumerBinding</code> events. Event ID 5861 is logged on Windows 10 systems when new <code>EventFilterToConsumerBinding</code> events are created.(Citation: Elastic - Hunting for Persistence Part 1)\n\nMonitor processes and command-line arguments that can be used to register WMI persistence, such as the <code> Register-WmiEvent</code> [PowerShell](https://attack.mitre.org/techniques/T1059/001) cmdlet (Citation: Microsoft Register-WmiEvent), as well as those that result from the execution of subscriptions (i.e. spawning from the WmiPrvSe.exe WMI Provider Host process).", "Type": 3, "Index": 4910}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6055}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brent Murphy, Elastic\",\n      \"David French, Elastic\"\n    ]", "Str": "", "Type": 5, "Index": 4681}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"WMI: WMI Creation\"\n    ]", "Str": "", "Type": 5, "Index": 4779}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5941}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5983}}', 'T1546.003', 'Adversaries may establish persistence and elevate privileges by executing malicious content triggered by a Windows Management Instrumentation (WMI) event subscription. WMI can be used to install event filters, providers, consumers, and bindings that execute code when a defined event occurs. Examples of events that may be subscribed to are the wall clock time, user loging, or the computer''s uptime. (Citation: Mandiant M-Trends 2015)
INSERT INTO public.attack_patterns VALUES (303, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.63056+00', NULL, 'attack-pattern--1c4e5d32-1fe9-4116-9d9d-59e3925bd6a2', '[{"url": "https://attack.mitre.org/techniques/T1036/005", "external_id": "T1036.005", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/177.html", "external_id": "CAPEC-177", "source_name": "capec"}, {"url": "http://pages.endgame.com/rs/627-YBU-612/images/EndgameJournal_The%20Masquerade%20Ball_Pages_R2.pdf", "description": "Ewing, P. (2016, October 31). How to Hunt: The Masquerade Ball. Retrieved October 31, 2016.", "source_name": "Elastic Masquerade Ball"}, {"url": "https://twitter.com/ItsReallyNick/status/1055321652777619457", "description": "Carr, N.. (2018, October 25). Nick Carr Status Update Masquerading. Retrieved April 22, 2019.", "source_name": "Twitter ItsReallyNick Masquerading Update"}, {"url": "https://docs.docker.com/engine/reference/commandline/images/", "description": "Docker. (n.d.). Docker Images. Retrieved April 6, 2021.", "source_name": "Docker Images"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4261}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect file hashes; file names that do not match their expected hash are suspect. Perform file monitoring; files with known names but in unusual locations are suspect. Likewise, files that are modified outside of an update or patch are suspect.\\n\\nIf file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)\\n\\nIn containerized environments, use image IDs and layer hashes to compare images instead of relying only on their names.(Citation: Docker Images) Monitor for the unexpected creation of new resources within your cluster in Kubernetes, especially those created by atypical users.\"", "Str": "Collect file hashes; file names that do not match their expected hash are suspect. Perform file monitoring; files with known names but in unusual locations are suspect. Likewise, files that are modified outside of an update or patch are suspect.\n\nIf file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)\n\nIn containerized environments, use image IDs and layer hashes to compare images instead of relying only on their names.(Citation: Docker Images) Monitor for the unexpected creation of new resources within your cluster in Kubernetes, especially those created by atypical users.", "Type": 3, "Index": 2870}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4163}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\"\n    ]", "Str": "", "Type": 5, "Index": 2531}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Image: Image Metadata\",\n      \"File: File Metadata\",\n      \"Process: Process Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2652}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4132}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control by file name or path\"\n    ]", "Str": "", "Type": 5, "Index": 2787}}', 'T1036.005', 'Adversaries may match or approximate the name or location of legitimate files or resources when naming/placing them. This is done for the sake of evading defenses and observation. This may be done by placing an executable in a commonly trusted directory (ex: under System32) or giving it the name of a legitimate, trusted program (ex: svchost.exe). In containerized environments, this may also be done by creating a resource in a namespace that matches the naming convention of a container pod or cluster. Alternatively, a file or container image name given may be a close approximation to legitimate programs/images or something innocuous.
INSERT INTO public.attack_patterns VALUES (305, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.71545+00', NULL, 'attack-pattern--144e007b-e638-431d-a894-45d90c54ab90', '[{"url": "https://attack.mitre.org/techniques/T1578", "external_id": "T1578", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2810}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish centralized logging for the activity of cloud compute infrastructure components. Monitor for suspicious sequences of events, such as the creation of multiple snapshots within a short period of time or the mount of a snapshot to a new instance by a new or unexpected user. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.\"", "Str": "Establish centralized logging for the activity of cloud compute infrastructure components. Monitor for suspicious sequences of events, such as the creation of multiple snapshots within a short period of time or the mount of a snapshot to a new instance by a new or unexpected user. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.", "Type": 3, "Index": 2108}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 2765}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Stop\",\n      \"Instance: Instance Start\",\n      \"Instance: Instance Creation\",\n      \"Instance: Instance Modification\",\n      \"Instance: Instance Deletion\",\n      \"Snapshot: Snapshot Creation\",\n      \"Snapshot: Snapshot Modification\",\n      \"Snapshot: Snapshot Deletion\",\n      \"Volume: Volume Creation\",\n      \"Volume: Volume Modification\",\n      \"Volume: Volume Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 1675}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2675}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2718}}', 'T1578', 'An adversary may attempt to modify a cloud account''s compute service infrastructure to evade defenses. A modification to the compute service infrastructure can include the creation, deletion, or modification of one or more components such as compute instances, virtual machines, and snapshots.
INSERT INTO public.attack_patterns VALUES (306, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.766991+00', NULL, 'attack-pattern--57340c81-c025-4189-8fa0-fc7ede51bae4', '[{"url": "https://attack.mitre.org/techniques/T1112", "external_id": "T1112", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/203.html", "external_id": "CAPEC-203", "source_name": "capec"}, {"url": "https://technet.microsoft.com/en-us/library/cc732643.aspx", "description": "Microsoft. (2012, April 17). Reg. Retrieved May 1, 2015.", "source_name": "Microsoft Reg"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/reghide", "description": "Russinovich, M. & Sharkey, K. (2006, January 10). Reghide. Retrieved August 9, 2018.", "source_name": "Microsoft Reghide NOV 2006"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/poweliks-malware-hides-in-windows-registry/", "description": "Santos, R. (2014, August 1). POWELIKS: Malware Hides In Windows Registry. Retrieved August 9, 2018.", "source_name": "TrendMicro POWELIKS AUG 2014"}, {"url": "https://posts.specterops.io/hiding-registry-keys-with-psreflect-b18ec5ac8353", "description": "Reitz, B. (2017, July 14). Hiding Registry keys with PSReflect. Retrieved August 9, 2018.", "source_name": "SpectorOps Hiding Reg Jul 2017"}, {"url": "https://technet.microsoft.com/en-us/library/cc754820.aspx", "description": "Microsoft. (n.d.). Enable the Remote Registry Service. Retrieved May 1, 2015.", "source_name": "Microsoft Remote"}, {"url": "https://docs.microsoft.com/windows/security/threat-protection/auditing/event-4657", "description": "Miroshnikov, A. & Hall, J. (2017, April 18). 4657(S): A registry value was modified. Retrieved August 9, 2018.", "source_name": "Microsoft 4657 APR 2017"}, {"url": "https://docs.microsoft.com/en-us/sysinternals/downloads/regdelnull", "description": "Russinovich, M. & Sharkey, K. (2016, July 4). RegDelNull v1.11. Retrieved August 10, 2018.", "source_name": "Microsoft RegDelNull July 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6972}, "x_mitre_detection": {"Num": 0, "Raw": "\"Modifications to the Registry are normal and occur throughout typical use of the Windows operating system. Consider enabling Registry Auditing on specific keys to produce an alertable event (Event ID 4657) whenever a value is changed (though this may not trigger when values are created with Reghide or other evasive methods). (Citation: Microsoft 4657 APR 2017) Changes to Registry entries that load software on Windows startup that do not correlate with known software, patch cycles, etc., are suspicious, as are additions or changes to files within the startup folder. Changes could also include new services and modification of existing binary paths to point to malicious files. If a change to a service-related entry occurs, then it will likely be followed by a local or remote service start or restart to execute the file.\\n\\nMonitor processes and command-line arguments for actions that could be taken to change or delete information in the Registry. Remote access tools with built-in features may interact directly with the Windows API to gather information. The Registry may also be modified through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\\n\\nMonitor for processes, command-line arguments, and API calls associated with concealing Registry keys, such as Reghide. (Citation: Microsoft Reghide NOV 2006) Inspect and cleanup malicious hidden Registry entries using Native Windows API calls and/or tools such as Autoruns (Citation: SpectorOps Hiding Reg Jul 2017) and RegDelNull (Citation: Microsoft RegDelNull July 2016).\"", "Str": "Modifications to the Registry are normal and occur throughout typical use of the Windows operating system. Consider enabling Registry Auditing on specific keys to produce an alertable event (Event ID 4657) whenever a value is changed (though this may not trigger when values are created with Reghide or other evasive methods). (Citation: Microsoft 4657 APR 2017) Changes to Registry entries that load software on Windows startup that do not correlate with known software, patch cycles, etc., are suspicious, as are additions or changes to files within the startup folder. Changes could also include new services and modification of existing binary paths to point to malicious files. If a change to a service-related entry occurs, then it will likely be followed by a local or remote service start or restart to execute the file.\n\nMonitor processes and command-line arguments for actions that could be taken to change or delete information in the Registry. Remote access tools with built-in features may interact directly with the Windows API to gather information. The Registry may also be modified through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\n\nMonitor for processes, command-line arguments, and API calls associated with concealing Registry keys, such as Reghide. (Citation: Microsoft Reghide NOV 2006) Inspect and cleanup malicious hidden Registry entries using Native Windows API calls and/or tools such as Autoruns (Citation: SpectorOps Hiding Reg Jul 2017) and RegDelNull (Citation: Microsoft RegDelNull July 2016).", "Type": 3, "Index": 4954}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6924}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bartosz Jerzman\",\n      \"Travis Smith, Tripwire\",\n      \"David Lu, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 4447}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Windows Registry: Windows Registry Key Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 4568}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6795}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 4889}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 6838}}', 'T1112', 'Adversaries may interact with the Windows Registry to hide configuration information within Registry keys, remove information as part of cleaning up, or as part of other techniques to aid in persistence and execution.
INSERT INTO public.attack_patterns VALUES (310, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.83031+00', NULL, 'attack-pattern--84e02621-8fdf-470f-bd58-993bb6a89d91', '[{"url": "https://attack.mitre.org/techniques/T1104", "external_id": "T1104", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2602}, "x_mitre_detection": {"Num": 0, "Raw": "\"Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure. Relating subsequent actions that may result from Discovery of the system and network information or Lateral Movement to the originating process may also yield useful data.\"", "Str": "Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure. Relating subsequent actions that may result from Discovery of the system and network information or Lateral Movement to the originating process may also yield useful data.", "Type": 3, "Index": 2019}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2524}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1885}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2450}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2493}}', 'T1104', 'Adversaries may create multiple stages for command and control that are employed under different conditions or for certain functions. Use of multiple stages may obfuscate the command and control channel to make detection more difficult.
INSERT INTO public.attack_patterns VALUES (311, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.870567+00', NULL, 'attack-pattern--a782ebe2-daba-42c7-bc82-e8e9d923162d', '[{"url": "https://attack.mitre.org/techniques/T1090/003", "external_id": "T1090.003", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Onion_routing", "description": "Wikipedia. (n.d.). Onion Routing. Retrieved October 20, 2020.", "source_name": "Onion Routing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 4230}, "x_mitre_detection": {"Num": 0, "Raw": "\"When observing use of Multi-hop proxies, network data from the actual command and control servers could allow correlating incoming and outgoing flows to trace malicious traffic back to its source. Multi-hop proxies can also be detected by alerting on traffic to known anonymity networks (such as [Tor](https://attack.mitre.org/software/S0183)) or known adversary infrastructure that uses this technique.\\n\\nIn context of network devices, monitor traffic for encrypted communications from the Internet that is addressed to border routers.  Compare this traffic with the configuration to determine whether it matches with any configured site-to-site Virtual Private Network (VPN) connections the device was intended to have. Monitor traffic for encrypted communications originating from potentially breached routers that is addressed to other routers within the organization.  Compare the source and destination with the configuration of the device to determine if these channels are an authorized Virtual Private Network (VPN) connections or other encrypted modes of communication. Monitor ICMP traffic from the Internet that is addressed to border routers and is encrypted.  Few if any legitimate use cases exist for sending encrypted data to a network device via ICMP.\"", "Str": "When observing use of Multi-hop proxies, network data from the actual command and control servers could allow correlating incoming and outgoing flows to trace malicious traffic back to its source. Multi-hop proxies can also be detected by alerting on traffic to known anonymity networks (such as [Tor](https://attack.mitre.org/software/S0183)) or known adversary infrastructure that uses this technique.\n\nIn context of network devices, monitor traffic for encrypted communications from the Internet that is addressed to border routers.  Compare this traffic with the configuration to determine whether it matches with any configured site-to-site Virtual Private Network (VPN) connections the device was intended to have. Monitor traffic for encrypted communications originating from potentially breached routers that is addressed to other routers within the organization.  Compare the source and destination with the configuration of the device to determine if these channels are an authorized Virtual Private Network (VPN) connections or other encrypted modes of communication. Monitor ICMP traffic from the Internet that is addressed to border routers and is encrypted.  Few if any legitimate use cases exist for sending encrypted data to a network device via ICMP.", "Type": 3, "Index": 2800}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 4135}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2616}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4104}}', 'T1090.003', 'To disguise the source of malicious traffic, adversaries may chain together multiple proxies. Typically, a defender will be able to identify the last proxy traffic traversed before it enters their network; the defender may or may not be able to identify any previous proxies before the last-hop proxy. This technique makes identifying the original source of the malicious traffic even more difficult by requiring the defender to trace malicious traffic through several proxies to identify its source. A particular variant of this behavior is to use onion routing networks, such as the publicly available TOR network. (Citation: Onion Routing)
INSERT INTO public.attack_patterns VALUES (313, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.934253+00', NULL, 'attack-pattern--f2857333-11d4-45bf-b064-2c28d8525be5', '[{"url": "https://attack.mitre.org/techniques/T1564/004", "external_id": "T1564.004", "source_name": "mitre-attack"}, {"url": "https://posts.specterops.io/host-based-threat-modeling-indicator-design-a9dbbb53d5ea", "description": "Atkinson, J. (2017, July 18). Host-based Threat Modeling & Indicator Design. Retrieved March 21, 2018.", "source_name": "SpectorOps Host-Based Jul 2017"}, {"url": "https://blogs.technet.microsoft.com/askcore/2010/08/25/ntfs-file-attributes/", "description": "Hughes, J. (2010, August 25). NTFS File Attributes. Retrieved March 21, 2018.", "source_name": "Microsoft NTFS File Attributes Aug 2010"}, {"url": "http://msdn.microsoft.com/en-us/library/aa364404", "description": "Microsoft. (n.d.). File Streams. Retrieved December 2, 2014.", "source_name": "Microsoft File Streams"}, {"url": "https://blog.malwarebytes.com/101/2015/07/introduction-to-alternate-data-streams/", "description": "Arntz, P. (2015, July 22). Introduction to Alternate Data Streams. Retrieved March 21, 2018.", "source_name": "MalwareBytes ADS July 2015"}, {"url": "https://blogs.technet.microsoft.com/askcore/2013/03/24/alternate-data-streams-in-ntfs/", "description": "Marlin, J. (2013, March 24). Alternate Data Streams in NTFS. Retrieved March 21, 2018.", "source_name": "Microsoft ADS Mar 2014"}, {"url": "http://journeyintoir.blogspot.com/2012/12/extracting-zeroaccess-from-ntfs.html", "description": "Harrell, C. (2012, December 11). Extracting ZeroAccess from NTFS Extended Attributes. Retrieved June 3, 2016.", "source_name": "Journey into IR ZeroAccess NTFS EA"}, {"url": "https://oddvar.moe/2018/01/14/putting-data-in-alternate-data-streams-and-how-to-execute-it/", "description": "Moe, O. (2018, January 14). Putting Data in Alternate Data Streams and How to Execute It. Retrieved June 30, 2018.", "source_name": "Oddvar Moe ADS1 Jan 2018"}, {"url": "https://oddvar.moe/2018/04/11/putting-data-in-alternate-data-streams-and-how-to-execute-it-part-2/", "description": "Moe, O. (2018, April 11). Putting Data in Alternate Data Streams and How to Execute It - Part 2. Retrieved June 30, 2018.", "source_name": "Oddvar Moe ADS2 Apr 2018"}, {"url": "https://www.symantec.com/connect/articles/what-you-need-know-about-alternate-data-streams-windows-your-data-secure-can-you-restore", "description": "Pravs. (2009, May 25). What you need to know about alternate data streams in windows? Is your Data secure? Can you restore that?. Retrieved March 21, 2018.", "source_name": "Symantec ADS May 2009"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6564}, "x_mitre_detection": {"Num": 0, "Raw": "\"Forensic techniques exist to identify information stored in NTFS EA. (Citation: Journey into IR ZeroAccess NTFS EA) Monitor calls to the <code>ZwSetEaFile</code> and <code>ZwQueryEaFile</code> Windows API functions as well as binaries used to interact with EA, (Citation: Oddvar Moe ADS1 Jan 2018) (Citation: Oddvar Moe ADS2 Apr 2018) and consider regularly scanning for the presence of modified information. (Citation: SpectorOps Host-Based Jul 2017)\\n\\nThere are many ways to create and interact with ADSs using Windows utilities. Monitor for operations (execution, copies, etc.) with file names that contain colons. This syntax (ex: <code>file.ext:ads[.ext]</code>) is commonly associated with ADSs. (Citation: Microsoft ADS Mar 2014) (Citation: Oddvar Moe ADS1 Jan 2018) (Citation: Oddvar Moe ADS2 Apr 2018) For a more exhaustive list of utilities that can be used to execute and create ADSs, see https://gist.github.com/api0cradle/cdd2d0d0ec9abb686f0e89306e277b8f.\\n\\nThe Streams tool of Sysinternals can be used to uncover files with ADSs. The <code>dir /r</code> command can also be used to display ADSs. (Citation: Symantec ADS May 2009) Many PowerShell commands (such as Get-Item, Set-Item, Remove-Item, and Get-ChildItem) can also accept a <code>-stream</code> parameter to interact with ADSs. (Citation: MalwareBytes ADS July 2015) (Citation: Microsoft ADS Mar 2014)\"", "Str": "Forensic techniques exist to identify information stored in NTFS EA. (Citation: Journey into IR ZeroAccess NTFS EA) Monitor calls to the <code>ZwSetEaFile</code> and <code>ZwQueryEaFile</code> Windows API functions as well as binaries used to interact with EA, (Citation: Oddvar Moe ADS1 Jan 2018) (Citation: Oddvar Moe ADS2 Apr 2018) and consider regularly scanning for the presence of modified information. (Citation: SpectorOps Host-Based Jul 2017)\n\nThere are many ways to create and interact with ADSs using Windows utilities. Monitor for operations (execution, copies, etc.) with file names that contain colons. This syntax (ex: <code>file.ext:ads[.ext]</code>) is commonly associated with ADSs. (Citation: Microsoft ADS Mar 2014) (Citation: Oddvar Moe ADS1 Jan 2018) (Citation: Oddvar Moe ADS2 Apr 2018) For a more exhaustive list of utilities that can be used to execute and create ADSs, see https://gist.github.com/api0cradle/cdd2d0d0ec9abb686f0e89306e277b8f.\n\nThe Streams tool of Sysinternals can be used to uncover files with ADSs. The <code>dir /r</code> command can also be used to display ADSs. (Citation: Symantec ADS May 2009) Many PowerShell commands (such as Get-Item, Set-Item, Remove-Item, and Get-ChildItem) can also accept a <code>-stream</code> parameter to interact with ADSs. (Citation: MalwareBytes ADS July 2015) (Citation: Microsoft ADS Mar 2014)", "Type": 3, "Index": 4993}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6436}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oddvar Moe, @oddvarmoe\",\n      \"Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 4612}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"File: File Metadata\",\n      \"File: File Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4700}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6405}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host forensic analysis\",\n      \"Signature-based detection\"\n    ]", "Str": "", "Type": 5, "Index": 4873}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"NTFS partitioned hard drive\"\n    ]", "Str": "", "Type": 5, "Index": 6496}}', 'T1564.004', 'Adversaries may use NTFS file attributes to hide their malicious data in order to evade detection. Every New Technology File System (NTFS) formatted partition contains a Master File Table (MFT) that maintains a record for every file/directory on the partition. (Citation: SpectorOps Host-Based Jul 2017) Within MFT entries are file attributes, (Citation: Microsoft NTFS File Attributes Aug 2010) such as Extended Attributes (EA) and Data [known as Alternate Data Streams (ADSs) when more than one Data attribute is present], that can be used to store arbitrary data (and even complete files). (Citation: SpectorOps Host-Based Jul 2017) (Citation: Microsoft File Streams) (Citation: MalwareBytes ADS July 2015) (Citation: Microsoft ADS Mar 2014)
INSERT INTO public.attack_patterns VALUES (316, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.044889+00', NULL, 'attack-pattern--4ffc1794-ec3b-45be-9e52-42dbcb2af2de', '[{"url": "https://attack.mitre.org/techniques/T1599/001", "external_id": "T1599.001", "source_name": "mitre-attack"}, {"url": "https://tools.ietf.org/html/rfc1918", "description": "IETF Network Working Group. (1996, February). Address Allocation for Private Internets. Retrieved October 20, 2020.", "source_name": "RFC1918"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3677}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring network traffic on both interfaces of border network devices.  Compare packets transmitted by the device between networks to look for signs of NAT being implemented.  Packets which have their IP addresses changed should still have the same size and contents in the data encapsulated beyond Layer 3.  In some cases, Port Address Translation (PAT) may also be used by an adversary.\\n\\nMonitor the border network device’s configuration to determine if any unintended NAT rules have been added without authorization.\"", "Str": "Consider monitoring network traffic on both interfaces of border network devices.  Compare packets transmitted by the device between networks to look for signs of NAT being implemented.  Packets which have their IP addresses changed should still have the same size and contents in the data encapsulated beyond Layer 3.  In some cases, Port Address Translation (PAT) may also be used by an adversary.\n\nMonitor the border network device’s configuration to determine if any unintended NAT rules have been added without authorization.", "Type": 3, "Index": 2962}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3629}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2832}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3531}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3573}}', 'T1599.001', 'Adversaries may bridge network boundaries by modifying a network device’s Network Address Translation (NAT) configuration. Malicious modifications to NAT may enable an adversary to bypass restrictions on traffic routing that otherwise separate trusted and untrusted networks.
INSERT INTO public.attack_patterns VALUES (317, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.076618+00', NULL, 'attack-pattern--b8017880-4b1e-42de-ad10-ae7ac6705166', '[{"url": "https://attack.mitre.org/techniques/T1599", "external_id": "T1599", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3357}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring network traffic on both interfaces of border network devices with out-of-band packet capture or network flow data, using a different device than the one in question.  Look for traffic that should be prohibited by the intended network traffic policy enforcement for the border network device.\\n\\nMonitor the border network device’s configuration to validate that the policy enforcement sections are what was intended.  Look for rules that are less restrictive, or that allow specific traffic types that were not previously authorized.\"", "Str": "Consider monitoring network traffic on both interfaces of border network devices with out-of-band packet capture or network flow data, using a different device than the one in question.  Look for traffic that should be prohibited by the intended network traffic policy enforcement for the border network device.\n\nMonitor the border network device’s configuration to validate that the policy enforcement sections are what was intended.  Look for rules that are less restrictive, or that allow specific traffic types that were not previously authorized.", "Type": 3, "Index": 2620}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3309}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2412}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3210}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Router ACL\",\n      \"Firewall\"\n    ]", "Str": "", "Type": 5, "Index": 2549}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3253}}', 'T1599', 'Adversaries may bridge network boundaries by compromising perimeter network devices. Breaching these devices may enable an adversary to bypass restrictions on traffic routing that otherwise separate trusted and untrusted networks.
INSERT INTO public.attack_patterns VALUES (318, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.1123+00', NULL, 'attack-pattern--d74c4a7e-ffbf-432f-9365-7ebf1f787cab', '[{"url": "https://attack.mitre.org/techniques/T1498", "external_id": "T1498", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2014/11/operation-poisoned-handover-unveiling-ties-between-apt-activity-in-hong-kongs-pro-democracy-movement.html", "description": "Ned Moran, Mike Scott, Mike Oppenheim of FireEye. (2014, November 3). Operation Poisoned Handover: Unveiling Ties Between APT Activity in Hong Kong’s Pro-Democracy Movement. Retrieved April 18, 2019.", "source_name": "FireEye OpPoisonedHandover February 2016"}, {"url": "https://www.ic3.gov/media/2012/FraudAlertFinancialInstitutionEmployeeCredentialsTargeted.pdf", "description": "FS-ISAC. (2012, September 17). Fraud Alert – Cyber Criminals Targeting Financial Institution Employee Credentials to Conduct Wire Transfer Fraud. Retrieved April 18, 2019.", "source_name": "FSISAC FraudNetDoS September 2012"}, {"url": "https://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-continued-rise-of-ddos-attacks.pdf", "description": "Wueest, C.. (2014, October 21). The continued rise of DDoS attacks. Retrieved April 24, 2019.", "source_name": "Symantec DDoS October 2014"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5606}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of Network DoS can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an Network DoS event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.\"", "Str": "Detection of Network DoS can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an Network DoS event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.", "Type": 3, "Index": 4399}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 5416}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 5323}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\"\n    ]", "Str": "", "Type": 5, "Index": 4162}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 4283}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5384}}', 'T1498', 'Adversaries may perform Network Denial of Service (DoS) attacks to degrade or block the availability of targeted resources to users. Network DoS can be performed by exhausting the network bandwidth services rely on. Example resources include specific websites, email services, DNS, and web-based applications. Adversaries have been observed conducting network DoS attacks for political purposes(Citation: FireEye OpPoisonedHandover February 2016) and to support other malicious activities, including distraction(Citation: FSISAC FraudNetDoS September 2012), hacktivism, and extortion.(Citation: Symantec DDoS October 2014)
INSERT INTO public.attack_patterns VALUES (319, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.153175+00', NULL, 'attack-pattern--fa44a152-ac48-441e-a524-dd7b04b8adcd', '[{"url": "https://attack.mitre.org/techniques/T1556/004", "external_id": "T1556.004", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/09/synful_knock_-_acis.html", "description": "Bill Hau, Tony Lee, Josh Homan. (2015, September 15). SYNful Knock - A Cisco router implant - Part I. Retrieved October 19, 2020.", "source_name": "FireEye - Synful Knock"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#7", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Image File Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Image File Verification"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#13", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Run-Time Memory Integrity Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Run-Time Memory Verification"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3649}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider verifying the checksum of the operating system file and verifying the image of the operating system in memory.(Citation: Cisco IOS Software Integrity Assurance - Image File Verification)(Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\\n\\nDetection of this behavior may be difficult, detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601).\"", "Str": "Consider verifying the checksum of the operating system file and verifying the image of the operating system in memory.(Citation: Cisco IOS Software Integrity Assurance - Image File Verification)(Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\n\nDetection of this behavior may be difficult, detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601).", "Type": 3, "Index": 2997}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3601}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2931}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3503}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3545}}', 'T1556.004', 'Adversaries may use [Patch System Image](https://attack.mitre.org/techniques/T1601/001) to hard code a password in the operating system, thus bypassing of native authentication mechanisms for local accounts on network devices.
INSERT INTO public.attack_patterns VALUES (321, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.217114+00', NULL, 'attack-pattern--52759bf1-fe12-4052-ace6-c5b0cf7dd7fd', '[{"url": "https://attack.mitre.org/techniques/T1602/002", "external_id": "T1602.002", "source_name": "mitre-attack"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/TA18-106A", "description": "US-CERT. (2018, April 20). Russian State-Sponsored Cyber Actors Targeting Network Infrastructure Devices. Retrieved October 19, 2020.", "source_name": "US-CERT TA18-106A Network Infrastructure Devices 2018"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-086A", "description": "US-CERT. (2018, March 27). TA18-068A Brute Force Attacks Conducted by Cyber Actors. Retrieved October 2, 2019.", "source_name": "US-CERT TA18-068A 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3318}, "x_mitre_detection": {"Num": 0, "Raw": "\"Identify network traffic sent or received by untrusted hosts or networks. Configure signatures to identify strings that may be found in a network device configuration. (Citation: US-CERT TA18-068A 2018)\"", "Str": "Identify network traffic sent or received by untrusted hosts or networks. Configure signatures to identify strings that may be found in a network device configuration. (Citation: US-CERT TA18-068A 2018)", "Type": 3, "Index": 2935}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3270}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2798}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3172}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3214}}', 'T1602.002', 'Adversaries may access network configuration files to collect sensitive data about the device and the network. The network configuration is a file containing parameters that determine the operation of the device. The device typically stores an in-memory copy of the configuration while operating, and a separate configuration on non-volatile storage to load after device reset. Adversaries can inspect the configuration files to reveal information about the target network and its layout, the network device and its software, or identifying legitimate accounts and credentials for later use.
INSERT INTO public.attack_patterns VALUES (323, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.28496+00', NULL, 'attack-pattern--6c2957f9-502a-478c-b1dd-d626c0659413', '[{"url": "https://attack.mitre.org/techniques/T1590/006", "external_id": "T1590.006", "source_name": "mitre-attack"}, {"url": "https://nmap.org/book/firewalls.html", "description": "Nmap. (n.d.). Chapter 10. Detecting and Subverting Firewalls and Intrusion Detection Systems. Retrieved October 20, 2020.", "source_name": "Nmap Firewalls NIDS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3011}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2570}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2967}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2936}}', 'T1590.006', 'Adversaries may gather information about the victim''s network security appliances that can be used during targeting. Information about network security appliances may include a variety of details, such as the existence and specifics of deployed firewalls, content filters, and proxies/bastion hosts. Adversaries may also target information about victim network-based intrusion detection systems (NIDS) or other appliances related to defensive cybersecurity operations.
INSERT INTO public.attack_patterns VALUES (324, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.316089+00', NULL, 'attack-pattern--e3a12395-188d-4051-9a16-ea8e14d07b88', '[{"url": "https://attack.mitre.org/techniques/T1046", "external_id": "T1046", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/300.html", "external_id": "CAPEC-300", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 2821}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nNormal, benign system and network events from legitimate remote service scanning may be uncommon, depending on the environment and how they are used. Legitimate open port and vulnerability scanning may be conducted within the environment and will need to be deconflicted with any detection capabilities developed. Network intrusion detection systems can also be used to identify scanning activity. Monitor for process use of the networks and inspect intra-network flows to detect port scans.\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nNormal, benign system and network events from legitimate remote service scanning may be uncommon, depending on the environment and how they are used. Legitimate open port and vulnerability scanning may be conducted within the environment and will need to be deconflicted with any detection capabilities developed. Network intrusion detection systems can also be used to identify scanning activity. Monitor for process use of the networks and inspect intra-network flows to detect port scans.", "Type": 3, "Index": 1748}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2709}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 1526}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Cloud Service: Cloud Service Enumeration\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 1582}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2580}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2623}}', 'T1046', 'Adversaries may attempt to get a listing of services running on remote hosts, including those that may be vulnerable to remote software exploitation. Methods to acquire this information include port scans and vulnerability scans using tools that are brought onto a system. 
INSERT INTO public.attack_patterns VALUES (328, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.422222+00', NULL, 'attack-pattern--34ab90a3-05f6-4259-8f21-621081fdaba5', '[{"url": "https://attack.mitre.org/techniques/T1590/004", "external_id": "T1590.004", "source_name": "mitre-attack"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2855}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2414}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2811}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2780}}', 'T1590.004', 'Adversaries may gather information about the victim''s network topology that can be used during targeting. Information about network topologies may include a variety of details, including the physical and/or logical arrangement of both external-facing and internal network environments. This information may also include specifics regarding network devices (gateways, routers, etc.) and other infrastructure.
INSERT INTO public.attack_patterns VALUES (335, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.490655+00', NULL, 'attack-pattern--b3d682b6-98f2-4fb0-aa3b-b4df007ca70a', '[{"url": "https://attack.mitre.org/techniques/T1027", "external_id": "T1027", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/267.html", "external_id": "CAPEC-267", "source_name": "capec"}, {"url": "https://www.volexity.com/blog/2016/11/09/powerduke-post-election-spear-phishing-campaigns-targeting-think-tanks-and-ngos/", "description": "Adair, S.. (2016, November 9). PowerDuke: Widespread Post-Election Spear Phishing Campaigns Targeting Think Tanks and NGOs. Retrieved January 11, 2017.", "source_name": "Volexity PowerDuke November 2016"}, {"url": "https://www.welivesecurity.com/2013/04/26/linuxcdorked-new-apache-backdoor-in-the-wild-serves-blackhole/", "description": "Pierre-Marc Bureau. (2013, April 26). Linux/Cdorked.A: New Apache backdoor being used in the wild to serve Blackhole. Retrieved September 10, 2017.", "source_name": "Linux/Cdorked.A We Live Security Analysis"}, {"url": "https://www.carbonblack.com/2016/09/23/security-advisory-variants-well-known-adware-families-discovered-include-sophisticated-obfuscation-techniques-previously-associated-nation-state-attacks/", "description": "Tedesco, B. (2016, September 23). Security Alert Summary. Retrieved February 12, 2018.", "source_name": "Carbon Black Obfuscation Sept 2016"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/06/obfuscation-in-the-wild.html", "description": "Bohannon, D. & Carr N. (2017, June 30). Obfuscation in the Wild: Targeted Attackers Lead the Way in Evasion Techniques. Retrieved February 12, 2018.", "source_name": "FireEye Obfuscation June 2017"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/blog/pdfs/revoke-obfuscation-report.pdf", "description": "Bohannon, D. & Holmes, L. (2017, July 27). Revoke-Obfuscation: PowerShell Obfuscation Detection Using Science. Retrieved February 12, 2018.", "source_name": "FireEye Revoke-Obfuscation July 2017"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/03/unit42-pulling-back-the-curtains-on-encodedcommand-powershell-attacks/", "description": "White, J. (2017, March 10). Pulling Back the Curtains on EncodedCommand PowerShell Attacks. Retrieved February 12, 2018.", "source_name": "PaloAlto EncodedCommand March 2017"}, {"url": "https://github.com/danielbohannon/Revoke-Obfuscation", "description": "Bohannon, D. (2017, July 27). Revoke-Obfuscation. Retrieved February 12, 2018.", "source_name": "GitHub Revoke-Obfuscation"}, {"url": "https://github.com/itsreallynick/office-crackros", "description": "Carr, N. (2016, August 14). OfficeCrackros. Retrieved February 12, 2018.", "source_name": "GitHub Office-Crackros Aug 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 8079}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of file obfuscation is difficult unless artifacts are left behind by the obfuscation process that are uniquely detectable with a signature. If detection of the obfuscation itself is not possible, it may be possible to detect the malicious activity that caused the obfuscated file (for example, the method that was used to write, read, or modify the file on the file system). \\n\\nFlag and analyze commands containing indicators of obfuscation and known suspicious syntax such as uninterpreted escape characters like ''''''^'''''' and ''''''\\\"''''''. Windows'' Sysmon and Event ID 4688 displays command-line arguments for processes. Deobfuscation tools can be used to detect these indicators in files/payloads. (Citation: GitHub Revoke-Obfuscation) (Citation: FireEye Revoke-Obfuscation July 2017) (Citation: GitHub Office-Crackros Aug 2016) \\n\\nObfuscation used in payloads for Initial Access can be detected at the network. Use network intrusion detection systems and email gateway filtering to identify compressed and encrypted attachments and scripts. Some email attachment detonation systems can open compressed and encrypted attachments. Payloads delivered over an encrypted connection from a website require encrypted network traffic inspection. \\n\\nThe first detection of a malicious tool may trigger an anti-virus or other security tool alert. Similar events may also occur at the boundary through network IDS, email scanning appliance, etc. The initial detection should be treated as an indication of a potentially more invasive intrusion. The alerting system should be thoroughly investigated beyond that initial alert for activity that was not detected. Adversaries may continue with an operation, assuming that individual events like an anti-virus detect will not be investigated or that an analyst will not be able to conclusively link that event to other activity occurring on the network. \"", "Str": "Detection of file obfuscation is difficult unless artifacts are left behind by the obfuscation process that are uniquely detectable with a signature. If detection of the obfuscation itself is not possible, it may be possible to detect the malicious activity that caused the obfuscated file (for example, the method that was used to write, read, or modify the file on the file system). \n\nFlag and analyze commands containing indicators of obfuscation and known suspicious syntax such as uninterpreted escape characters like ''''''^'''''' and ''''''\"''''''. Windows'' Sysmon and Event ID 4688 displays command-line arguments for processes. Deobfuscation tools can be used to detect these indicators in files/payloads. (Citation: GitHub Revoke-Obfuscation) (Citation: FireEye Revoke-Obfuscation July 2017) (Citation: GitHub Office-Crackros Aug 2016) \n\nObfuscation used in payloads for Initial Access can be detected at the network. Use network intrusion detection systems and email gateway filtering to identify compressed and encrypted attachments and scripts. Some email attachment detonation systems can open compressed and encrypted attachments. Payloads delivered over an encrypted connection from a website require encrypted network traffic inspection. \n\nThe first detection of a malicious tool may trigger an anti-virus or other security tool alert. Similar events may also occur at the boundary through network IDS, email scanning appliance, etc. The initial detection should be treated as an indication of a potentially more invasive intrusion. The alerting system should be thoroughly investigated beyond that initial alert for activity that was not detected. Adversaries may continue with an operation, assuming that individual events like an anti-virus detect will not be investigated or that an analyst will not be able to conclusively link that event to other activity occurring on the network. ", "Type": 3, "Index": 6034}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 8001}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Red Canary\",\n      \"Christiaan Beek, @ChristiaanBeek\"\n    ]", "Str": "", "Type": 5, "Index": 5459}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Content\",\n      \"File: File Metadata\",\n      \"File: File Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5557}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7969}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\",\n      \"Signature-based detection\",\n      \"Host intrusion prevention systems\",\n      \"Application control\",\n      \"Log analysis\",\n      \"Application control by file name or path\"\n    ]", "Str": "", "Type": 5, "Index": 5790}}', 'T1027', 'Adversaries may attempt to make an executable or file difficult to discover or analyze by encrypting, encoding, or otherwise obfuscating its contents on the system or in transit. This is common behavior that can be used across different platforms and the network to evade defenses. 
INSERT INTO public.attack_patterns VALUES (332, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.599453+00', NULL, 'attack-pattern--b18eae87-b469-4e14-b454-b171b416bc18', '[{"url": "https://attack.mitre.org/techniques/T1571", "external_id": "T1571", "source_name": "mitre-attack"}, {"url": "https://www.symantec.com/blogs/threat-intelligence/elfin-apt33-espionage", "description": "Security Response attack Investigation Team. (2019, March 27). Elfin: Relentless Espionage Group Targets Multiple Organizations in Saudi Arabia and U.S.. Retrieved April 10, 2019.", "source_name": "Symantec Elfin Mar 2019"}, {"url": "https://www.fortinet.com/blog/threat-research/analysis-of-new-agent-tesla-spyware-variant.html", "description": "Zhang, X. (2018, April 05). Analysis of New Agent Tesla Spyware Variant. Retrieved November 5, 2018.", "source_name": "Fortinet Agent Tesla April 2018"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2961}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2)\"", "Str": "Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2393}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2883}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2209}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2851}}', 'T1571', 'Adversaries may communicate using a protocol and port paring that are typically not associated. For example, HTTPS over port 8088(Citation: Symantec Elfin Mar 2019) or port 587(Citation: Fortinet Agent Tesla April 2018) as opposed to the traditional port 443. Adversaries may make changes to the standard port used by a protocol to bypass filtering or muddle analysis/parsing of network data.', 'Non-Standard Port', 0);
INSERT INTO public.attack_patterns VALUES (333, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.641394+00', NULL, 'attack-pattern--0a3ead4e-6d47-4ccb-854c-a6a4f9d96b22', '[{"url": "https://attack.mitre.org/techniques/T1003", "external_id": "T1003", "source_name": "mitre-attack"}, {"url": "https://medium.com/threatpunter/detecting-attempts-to-steal-passwords-from-memory-558f16dce4ea", "description": "French, D. (2018, October 2). Detecting Attempts to Steal Passwords from Memory. Retrieved October 11, 2019.", "source_name": "Medium Detecting Attempts to Steal Passwords from Memory"}, {"url": "https://github.com/mattifestation/PowerSploit", "description": "PowerSploit. (n.d.). Retrieved December 4, 2014.", "source_name": "Powersploit"}, {"url": "https://msdn.microsoft.com/library/cc228086.aspx", "description": "Microsoft. (2017, December 1). MS-DRSR Directory Replication Service (DRS) Remote Protocol. Retrieved December 4, 2017.", "source_name": "Microsoft DRSR Dec 2017"}, {"url": "https://msdn.microsoft.com/library/dd207691.aspx", "description": "Microsoft. (n.d.). IDL_DRSGetNCChanges (Opnum 3). Retrieved December 4, 2017.", "source_name": "Microsoft GetNCCChanges"}, {"url": "https://wiki.samba.org/index.php/DRSUAPI", "description": "SambaWiki. (n.d.). DRSUAPI. Retrieved December 4, 2017.", "source_name": "Samba DRSUAPI"}, {"url": "http://www.harmj0y.net/blog/redteaming/mimikatz-and-dcsync-and-extrasids-oh-my/", "description": "Schroeder, W. (2015, September 22). Mimikatz and DCSync and ExtraSids, Oh My. Retrieved December 4, 2017.", "source_name": "Harmj0y DCSync Sept 2015"}, {"url": "https://msdn.microsoft.com/library/cc237008.aspx", "description": "Microsoft. (2017, December 1). MS-NRPC - Netlogon Remote Protocol. Retrieved December 6, 2017.", "source_name": "Microsoft NRPC Dec 2017"}, {"url": "https://msdn.microsoft.com/library/cc245496.aspx", "description": "Microsoft. (n.d.). MS-SAMR Security Account Manager (SAM) Remote Protocol (Client-to-Server) - Transport. Retrieved December 4, 2017.", "source_name": "Microsoft SAMR"}, {"url": "https://adsecurity.org/?p=1729", "description": "Metcalf, S. (2015, September 25). Mimikatz DCSync Usage, Exploitation, and Detection. Retrieved December 4, 2017.", "source_name": "AdSecurity DCSync Sept 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 7470}, "x_mitre_detection": {"Num": 0, "Raw": "\"### Windows\\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as [Mimikatz](https://attack.mitre.org/software/S0002) access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details are stored. Credential dumpers may also use methods for reflective [Process Injection](https://attack.mitre.org/techniques/T1055) to reduce potential indicators of malicious activity.\\n\\nHash dumpers open the Security Accounts Manager (SAM) on the local file system (%SystemRoot%/system32/config/SAM) or create a dump of the Registry SAM key to access stored account password hashes. Some hash dumpers will open the local file system as a device and parse to the SAM table to avoid file access defenses. Others will make an in-memory copy of the SAM table before reading hashes. Detection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well. \\n\\nOn Windows 8.1 and Windows Server 2012 R2, monitor Windows Logs for LSASS.exe creation to verify that LSASS started as a protected process.\\n\\nMonitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like [Mimikatz](https://attack.mitre.org/software/S0002). [PowerShell](https://attack.mitre.org/techniques/T1059/001) scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module, (Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\\n\\nMonitor domain controller logs for replication requests and other unscheduled activity possibly associated with DCSync. (Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft GetNCCChanges) (Citation: Samba DRSUAPI) Note: Domain controllers may not log replication requests originating from the default domain controller account. (Citation: Harmj0y DCSync Sept 2015). Also monitor for network protocols  (Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft NRPC Dec 2017) and other replication requests (Citation: Microsoft SAMR) from IPs not associated with known domain controllers. (Citation: AdSecurity DCSync Sept 2015)\\n\\n### Linux\\nTo obtain the passwords and hashes stored in memory, processes must open a maps file in the /proc filesystem for the process being analyzed. This file is stored under the path <code>/proc/<pid>/maps</code>, where the <code><pid></code> directory is the unique pid of the program being interrogated for such authentication data. The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes opening this file in the proc file system, alerting on the pid, process name, and arguments of such programs.\"", "Str": "### Windows\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as [Mimikatz](https://attack.mitre.org/software/S0002) access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details are stored. Credential dumpers may also use methods for reflective [Process Injection](https://attack.mitre.org/techniques/T1055) to reduce potential indicators of malicious activity.\n\nHash dumpers open the Security Accounts Manager (SAM) on the local file system (%SystemRoot%/system32/config/SAM) or create a dump of the Registry SAM key to access stored account password hashes. Some hash dumpers will open the local file system as a device and parse to the SAM table to avoid file access defenses. Others will make an in-memory copy of the SAM table before reading hashes. Detection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well. \n\nOn Windows 8.1 and Windows Server 2012 R2, monitor Windows Logs for LSASS.exe creation to verify that LSASS started as a protected process.\n\nMonitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like [Mimikatz](https://attack.mitre.org/software/S0002). [PowerShell](https://attack.mitre.org/techniques/T1059/001) scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module, (Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\n\nMonitor domain controller logs for replication requests and other unscheduled activity possibly associated with DCSync. (Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft GetNCCChanges) (Citation: Samba DRSUAPI) Note: Domain controllers may not log replication requests originating from the default domain controller account. (Citation: Harmj0y DCSync Sept 2015). Also monitor for network protocols  (Citation: Microsoft DRSR Dec 2017) (Citation: Microsoft NRPC Dec 2017) and other replication requests (Citation: Microsoft SAMR) from IPs not associated with known domain controllers. (Citation: AdSecurity DCSync Sept 2015)\n\n### Linux\nTo obtain the passwords and hashes stored in memory, processes must open a maps file in the /proc filesystem for the process being analyzed. This file is stored under the path <code>/proc/<pid>/maps</code>, where the <code><pid></code> directory is the unique pid of the program being interrogated for such authentication data. The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes opening this file in the proc file system, alerting on the pid, process name, and arguments of such programs.", "Type": 3, "Index": 4199}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 7392}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vincent Le Toux\",\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 3685}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Process: Process Access\",\n      \"Command: Command Execution\",\n      \"File: File Access\",\n      \"Windows Registry: Windows Registry Key Access\",\n      \"Active Directory: Active Directory Object Access\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3790}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7263}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 7306}}', 'T1003', 'Adversaries may attempt to dump credentials to obtain account login and credential material, normally in the form of a hash or a clear text password, from the operating system and software. Credentials can then be used to perform [Lateral Movement](https://attack.mitre.org/tactics/TA0008) and access restricted information.
INSERT INTO public.attack_patterns VALUES (334, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.681488+00', NULL, 'attack-pattern--0df05477-c572-4ed6-88a9-47c581f548f7', '[{"url": "https://attack.mitre.org/techniques/T1499/001", "external_id": "T1499.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/469.html", "external_id": "CAPEC-469", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/482.html", "external_id": "CAPEC-482", "source_name": "capec"}, {"url": "https://pages.arbornetworks.com/rs/082-KNA-087/images/13th_Worldwide_Infrastructure_Security_Report.pdf", "description": "Philippe Alcoy, Steinthor Bjarnason, Paul Bowen, C.F. Chui, Kirill Kasavchnko, and Gary Sockrider of Netscout Arbor. (2018, January). Insight into the Global Threat Landscape - Netscout Arbor''s 13th Annual Worldwide Infrastructure Security Report. Retrieved April 22, 2019.", "source_name": "Arbor AnnualDoSreport Jan 2018"}, {"url": "https://www.cloudflare.com/learning/ddos/syn-flood-ddos-attack/", "description": "Cloudflare. (n.d.). What is a SYN flood attack?. Retrieved April 22, 2019.", "source_name": "Cloudflare SynFlood"}, {"url": "https://www.corero.com/resources/ddos-attack-types/syn-flood-ack.html", "description": "Corero. (n.d.). What is a SYN-ACK Flood Attack?. Retrieved April 22, 2019.", "source_name": "Corero SYN-ACKflood"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5010}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\"", "Str": "Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.", "Type": 3, "Index": 4225}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4932}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4840}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 4059}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4901}}', 'T1499.001', 'Adversaries may target the operating system (OS) for a DoS attack, since the (OS) is responsible for managing the finite resources on a system. These attacks do not need to exhaust the actual resources on a system since they can simply exhaust the limits that an OS self-imposes to prevent the entire system from being overwhelmed by excessive demands on its capacity.
INSERT INTO public.attack_patterns VALUES (337, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.747938+00', NULL, 'attack-pattern--6e3bd510-6b33-41a4-af80-2d80f3ee0071', '[{"url": "https://attack.mitre.org/techniques/T1218/008", "external_id": "T1218.008", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/sql/odbc/odbcconf-exe?view=sql-server-2017", "description": "Microsoft. (2017, January 18). ODBCCONF.EXE. Retrieved March 7, 2019.", "source_name": "Microsoft odbcconf.exe"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Odbcconf/", "description": "LOLBAS. (n.d.). Odbcconf.exe. Retrieved March 7, 2019.", "source_name": "LOLBAS Odbcconf"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/backdoor-carrying-emails-set-sights-on-russian-speaking-businesses/", "description": "Bermejo, L., Giagone, R., Wu, R., and Yarochkin, F. (2017, August 7). Backdoor-carrying Emails Set Sights on Russian-speaking Businesses. Retrieved March 7, 2019.", "source_name": "TrendMicro Squiblydoo Aug 2017"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/cobalt-spam-runs-use-macros-cve-2017-8759-exploit/", "description": "Giagone, R., Bermejo, L., and Yarochkin, F. (2017, November 20). Cobalt Strikes Again: Spam Runs Use Macros and CVE-2017-8759 Exploit Against Russian Banks. Retrieved March 7, 2019.", "source_name": "TrendMicro Cobalt Group Nov 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3701}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of odbcconf.exe. Compare recent invocations of odbcconf.exe with prior history of known good arguments and loaded DLLs to determine anomalous and potentially adversarial activity. Command arguments used before and after the invocation of odbcconf.exe may also be useful in determining the origin and purpose of the DLL being loaded.\"", "Str": "Use process monitoring to monitor the execution and arguments of odbcconf.exe. Compare recent invocations of odbcconf.exe with prior history of known good arguments and loaded DLLs to determine anomalous and potentially adversarial activity. Command arguments used before and after the invocation of odbcconf.exe may also be useful in determining the origin and purpose of the DLL being loaded.", "Type": 3, "Index": 3135}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3653}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2893}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3564}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3033}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3606}}', 'T1218.008', 'Adversaries may abuse odbcconf.exe to proxy execution of malicious payloads. Odbcconf.exe is a Windows utility that allows you to configure Open Database Connectivity (ODBC) drivers and data source names.(Citation: Microsoft odbcconf.exe) Odbcconf.exe is digitally signed by Microsoft.
INSERT INTO public.attack_patterns VALUES (339, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.816062+00', NULL, 'attack-pattern--79a47ad0-fc3b-4821-9f01-a026b1ddba21', '[{"url": "https://attack.mitre.org/techniques/T1137/001", "external_id": "T1137.001", "source_name": "mitre-attack"}, {"url": "https://support.office.com/article/Change-the-Normal-template-Normal-dotm-06de294b-d216-47f6-ab77-ccb5166f98ea", "description": "Microsoft. (n.d.). Change the Normal template (Normal.dotm). Retrieved July 3, 2017.", "source_name": "Microsoft Change Normal Template"}, {"url": "https://msdn.microsoft.com/en-us/vba/office-shared-vba/articles/getting-started-with-vba-in-office", "description": "Austin, J. (2017, June 6). Getting Started with VBA in Office. Retrieved July 3, 2017.", "source_name": "MSDN VBA in Office"}, {"url": "https://enigma0x3.net/2014/01/23/maintaining-access-with-normal-dotm/comment-page-1/", "description": "Nelson, M. (2014, January 23). Maintaining Access with normal.dotm. Retrieved July 3, 2017.", "source_name": "enigma0x3 normal.dotm"}, {"url": "http://www.hexacorn.com/blog/2017/04/19/beyond-good-ol-run-key-part-62/", "description": "Hexacorn. (2017, April 17). Beyond good ol’ Run key, Part 62. Retrieved July 3, 2017.", "source_name": "Hexacorn Office Template Macros"}, {"url": "https://www.221bluestreet.com/post/office-templates-and-globaldotname-a-stealthy-office-persistence-technique", "description": "Shukrun, S. (2019, June 2). Office Templates and GlobalDotName - A Stealthy Office Persistence Technique. Retrieved August 26, 2019.", "source_name": "GlobalDotName Jun 2019"}, {"url": "https://malware.news/t/using-outlook-forms-for-lateral-movement-and-persistence/13746", "description": "Parisi, T., et al. (2017, July). Using Outlook Forms for Lateral Movement and Persistence. Retrieved February 5, 2019.", "source_name": "CrowdStrike Outlook Forms"}, {"url": "https://medium.com/@bwtech789/outlook-today-homepage-persistence-33ea9b505943", "description": "Soutcast. (2018, September 14). Outlook Today Homepage Persistence. Retrieved February 5, 2019.", "source_name": "Outlook Today Home Page"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6198}, "x_mitre_detection": {"Num": 0, "Raw": "\"Many Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence.(Citation: CrowdStrike Outlook Forms)(Citation: Outlook Today Home Page) Modification to base templates, like Normal.dotm, should also be investigated since the base templates should likely not contain VBA macros. Changes to the Office macro security settings should also be investigated.(Citation: GlobalDotName Jun 2019)\"", "Str": "Many Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence.(Citation: CrowdStrike Outlook Forms)(Citation: Outlook Today Home Page) Modification to base templates, like Normal.dotm, should also be investigated since the base templates should likely not contain VBA macros. Changes to the Office macro security settings should also be investigated.(Citation: GlobalDotName Jun 2019)", "Type": 3, "Index": 5349}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 6130}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 5065}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6018}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6060}}', 'T1137.001', 'Adversaries may abuse Microsoft Office templates to obtain persistence on a compromised system. Microsoft Office contains templates that are part of common Office applications and are used to customize styles. The base templates within the application are used each time an application starts. (Citation: Microsoft Change Normal Template)
INSERT INTO public.attack_patterns VALUES (340, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.847918+00', NULL, 'attack-pattern--ed7efd4d-ce28-4a19-a8e6-c58011eb2c7a', '[{"url": "https://attack.mitre.org/techniques/T1137/002", "external_id": "T1137.002", "source_name": "mitre-attack"}, {"url": "http://www.hexacorn.com/blog/2014/04/16/beyond-good-ol-run-key-part-10/", "description": "Hexacorn. (2014, April 16). Beyond good ol’ Run key, Part 10. Retrieved July 3, 2017.", "source_name": "Hexacorn Office Test"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/07/unit42-technical-walkthrough-office-test-persistence-method-used-in-recent-sofacy-attacks/", "description": "Falcone, R. (2016, July 20). Technical Walkthrough: Office Test Persistence Method Used In Recent Sofacy Attacks. Retrieved July 3, 2017.", "source_name": "Palo Alto Office Test Sofacy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3626}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for the creation of the Office Test Registry key. Many Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence. Since v13.52, Autoruns can detect tasks set up using the Office Test Registry key.(Citation: Palo Alto Office Test Sofacy)\\n\\nConsider monitoring Office processes for anomalous DLL loads.\"", "Str": "Monitor for the creation of the Office Test Registry key. Many Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence. Since v13.52, Autoruns can detect tasks set up using the Office Test Registry key.(Citation: Palo Alto Office Test Sofacy)\n\nConsider monitoring Office processes for anomalous DLL loads.", "Type": 3, "Index": 2767}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3472}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2454}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3360}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Office 2007, 2010, 2013, and 2016\"\n    ]", "Str": "", "Type": 5, "Index": 3552}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3402}}', 'T1137.002', 'Adversaries may abuse the Microsoft Office "Office Test" Registry key to obtain persistence on a compromised system. An Office Test Registry location exists that allows a user to specify an arbitrary DLL that will be executed every time an Office application is started. This Registry key is thought to be used by Microsoft to load DLLs for testing and debugging purposes while developing Office applications. This Registry key is not created by default during an Office installation.(Citation: Hexacorn Office Test)(Citation: Palo Alto Office Test Sofacy)
INSERT INTO public.attack_patterns VALUES (342, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.913645+00', NULL, 'attack-pattern--a9e2cea0-c805-4bf8-9e31-f5f0513a3634', '[{"url": "https://attack.mitre.org/techniques/T1137/003", "external_id": "T1137.003", "source_name": "mitre-attack"}, {"url": "https://sensepost.com/blog/2017/outlook-forms-and-shells/", "description": "Stalmans, E. (2017, April 28). Outlook Forms and Shells. Retrieved February 4, 2019.", "source_name": "SensePost Outlook Forms"}, {"url": "https://docs.microsoft.com/en-us/office365/securitycompliance/detect-and-remediate-outlook-rules-forms-attack", "description": "Fox, C., Vangel, D. (2018, April 22). Detect and Remediate Outlook Rules and Custom Forms Injections Attacks in Office 365. Retrieved February 4, 2019.", "source_name": "Microsoft Detect Outlook Forms"}, {"url": "https://github.com/sensepost/notruler", "description": "SensePost. (2017, September 21). NotRuler - The opposite of Ruler, provides blue teams with the ability to detect Ruler usage against Exchange. Retrieved February 4, 2019.", "source_name": "SensePost NotRuler"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3451}, "x_mitre_detection": {"Num": 0, "Raw": "\"Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\\n\\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.\"", "Str": "Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\n\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.", "Type": 3, "Index": 2551}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3383}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2397}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3271}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3313}}', 'T1137.003', 'Adversaries may abuse Microsoft Outlook forms to obtain persistence on a compromised system. Outlook forms are used as templates for presentation and functionality in Outlook messages. Custom Outlook forms can be created that will execute code when a specifically crafted email is sent by an adversary utilizing the same custom Outlook form.(Citation: SensePost Outlook Forms)
INSERT INTO public.attack_patterns VALUES (343, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.944944+00', NULL, 'attack-pattern--bf147104-abf9-4221-95d1-e81585859441', '[{"url": "https://attack.mitre.org/techniques/T1137/004", "external_id": "T1137.004", "source_name": "mitre-attack"}, {"url": "https://sensepost.com/blog/2017/outlook-home-page-another-ruler-vector/", "description": "Stalmans, E. (2017, October 11). Outlook Home Page – Another Ruler Vector. Retrieved February 4, 2019.", "source_name": "SensePost Outlook Home Page"}, {"url": "https://docs.microsoft.com/en-us/office365/securitycompliance/detect-and-remediate-outlook-rules-forms-attack", "description": "Fox, C., Vangel, D. (2018, April 22). Detect and Remediate Outlook Rules and Custom Forms Injections Attacks in Office 365. Retrieved February 4, 2019.", "source_name": "Microsoft Detect Outlook Forms"}, {"url": "https://github.com/sensepost/notruler", "description": "SensePost. (2017, September 21). NotRuler - The opposite of Ruler, provides blue teams with the ability to detect Ruler usage against Exchange. Retrieved February 4, 2019.", "source_name": "SensePost NotRuler"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3557}, "x_mitre_detection": {"Num": 0, "Raw": "\"Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\\n\\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.\"", "Str": "Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\n\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.", "Type": 3, "Index": 2657}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3489}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2503}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3377}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3419}}', 'T1137.004', 'Adversaries may abuse Microsoft Outlook''s Home Page feature to obtain persistence on a compromised system. Outlook Home Page is a legacy feature used to customize the presentation of Outlook folders. This feature allows for an internal or external URL to be loaded and presented whenever a folder is opened. A malicious HTML page can be crafted that will execute code when loaded by Outlook Home Page.(Citation: SensePost Outlook Home Page)
INSERT INTO public.attack_patterns VALUES (344, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.978235+00', NULL, 'attack-pattern--3d1b9d7e-3921-4d25-845a-7d9f15c0da44', '[{"url": "https://attack.mitre.org/techniques/T1137/005", "external_id": "T1137.005", "source_name": "mitre-attack"}, {"url": "https://silentbreaksecurity.com/malicious-outlook-rules/", "description": "Landers, N. (2015, December 4). Malicious Outlook Rules. Retrieved February 4, 2019.", "source_name": "SilentBreak Outlook Rules"}, {"url": "https://docs.microsoft.com/en-us/office365/securitycompliance/detect-and-remediate-outlook-rules-forms-attack", "description": "Fox, C., Vangel, D. (2018, April 22). Detect and Remediate Outlook Rules and Custom Forms Injections Attacks in Office 365. Retrieved February 4, 2019.", "source_name": "Microsoft Detect Outlook Forms"}, {"url": "https://github.com/sensepost/notruler", "description": "SensePost. (2017, September 21). NotRuler - The opposite of Ruler, provides blue teams with the ability to detect Ruler usage against Exchange. Retrieved February 4, 2019.", "source_name": "SensePost NotRuler"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3577}, "x_mitre_detection": {"Num": 0, "Raw": "\"Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\\n\\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.\"", "Str": "Microsoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\n\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior.", "Type": 3, "Index": 2677}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3509}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2523}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3397}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3439}}', 'T1137.005', 'Adversaries may abuse Microsoft Outlook rules to obtain persistence on a compromised system. Outlook rules allow a user to define automated behavior to manage email messages. A benign rule might, for example, automatically move an email to a particular folder in Outlook if it contains specific words from a specific sender. Malicious Outlook rules can be created that can trigger code execution when an adversary sends a specifically crafted email to that user.(Citation: SilentBreak Outlook Rules)
INSERT INTO public.attack_patterns VALUES (345, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.012842+00', NULL, 'attack-pattern--93591901-3172-4e94-abf8-6034ab26f44a', '[{"url": "https://attack.mitre.org/techniques/T1134/004", "external_id": "T1134.004", "source_name": "mitre-attack"}, {"url": "https://blog.didierstevens.com/2009/11/22/quickpost-selectmyparent-or-playing-with-the-windows-process-tree/", "description": "Stevens, D. (2009, November 22). Quickpost: SelectMyParent or Playing With the Windows Process Tree. Retrieved June 3, 2019.", "source_name": "DidierStevens SelectMyParent Nov 2009"}, {"url": "https://docs.microsoft.com/windows/security/identity-protection/user-account-control/how-user-account-control-works", "description": "Montemayor, D. et al.. (2018, November 15). How User Account Control works. Retrieved June 3, 2019.", "source_name": "Microsoft UAC Nov 2018"}, {"url": "https://www.countercept.com/blog/detecting-parent-pid-spoofing/", "description": "Loh, I. (2018, December 21). Detecting Parent PID Spoofing. Retrieved June 3, 2019.", "source_name": "CounterCept PPID Spoofing Dec 2018"}, {"url": "https://blog.christophetd.fr/building-an-office-macro-to-spoof-process-parent-and-command-line/", "description": "Tafani-Dereeper, C. (2019, March 12). Building an Office macro to spoof parent processes and command line arguments. Retrieved June 3, 2019.", "source_name": "CTD PPID Spoofing Macro Mar 2019"}, {"url": "https://blog.xpnsec.com/becoming-system/", "description": "Chester, A. (2017, November 20). Alternative methods of becoming SYSTEM. Retrieved June 4, 2019.", "source_name": "XPNSec PPID Nov 2017"}, {"url": "https://docs.microsoft.com/windows/desktop/ProcThread/process-creation-flags", "description": "Schofield, M. & Satran, M. (2018, May 30). Process Creation Flags. Retrieved June 4, 2019.", "source_name": "Microsoft Process Creation Flags May 2018"}, {"url": "https://www.securityinbits.com/malware-analysis/parent-pid-spoofing-stage-2-ataware-ransomware-part-3", "description": "Secuirtyinbits . (2019, May 14). Parent PID Spoofing (Stage 2) Ataware Ransomware Part 3. Retrieved June 6, 2019.", "source_name": "Secuirtyinbits Ataware3 May 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6993}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for inconsistencies between the various fields that store PPID information, such as the EventHeader ProcessId from data collected via Event Tracing for Windows (ETW), Creator Process ID/Name from Windows event logs, and the ProcessID and ParentProcessID (which are also produced from ETW and other utilities such as Task Manager and Process Explorer). The ETW provided EventHeader ProcessId identifies the actual parent process.(Citation: CounterCept PPID Spoofing Dec 2018)\\n\\nMonitor and analyze API calls to <code>CreateProcess</code>/<code>CreateProcessA</code>, specifically those from user/potentially malicious processes and with parameters explicitly assigning PPIDs (ex: the Process Creation Flags of 0x8XXX, indicating that the process is being created with extended startup information(Citation: Microsoft Process Creation Flags May 2018)). Malicious use of <code>CreateProcess</code>/<code>CreateProcessA</code> may also be proceeded by a call to <code>UpdateProcThreadAttribute</code>, which may be necessary to update process creation attributes.(Citation: Secuirtyinbits Ataware3 May 2019) This may generate false positives from normal UAC elevation behavior, so compare to a system baseline/understanding of normal system activity if possible.\"", "Str": "Look for inconsistencies between the various fields that store PPID information, such as the EventHeader ProcessId from data collected via Event Tracing for Windows (ETW), Creator Process ID/Name from Windows event logs, and the ProcessID and ParentProcessID (which are also produced from ETW and other utilities such as Task Manager and Process Explorer). The ETW provided EventHeader ProcessId identifies the actual parent process.(Citation: CounterCept PPID Spoofing Dec 2018)\n\nMonitor and analyze API calls to <code>CreateProcess</code>/<code>CreateProcessA</code>, specifically those from user/potentially malicious processes and with parameters explicitly assigning PPIDs (ex: the Process Creation Flags of 0x8XXX, indicating that the process is being created with extended startup information(Citation: Microsoft Process Creation Flags May 2018)). Malicious use of <code>CreateProcess</code>/<code>CreateProcessA</code> may also be proceeded by a call to <code>UpdateProcThreadAttribute</code>, which may be necessary to update process creation attributes.(Citation: Secuirtyinbits Ataware3 May 2019) This may generate false positives from normal UAC elevation behavior, so compare to a system baseline/understanding of normal system activity if possible.", "Type": 3, "Index": 5534}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6945}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Wayne Silva, F-Secure Countercept\"\n    ]", "Str": "", "Type": 5, "Index": 5216}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Creation\",\n      \"Process: Process Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 5295}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6833}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Heuristic Detection\",\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 5440}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 6875}}', 'T1134.004', 'Adversaries may spoof the parent process identifier (PPID) of a new process to evade process-monitoring defenses or to elevate privileges. New processes are typically spawned directly from their parent, or calling, process unless explicitly specified. One way of explicitly assigning the PPID of a new process is via the <code>CreateProcess</code> API call, which supports a parameter that defines the PPID to use.(Citation: DidierStevens SelectMyParent Nov 2009) This functionality is used by Windows features such as User Account Control (UAC) to correctly set the PPID after a requested elevated process is spawned by SYSTEM (typically via <code>svchost.exe</code> or <code>consent.exe</code>) rather than the current user context.(Citation: Microsoft UAC Nov 2018)
INSERT INTO public.attack_patterns VALUES (375, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.047786+00', NULL, 'attack-pattern--7007935a-a8a7-4c0b-bd98-4e85be8ed197', '[{"url": "https://attack.mitre.org/techniques/T1055/013", "external_id": "T1055.013", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/bb968806.aspx", "description": "Microsoft. (n.d.). Transactional NTFS (TxF). Retrieved December 20, 2017.", "source_name": "Microsoft TxF"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/dd979526.aspx", "description": "Microsoft. (n.d.). Basic TxF Concepts. Retrieved December 20, 2017.", "source_name": "Microsoft Basic TxF Concepts"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/aa365738.aspx", "description": "Microsoft. (n.d.). When to Use Transactional NTFS. Retrieved December 20, 2017.", "source_name": "Microsoft Where to use TxF"}, {"url": "https://www.blackhat.com/docs/eu-17/materials/eu-17-Liberman-Lost-In-Transaction-Process-Doppelganging.pdf", "description": "Liberman, T. & Kogan, E. (2017, December 7). Lost in Transaction: Process Doppelgänging. Retrieved December 20, 2017.", "source_name": "BlackHat Process Doppelgänging Dec 2017"}, {"url": "https://hshrzd.wordpress.com/2017/12/18/process-doppelganging-a-new-way-to-impersonate-a-process/", "description": "hasherezade. (2017, December 18). Process Doppelgänging – a new way to impersonate a process. Retrieved December 20, 2017.", "source_name": "hasherezade Process Doppelgänging Dec 2017"}, {"url": "https://msdn.microsoft.com/library/windows/hardware/ff559951.aspx", "description": "Microsoft. (n.d.). PsSetCreateProcessNotifyRoutine routine. Retrieved December 20, 2017.", "source_name": "Microsoft PsSetCreateProcessNotifyRoutine routine"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6995}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and analyze calls to <code>CreateTransaction</code>, <code>CreateFileTransacted</code>, <code>RollbackTransaction</code>, and other rarely used functions indicative of TxF activity. Process Doppelgänging also invokes an outdated and undocumented implementation of the Windows process loader via calls to <code>NtCreateProcessEx</code> and <code>NtCreateThreadEx</code> as well as API calls used to modify memory within another process, such as <code>WriteProcessMemory</code>. (Citation: BlackHat Process Doppelgänging Dec 2017) (Citation: hasherezade Process Doppelgänging Dec 2017)\\n\\nScan file objects reported during the PsSetCreateProcessNotifyRoutine, (Citation: Microsoft PsSetCreateProcessNotifyRoutine routine) which triggers a callback whenever a process is created or deleted, specifically looking for file objects with enabled write access. (Citation: BlackHat Process Doppelgänging Dec 2017) Also consider comparing file objects loaded in memory to the corresponding file on disk. (Citation: hasherezade Process Doppelgänging Dec 2017)\\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior.\"", "Str": "Monitor and analyze calls to <code>CreateTransaction</code>, <code>CreateFileTransacted</code>, <code>RollbackTransaction</code>, and other rarely used functions indicative of TxF activity. Process Doppelgänging also invokes an outdated and undocumented implementation of the Windows process loader via calls to <code>NtCreateProcessEx</code> and <code>NtCreateThreadEx</code> as well as API calls used to modify memory within another process, such as <code>WriteProcessMemory</code>. (Citation: BlackHat Process Doppelgänging Dec 2017) (Citation: hasherezade Process Doppelgänging Dec 2017)\n\nScan file objects reported during the PsSetCreateProcessNotifyRoutine, (Citation: Microsoft PsSetCreateProcessNotifyRoutine routine) which triggers a callback whenever a process is created or deleted, specifically looking for file objects with enabled write access. (Citation: BlackHat Process Doppelgänging Dec 2017) Also consider comparing file objects loaded in memory to the corresponding file on disk. (Citation: hasherezade Process Doppelgänging Dec 2017)\n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior.", "Type": 3, "Index": 5499}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6947}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 5313}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6819}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 5417}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6861}}', 'T1055.013', 'Adversaries may inject malicious code into process via process doppelgänging in order to evade process-based defenses as well as possibly elevate privileges. Process doppelgänging is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (347, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.137729+00', NULL, 'attack-pattern--7b211ac6-c815-4189-93a9-ab415deca926', '[{"url": "https://attack.mitre.org/techniques/T1550/003", "external_id": "T1550.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/645.html", "external_id": "CAPEC-645", "source_name": "capec"}, {"url": "https://adsecurity.org/?p=556", "description": "Metcalf, S. (2014, November 22). Mimikatz and Active Directory Kerberos Attacks. Retrieved June 2, 2016.", "source_name": "ADSecurity AD Kerberos Attacks"}, {"url": "http://blog.gentilkiwi.com/securite/mimikatz/pass-the-ticket-kerberos", "description": "Deply, B. (2014, January 13). Pass the ticket. Retrieved June 2, 2016.", "source_name": "GentilKiwi Pass the Ticket"}, {"url": "http://defcon.org/images/defcon-22/dc-22-presentations/Campbell/DEFCON-22-Christopher-Campbell-The-Secret-Life-of-Krbtgt.pdf", "description": "Campbell, C. (2014). The Secret Life of Krbtgt. Retrieved December 4, 2014.", "source_name": "Campbell 2014"}, {"url": "https://stealthbits.com/blog/how-to-detect-overpass-the-hash-attacks/", "description": "Warren, J. (2019, February 26). How to Detect Overpass-the-Hash Attacks. Retrieved February 4, 2021.", "source_name": "Stealthbits Overpass-the-Hash"}, {"url": "https://cert.europa.eu/static/WhitePapers/UPDATED%20-%20CERT-EU_Security_Whitepaper_2014-007_Kerberos_Golden_Ticket_Protection_v1_4.pdf", "description": "Abolins, D., Boldea, C., Socha, K., Soria-Machado, M. (2016, April 26). Kerberos Golden Ticket Protection. Retrieved July 13, 2017.", "source_name": "CERT-EU Golden Ticket Protection"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5726}, "x_mitre_detection": {"Num": 0, "Raw": "\"Audit all Kerberos authentication and credential use events and review for discrepancies. Unusual remote authentication events that correlate with other suspicious activity (such as writing and executing binaries) may indicate malicious activity.\\n\\nEvent ID 4769 is generated on the Domain Controller when using a golden ticket after the KRBTGT password has been reset twice, as mentioned in the mitigation section. The status code 0x1F indicates the action has failed due to \\\"Integrity check on decrypted field failed\\\" and indicates misuse by a previously invalidated golden ticket.(Citation: CERT-EU Golden Ticket Protection)\"", "Str": "Audit all Kerberos authentication and credential use events and review for discrepancies. Unusual remote authentication events that correlate with other suspicious activity (such as writing and executing binaries) may indicate malicious activity.\n\nEvent ID 4769 is generated on the Domain Controller when using a golden ticket after the KRBTGT password has been reset twice, as mentioned in the mitigation section. The status code 0x1F indicates the action has failed due to \"Integrity check on decrypted field failed\" and indicates misuse by a previously invalidated golden ticket.(Citation: CERT-EU Golden Ticket Protection)", "Type": 3, "Index": 4898}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5594}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vincent Le Toux\",\n      \"Ryan Becwar\"\n    ]", "Str": "", "Type": 5, "Index": 4550}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\",\n      \"Active Directory: Active Directory Credential Request\"\n    ]", "Str": "", "Type": 5, "Index": 4632}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5563}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"System Access Controls\"\n    ]", "Str": "", "Type": 5, "Index": 4833}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Kerberos authentication enabled\"\n    ]", "Str": "", "Type": 5, "Index": 5654}}', 'T1550.003', 'Adversaries may “pass the ticket” using stolen Kerberos tickets to move laterally within an environment, bypassing normal system access controls. Pass the ticket (PtT) is a method of authenticating to a system using Kerberos tickets without having access to an account''s password. Kerberos authentication can be used as the first step to lateral movement to a remote system.
INSERT INTO public.attack_patterns VALUES (348, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.175467+00', NULL, 'attack-pattern--1d24cdee-9ea2-4189-b08e-af110bf2435d', '[{"url": "https://attack.mitre.org/techniques/T1110/002", "external_id": "T1110.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/55.html", "external_id": "CAPEC-55", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Password_cracking", "description": "Wikipedia. (n.d.). Password cracking. Retrieved December 23, 2015.", "source_name": "Wikipedia Password cracking"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2816}, "x_mitre_detection": {"Num": 0, "Raw": "\"It is difficult to detect when hashes are cracked, since this is generally done outside the scope of the target network. Consider focusing efforts on detecting other adversary behavior used to acquire credential materials, such as [OS Credential Dumping](https://attack.mitre.org/techniques/T1003) or [Kerberoasting](https://attack.mitre.org/techniques/T1558/003).\"", "Str": "It is difficult to detect when hashes are cracked, since this is generally done outside the scope of the target network. Consider focusing efforts on detecting other adversary behavior used to acquire credential materials, such as [OS Credential Dumping](https://attack.mitre.org/techniques/T1003) or [Kerberoasting](https://attack.mitre.org/techniques/T1558/003).", "Type": 3, "Index": 2212}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Office 365\",\n      \"Azure AD\"\n    ]", "Str": "", "Type": 5, "Index": 2700}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2078}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2611}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2653}}', 'T1110.002', 'Adversaries may use password cracking to attempt to recover usable credentials, such as plaintext passwords, when credential material such as password hashes are obtained. [OS Credential Dumping](https://attack.mitre.org/techniques/T1003) is used to obtain password hashes, this may only get an adversary so far when [Pass the Hash](https://attack.mitre.org/techniques/T1550/002) is not an option. Techniques to systematically guess the passwords used to compute hashes are available, or the adversary may use a pre-computed rainbow table to crack hashes. Cracking hashes is usually done on adversary-controlled systems outside of the target network.(Citation: Wikipedia Password cracking) The resulting plaintext password resulting from a successfully cracked hash may be used to log into systems, resources, and services in which the account has access.', 'Password Cracking', 48);
INSERT INTO public.attack_patterns VALUES (349, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.208479+00', NULL, 'attack-pattern--3731fbcd-0e43-47ae-ae6c-d15e510f0d42', '[{"url": "https://attack.mitre.org/techniques/T1556/002", "external_id": "T1556.002", "source_name": "mitre-attack"}, {"url": "http://carnal0wnage.attackresearch.com/2013/09/stealing-passwords-every-time-they.html", "description": "Fuller, R. (2013, September 11). Stealing passwords every time they change. Retrieved November 21, 2017.", "source_name": "Carnal Ownage Password Filters Sept 2013"}, {"url": "https://clymb3r.wordpress.com/2013/09/15/intercepting-password-changes-with-function-hooking/", "description": "Bialek, J. (2013, September 15). Intercepting Password Changes With Function Hooking. Retrieved November 21, 2017.", "source_name": "Clymb3r Function Hook Passwords Sept 2013"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3719}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for new, unfamiliar DLL files written to a domain controller and/or local computer. Monitor for changes to Registry entries for password filters (ex: <code>HKEY_LOCAL_MACHINE\\\\SYSTEM\\\\CurrentControlSet\\\\Control\\\\Lsa\\\\Notification Packages</code>) and correlate then investigate the DLL files these files reference.\\n\\nPassword filters will also show up as an autorun and loaded DLL in lsass.exe.(Citation: Clymb3r Function Hook Passwords Sept 2013)\"", "Str": "Monitor for new, unfamiliar DLL files written to a domain controller and/or local computer. Monitor for changes to Registry entries for password filters (ex: <code>HKEY_LOCAL_MACHINE\\SYSTEM\\CurrentControlSet\\Control\\Lsa\\Notification Packages</code>) and correlate then investigate the DLL files these files reference.\n\nPassword filters will also show up as an autorun and loaded DLL in lsass.exe.(Citation: Clymb3r Function Hook Passwords Sept 2013)", "Type": 3, "Index": 3066}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3671}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vincent Le Toux\"\n    ]", "Str": "", "Type": 5, "Index": 2853}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Module: Module Load\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2914}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3557}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3599}}', 'T1556.002', 'Adversaries may register malicious password filter dynamic link libraries (DLLs) into the authentication process to acquire user credentials as they are validated. 
INSERT INTO public.attack_patterns VALUES (350, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.24598+00', NULL, 'attack-pattern--09c4c11e-4fa1-4f8c-8dad-3cf8e69ad119', '[{"url": "https://attack.mitre.org/techniques/T1110/001", "external_id": "T1110.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/49.html", "external_id": "CAPEC-49", "source_name": "capec"}, {"url": "https://web.archive.org/web/20200302085133/https://www.cylance.com/content/dam/cylance/pages/operation-cleaver/Cylance_Operation_Cleaver_Report.pdf", "description": "Cylance. (2014, December). Operation Cleaver. Retrieved September 14, 2017.", "source_name": "Cylance Cleaver"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-086A", "description": "US-CERT. (2018, March 27). TA18-068A Brute Force Attacks Conducted by Cyber Actors. Retrieved October 2, 2019.", "source_name": "US-CERT TA18-068A 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4103}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials.\"", "Str": "Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials.", "Type": 3, "Index": 3522}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3913}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 3298}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3388}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3824}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3866}}', 'T1110.001', 'Adversaries with no prior knowledge of legitimate credentials within the system or environment may guess passwords to attempt access to accounts. Without knowledge of the password for an account, an adversary may opt to systematically guess the password using a repetitive or iterative mechanism. An adversary may guess login credentials without prior knowledge of system or environment passwords during an operation by using a list of common passwords. Password guessing may or may not take into account the target''s policies on password complexity or use policies that may lock accounts out after a number of failed attempts.
INSERT INTO public.attack_patterns VALUES (352, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.317392+00', NULL, 'attack-pattern--b6075259-dba3-44e9-87c7-e954f37ec0d5', '[{"url": "https://attack.mitre.org/techniques/T1201", "external_id": "T1201", "source_name": "mitre-attack"}, {"url": "https://superuser.com/questions/150675/how-to-display-password-policy-information-for-a-user-ubuntu", "description": "Matutiae, M. (2014, August 6). How to display password policy information for a user (Ubuntu)?. Retrieved April 5, 2018.", "source_name": "Superuser Linux Password Policies"}, {"url": "https://www.jamf.com/jamf-nation/discussions/18574/user-password-policies-on-non-ad-machines", "description": "Holland, J. (2016, January 25). User password policies on non AD machines. Retrieved April 5, 2018.", "source_name": "Jamf User Password Policies"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3358}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for tools and command line arguments that may indicate they''re being used for password policy discovery. Correlate that activity with other suspicious activity from the originating system to reduce potential false positives from valid user or administrator activity. Adversaries will likely attempt to find the password policy early in an operation and the activity is likely to happen with other Discovery activity.\"", "Str": "Monitor processes for tools and command line arguments that may indicate they''re being used for password policy discovery. Correlate that activity with other suspicious activity from the originating system to reduce potential false positives from valid user or administrator activity. Adversaries will likely attempt to find the password policy early in an operation and the activity is likely to happen with other Discovery activity.", "Type": 3, "Index": 2721}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3280}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Sudhanshu Chauhan, @Sudhanshu_C\"\n    ]", "Str": "", "Type": 5, "Index": 2540}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2617}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3190}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3233}}', 'T1201', 'Adversaries may attempt to access detailed information about the password policy used within an enterprise network. Password policies for networks are a way to enforce complex passwords that are difficult to guess or crack through [Brute Force](https://attack.mitre.org/techniques/T1110). This would help the adversary to create a list of common passwords and launch dictionary and/or brute force attacks which adheres to the policy (e.g. if the minimum password length should be 8, then not trying passwords such as ''pass123''; not checking for more than 3-4 passwords per account if the lockout is set to 6 as to not lock out accounts).
INSERT INTO public.attack_patterns VALUES (353, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.361233+00', NULL, 'attack-pattern--692074ae-bb62-4a5e-a735-02cb6bde458c', '[{"url": "https://attack.mitre.org/techniques/T1110/003", "external_id": "T1110.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/565.html", "external_id": "CAPEC-565", "source_name": "capec"}, {"url": "http://www.blackhillsinfosec.com/?p=4645", "description": "Thyer, J. (2015, October 30). Password Spraying & Other Fun with RPCCLIENT. Retrieved April 25, 2017.", "source_name": "BlackHillsInfosec Password Spraying"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-086A", "description": "US-CERT. (2018, March 27). TA18-068A Brute Force Attacks Conducted by Cyber Actors. Retrieved October 2, 2019.", "source_name": "US-CERT TA18-068A 2018"}, {"url": "https://www.trimarcsecurity.com/single-post/2018/05/06/Trimarc-Research-Detecting-Password-Spraying-with-Security-Event-Auditing", "description": "Metcalf, S. (2018, May 6). Trimarc Research: Detecting Password Spraying with Security Event Auditing. Retrieved January 16, 2019.", "source_name": "Trimarc Detecting Password Spraying"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4498}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). Specifically, monitor for many failed authentication attempts across various accounts that may result from password spraying attempts.\\n\\nConsider the following event IDs:(Citation: Trimarc Detecting Password Spraying)\\n\\n* Domain Controllers: \\\"Audit Logon\\\" (Success & Failure) for event ID 4625.\\n* Domain Controllers: \\\"Audit Kerberos Authentication Service\\\" (Success & Failure) for event ID 4771.\\n* All systems: \\\"Audit Logon\\\" (Success & Failure) for event ID 4648.\"", "Str": "Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). Specifically, monitor for many failed authentication attempts across various accounts that may result from password spraying attempts.\n\nConsider the following event IDs:(Citation: Trimarc Detecting Password Spraying)\n\n* Domain Controllers: \"Audit Logon\" (Success & Failure) for event ID 4625.\n* Domain Controllers: \"Audit Kerberos Authentication Service\" (Success & Failure) for event ID 4771.\n* All systems: \"Audit Logon\" (Success & Failure) for event ID 4648.", "Type": 3, "Index": 3577}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4308}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\",\n      \"John Strand\"\n    ]", "Str": "", "Type": 5, "Index": 3332}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3443}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4219}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4261}}', 'T1110.003', 'Adversaries may use a single or small list of commonly used passwords against many different accounts to attempt to acquire valid account credentials. Password spraying uses one password (e.g. ''Password01''), or a small list of commonly used passwords, that may match the complexity policy of the domain. Logins are attempted with that password against many different accounts on a network to avoid account lockouts that would normally occur when brute forcing a single account with many passwords. (Citation: BlackHillsInfosec Password Spraying)
INSERT INTO public.attack_patterns VALUES (354, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.396519+00', NULL, 'attack-pattern--d245808a-7086-4310-984a-a84aaaa43f8f', '[{"url": "https://attack.mitre.org/techniques/T1601/001", "external_id": "T1601.001", "source_name": "mitre-attack"}, {"url": "https://drwho.virtadpt.net/images/killing_the_myth_of_cisco_ios_rootkits.pdf", "description": "Sebastian ''topo'' Muñiz. (2008, May). Killing the myth of Cisco IOS rootkits. Retrieved October 20, 2020.", "source_name": "Killing the myth of Cisco IOS rootkits"}, {"url": "https://www.usenix.org/legacy/event/woot/tech/final_files/Cui.pdf", "description": "Ang Cui, Jatin Kataria, Salvatore J. Stolfo. (2011, August). Killing the myth of Cisco IOS diversity: recent advances in reliable shellcode design. Retrieved October 20, 2020.", "source_name": "Killing IOS diversity myth"}, {"url": "http://2015.zeronights.org/assets/files/05-Nosenko.pdf", "description": "George Nosenko. (2015). CISCO IOS SHELLCODE: ALL-IN-ONE. Retrieved October 21, 2020.", "source_name": "Cisco IOS Shellcode"}, {"url": "https://www.recurity-labs.com/research/RecurityLabs_Developments_in_IOS_Forensics.pdf", "description": "Felix ''FX'' Lindner. (2008, February). Developments in Cisco IOS Forensics. Retrieved October 21, 2020.", "source_name": "Cisco IOS Forensics Developments"}, {"url": "https://www.blackhat.com/presentations/bh-usa-09/NEILSON/BHUSA09-Neilson-NetscreenDead-SLIDES.pdf", "description": "Graeme Neilson . (2009, August). Juniper Netscreen of the Dead. Retrieved October 20, 2020.", "source_name": "Juniper Netscreen of the Dead"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#7", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Image File Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Image File Verification"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#13", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Run-Time Memory Integrity Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Run-Time Memory Verification"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 8269}, "x_mitre_detection": {"Num": 0, "Raw": "\"Compare the checksum of the operating system file with the checksum of a known good copy from a trusted source.  Some embedded network device platforms may have the capability to calculate the checksum of the file, while others may not.  Even for those platforms that have the capability, it is recommended to download a copy of the file to a trusted computer to calculate the checksum with software that is not compromised.(Citation: Cisco IOS Software Integrity Assurance - Image File Verification)\\n\\nMany vendors of embedded network devices can provide advanced debugging support that will allow them to work with device owners to validate the integrity of the operating system running in memory.  If a compromise of the operating system is suspected, contact the vendor technical support and seek such services for a more thorough inspection of the current running system.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\"", "Str": "Compare the checksum of the operating system file with the checksum of a known good copy from a trusted source.  Some embedded network device platforms may have the capability to calculate the checksum of the file, while others may not.  Even for those platforms that have the capability, it is recommended to download a copy of the file to a trusted computer to calculate the checksum with software that is not compromised.(Citation: Cisco IOS Software Integrity Assurance - Image File Verification)\n\nMany vendors of embedded network devices can provide advanced debugging support that will allow them to work with device owners to validate the integrity of the operating system running in memory.  If a compromise of the operating system is suspected, contact the vendor technical support and seek such services for a more thorough inspection of the current running system.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)", "Type": 3, "Index": 7128}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 8221}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 7062}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 8123}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 8165}}', 'T1601.001', 'Adversaries may modify the operating system of a network device to introduce new capabilities or weaken existing defenses.(Citation: Killing the myth of Cisco IOS rootkits) (Citation: Killing IOS diversity myth) (Citation: Cisco IOS Shellcode) (Citation: Cisco IOS Forensics Developments) (Citation: Juniper Netscreen of the Dead) Some network devices are built with a monolithic architecture, where the entire operating system and most of the functionality of the device is contained within a single file.  Adversaries may change this file in storage, to be loaded in a future boot, or in memory during runtime.
INSERT INTO public.attack_patterns VALUES (357, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.497359+00', NULL, 'attack-pattern--bf96a5a3-3bce-43b7-8597-88545984c07b', '[{"url": "https://attack.mitre.org/techniques/T1574/009", "external_id": "T1574.009", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/38.html", "external_id": "CAPEC-38", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/windows-hardware/drivers/install/hklm-system-currentcontrolset-services-registry-tree", "description": "Microsoft. (2017, April 20). HKLM\\SYSTEM\\CurrentControlSet\\Services Registry Tree. Retrieved March 16, 2020.", "source_name": "Microsoft CurrentControlSet Services"}, {"url": "https://isc.sans.edu/diary/Help+eliminate+unquoted+path+vulnerabilities/14464", "description": "Mark Baggett. (2012, November 8). Help eliminate unquoted path vulnerabilities. Retrieved November 8, 2012.", "source_name": "Help eliminate unquoted path"}, {"url": "https://securityboulevard.com/2018/04/windows-privilege-escalation-unquoted-services/", "description": "HackHappy. (2018, April 23). Windows Privilege Escalation – Unquoted Services. Retrieved August 10, 2018.", "source_name": "Windows Unquoted Services"}, {"url": "https://www.absolomb.com/2018-01-26-Windows-Privilege-Escalation-Guide/", "description": "absolomb. (2018, January 26). Windows Privilege Escalation Guide. Retrieved August 10, 2018.", "source_name": "Windows Privilege Escalation Guide"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5001}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \\\"findstr,\\\" \\\"net,\\\" and \\\"python\\\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\\n\\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \"findstr,\" \"net,\" and \"python\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\n\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 4023}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4953}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 3833}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3893}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4922}}', 'T1574.009', 'Adversaries may execute their own malicious payloads by hijacking vulnerable file path references. Adversaries can take advantage of paths that lack surrounding quotations by placing an executable in a higher level directory within the path, so that Windows will choose the adversary''s executable to launch.
INSERT INTO public.attack_patterns VALUES (358, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.526222+00', NULL, 'attack-pattern--348f1eef-964b-4eb6-bb53-69b3dcb0c643', '[{"url": "https://attack.mitre.org/techniques/T1120", "external_id": "T1120", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/646.html", "external_id": "CAPEC-646", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2514}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1555}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2451}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1416}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2322}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2365}}', 'T1120', 'Adversaries may attempt to gather information about attached peripheral devices and components connected to a computer system. Peripheral devices could include auxiliary resources that support a variety of functionalities such as keyboards, printers, cameras, smart card readers, or removable storage. The information may be used to enhance their awareness of the system and network environment or may be used for further actions.', 'Peripheral Device Discovery', 0);
INSERT INTO public.attack_patterns VALUES (359, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.560638+00', NULL, 'attack-pattern--15dbf668-795c-41e6-8219-f0447c0e64ce', '[{"url": "https://attack.mitre.org/techniques/T1069", "external_id": "T1069", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/576.html", "external_id": "CAPEC-576", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.3\"", "Str": "2.3", "Type": 3, "Index": 2605}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1551}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2435}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 1242}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Group: Group Enumeration\",\n      \"Group: Group Metadata\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1332}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2345}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2388}}', 'T1069', 'Adversaries may attempt to find group and permission settings. This information can help adversaries determine which user accounts and groups are available, the membership of users in particular groups, and which users and groups have elevated permissions.', 'Permission Groups Discovery', 0);
INSERT INTO public.attack_patterns VALUES (361, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.637444+00', NULL, 'attack-pattern--cca0ccb6-a068-4574-a722-b1556f86833a', '[{"url": "https://attack.mitre.org/techniques/T1598", "external_id": "T1598", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/facebook-launching-pad-phishing-attacks/160351/", "description": "O''Donnell, L. (2020, October 20). Facebook: A Top Launching Pad For Phishing Attacks. Retrieved October 20, 2020.", "source_name": "ThreatPost Social Media Phishing"}, {"url": "https://www.trendmicro.com/en_us/research/20/i/tricky-forms-of-phishing.html", "description": "Babon, P. (2020, September 3). Tricky ''Forms'' of Phishing. Retrieved October 20, 2020.", "source_name": "TrendMictro Phishing"}, {"url": "https://www.pcmag.com/news/hackers-try-to-phish-united-nations-staffers-with-fake-login-pages", "description": "Kan, M. (2019, October 24). Hackers Try to Phish United Nations Staffers With Fake Login Pages. Retrieved October 20, 2020.", "source_name": "PCMag FakeLogin"}, {"url": "https://nakedsecurity.sophos.com/2020/10/02/serious-security-phishing-without-links-when-phishers-bring-along-their-own-web-pages/", "description": "Ducklin, P. (2020, October 2). Serious Security: Phishing without links – when phishers bring along their own web pages. Retrieved October 20, 2020.", "source_name": "Sophos Attachment"}, {"url": "https://github.com/ryhanson/phishery", "description": "Ryan Hanson. (2016, September 24). phishery. Retrieved October 23, 2020.", "source_name": "GitHub Phishery"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5613}, "x_mitre_detection": {"Num": 0, "Raw": "\"Depending on the specific method of phishing, the detections can vary. Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\\n\\nWhen it comes to following links, monitor for references to uncategorized or known-bad sites. URL inspection within email (including expanding shortened links) can also help detect links leading to known malicious sites.\\n\\nMonitor social media traffic for suspicious activity, including messages requesting information as well as abnormal file or data transfers (especially those involving unknown, or otherwise suspicious accounts).\"", "Str": "Depending on the specific method of phishing, the detections can vary. Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\n\nWhen it comes to following links, monitor for references to uncategorized or known-bad sites. URL inspection within email (including expanding shortened links) can also help detect links leading to known malicious sites.\n\nMonitor social media traffic for suspicious activity, including messages requesting information as well as abnormal file or data transfers (especially those involving unknown, or otherwise suspicious accounts).", "Type": 3, "Index": 4709}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 5569}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\",\n      \"Sebastian Salla, McAfee\",\n      \"Robert Simmons, @MalwareUtkonos\"\n    ]", "Str": "", "Type": 5, "Index": 4395}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 4529}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5537}}', 'T1598', 'Adversaries may send phishing messages to elicit sensitive information that can be used during targeting. Phishing for information is an attempt to trick targets into divulging information, frequently credentials or other actionable information. Phishing for information is different from [Phishing](https://attack.mitre.org/techniques/T1566) in that the objective is gathering data from the victim rather than executing malicious code.
INSERT INTO public.attack_patterns VALUES (363, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.703049+00', NULL, 'attack-pattern--06c00069-771a-4d57-8ef5-d3718c1a8771', '[{"url": "https://attack.mitre.org/techniques/T1556/003", "external_id": "T1556.003", "source_name": "mitre-attack"}, {"url": "https://opensource.apple.com/source/dovecot/dovecot-239/dovecot/doc/wiki/PasswordDatabase.PAM.txt", "description": "Apple. (2011, May 11). PAM - Pluggable Authentication Modules. Retrieved June 25, 2020.", "source_name": "Apple PAM"}, {"url": "https://linux.die.net/man/8/pam_unix", "description": "die.net. (n.d.). pam_unix(8) - Linux man page. Retrieved June 25, 2020.", "source_name": "Man Pam_Unix"}, {"url": "https://access.redhat.com/documentation/en-us/red_hat_enterprise_linux/6/html/managing_smart_cards/pluggable_authentication_modules", "description": "Red Hat. (n.d.). CHAPTER 2. USING PLUGGABLE AUTHENTICATION MODULES (PAM). Retrieved June 25, 2020.", "source_name": "Red Hat PAM"}, {"url": "https://github.com/zephrax/linux-pam-backdoor", "description": "zephrax. (2018, August 3). linux-pam-backdoor. Retrieved June 25, 2020.", "source_name": "PAM Backdoor"}, {"url": "https://x-c3ll.github.io/posts/PAM-backdoor-DNS/", "description": "Fernández, J. M. (2018, June 27). Exfiltrating credentials via PAM backdoors & DNS requests. Retrieved June 26, 2020.", "source_name": "PAM Creds"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 4740}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor PAM configuration and module paths (ex: <code>/etc/pam.d/</code>) for changes. Use system-integrity tools such as AIDE and monitoring tools such as auditd to monitor PAM files.\\n\\nLook for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times (ex: when the user is not present) or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\"", "Str": "Monitor PAM configuration and module paths (ex: <code>/etc/pam.d/</code>) for changes. Use system-integrity tools such as AIDE and monitoring tools such as auditd to monitor PAM files.\n\nLook for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times (ex: when the user is not present) or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).", "Type": 3, "Index": 3708}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4679}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Scott Knight, @sdotknight, VMware Carbon Black\",\n      \"George Allen, VMware Carbon Black\"\n    ]", "Str": "", "Type": 5, "Index": 3460}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3595}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4590}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4632}}', 'T1556.003', 'Adversaries may modify pluggable authentication modules (PAM) to access user credentials or enable otherwise unwarranted access to accounts. PAM is a modular system of configuration files, libraries, and executable files which guide authentication for many services. The most common authentication module is <code>pam_unix.so</code>, which retrieves, sets, and verifies account authentication information in <code>/etc/passwd</code> and <code>/etc/shadow</code>.(Citation: Apple PAM)(Citation: Man Pam_Unix)(Citation: Red Hat PAM)
INSERT INTO public.attack_patterns VALUES (366, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.784166+00', NULL, 'attack-pattern--806a49c4-970d-43f9-9acc-ac0ee11e6662', '[{"url": "https://attack.mitre.org/techniques/T1055/002", "external_id": "T1055.002", "source_name": "mitre-attack"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3690}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code> and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code> and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 2674}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3642}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 2484}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3553}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2592}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3595}}', 'T1055.002', 'Adversaries may inject portable executables (PE) into processes in order to evade process-based defenses as well as possibly elevate privileges. PE injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (367, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.812306+00', NULL, 'attack-pattern--970a3432-3237-47ad-bcca-7d8cbb217736', '[{"url": "https://attack.mitre.org/techniques/T1059/001", "external_id": "T1059.001", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/scriptcenter/dd742419.aspx", "description": "Microsoft. (n.d.). Windows PowerShell Scripting. Retrieved April 28, 2016.", "source_name": "TechNet PowerShell"}, {"url": "https://github.com/jaredhaight/PSAttack", "description": "Haight, J. (2016, April 21). PS>Attack. Retrieved June 1, 2016.", "source_name": "Github PSAttack"}, {"url": "http://www.sixdub.net/?p=367", "description": "Warner, J.. (2015, January 6). Inexorable PowerShell – A Red Teamer’s Tale of Overcoming Simple AppLocker Policies. Retrieved December 8, 2018.", "source_name": "Sixdub PowerPick Jan 2016"}, {"url": "https://silentbreaksecurity.com/powershell-jobs-without-powershell-exe/", "description": "Christensen, L.. (2015, December 28). The Evolution of Offensive PowerShell Invocation. Retrieved December 8, 2018.", "source_name": "SilentBreak Offensive PS Dec 2015"}, {"url": "https://blogs.msdn.microsoft.com/kebab/2014/04/28/executing-powershell-scripts-from-c/", "description": "Babinec, K. (2014, April 28). Executing PowerShell scripts from C#. Retrieved April 22, 2019.", "source_name": "Microsoft PSfromCsharp APR 2014"}, {"url": "http://www.malwarearchaeology.com/s/Windows-PowerShell-Logging-Cheat-Sheet-ver-June-2016-v2.pdf", "description": "Malware Archaeology. (2016, June). WINDOWS POWERSHELL LOGGING CHEAT SHEET - Win 7/Win 2008 or later. Retrieved June 24, 2016.", "source_name": "Malware Archaeology PowerShell Cheat Sheet"}, {"url": "https://www.fireeye.com/blog/threat-research/2016/02/greater_visibilityt.html", "description": "Dunwoody, M. (2016, February 11). GREATER VISIBILITY THROUGH POWERSHELL LOGGING. Retrieved February 16, 2016.", "source_name": "FireEye PowerShell Logging 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6017}, "x_mitre_detection": {"Num": 0, "Raw": "\"If proper execution policy is set, adversaries will likely be able to define their own execution policy if they obtain administrator or system access, either through the Registry or at the command line. This change in policy on a system may be a way to detect malicious use of PowerShell. If PowerShell is not used in an environment, then simply looking for PowerShell execution may detect malicious activity.\\n\\nMonitor for loading and/or execution of artifacts associated with PowerShell specific assemblies, such as System.Management.Automation.dll (especially to unusual process names/locations).(Citation: Sixdub PowerPick Jan 2016)(Citation: SilentBreak Offensive PS Dec 2015)\\n\\nIt is also beneficial to turn on PowerShell logging to gain increased fidelity in what occurs during execution (which is applied to .NET invocations). (Citation: Malware Archaeology PowerShell Cheat Sheet) PowerShell 5.0 introduced enhanced logging capabilities, and some of those features have since been added to PowerShell 4.0. Earlier versions of PowerShell do not have many logging features.(Citation: FireEye PowerShell Logging 2016) An organization can gather PowerShell execution details in a data analytic platform to supplement it with other data.\"", "Str": "If proper execution policy is set, adversaries will likely be able to define their own execution policy if they obtain administrator or system access, either through the Registry or at the command line. This change in policy on a system may be a way to detect malicious use of PowerShell. If PowerShell is not used in an environment, then simply looking for PowerShell execution may detect malicious activity.\n\nMonitor for loading and/or execution of artifacts associated with PowerShell specific assemblies, such as System.Management.Automation.dll (especially to unusual process names/locations).(Citation: Sixdub PowerPick Jan 2016)(Citation: SilentBreak Offensive PS Dec 2015)\n\nIt is also beneficial to turn on PowerShell logging to gain increased fidelity in what occurs during execution (which is applied to .NET invocations). (Citation: Malware Archaeology PowerShell Cheat Sheet) PowerShell 5.0 introduced enhanced logging capabilities, and some of those features have since been added to PowerShell 4.0. Earlier versions of PowerShell do not have many logging features.(Citation: FireEye PowerShell Logging 2016) An organization can gather PowerShell execution details in a data analytic platform to supplement it with other data.", "Type": 3, "Index": 4543}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5933}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 4320}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4376}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5988}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5821}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5863}}', 'T1059.001', 'Adversaries may abuse PowerShell commands and scripts for execution. PowerShell is a powerful interactive command-line interface and scripting environment included in the Windows operating system. (Citation: TechNet PowerShell) Adversaries can use PowerShell to perform a number of actions, including discovery of information and execution of code. Examples include the <code>Start-Process</code> cmdlet which can be used to run an executable and the <code>Invoke-Command</code> cmdlet which runs a command locally or on a remote computer (though administrator permissions are required to use PowerShell to connect to remote systems).
INSERT INTO public.attack_patterns VALUES (369, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.878847+00', NULL, 'attack-pattern--7f0ca133-88c4-40c6-a62f-b3083a7fbc2e', '[{"url": "https://attack.mitre.org/techniques/T1542", "external_id": "T1542", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Booting", "description": "Wikipedia. (n.d.). Booting. Retrieved November 13, 2019.", "source_name": "Wikipedia Booting"}, {"url": "https://www.itworld.com/article/2853992/3-tools-to-check-your-hard-drives-health-and-make-sure-its-not-already-dying-on-you.html", "description": "Pinola, M. (2014, December 14). 3 tools to check your hard drive''s health and make sure it''s not already dying on you. Retrieved October 2, 2018.", "source_name": "ITWorld Hard Disk Health Dec 2014"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3335}, "x_mitre_detection": {"Num": 0, "Raw": "\"Perform integrity checking on pre-OS boot mechanisms that can be manipulated for malicious purposes. Take snapshots of boot records and firmware and compare against known good images. Log changes to boot records, BIOS, and EFI, which can be performed by API calls, and compare against known good behavior and patching.\\n\\nDisk check, forensic utilities, and data from device drivers (i.e. processes and API calls) may reveal anomalies that warrant deeper investigation. (Citation: ITWorld Hard Disk Health Dec 2014)\"", "Str": "Perform integrity checking on pre-OS boot mechanisms that can be manipulated for malicious purposes. Take snapshots of boot records and firmware and compare against known good images. Log changes to boot records, BIOS, and EFI, which can be performed by API calls, and compare against known good behavior and patching.\n\nDisk check, forensic utilities, and data from device drivers (i.e. processes and API calls) may reveal anomalies that warrant deeper investigation. (Citation: ITWorld Hard Disk Health Dec 2014)", "Type": 3, "Index": 2590}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3255}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Firmware: Firmware Modification\",\n      \"Driver: Driver Metadata\",\n      \"Process: OS API Execution\",\n      \"Drive: Drive Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2195}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3140}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host intrusion prevention systems\",\n      \"File monitoring\"\n    ]", "Str": "", "Type": 5, "Index": 2469}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3183}}', 'T1542', 'Adversaries may abuse Pre-OS Boot mechanisms as a way to establish persistence on a system. During the booting process of a computer, firmware and various startup services are loaded before the operating system. These programs control flow of execution before the operating system takes control.(Citation: Wikipedia Booting)
INSERT INTO public.attack_patterns VALUES (371, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.9509+00', NULL, 'attack-pattern--60b508a1-6a5e-46b1-821a-9f7b78752abf', '[{"url": "https://attack.mitre.org/techniques/T1552/004", "external_id": "T1552.004", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Public-key_cryptography", "description": "Wikipedia. (2017, June 29). Public-key cryptography. Retrieved July 5, 2017.", "source_name": "Wikipedia Public Key Crypto"}, {"url": "https://kasperskycontenthub.com/wp-content/uploads/sites/43/vlpdfs/unveilingthemask_v1.0.pdf", "description": "Kaspersky Labs. (2014, February 11). Unveiling “Careto” - The Masked APT. Retrieved July 5, 2017.", "source_name": "Kaspersky Careto"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/06/unit42-prince-of-persia-game-over/", "description": "Bar, T., Conant, S., Efraim, L. (2016, June 28). Prince of Persia – Game Over. Retrieved July 5, 2017.", "source_name": "Palo Alto Prince of Persia"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3637}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor access to files and directories related to cryptographic keys and certificates as a means for potentially detecting access patterns that may indicate collection and exfiltration activity. Collect authentication logs and look for potentially abnormal activity that may indicate improper use of keys or certificates for remote authentication.\"", "Str": "Monitor access to files and directories related to cryptographic keys and certificates as a means for potentially detecting access patterns that may indicate collection and exfiltration activity. Collect authentication logs and look for potentially abnormal activity that may indicate improper use of keys or certificates for remote authentication.", "Type": 3, "Index": 3087}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3559}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Itzik Kotler, SafeBreach\"\n    ]", "Str": "", "Type": 5, "Index": 2921}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2991}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3470}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3512}}', 'T1552.004', 'Adversaries may search for private key certificate files on compromised systems for insecurely stored credentials. Private cryptographic keys and certificates are used for authentication, encryption/decryption, and digital signatures.(Citation: Wikipedia Public Key Crypto) Common key and certificate file extensions include: .key, .pgp, .gpg, .ppk., .p12, .pem, .pfx, .cer, .p7b, .asc. 
INSERT INTO public.attack_patterns VALUES (373, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.011411+00', NULL, 'attack-pattern--d201d4cc-214d-4a74-a1ba-b3fa09fd4591', '[{"url": "https://attack.mitre.org/techniques/T1055/009", "external_id": "T1055.009", "source_name": "mitre-attack"}, {"url": "http://hick.org/code/skape/papers/needle.txt", "description": "skape. (2003, January 19). Linux x86 run-time process manipulation. Retrieved December 20, 2017.", "source_name": "Uninformed Needle"}, {"url": "https://blog.gdssecurity.com/labs/2017/9/5/linux-based-inter-process-code-injection-without-ptrace2.html", "description": "McNamara, R. (2017, September 5). Linux Based Inter-Process Code Injection Without Ptrace(2). Retrieved February 21, 2020.", "source_name": "GDS Linux Injection"}, {"url": "http://man7.org/linux/man-pages/man1/dd.1.html", "description": "Kerrisk, M. (2020, February 2). DD(1) User Commands. Retrieved February 21, 2020.", "source_name": "DD Man"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4224}, "x_mitre_detection": {"Num": 0, "Raw": "\"File system monitoring can determine if /proc files are being modified. Users should not have permission to modify these in most cases. \\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "File system monitoring can determine if /proc files are being modified. Users should not have permission to modify these in most cases. \n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 3751}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4178}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3596}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4147}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 3669}}', 'T1055.009', 'Adversaries may inject malicious code into processes via the /proc filesystem in order to evade process-based defenses as well as possibly elevate privileges. Proc memory injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (374, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.039737+00', NULL, 'attack-pattern--8f4a33ec-8b1f-4b80-a2f6-642b2e479580', '[{"url": "https://attack.mitre.org/techniques/T1057", "external_id": "T1057", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/573.html", "external_id": "CAPEC-573", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3453}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nNormal, benign system and network events that look like process discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nNormal, benign system and network events that look like process discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2190}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3256}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2051}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3127}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Administrator, SYSTEM may provide better process ownership details\"\n    ]", "Str": "", "Type": 5, "Index": 3346}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3170}}', 'T1057', 'Adversaries may attempt to get information about running processes on a system. Information obtained could be used to gain an understanding of common software/applications running on systems within the network. Adversaries may use the information from [Process Discovery](https://attack.mitre.org/techniques/T1057) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.
INSERT INTO public.attack_patterns VALUES (377, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.122354+00', NULL, 'attack-pattern--43e7dc91-05b2-474c-b9ac-2ed4fe101f4d', '[{"url": "https://attack.mitre.org/techniques/T1055", "external_id": "T1055", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/640.html", "external_id": "CAPEC-640", "source_name": "capec"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"description": "Ligh, M.H. et al.. (2014, July). The Art of Memory Forensics: Detecting Malware and Threats in Windows, Linux, and Mac Memory. Retrieved December 20, 2017.", "source_name": "ArtOfMemoryForensics"}, {"url": "https://www.gnu.org/software/acct/", "description": "GNU. (2010, February 5). The GNU Accounting Utilities. Retrieved December 20, 2017.", "source_name": "GNU Acct"}, {"url": "https://access.redhat.com/documentation/en-us/red_hat_enterprise_linux/6/html/security_guide/chap-system_auditing", "description": "Jahoda, M. et al.. (2017, March 14). redhat Security Guide - Chapter 7 - System Auditing. Retrieved December 20, 2017.", "source_name": "RHEL auditd"}, {"url": "http://www.chokepoint.net/2014/02/detecting-userland-preload-rootkits.html", "description": "stderr. (2014, February 14). Detecting Userland Preload Rootkits. Retrieved December 20, 2017.", "source_name": "Chokepoint preload rootkits"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/sysmon", "description": "Russinovich, M. & Garnier, T. (2017, May 22). Sysmon v6.20. Retrieved December 13, 2017.", "source_name": "Microsoft Sysmon v6 May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 6070}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, <code>QueueUserAPC</code>/<code>NtQueueApcThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017) \\n\\nMonitor DLL/PE file events, specifically creation of these binary files as well as the loading of DLLs into processes. Look for DLLs that are not recognized or not normally loaded into a process. \\n\\nMonitoring for Linux specific calls such as the ptrace system call should not generate large amounts of data due to their specialized nature, and can be a very effective method to detect some of the common process injection methods.(Citation: ArtOfMemoryForensics)  (Citation: GNU Acct)  (Citation: RHEL auditd)  (Citation: Chokepoint preload rootkits) \\n\\nMonitor for named pipe creation and connection events (Event IDs 17 and 18) for possible indicators of infected processes with external modules.(Citation: Microsoft Sysmon v6 May 2017) \\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, <code>QueueUserAPC</code>/<code>NtQueueApcThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017) \n\nMonitor DLL/PE file events, specifically creation of these binary files as well as the loading of DLLs into processes. Look for DLLs that are not recognized or not normally loaded into a process. \n\nMonitoring for Linux specific calls such as the ptrace system call should not generate large amounts of data due to their specialized nature, and can be a very effective method to detect some of the common process injection methods.(Citation: ArtOfMemoryForensics)  (Citation: GNU Acct)  (Citation: RHEL auditd)  (Citation: Chokepoint preload rootkits) \n\nMonitor for named pipe creation and connection events (Event IDs 17 and 18) for possible indicators of infected processes with external modules.(Citation: Microsoft Sysmon v6 May 2017) \n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 4192}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5992}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Anastasios Pingios\",\n      \"Christiaan Beek, @ChristiaanBeek\",\n      \"Ryan Becwar\"\n    ]", "Str": "", "Type": 5, "Index": 3784}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\",\n      \"File: File Modification\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3911}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5960}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 4110}}', 'T1055', 'Adversaries may inject code into processes in order to evade process-based defenses as well as possibly elevate privileges. Process injection is a method of executing arbitrary code in the address space of a separate live process. Running code in the context of another process may allow access to the process''s memory, system/network resources, and possibly elevated privileges. Execution via process injection may also evade detection from security products since the execution is masked under a legitimate process. 
INSERT INTO public.attack_patterns VALUES (378, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.184812+00', NULL, 'attack-pattern--c325b232-d5bc-4dde-a3ec-71f3db9e8adc', '[{"url": "https://attack.mitre.org/techniques/T1001/003", "external_id": "T1001.003", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2343}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1776}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2265}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1693}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2234}}', 'T1001.003', 'Adversaries may impersonate legitimate protocols or web service traffic to disguise command and control activity and thwart analysis efforts. By impersonating legitimate protocols or web services, adversaries can make their command and control traffic blend in with legitimate network traffic.  
INSERT INTO public.attack_patterns VALUES (390, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.253713+00', NULL, 'attack-pattern--3a40f208-a9c1-4efa-a598-4003c3681fb8', '[{"url": "https://attack.mitre.org/techniques/T1600/001", "external_id": "T1600.001", "source_name": "mitre-attack"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3029}, "x_mitre_detection": {"Num": 0, "Raw": "\"There is no documented method for defenders to directly identify behaviors that reduce encryption key space. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601) and [Network Device CLI](https://attack.mitre.org/techniques/T1059/008). Some detection methods require vendor support to aid in investigation.\"", "Str": "There is no documented method for defenders to directly identify behaviors that reduce encryption key space. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601) and [Network Device CLI](https://attack.mitre.org/techniques/T1059/008). Some detection methods require vendor support to aid in investigation.", "Type": 3, "Index": 2450}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2981}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2384}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2883}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2925}}', 'T1600.001', 'Adversaries may reduce the level of effort required to decrypt data transmitted over the network by reducing the cipher strength of encrypted communications.(Citation: Cisco Synful Knock Evolution)
INSERT INTO public.attack_patterns VALUES (381, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.330606+00', NULL, 'attack-pattern--ea016b56-ae0e-47fe-967a-cc0ad51af67f', '[{"url": "https://attack.mitre.org/techniques/T1055/008", "external_id": "T1055.008", "source_name": "mitre-attack"}, {"url": "http://man7.org/linux/man-pages/man2/ptrace.2.html", "description": "Kerrisk, M. (2020, February 9). PTRACE(2) - Linux Programmer''s Manual. Retrieved February 21, 2020.", "source_name": "PTRACE man"}, {"url": "https://medium.com/@jain.sm/code-injection-in-running-process-using-ptrace-d3ea7191a4be", "description": "Jain, S. (2018, July 25). Code injection in running process using ptrace. Retrieved February 21, 2020.", "source_name": "Medium Ptrace JUL 2018"}, {"url": "https://github.com/gaffe23/linux-inject/blob/master/slides_BHArsenal2015.pdf", "description": "Colgan, T. (2015, August 15). Linux-Inject. Retrieved February 21, 2020.", "source_name": "BH Linux Inject"}, {"description": "Ligh, M.H. et al.. (2014, July). The Art of Memory Forensics: Detecting Malware and Threats in Windows, Linux, and Mac Memory. Retrieved December 20, 2017.", "source_name": "ArtOfMemoryForensics"}, {"url": "https://www.gnu.org/software/acct/", "description": "GNU. (2010, February 5). The GNU Accounting Utilities. Retrieved December 20, 2017.", "source_name": "GNU Acct"}, {"url": "https://access.redhat.com/documentation/en-us/red_hat_enterprise_linux/6/html/security_guide/chap-system_auditing", "description": "Jahoda, M. et al.. (2017, March 14). redhat Security Guide - Chapter 7 - System Auditing. Retrieved December 20, 2017.", "source_name": "RHEL auditd"}, {"url": "http://www.chokepoint.net/2014/02/detecting-userland-preload-rootkits.html", "description": "stderr. (2014, February 14). Detecting Userland Preload Rootkits. Retrieved December 20, 2017.", "source_name": "Chokepoint preload rootkits"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5306}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring for Linux specific calls such as the ptrace system call should not generate large amounts of data due to their specialized nature, and can be a very effective method to detect some of the common process injection methods.(Citation: ArtOfMemoryForensics)  (Citation: GNU Acct)  (Citation: RHEL auditd)  (Citation: Chokepoint preload rootkits) \\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring for Linux specific calls such as the ptrace system call should not generate large amounts of data due to their specialized nature, and can be a very effective method to detect some of the common process injection methods.(Citation: ArtOfMemoryForensics)  (Citation: GNU Acct)  (Citation: RHEL auditd)  (Citation: Chokepoint preload rootkits) \n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 4616}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 5260}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 4426}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5229}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 4534}}', 'T1055.008', 'Adversaries may inject malicious code into processes via ptrace (process trace) system calls in order to evade process-based defenses as well as possibly elevate privileges. Ptrace system call injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (382, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.364004+00', NULL, 'attack-pattern--09cd431f-eaf4-4d2a-acaf-2a7acfe7ed58', '[{"url": "https://attack.mitre.org/techniques/T1216/001", "external_id": "T1216.001", "source_name": "mitre-attack"}, {"url": "https://enigma0x3.net/2017/08/03/wsh-injection-a-case-study/", "description": "Nelson, M. (2017, August 3). WSH INJECTION: A CASE STUDY. Retrieved April 9, 2018.", "source_name": "Enigma0x3 PubPrn Bypass"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2172}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor script processes, such as `cscript`, and command-line parameters for scripts like PubPrn.vbs that may be used to proxy execution of malicious files.\"", "Str": "Monitor script processes, such as `cscript`, and command-line parameters for scripts like PubPrn.vbs that may be used to proxy execution of malicious files.", "Type": 3, "Index": 1844}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2124}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1706}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2035}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2077}}', 'T1216.001', 'Adversaries may use the trusted PubPrn script to proxy execution of malicious files. This behavior may bypass signature validation restrictions and application control solutions that do not account for use of these scripts.
INSERT INTO public.attack_patterns VALUES (383, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.400377+00', NULL, 'attack-pattern--0a241b6c-7bb2-48f9-98f7-128145b4d27f', '[{"url": "https://attack.mitre.org/techniques/T1597/002", "external_id": "T1597.002", "source_name": "mitre-attack"}, {"url": "https://www.zdnet.com/article/a-hacker-group-is-selling-more-than-73-million-user-records-on-the-dark-web/", "description": "Cimpanu, C. (2020, May 9). A hacker group is selling more than 73 million user records on the dark web. Retrieved October 20, 2020.", "source_name": "ZDNET Selling Data"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2994}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2553}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2950}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2919}}', 'T1597.002', 'Adversaries may purchase technical information about victims that can be used during targeting. Information about victims may be available for purchase within reputable private sources and databases, such as paid subscriptions to feeds of scan databases or other data aggregation services. Adversaries may also purchase information from less-reputable sources such as dark web or cybercrime blackmarkets.
INSERT INTO public.attack_patterns VALUES (384, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.438762+00', NULL, 'attack-pattern--cc3502b5-30cc-4473-ad48-42d51a6ef6d1', '[{"url": "https://attack.mitre.org/techniques/T1059/006", "external_id": "T1059.006", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2945}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor systems for abnormal Python usage and python.exe behavior, which could be an indicator of malicious activity. Understanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\\n\\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information Discovery, Collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.\"", "Str": "Monitor systems for abnormal Python usage and python.exe behavior, which could be an indicator of malicious activity. Understanding standard usage patterns is important to avoid a high number of false positives. If scripting is restricted for normal users, then any attempts to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\n\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information Discovery, Collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.", "Type": 3, "Index": 1595}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2794}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1491}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2666}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Python is installed.\"\n    ]", "Str": "", "Type": 5, "Index": 2884}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2708}}', 'T1059.006', 'Adversaries may abuse Python commands and scripts for execution. Python is a very popular scripting/programming language, with capabilities to perform many functions. Python can be executed interactively from the command-line (via the <code>python.exe</code> interpreter) or via scripts (.py) that can be written and distributed to different systems. Python code can also be compiled into binary executables.
INSERT INTO public.attack_patterns VALUES (387, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.541679+00', NULL, 'attack-pattern--e0033c16-a07e-48aa-8204-7c3ca669998c', '[{"url": "https://attack.mitre.org/techniques/T1563/002", "external_id": "T1563.002", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/windowsserver/ee236407.aspx", "description": "Microsoft. (n.d.). Remote Desktop Services. Retrieved June 1, 2016.", "source_name": "TechNet Remote Desktop Services"}, {"url": "http://www.korznikov.com/2017/03/0-day-or-feature-privilege-escalation.html", "description": "Korznikov, A. (2017, March 17). Passwordless RDP Session Hijacking Feature All Windows versions. Retrieved December 11, 2017.", "source_name": "RDP Hijacking Korznikov"}, {"url": "https://medium.com/@networksecurity/rdp-hijacking-how-to-hijack-rds-and-remoteapp-sessions-transparently-to-move-through-an-da2a1e73a5f6", "description": "Beaumont, K. (2017, March 19). RDP hijacking — how to hijack RDS and RemoteApp sessions transparently to move through an organisation. Retrieved December 11, 2017.", "source_name": "RDP Hijacking Medium"}, {"url": "https://github.com/nccgroup/redsnarf", "description": "NCC Group PLC. (2016, November 1). Kali Redsnarf. Retrieved December 11, 2017.", "source_name": "Kali Redsnarf"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4170}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring processes for `tscon.exe` usage and monitor service creation that uses `cmd.exe /k` or `cmd.exe /c` in its arguments to detect RDP session hijacking.\\n\\nUse of RDP may be legitimate, depending on the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with RDP.\"", "Str": "Consider monitoring processes for `tscon.exe` usage and monitor service creation that uses `cmd.exe /k` or `cmd.exe /c` in its arguments to detect RDP session hijacking.\n\nUse of RDP may be legitimate, depending on the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with RDP.", "Type": 3, "Index": 3595}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4122}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3347}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4031}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4073}}', 'T1563.002', 'Adversaries may hijack a legitimate user’s remote desktop session to move laterally within an environment. Remote desktop is a common feature in operating systems. It allows a user to log into an interactive session with a system desktop graphical user interface on a remote system. Microsoft refers to its implementation of the Remote Desktop Protocol (RDP) as Remote Desktop Services (RDS).(Citation: TechNet Remote Desktop Services)
INSERT INTO public.attack_patterns VALUES (388, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.576504+00', NULL, 'attack-pattern--a6557c75-798f-42e4-be70-ab4502e0a3bc', '[{"url": "https://attack.mitre.org/techniques/T1542/004", "external_id": "T1542.004", "source_name": "mitre-attack"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2894}, "x_mitre_detection": {"Num": 0, "Raw": "\"There are no documented means for defenders to validate the operation of the ROMMON outside of vendor support. If a network device is suspected of being compromised, contact the vendor to assist in further investigation.\"", "Str": "There are no documented means for defenders to validate the operation of the ROMMON outside of vendor support. If a network device is suspected of being compromised, contact the vendor to assist in further investigation.", "Type": 3, "Index": 2493}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2846}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Firmware: Firmware Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2419}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2748}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2790}}', 'T1542.004', 'Adversaries may abuse the ROM Monitor (ROMMON) by loading an unauthorized firmware with adversary code to provide persistent access and manipulate device behavior that is difficult to detect. (Citation: Cisco Synful Knock Evolution)(Citation: Cisco Blog Legacy Device Attacks)
INSERT INTO public.attack_patterns VALUES (389, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.612207+00', NULL, 'attack-pattern--e5cc9e7a-e61a-46a1-b869-55fb6eab058e', '[{"url": "https://attack.mitre.org/techniques/T1547/007", "external_id": "T1547.007", "source_name": "mitre-attack"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2457}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring the specific plist files associated with reopening applications can indicate when an application has registered itself to be reopened.\"", "Str": "Monitoring the specific plist files associated with reopening applications can indicate when an application has registered itself to be reopened.", "Type": 3, "Index": 2142}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2411}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2040}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2322}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2364}}', 'T1547.007', 'Adversaries may modify plist files to automatically run an application when a user logs in. Starting in Mac OS X 10.7 (Lion), users can specify certain applications to be re-opened when a user logs into their machine after reboot. While this is usually done via a Graphical User Interface (GUI) on an app-by-app basis, there are property list files (plist) that contain this information as well located at <code>~/Library/Preferences/com.apple.loginwindow.plist</code> and <code>~/Library/Preferences/ByHost/com.apple.loginwindow.* .plist</code>. 
INSERT INTO public.attack_patterns VALUES (412, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.647832+00', NULL, 'attack-pattern--1f9c2bae-b441-4f66-a8af-b65946ee72f2', '[{"url": "https://attack.mitre.org/techniques/T1606/002", "external_id": "T1606.002", "source_name": "mitre-attack"}, {"url": "https://blogs.microsoft.com/on-the-issues/2020/12/13/customers-protect-nation-state-cyberattacks/", "description": "Lambert, J. (2020, December 13). Important steps for customers to protect themselves from recent nation-state cyberattacks. Retrieved December 17, 2020.", "source_name": "Microsoft SolarWinds Steps"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/active-directory-configurable-token-lifetimes", "description": "Microsoft. (2020, December 14). Configurable token lifetimes in Microsoft Identity Platform. Retrieved December 22, 2020.", "source_name": "Microsoft SAML Token Lifetimes"}, {"url": "https://www.cyberark.com/resources/threat-research-blog/golden-saml-newly-discovered-attack-technique-forges-authentication-to-cloud-apps", "description": "Reiner, S. (2017, November 21). Golden SAML: Newly Discovered Attack Technique Forges Authentication to Cloud Apps. Retrieved December 17, 2020.", "source_name": "Cyberark Golden SAML"}, {"url": "https://msrc-blog.microsoft.com/2020/12/13/customer-guidance-on-recent-nation-state-cyber-attacks/", "description": "MSRC. (2020, December 13). Customer Guidance on Recent Nation-State Cyber Attacks. Retrieved December 17, 2020.", "source_name": "Microsoft SolarWinds Customer Guidance"}, {"url": "https://www.sygnia.co/golden-saml-advisory", "description": "Sygnia. (2020, December). Detection and Hunting of Golden SAML Attack. Retrieved January 6, 2021.", "source_name": "Sygnia Golden SAML"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5710}, "x_mitre_detection": {"Num": 0, "Raw": "\"This technique may be difficult to detect as SAML tokens are signed by a trusted certificate. The forging process may not be detectable since it is likely to happen outside of a defender''s visibility, but subsequent usage of the forged token may be seen. Monitor for anomalous logins using SAML tokens created by a compromised or adversary generated token-signing certificate. These logins may occur on any on-premises resources as well as from any cloud environment that trusts the certificate.(Citation: Microsoft SolarWinds Customer Guidance) Search for logins to service providers using SAML SSO which do not have corresponding 4769, 1200, and 1202 events in the Domain.(Citation: Sygnia Golden SAML)\\n\\nConsider modifying SAML responses to include custom elements for each service provider. Monitor these custom elements in service provider access logs to detect any anomalous requests.(Citation: Sygnia Golden SAML)\"", "Str": "This technique may be difficult to detect as SAML tokens are signed by a trusted certificate. The forging process may not be detectable since it is likely to happen outside of a defender''s visibility, but subsequent usage of the forged token may be seen. Monitor for anomalous logins using SAML tokens created by a compromised or adversary generated token-signing certificate. These logins may occur on any on-premises resources as well as from any cloud environment that trusts the certificate.(Citation: Microsoft SolarWinds Customer Guidance) Search for logins to service providers using SAML SSO which do not have corresponding 4769, 1200, and 1202 events in the Domain.(Citation: Sygnia Golden SAML)\n\nConsider modifying SAML responses to include custom elements for each service provider. Monitor these custom elements in service provider access logs to detect any anomalous requests.(Citation: Sygnia Golden SAML)", "Type": 3, "Index": 4530}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"SaaS\",\n      \"Windows\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 5584}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Blake Strom, Microsoft 365 Defender\",\n      \"Oleg Kolesnikov, Securonix\"\n    ]", "Str": "", "Type": 5, "Index": 4238}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Web Credential: Web Credential Creation\",\n      \"Web Credential: Web Credential Usage\"\n    ]", "Str": "", "Type": 5, "Index": 4355}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5486}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5528}}', 'T1606.002', 'An adversary may forge SAML tokens with any permissions claims and lifetimes if they possess a valid SAML token-signing certificate.(Citation: Microsoft SolarWinds Steps) The default lifetime of a SAML token is one hour, but the validity period can be specified in the <code>NotOnOrAfter</code> value of the <code>conditions ...</code> element in a token. This value can be changed using the <code>AccessTokenLifetime</code> in a <code>LifetimeTokenPolicy</code>.(Citation: Microsoft SAML Token Lifetimes) Forged SAML tokens enable adversaries to authenticate across services that use SAML 2.0 as an SSO (single sign-on) mechanism.(Citation: Cyberark Golden SAML)
INSERT INTO public.attack_patterns VALUES (392, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.722219+00', NULL, 'attack-pattern--9efb1ea7-c37b-4595-9640-b7680cd84279', '[{"url": "https://attack.mitre.org/techniques/T1547/001", "external_id": "T1547.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/270.html", "external_id": "CAPEC-270", "source_name": "capec"}, {"url": "http://msdn.microsoft.com/en-us/library/aa376977", "description": "Microsoft. (n.d.). Run and RunOnce Registry Keys. Retrieved November 12, 2014.", "source_name": "Microsoft Run Key"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/sysinfo/32-bit-and-64-bit-application-data-in-the-registry", "description": "Microsoft. (2018, May 31). 32-bit and 64-bit Application Data in the Registry. Retrieved August 3, 2020.", "source_name": "Microsoft Wow6432Node 2018"}, {"url": "https://blog.malwarebytes.com/cybercrime/2013/10/hiding-in-plain-sight/", "description": "Arntz, P. (2016, March 30). Hiding in Plain Sight. Retrieved August 3, 2020.", "source_name": "Malwarebytes Wow6432Node 2016"}, {"url": "https://support.microsoft.com/help/310593/description-of-the-runonceex-registry-key", "description": "Microsoft. (2018, August 20). Description of the RunOnceEx Registry Key. Retrieved June 29, 2018.", "source_name": "Microsoft RunOnceEx APR 2018"}, {"url": "https://oddvar.moe/2018/03/21/persistence-using-runonceex-hidden-from-autoruns-exe/", "description": "Moe, O. (2018, March 21). Persistence using RunOnceEx - Hidden from Autoruns.exe. Retrieved June 29, 2018.", "source_name": "Oddvar Moe RunOnceEx Mar 2018"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 9054}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor Registry for changes to run keys that do not correlate with known software, patch cycles, etc. Monitor the start folder for additions or changes. Tools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing the run keys'' Registry locations and startup folders. (Citation: TechNet Autoruns) Suspicious program execution as startup programs may show up as outlier processes that have not been seen before when compared against historical data.\\n\\nChanges to these locations typically happen under normal conditions when legitimate software is installed. To increase confidence of malicious activity, data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor Registry for changes to run keys that do not correlate with known software, patch cycles, etc. Monitor the start folder for additions or changes. Tools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing the run keys'' Registry locations and startup folders. (Citation: TechNet Autoruns) Suspicious program execution as startup programs may show up as outlier processes that have not been seen before when compared against historical data.\n\nChanges to these locations typically happen under normal conditions when legitimate software is installed. To increase confidence of malicious activity, data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 7919}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 9006}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oddvar Moe, @oddvarmoe\"\n    ]", "Str": "", "Type": 5, "Index": 7596}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Modification\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 7664}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 8894}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 8936}}', 'T1547.001', 'Adversaries may achieve persistence by adding a program to a startup folder or referencing it with a Registry run key. Adding an entry to the "run keys" in the Registry or startup folder will cause the program referenced to be executed when a user logs in. (Citation: Microsoft Run Key) These programs will be executed under the context of the user and will have the account''s associated permissions level.
INSERT INTO public.attack_patterns VALUES (393, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.758173+00', NULL, 'attack-pattern--c48a67ee-b657-45c1-91bf-6cdbe27205f8', '[{"url": "https://attack.mitre.org/techniques/T1218/009", "external_id": "T1218.009", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/en-us/library/04za0hca.aspx", "description": "Microsoft. (n.d.). Regsvcs.exe (.NET Services Installation Tool). Retrieved July 1, 2016.", "source_name": "MSDN Regsvcs"}, {"url": "https://msdn.microsoft.com/en-us/library/tzat5yw6.aspx", "description": "Microsoft. (n.d.). Regasm.exe (Assembly Registration Tool). Retrieved July 1, 2016.", "source_name": "MSDN Regasm"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Regsvcs/", "description": "LOLBAS. (n.d.). Regsvcs.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Regsvcs"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Regasm/", "description": "LOLBAS. (n.d.). Regasm.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Regasm"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3489}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of Regsvcs.exe and Regasm.exe. Compare recent invocations of Regsvcs.exe and Regasm.exe with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after Regsvcs.exe or Regasm.exe invocation may also be useful in determining the origin and purpose of the binary being executed.\"", "Str": "Use process monitoring to monitor the execution and arguments of Regsvcs.exe and Regasm.exe. Compare recent invocations of Regsvcs.exe and Regasm.exe with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. Command arguments used before and after Regsvcs.exe or Regasm.exe invocation may also be useful in determining the origin and purpose of the binary being executed.", "Type": 3, "Index": 2855}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3441}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Casey Smith\"\n    ]", "Str": "", "Type": 5, "Index": 2585}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2642}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3329}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2753}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3371}}', 'T1218.009', 'Adversaries may abuse Regsvcs and Regasm to proxy execution of code through a trusted Windows utility. Regsvcs and Regasm are Windows command-line utilities that are used to register .NET [Component Object Model](https://attack.mitre.org/techniques/T1559/001) (COM) assemblies. Both are digitally signed by Microsoft. (Citation: MSDN Regsvcs) (Citation: MSDN Regasm)
INSERT INTO public.attack_patterns VALUES (394, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.794743+00', NULL, 'attack-pattern--b97f1d35-4249-4486-a6b5-ee60ccf24fab', '[{"url": "https://attack.mitre.org/techniques/T1218/010", "external_id": "T1218.010", "source_name": "mitre-attack"}, {"url": "https://support.microsoft.com/en-us/kb/249873", "description": "Microsoft. (2015, August 14). How to use the Regsvr32 tool and troubleshoot Regsvr32 error messages. Retrieved June 22, 2016.", "source_name": "Microsoft Regsvr32"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Regsvr32/", "description": "LOLBAS. (n.d.). Regsvr32.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Regsvr32"}, {"url": "https://www.carbonblack.com/2016/04/28/threat-advisory-squiblydoo-continues-trend-of-attackers-using-native-os-tools-to-live-off-the-land/", "description": "Nolen, R. et al.. (2016, April 28). Threat Advisory: “Squiblydoo” Continues Trend of Attackers Using Native OS Tools to “Live off the Land”. Retrieved April 9, 2018.", "source_name": "Carbon Black Squiblydoo Apr 2016"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/02/spear_phishing_techn.html", "description": "Anubhav, A., Kizhakkinan, D. (2017, February 22). Spear Phishing Techniques Used in Attacks Targeting the Mongolian Government. Retrieved February 24, 2017.", "source_name": "FireEye Regsvr32 Targeting Mongolian Gov"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4640}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of regsvr32.exe. Compare recent invocations of regsvr32.exe with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity. Command arguments used before and after the regsvr32.exe invocation may also be useful in determining the origin and purpose of the script or DLL being loaded. (Citation: Carbon Black Squiblydoo Apr 2016)\"", "Str": "Use process monitoring to monitor the execution and arguments of regsvr32.exe. Compare recent invocations of regsvr32.exe with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity. Command arguments used before and after the regsvr32.exe invocation may also be useful in determining the origin and purpose of the script or DLL being loaded. (Citation: Carbon Black Squiblydoo Apr 2016)", "Type": 3, "Index": 3998}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4592}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Casey Smith\"\n    ]", "Str": "", "Type": 5, "Index": 3625}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3682}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4480}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3876}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4522}}', 'T1218.010', 'Adversaries may abuse Regsvr32.exe to proxy execution of malicious code. Regsvr32.exe is a command-line program used to register and unregister object linking and embedding controls, including dynamic link libraries (DLLs), on Windows systems. Regsvr32.exe is also a Microsoft signed binary. (Citation: Microsoft Regsvr32)
INSERT INTO public.attack_patterns VALUES (396, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.869606+00', NULL, 'attack-pattern--359b00ad-9425-420b-bba5-6de8d600cbc0', '[{"url": "https://attack.mitre.org/techniques/T1074/002", "external_id": "T1074.002", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3266}, "x_mitre_detection": {"Num": 0, "Raw": "\"Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\\n\\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Processes that appear to be reading files from disparate locations and writing them to the same directory or file may be an indication of data being staged, especially if they are suspected of performing encryption or compression on the files, such as 7zip, RAR, ZIP, or zlib. Monitor publicly writeable directories, central locations, and commonly used staging directories (recycle bin, temp folders, etc.) to regularly check for compressed or encrypted data that may be indicative of staging.\n\nMonitor processes and command-line arguments for actions that could be taken to collect and combine files. Remote access tools with built-in features may interact directly with the Windows API to gather and copy to a location. Data may also be acquired and staged through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2154}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3174}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 1973}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"File: File Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2029}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3143}}', 'T1074.002', 'Adversaries may stage data collected from multiple systems in a central location or directory on one system prior to Exfiltration. Data may be kept in separate files or combined into one file through techniques such as [Archive Collected Data](https://attack.mitre.org/techniques/T1560). Interactive command shells may be used, and common functionality within [cmd](https://attack.mitre.org/software/S0106) and bash may be used to copy data into a staging location.
INSERT INTO public.attack_patterns VALUES (399, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.986989+00', NULL, 'attack-pattern--5b0ad6f8-6a16-4966-a4ef-d09ea6e2a9f5', '[{"url": "https://attack.mitre.org/techniques/T1563", "external_id": "T1563", "source_name": "mitre-attack"}, {"url": "https://medium.com/@networksecurity/rdp-hijacking-how-to-hijack-rds-and-remoteapp-sessions-transparently-to-move-through-an-da2a1e73a5f6", "description": "Beaumont, K. (2017, March 19). RDP hijacking — how to hijack RDS and RemoteApp sessions transparently to move through an organisation. Retrieved December 11, 2017.", "source_name": "RDP Hijacking Medium"}, {"url": "https://matrix.org/blog/2019/05/08/post-mortem-and-remediations-for-apr-11-security-incident", "description": "Hodgson, M. (2019, May 8). Post-mortem and remediations for Apr 11 security incident. Retrieved February 17, 2020.", "source_name": "Breach Post-mortem SSH Hijack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3397}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use of these services may be legitimate, depending upon the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with that service. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.\\n\\nMonitor for processes and command-line arguments associated with hijacking service sessions.\"", "Str": "Use of these services may be legitimate, depending upon the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with that service. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.\n\nMonitor for processes and command-line arguments associated with hijacking service sessions.", "Type": 3, "Index": 2676}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3319}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2428}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3213}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3256}}', 'T1563', 'Adversaries may take control of preexisting sessions with remote services to move laterally in an environment. Users may use valid credentials to log into a service specifically designed to accept remote connections, such as telnet, SSH, and RDP. When a user logs into a service, a session will be established that will allow them to maintain a continuous interaction with that service.
INSERT INTO public.attack_patterns VALUES (400, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.055934+00', NULL, 'attack-pattern--54a649ff-439a-41a4-9856-8d144a2551ba', '[{"url": "https://attack.mitre.org/techniques/T1021", "external_id": "T1021", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/555.html", "external_id": "CAPEC-555", "source_name": "capec"}, {"url": "https://www.ssh.com/ssh", "description": "SSH.COM. (n.d.). SSH (Secure Shell). Retrieved March 23, 2020.", "source_name": "SSH Secure Shell"}, {"url": "https://technet.microsoft.com/en-us/windowsserver/ee236407.aspx", "description": "Microsoft. (n.d.). Remote Desktop Services. Retrieved June 1, 2016.", "source_name": "TechNet Remote Desktop Services"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3035}, "x_mitre_detection": {"Num": 0, "Raw": "\"Correlate use of login activity related to remote services with unusual behavior or other malicious or suspicious activity. Adversaries will likely need to learn about an environment and the relationships between systems through Discovery techniques prior to attempting Lateral Movement.\"", "Str": "Correlate use of login activity related to remote services with unusual behavior or other malicious or suspicious activity. Adversaries will likely need to learn about an environment and the relationships between systems through Discovery techniques prior to attempting Lateral Movement.", "Type": 3, "Index": 2485}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2839}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\",\n      \"Network Share: Network Share Access\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2159}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2807}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Active remote service accepting connections and valid credentials\"\n    ]", "Str": "", "Type": 5, "Index": 2929}}', 'T1021', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to log into a service specifically designed to accept remote connections, such as telnet, SSH, and VNC. The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (401, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.107326+00', NULL, 'attack-pattern--e358d692-23c0-4a31-9eb6-ecc13a8d7735', '[{"url": "https://attack.mitre.org/techniques/T1018", "external_id": "T1018", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/292.html", "external_id": "CAPEC-292", "source_name": "capec"}, {"url": "https://www.elastic.co/blog/embracing-offensive-tooling-building-detections-against-koadic-using-eql", "description": "Stepanic, D.. (2020, January 13). Embracing offensive tooling: Building detections against Koadic using EQL. Retrieved November 30, 2020.", "source_name": "Elastic - Koadiac Detection with EQL"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"3.1\"", "Str": "3.1", "Type": 3, "Index": 3819}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nNormal, benign system and network events related to legitimate remote system discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nMonitor for processes that can be used to discover remote systems, such as <code>ping.exe</code> and <code>tracert.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nNormal, benign system and network events related to legitimate remote system discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nMonitor for processes that can be used to discover remote systems, such as <code>ping.exe</code> and <code>tracert.exe</code>, especially when executed in quick succession.(Citation: Elastic - Koadiac Detection with EQL)", "Type": 3, "Index": 2438}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3741}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniel Stepanic, Elastic\",\n      \"RedHuntLabs, @redhuntlabs\"\n    ]", "Str": "", "Type": 5, "Index": 2148}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 2253}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3612}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3655}}', 'T1018', 'Adversaries may attempt to get a listing of other systems by IP address, hostname, or other logical identifier on a network that may be used for Lateral Movement from the current system. Functionality could exist within remote access tools to enable this, but utilities available on the operating system could also be used such as  [Ping](https://attack.mitre.org/software/S0097) or <code>net view</code> using [Net](https://attack.mitre.org/software/S0039). Adversaries may also use local host files (ex: <code>C:\Windows\System32\Drivers\etc\hosts</code> or <code>/etc/hosts</code>) in order to discover the hostname to IP address mappings of remote systems. 
INSERT INTO public.attack_patterns VALUES (403, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.175048+00', NULL, 'attack-pattern--3b744087-9945-4a6f-91e8-9dbceda417a4', '[{"url": "https://attack.mitre.org/techniques/T1091", "external_id": "T1091", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2471}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file access on removable media. Detect processes that execute from removable media after it is mounted or when initiated by a user. If a remote access tool is used in this manner to move laterally, then additional actions are likely to occur after execution, such as opening network connections for Command and Control and system and network information Discovery.\"", "Str": "Monitor file access on removable media. Detect processes that execute from removable media after it is mounted or when initiated by a user. If a remote access tool is used in this manner to move laterally, then additional actions are likely to occur after execution, such as opening network connections for Command and Control and system and network information Discovery.", "Type": 3, "Index": 1777}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2274}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Access\",\n      \"File: File Creation\",\n      \"Drive: Drive Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1622}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2184}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Removable media allowed, Autorun enabled or vulnerability present that allows for code execution\"\n    ]", "Str": "", "Type": 5, "Index": 2334}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2227}}', 'T1091', 'Adversaries may move onto systems, possibly those on disconnected or air-gapped networks, by copying malware to removable media and taking advantage of Autorun features when the media is inserted into a system and executes. In the case of Lateral Movement, this may occur through modification of executable files stored on removable media or by copying malware and renaming it to look like a legitimate file to trick users into executing it on a separate system. In the case of Initial Access, this may occur through manual manipulation of the media, modification of systems used to initially format the media, or modification to the media''s firmware itself.', 'Replication Through Removable Media', 0);
INSERT INTO public.attack_patterns VALUES (404, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.208724+00', NULL, 'attack-pattern--cd25c1b4-935c-4f0e-ba8d-552f28bc4783', '[{"url": "https://attack.mitre.org/techniques/T1496", "external_id": "T1496", "source_name": "mitre-attack"}, {"url": "https://securelist.com/lazarus-under-the-hood/77908/", "description": "GReAT. (2017, April 3). Lazarus Under the Hood. Retrieved April 17, 2019.", "source_name": "Kaspersky Lazarus Under The Hood Blog 2017"}, {"url": "https://blog.cloudsploit.com/the-danger-of-unused-aws-regions-af0bf1b878fc", "description": "CloudSploit. (2019, June 8). The Danger of Unused AWS Regions. Retrieved October 8, 2019.", "source_name": "CloudSploit - Unused AWS Regions"}, {"url": "https://unit42.paloaltonetworks.com/hildegard-malware-teamtnt/", "description": "Chen, J. et al. (2021, February 3). Hildegard: New TeamTNT Cryptojacking Malware Targeting Kubernetes. Retrieved April 5, 2021.", "source_name": "Unit 42 Hildegard Malware"}, {"url": "https://www.trendmicro.com/en_us/research/19/e/infected-cryptocurrency-mining-containers-target-docker-hosts-with-exposed-apis-use-shodan-to-find-additional-victims.html", "description": "Oliveira, A. (2019, May 30). Infected Containers Target Docker via Exposed APIs. Retrieved April 6, 2021.", "source_name": "Trend Micro Exposed Docker APIs"}, {"url": "https://www.trendmicro.com/en_us/research/20/i/war-of-linux-cryptocurrency-miners-a-battle-for-resources.html", "description": "Oliveira, A., Fiser, D. (2020, September 10). War of Linux Cryptocurrency Miners: A Battle for Resources. Retrieved April 6, 2021.", "source_name": "Trend Micro War of Crypto Miners"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4928}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring process resource usage to determine anomalous activity associated with malicious hijacking of computer resources such as CPU, memory, and graphics processing resources. Monitor for suspicious use of network resources associated with cryptocurrency mining software. Monitor for common cryptomining software process names and files on local systems that may indicate compromise and resource usage.\"", "Str": "Consider monitoring process resource usage to determine anomalous activity associated with malicious hijacking of computer resources such as CPU, memory, and graphics processing resources. Monitor for suspicious use of network resources associated with cryptocurrency mining software. Monitor for common cryptomining software process names and files on local systems that may indicate compromise and resource usage.", "Type": 3, "Index": 4196}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4816}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4642}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Alfredo Oliveira, Trend Micro\",\n      \"Jay Chen, Palo Alto Networks\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Vishwas Manral, McAfee\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 3638}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Sensor Health: Host Status\"\n    ]", "Str": "", "Type": 5, "Index": 3926}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4703}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4746}}', 'T1496', 'Adversaries may leverage the resources of co-opted systems in order to solve resource intensive problems which may impact system and/or hosted service availability. 
INSERT INTO public.attack_patterns VALUES (406, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.292837+00', NULL, 'attack-pattern--77eae145-55db-4519-8ae5-77b0c7215d69', '[{"url": "https://attack.mitre.org/techniques/T1036/002", "external_id": "T1036.002", "source_name": "mitre-attack"}, {"url": "https://resources.infosecinstitute.com/spoof-using-right-to-left-override-rtlo-technique-2/", "description": "Security Ninja. (2015, April 16). Spoof Using Right to Left Override (RTLO) Technique. Retrieved April 22, 2019.", "source_name": "Infosecinstitute RTLO Technique"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/plead-targeted-attacks-against-taiwanese-government-agencies-2/", "description": "Alintanahin, K.. (2014, May 23). PLEAD Targeted Attacks Against Taiwanese Government Agencies. Retrieved April 22, 2019.", "source_name": "Trend Micro PLEAD RTLO"}, {"url": "https://securelist.com/zero-day-vulnerability-in-telegram/83800/", "description": "Firsh, A.. (2018, February 13). Zero-day vulnerability in Telegram - Cybercriminals exploited Telegram flaw to launch multipurpose attacks. Retrieved April 22, 2019.", "source_name": "Kaspersky RTLO Cyber Crime"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3637}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection methods should include looking for common formats of RTLO characters within filenames such as <code>\\\\u202E</code>, <code>[U+202E]</code>, and <code>%E2%80%AE</code>. Defenders should also check their analysis tools to ensure they do not interpret the RTLO character and instead print the true name of the file containing it.\"", "Str": "Detection methods should include looking for common formats of RTLO characters within filenames such as <code>\\u202E</code>, <code>[U+202E]</code>, and <code>%E2%80%AE</code>. Defenders should also check their analysis tools to ensure they do not interpret the RTLO character and instead print the true name of the file containing it.", "Type": 3, "Index": 3158}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3559}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3096}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3528}}', 'T1036.002', 'Adversaries may use the right-to-left override (RTLO or RLO) character (U+202E) as a means of tricking a user into executing what they think is a benign file type but is actually executable code. RTLO is a non-printing character that causes the text that follows it to be displayed in reverse.(Citation: Infosecinstitute RTLO Technique) For example, a Windows screensaver executable named <code>March 25 \u202Excod.scr</code> will display as <code>March 25 rcs.docx</code>. A JavaScript file named <code>photo_high_re\u202Egnp.js</code> will be displayed as <code>photo_high_resj.png</code>.
INSERT INTO public.attack_patterns VALUES (408, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.361912+00', NULL, 'attack-pattern--0f20e3cb-245b-4a61-8a91-2d93f7cb0e9b', '[{"url": "https://attack.mitre.org/techniques/T1014", "external_id": "T1014", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/552.html", "external_id": "CAPEC-552", "source_name": "capec"}, {"url": "https://www.symantec.com/avcenter/reference/windows.rootkit.overview.pdf", "description": "Symantec. (n.d.). Windows Rootkit Overview. Retrieved December 21, 2017.", "source_name": "Symantec Windows Rootkits"}, {"url": "https://en.wikipedia.org/wiki/Rootkit", "description": "Wikipedia. (2016, June 1). Rootkit. Retrieved June 2, 2016.", "source_name": "Wikipedia Rootkit"}, {"url": "https://www.crowdstrike.com/blog/http-iframe-injecting-linux-rootkit/", "description": "Kurtz, G. (2012, November 19). HTTP iframe Injecting Linux Rootkit. Retrieved December 21, 2017.", "source_name": "CrowdStrike Linux Rootkit"}, {"url": "http://www.blackhat.com/docs/asia-14/materials/Tsai/WP-Asia-14-Tsai-You-Cant-See-Me-A-Mac-OS-X-Rootkit-Uses-The-Tricks-You-Havent-Known-Yet.pdf", "description": "Pan, M., Tsai, S. (2014). You can’t see me: A Mac OS X Rootkit uses the tricks you haven''t known yet. Retrieved December 21, 2017.", "source_name": "BlackHat Mac OSX Rootkit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3757}, "x_mitre_detection": {"Num": 0, "Raw": "\"Some rootkit protections may be built into anti-virus or operating system software. There are dedicated rootkit detection tools that look for specific types of rootkit behavior. Monitor for the existence of unrecognized DLLs, devices, services, and changes to the MBR. (Citation: Wikipedia Rootkit)\"", "Str": "Some rootkit protections may be built into anti-virus or operating system software. There are dedicated rootkit detection tools that look for specific types of rootkit behavior. Monitor for the existence of unrecognized DLLs, devices, services, and changes to the MBR. (Citation: Wikipedia Rootkit)", "Type": 3, "Index": 3217}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3679}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Drive: Drive Modification\",\n      \"Firmware: Firmware Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2834}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3550}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"File monitoring\",\n      \"Host intrusion prevention systems\",\n      \"Application control\",\n      \"Signature-based detection\",\n      \"System access controls\",\n      \"Application control by file name or path\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 2950}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3593}}', 'T1014', 'Adversaries may use rootkits to hide the presence of programs, files, network connections, services, drivers, and other system components. Rootkits are programs that hide the existence of malware by intercepting/hooking and modifying operating system API calls that supply system information. (Citation: Symantec Windows Rootkits) 
INSERT INTO public.attack_patterns VALUES (409, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.398879+00', NULL, 'attack-pattern--b5327dd1-6bf9-4785-a199-25bcbd1f4a9d', '[{"url": "https://attack.mitre.org/techniques/T1564/006", "external_id": "T1564.006", "source_name": "mitre-attack"}, {"url": "https://www.mci.gov.sg/-/media/mcicorp/doc/report-of-the-coi-into-the-cyber-attack-on-singhealth-10-jan-2019.ashx", "description": "Committee of Inquiry into the Cyber Attack on SingHealth. (2019, January 10). Public Report of the Committee of Inquiry into the Cyber Attack on Singapore Health Services Private Limited''s Patient Database. Retrieved June 29, 2020.", "source_name": "SingHealth Breach Jan 2019"}, {"url": "https://news.sophos.com/en-us/2020/05/21/ragnar-locker-ransomware-deploys-virtual-machine-to-dodge-security/", "description": "SophosLabs. (2020, May 21). Ragnar Locker ransomware deploys virtual machine to dodge security. Retrieved June 29, 2020.", "source_name": "Sophos Ragnar May 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4253}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring for files and processes associated with running a virtual instance, such as binary files associated with common virtualization technologies (ex: VirtualBox, VMware, QEMU, Hyper-V). Consider monitoring for process command-line arguments that may be atypical for benign use of virtualization software. Usage of virtualization binaries or command-line arguments associated with running a headless (in the background with no UI) virtual instance may be especially suspect. Network adapter information may also be helpful in detecting the use of virtual instances.\\n\\nIf virtualization software is installed by the adversary, the Registry may provide detection opportunities. Consider monitoring for [Windows Service](https://attack.mitre.org/techniques/T1543/003), with respect to virtualization software. \\n\\nBenign usage of virtualization technology is common in enterprise environments, data and events should not be viewed in isolation, but as part of a chain of behavior.\"", "Str": "Consider monitoring for files and processes associated with running a virtual instance, such as binary files associated with common virtualization technologies (ex: VirtualBox, VMware, QEMU, Hyper-V). Consider monitoring for process command-line arguments that may be atypical for benign use of virtualization software. Usage of virtualization binaries or command-line arguments associated with running a headless (in the background with no UI) virtual instance may be especially suspect. Network adapter information may also be helpful in detecting the use of virtual instances.\n\nIf virtualization software is installed by the adversary, the Registry may provide detection opportunities. Consider monitoring for [Windows Service](https://attack.mitre.org/techniques/T1543/003), with respect to virtualization software. \n\nBenign usage of virtualization technology is common in enterprise environments, data and events should not be viewed in isolation, but as part of a chain of behavior.", "Type": 3, "Index": 3059}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4175}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Janantha Marasinghe\",\n      \"Menachem Shafran, XM Cyber\"\n    ]", "Str": "", "Type": 5, "Index": 2729}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Command: Command Execution\",\n      \"Service: Service Creation\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2830}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4086}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4128}}', 'T1564.006', 'Adversaries may carry out malicious operations using a virtual instance to avoid detection. A wide variety of virtualization technologies exist that allow for the emulation of a computer or computing environment. By running malicious code inside of a virtual instance, adversaries can hide artifacts associated with their behavior from security tools that are unable to monitor activity inside the virtual instance. Additionally, depending on the virtual networking implementation (ex: bridged adapter), network traffic generated by the virtual instance can be difficult to trace back to the compromised host as the IP address and hostname might not match known values.(Citation: SingHealth Breach Jan 2019)
INSERT INTO public.attack_patterns VALUES (411, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.461111+00', NULL, 'attack-pattern--32ad5c86-2bcf-47d8-8fdc-d7f3d79a7490', '[{"url": "https://attack.mitre.org/techniques/T1565/003", "external_id": "T1565.003", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/apt/rpt-apt38", "description": "FireEye. (2018, October 03). APT38: Un-usual Suspects. Retrieved November 6, 2018.", "source_name": "FireEye APT38 Oct 2018"}, {"url": "https://www.justice.gov/opa/press-release/file/1092091/download", "description": "Department of Justice. (2018, September 6). Criminal Complaint - United States of America v. PARK JIN HYOK. Retrieved March 29, 2019.", "source_name": "DOJ Lazarus Sony 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3017}, "x_mitre_detection": {"Num": 0, "Raw": "\"Inspect important application binary file hashes, locations, and modifications for suspicious/unexpected values.\"", "Str": "Inspect important application binary file hashes, locations, and modifications for suspicious/unexpected values.", "Type": 3, "Index": 2596}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2939}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 2739}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"File: File Creation\",\n      \"File: File Deletion\",\n      \"File: File Modification\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2408}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2797}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2839}}', 'T1565.003', 'Adversaries may modify systems in order to manipulate the data as it is accessed and displayed to an end user.(Citation: FireEye APT38 Oct 2018)(Citation: DOJ Lazarus Sony 2018) By manipulating runtime data, adversaries may attempt to affect a business process, organizational understanding, and decision making.
INSERT INTO public.attack_patterns VALUES (414, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.522107+00', NULL, 'attack-pattern--543fceb5-cb92-40cb-aacf-6913d4db58bc', '[{"url": "https://attack.mitre.org/techniques/T1553/003", "external_id": "T1553.003", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/ms537359.aspx", "description": "Microsoft. (n.d.). Authenticode. Retrieved January 31, 2018.", "source_name": "Microsoft Authenticode"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/aa388208.aspx", "description": "Microsoft. (n.d.). WinVerifyTrust function. Retrieved January 31, 2018.", "source_name": "Microsoft WinVerifyTrust"}, {"url": "https://specterops.io/assets/resources/SpecterOps_Subverting_Trust_in_Windows.pdf", "description": "Graeber, M. (2017, September). Subverting Trust in Windows. Retrieved January 31, 2018.", "source_name": "SpectorOps Subverting Trust Sept 2017"}, {"url": "https://blogs.technet.microsoft.com/eduardonavarro/2008/07/11/sips-subject-interface-package-and-authenticode/", "description": "Navarro, E. (2008, July 11). SIP’s (Subject Interface Package) and Authenticode. Retrieved January 31, 2018.", "source_name": "EduardosBlog SIPs July 2008"}, {"url": "https://docs.microsoft.com/windows-hardware/drivers/install/catalog-files", "description": "Hudek, T. (2017, April 20). Catalog Files and Digital Signatures. Retrieved January 31, 2018.", "source_name": "Microsoft Catalog Files and Signatures April 2017"}, {"url": "https://github.com/mattifestation/PoCSubjectInterfacePackage", "description": "Graeber, M. (2017, September 14). PoCSubjectInterfacePackage. Retrieved January 31, 2018.", "source_name": "GitHub SIP POC Sept 2017"}, {"url": "http://www.entrust.net/knowledge-base/technote.cfm?tn=8165", "description": "Entrust Datacard. (2017, August 16). How do I enable CAPI 2.0 logging in Windows Vista, Windows 7 and Windows 2008 Server?. Retrieved January 31, 2018.", "source_name": "Entrust Enable CAPI2 Aug 2017"}, {"url": "https://docs.microsoft.com/previous-versions/windows/it-pro/windows-server-2012-R2-and-2012/dn311461(v=ws.11)", "description": "Microsoft. (2016, August 31). Registry (Global Object Access Auditing). Retrieved January 31, 2018.", "source_name": "Microsoft Registry Auditing Aug 2016"}, {"url": "https://docs.microsoft.com/previous-versions/windows/it-pro/windows-server-2008-R2-and-2008/dd941614(v=ws.10)", "description": "Microsoft. (2012, July 2). Audit Registry. Retrieved January 31, 2018.", "source_name": "Microsoft Audit Registry July 2012"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 10927}, "x_mitre_detection": {"Num": 0, "Raw": "\"Periodically baseline registered SIPs and trust providers (Registry entries and files on disk), specifically looking for new, modified, or non-Microsoft entries. (Citation: SpectorOps Subverting Trust Sept 2017)\\n\\nEnable CryptoAPI v2 (CAPI) event logging (Citation: Entrust Enable CAPI2 Aug 2017) to monitor and analyze error events related to failed trust validation (Event ID 81, though this event can be subverted by hijacked trust provider components) as well as any other provided information events (ex: successful validations). Code Integrity event logging may also provide valuable indicators of malicious SIP or trust provider loads, since protected processes that attempt to load a maliciously-crafted trust validation component will likely fail (Event ID 3033). (Citation: SpectorOps Subverting Trust Sept 2017)\\n\\nUtilize Sysmon detection rules and/or enable the Registry (Global Object Access Auditing) (Citation: Microsoft Registry Auditing Aug 2016) setting in the Advanced Security Audit policy to apply a global system access control list (SACL) and event auditing on modifications to Registry values (sub)keys related to SIPs and trust providers: (Citation: Microsoft Audit Registry July 2012)\\n\\n* HKLM\\\\SOFTWARE\\\\Microsoft\\\\Cryptography\\\\OID\\n* HKLM\\\\SOFTWARE\\\\WOW6432Node\\\\Microsoft\\\\Cryptography\\\\OID\\n* HKLM\\\\SOFTWARE\\\\Microsoft\\\\Cryptography\\\\Providers\\\\Trust\\n* HKLM\\\\SOFTWARE\\\\WOW6432Node\\\\Microsoft\\\\Cryptography\\\\Providers\\\\Trust\\n\\n**Note:** As part of this technique, adversaries may attempt to manually edit these Registry keys (ex: Regedit) or utilize the legitimate registration process using [Regsvr32](https://attack.mitre.org/techniques/T1218/010). (Citation: SpectorOps Subverting Trust Sept 2017)\\n\\nAnalyze Autoruns data for oddities and anomalies, specifically malicious files attempting persistent execution by hiding within auto-starting locations. Autoruns will hide entries signed by Microsoft or Windows by default, so ensure “Hide Microsoft Entries” and “Hide Windows Entries” are both deselected. (Citation: SpectorOps Subverting Trust Sept 2017)\"", "Str": "Periodically baseline registered SIPs and trust providers (Registry entries and files on disk), specifically looking for new, modified, or non-Microsoft entries. (Citation: SpectorOps Subverting Trust Sept 2017)\n\nEnable CryptoAPI v2 (CAPI) event logging (Citation: Entrust Enable CAPI2 Aug 2017) to monitor and analyze error events related to failed trust validation (Event ID 81, though this event can be subverted by hijacked trust provider components) as well as any other provided information events (ex: successful validations). Code Integrity event logging may also provide valuable indicators of malicious SIP or trust provider loads, since protected processes that attempt to load a maliciously-crafted trust validation component will likely fail (Event ID 3033). (Citation: SpectorOps Subverting Trust Sept 2017)\n\nUtilize Sysmon detection rules and/or enable the Registry (Global Object Access Auditing) (Citation: Microsoft Registry Auditing Aug 2016) setting in the Advanced Security Audit policy to apply a global system access control list (SACL) and event auditing on modifications to Registry values (sub)keys related to SIPs and trust providers: (Citation: Microsoft Audit Registry July 2012)\n\n* HKLM\\SOFTWARE\\Microsoft\\Cryptography\\OID\n* HKLM\\SOFTWARE\\WOW6432Node\\Microsoft\\Cryptography\\OID\n* HKLM\\SOFTWARE\\Microsoft\\Cryptography\\Providers\\Trust\n* HKLM\\SOFTWARE\\WOW6432Node\\Microsoft\\Cryptography\\Providers\\Trust\n\n**Note:** As part of this technique, adversaries may attempt to manually edit these Registry keys (ex: Regedit) or utilize the legitimate registration process using [Regsvr32](https://attack.mitre.org/techniques/T1218/010). (Citation: SpectorOps Subverting Trust Sept 2017)\n\nAnalyze Autoruns data for oddities and anomalies, specifically malicious files attempting persistent execution by hiding within auto-starting locations. Autoruns will hide entries signed by Microsoft or Windows by default, so ensure “Hide Microsoft Entries” and “Hide Windows Entries” are both deselected. (Citation: SpectorOps Subverting Trust Sept 2017)", "Type": 3, "Index": 8628}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 10879}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matt Graeber, @mattifestation, SpecterOps\"\n    ]", "Str": "", "Type": 5, "Index": 8209}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 8296}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 10765}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Autoruns Analysis\",\n      \"Digital Certificate Validation\",\n      \"User Mode Signature Validation\"\n    ]", "Str": "", "Type": 5, "Index": 8459}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 10807}}', 'T1553.003', 'Adversaries may tamper with SIP and trust provider components to mislead the operating system and application control tools when conducting signature validation checks. In user mode, Windows Authenticode (Citation: Microsoft Authenticode) digital signatures are used to verify a file''s origin and integrity, variables that may be used to establish trust in signed code (ex: a driver with a valid Microsoft signature may be handled as safe). The signature validation process is handled via the WinVerifyTrust application programming interface (API) function,  (Citation: Microsoft WinVerifyTrust) which accepts an inquiry and coordinates with the appropriate trust provider, which is responsible for validating parameters of a signature. (Citation: SpectorOps Subverting Trust Sept 2017)
INSERT INTO public.attack_patterns VALUES (415, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.554298+00', NULL, 'attack-pattern--4f9ca633-15c5-463c-9724-bdcd54fde541', '[{"url": "https://attack.mitre.org/techniques/T1021/002", "external_id": "T1021.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/561.html", "external_id": "CAPEC-561", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Server_Message_Block", "description": "Wikipedia. (2017, December 16). Server Message Block. Retrieved December 21, 2017.", "source_name": "Wikipedia Server Message Block"}, {"url": "https://technet.microsoft.com/en-us/library/cc787851.aspx", "description": "Microsoft. (2003, March 28). What Is RPC?. Retrieved June 12, 2016.", "source_name": "TechNet RPC"}, {"url": "http://support.microsoft.com/kb/314984", "description": "Microsoft. (n.d.). How to create and delete hidden or administrative shares on client computers. Retrieved November 20, 2014.", "source_name": "Microsoft Admin Shares"}, {"url": "https://docs.microsoft.com/en-us/archive/blogs/jepayne/tracking-lateral-movement-part-one-special-groups-and-specific-service-accounts", "description": "Payne, J. (2015, November 26). Tracking Lateral Movement Part One - Special Groups and Specific Service Accounts. Retrieved February 1, 2016.", "source_name": "Lateral Movement Payne"}, {"url": "https://docs.microsoft.com/en-us/archive/blogs/jepayne/monitoring-what-matters-windows-event-forwarding-for-everyone-even-if-you-already-have-a-siem", "description": "Payne, J. (2015, November 23). Monitoring what matters - Windows Event Forwarding for everyone (even if you already have a SIEM.). Retrieved February 1, 2016.", "source_name": "Windows Event Forwarding Payne"}, {"url": "https://medium.com/threatpunter/detecting-removing-wmi-persistence-60ccbb7dff96", "description": "French, D. (2018, October 9). Detecting & Removing an Attacker’s WMI Persistence. Retrieved October 11, 2019.", "source_name": "Medium Detecting WMI Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5999}, "x_mitre_detection": {"Num": 0, "Raw": "\"Ensure that proper logging of accounts used to log into systems is turned on and centrally collected. Windows logging is able to collect success/failure for accounts that may be used to move laterally and can be collected using tools such as Windows Event Forwarding. (Citation: Lateral Movement Payne)(Citation: Windows Event Forwarding Payne) Monitor remote login events and associated SMB activity for file transfers and remote process execution. Monitor the actions of remote users who connect to administrative shares. Monitor for use of tools and commands to connect to remote shares, such as [Net](https://attack.mitre.org/software/S0039), on the command-line interface and Discovery techniques that could be used to find remotely accessible systems.(Citation: Medium Detecting WMI Persistence)\"", "Str": "Ensure that proper logging of accounts used to log into systems is turned on and centrally collected. Windows logging is able to collect success/failure for accounts that may be used to move laterally and can be collected using tools such as Windows Event Forwarding. (Citation: Lateral Movement Payne)(Citation: Windows Event Forwarding Payne) Monitor remote login events and associated SMB activity for file transfers and remote process execution. Monitor the actions of remote users who connect to administrative shares. Monitor for use of tools and commands to connect to remote shares, such as [Net](https://attack.mitre.org/software/S0039), on the command-line interface and Discovery techniques that could be used to find remotely accessible systems.(Citation: Medium Detecting WMI Persistence)", "Type": 3, "Index": 4765}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5713}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Logon Session: Logon Session Creation\",\n      \"Network Share: Network Share Access\"\n    ]", "Str": "", "Type": 5, "Index": 4503}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5601}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"SMB enabled; Host/network firewalls not blocking SMB ports between source and destination; Use of domain account in administrator group on remote system or default system admin account.\"\n    ]", "Str": "", "Type": 5, "Index": 5773}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5643}}', 'T1021.002', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to interact with a remote network share using Server Message Block (SMB). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (417, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.614882+00', NULL, 'attack-pattern--f9e9365a-9ca2-4d9c-8e7c-050d73d1101a', '[{"url": "https://attack.mitre.org/techniques/T1505/001", "external_id": "T1505.001", "source_name": "mitre-attack"}, {"url": "https://blog.netspi.com/sql-server-persistence-part-1-startup-stored-procedures/", "description": "Sutherland, S. (2016, March 7). Maintaining Persistence via SQL Server – Part 1: Startup Stored Procedures. Retrieved July 8, 2019.", "source_name": "NetSPI Startup Stored Procedures"}, {"url": "https://securelist.com/malicious-tasks-in-ms-sql-server/92167/", "description": "Plakhov, A., Sitchikhin, D. (2019, August 22). Agent 1433: remote attack on Microsoft SQL Server. Retrieved September 4, 2019.", "source_name": "Kaspersky MSSQL Aug 2019"}, {"url": "https://docs.microsoft.com/en-us/sql/relational-databases/system-stored-procedures/xp-cmdshell-transact-sql?view=sql-server-2017", "description": "Microsoft. (2017, March 15). xp_cmdshell (Transact-SQL). Retrieved September 9, 2019.", "source_name": "Microsoft xp_cmdshell 2017"}, {"url": "https://docs.microsoft.com/en-us/sql/relational-databases/clr-integration/common-language-runtime-integration-overview?view=sql-server-2017", "description": "Microsoft. (2017, June 19). Common Language Runtime Integration. Retrieved July 8, 2019.", "source_name": "Microsoft CLR Integration 2017"}, {"url": "https://blog.netspi.com/attacking-sql-server-clr-assemblies/", "description": "Sutherland, S. (2017, July 13). Attacking SQL Server CLR Assemblies. Retrieved July 8, 2019.", "source_name": "NetSPI SQL Server CLR"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4370}, "x_mitre_detection": {"Num": 0, "Raw": "\"On a MSSQL Server, consider monitoring for xp_cmdshell usage.(Citation: NetSPI Startup Stored Procedures) Consider enabling audit features that can log malicious startup activities.\"", "Str": "On a MSSQL Server, consider monitoring for xp_cmdshell usage.(Citation: NetSPI Startup Stored Procedures) Consider enabling audit features that can log malicious startup activities.", "Type": 3, "Index": 3963}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4307}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Carlos Borges, @huntingneo, CIP\",\n      \"Lucas da Silva Pereira, @vulcanunsec, CIP\",\n      \"Kaspersky\"\n    ]", "Str": "", "Type": 5, "Index": 3733}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3880}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4179}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4221}}', 'T1505.001', 'Adversaries may abuse SQL stored procedures to establish persistent access to systems. SQL Stored Procedures are code that can be saved and reused so that database users do not waste time rewriting frequently used SQL queries. Stored procedures can be invoked via SQL statements to the database using the procedure name or via defined events (e.g. when a SQL server application is started/restarted).
INSERT INTO public.attack_patterns VALUES (419, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.674664+00', NULL, 'attack-pattern--6b57dc31-b814-4a03-8706-28bc20d739c4', '[{"url": "https://attack.mitre.org/techniques/T1098/004", "external_id": "T1098.004", "source_name": "mitre-attack"}, {"url": "https://www.ssh.com/ssh/authorized_keys/", "description": "ssh.com. (n.d.). Authorized_keys File in SSH. Retrieved June 24, 2020.", "source_name": "SSH Authorized Keys"}, {"url": "https://www.venafi.com/blog/growing-abuse-ssh-keys-commodity-malware-campaigns-now-equipped-ssh-capabilities", "description": "Blachman, Y. (2020, April 22). Growing Abuse of SSH Keys: Commodity Malware Campaigns Now Equipped with SSH Capabilities. Retrieved June 24, 2020.", "source_name": "Venafi SSH Key Abuse"}, {"url": "https://www.cybereason.com/blog/new-pervasive-worm-exploiting-linux-exim-server-vulnerability", "description": "Cybereason Nocturnus. (2019, June 13). New Pervasive Worm Exploiting Linux Exim Server Vulnerability. Retrieved June 24, 2020.", "source_name": "Cybereason Linux Exim Worm"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3629}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use file integrity monitoring to detect changes made to the <code>authorized_keys</code> file for each user on a system. Monitor for suspicious processes modifying the <code>authorized_keys</code> file.\\n\\nMonitor for changes to and suspicious processes modifiying <code>/etc/ssh/sshd_config</code>.\"", "Str": "Use file integrity monitoring to detect changes made to the <code>authorized_keys</code> file for each user on a system. Monitor for suspicious processes modifying the <code>authorized_keys</code> file.\n\nMonitor for changes to and suspicious processes modifiying <code>/etc/ssh/sshd_config</code>.", "Type": 3, "Index": 3122}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3568}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tony Lambert, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 2915}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2985}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3456}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3498}}', 'T1098.004', 'Adversaries may modify the SSH <code>authorized_keys</code> file to maintain persistence on a victim host. Linux distributions and macOS commonly use key-based authentication to secure the authentication process of SSH sessions for remote management. The <code>authorized_keys</code> file in SSH specifies the SSH keys that can be used for logging into the user account for which the file is configured. This file is usually found in the user''s home directory under <code>&lt;user-home&gt;/.ssh/authorized_keys</code>.(Citation: SSH Authorized Keys) Users may edit the system’s SSH config file to modify the directives PubkeyAuthentication and RSAAuthentication to the value “yes” to ensure public key and RSA authentication are enabled. The SSH config file is usually located under <code>/etc/ssh/sshd_config</code>.
INSERT INTO public.attack_patterns VALUES (421, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.731038+00', NULL, 'attack-pattern--ec4be82f-940c-4dcb-87fe-2bbdd17c692f', '[{"url": "https://attack.mitre.org/techniques/T1596/005", "external_id": "T1596.005", "source_name": "mitre-attack"}, {"url": "https://shodan.io", "description": "Shodan. (n.d.). Shodan. Retrieved October 20, 2020.", "source_name": "Shodan"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2690}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2249}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2646}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2615}}', 'T1596.005', 'Adversaries may search within public scan databases for information about victims that can be used during targeting. Various online services continuously publish the results of Internet scans/surveys, often harvesting information such as active IP addresses, hostnames, open ports, certificates, and even server banners.(Citation: Shodan)
INSERT INTO public.attack_patterns VALUES (422, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.76056+00', NULL, 'attack-pattern--db8f5003-3b20-48f0-9b76-123e44208120', '[{"url": "https://attack.mitre.org/techniques/T1595/001", "external_id": "T1595.001", "source_name": "mitre-attack"}, {"url": "https://www.caida.org/publications/papers/2012/analysis_slash_zero/analysis_slash_zero.pdf", "description": "Dainotti, A. et al. (2012). Analysis of a “/0” Stealth Scan from a Botnet. Retrieved October 20, 2020.", "source_name": "Botnet Scan"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3037}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet).\\n\\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet).\n\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2379}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2993}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2299}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2962}}', 'T1595.001', 'Adversaries may scan victim IP blocks to gather information that can be used during targeting. Public IP addresses may be allocated to organizations by block, or a range of sequential addresses.
INSERT INTO public.attack_patterns VALUES (424, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.825675+00', NULL, 'attack-pattern--35dd844a-b219-4e2b-a6bb-efa9a75995a9', '[{"url": "https://attack.mitre.org/techniques/T1053", "external_id": "T1053", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/557.html", "external_id": "CAPEC-557", "source_name": "capec"}, {"url": "https://technet.microsoft.com/en-us/library/cc785125.aspx", "description": "Microsoft. (2005, January 21). Task Scheduler and security. Retrieved June 8, 2016.", "source_name": "TechNet Task Scheduler Security"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 3852}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \\n\\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \n\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 2741}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3718}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Prashant Verma, Paladion\",\n      \"Leo Loobeek, @leoloobeek\",\n      \"Travis Smith, Tripwire\",\n      \"Alain Homewood, Insomnia Security\"\n    ]", "Str": "", "Type": 5, "Index": 2310}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Container: Container Creation\",\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2489}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3823}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3589}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3632}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3497}}', 'T1053', 'Adversaries may abuse task scheduling functionality to facilitate initial or recurring execution of malicious code. Utilities exist within all major operating systems to schedule programs or scripts to be executed at a specified date and time. A task can also be scheduled on a remote system, provided the proper authentication is met (ex: RPC and file and printer sharing in Windows environments). Scheduling a task on a remote system typically requires being a member of an admin or otherwise privileged group on the remote system.(Citation: TechNet Task Scheduler Security)
INSERT INTO public.attack_patterns VALUES (425, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.868217+00', NULL, 'attack-pattern--4eeaf8a9-c86b-4954-a663-9555fb406466', '[{"url": "https://attack.mitre.org/techniques/T1029", "external_id": "T1029", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1947}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process file access patterns and network behavior. Unrecognized processes or scripts that appear to be traversing file systems and sending network traffic may be suspicious. Network connections to the same destination that occur at the same time of day for multiple days are suspicious.\"", "Str": "Monitor process file access patterns and network behavior. Unrecognized processes or scripts that appear to be traversing file systems and sending network traffic may be suspicious. Network connections to the same destination that occur at the same time of day for multiple days are suspicious.", "Type": 3, "Index": 1466}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1869}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 1332}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1795}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1838}}', 'T1029', 'Adversaries may schedule data exfiltration to be performed only at certain times of day or at certain intervals. This could be done to blend traffic patterns with normal activity or availability.
INSERT INTO public.attack_patterns VALUES (427, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.935324+00', NULL, 'attack-pattern--ce4b7013-640e-48a9-b501-d0025a95f4bf', '[{"url": "https://attack.mitre.org/techniques/T1546/002", "external_id": "T1546.002", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Screensaver", "description": "Wikipedia. (2017, November 22). Screensaver. Retrieved December 5, 2017.", "source_name": "Wikipedia Screensaver"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2017/08/eset-gazer.pdf", "description": "ESET. (2017, August). Gazing at Gazer: Turla’s new second stage backdoor. Retrieved September 14, 2017.", "source_name": "ESET Gazer Aug 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3533}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process execution and command-line parameters of .scr files. Monitor changes to screensaver configuration changes in the Registry that may not correlate with typical user behavior.\\n\\nTools such as Sysinternals Autoruns can be used to detect changes to the screensaver binary path in the Registry. Suspicious paths and PE files may indicate outliers among legitimate screensavers in a network and should be investigated.\"", "Str": "Monitor process execution and command-line parameters of .scr files. Monitor changes to screensaver configuration changes in the Registry that may not correlate with typical user behavior.\n\nTools such as Sysinternals Autoruns can be used to detect changes to the screensaver binary path in the Registry. Suspicious paths and PE files may indicate outliers among legitimate screensavers in a network and should be investigated.", "Type": 3, "Index": 2933}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3485}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bartosz Jerzman\"\n    ]", "Str": "", "Type": 5, "Index": 2645}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2706}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3396}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3438}}', 'T1546.002', 'Adversaries may establish persistence by executing malicious content triggered by user inactivity. Screensavers are programs that execute after a configurable time of user inactivity and consist of Portable Executable (PE) files with a .scr file extension.(Citation: Wikipedia Screensaver) The Windows screensaver application scrnsave.scr is located in <code>C:\Windows\System32\</code>, and <code>C:\Windows\sysWOW64\</code>  on 64-bit Windows systems, along with screensavers included with base Windows installations.
INSERT INTO public.attack_patterns VALUES (429, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.001175+00', NULL, 'attack-pattern--6e561441-8431-4773-a9b8-ccf28ef6a968', '[{"url": "https://attack.mitre.org/techniques/T1593/002", "external_id": "T1593.002", "source_name": "mitre-attack"}, {"url": "https://securitytrails.com/blog/google-hacking-techniques", "description": "Borges, E. (2019, March 5). Exploring Google Hacking Techniques. Retrieved October 20, 2020.", "source_name": "SecurityTrails Google Hacking"}, {"url": "https://www.exploit-db.com/google-hacking-database", "description": "Offensive Security. (n.d.). Google Hacking Database. Retrieved October 23, 2020.", "source_name": "ExploitDB GoogleHacking"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3043}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2602}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2999}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2968}}', 'T1593.002', 'Adversaries may use search engines to collect information about victims that can be used during targeting. Search engine services typical crawl online sites to index context and may provide users with specialized syntax to search for specific keywords or specific types of content (i.e. filetypes).(Citation: SecurityTrails Google Hacking)(Citation: ExploitDB GoogleHacking)
INSERT INTO public.attack_patterns VALUES (430, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.042516+00', NULL, 'attack-pattern--55fc4df0-b42c-479a-b860-7a6761bcaad0', '[{"url": "https://attack.mitre.org/techniques/T1596", "external_id": "T1596", "source_name": "mitre-attack"}, {"url": "https://www.whois.net/", "description": "NTT America. (n.d.). Whois Lookup. Retrieved October 20, 2020.", "source_name": "WHOIS"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}, {"url": "https://medium.com/@menakajain/export-download-ssl-certificate-from-server-site-url-bcfc41ea46a2", "description": "Jain, M. (2019, September 16). Export & Download — SSL Certificate from Server (Site URL). Retrieved October 20, 2020.", "source_name": "Medium SSL Cert"}, {"url": "https://www.sslshopper.com/ssl-checker.html", "description": "SSL Shopper. (n.d.). SSL Checker. Retrieved October 20, 2020.", "source_name": "SSLShopper Lookup"}, {"url": "https://www.digitalshadows.com/blog-and-research/content-delivery-networks-cdns-can-leave-you-exposed-how-you-might-be-affected-and-what-you-can-do-about-it/", "description": "Swisscom & Digital Shadows. (2017, September 6). Content Delivery Networks (CDNs) Can Leave You Exposed – How You Might Be Affected And What You Can Do About It. Retrieved October 20, 2020.", "source_name": "DigitalShadows CDN"}, {"url": "https://shodan.io", "description": "Shodan. (n.d.). Shodan. Retrieved October 20, 2020.", "source_name": "Shodan"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4252}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 3810}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4208}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4176}}', 'T1596', 'Adversaries may search freely available technical databases for information about victims that can be used during targeting. Information about victims may be available in online databases and repositories, such as registrations of domains/certificates as well as public collections of network data/artifacts gathered from traffic and/or scans.(Citation: WHOIS)(Citation: DNS Dumpster)(Citation: Circl Passive DNS)(Citation: Medium SSL Cert)(Citation: SSLShopper Lookup)(Citation: DigitalShadows CDN)(Citation: Shodan)
INSERT INTO public.attack_patterns VALUES (433, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.162766+00', NULL, 'attack-pattern--1644e709-12d2-41e5-a60f-3470991f5011', '[{"url": "https://attack.mitre.org/techniques/T1003/002", "external_id": "T1003.002", "source_name": "mitre-attack"}, {"url": "https://github.com/Neohapsis/creddump7", "description": "Flathers, R. (2018, February 19). creddump7. Retrieved April 11, 2018.", "source_name": "GitHub Creddump7"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3046}, "x_mitre_detection": {"Num": 0, "Raw": "\"Hash dumpers open the Security Accounts Manager (SAM) on the local file system (<code>%SystemRoot%/system32/config/SAM</code>) or create a dump of the Registry SAM key to access stored account password hashes. Some hash dumpers will open the local file system as a device and parse to the SAM table to avoid file access defenses. Others will make an in-memory copy of the SAM table before reading hashes. Detection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well.\"", "Str": "Hash dumpers open the Security Accounts Manager (SAM) on the local file system (<code>%SystemRoot%/system32/config/SAM</code>) or create a dump of the Registry SAM key to access stored account password hashes. Some hash dumpers will open the local file system as a device and parse to the SAM table to avoid file access defenses. Others will make an in-memory copy of the SAM table before reading hashes. Detection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well.", "Type": 3, "Index": 2343}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2998}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 2112}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Access\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 2192}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2907}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2949}}', 'T1003.002', 'Adversaries may attempt to extract credential material from the Security Account Manager (SAM) database either through in-memory techniques or through the Windows Registry where the SAM database is stored. The SAM is a database file that contains local accounts for the host, typically those found with the <code>net user</code> command. Enumerating the SAM database requires SYSTEM level access.
INSERT INTO public.attack_patterns VALUES (435, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.222757+00', NULL, 'attack-pattern--5095a853-299c-4876-abd7-ac0050fb5462', '[{"url": "https://attack.mitre.org/techniques/T1547/005", "external_id": "T1547.005", "source_name": "mitre-attack"}, {"url": "http://docplayer.net/20839173-Analysis-of-malicious-security-support-provider-dlls.html", "description": "Graeber, M. (2014, October). Analysis of Malicious Security Support Provider DLLs. Retrieved March 1, 2017.", "source_name": "Graeber 2014"}, {"url": "https://technet.microsoft.com/en-us/library/dn408187.aspx", "description": "Microsoft. (2013, July 31). Configuring Additional LSA Protection. Retrieved June 24, 2015.", "source_name": "Microsoft Configure LSA"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3077}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the Registry for changes to the SSP Registry keys. Monitor the LSA process for DLL loads. Windows 8.1 and Windows Server 2012 R2 may generate events when unsigned SSP DLLs try to load into the LSA by setting the Registry key <code>HKLM\\\\SOFTWARE\\\\Microsoft\\\\Windows NT\\\\CurrentVersion\\\\Image File Execution Options\\\\LSASS.exe</code> with AuditLevel = 8. (Citation: Graeber 2014) (Citation: Microsoft Configure LSA)\"", "Str": "Monitor the Registry for changes to the SSP Registry keys. Monitor the LSA process for DLL loads. Windows 8.1 and Windows Server 2012 R2 may generate events when unsigned SSP DLLs try to load into the LSA by setting the Registry key <code>HKLM\\SOFTWARE\\Microsoft\\Windows NT\\CurrentVersion\\Image File Execution Options\\LSASS.exe</code> with AuditLevel = 8. (Citation: Graeber 2014) (Citation: Microsoft Configure LSA)", "Type": 3, "Index": 2474}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3029}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2315}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2931}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2973}}', 'T1547.005', 'Adversaries may abuse security support providers (SSPs) to execute DLLs when the system boots. Windows SSP DLLs are loaded into the Local Security Authority (LSA) process at system start. Once loaded into the LSA, SSP DLLs have access to encrypted and plaintext passwords that are stored in Windows, such as any logged-on user''s Domain password or smart card PINs.
INSERT INTO public.attack_patterns VALUES (436, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.281362+00', NULL, 'attack-pattern--1a80d097-54df-41d8-9d33-34e755ec5e72', '[{"url": "https://attack.mitre.org/techniques/T1555/002", "external_id": "T1555.002", "source_name": "mitre-attack"}, {"url": "http://juusosalonen.com/post/30923743427/breaking-into-the-os-x-keychain", "description": "Juuso Salonen. (2012, September 5). Breaking into the OS X keychain. Retrieved July 15, 2017.", "source_name": "OS X Keychain"}, {"url": "https://www.welivesecurity.com/2016/07/06/new-osxkeydnap-malware-hungry-credentials/", "description": "Marc-Etienne M.Leveille. (2016, July 6). New OSX/Keydnap malware is hungry for credentials. Retrieved July 3, 2017.", "source_name": "OSX Keydnap malware"}, {"url": "http://www.slideshare.net/StephanBorosh/external-to-da-the-os-x-way", "description": "Alex Rymdeko-Harvey, Steve Borosh. (2016, May 14). External to DA, the OS X Way. Retrieved July 3, 2017.", "source_name": "External to DA, the OS X Way"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3209}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for activity surrounded users searching for credentials or using automated tools to scan memory for passwords.\"", "Str": "Monitor processes and command-line arguments for activity surrounded users searching for credentials or using automated tools to scan memory for passwords.", "Type": 3, "Index": 2869}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3148}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 2767}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3059}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3101}}', 'T1555.002', 'An adversary may obtain root access (allowing them to read securityd’s memory), then they can scan through memory to find the correct sequence of keys in relatively few tries to decrypt the user’s logon keychain. This provides the adversary with all the plaintext passwords for users, WiFi, mail, browsers, certificates, secure notes, etc.(Citation: OS X Keychain) (Citation: OSX Keydnap malware)
INSERT INTO public.attack_patterns VALUES (441, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.315255+00', NULL, 'attack-pattern--38eb0c22-6caf-46ce-8869-5964bd735858', '[{"url": "https://attack.mitre.org/techniques/T1499/002", "external_id": "T1499.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/488.html", "external_id": "CAPEC-488", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/489.html", "external_id": "CAPEC-489", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/528.html", "external_id": "CAPEC-528", "source_name": "capec"}, {"url": "https://pages.arbornetworks.com/rs/082-KNA-087/images/13th_Worldwide_Infrastructure_Security_Report.pdf", "description": "Philippe Alcoy, Steinthor Bjarnason, Paul Bowen, C.F. Chui, Kirill Kasavchnko, and Gary Sockrider of Netscout Arbor. (2018, January). Insight into the Global Threat Landscape - Netscout Arbor''s 13th Annual Worldwide Infrastructure Security Report. Retrieved April 22, 2019.", "source_name": "Arbor AnnualDoSreport Jan 2018"}, {"url": "https://www.cloudflare.com/learning/ddos/http-flood-ddos-attack/", "description": "Cloudflare. (n.d.). What is an HTTP flood DDoS attack?. Retrieved April 22, 2019.", "source_name": "Cloudflare HTTPflood"}, {"url": "https://www.netscout.com/blog/asert/ddos-attacks-ssl-something-old-something-new", "description": "ASERT Team, Netscout Arbor. (2012, April 24). DDoS Attacks on SSL: Something Old, Something New. Retrieved April 22, 2019.", "source_name": "Arbor SSLDoS April 2012"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 5659}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\\n\\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.\\n\\nExternally monitor the availability of services that may be targeted by an Endpoint DoS.\"", "Str": "Detection of Endpoint DoS can sometimes be achieved before the effect is sufficient to cause significant impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness. Typical network throughput monitoring tools such as netflow, SNMP, and custom scripts can be used to detect sudden increases in circuit utilization.(Citation: Cisco DoSdetectNetflow) Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect an attack as it starts.\n\nIn addition to network level detections, endpoint logging and instrumentation can be useful for detection. Attacks targeting web applications may generate logs in the web server, application server, and/or database server that can be used to identify the type of attack, possibly before the impact is felt.\n\nExternally monitor the availability of services that may be targeted by an Endpoint DoS.", "Type": 3, "Index": 4380}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 5489}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 5397}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 4164}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5458}}', 'T1499.002', 'Adversaries may target the different network services provided by systems to conduct a DoS. Adversaries often target DNS and web services, however others have been targeted as well.(Citation: Arbor AnnualDoSreport Jan 2018) Web server software can be attacked through a variety of means, some of which apply generally while others are specific to the software being used to provide the service.
INSERT INTO public.attack_patterns VALUES (438, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.39324+00', NULL, 'attack-pattern--e196b5c5-8118-4a1c-ab8a-936586ce3db5', '[{"url": "https://attack.mitre.org/techniques/T1584/004", "external_id": "T1584.004", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1855}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 1497}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 1811}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1780}}', 'T1584.004', 'Adversaries may compromise third-party servers that can be used during targeting. Use of servers allows an adversary to stage, launch, and execute an operation. During post-compromise activity, adversaries may utilize servers for various tasks, including for Command and Control. Instead of purchasing a [Server](https://attack.mitre.org/techniques/T1583/004) or [Virtual Private Server](https://attack.mitre.org/techniques/T1583/003), adversaries may compromise third-party servers in support of operations.
INSERT INTO public.attack_patterns VALUES (439, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.424209+00', NULL, 'attack-pattern--d456de47-a16f-4e46-8980-e67478a12dcb', '[{"url": "https://attack.mitre.org/techniques/T1505", "external_id": "T1505", "source_name": "mitre-attack"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA15-314A", "description": "US-CERT. (2015, November 13). Compromised Web Servers and Web Shells - Threat Awareness and Guidance. Retrieved June 8, 2016.", "source_name": "US-CERT Alert TA15-314A Web Shells"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2648}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring application logs for abnormal behavior that may indicate suspicious installation of application software components. Consider monitoring file locations associated with the installation of new application software components such as paths from which applications typically load such extensible components.\\n\\nProcess monitoring may be used to detect servers components that perform suspicious actions such as running cmd.exe or accessing files. Log authentication attempts to the server and any unusual traffic patterns to or from the server and internal network. (Citation: US-CERT Alert TA15-314A Web Shells) \"", "Str": "Consider monitoring application logs for abnormal behavior that may indicate suspicious installation of application software components. Consider monitoring file locations associated with the installation of new application software components such as paths from which applications typically load such extensible components.\n\nProcess monitoring may be used to detect servers components that perform suspicious actions such as running cmd.exe or accessing files. Log authentication attempts to the server and any unusual traffic patterns to or from the server and internal network. (Citation: US-CERT Alert TA15-314A Web Shells) ", "Type": 3, "Index": 1776}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2570}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1499}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2441}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2484}}', 'T1505', 'Adversaries may abuse legitimate extensible development features of servers to establish persistent access to systems. Enterprise server applications may include features that allow developers to write and install software or scripts to extend the functionality of the main application. Adversaries may install malicious components to extend and abuse server applications.', 'Server Software Component', 0);
INSERT INTO public.attack_patterns VALUES (440, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.466998+00', NULL, 'attack-pattern--f1951e8a-500e-4a26-8803-76d95c4554b4', '[{"url": "https://attack.mitre.org/techniques/T1569/002", "external_id": "T1569.002", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/windows/win32/services/service-control-manager", "description": "Microsoft. (2018, May 31). Service Control Manager. Retrieved March 28, 2020.", "source_name": "Microsoft Service Control Manager"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb897553.aspx", "description": "Russinovich, M. (2014, May 2). Windows Sysinternals PsExec v2.11. Retrieved May 13, 2015.", "source_name": "Russinovich Sysinternals"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3267}, "x_mitre_detection": {"Num": 0, "Raw": "\"Changes to service Registry entries and command line invocation of tools capable of modifying services that do not correlate with known software, patch cycles, etc., may be suspicious. If a service is used only to execute a binary or script and not to persist, then it will likely be changed back to its original form shortly after the service is restarted so the service is not left broken, as is the case with the common administrator tool [PsExec](https://attack.mitre.org/software/S0029).\"", "Str": "Changes to service Registry entries and command line invocation of tools capable of modifying services that do not correlate with known software, patch cycles, etc., may be suspicious. If a service is used only to execute a binary or script and not to persist, then it will likely be changed back to its original form shortly after the service is restarted so the service is not left broken, as is the case with the common administrator tool [PsExec](https://attack.mitre.org/software/S0029).", "Type": 3, "Index": 2542}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3183}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Service: Service Creation\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2342}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3238}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3069}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3111}}', 'T1569.002', 'Adversaries may abuse the Windows service control manager to execute malicious commands or payloads. The Windows service control manager (<code>services.exe</code>) is an interface to manage and manipulate services.(Citation: Microsoft Service Control Manager) The service control manager is accessible to users via GUI components as well as system utilities such as <code>sc.exe</code> and [Net](https://attack.mitre.org/software/S0039).
INSERT INTO public.attack_patterns VALUES (457, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.492983+00', NULL, 'attack-pattern--e3b6daca-e963-4a69-aee6-ed4fd653ad58', '[{"url": "https://attack.mitre.org/techniques/T1518", "external_id": "T1518", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/580.html", "external_id": "CAPEC-580", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 2954}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1877}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 2784}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Firewall: Firewall Metadata\",\n      \"Firewall: Firewall Enumeration\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1661}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2671}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2714}}', 'T1518', 'Adversaries may attempt to get a listing of software and software versions that are installed on a system or in a cloud environment. Adversaries may use the information from [Software Discovery](https://attack.mitre.org/techniques/T1518) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.
INSERT INTO public.attack_patterns VALUES (443, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.56522+00', NULL, 'attack-pattern--9e8b28c9-35fe-48ac-a14d-e6cc032dcbcd', '[{"url": "https://attack.mitre.org/techniques/T1574/010", "external_id": "T1574.010", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/17.html", "external_id": "CAPEC-17", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3435}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\\n\\nLook for abnormal process call trees from typical processes and services and for execution of other commands that could relate to Discovery or other adversary techniques. \"", "Str": "Look for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\n\nLook for abnormal process call trees from typical processes and services and for execution of other commands that could relate to Discovery or other adversary techniques. ", "Type": 3, "Index": 2601}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3387}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 2344}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Service: Service Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2436}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3275}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3317}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3183}}', 'T1574.010', 'Adversaries may execute their own malicious payloads by hijacking the binaries used by services. Adversaries may use flaws in the permissions of Windows services to replace the binary that is executed upon service start. These service processes may automatically execute specific binaries as part of their functionality or to perform other actions. If the permissions on the file system directory containing a target binary, or permissions on the binary itself are improperly set, then the target binary may be overwritten with another binary using user-level permissions and executed by the original process. If the original process and thread are running under a higher permissions level, then the replaced binary will also execute under higher-level permissions, which could include SYSTEM.
INSERT INTO public.attack_patterns VALUES (444, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.596701+00', NULL, 'attack-pattern--17cc750b-e95b-4d7d-9dde-49e0de24148c', '[{"url": "https://attack.mitre.org/techniques/T1574/011", "external_id": "T1574.011", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/478.html", "external_id": "CAPEC-478", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/sysinfo/registry-key-security-and-access-rights?redirectedfrom=MSDN", "description": "Microsoft. (2018, May 31). Registry Key Security and Access Rights. Retrieved March 16, 2017.", "source_name": "Registry Key Security"}, {"url": "https://trustedsignal.blogspot.com/2014/05/kansa-service-related-collectors-and.html", "description": "Hull, D.. (2014, May 3). Kansa: Service related collectors and analysis. Retrieved October 10, 2019.", "source_name": "Kansa Service related collectors"}, {"url": "https://twitter.com/r0wdy_/status/936365549553991680", "description": "@r0wdy_. (2017, November 30). Service Recovery Parameters. Retrieved April 9, 2018.", "source_name": "Tweet Registry Perms Weakness"}, {"url": "https://docs.microsoft.com/en-us/sysinternals/downloads/autoruns", "description": "Mark Russinovich. (2019, June 28). Autoruns for Windows v13.96. Retrieved March 13, 2020.", "source_name": "Autoruns for Windows"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6233}, "x_mitre_detection": {"Num": 0, "Raw": "\"Service changes are reflected in the Registry. Modification to existing services should not occur frequently. If a service binary path or failure parameters are changed to values that are not typical for that service and does not correlate with software updates, then it may be due to malicious activity. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current service information. (Citation: Autoruns for Windows) Look for changes to services that do not correlate with known software, patch cycles, etc. Suspicious program execution through services may show up as outlier processes that have not been seen before when compared against historical data.\\n\\nMonitor processes and command-line arguments for actions that could be done to modify services. Remote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Services may also be changed through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), so additional logging may need to be configured to gather the appropriate data.\"", "Str": "Service changes are reflected in the Registry. Modification to existing services should not occur frequently. If a service binary path or failure parameters are changed to values that are not typical for that service and does not correlate with software updates, then it may be due to malicious activity. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current service information. (Citation: Autoruns for Windows) Look for changes to services that do not correlate with known software, patch cycles, etc. Suspicious program execution through services may show up as outlier processes that have not been seen before when compared against historical data.\n\nMonitor processes and command-line arguments for actions that could be done to modify services. Remote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Services may also be changed through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), so additional logging may need to be configured to gather the appropriate data.", "Type": 3, "Index": 4421}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6185}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 4045}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Service: Service Metadata\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4152}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6073}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 4359}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6115}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 6018}}', 'T1574.011', 'Adversaries may execute their own malicious payloads by hijacking the Registry entries used by services. Adversaries may use flaws in the permissions for registry to redirect from the originally specified executable to one that they control, in order to launch their own code at Service start.  Windows stores local service configuration information in the Registry under <code>HKLM\SYSTEM\CurrentControlSet\Services</code>. The information stored under a service''s Registry keys can be manipulated to modify a service''s execution parameters through tools such as the service controller, sc.exe,  [PowerShell](https://attack.mitre.org/techniques/T1059/001), or [Reg](https://attack.mitre.org/software/S0075). Access to Registry keys is controlled through Access Control Lists and permissions. (Citation: Registry Key Security)
INSERT INTO public.attack_patterns VALUES (445, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.629331+00', NULL, 'attack-pattern--6831414d-bb70-42b7-8030-d4e06b2660c9', '[{"url": "https://attack.mitre.org/techniques/T1548/001", "external_id": "T1548.001", "source_name": "mitre-attack"}, {"url": "http://man7.org/linux/man-pages/man2/setuid.2.html", "description": "Michael Kerrisk. (2017, September 15). Linux Programmer''s Manual. Retrieved September 21, 2018.", "source_name": "setuid man page"}, {"url": "https://www.welivesecurity.com/2016/07/06/new-osxkeydnap-malware-hungry-credentials/", "description": "Marc-Etienne M.Leveille. (2016, July 6). New OSX/Keydnap malware is hungry for credentials. Retrieved July 3, 2017.", "source_name": "OSX Keydnap malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3297}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the file system for files that have the setuid or setgid bits set. Monitor for execution of utilities, like chmod, and their command-line arguments to look for setuid or setguid bits being set.\"", "Str": "Monitor the file system for files that have the setuid or setgid bits set. Monitor for execution of utilities, like chmod, and their command-line arguments to look for setuid or setguid bits being set.", "Type": 3, "Index": 2911}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3236}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Metadata\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2780}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3147}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3189}}', 'T1548.001', 'An adversary may perform shell escapes or exploit vulnerabilities in an application with the setsuid or setgid bits to get code running in a different user’s context. On Linux or macOS, when the setuid or setgid bits are set for an application, the application will run with the privileges of the owning user or group respectively. (Citation: setuid man page). Normally an application is run in the current user’s context, regardless of which user or group owns the application. However, there are instances where programs need to be executed in an elevated context to function properly, but the user running them doesn’t need the elevated privileges.
INSERT INTO public.attack_patterns VALUES (446, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.661344+00', NULL, 'attack-pattern--0a5231ec-41af-4a35-83d0-6bdf11f28c65', '[{"url": "https://attack.mitre.org/techniques/T1129", "external_id": "T1129", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Microsoft_Windows_library_files", "description": "Wikipedia. (2017, January 31). Microsoft Windows library files. Retrieved February 13, 2017.", "source_name": "Wikipedia Windows Library Files"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3652}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring DLL module loads may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances, since benign use of Windows modules load functions are common and may be difficult to distinguish from malicious behavior. Legitimate software will likely only need to load routine, bundled DLL modules or Windows system DLLs such that deviation from known module loads may be suspicious. Limiting DLL module loads to <code>%SystemRoot%</code> and <code>%ProgramFiles%</code> directories will protect against module loads from unsafe paths. \\n\\nCorrelation of other events with behavior surrounding module loads using API monitoring and suspicious DLLs written to disk will provide additional context to an event that may assist in determining if it is due to malicious behavior.\"", "Str": "Monitoring DLL module loads may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances, since benign use of Windows modules load functions are common and may be difficult to distinguish from malicious behavior. Legitimate software will likely only need to load routine, bundled DLL modules or Windows system DLLs such that deviation from known module loads may be suspicious. Limiting DLL module loads to <code>%SystemRoot%</code> and <code>%ProgramFiles%</code> directories will protect against module loads from unsafe paths. \n\nCorrelation of other events with behavior surrounding module loads using API monitoring and suspicious DLLs written to disk will provide additional context to an event that may assist in determining if it is due to malicious behavior.", "Type": 3, "Index": 2641}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3604}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 2484}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2544}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3514}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3557}}', 'T1129', 'Adversaries may abuse shared modules to execute malicious payloads. The Windows module loader can be instructed to load DLLs from arbitrary local paths and arbitrary Universal Naming Convention (UNC) network paths. This functionality resides in NTDLL.dll and is part of the Windows [Native API](https://attack.mitre.org/techniques/T1106) which is called from functions like <code>CreateProcess</code>, <code>LoadLibrary</code>, etc. of the Win32 API. (Citation: Wikipedia Windows Library Files)
INSERT INTO public.attack_patterns VALUES (447, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.702199+00', NULL, 'attack-pattern--0c4b4fda-9062-47da-98b9-ceae2dcf052a', '[{"url": "https://attack.mitre.org/techniques/T1213/002", "external_id": "T1213.002", "source_name": "mitre-attack"}, {"url": "https://support.office.com/en-us/article/configure-audit-settings-for-a-site-collection-a9920c97-38c0-44f2-8bcb-4cf1e2ae22d2", "description": "Microsoft. (2017, July 19). Configure audit settings for a site collection. Retrieved April 4, 2018.", "source_name": "Microsoft SharePoint Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3158}, "x_mitre_detection": {"Num": 0, "Raw": "\"The user access logging within Microsoft''s SharePoint can be configured to report access to certain pages and documents. (Citation: Microsoft SharePoint Logging). As information repositories generally have a considerably large user base, detection of malicious use can be non-trivial. At minimum, access to information repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) should be closely monitored and alerted upon, as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies. \\n\\n\"", "Str": "The user access logging within Microsoft''s SharePoint can be configured to report access to certain pages and documents. (Citation: Microsoft SharePoint Logging). As information repositories generally have a considerably large user base, detection of malicious use can be non-trivial. At minimum, access to information repositories performed by privileged users (for example, Active Directory Domain, Enterprise, or Schema Administrators) should be closely monitored and alerted upon, as these types of accounts should not generally used to access information repositories. If the capability exists, it may be of value to monitor and alert on users that are retrieving and viewing a large number of documents and pages; this behavior may be indicative of programmatic means being used to retrieve all data within the repository. In environments with high-maturity, it may be possible to leverage User-Behavioral Analytics (UBA) platforms to detect and alert on user based anomalies. \n\n", "Type": 3, "Index": 1979}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3090}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1849}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3001}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3043}}', 'T1213.002', 'Adversaries may leverage the SharePoint repository as a source to mine valuable information. SharePoint will often contain useful information for an adversary to learn about the structure and functionality of the internal network and systems. For example, the following is a list of example information that may hold potential value to an adversary and may also be found on SharePoint:
INSERT INTO public.attack_patterns VALUES (450, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.80722+00', NULL, 'attack-pattern--f6fe9070-7a65-49ea-ae72-76292f42cebe', '[{"url": "https://attack.mitre.org/techniques/T1216", "external_id": "T1216", "source_name": "mitre-attack"}, {"url": "https://github.com/api0cradle/UltimateAppLockerByPassList", "description": "Moe, O. (2018, March 1). Ultimate AppLocker Bypass List. Retrieved April 10, 2018.", "source_name": "GitHub Ultimate AppLocker Bypass List"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2154}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor script processes, such as `cscript`, and command-line parameters for scripts like PubPrn.vbs that may be used to proxy execution of malicious files.\"", "Str": "Monitor script processes, such as `cscript`, and command-line parameters for scripts like PubPrn.vbs that may be used to proxy execution of malicious files.", "Type": 3, "Index": 1825}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2106}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 1522}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1578}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2016}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 1723}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2059}}', 'T1216', 'Adversaries may use scripts signed with trusted certificates to proxy execution of malicious files. Several Microsoft signed scripts that are default on Windows installations can be used to proxy execution of other files. This behavior may be abused by adversaries to execute malicious files that could bypass application control and signature validation on systems.(Citation: GitHub Ultimate AppLocker Bypass List)', 'Signed Script Proxy Execution', 0);
INSERT INTO public.attack_patterns VALUES (451, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.842171+00', NULL, 'attack-pattern--d273434a-448e-4598-8e14-607f4a0d5e27', '[{"url": "https://attack.mitre.org/techniques/T1558/002", "external_id": "T1558.002", "source_name": "mitre-attack"}, {"url": "https://adsecurity.org/?p=2011", "description": "Sean Metcalf. (2015, November 17). How Attackers Use Kerberos Silver Tickets to Exploit Systems. Retrieved February 27, 2020.", "source_name": "ADSecurity Silver Tickets"}, {"url": "https://adsecurity.org/?p=1515", "description": "Metcalf, S. (2015, May 03). Detecting Forged Kerberos Ticket (Golden Ticket & Silver Ticket) Use in Active Directory. Retrieved December 23, 2015.", "source_name": "ADSecurity Detecting Forged Tickets"}, {"url": "https://medium.com/threatpunter/detecting-attempts-to-steal-passwords-from-memory-558f16dce4ea", "description": "French, D. (2018, October 2). Detecting Attempts to Steal Passwords from Memory. Retrieved October 11, 2019.", "source_name": "Medium Detecting Attempts to Steal Passwords from Memory"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3496}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4634, 4672).(Citation: ADSecurity Detecting Forged Tickets) \\n\\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as Mimikatz access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details, including Kerberos tickets, are stored.\"", "Str": "Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4634, 4672).(Citation: ADSecurity Detecting Forged Tickets) \n\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as Mimikatz access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details, including Kerberos tickets, are stored.", "Type": 3, "Index": 2760}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3448}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2680}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3359}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3401}}', 'T1558.002', 'Adversaries who have the password hash of a target service account (e.g. SharePoint, MSSQL) may forge Kerberos ticket granting service (TGS) tickets, also known as silver tickets. Kerberos TGS tickets are also known as service tickets.(Citation: ADSecurity Silver Tickets)
INSERT INTO public.attack_patterns VALUES (458, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.86735+00', NULL, 'attack-pattern--deb98323-e13f-4b0c-8d94-175379069062', '[{"url": "https://attack.mitre.org/techniques/T1027/002", "external_id": "T1027.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/570.html", "external_id": "CAPEC-570", "source_name": "capec"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2018/01/WP-FinFisher.pdf", "description": "Kafka, F. (2018, January). ESET''s Guide to Deobfuscating and Devirtualizing FinFisher. Retrieved August 12, 2019.", "source_name": "ESET FinFisher Jan 2018"}, {"url": "http://en.wikipedia.org/wiki/Executable_compression", "description": "Executable compression. (n.d.). Retrieved December 4, 2014.", "source_name": "Wikipedia Exe Compression"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3090}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use file scanning to look for known software packers or artifacts of packing techniques. Packing is not a definitive indicator of malicious activity, because legitimate software may use packing techniques to reduce binary size or to protect proprietary code.\"", "Str": "Use file scanning to look for known software packers or artifacts of packing techniques. Packing is not a definitive indicator of malicious activity, because legitimate software may use packing techniques to reduce binary size or to protect proprietary code.", "Type": 3, "Index": 2703}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3027}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Filip Kafka, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2426}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\",\n      \"File: File Content\"\n    ]", "Str": "", "Type": 5, "Index": 2489}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2996}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Heuristic detection\",\n      \"Signature-based detection\"\n    ]", "Str": "", "Type": 5, "Index": 2586}}', 'T1027.002', 'Adversaries may perform software packing or virtual machine software protection to conceal their code. Software packing is a method of compressing or encrypting an executable. Packing an executable changes the file signature in an attempt to avoid signature-based detection. Most decompression techniques decompress the executable code in memory. Virtual machine software protection translates an executable''s original code into a special format that only a special virtual machine can run. A virtual machine is then called to run this code.(Citation: ESET FinFisher Jan 2018) 
INSERT INTO public.attack_patterns VALUES (453, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.946414+00', NULL, 'attack-pattern--b1ccd744-3f78-4a0e-9bb2-2002057f7928', '[{"url": "https://attack.mitre.org/techniques/T1585/001", "external_id": "T1585.001", "source_name": "mitre-attack"}, {"url": "https://www.securityweek.com/iranian-hackers-targeted-us-officials-elaborate-social-media-attack-operation", "description": "Lennon, M. (2014, May 29). Iranian Hackers Targeted US Officials in Elaborate Social Media Attack Operation. Retrieved March 1, 2017.", "source_name": "NEWSCASTER2014"}, {"url": "http://media.blackhat.com/bh-us-10/whitepapers/Ryan/BlackHat-USA-2010-Ryan-Getting-In-Bed-With-Robin-Sage-v1.0.pdf", "description": "Ryan, T. (2010). “Getting In Bed with Robin Sage.”. Retrieved March 6, 2017.", "source_name": "BlackHatRobinSage"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3388}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently created/modified accounts making numerous connection requests to accounts affiliated with your organization.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Spearphishing via Service](https://attack.mitre.org/techniques/T1566/003)).\"", "Str": "Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently created/modified accounts making numerous connection requests to accounts affiliated with your organization.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Spearphishing via Service](https://attack.mitre.org/techniques/T1566/003)).", "Type": 3, "Index": 2812}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3344}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3313}}', 'T1585.001', 'Adversaries may create and cultivate social media accounts that can be used during targeting. Adversaries can create social media accounts that can be used to build a persona to further operations. Persona development consists of the development of public information, presence, history and appropriate affiliations.(Citation: NEWSCASTER2014)(Citation: BlackHatRobinSage)
INSERT INTO public.attack_patterns VALUES (455, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.015174+00', NULL, 'attack-pattern--baf60e1a-afe5-4d31-830f-1b1ba2351884', '[{"url": "https://attack.mitre.org/techniques/T1592/002", "external_id": "T1592.002", "source_name": "mitre-attack"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3198}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2757}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3154}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3123}}', 'T1592.002', 'Adversaries may gather information about the victim''s host software that can be used during targeting. Information about installed software may include a variety of details such as types and versions on specific hosts, as well as the presence of additional components that might be indicative of added defensive protections (ex: antivirus, SIEMs, etc.).
INSERT INTO public.attack_patterns VALUES (456, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.039708+00', NULL, 'attack-pattern--92a78814-b191-47ca-909c-1ccfe3777414', '[{"url": "https://attack.mitre.org/techniques/T1072", "external_id": "T1072", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/187.html", "external_id": "CAPEC-187", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 3953}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection methods will vary depending on the type of third-party software or system and how it is typically used. \\n\\nThe same investigation process can be applied here as with other potentially malicious activities where the distribution vector is initially unknown but the resulting activity follows a discernible pattern. Analyze the process execution trees, historical activities from the third-party application (such as what types of files are usually pushed), and the resulting activities or events from the file/binary/script pushed to systems. \\n\\nOften these third-party applications will have logs of their own that can be collected and correlated with other data from the environment. Ensure that third-party application logs are on-boarded to the enterprise logging system and the logs are regularly reviewed. Audit software deployment logs and look for suspicious or unauthorized activity. A system not typically used to push software to clients that suddenly is used for such a task outside of a known admin function may be suspicious. Monitor account login activity on these applications to detect suspicious/abnormal usage.\\n\\nPerform application deployment at regular times so that irregular deployment activity stands out. Monitor process activity that does not correlate to known good software. Monitor account login activity on the deployment system.\"", "Str": "Detection methods will vary depending on the type of third-party software or system and how it is typically used. \n\nThe same investigation process can be applied here as with other potentially malicious activities where the distribution vector is initially unknown but the resulting activity follows a discernible pattern. Analyze the process execution trees, historical activities from the third-party application (such as what types of files are usually pushed), and the resulting activities or events from the file/binary/script pushed to systems. \n\nOften these third-party applications will have logs of their own that can be collected and correlated with other data from the environment. Ensure that third-party application logs are on-boarded to the enterprise logging system and the logs are regularly reviewed. Audit software deployment logs and look for suspicious or unauthorized activity. A system not typically used to push software to clients that suddenly is used for such a task outside of a known admin function may be suspicious. Monitor account login activity on these applications to detect suspicious/abnormal usage.\n\nPerform application deployment at regular times so that irregular deployment activity stands out. Monitor process activity that does not correlate to known good software. Monitor account login activity on the deployment system.", "Type": 3, "Index": 2304}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3839}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Shane Tully, @securitygypsy\"\n    ]", "Str": "", "Type": 5, "Index": 2113}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2186}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3924}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3710}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3753}}', 'T1072', 'Adversaries may gain access to and use third-party software suites installed within an enterprise network, such as administration, monitoring, and deployment systems, to move laterally through the network. Third-party applications and software deployment systems may be in use in the network environment for administration purposes (e.g., SCCM, HBSS, Altiris, etc.).
INSERT INTO public.attack_patterns VALUES (460, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.101878+00', NULL, 'attack-pattern--2e34237d-8574-43f6-aace-ae2915de8597', '[{"url": "https://attack.mitre.org/techniques/T1566/001", "external_id": "T1566.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/163.html", "external_id": "CAPEC-163", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}, {"url": "https://www.elastic.co/blog/embracing-offensive-tooling-building-detections-against-koadic-using-eql", "description": "Stepanic, D.. (2020, January 13). Embracing offensive tooling: Building detections against Koadic using EQL. Retrieved November 30, 2020.", "source_name": "Elastic - Koadiac Detection with EQL"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 5067}, "x_mitre_detection": {"Num": 0, "Raw": "\"Network intrusion detection systems and email gateways can be used to detect spearphishing with malicious attachments in transit. Detonation chambers may also be used to identify malicious attachments. Solutions can be signature and behavior based, but adversaries may construct attachments in a way to avoid these systems.\\n\\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\\n\\nAnti-virus can potentially detect malicious documents and attachments as they''re scanned to be stored on the email server or on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the attachment is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning Powershell.exe) for techniques such as [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203) or usage of malicious scripts.\\n\\nMonitor for suspicious descendant process spawning from Microsoft Office and other productivity software.(Citation: Elastic - Koadiac Detection with EQL)\"", "Str": "Network intrusion detection systems and email gateways can be used to detect spearphishing with malicious attachments in transit. Detonation chambers may also be used to identify malicious attachments. Solutions can be signature and behavior based, but adversaries may construct attachments in a way to avoid these systems.\n\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\n\nAnti-virus can potentially detect malicious documents and attachments as they''re scanned to be stored on the email server or on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the attachment is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning Powershell.exe) for techniques such as [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203) or usage of malicious scripts.\n\nMonitor for suspicious descendant process spawning from Microsoft Office and other productivity software.(Citation: Elastic - Koadiac Detection with EQL)", "Type": 3, "Index": 3787}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4989}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\"\n    ]", "Str": "", "Type": 5, "Index": 3547}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3607}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4958}}', 'T1566.001', 'Adversaries may send spearphishing emails with a malicious attachment in an attempt to gain access to victim systems. Spearphishing attachment is a specific variant of spearphishing. Spearphishing attachment is different from other forms of spearphishing in that it employs the use of malware attached to an email. All forms of spearphishing are electronically delivered social engineering targeted at a specific individual, company, or industry. In this scenario, adversaries attach a file to the spearphishing email and usually rely upon [User Execution](https://attack.mitre.org/techniques/T1204) to gain execution. Spearphishing may also involve social engineering techniques, such as posing as a trusted source.
INSERT INTO public.attack_patterns VALUES (461, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.132166+00', NULL, 'attack-pattern--8982a661-d84c-48c0-b4ec-1db29c6cf3bc', '[{"url": "https://attack.mitre.org/techniques/T1598/002", "external_id": "T1598.002", "source_name": "mitre-attack"}, {"url": "https://nakedsecurity.sophos.com/2020/10/02/serious-security-phishing-without-links-when-phishers-bring-along-their-own-web-pages/", "description": "Ducklin, P. (2020, October 2). Serious Security: Phishing without links – when phishers bring along their own web pages. Retrieved October 20, 2020.", "source_name": "Sophos Attachment"}, {"url": "https://github.com/ryhanson/phishery", "description": "Ryan Hanson. (2016, September 24). phishery. Retrieved October 23, 2020.", "source_name": "GitHub Phishery"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4222}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\"", "Str": "Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)", "Type": 3, "Index": 3828}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4178}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\",\n      \"Sebastian Salla, McAfee\",\n      \"Robert Simmons, @MalwareUtkonos\"\n    ]", "Str": "", "Type": 5, "Index": 3514}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3648}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4147}}', 'T1598.002', 'Adversaries may send spearphishing messages with a malicious attachment to elicit sensitive information that can be used during targeting. Spearphishing for information is an attempt to trick targets into divulging information, frequently credentials or other actionable information. Spearphishing for information frequently involves social engineering techniques, such as posing as a source with a reason to collect information (ex: [Establish Accounts](https://attack.mitre.org/techniques/T1585) or [Compromise Accounts](https://attack.mitre.org/techniques/T1586)) and/or sending multiple, seemingly urgent messages.
INSERT INTO public.attack_patterns VALUES (462, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.166885+00', NULL, 'attack-pattern--2b742742-28c3-4e1b-bab7-8350d6300fa7', '[{"url": "https://attack.mitre.org/techniques/T1566/002", "external_id": "T1566.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/163.html", "external_id": "CAPEC-163", "source_name": "capec"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/pawn-storm-abuses-open-authentication-advanced-social-engineering-attacks", "description": "Hacquebord, F.. (2017, April 25). Pawn Storm Abuses Open Authentication in Advanced Social Engineering Attacks. Retrieved October 4, 2019.", "source_name": "Trend Micro Pawn Storm OAuth 2017"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 5033}, "x_mitre_detection": {"Num": 0, "Raw": "\"URL inspection within email (including expanding shortened links) can help detect links leading to known malicious sites. Detonation chambers can be used to detect these links and either automatically go to these sites to determine if they''re potentially malicious, or wait and capture the content if a user visits the link.\\n\\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\\n\\nBecause this technique usually involves user interaction on the endpoint, many of the possible detections take place once [User Execution](https://attack.mitre.org/techniques/T1204) occurs.\"", "Str": "URL inspection within email (including expanding shortened links) can help detect links leading to known malicious sites. Detonation chambers can be used to detect these links and either automatically go to these sites to determine if they''re potentially malicious, or wait and capture the content if a user visits the link.\n\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\n\nBecause this technique usually involves user interaction on the endpoint, many of the possible detections take place once [User Execution](https://attack.mitre.org/techniques/T1204) occurs.", "Type": 3, "Index": 4146}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Office 365\",\n      \"SaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4895}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\",\n      \"Shailesh Tiwary (Indian Army)\",\n      \"Mark Wee\",\n      \"Jeff Sakowicz, Microsoft Identity Developer Platform Services (IDPM Services)\",\n      \"Saisha Agrawal, Microsoft Threat Intelligent Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 3693}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3966}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4864}}', 'T1566.002', 'Adversaries may send spearphishing emails with a malicious link in an attempt to gain access to victim systems. Spearphishing with a link is a specific variant of spearphishing. It is different from other forms of spearphishing in that it employs the use of links to download malware contained in email, instead of attaching malicious files to the email itself, to avoid defenses that may inspect email attachments. Spearphishing may also involve social engineering techniques, such as posing as a trusted source.
INSERT INTO public.attack_patterns VALUES (464, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.287791+00', NULL, 'attack-pattern--f870408c-b1cd-49c7-a5c7-0ef0fc496cc6', '[{"url": "https://attack.mitre.org/techniques/T1598/001", "external_id": "T1598.001", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/facebook-launching-pad-phishing-attacks/160351/", "description": "O''Donnell, L. (2020, October 20). Facebook: A Top Launching Pad For Phishing Attacks. Retrieved October 20, 2020.", "source_name": "ThreatPost Social Media Phishing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3697}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor social media traffic for suspicious activity, including messages requesting information as well as abnormal file or data transfers (especially those involving unknown, or otherwise suspicious accounts).\\n\\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Monitor social media traffic for suspicious activity, including messages requesting information as well as abnormal file or data transfers (especially those involving unknown, or otherwise suspicious accounts).\n\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 3042}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3653}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Robert Simmons, @MalwareUtkonos\"\n    ]", "Str": "", "Type": 5, "Index": 2785}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2862}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3622}}', 'T1598.001', 'Adversaries may send spearphishing messages via third-party services to elicit sensitive information that can be used during targeting. Spearphishing for information is an attempt to trick targets into divulging information, frequently credentials or other actionable information. Spearphishing for information frequently involves social engineering techniques, such as posing as a source with a reason to collect information (ex: [Establish Accounts](https://attack.mitre.org/techniques/T1585) or [Compromise Accounts](https://attack.mitre.org/techniques/T1586)) and/or sending multiple, seemingly urgent messages.
INSERT INTO public.attack_patterns VALUES (466, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.364405+00', NULL, 'attack-pattern--84771bc3-f6a0-403e-b144-01af70e5fda0', '[{"url": "https://attack.mitre.org/techniques/T1608", "external_id": "T1608", "source_name": "mitre-attack"}, {"url": "https://www.volexity.com/blog/2020/11/06/oceanlotus-extending-cyber-espionage-operations-through-fake-websites/", "description": "Adair, S. and Lancaster, T. (2020, November 6). OceanLotus: Extending Cyber Espionage Operations Through Fake Websites. Retrieved November 20, 2020.", "source_name": "Volexity Ocean Lotus November 2020"}, {"url": "https://www.fireeye.com/blog/threat-research/2012/12/council-foreign-relations-water-hole-attack-details.html", "description": "Kindlund, D. (2012, December 30). CFR Watering Hole Attack Details. Retrieved December 18, 2020.", "source_name": "FireEye CFR Watering Hole 2012"}, {"url": "http://arstechnica.com/security/2015/08/newly-discovered-chinese-hacking-group-hacked-100-websites-to-use-as-watering-holes/", "description": "Gallagher, S.. (2015, August 5). Newly discovered Chinese hacking group hacked 100+ websites to use as “watering holes”. Retrieved January 25, 2016.", "source_name": "Gallagher 2015"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}, {"url": "https://blog.malwarebytes.com/malwarebytes-news/2020/10/silent-librarian-apt-phishing-attack/", "description": "Malwarebytes Threat Intelligence Team. (2020, October 14). Silent Librarian APT right on schedule for 20/21 academic year. Retrieved February 3, 2021.", "source_name": "Malwarebytes Silent Librarian October 2020"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/threat-actor-profile-ta407-silent-librarian", "description": "Proofpoint Threat Insight Team. (2019, September 5). Threat Actor Profile: TA407, the Silent Librarian. Retrieved February 3, 2021.", "source_name": "Proofpoint TA407 September 2019"}, {"url": "https://www.digicert.com/kb/ssl-certificate-installation.htm", "description": "DigiCert. (n.d.). How to Install an SSL Certificate. Retrieved April 19, 2021.", "source_name": "DigiCert Install SSL Cert"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5436}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as initial access and post-compromise behaviors.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as initial access and post-compromise behaviors.", "Type": 3, "Index": 5059}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 5392}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5360}}', 'T1608', 'Adversaries may upload, install, or otherwise set up capabilities that can be used during targeting. To support their operations, an adversary may need to take capabilities they developed ([Develop Capabilities](https://attack.mitre.org/techniques/T1587)) or obtained ([Obtain Capabilities](https://attack.mitre.org/techniques/T1588)) and stage them on infrastructure under their control. These capabilities may be staged on infrastructure that was previously purchased/rented by the adversary ([Acquire Infrastructure](https://attack.mitre.org/techniques/T1583)) or was otherwise compromised by them ([Compromise Infrastructure](https://attack.mitre.org/techniques/T1584)). Capabilities can also be staged on web services, such as GitHub or Pastebin.(Citation: Volexity Ocean Lotus November 2020)
INSERT INTO public.attack_patterns VALUES (486, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.407899+00', NULL, 'attack-pattern--322bad5a-1c49-4d23-ab79-76d641794afa', '[{"url": "https://attack.mitre.org/techniques/T1007", "external_id": "T1007", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/574.html", "external_id": "CAPEC-574", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2693}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system information related to services. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system information related to services. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1714}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2645}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1610}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2516}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2559}}', 'T1007', 'Adversaries may try to get information about registered services. Commands that may obtain information about services using operating system utilities are "sc," "tasklist /svc" using [Tasklist](https://attack.mitre.org/software/S0057), and "net start" using [Net](https://attack.mitre.org/software/S0039), but adversaries may also use other tools as well. Adversaries may use the information from [System Service Discovery](https://attack.mitre.org/techniques/T1007) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.', 'System Service Discovery', 0);
INSERT INTO public.attack_patterns VALUES (468, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.497755+00', NULL, 'attack-pattern--c0dfe7b0-b873-4618-9ff8-53e31f70907f', '[{"url": "https://attack.mitre.org/techniques/T1037/005", "external_id": "T1037.005", "source_name": "mitre-attack"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/StartupItems.html", "description": "Apple. (2016, September 13). Startup Items. Retrieved July 11, 2017.", "source_name": "Startup Items"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3314}, "x_mitre_detection": {"Num": 0, "Raw": "\"The <code>/Library/StartupItems</code> folder can be monitored for changes. Similarly, the programs that are actually executed from this mechanism should be checked against a whitelist.\\n\\nMonitor processes that are executed during the bootup process to check for unusual or unknown applications and behavior.\"", "Str": "The <code>/Library/StartupItems</code> folder can be monitored for changes. Similarly, the programs that are actually executed from this mechanism should be checked against a whitelist.\n\nMonitor processes that are executed during the bootup process to check for unusual or unknown applications and behavior.", "Type": 3, "Index": 2826}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3268}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2660}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3170}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3212}}', 'T1037.005', 'Adversaries may use startup items automatically executed at boot initialization to establish persistence. Startup items execute during the final phase of the boot process and contain shell scripts or other executable files along with configuration information used by the system to determine the execution order for all startup items. (Citation: Startup Items)
INSERT INTO public.attack_patterns VALUES (470, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.564036+00', NULL, 'attack-pattern--10ffac09-e42d-4f56-ab20-db94c67d76ff', '[{"url": "https://attack.mitre.org/techniques/T1539", "external_id": "T1539", "source_name": "mitre-attack"}, {"url": "https://wunderwuzzi23.github.io/blog/passthecookie.html", "description": "Rehberger, J. (2018, December). Pivot to the Cloud using Pass the Cookie. Retrieved April 5, 2019.", "source_name": "Pass The Cookie"}, {"url": "https://securelist.com/project-tajmahal/90240/", "description": "GReAT. (2019, April 10). Project TajMahal – a sophisticated new APT framework. Retrieved October 14, 2019.", "source_name": "Kaspersky TajMahal April 2019"}, {"url": "https://unit42.paloaltonetworks.com/mac-malware-steals-cryptocurrency-exchanges-cookies/", "description": "Chen, Y., Hu, W., Xu, Z., et. al. (2019, January 31). Mac Malware Steals Cryptocurrency Exchanges’ Cookies. Retrieved October 14, 2019.", "source_name": "Unit 42 Mac Crypto Cookies January 2019"}, {"url": "https://github.com/kgretzky/evilginx2", "description": "Gretzky, Kuba. (2019, April 10). Retrieved October 8, 2019.", "source_name": "Github evilginx2"}, {"url": "https://github.com/muraenateam/muraena", "description": "Orrù, M., Trotta, G.. (2019, September 11). Muraena. Retrieved October 14, 2019.", "source_name": "GitHub Mauraena"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4266}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for attempts to access files and repositories on a local system that are used to store browser session cookies. Monitor for attempts by programs to inject into or dump browser process memory.\"", "Str": "Monitor for attempts to access files and repositories on a local system that are used to store browser session cookies. Monitor for attempts by programs to inject into or dump browser process memory.", "Type": 3, "Index": 3804}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Office 365\",\n      \"SaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4128}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\",\n      \"Johann Rehberger\"\n    ]", "Str": "", "Type": 5, "Index": 3595}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 3711}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4038}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4081}}', 'T1539', 'An adversary may steal web application or service session cookies and use them to gain access to web applications or Internet services as an authenticated user without needing credentials. Web applications and services often use session cookies as an authentication token after a user has authenticated to a website.
INSERT INTO public.attack_patterns VALUES (471, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.5998+00', NULL, 'attack-pattern--3fc01293-ef5e-41c6-86ce-61f10706b64a', '[{"url": "https://attack.mitre.org/techniques/T1558", "external_id": "T1558", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/652.html", "external_id": "CAPEC-652", "source_name": "capec"}, {"url": "https://adsecurity.org/?p=227", "description": "Sean Metcalf. (2014, September 12). Kerberos, Active Directory’s Secret Decoder Ring. Retrieved February 27, 2020.", "source_name": "ADSecurity Kerberos Ring Decoder"}, {"url": "https://adsecurity.org/?p=1515", "description": "Metcalf, S. (2015, May 03). Detecting Forged Kerberos Ticket (Golden Ticket & Silver Ticket) Use in Active Directory. Retrieved December 23, 2015.", "source_name": "ADSecurity Detecting Forged Tickets"}, {"url": "https://blog.stealthbits.com/detect-pass-the-ticket-attacks", "description": "Jeff Warren. (2019, February 19). How to Detect Pass-the-Ticket Attacks. Retrieved February 27, 2020.", "source_name": "Stealthbits Detect PtT 2019"}, {"url": "https://cert.europa.eu/static/WhitePapers/UPDATED%20-%20CERT-EU_Security_Whitepaper_2014-007_Kerberos_Golden_Ticket_Protection_v1_4.pdf", "description": "Abolins, D., Boldea, C., Socha, K., Soria-Machado, M. (2016, April 26). Kerberos Golden Ticket Protection. Retrieved July 13, 2017.", "source_name": "CERT-EU Golden Ticket Protection"}, {"url": "https://gallery.technet.microsoft.com/scriptcenter/Kerberos-Golden-Ticket-b4814285", "description": "Microsoft. (2015, March 24). Kerberos Golden Ticket Check (Updated). Retrieved February 27, 2020.", "source_name": "Microsoft Kerberos Golden Ticket"}, {"url": "https://blogs.technet.microsoft.com/motiba/2018/02/23/detecting-kerberoasting-activity-using-azure-security-center/", "description": "Bani, M. (2018, February 23). Detecting Kerberoasting activity using Azure Security Center. Retrieved March 23, 2018.", "source_name": "Microsoft Detecting Kerberoasting Feb 2018"}, {"url": "https://adsecurity.org/?p=2293", "description": "Metcalf, S. (2015, December 31). Cracking Kerberos TGS Tickets Using Kerberoast – Exploiting Kerberos to Compromise the Active Directory Domain. Retrieved March 22, 2018.", "source_name": "AdSecurity Cracking Kerberos Dec 2015"}, {"url": "https://medium.com/threatpunter/detecting-attempts-to-steal-passwords-from-memory-558f16dce4ea", "description": "French, D. (2018, October 2). Detecting Attempts to Steal Passwords from Memory. Retrieved October 11, 2019.", "source_name": "Medium Detecting Attempts to Steal Passwords from Memory"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6193}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4672, 4634), RC4 encryption within ticket granting tickets (TGTs), and ticket granting service (TGS) requests without preceding TGT requests.(Citation: ADSecurity Detecting Forged Tickets)(Citation: Stealthbits Detect PtT 2019)(Citation: CERT-EU Golden Ticket Protection)\\n\\nMonitor the lifetime of TGT tickets for values that differ from the default domain duration.(Citation: Microsoft Kerberos Golden Ticket)\\n\\nMonitor for indications of [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003) being used to move laterally. \\n\\nEnable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17]).(Citation: Microsoft Detecting Kerberoasting Feb 2018) (Citation: AdSecurity Cracking Kerberos Dec 2015)\\n\\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as [Mimikatz](https://attack.mitre.org/software/S0002) access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details, including Kerberos tickets, are stored.\"", "Str": "Monitor for anomalous Kerberos activity, such as malformed or blank fields in Windows logon/logoff events (Event ID 4624, 4672, 4634), RC4 encryption within ticket granting tickets (TGTs), and ticket granting service (TGS) requests without preceding TGT requests.(Citation: ADSecurity Detecting Forged Tickets)(Citation: Stealthbits Detect PtT 2019)(Citation: CERT-EU Golden Ticket Protection)\n\nMonitor the lifetime of TGT tickets for values that differ from the default domain duration.(Citation: Microsoft Kerberos Golden Ticket)\n\nMonitor for indications of [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003) being used to move laterally. \n\nEnable Audit Kerberos Service Ticket Operations to log Kerberos TGS service ticket requests. Particularly investigate irregular patterns of activity (ex: accounts making numerous requests, Event ID 4769, within a small time frame, especially if they also request RC4 encryption [Type 0x17]).(Citation: Microsoft Detecting Kerberoasting Feb 2018) (Citation: AdSecurity Cracking Kerberos Dec 2015)\n\nMonitor for unexpected processes interacting with lsass.exe.(Citation: Medium Detecting Attempts to Steal Passwords from Memory) Common credential dumpers such as [Mimikatz](https://attack.mitre.org/software/S0002) access the LSA Subsystem Service (LSASS) process by opening the process, locating the LSA secrets key, and decrypting the sections in memory where credential details, including Kerberos tickets, are stored.", "Type": 3, "Index": 4511}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6061}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Credential Request\",\n      \"Logon Session: Logon Session Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 4368}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6029}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Kerberos authentication enabled\"\n    ]", "Str": "", "Type": 5, "Index": 6121}}', 'T1558', 'Adversaries may attempt to subvert Kerberos authentication by stealing or forging Kerberos tickets to enable [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003). 
INSERT INTO public.attack_patterns VALUES (474, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.675129+00', NULL, 'attack-pattern--1cfcb312-b8d7-47a4-b560-4b16cc677292', '[{"url": "https://attack.mitre.org/techniques/T1565/001", "external_id": "T1565.001", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/apt/rpt-apt38", "description": "FireEye. (2018, October 03). APT38: Un-usual Suspects. Retrieved November 6, 2018.", "source_name": "FireEye APT38 Oct 2018"}, {"url": "https://www.justice.gov/opa/press-release/file/1092091/download", "description": "Department of Justice. (2018, September 6). Criminal Complaint - United States of America v. PARK JIN HYOK. Retrieved March 29, 2019.", "source_name": "DOJ Lazarus Sony 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2761}, "x_mitre_detection": {"Num": 0, "Raw": "\"Where applicable, inspect important file hashes, locations, and modifications for suspicious/unexpected values.\"", "Str": "Where applicable, inspect important file hashes, locations, and modifications for suspicious/unexpected values.", "Type": 3, "Index": 2341}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2683}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 2483}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Deletion\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2217}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2541}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2583}}', 'T1565.001', 'Adversaries may insert, delete, or manipulate data at rest in order to manipulate external outcomes or hide activity.(Citation: FireEye APT38 Oct 2018)(Citation: DOJ Lazarus Sony 2018) By manipulating stored data, adversaries may attempt to affect a business process, organizational understanding, and decision making.
INSERT INTO public.attack_patterns VALUES (475, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.7108+00', NULL, 'attack-pattern--b83e166d-13d7-4b52-8677-dff90c548fd7', '[{"url": "https://attack.mitre.org/techniques/T1553", "external_id": "T1553", "source_name": "mitre-attack"}, {"url": "https://specterops.io/assets/resources/SpecterOps_Subverting_Trust_in_Windows.pdf", "description": "Graeber, M. (2017, September). Subverting Trust in Windows. Retrieved January 31, 2018.", "source_name": "SpectorOps Subverting Trust Sept 2017"}, {"url": "https://securelist.com/why-you-shouldnt-completely-trust-files-signed-with-digital-certificates/68593/", "description": "Ladikov, A. (2015, January 29). Why You Shouldn’t Completely Trust Files Signed with Digital Certificates. Retrieved March 31, 2016.", "source_name": "Securelist Digital Certificates"}, {"url": "http://www.symantec.com/connect/blogs/how-attackers-steal-private-keys-digital-certificates", "description": "Shinotsuka, H. (2013, February 22). How Attackers Steal Private Keys from Digital Certificates. Retrieved March 31, 2016.", "source_name": "Symantec Digital Certificates"}, {"url": "https://posts.specterops.io/code-signing-certificate-cloning-attacks-and-defenses-6f98657fc6ec", "description": "Graeber, M. (2017, December 22). Code Signing Certificate Cloning Attacks and Defenses. Retrieved April 3, 2018.", "source_name": "SpectorOps Code Signing Dec 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5357}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect and analyze signing certificate metadata on software that executes within the environment to look for unusual certificate characteristics and outliers. Periodically baseline registered SIPs and trust providers (Registry entries and files on disk), specifically looking for new, modified, or non-Microsoft entries. (Citation: SpectorOps Subverting Trust Sept 2017) A system''s root certificates are unlikely to change frequently. Monitor new certificates installed on a system that could be due to malicious activity.(Citation: SpectorOps Code Signing Dec 2017)\\n\\nAnalyze Autoruns data for oddities and anomalies, specifically malicious files attempting persistent execution by hiding within auto-starting locations. Autoruns will hide entries signed by Microsoft or Windows by default, so ensure \\\"Hide Microsoft Entries\\\" and \\\"Hide Windows Entries\\\" are both deselected.(Citation: SpectorOps Subverting Trust Sept 2017) \\n\\nMonitor and investigate attempts to modify extended file attributes with utilities such as <code>xattr</code>. Built-in system utilities may generate high false positive alerts, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible. \"", "Str": "Collect and analyze signing certificate metadata on software that executes within the environment to look for unusual certificate characteristics and outliers. Periodically baseline registered SIPs and trust providers (Registry entries and files on disk), specifically looking for new, modified, or non-Microsoft entries. (Citation: SpectorOps Subverting Trust Sept 2017) A system''s root certificates are unlikely to change frequently. Monitor new certificates installed on a system that could be due to malicious activity.(Citation: SpectorOps Code Signing Dec 2017)\n\nAnalyze Autoruns data for oddities and anomalies, specifically malicious files attempting persistent execution by hiding within auto-starting locations. Autoruns will hide entries signed by Microsoft or Windows by default, so ensure \"Hide Microsoft Entries\" and \"Hide Windows Entries\" are both deselected.(Citation: SpectorOps Subverting Trust Sept 2017) \n\nMonitor and investigate attempts to modify extended file attributes with utilities such as <code>xattr</code>. Built-in system utilities may generate high false positive alerts, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible. ", "Type": 3, "Index": 3932}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 5279}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\",\n      \"File: File Modification\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 3355}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5247}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\",\n      \"Autoruns Analysis\",\n      \"Digital Certificate Validation\",\n      \"Process whitelisting\",\n      \"User Mode Signature Validation\",\n      \"Windows User Account Control\"\n    ]", "Str": "", "Type": 5, "Index": 3675}}', 'T1553', 'Adversaries may undermine security controls that will either warn users of untrusted activity or prevent execution of untrusted programs. Operating systems and security products may contain mechanisms to identify programs or websites as possessing some level of trust. Examples of such features would include a program being allowed to run because it is signed by a valid code signing certificate, a program prompting the user with a warning because it has an attribute set from being downloaded from the Internet, or getting an indication that you are about to connect to an untrusted site.
INSERT INTO public.attack_patterns VALUES (476, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.775795+00', NULL, 'attack-pattern--1365fe3b-0f50-455d-b4da-266ce31c23b0', '[{"url": "https://attack.mitre.org/techniques/T1548/003", "external_id": "T1548.003", "source_name": "mitre-attack"}, {"url": "https://www.sudo.ws/", "description": "Todd C. Miller. (2018). Sudo Man Page. Retrieved March 19, 2018.", "source_name": "sudo man page 2018"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/04/new-osx-dok-malware-intercepts-web-traffic/", "description": "Thomas Reed. (2017, July 7). New OSX.Dok malware intercepts web traffic. Retrieved July 10, 2017.", "source_name": "OSX.Dok Malware"}, {"url": "https://www.cybereason.com/blog/labs-proton-b-what-this-mac-malware-actually-does", "description": "Amit Serper. (2018, May 10). ProtonB What this Mac Malware Actually Does. Retrieved March 19, 2018.", "source_name": "cybereason osx proton"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5467}, "x_mitre_detection": {"Num": 0, "Raw": "\"On Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo). This technique is abusing normal functionality in macOS and Linux systems, but sudo has the ability to log all input and output based on the <code>LOG_INPUT</code> and <code>LOG_OUTPUT</code> directives in the <code>/etc/sudoers</code> file.\"", "Str": "On Linux, auditd can alert every time a user''s actual ID and effective ID are different (this is what happens when you sudo). This technique is abusing normal functionality in macOS and Linux systems, but sudo has the ability to log all input and output based on the <code>LOG_INPUT</code> and <code>LOG_OUTPUT</code> directives in the <code>/etc/sudoers</code> file.", "Type": 3, "Index": 4856}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 5406}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Metadata\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4684}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5317}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5359}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 5264}}', 'T1548.003', 'Adversaries may perform sudo caching and/or use the suoders file to elevate privileges. Adversaries may do this to execute commands as other users or spawn processes with higher privileges.
INSERT INTO public.attack_patterns VALUES (487, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.80109+00', NULL, 'attack-pattern--d157f9d2-d09a-4efa-bb2a-64963f94e253', '[{"url": "https://attack.mitre.org/techniques/T1569", "external_id": "T1569", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2009}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for command line invocations of tools capable of modifying services that doesn’t correspond to normal usage patterns and known software, patch cycles, etc. Also monitor for changes to executables and other files associated with services. Changes to Windows services may also be reflected in the Registry.\"", "Str": "Monitor for command line invocations of tools capable of modifying services that doesn’t correspond to normal usage patterns and known software, patch cycles, etc. Also monitor for changes to executables and other files associated with services. Changes to Windows services may also be reflected in the Registry.", "Type": 3, "Index": 1454}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1946}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Service: Service Creation\",\n      \"File: File Modification\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1221}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1803}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 1846}}', 'T1569', 'Adversaries may abuse system services or daemons to execute commands or programs. Adversaries can execute malicious content by interacting with or creating services. Many services are set to run at boot, which can aid in achieving persistence ([Create or Modify System Process](https://attack.mitre.org/techniques/T1543)), but adversaries can also abuse services for one-time or temporary execution.', 'System Services', 0);
INSERT INTO public.attack_patterns VALUES (478, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.870261+00', NULL, 'attack-pattern--24bfaeba-cb0d-4525-b3dc-507c77ecec41', '[{"url": "https://attack.mitre.org/techniques/T1573/001", "external_id": "T1573.001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2366}, "x_mitre_detection": {"Num": 0, "Raw": "\"With symmetric encryption, it may be possible to obtain the algorithm and key from samples and use them to decode network traffic to detect malware communications signatures.\\n\\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "With symmetric encryption, it may be possible to obtain the algorithm and key from samples and use them to decode network traffic to detect malware communications signatures.\n\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1609}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2288}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1526}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2257}}', 'T1573.001', 'Adversaries may employ a known symmetric encryption algorithm to conceal command and control traffic rather than relying on any inherent protections provided by a communication protocol. Symmetric encryption algorithms use the same key for plaintext encryption and ciphertext decryption. Common symmetric encryption algorithms include AES, DES, 3DES, Blowfish, and RC4.', 'Symmetric Cryptography', 181);
INSERT INTO public.attack_patterns VALUES (479, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.894076+00', NULL, 'attack-pattern--29be378d-262d-4e99-b00d-852d573628e6', '[{"url": "https://attack.mitre.org/techniques/T1497/001", "external_id": "T1497.001", "source_name": "mitre-attack"}, {"url": "https://securingtomorrow.mcafee.com/other-blogs/mcafee-labs/stopping-malware-fake-virtual-machine/", "description": "Roccia, T. (2017, January 19). Stopping Malware With a Fake Virtual Machine. Retrieved April 17, 2019.", "source_name": "McAfee Virtual Jan 2017"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/09/unit42-oilrig-targets-middle-eastern-government-adds-evasion-techniques-oopsie/", "description": "Falcone, R., et al. (2018, September 04). OilRig Targets a Middle Eastern Government and Adds Evasion Techniques to OopsIE. Retrieved September 24, 2018.", "source_name": "Unit 42 OilRig Sept 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 5148}, "x_mitre_detection": {"Num": 0, "Raw": "\"Virtualization/sandbox related system checks will likely occur in the first steps of an operation but may also occur throughout as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained. Detecting actions related to virtualization and sandbox identification may be difficult depending on the adversary''s implementation and monitoring required. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of Discovery, especially in a short period of time, may aid in detection.\"", "Str": "Virtualization/sandbox related system checks will likely occur in the first steps of an operation but may also occur throughout as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained. Detecting actions related to virtualization and sandbox identification may be difficult depending on the adversary''s implementation and monitoring required. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of Discovery, especially in a short period of time, may aid in detection.", "Type": 3, "Index": 4305}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5070}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Deloitte Threat Library Team\"\n    ]", "Str": "", "Type": 5, "Index": 3935}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4009}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5039}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Static File Analysis\",\n      \"Signature-based detection\",\n      \"Host forensic analysis\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 4155}}', 'T1497.001', 'Adversaries may employ various system checks to detect and avoid virtualization and analysis environments. This may include changing behaviors based on the results of checks for the presence of artifacts indicative of a virtual machine environment (VME) or sandbox. If the adversary detects a VME, they may alter their malware to disengage from the victim or conceal the core functions of the implant. They may also search for VME artifacts before dropping secondary or additional payloads. Adversaries may use the information learned from [Virtualization/Sandbox Evasion](https://attack.mitre.org/techniques/T1497) during automated discovery to shape follow-on behaviors. 
INSERT INTO public.attack_patterns VALUES (488, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.943278+00', NULL, 'attack-pattern--ff73aa03-0090-4464-83ac-f89e233c02bc', '[{"url": "https://attack.mitre.org/techniques/T1529", "external_id": "T1529", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows-server/administration/windows-commands/shutdown", "description": "Microsoft. (2017, October 15). Shutdown. Retrieved October 4, 2019.", "source_name": "Microsoft Shutdown Oct 2017"}, {"url": "https://blog.talosintelligence.com/2017/06/worldwide-ransomware-variant.html", "description": "Chiu, A. (2016, June 27). New Ransomware Variant \"Nyetya\" Compromises Systems Worldwide. Retrieved March 26, 2019.", "source_name": "Talos Nyetya June 2017"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3200}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and command line parameters of binaries involved in shutting down or rebooting systems. Windows event logs may also designate activity associated with a shutdown/reboot, ex. Event ID 1074 and 6006.\"", "Str": "Use process monitoring to monitor the execution and command line parameters of binaries involved in shutting down or rebooting systems. Windows event logs may also designate activity associated with a shutdown/reboot, ex. Event ID 1074 and 6006.", "Type": 3, "Index": 2642}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3122}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 2918}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Sensor Health: Host Status\"\n    ]", "Str": "", "Type": 5, "Index": 2502}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2979}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3022}}', 'T1529', 'Adversaries may shutdown/reboot systems to interrupt access to, or aid in the destruction of, those systems. Operating systems may contain commands to initiate a shutdown/reboot of a machine. In some cases, these commands may also be used to initiate a shutdown/reboot of a remote computer.(Citation: Microsoft Shutdown Oct 2017) Shutting down or rebooting systems may disrupt access to computer resources for legitimate users.
INSERT INTO public.attack_patterns VALUES (483, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.048474+00', NULL, 'attack-pattern--707399d6-ab3e-4963-9315-d9d3818cd6a0', '[{"url": "https://attack.mitre.org/techniques/T1016", "external_id": "T1016", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/309.html", "external_id": "CAPEC-309", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2944}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1982}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2866}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Script: Script Execution\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1809}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2776}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2819}}', 'T1016', 'Adversaries may look for details about the network configuration and settings of systems they access or through information discovery of remote systems. Several operating system administration utilities exist that can be used to gather this information. Examples include [Arp](https://attack.mitre.org/software/S0099), [ipconfig](https://attack.mitre.org/software/S0100)/[ifconfig](https://attack.mitre.org/software/S0101), [nbtstat](https://attack.mitre.org/software/S0102), and [route](https://attack.mitre.org/software/S0103).
INSERT INTO public.attack_patterns VALUES (485, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.113503+00', NULL, 'attack-pattern--03d7999c-1f4c-42cc-8373-e7690d318104', '[{"url": "https://attack.mitre.org/techniques/T1033", "external_id": "T1033", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/577.html", "external_id": "CAPEC-577", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3089}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2131}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3011}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2027}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2898}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2941}}', 'T1033', 'Adversaries may attempt to identify the primary user, currently logged in user, set of users that commonly uses a system, or whether a user is actively using the system. They may do this, for example, by retrieving account usernames or by using [OS Credential Dumping](https://attack.mitre.org/techniques/T1003). The information may be collected in a number of different ways using other Discovery techniques, because user and username details are prevalent throughout a system and include running process ownership, file/directory ownership, session information, and system logs. Adversaries may use the information from [System Owner/User Discovery](https://attack.mitre.org/techniques/T1033) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.
INSERT INTO public.attack_patterns VALUES (490, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.164257+00', NULL, 'attack-pattern--dfefe2ed-4389-4318-8762-f0272b350a1b', '[{"url": "https://attack.mitre.org/techniques/T1543/002", "external_id": "T1543.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/550.html", "external_id": "CAPEC-550", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/551.html", "external_id": "CAPEC-551", "source_name": "capec"}, {"url": "http://man7.org/linux/man-pages/man1/systemd.1.html", "description": "Linux man-pages. (2014, January). systemd(1) - Linux manual page. Retrieved April 23, 2019.", "source_name": "Linux man-pages: systemd January 2014"}, {"url": "https://www.freedesktop.org/wiki/Software/systemd/", "description": "Freedesktop.org. (2018, September 29). systemd System and Service Manager. Retrieved April 23, 2019.", "source_name": "Freedesktop.org Linux systemd 29SEP2018"}, {"url": "https://www.anomali.com/blog/rocke-evolves-its-arsenal-with-a-new-malware-family-written-in-golang", "description": "Anomali Labs. (2019, March 15). Rocke Evolves Its Arsenal With a New Malware Family Written in Golang. Retrieved April 24, 2019.", "source_name": "Anomali Rocke March 2019"}, {"url": "https://www.rapid7.com/db/modules/exploit/linux/local/service_persistence", "description": "Rapid7. (2016, June 22). Service Persistence. Retrieved April 23, 2019.", "source_name": "Rapid7 Service Persistence 22JUNE2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6004}, "x_mitre_detection": {"Num": 0, "Raw": "\"Systemd service unit files may be detected by auditing file creation and modification events within the <code>/etc/systemd/system</code>, <code>/usr/lib/systemd/system/</code>, and <code>/home/<username>/.config/systemd/user/</code> directories, as well as associated symbolic links. Suspicious processes or scripts spawned in this manner will have a parent process of ‘systemd’, a parent process ID of 1, and will usually execute as the ‘root’ user.\\n\\nSuspicious systemd services can also be identified by comparing results against a trusted system baseline. Malicious systemd services may be detected by using the systemctl utility to examine system wide services: <code>systemctl list-units -–type=service –all</code>. Analyze the contents of <code>.service</code> files present on the file system and ensure that they refer to legitimate, expected executables.\\n\\nAuditing the execution and command-line arguments of the ''systemctl'' utility, as well related utilities such as <code>/usr/sbin/service</code> may reveal malicious systemd service execution.\"", "Str": "Systemd service unit files may be detected by auditing file creation and modification events within the <code>/etc/systemd/system</code>, <code>/usr/lib/systemd/system/</code>, and <code>/home/<username>/.config/systemd/user/</code> directories, as well as associated symbolic links. Suspicious processes or scripts spawned in this manner will have a parent process of ‘systemd’, a parent process ID of 1, and will usually execute as the ‘root’ user.\n\nSuspicious systemd services can also be identified by comparing results against a trusted system baseline. Malicious systemd services may be detected by using the systemctl utility to examine system wide services: <code>systemctl list-units -–type=service –all</code>. Analyze the contents of <code>.service</code> files present on the file system and ensure that they refer to legitimate, expected executables.\n\nAuditing the execution and command-line arguments of the ''systemctl'' utility, as well related utilities such as <code>/usr/sbin/service</code> may reveal malicious systemd service execution.", "Type": 3, "Index": 4749}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 5958}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tony Lambert, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 4439}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Service: Service Creation\",\n      \"Service: Service Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4509}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5855}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 5897}}', 'T1543.002', 'Adversaries may create or modify systemd services to repeatedly execute malicious payloads as part of persistence. The systemd service manager is commonly used for managing background daemon processes (also known as services) and other system resources.(Citation: Linux man-pages: systemd January 2014)(Citation: Freedesktop.org Linux systemd 29SEP2018) Systemd is the default initialization (init) system on many Linux distributions starting with Debian 8, Ubuntu 15.04, CentOS 7, RHEL 7, Fedora 15, and replaces legacy init systems including SysVinit and Upstart while remaining backwards compatible with the aforementioned init systems.
INSERT INTO public.attack_patterns VALUES (492, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.214111+00', NULL, 'attack-pattern--28abec6c-4443-4b03-8206-07f2e264a6b4', '[{"url": "https://attack.mitre.org/techniques/T1542/005", "external_id": "T1542.005", "source_name": "mitre-attack"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#35", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Secure Boot. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Secure Boot"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#7", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Image File Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Image File Verification"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#13", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Cisco IOS Run-Time Memory Integrity Verification. Retrieved October 19, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Run-Time Memory Verification"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#23", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Command History. Retrieved October 21, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Command History"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#26", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Boot Information. Retrieved October 21, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Boot Information"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5612}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider comparing a copy of the network device configuration and system image against a known-good version to discover unauthorized changes to system boot, startup configuration, or the running OS. (Citation: Cisco IOS Software Integrity Assurance - Secure Boot) (Citation: Cisco IOS Software Integrity Assurance - Image File Verification)The same process can be accomplished through a comparison of the run-time memory, though this is non-trivial and may require assistance from the vendor.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\\n\\nReview command history in either the console or as part of the running memory to determine if unauthorized or suspicious commands were used to modify device configuration. (Citation: Cisco IOS Software Integrity Assurance - Command History) Check boot information including system uptime, image booted, and startup configuration to determine if results are consistent with expected behavior in the environment. (Citation: Cisco IOS Software Integrity Assurance - Boot Information) Monitor unusual connections or connection attempts to the device that may specifically target TFTP or other file-sharing protocols.\"", "Str": "Consider comparing a copy of the network device configuration and system image against a known-good version to discover unauthorized changes to system boot, startup configuration, or the running OS. (Citation: Cisco IOS Software Integrity Assurance - Secure Boot) (Citation: Cisco IOS Software Integrity Assurance - Image File Verification)The same process can be accomplished through a comparison of the run-time memory, though this is non-trivial and may require assistance from the vendor.  (Citation: Cisco IOS Software Integrity Assurance - Run-Time Memory Verification)\n\nReview command history in either the console or as part of the running memory to determine if unauthorized or suspicious commands were used to modify device configuration. (Citation: Cisco IOS Software Integrity Assurance - Command History) Check boot information including system uptime, image booted, and startup configuration to determine if results are consistent with expected behavior in the environment. (Citation: Cisco IOS Software Integrity Assurance - Boot Information) Monitor unusual connections or connection attempts to the device that may specifically target TFTP or other file-sharing protocols.", "Type": 3, "Index": 4240}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 5564}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Firmware: Firmware Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4076}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5466}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5508}}', 'T1542.005', 'Adversaries may abuse netbooting to load an unauthorized network device operating system from a Trivial File Transfer Protocol (TFTP) server. TFTP boot (netbooting) is commonly used by network administrators to load configuration-controlled network device images from a centralized management server. Netbooting is one option in the boot sequence and can be used to centralize, manage, and control device images.
INSERT INTO public.attack_patterns VALUES (493, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.239702+00', NULL, 'attack-pattern--246fd3c7-f5e3-466d-8787-4c13d9e3b61c', '[{"url": "https://attack.mitre.org/techniques/T1080", "external_id": "T1080", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/562.html", "external_id": "CAPEC-562", "source_name": "capec"}, {"url": "https://rewtin.blogspot.ch/2017/11/abusing-user-shares-for-efficient.html", "description": "Routin, D. (2017, November 13). Abusing network shares for efficient lateral movements and privesc (DirSharePivot). Retrieved April 12, 2018.", "source_name": "Retwin Directory Share Pivot"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4329}, "x_mitre_detection": {"Num": 0, "Raw": "\"Processes that write or overwrite many files to a network shared directory may be suspicious. Monitor processes that are executed from removable media for malicious or abnormal activity such as network connections due to Command and Control and possible network Discovery techniques.\\n\\nFrequently scan shared network directories for malicious files, hidden files, .LNK files, and other file types that may not typical exist in directories used to share specific types of content.\"", "Str": "Processes that write or overwrite many files to a network shared directory may be suspicious. Monitor processes that are executed from removable media for malicious or abnormal activity such as network connections due to Command and Control and possible network Discovery techniques.\n\nFrequently scan shared network directories for malicious files, hidden files, .LNK files, and other file types that may not typical exist in directories used to share specific types of content.", "Type": 3, "Index": 3564}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4169}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Michal Dida, ESET\",\n      \"David Routin\"\n    ]", "Str": "", "Type": 5, "Index": 3304}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Network Share: Network Share Access\"\n    ]", "Str": "", "Type": 5, "Index": 3389}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4079}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Access to shared folders and content with write permissions\"\n    ]", "Str": "", "Type": 5, "Index": 4229}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4122}}', 'T1080', '
INSERT INTO public.attack_patterns VALUES (496, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.295021+00', NULL, 'attack-pattern--e49ee9d2-0d98-44ef-85e5-5d3100065744', '[{"url": "https://attack.mitre.org/techniques/T1055/005", "external_id": "T1055.005", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/11/ursnif-variant-malicious-tls-callback-technique.html", "description": "Vaish, A. & Nemes, S. (2017, November 28). Newly Observed Ursnif Variant Employs Malicious TLS Callback Technique to Achieve Process Injection. Retrieved December 18, 2017.", "source_name": "FireEye TLS Nov 2017"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4163}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 3120}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4115}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 2930}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4084}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3038}}', 'T1055.005', 'Adversaries may inject malicious code into processes via thread local storage (TLS) callbacks in order to evade process-based defenses as well as possibly elevate privileges. TLS callback injection is a method of executing arbitrary code in the address space of a separate live process. 
INSERT INTO public.attack_patterns VALUES (497, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.319148+00', NULL, 'attack-pattern--51e54974-a541-4fb6-a61b-0518e4c6de41', '[{"url": "https://attack.mitre.org/techniques/T1597/001", "external_id": "T1597.001", "source_name": "mitre-attack"}, {"url": "https://d3security.com/blog/10-of-the-best-open-source-threat-intelligence-feeds/", "description": "Banerd, W. (2019, April 30). 10 of the Best Open Source Threat Intelligence Feeds. Retrieved October 20, 2020.", "source_name": "D3Secutrity CTI Feeds"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2942}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2501}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2898}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2867}}', 'T1597.001', 'Adversaries may search private data from threat intelligence vendors for information that can be used during targeting. Threat intelligence vendors may offer paid feeds or portals that offer more data than what is publicly reported. Although sensitive details (such as customer names and other identifiers) may be redacted, this information may contain trends regarding breaches such as target industries, attribution claims, and successful TTPs/countermeasures.(Citation: D3Secutrity CTI Feeds)
INSERT INTO public.attack_patterns VALUES (499, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.3743+00', NULL, 'attack-pattern--61afc315-860c-4364-825d-0d62b2e91edc', '[{"url": "https://attack.mitre.org/techniques/T1547/003", "external_id": "T1547.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/windows-server/networking/windows-time-service/windows-time-service-top", "description": "Microsoft. (2018, February 1). Windows Time Service (W32Time). Retrieved March 26, 2018.", "source_name": "Microsoft W32Time Feb 2018"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms725475.aspx", "description": "Microsoft. (n.d.). Time Provider. Retrieved March 26, 2018.", "source_name": "Microsoft TimeProvider"}, {"url": "https://github.com/scottlundgren/w32time", "description": "Lundgren, S. (2017, October 28). w32time. Retrieved March 26, 2018.", "source_name": "Github W32Time Oct 2017"}, {"url": "https://docs.microsoft.com/windows-server/networking/windows-time-service/windows-time-service-tools-and-settings", "description": "Mathers, B. (2017, May 31). Windows Time Service Tools and Settings. Retrieved March 26, 2018.", "source_name": "Microsoft W32Time May 2017"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4569}, "x_mitre_detection": {"Num": 0, "Raw": "\"Baseline values and monitor/analyze activity related to modifying W32Time information in the Registry, including application programming interface (API) calls such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code> as well as execution of the W32tm.exe utility. (Citation: Microsoft W32Time May 2017) There is no restriction on the number of custom time providers registrations, though each may require a DLL payload written to disk. (Citation: Github W32Time Oct 2017)\\n\\nThe Sysinternals Autoruns tool may also be used to analyze auto-starting locations, including DLLs listed as time providers. (Citation: TechNet Autoruns)\"", "Str": "Baseline values and monitor/analyze activity related to modifying W32Time information in the Registry, including application programming interface (API) calls such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code> as well as execution of the W32tm.exe utility. (Citation: Microsoft W32Time May 2017) There is no restriction on the number of custom time providers registrations, though each may require a DLL payload written to disk. (Citation: Github W32Time Oct 2017)\n\nThe Sysinternals Autoruns tool may also be used to analyze auto-starting locations, including DLLs listed as time providers. (Citation: TechNet Autoruns)", "Type": 3, "Index": 3735}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4521}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Scott Lundgren, @5twenty9, Carbon Black\"\n    ]", "Str": "", "Type": 5, "Index": 3456}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3541}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4407}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4449}}', 'T1547.003', 'Adversaries may abuse time providers to execute DLLs when the system boots. The Windows Time service (W32Time) enables time synchronization across and within domains. (Citation: Microsoft W32Time Feb 2018) W32Time time providers are responsible for retrieving time stamps from hardware/network resources and outputting these values to other network clients. (Citation: Microsoft TimeProvider)
INSERT INTO public.attack_patterns VALUES (501, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.434686+00', NULL, 'attack-pattern--86850eff-2729-40c3-b85e-c4af26da4a2d', '[{"url": "https://attack.mitre.org/techniques/T1134/001", "external_id": "T1134.001", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/windows-server-docs/identity/ad-ds/manage/component-updates/command-line-process-auditing", "description": "Mathers, B. (2017, March 7). Command line process auditing. Retrieved April 21, 2017.", "source_name": "Microsoft Command-line Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2971}, "x_mitre_detection": {"Num": 0, "Raw": "\"If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\\n\\nAnalysts can also monitor for use of Windows APIs such as <code>DuplicateToken(Ex)</code>, <code> ImpersonateLoggedOnUser </code>, and <code> SetThreadToken </code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.\"", "Str": "If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\n\nAnalysts can also monitor for use of Windows APIs such as <code>DuplicateToken(Ex)</code>, <code> ImpersonateLoggedOnUser </code>, and <code> SetThreadToken </code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.", "Type": 3, "Index": 2226}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2923}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1975}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2892}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\",\n      \"System access controls\",\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 2086}}', 'T1134.001', 'Adversaries may duplicate then impersonate another user''s token to escalate privileges and bypass access controls. An adversary can create a new access token that duplicates an existing token using <code>DuplicateToken(Ex)</code>. The token can then be used with <code>ImpersonateLoggedOnUser</code> to allow the calling thread to impersonate a logged on user''s security context, or with <code>SetThreadToken</code> to assign the impersonated token to a thread.
INSERT INTO public.attack_patterns VALUES (503, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.497507+00', NULL, 'attack-pattern--7c46b364-8496-4234-8a56-f7e6727e21e1', '[{"url": "https://attack.mitre.org/techniques/T1020/001", "external_id": "T1020.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/117.html", "external_id": "CAPEC-117", "source_name": "capec"}, {"url": "https://www.cisco.com/c/en/us/td/docs/routers/crs/software/crs_r5-1/interfaces/configuration/guide/hc51xcrsbook/hc51span.html", "description": "Cisco. (n.d.). Cisco IOS XR Interface and Hardware Component Configuration Guide for the Cisco CRS Router, Release 5.1.x. Retrieved October 19, 2020.", "source_name": "Cisco Traffic Mirroring"}, {"url": "https://www.juniper.net/documentation/en_US/junos/topics/concept/port-mirroring-ex-series.html", "description": "Juniper. (n.d.). Understanding Port Mirroring on EX2200, EX3200, EX3300, EX4200, EX4500, EX4550, EX6200, and EX8200 Series Switches. Retrieved October 19, 2020.", "source_name": "Juniper Traffic Mirroring"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-106A", "description": "US-CERT. (2018, April 20). Alert (TA18-106A) Russian State-Sponsored Cyber Actors Targeting Network Infrastructure Devices. Retrieved October 19, 2020.", "source_name": "US-CERT-TA18-106A"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4215}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor network traffic for uncommon data flows (e.g. unusual network communications, suspicious communications that have never been seen before, communications sending fixed size data packets at regular intervals).  Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. \"", "Str": "Monitor network traffic for uncommon data flows (e.g. unusual network communications, suspicious communications that have never been seen before, communications sending fixed size data packets at regular intervals).  Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. ", "Type": 3, "Index": 3685}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 4167}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3551}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4069}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4111}}', 'T1020.001', 'Adversaries may leverage traffic mirroring in order to automate data exfiltration over compromised network infrastructure.  Traffic mirroring is a native feature for some network devices and used for network analysis and may be configured to duplicate traffic and forward to one or more destinations for analysis by a network analyzer or other monitoring device. (Citation: Cisco Traffic Mirroring) (Citation: Juniper Traffic Mirroring)
INSERT INTO public.attack_patterns VALUES (504, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.529279+00', NULL, 'attack-pattern--451a9977-d255-43c9-b431-66de80130c8c', '[{"url": "https://attack.mitre.org/techniques/T1205", "external_id": "T1205", "source_name": "mitre-attack"}, {"url": "https://www.giac.org/paper/gcih/342/handle-cd00r-invisible-backdoor/103631", "description": "Hartrell, Greg. (2002, August). Get a handle on cd00r: The invisible backdoor. Retrieved October 13, 2018.", "source_name": "Hartrell cd00r 2002"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/09/synful_knock_-_acis.html", "description": "Bill Hau, Tony Lee, Josh Homan. (2015, September 15). SYNful Knock - A Cisco router implant - Part I. Retrieved October 19, 2020.", "source_name": "FireEye - Synful Knock"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://www.bleepingcomputer.com/news/security/ryuk-ransomware-uses-wake-on-lan-to-encrypt-offline-devices/", "description": "Abrams, L. (2021, January 14). Ryuk Ransomware Uses Wake-on-Lan To Encrypt Offline Devices. Retrieved February 11, 2021.", "source_name": "Bleeping Computer - Ryuk WoL"}, {"url": "https://www.amd.com/system/files/TechDocs/20213.pdf", "description": "AMD. (1995, November 1). Magic Packet Technical White Paper. Retrieved February 17, 2021.", "source_name": "AMD Magic Packet"}, {"url": "https://gitlab.com/wireshark/wireshark/-/wikis/WakeOnLAN", "description": "Perry, David. (2020, August 11). WakeOnLAN (WOL). Retrieved February 17, 2021.", "source_name": "GitLab WakeOnLAN"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 6716}, "x_mitre_detection": {"Num": 0, "Raw": "\"Record network packets sent to and from the system, looking for extraneous packets that do not belong to established flows.\\n\\nThe Wake-on-LAN magic packet consists of 6 bytes of <code>FF</code> followed by sixteen repetitions of the target system''s IEEE address. Seeing this string anywhere in a packet''s payload may be indicative of a Wake-on-LAN attempt.(Citation: GitLab WakeOnLAN)\"", "Str": "Record network packets sent to and from the system, looking for extraneous packets that do not belong to established flows.\n\nThe Wake-on-LAN magic packet consists of 6 bytes of <code>FF</code> followed by sixteen repetitions of the target system''s IEEE address. Seeing this string anywhere in a packet''s payload may be indicative of a Wake-on-LAN attempt.(Citation: GitLab WakeOnLAN)", "Type": 3, "Index": 6069}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 6621}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Josh Day, Gigamon\"\n    ]", "Str": "", "Type": 5, "Index": 5738}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 5801}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6489}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Defensive network service scanning\"\n    ]", "Str": "", "Type": 5, "Index": 5992}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6532}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6574}}', 'T1205', 'Adversaries may use traffic signaling to hide open ports or other malicious functionality used for persistence or command and control. Traffic signaling involves the use of a magic value or sequence that must be sent to a system to trigger a special response, such as opening a closed port or executing a malicious task. This may take the form of sending a series of packets with certain characteristics before a port will be opened that the adversary can use for command and control. Usually this series of packets consists of attempted connections to a predefined sequence of closed ports (i.e. [Port Knocking](https://attack.mitre.org/techniques/T1205/001)), but can involve unusual flags, specific strings, or other unique characteristics. After the sequence is completed, opening a port may be accomplished by the host-based firewall, but could also be implemented by custom software.
INSERT INTO public.attack_patterns VALUES (506, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.598475+00', NULL, 'attack-pattern--d0613359-5781-4fd2-b5be-c269270be1f6', '[{"url": "https://attack.mitre.org/techniques/T1565/002", "external_id": "T1565.002", "source_name": "mitre-attack"}, {"url": "https://content.fireeye.com/apt/rpt-apt38", "description": "FireEye. (2018, October 03). APT38: Un-usual Suspects. Retrieved November 6, 2018.", "source_name": "FireEye APT38 Oct 2018"}, {"url": "https://www.justice.gov/opa/press-release/file/1092091/download", "description": "Department of Justice. (2018, September 6). Criminal Complaint - United States of America v. PARK JIN HYOK. Retrieved March 29, 2019.", "source_name": "DOJ Lazarus Sony 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3153}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the manipulation of data as at passes over a network can be difficult without the appropriate tools. In some cases integrity verification checks, such as file hashing, may be used on critical files as they transit a network. With some critical processes involving transmission of data, manual or out-of-band integrity checking may be useful for identifying manipulated data. \"", "Str": "Detecting the manipulation of data as at passes over a network can be difficult without the appropriate tools. In some cases integrity verification checks, such as file hashing, may be used on critical files as they transit a network. With some critical processes involving transmission of data, manual or out-of-band integrity checking may be useful for identifying manipulated data. ", "Type": 3, "Index": 2459}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3075}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 2875}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2294}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2933}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2975}}', 'T1565.002', 'Adversaries may alter data en route to storage or other systems in order to manipulate external outcomes or hide activity.(Citation: FireEye APT38 Oct 2018)(Citation: DOJ Lazarus Sony 2018) By manipulating transmitted data, adversaries may attempt to affect a business process, organizational understanding, and decision making.
INSERT INTO public.attack_patterns VALUES (508, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.684498+00', NULL, 'attack-pattern--63220765-d418-44de-8fae-694b3912317d', '[{"url": "https://attack.mitre.org/techniques/T1546/005", "external_id": "T1546.005", "source_name": "mitre-attack"}, {"url": "https://ss64.com/bash/trap.html", "description": "ss64. (n.d.). trap. Retrieved May 21, 2019.", "source_name": "Trap Manual"}, {"url": "https://bash.cyberciti.biz/guide/Trap_statement", "description": "Cyberciti. (2016, March 29). Trap statement. Retrieved May 21, 2019.", "source_name": "Cyberciti Trap Statements"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2738}, "x_mitre_detection": {"Num": 0, "Raw": "\"Trap commands must be registered for the shell or programs, so they appear in files. Monitoring files for suspicious or overly broad trap commands can narrow down suspicious behavior during an investigation. Monitor for suspicious processes executed through trap interrupts.\"", "Str": "Trap commands must be registered for the shell or programs, so they appear in files. Monitoring files for suspicious or overly broad trap commands can narrow down suspicious behavior during an investigation. Monitor for suspicious processes executed through trap interrupts.", "Type": 3, "Index": 2256}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2677}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2090}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2565}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2607}}', 'T1546.005', 'Adversaries may establish persistence by executing malicious content triggered by an interrupt signal. The <code>trap</code> command allows programs and shells to specify commands that will be executed upon receiving interrupt signals. A common situation is a script allowing for graceful termination and handling of common keyboard interrupts like <code>ctrl+c</code> and <code>ctrl+d</code>.
INSERT INTO public.attack_patterns VALUES (510, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.760238+00', NULL, 'attack-pattern--9fa07bef-9c81-421e-a8e5-ad4366c5a925', '[{"url": "https://attack.mitre.org/techniques/T1199", "external_id": "T1199", "source_name": "mitre-attack"}, {"url": "https://us-cert.cisa.gov/APTs-Targeting-IT-Service-Provider-Customers", "description": "CISA. (n.d.). APTs Targeting IT Service Provider Customers. Retrieved November 16, 2020.", "source_name": "CISA IT Service Providers"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 3094}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish monitoring for activity conducted by second and third party providers and other trusted entities that may be leveraged as a means to gain access to the network. Depending on the type of relationship, an adversary may have access to significant amounts of information about the target before conducting an operation, especially if the trusted relationship is based on IT services. Adversaries may be able to act quickly towards an objective, so proper monitoring for behavior related to Credential Access, Lateral Movement, and Collection will be important to detect the intrusion.\"", "Str": "Establish monitoring for activity conducted by second and third party providers and other trusted entities that may be leveraged as a means to gain access to the network. Depending on the type of relationship, an adversary may have access to significant amounts of information about the target before conducting an operation, especially if the trusted relationship is based on IT services. Adversaries may be able to act quickly towards an objective, so proper monitoring for behavior related to Credential Access, Lateral Movement, and Collection will be important to detect the intrusion.", "Type": 3, "Index": 2331}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2988}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2098}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Logon Session: Logon Session Metadata\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2154}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2956}}', 'T1199', 'Adversaries may breach or otherwise leverage organizations who have access to intended victims. Access through trusted third party relationship exploits an existing connection that may not be protected or receives less scrutiny than standard mechanisms of gaining access to a network.
INSERT INTO public.attack_patterns VALUES (511, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.794626+00', NULL, 'attack-pattern--dd43c543-bb85-4a6f-aa6e-160d90d06a49', '[{"url": "https://attack.mitre.org/techniques/T1111", "external_id": "T1111", "source_name": "mitre-attack"}, {"url": "https://dl.mandiant.com/EE/assets/PDF_MTrends_2011.pdf", "description": "Mandiant. (2011, January 27). Mandiant M-Trends 2011. Retrieved January 10, 2016.", "source_name": "Mandiant M Trends 2011"}, {"url": "https://gcn.com/articles/2011/06/07/rsa-confirms-tokens-used-to-hack-lockheed.aspx", "description": "Jackson, William. (2011, June 7). RSA confirms its tokens used in Lockheed hack. Retrieved September 24, 2018.", "source_name": "GCN RSA June 2011"}, {"url": "http://www.trendmicro.com/cloud-content/us/pdfs/security-intelligence/white-papers/wp-finding-holes-operation-emmental.pdf", "description": "Sancho, D., Hacquebord, F., Link, R. (2014, July 22). Finding Holes Operation Emmental. Retrieved February 9, 2016.", "source_name": "Operation Emmental"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4889}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting use of proxied smart card connections by an adversary may be difficult because it requires the token to be inserted into a system; thus it is more likely to be in use by a legitimate user and blend in with other network behavior.\\n\\nSimilar to [Input Capture](https://attack.mitre.org/techniques/T1056), keylogging activity can take various forms but can may be detected via installation of a driver, setting a hook, or usage of particular API calls associated with polling to intercept keystrokes.\"", "Str": "Detecting use of proxied smart card connections by an adversary may be difficult because it requires the token to be inserted into a system; thus it is more likely to be in use by a legitimate user and blend in with other network behavior.\n\nSimilar to [Input Capture](https://attack.mitre.org/techniques/T1056), keylogging activity can take various forms but can may be detected via installation of a driver, setting a hook, or usage of particular API calls associated with polling to intercept keystrokes.", "Type": 3, "Index": 3751}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4409}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"John Lambert, Microsoft Threat Intelligence Center\"\n    ]", "Str": "", "Type": 5, "Index": 3497}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Driver: Driver Load\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3593}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4294}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Smart card Proxy: Use of smart cards for single or multifactor authentication to access to network resources. Attached smart card reader with card inserted.\\n\\nOut-of-band one-time code: Access to the device, service, or communications to intercept the one-time code.\\n\\nHardware token: Access to the seed and algorithm of generating one-time codes.\"\n    ]", "Str": "", "Type": 5, "Index": 4499}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4337}}', 'T1111', 'Adversaries may target two-factor authentication mechanisms, such as smart cards, to gain access to credentials that can be used to access systems, services, and network resources. Use of two or multi-factor authentication (2FA or MFA) is recommended and provides a higher level of security than user names and passwords alone, but organizations should be aware of techniques that could be used to intercept and bypass these security mechanisms. 
INSERT INTO public.attack_patterns VALUES (513, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.854709+00', NULL, 'attack-pattern--b63a34e8-0a61-4c97-a23b-bf8a2ed812e2', '[{"url": "https://attack.mitre.org/techniques/T1546/004", "external_id": "T1546.004", "source_name": "mitre-attack"}, {"url": "https://www.intezer.com/blog/research/kaiji-new-chinese-linux-malware-turning-to-golang/", "description": "Paul Litvak. (2020, May 4). Kaiji: New Chinese Linux malware turning to Golang. Retrieved December 17, 2020.", "source_name": "intezer-kaiji-malware"}, {"url": "https://bencane.com/2013/09/16/understanding-a-little-more-about-etcprofile-and-etcbashrc/", "description": "Benjamin Cane. (2013, September 16). Understanding a little more about /etc/profile and /etc/bashrc. Retrieved February 25, 2021.", "source_name": "bencane blog bashrc"}, {"url": "https://www.anomali.com/blog/illicit-cryptomining-threat-actor-rocke-changes-tactics-now-more-difficult-to-detect", "description": "Anomali Threat Research. (2019, October 15). Illicit Cryptomining Threat Actor Rocke Changes Tactics, Now More Difficult to Detect. Retrieved December 17, 2020.", "source_name": "anomali-rocke-tactics"}, {"url": "https://wiki.archlinux.org/index.php/Bash#Invocation", "description": "ArchWiki. (2021, January 19). Bash. Retrieved February 25, 2021.", "source_name": "Linux manual bash invocation"}, {"url": "https://unit42.paloaltonetworks.com/unit42-new-iotlinux-malware-targets-dvrs-forms-botnet/", "description": "Claud Xiao and Cong Zheng. (2017, April 6). New IoT/Linux Malware Targets DVRs, Forms Botnet. Retrieved December 17, 2020.", "source_name": "Tsunami"}, {"url": "https://www.anomali.com/blog/pulling-linux-rabbit-rabbot-malware-out-of-a-hat", "description": "Anomali Threat Research. (2018, December 6). Pulling Linux Rabbit/Rabbot Malware Out of a Hat. Retrieved December 17, 2020.", "source_name": "anomali-linux-rabbit"}, {"url": "https://blog.sucuri.net/2018/05/shell-logins-as-a-magento-reinfection-vector.html", "description": "Cesar Anjos. (2018, May 31). Shell Logins as a Magento Reinfection Vector. Retrieved December 17, 2020.", "source_name": "Magento"}, {"url": "https://scriptingosx.com/2019/06/moving-to-zsh-part-2-configuration-files/", "description": "Armin Briegel. (2019, June 5). Moving to zsh, part 2: Configuration Files. Retrieved February 25, 2021.", "source_name": "ScriptingOSX zsh"}, {"url": "https://posts.specterops.io/persistent-jxa-66e1c3cd1cf5", "description": "Leo Pitt. (2020, August 6). Persistent JXA - A poor man''s Powershell for macOS. Retrieved January 11, 2021.", "source_name": "PersistentJXA_leopitt"}, {"url": "https://github.com/D00MFist/PersistentJXA/blob/master/BashProfilePersist.js", "description": "Leo Pitt. (2020, November 11). Github - PersistentJXA/BashProfilePersist.js. Retrieved January 11, 2021.", "source_name": "code_persistence_zsh"}, {"url": "https://objective-see.com/blog/blog_0x48.html", "description": "Patrick Wardle. (2019, September 17). Writing a File Monitor with Apple''s Endpoint Security Framework. Retrieved December 17, 2020.", "source_name": "ESF_filemonitor"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 8686}, "x_mitre_detection": {"Num": 0, "Raw": "\"While users may customize their shell profile files, there are only certain types of commands that typically appear in these files. Monitor for abnormal commands such as execution of unknown programs, opening network sockets, or reaching out across the network when user profiles are loaded during the login process.\\n\\nMonitor for changes to <code>/etc/profile</code> and <code>/etc/profile.d</code>, these files should only be modified by system administrators. MacOS users can leverage Endpoint Security Framework file events monitoring these specific files.(Citation: ESF_filemonitor) \\n\\nFor most Linux and macOS systems, a list of file paths for valid shell options available on a system are located in the <code>/etc/shells</code> file.\\n\"", "Str": "While users may customize their shell profile files, there are only certain types of commands that typically appear in these files. Monitor for abnormal commands such as execution of unknown programs, opening network sockets, or reaching out across the network when user profiles are loaded during the login process.\n\nMonitor for changes to <code>/etc/profile</code> and <code>/etc/profile.d</code>, these files should only be modified by system administrators. MacOS users can leverage Endpoint Security Framework file events monitoring these specific files.(Citation: ESF_filemonitor) \n\nFor most Linux and macOS systems, a list of file paths for valid shell options available on a system are located in the <code>/etc/shells</code> file.\n", "Type": 3, "Index": 7733}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 8625}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Robert Wilson\",\n      \"Tony Lambert, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 7474}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 7567}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 8513}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 8555}}', 'T1546.004', 'Adversaries may establish persistence through executing malicious commands triggered by a user’s shell. User [Unix Shell](https://attack.mitre.org/techniques/T1059/004)s execute several configuration scripts at different points throughout the session based on events. For example, when a user opens a command-line interface or remotely logs in (such as via SSH) a login shell is initiated. The login shell executes scripts from the system (<code>/etc</code>) and the user’s home directory (<code>~/</code>) to configure the environment. All login shells on a system use /etc/profile when initiated. These configuration scripts run at the permission level of their directory and are often used to set environment variables, create aliases, and customize the user’s environment. When the shell exits or terminates, additional shell scripts are executed to ensure the shell exits appropriately. 
INSERT INTO public.attack_patterns VALUES (514, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.895149+00', NULL, 'attack-pattern--435dfb86-2697-4867-85b5-2fef496c0517', '[{"url": "https://attack.mitre.org/techniques/T1552", "external_id": "T1552", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3382}, "x_mitre_detection": {"Num": 0, "Raw": "\"While detecting adversaries accessing credentials may be difficult without knowing they exist in the environment, it may be possible to detect adversary use of credentials they have obtained. Monitor the command-line arguments of executing processes for suspicious words or regular expressions that may indicate searching for a password (for example: password, pwd, login, secure, or credentials). See [Valid Accounts](https://attack.mitre.org/techniques/T1078) for more information.\\n\\nMonitor for suspicious file access activity, specifically indications that a process is reading multiple files in a short amount of time and/or using command-line arguments  indicative of searching for credential material (ex: regex patterns). These may be indicators of automated/scripted credential access behavior.\\n\\nMonitoring when the user''s <code>.bash_history</code> is read can help alert to suspicious activity. While users do typically rely on their history of commands, they often access this history through other utilities like \\\"history\\\" instead of commands like <code>cat ~/.bash_history</code>.\\n\\nAdditionally, monitor processes for applications that can be used to query the Registry, such as [Reg](https://attack.mitre.org/software/S0075), and collect command parameters that may indicate credentials are being searched. Correlate activity with related suspicious behavior that may indicate an active intrusion to reduce false positives.\"", "Str": "While detecting adversaries accessing credentials may be difficult without knowing they exist in the environment, it may be possible to detect adversary use of credentials they have obtained. Monitor the command-line arguments of executing processes for suspicious words or regular expressions that may indicate searching for a password (for example: password, pwd, login, secure, or credentials). See [Valid Accounts](https://attack.mitre.org/techniques/T1078) for more information.\n\nMonitor for suspicious file access activity, specifically indications that a process is reading multiple files in a short amount of time and/or using command-line arguments  indicative of searching for credential material (ex: regex patterns). These may be indicators of automated/scripted credential access behavior.\n\nMonitoring when the user''s <code>.bash_history</code> is read can help alert to suspicious activity. While users do typically rely on their history of commands, they often access this history through other utilities like \"history\" instead of commands like <code>cat ~/.bash_history</code>.\n\nAdditionally, monitor processes for applications that can be used to query the Registry, such as [Reg](https://attack.mitre.org/software/S0075), and collect command parameters that may indicate credentials are being searched. Correlate activity with related suspicious behavior that may indicate an active intrusion to reduce false positives.", "Type": 3, "Index": 1583}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3192}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Access\",\n      \"Process: Process Creation\",\n      \"User Account: User Account Authentication\",\n      \"Windows Registry: Windows Registry Key Access\"\n    ]", "Str": "", "Type": 5, "Index": 1346}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3063}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3106}}', 'T1552', 'Adversaries may search compromised systems to find and obtain insecurely stored credentials. These credentials can be stored and/or misplaced in many locations on a system, including plaintext files (e.g. [Bash History](https://attack.mitre.org/techniques/T1552/003)), operating system or application-specific repositories (e.g. [Credentials in Registry](https://attack.mitre.org/techniques/T1552/002)), or other specialized files/artifacts (e.g. [Private Keys](https://attack.mitre.org/techniques/T1552/004)).', 'Unsecured Credentials', 0);
INSERT INTO public.attack_patterns VALUES (516, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.969903+00', NULL, 'attack-pattern--3ee16395-03f0-4690-a32e-69ce9ada0f9e', '[{"url": "https://attack.mitre.org/techniques/T1608/001", "external_id": "T1608.001", "source_name": "mitre-attack"}, {"url": "https://www.volexity.com/blog/2020/11/06/oceanlotus-extending-cyber-espionage-operations-through-fake-websites/", "description": "Adair, S. and Lancaster, T. (2020, November 6). OceanLotus: Extending Cyber Espionage Operations Through Fake Websites. Retrieved November 20, 2020.", "source_name": "Volexity Ocean Lotus November 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3146}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle, such as [User Execution](https://attack.mitre.org/techniques/T1204) or [Ingress Tool Transfer](https://attack.mitre.org/techniques/T1105).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle, such as [User Execution](https://attack.mitre.org/techniques/T1204) or [Ingress Tool Transfer](https://attack.mitre.org/techniques/T1105).", "Type": 3, "Index": 2677}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3102}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Kobi Haimovich, CardinalOps\"\n    ]", "Str": "", "Type": 5, "Index": 2607}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3071}}', 'T1608.001', 'Adversaries may upload malware to third-party or adversary controlled infrastructure to make it accessible during targeting. Malicious software can include payloads, droppers, post-compromise tools, backdoors, and a variety of other malicious content. Adversaries may upload malware to support their operations, such as making a payload available to a victim network to enable [Ingress Tool Transfer](https://attack.mitre.org/techniques/T1105) by placing it on an Internet accessible web server.
INSERT INTO public.attack_patterns VALUES (518, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.032292+00', NULL, 'attack-pattern--51a14c76-dd3b-440b-9c20-2bf91d25a814', '[{"url": "https://attack.mitre.org/techniques/T1550", "external_id": "T1550", "source_name": "mitre-attack"}, {"url": "https://csrc.nist.gov/glossary/term/authentication", "description": "NIST. (n.d.). Authentication. Retrieved January 30, 2020.", "source_name": "NIST Authentication"}, {"url": "https://csrc.nist.gov/glossary/term/Multi_Factor-Authentication", "description": "NIST. (n.d.). Multi-Factor Authentication (MFA). Retrieved January 30, 2020.", "source_name": "NIST MFA"}, {"url": "https://technet.microsoft.com/en-us/library/dn487457.aspx", "description": "Microsoft. (2016, April 15). Audit Policy Recommendations. Retrieved June 3, 2016.", "source_name": "TechNet Audit Policy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4346}, "x_mitre_detection": {"Num": 0, "Raw": "\"Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\"", "Str": "Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services.(Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).", "Type": 3, "Index": 3391}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\",\n      \"SaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4238}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Web Credential: Web Credential Usage\",\n      \"Application Log: Application Log Content\",\n      \"User Account: User Account Authentication\",\n      \"Active Directory: Active Directory Credential Request\"\n    ]", "Str": "", "Type": 5, "Index": 3029}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4206}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"System Access Controls\"\n    ]", "Str": "", "Type": 5, "Index": 3326}}', 'T1550', 'Adversaries may use alternate authentication material, such as password hashes, Kerberos tickets, and application access tokens, in order to move laterally within an environment and bypass normal system access controls. 
INSERT INTO public.attack_patterns VALUES (519, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.072542+00', NULL, 'attack-pattern--91541e7e-b969-40c6-bbd8-1b5352ec2938', '[{"url": "https://attack.mitre.org/techniques/T1497/002", "external_id": "T1497.002", "source_name": "mitre-attack"}, {"url": "https://www.sans.org/reading-room/whitepapers/forensics/detecting-malware-sandbox-evasion-techniques-36667", "description": "Keragala, D. (2016, January 16). Detecting Malware and Sandbox Evasion Techniques. Retrieved April 17, 2019.", "source_name": "Sans Virtual Jan 2016"}, {"url": "https://unit42.paloaltonetworks.com/unit42-sofacy-continues-global-attacks-wheels-new-cannon-trojan/", "description": "Falcone, R., Lee, B.. (2018, November 20). Sofacy Continues Global Attacks and Wheels Out New ‘Cannon’ Trojan. Retrieved April 23, 2019.", "source_name": "Unit 42 Sofacy Nov 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/04/fin7-phishing-lnk.html", "description": "Carr, N., et al. (2017, April 24). FIN7 Evolution and the Phishing LNK. Retrieved April 24, 2017.", "source_name": "FireEye FIN7 April 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4389}, "x_mitre_detection": {"Num": 0, "Raw": "\"User activity-based checks will likely occur in the first steps of an operation but may also occur throughout as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained. Detecting actions related to virtualization and sandbox identification may be difficult depending on the adversary''s implementation and monitoring required. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of Discovery, especially in a short period of time, may aid in detection. \"", "Str": "User activity-based checks will likely occur in the first steps of an operation but may also occur throughout as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained. Detecting actions related to virtualization and sandbox identification may be difficult depending on the adversary''s implementation and monitoring required. Monitoring for suspicious processes being spawned that gather a variety of system information or perform other forms of Discovery, especially in a short period of time, may aid in detection. ", "Type": 3, "Index": 3563}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4311}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Deloitte Threat Library Team\"\n    ]", "Str": "", "Type": 5, "Index": 3193}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3267}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4280}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Static File Analysis\",\n      \"Signature-based detection\",\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 3413}}', 'T1497.002', 'Adversaries may employ various user activity checks to detect and avoid virtualization and analysis environments. This may include changing behaviors based on the results of checks for the presence of artifacts indicative of a virtual machine environment (VME) or sandbox. If the adversary detects a VME, they may alter their malware to disengage from the victim or conceal the core functions of the implant. They may also search for VME artifacts before dropping secondary or additional payloads. Adversaries may use the information learned from [Virtualization/Sandbox Evasion](https://attack.mitre.org/techniques/T1497) during automated discovery to shape follow-on behaviors. 
INSERT INTO public.attack_patterns VALUES (523, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.131999+00', NULL, 'attack-pattern--01327cde-66c4-4123-bf34-5f258d59457b', '[{"url": "https://attack.mitre.org/techniques/T1021/005", "external_id": "T1021.005", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/555.html", "external_id": "CAPEC-555", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2157}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use of VNC may be legitimate depending on the environment and how it’s used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with VNC.\"", "Str": "Use of VNC may be legitimate depending on the environment and how it’s used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with VNC.", "Type": 3, "Index": 1689}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1975}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1520}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1944}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"VNC server installed and listening for connections.\"\n    ]", "Str": "", "Type": 5, "Index": 2065}}', 'T1021.005', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to remotely control machines using Virtual Network Computing (VNC). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (524, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.159836+00', NULL, 'attack-pattern--b17a1a56-e99c-403c-8948-561df0cffe81', '[{"url": "https://attack.mitre.org/techniques/T1078", "external_id": "T1078", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/560.html", "external_id": "CAPEC-560", "source_name": "capec"}, {"url": "https://technet.microsoft.com/en-us/library/dn535501.aspx", "description": "Microsoft. (2016, April 15). Attractive Accounts for Credential Theft. Retrieved June 3, 2016.", "source_name": "TechNet Credential Theft"}, {"url": "https://technet.microsoft.com/en-us/library/dn487457.aspx", "description": "Microsoft. (2016, April 15). Audit Policy Recommendations. Retrieved June 3, 2016.", "source_name": "TechNet Audit Policy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 5036}, "x_mitre_detection": {"Num": 0, "Raw": "\"Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services. (Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\\n\\nPerform regular audits of domain and local system accounts to detect accounts that may have been created by an adversary for persistence. Checks on these accounts could also include whether default accounts such as Guest have been activated. These audits should also include checks on any appliances and applications for default credentials or SSH keys, and if any are discovered, they should be updated immediately.\"", "Str": "Configure robust, consistent account activity audit policies across the enterprise and with externally accessible services. (Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\n\nPerform regular audits of domain and local system accounts to detect accounts that may have been created by an adversary for persistence. Checks on these accounts could also include whether default accounts such as Guest have been activated. These audits should also include checks on any appliances and applications for default credentials or SSH keys, and if any are discovered, they should be updated immediately.", "Type": 3, "Index": 3415}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4846}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Netskope\",\n      \"Mark Wee\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2913}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3058}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4733}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Firewall\",\n      \"Host intrusion prevention systems\",\n      \"Network intrusion detection system\",\n      \"Application control\",\n      \"System access controls\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 3196}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4776}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4657}}', 'T1078', 'Adversaries may obtain and abuse credentials of existing accounts as a means of gaining Initial Access, Persistence, Privilege Escalation, or Defense Evasion. Compromised credentials may be used to bypass access controls placed on various resources on systems within the network and may even be used for persistent access to remote systems and externally available services, such as VPNs, Outlook Web Access and remote desktop. Compromised credentials may also grant an adversary increased privilege to specific systems or access to restricted areas of the network. Adversaries may choose not to use malware or tools in conjunction with the legitimate access those credentials provide to make it harder to detect their presence.
INSERT INTO public.attack_patterns VALUES (526, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.22955+00', NULL, 'attack-pattern--6faf650d-bf31-4eb4-802d-1000cf38efaf', '[{"url": "https://attack.mitre.org/techniques/T1125", "external_id": "T1125", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/634.html", "external_id": "CAPEC-634", "source_name": "capec"}, {"url": "https://objective-see.com/blog/blog_0x25.html", "description": "Patrick Wardle. (n.d.). Retrieved March 20, 2018.", "source_name": "objective-see 2017 review"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3012}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of this technique may be difficult due to the various APIs that may be used. Telemetry data regarding API use may not be useful depending on how a system is normally used, but may provide context to other potentially malicious activity occurring on a system.\\n\\nBehavior that could indicate technique use include an unknown or unusual process accessing APIs associated with devices or software that interact with the video camera, recording devices, or recording software, and a process periodically writing files to disk that contain video or camera image data.\"", "Str": "Detection of this technique may be difficult due to the various APIs that may be used. Telemetry data regarding API use may not be useful depending on how a system is normally used, but may provide context to other potentially malicious activity occurring on a system.\n\nBehavior that could indicate technique use include an unknown or unusual process accessing APIs associated with devices or software that interact with the video camera, recording devices, or recording software, and a process periodically writing files to disk that contain video or camera image data.", "Type": 3, "Index": 2252}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2949}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2092}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2148}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2859}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2902}}', 'T1125', 'An adversary can leverage a computer''s peripheral devices (e.g., integrated cameras or webcams) or applications (e.g., video call services) to capture video recordings for the purpose of gathering information. Images may also be captured from devices or applications, potentially in specified intervals, in lieu of video files.
INSERT INTO public.attack_patterns VALUES (528, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.309561+00', NULL, 'attack-pattern--39cc9f64-cf74-4a48-a4d8-fe98c54a02e0', '[{"url": "https://attack.mitre.org/techniques/T1584/003", "external_id": "T1584.003", "source_name": "mitre-attack"}, {"url": "https://media.defense.gov/2019/Oct/18/2002197242/-1/-1/0/NSA_CSA_Turla_20191021%20ver%204%20-%20nsa.gov.pdf", "description": "NSA/NCSC. (2019, October 21). Cybersecurity Advisory: Turla Group Exploits Iranian APT To Expand Coverage Of Victims. Retrieved October 16, 2020.", "source_name": "NSA NCSC Turla OilRig"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2286}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 1928}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2242}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2211}}', 'T1584.003', 'Adversaries may compromise third-party Virtual Private Servers (VPSs) that can be used during targeting. There exist a variety of cloud service providers that will sell virtual machines/containers as a service. Adversaries may compromise VPSs purchased by third-party entities. By compromising a VPS to use as infrastructure, adversaries can make it difficult to physically tie back operations to themselves.(Citation: NSA NCSC Turla OilRig)
INSERT INTO public.attack_patterns VALUES (530, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.385285+00', NULL, 'attack-pattern--dfd7cc1d-e1d8-4394-a198-97c4cab8aa67', '[{"url": "https://attack.mitre.org/techniques/T1059/005", "external_id": "T1059.005", "source_name": "mitre-attack"}, {"url": "https://devblogs.microsoft.com/vbteam/visual-basic-support-planned-for-net-5-0/", "description": ".NET Team. (2020, March 11). Visual Basic support planned for .NET 5.0. Retrieved June 23, 2020.", "source_name": "VB .NET Mar 2020"}, {"url": "https://docs.microsoft.com/dotnet/visual-basic/", "description": "Microsoft. (n.d.). Visual Basic documentation. Retrieved June 23, 2020.", "source_name": "VB Microsoft"}, {"url": "https://docs.microsoft.com/office/vba/api/overview/", "description": "Microsoft. (2019, June 11). Office VBA Reference. Retrieved June 23, 2020.", "source_name": "Microsoft VBA"}, {"url": "https://en.wikipedia.org/wiki/Visual_Basic_for_Applications", "description": "Wikipedia. (n.d.). Visual Basic for Applications. Retrieved August 13, 2020.", "source_name": "Wikipedia VBA"}, {"url": "https://docs.microsoft.com/previous-versions//1kw29xwf(v=vs.85)", "description": "Microsoft. (2011, April 19). What Is VBScript?. Retrieved March 28, 2020.", "source_name": "Microsoft VBScript"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5255}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for events associated with VB execution, such as Office applications spawning processes, usage of the Windows Script Host (typically cscript.exe or wscript.exe), file activity involving VB payloads or scripts, or loading of modules associated with VB languages (ex: vbscript.dll). VB execution is likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other programable post-compromise behaviors and could be used as indicators of detection leading back to the source.\\n\\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If VB execution is restricted for normal users, then any attempts to enable related components running on a system would be considered suspicious. If VB execution is not commonly used on a system, but enabled, execution running out of cycle from patching or other administrator functions is suspicious. Payloads and scripts should be captured from the file system when possible to determine their actions and intent.\"", "Str": "Monitor for events associated with VB execution, such as Office applications spawning processes, usage of the Windows Script Host (typically cscript.exe or wscript.exe), file activity involving VB payloads or scripts, or loading of modules associated with VB languages (ex: vbscript.dll). VB execution is likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for execution and subsequent behavior. Actions may be related to network and system information [Discovery](https://attack.mitre.org/tactics/TA0007), [Collection](https://attack.mitre.org/tactics/TA0009), or other programable post-compromise behaviors and could be used as indicators of detection leading back to the source.\n\nUnderstanding standard usage patterns is important to avoid a high number of false positives. If VB execution is restricted for normal users, then any attempts to enable related components running on a system would be considered suspicious. If VB execution is not commonly used on a system, but enabled, execution running out of cycle from patching or other administrator functions is suspicious. Payloads and scripts should be captured from the file system when possible to determine their actions and intent.", "Type": 3, "Index": 3698}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 5177}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3531}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5049}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5091}}', 'T1059.005', 'Adversaries may abuse Visual Basic (VB) for execution. VB is a programming language created by Microsoft with interoperability with many Windows technologies such as [Component Object Model](https://attack.mitre.org/techniques/T1559/001) and the [Native API](https://attack.mitre.org/techniques/T1106) through the Windows API. Although tagged as legacy with no planned future evolutions, VB is integrated and supported in the .NET Framework and cross-platform .NET Core.(Citation: VB .NET Mar 2020)(Citation: VB Microsoft)
INSERT INTO public.attack_patterns VALUES (532, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.459025+00', NULL, 'attack-pattern--5502c4e9-24ef-4d5f-8ee9-9e906c2f82c4', '[{"url": "https://attack.mitre.org/techniques/T1595/002", "external_id": "T1595.002", "source_name": "mitre-attack"}, {"url": "https://wiki.owasp.org/index.php/OAT-014_Vulnerability_Scanning", "description": "OWASP Wiki. (2018, February 16). OAT-014 Vulnerability Scanning. Retrieved October 20, 2020.", "source_name": "OWASP Vuln Scanning"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3254}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.\\n\\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Monitor for suspicious network traffic that could be indicative of scanning, such as large quantities originating from a single source (especially if the source is known to be associated with an adversary/botnet). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.\n\nMuch of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2437}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3210}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2307}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3179}}', 'T1595.002', 'Adversaries may scan victims for vulnerabilities that can be used during targeting. Vulnerability scans typically check if the configuration of a target host/application (ex: software and version) potentially aligns with the target of a specific exploit the adversary may seek to use.
INSERT INTO public.attack_patterns VALUES (533, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.486588+00', NULL, 'attack-pattern--166de1c6-2814-4fe5-8438-4e80f76b169f', '[{"url": "https://attack.mitre.org/techniques/T1596/002", "external_id": "T1596.002", "source_name": "mitre-attack"}, {"url": "https://www.whois.net/", "description": "NTT America. (n.d.). Whois Lookup. Retrieved October 20, 2020.", "source_name": "WHOIS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2642}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2201}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2598}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2567}}', 'T1596.002', 'Adversaries may search public WHOIS data for information about victims that can be used during targeting. WHOIS data is stored by regional Internet registries (RIR) responsible for allocating and assigning Internet resources such as domain names. Anyone can query WHOIS servers for information about a registered domain, such as assigned IP blocks, contact information, and DNS nameservers.(Citation: WHOIS)
INSERT INTO public.attack_patterns VALUES (534, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.515661+00', NULL, 'attack-pattern--1f9012ef-1e10-4e48-915e-e03563435fe8', '[{"url": "https://attack.mitre.org/techniques/T1600", "external_id": "T1600", "source_name": "mitre-attack"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3351}, "x_mitre_detection": {"Num": 0, "Raw": "\"There is no documented method for defenders to directly identify behaviors that weaken encryption. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601). Some detection methods require vendor support to aid in investigation.\"", "Str": "There is no documented method for defenders to directly identify behaviors that weaken encryption. Detection efforts may be focused on closely related adversary behaviors, such as [Modify System Image](https://attack.mitre.org/techniques/T1601). Some detection methods require vendor support to aid in investigation.", "Type": 3, "Index": 2853}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3303}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2727}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3204}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Encryption\"\n    ]", "Str": "", "Type": 5, "Index": 2800}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3247}}', 'T1600', 'Adversaries may compromise a network device’s encryption capability in order to bypass encryption that would otherwise protect data communications. (Citation: Cisco Synful Knock Evolution)
INSERT INTO public.attack_patterns VALUES (535, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.557405+00', NULL, 'attack-pattern--861b8fd2-57f3-4ee1-ab5d-c19c3b8c7a4a', '[{"url": "https://attack.mitre.org/techniques/T1606/001", "external_id": "T1606.001", "source_name": "mitre-attack"}, {"url": "https://wunderwuzzi23.github.io/blog/passthecookie.html", "description": "Rehberger, J. (2018, December). Pivot to the Cloud using Pass the Cookie. Retrieved April 5, 2019.", "source_name": "Pass The Cookie"}, {"url": "https://www.volexity.com/blog/2020/12/14/dark-halo-leverages-solarwinds-compromise-to-breach-organizations/", "description": "Cash, D. et al. (2020, December 14). Dark Halo Leverages SolarWinds Compromise to Breach Organizations. Retrieved December 29, 2020.", "source_name": "Volexity SolarWinds"}, {"url": "https://unit42.paloaltonetworks.com/mac-malware-steals-cryptocurrency-exchanges-cookies/", "description": "Chen, Y., Hu, W., Xu, Z., et. al. (2019, January 31). Mac Malware Steals Cryptocurrency Exchanges’ Cookies. Retrieved October 14, 2019.", "source_name": "Unit 42 Mac Crypto Cookies January 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3725}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for anomalous authentication activity, such as logons or other user session activity associated with unknown accounts. Monitor for unexpected and abnormal access to resources, including access of websites and cloud-based applications by the same user in different locations or by different systems that do not match expected configurations.\"", "Str": "Monitor for anomalous authentication activity, such as logons or other user session activity associated with unknown accounts. Monitor for unexpected and abnormal access to resources, including access of websites and cloud-based applications by the same user in different locations or by different systems that do not match expected configurations.", "Type": 3, "Index": 3219}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"SaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3633}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Web Credential: Web Credential Creation\",\n      \"Web Credential: Web Credential Usage\"\n    ]", "Str": "", "Type": 5, "Index": 3044}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3602}}', 'T1606.001', 'Adversaries may forge web cookies that can be used to gain access to web applications or Internet services. Web applications and services (hosted in cloud SaaS environments or on-premise servers) often use session cookies to authenticate and authorize user access.
INSERT INTO public.attack_patterns VALUES (537, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.62162+00', NULL, 'attack-pattern--df8b2a25-8bdf-4856-953c-a04372b1c161', '[{"url": "https://attack.mitre.org/techniques/T1071/001", "external_id": "T1071.001", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2633}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\\n\\nMonitor for web traffic to/from known-bad or suspicious domains. \"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\n\nMonitor for web traffic to/from known-bad or suspicious domains. ", "Type": 3, "Index": 1921}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2555}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1791}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2524}}', 'T1071.001', 'Adversaries may communicate using application layer protocols associated with web traffic to avoid detection/network filtering by blending in with existing traffic. Commands to the remote system, and often the results of those commands, will be embedded within the protocol traffic between the client and server. 
INSERT INTO public.attack_patterns VALUES (539, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.702885+00', NULL, 'attack-pattern--88d31120-5bc7-4ce3-a9c0-7cf147be8e54', '[{"url": "https://attack.mitre.org/techniques/T1583/006", "external_id": "T1583.006", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1970}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567).", "Type": 3, "Index": 1475}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 1926}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1895}}', 'T1583.006', 'Adversaries may register for web services that can be used during targeting. A variety of popular websites exist for adversaries to register for a web-based service that can be abused during later stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567). Using common services, such as those offered by Google or Twitter, makes it easier for adversaries to hide in expected noise. By utilizing a web service, adversaries can make it difficult to physically tie back operations to them.', 'Web Services', 10);
INSERT INTO public.attack_patterns VALUES (540, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.736469+00', NULL, 'attack-pattern--ae797531-3219-49a4-bccf-324ad7a4c7b2', '[{"url": "https://attack.mitre.org/techniques/T1584/006", "external_id": "T1584.006", "source_name": "mitre-attack"}, {"url": "https://www.recordedfuture.com/turla-apt-infrastructure/", "description": "Insikt Group. (2020, March 12). Swallowing the Snake’s Tail: Tracking Turla Infrastructure. Retrieved October 20, 2020.", "source_name": "Recorded Future Turla Infra 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2615}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567).", "Type": 3, "Index": 2120}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2571}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2540}}', 'T1584.006', 'Adversaries may compromise access to third-party web services that can be used during targeting. A variety of popular websites exist for legitimate users to register for web-based services, such as GitHub, Twitter, Dropbox, Google, etc. Adversaries may try to take ownership of a legitimate user''s access to a web service and use that web service as infrastructure in support of cyber operations. Such web services can be abused during later stages of the adversary lifecycle, such as during Command and Control ([Web Service](https://attack.mitre.org/techniques/T1102)) or [Exfiltration Over Web Service](https://attack.mitre.org/techniques/T1567).(Citation: Recorded Future Turla Infra 2020) Using common services, such as those offered by Google or Twitter, makes it easier for adversaries to hide in expected noise. By utilizing a web service, particularly when access is stolen from legitimate users, adversaries can make it difficult to physically tie back operations to them.', 'Web Services', 84);
INSERT INTO public.attack_patterns VALUES (542, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.811454+00', NULL, 'attack-pattern--5d0d3609-d06d-49e1-b9c9-b544e0c618cb', '[{"url": "https://attack.mitre.org/techniques/T1505/003", "external_id": "T1505.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/650.html", "external_id": "CAPEC-650", "source_name": "capec"}, {"url": "https://www.fireeye.com/blog/threat-research/2013/08/breaking-down-the-china-chopper-web-shell-part-i.html", "description": "Lee, T., Hanzlik, D., Ahl, I. (2013, August 7). Breaking Down the China Chopper Web Shell - Part I. Retrieved March 27, 2015.", "source_name": "Lee 2013"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA15-314A", "description": "US-CERT. (2015, November 13). Compromised Web Servers and Web Shells - Threat Awareness and Guidance. Retrieved June 8, 2016.", "source_name": "US-CERT Alert TA15-314A Web Shells"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3806}, "x_mitre_detection": {"Num": 0, "Raw": "\"Web shells can be difficult to detect. Unlike other forms of persistent remote access, they do not initiate connections. The portion of the Web shell that is on the server may be small and innocuous looking. The PHP version of the China Chopper Web shell, for example, is the following short payload: (Citation: Lee 2013) \\n\\n<code>&lt;?php @eval($_POST[''password'']);&gt;</code>\\n\\nNevertheless, detection mechanisms exist. Process monitoring may be used to detect Web servers that perform suspicious actions such as running cmd.exe or accessing files that are not in the Web directory. File monitoring may be used to detect changes to files in the Web directory of a Web server that do not match with updates to the Web server''s content and may indicate implantation of a Web shell script. Log authentication attempts to the server and any unusual traffic patterns to or from the server and internal network. (Citation: US-CERT Alert TA15-314A Web Shells) \"", "Str": "Web shells can be difficult to detect. Unlike other forms of persistent remote access, they do not initiate connections. The portion of the Web shell that is on the server may be small and innocuous looking. The PHP version of the China Chopper Web shell, for example, is the following short payload: (Citation: Lee 2013) \n\n<code>&lt;?php @eval($_POST[''password'']);&gt;</code>\n\nNevertheless, detection mechanisms exist. Process monitoring may be used to detect Web servers that perform suspicious actions such as running cmd.exe or accessing files that are not in the Web directory. File monitoring may be used to detect changes to files in the Web directory of a Web server that do not match with updates to the Web server''s content and may indicate implantation of a Web shell script. Log authentication attempts to the server and any unusual traffic patterns to or from the server and internal network. (Citation: US-CERT Alert TA15-314A Web Shells) ", "Type": 3, "Index": 2478}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3575}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2201}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3470}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Adversary access to Web server with vulnerability or account to upload and serve the Web shell file.\"\n    ]", "Str": "", "Type": 5, "Index": 3665}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3512}}', 'T1505.003', 'Adversaries may backdoor web servers with web shells to establish persistent access to systems. A Web shell is a Web script that is placed on an openly accessible Web server to allow an adversary to use the Web server as a gateway into a network. A Web shell may provide a set of functions to execute or a command-line interface on the system that hosts the Web server.
INSERT INTO public.attack_patterns VALUES (543, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.847196+00', NULL, 'attack-pattern--d1fcf083-a721-4223-aedf-bf8960798d62', '[{"url": "https://attack.mitre.org/techniques/T1059/003", "external_id": "T1059.003", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3060}, "x_mitre_detection": {"Num": 0, "Raw": "\"Usage of the Windows command shell may be common on administrator, developer, or power user systems depending on job function. If scripting is restricted for normal users, then any attempt to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\\n\\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information Discovery, Collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.\"", "Str": "Usage of the Windows command shell may be common on administrator, developer, or power user systems depending on job function. If scripting is restricted for normal users, then any attempt to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\n\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information Discovery, Collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.", "Type": 3, "Index": 1938}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3012}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1834}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2923}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2965}}', 'T1059.003', 'Adversaries may abuse the Windows command shell for execution. The Windows command shell ([cmd](https://attack.mitre.org/software/S0106)) is the primary command prompt on Windows systems. The Windows command prompt can be used to control almost any aspect of a system, with various permission levels required for different subsets of commands. 
INSERT INTO public.attack_patterns VALUES (544, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.897414+00', NULL, 'attack-pattern--d336b553-5da9-46ca-98a8-0b23f49fb447', '[{"url": "https://attack.mitre.org/techniques/T1555/004", "external_id": "T1555.004", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/it-pro/windows-server-2012-r2-and-2012/hh994565(v=ws.11)#credential-manager-store", "description": "Microsoft. (2016, August 31). Cached and Stored Credentials Technical Overview. Retrieved November 24, 2020.", "source_name": "Microsoft Credential Manager store"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/it-pro/windows-8.1-and-8/jj554668(v=ws.11)?redirectedfrom=MSDN", "description": "Microsoft. (2013, October 23). Credential Locker Overview. Retrieved November 24, 2020.", "source_name": "Microsoft Credential Locker"}, {"url": "https://www.passcape.com/windows_password_recovery_vault_explorer", "description": "Passcape. (n.d.). Windows Password Recovery - Vault Explorer and Decoder. Retrieved November 24, 2020.", "source_name": "passcape Windows Vault"}, {"url": "https://blog.malwarebytes.com/101/2016/01/the-windows-vaults/ ", "description": "Arntz, P. (2016, March 30). The Windows Vault . Retrieved November 23, 2020.", "source_name": "Malwarebytes The Windows Vault"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/api/wincred/nf-wincred-credenumeratea", "description": "Microsoft. (2018, December 5). CredEnumarateA function (wincred.h). Retrieved November 24, 2020.", "source_name": "Microsoft CredEnumerate"}, {"url": "https://github.com/gentilkiwi/mimikatz/wiki/howto-~-credential-manager-saved-credentials", "description": "Delpy, B. (2017, December 12). howto ~ credential manager saved credentials. Retrieved November 23, 2020.", "source_name": "Delpy Mimikatz Crendential Manager"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5761}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process and command-line parameters of <code>vaultcmd.exe</code> for suspicious activity, such as listing credentials from the Windows Credentials locker (i.e., <code>vaultcmd /listcreds:“Windows Credentials”</code>).(Citation: Malwarebytes The Windows Vault)\\n\\nConsider monitoring API calls such as <code>CredEnumerateA</code> that may list credentials from the Windows Credential Manager.(Citation: Microsoft CredEnumerate)(Citation: Delpy Mimikatz Crendential Manager)\\n\\nConsider monitoring file reads to Vault locations, <code>%Systemdrive%\\\\Users\\\\\\\\[Username]\\\\AppData\\\\Local\\\\Microsoft\\\\\\\\[Vault/Credentials]\\\\</code>, for suspicious activity.(Citation: Malwarebytes The Windows Vault)\"", "Str": "Monitor process and command-line parameters of <code>vaultcmd.exe</code> for suspicious activity, such as listing credentials from the Windows Credentials locker (i.e., <code>vaultcmd /listcreds:“Windows Credentials”</code>).(Citation: Malwarebytes The Windows Vault)\n\nConsider monitoring API calls such as <code>CredEnumerateA</code> that may list credentials from the Windows Credential Manager.(Citation: Microsoft CredEnumerate)(Citation: Delpy Mimikatz Crendential Manager)\n\nConsider monitoring file reads to Vault locations, <code>%Systemdrive%\\Users\\\\[Username]\\AppData\\Local\\Microsoft\\\\[Vault/Credentials]\\</code>, for suspicious activity.(Citation: Malwarebytes The Windows Vault)", "Type": 3, "Index": 4883}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5713}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bernaldo Penas Antelo\",\n      \"Mugdha Peter Bansode\"\n    ]", "Str": "", "Type": 5, "Index": 4620}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 4717}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5624}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5666}}', 'T1555.004', 'Adversaries may acquire credentials from the Windows Credential Manager. The Credential Manager stores credentials for signing into websites, applications, and/or devices that request authentication through NTLM or Kerberos in Credential Lockers (previously known as Windows Vaults).(Citation: Microsoft Credential Manager store)(Citation: Microsoft Credential Locker)
INSERT INTO public.attack_patterns VALUES (545, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.931942+00', NULL, 'attack-pattern--34e793de-0274-4982-9c1a-246ed1c19dee', '[{"url": "https://attack.mitre.org/techniques/T1222/001", "external_id": "T1222.001", "source_name": "mitre-attack"}, {"url": "https://www.hybrid-analysis.com/sample/ef0d2628823e8e0a0de3b08b8eacaf41cf284c086a948bdfd67f4e4373c14e4d?environmentId=100", "description": "Hybrid Analysis. (2018, June 12). c9b65b764985dfd7a11d3faf599c56b8.exe. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls1 June 2018"}, {"url": "https://www.hybrid-analysis.com/sample/22dab012c3e20e3d9291bce14a2bfc448036d3b966c6e78167f4626f5f9e38d6?environmentId=110", "description": "Hybrid Analysis. (2018, May 30). 2a8efbfadd798f6111340f7c1c956bee.dll. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls2 May 2018"}, {"url": "https://docs.microsoft.com/windows/desktop/secauthz/dacls-and-aces", "description": "Microsoft. (2018, May 30). DACLs and ACEs. Retrieved August 19, 2018.", "source_name": "Microsoft DACL May 2018"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/secauthz/access-control-lists", "description": "M. Satran, M. Jacobs. (2018, May 30). Access Control Lists. Retrieved February 4, 2020.", "source_name": "Microsoft Access Control Lists May 2018"}, {"url": "https://www.eventtracker.com/tech-articles/monitoring-file-permission-changes-windows-security-log/", "description": "Netsurion. (2014, February 19). Monitoring File Permission Changes with the Windows Security Log. Retrieved August 19, 2018.", "source_name": "EventTracker File Permissions Feb 2014"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5326}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and investigate attempts to modify DACLs and file/directory ownership. Many of the commands used to modify DACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\\n\\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files. For example, Windows Security Log events (Event ID 4670) are created when DACLs are modified.(Citation: EventTracker File Permissions Feb 2014)\"", "Str": "Monitor and investigate attempts to modify DACLs and file/directory ownership. Many of the commands used to modify DACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\n\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files. For example, Windows Security Log events (Event ID 4670) are created when DACLs are modified.(Citation: EventTracker File Permissions Feb 2014)", "Type": 3, "Index": 4456}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5278}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 4259}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5150}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5192}}', 'T1222.001', 'Adversaries may modify file or directory permissions/attributes to evade access control lists (ACLs) and access protected files.(Citation: Hybrid Analysis Icacls1 June 2018)(Citation: Hybrid Analysis Icacls2 May 2018) File and directory permissions are commonly managed by ACLs configured by the file or directory owner, or users with the appropriate permissions. File and directory ACL implementations vary by platform, but generally explicitly designate which users or groups can perform which actions (read, write, execute, etc.).
INSERT INTO public.attack_patterns VALUES (546, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.965097+00', NULL, 'attack-pattern--01a5a209-b94c-450b-b7f9-946497d91055', '[{"url": "https://attack.mitre.org/techniques/T1047", "external_id": "T1047", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Server_Message_Block", "description": "Wikipedia. (2016, June 12). Server Message Block. Retrieved June 12, 2016.", "source_name": "Wikipedia SMB"}, {"url": "https://technet.microsoft.com/en-us/library/cc787851.aspx", "description": "Microsoft. (2003, March 28). What Is RPC?. Retrieved June 12, 2016.", "source_name": "TechNet RPC"}, {"url": "https://msdn.microsoft.com/en-us/library/aa394582.aspx", "description": "Microsoft. (n.d.). Windows Management Instrumentation. Retrieved April 27, 2016.", "source_name": "MSDN WMI"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/sans-dfir-2015.pdf", "description": "Devon Kerr. (2015). There''s Something About WMI. Retrieved May 4, 2020.", "source_name": "FireEye WMI SANS 2015"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/wp-windows-management-instrumentation.pdf", "description": "Ballenthin, W., et al. (2015). Windows Management Instrumentation (WMI) Offense, Defense, and Forensics. Retrieved March 30, 2016.", "source_name": "FireEye WMI 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3758}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor network traffic for WMI connections; the use of WMI in environments that do not typically use WMI may be suspect. Perform process monitoring to capture command-line arguments of \\\"wmic\\\" and detect commands that are used to perform remote behavior. (Citation: FireEye WMI 2015)\"", "Str": "Monitor network traffic for WMI connections; the use of WMI in environments that do not typically use WMI may be suspect. Perform process monitoring to capture command-line arguments of \"wmic\" and detect commands that are used to perform remote behavior. (Citation: FireEye WMI 2015)", "Type": 3, "Index": 3058}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3491}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2900}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3546}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3378}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"WMI service, winmgmt, running.\\nHost/network firewalls allowing SMB and WMI ports from source to destination.\\nSMB authentication.\"\n    ]", "Str": "", "Type": 5, "Index": 3587}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3421}}', 'T1047', 'Adversaries may abuse Windows Management Instrumentation (WMI) to achieve execution. WMI is a Windows administration feature that provides a uniform environment for local and remote access to Windows system components. It relies on the WMI service for local and remote access and the server message block (SMB) (Citation: Wikipedia SMB) and Remote Procedure Call Service (RPCS) (Citation: TechNet RPC) for remote access. RPCS operates over port 135. (Citation: MSDN WMI)
INSERT INTO public.attack_patterns VALUES (550, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:47.061792+00', NULL, 'attack-pattern--6836813e-8ec8-4375-b459-abb388cb1a35', '[{"url": "https://attack.mitre.org/techniques/T1547/004", "external_id": "T1547.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/579.html", "external_id": "CAPEC-579", "source_name": "capec"}, {"url": "https://blog.cylance.com/windows-registry-persistence-part-2-the-run-keys-and-search-order", "description": "Langendorf, S. (2013, September 24). Windows Registry Persistence, Part 2: The Run Keys and Search-Order. Retrieved April 11, 2018.", "source_name": "Cylance Reg Persistence Sept 2013"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4157}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for changes to Registry entries associated with Winlogon that do not correlate with known software, patch cycles, etc. Tools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current Winlogon helper values. (Citation: TechNet Autoruns)  New DLLs written to System32 that do not correlate with known good software or patching may also be suspicious.\\n\\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor for changes to Registry entries associated with Winlogon that do not correlate with known software, patch cycles, etc. Tools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current Winlogon helper values. (Citation: TechNet Autoruns)  New DLLs written to System32 that do not correlate with known good software or patching may also be suspicious.\n\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 3172}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4109}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2957}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3013}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3995}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4037}}', 'T1547.004', 'Adversaries may abuse features of Winlogon to execute DLLs and/or executables when a user logs in. Winlogon.exe is a Windows component responsible for actions at logon/logoff as well as the secure attention sequence (SAS) triggered by Ctrl-Alt-Delete. Registry entries in <code>HKLM\Software[\\Wow6432Node\\]\Microsoft\Windows NT\CurrentVersion\Winlogon\</code> and <code>HKCU\Software\Microsoft\Windows NT\CurrentVersion\Winlogon\</code> are used to manage additional helper programs and functionalities that support Winlogon. (Citation: Cylance Reg Persistence Sept 2013) 
INSERT INTO public.attack_patterns VALUES (551, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:47.093381+00', NULL, 'attack-pattern--e0232cb0-ded5-4c2e-9dc7-2893142a5c11', '[{"url": "https://attack.mitre.org/techniques/T1547/013", "external_id": "T1547.013", "source_name": "mitre-attack"}, {"url": "https://specifications.freedesktop.org/autostart-spec/autostart-spec-latest.html", "description": "Free Desktop. (2006, February 13). Desktop Application Autostart Specification. Retrieved September 12, 2019.", "source_name": "Free Desktop Application Autostart Feb 2006"}, {"url": "https://specifications.freedesktop.org/desktop-entry-spec/1.2/ar01s06.html", "description": "Free Desktop. (2017, December 24). Recognized Desktop Entry Keys. Retrieved September 12, 2019.", "source_name": "Free Desktop Entry Keys"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4138}, "x_mitre_detection": {"Num": 0, "Raw": "\"Malicious XDG autostart entries may be detected by auditing file creation and modification events within the <code>/etc/xdg/autostart</code> and <code>~/.config/autostart</code> directories. Depending on individual configurations, defenders may need to query the environment variables <code>$XDG_CONFIG_HOME</code> or <code>$XDG_CONFIG_DIRS</code> to determine the paths of Autostart entries. Autostart entry files not associated with legitimate packages may be considered suspicious. Suspicious entries can also be identified by comparing entries to a trusted system baseline.\\n \\nSuspicious processes or scripts spawned in this manner will have a parent process of the desktop component implementing the XDG specification and will execute as the logged on user.\"", "Str": "Malicious XDG autostart entries may be detected by auditing file creation and modification events within the <code>/etc/xdg/autostart</code> and <code>~/.config/autostart</code> directories. Depending on individual configurations, defenders may need to query the environment variables <code>$XDG_CONFIG_HOME</code> or <code>$XDG_CONFIG_DIRS</code> to determine the paths of Autostart entries. Autostart entry files not associated with legitimate packages may be considered suspicious. Suspicious entries can also be identified by comparing entries to a trusted system baseline.\n \nSuspicious processes or scripts spawned in this manner will have a parent process of the desktop component implementing the XDG specification and will execute as the logged on user.", "Type": 3, "Index": 3191}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4092}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tony Lambert, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 2955}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3025}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3989}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4031}}', 'T1547.013', 'Adversaries may modify XDG autostart entries to execute programs or commands during system boot. Linux desktop environments that are XDG compliant implement functionality for XDG autostart entries. These entries will allow an application to automatically start during the startup of a desktop environment after user logon. By default, XDG autostart entries are stored within the <code>/etc/xdg/autostart</code> or <code>~/.config/autostart</code> directories and have a .desktop file extension.(Citation: Free Desktop Application Autostart Feb 2006)
INSERT INTO public.attack_patterns VALUES (552, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:47.122411+00', NULL, 'attack-pattern--ebbe170d-aa74-4946-8511-9921243415a3', '[{"url": "https://attack.mitre.org/techniques/T1220", "external_id": "T1220", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/dotnet/standard/data/xml/xslt-stylesheet-scripting-using-msxsl-script", "description": "Wenzel, M. et al. (2017, March 30). XSLT Stylesheet Scripting Using <msxsl:script>. Retrieved July 3, 2018.", "source_name": "Microsoft XSLT Script Mar 2017"}, {"url": "https://www.microsoft.com/download/details.aspx?id=21714", "description": "Microsoft. (n.d.). Command Line Transformation Utility (msxsl.exe). Retrieved July 3, 2018.", "source_name": "Microsoft msxsl.exe"}, {"url": "https://pentestlab.blog/2017/07/06/applocker-bypass-msxsl/", "description": "netbiosX. (2017, July 6). AppLocker Bypass – MSXSL. Retrieved July 3, 2018.", "source_name": "Penetration Testing Lab MSXSL July 2017"}, {"url": "https://reaqta.com/2018/03/spear-phishing-campaign-leveraging-msxsl/", "description": "Admin. (2018, March 2). Spear-phishing campaign leveraging on MSXSL. Retrieved July 3, 2018.", "source_name": "Reaqta MSXSL Spearphishing MAR 2018"}, {"url": "https://medium.com/@threathuntingteam/msxsl-exe-and-wmic-exe-a-way-to-proxy-code-execution-8d524f642b75", "description": "Singh, A. (2019, March 14). MSXSL.EXE and WMIC.EXE — A Way to Proxy Code Execution. Retrieved August 2, 2019.", "source_name": "XSL Bypass Mar 2019"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Wmic/", "description": "LOLBAS. (n.d.). Wmic.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Wmic"}, {"url": "https://twitter.com/dez_/status/986614411711442944", "description": "Desimone, J. (2018, April 18). Status Update. Retrieved July 3, 2018.", "source_name": "Twitter SquiblyTwo Detection APR 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 6608}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of msxsl.exe and wmic.exe. Compare recent invocations of these utilities with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity (ex: URL command line arguments, creation of external network connections, loading of DLLs associated with scripting). (Citation: LOLBAS Wmic) (Citation: Twitter SquiblyTwo Detection APR 2018) Command arguments used before and after the script invocation may also be useful in determining the origin and purpose of the payload being loaded.\\n\\nThe presence of msxsl.exe or other utilities that enable proxy execution that are typically used for development, debugging, and reverse engineering on a system that is not used for these purposes may be suspicious.\"", "Str": "Use process monitoring to monitor the execution and arguments of msxsl.exe and wmic.exe. Compare recent invocations of these utilities with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity (ex: URL command line arguments, creation of external network connections, loading of DLLs associated with scripting). (Citation: LOLBAS Wmic) (Citation: Twitter SquiblyTwo Detection APR 2018) Command arguments used before and after the script invocation may also be useful in determining the origin and purpose of the payload being loaded.\n\nThe presence of msxsl.exe or other utilities that enable proxy execution that are typically used for development, debugging, and reverse engineering on a system that is not used for these purposes may be suspicious.", "Type": 3, "Index": 5511}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6450}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Avneet Singh\",\n      \"Casey Smith\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 5186}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 5285}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6360}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\",\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 5389}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Microsoft Core XML Services (MSXML) or access to wmic.exe\"\n    ]", "Str": "", "Type": 5, "Index": 6510}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6403}}', 'T1220', 'Adversaries may bypass application control and obscure execution of code by embedding scripts inside XSL files. Extensible Stylesheet Language (XSL) files are commonly used to describe the processing and rendering of data within XML files. To support complex operations, the XSL standard includes support for embedded scripting in various languages. (Citation: Microsoft XSLT Script Mar 2017)
INSERT INTO public.attack_patterns VALUES (1, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:25.233293+00', NULL, 'attack-pattern--d0b4fcdb-d67d-4ed2-99ce-788b12f8c0f4', '[{"url": "https://attack.mitre.org/techniques/T1003/008", "external_id": "T1003.008", "source_name": "mitre-attack"}, {"url": "https://www.tldp.org/LDP/lame/LAME/linux-admin-made-easy/shadow-file-formats.html", "description": "The Linux Documentation Project. (n.d.). Linux Password and Shadow File Formats. Retrieved February 19, 2020.", "source_name": "Linux Password and Shadow File Formats"}, {"url": "https://www.cyberciti.biz/faq/unix-linux-password-cracking-john-the-ripper/", "description": "Vivek Gite. (2014, September 17). Linux Password Cracking: Explain unshadow and john Commands (John the Ripper Tool). Retrieved February 19, 2020.", "source_name": "nixCraft - John the Ripper"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2760}, "x_mitre_detection": {"Num": 0, "Raw": "\"The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes attempting to access <code>/etc/passwd</code> and <code>/etc/shadow</code>, alerting on the pid, process name, and arguments of such programs.\"", "Str": "The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes attempting to access <code>/etc/passwd</code> and <code>/etc/shadow</code>, alerting on the pid, process name, and arguments of such programs.", "Type": 3, "Index": 2330}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2714}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 2234}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2625}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2667}}', 'T1003.008', 'Adversaries may attempt to dump the contents of <code>/etc/passwd</code> and <code>/etc/shadow</code> to enable offline password cracking. Most modern Linux operating systems use a combination of <code>/etc/passwd</code> and <code>/etc/shadow</code> to store user account information including password hashes in <code>/etc/shadow</code>. By default, <code>/etc/shadow</code> is only readable by the root user.(Citation: Linux Password and Shadow File Formats)
INSERT INTO public.attack_patterns VALUES (7, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.000204+00', NULL, 'attack-pattern--b24e2a20-3b3d-4bf0-823b-1ed765398fb0', '[{"url": "https://attack.mitre.org/techniques/T1531", "external_id": "T1531", "source_name": "mitre-attack"}, {"url": "https://www.carbonblack.com/2019/03/22/tau-threat-intelligence-notification-lockergoga-ransomware/", "description": "CarbonBlack Threat Analysis Unit. (2019, March 22). TAU Threat Intelligence Notification – LockerGoga Ransomware. Retrieved April 16, 2019.", "source_name": "CarbonBlack LockerGoga 2019"}, {"url": "https://unit42.paloaltonetworks.com/born-this-way-origins-of-lockergoga/", "description": "Harbison, M.. (2019, March 26). Born This Way? Origins of LockerGoga. Retrieved April 16, 2019.", "source_name": "Unit42 LockerGoga 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3231}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and command line parameters of binaries involved in deleting accounts or changing passwords, such as use of [Net](https://attack.mitre.org/software/S0039). Windows event logs may also designate activity associated with an adversary''s attempt to remove access to an account:\\n\\n* Event ID 4723 - An attempt was made to change an account''s password\\n* Event ID 4724 - An attempt was made to reset an account''s password\\n* Event ID 4726 - A user account was deleted\\n* Event ID 4740 - A user account was locked out\\n\\nAlerting on [Net](https://attack.mitre.org/software/S0039) and these Event IDs may generate a high degree of false positives, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\"", "Str": "Use process monitoring to monitor the execution and command line parameters of binaries involved in deleting accounts or changing passwords, such as use of [Net](https://attack.mitre.org/software/S0039). Windows event logs may also designate activity associated with an adversary''s attempt to remove access to an account:\n\n* Event ID 4723 - An attempt was made to change an account''s password\n* Event ID 4724 - An attempt was made to reset an account''s password\n* Event ID 4726 - A user account was deleted\n* Event ID 4740 - A user account was locked out\n\nAlerting on [Net](https://attack.mitre.org/software/S0039) and these Event IDs may generate a high degree of false positives, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.", "Type": 3, "Index": 2062}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3153}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 2949}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Deletion\",\n      \"User Account: User Account Modification\",\n      \"Active Directory: Active Directory Object Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1871}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3010}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3053}}', 'T1531', 'Adversaries may interrupt availability of system and network resources by inhibiting access to accounts utilized by legitimate users. Accounts may be deleted, locked, or manipulated (ex: changed credentials) to remove access to accounts.
INSERT INTO public.attack_patterns VALUES (9, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.096443+00', NULL, 'attack-pattern--a10641f4-87b4-45a3-a906-92a149cb2c27', '[{"url": "https://attack.mitre.org/techniques/T1098", "external_id": "T1098", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/event-4738", "description": "Lich, B., Miroshnikov, A. (2017, April 5). 4738(S): A user account was changed. Retrieved June 30, 2017.", "source_name": "Microsoft User Modified Event"}, {"url": "https://www.ultimatewindowssecurity.com/securitylog/encyclopedia/event.aspx?eventID=4670", "description": "Franklin Smith, R. (n.d.). Windows Security Log Event ID 4670. Retrieved November 4, 2019.", "source_name": "Microsoft Security Event 4670"}, {"url": "https://blog.stealthbits.com/manipulating-user-passwords-with-mimikatz-SetNTLM-ChangeNTLM", "description": "Warren, J. (2017, July 11). Manipulating User Passwords with Mimikatz. Retrieved December 4, 2017.", "source_name": "InsiderThreat ChangeNTLM July 2017"}, {"url": "https://github.com/gentilkiwi/mimikatz/issues/92", "description": "Warren, J. (2017, June 22). lsadump::changentlm and lsadump::setntlm work, but generate Windows events #92. Retrieved December 4, 2017.", "source_name": "GitHub Mimikatz Issue 92 June 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 4230}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect events that correlate with changes to account objects and/or permissions on systems and the domain, such as event IDs 4738, 4728 and 4670.(Citation: Microsoft User Modified Event)(Citation: Microsoft Security Event 4670)(Citation: Microsoft Security Event 4670) Monitor for modification of accounts in correlation with other suspicious activity. Changes may occur at unusual times or from unusual systems. Especially flag events where the subject and target accounts differ(Citation: InsiderThreat ChangeNTLM July 2017) or that include additional flags such as changing a password without knowledge of the old password.(Citation: GitHub Mimikatz Issue 92 June 2017)\\n\\nMonitor for use of credentials at unusual times or to unusual systems or services. This may also correlate with other suspicious activity.\\n\\nMonitor for unusual permissions changes that may indicate excessively broad permissions being granted to compromised accounts.\"", "Str": "Collect events that correlate with changes to account objects and/or permissions on systems and the domain, such as event IDs 4738, 4728 and 4670.(Citation: Microsoft User Modified Event)(Citation: Microsoft Security Event 4670)(Citation: Microsoft Security Event 4670) Monitor for modification of accounts in correlation with other suspicious activity. Changes may occur at unusual times or from unusual systems. Especially flag events where the subject and target accounts differ(Citation: InsiderThreat ChangeNTLM July 2017) or that include additional flags such as changing a password without knowledge of the old password.(Citation: GitHub Mimikatz Issue 92 June 2017)\n\nMonitor for use of credentials at unusual times or to unusual systems or services. This may also correlate with other suspicious activity.\n\nMonitor for unusual permissions changes that may indicate excessively broad permissions being granted to compromised accounts.", "Type": 3, "Index": 3062}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4074}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jannie Li, Microsoft Threat Intelligence Center (MSTIC)\",\n      \"Praetorian\",\n      \"Tim MalcomVetter\"\n    ]", "Str": "", "Type": 5, "Index": 2626}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Group: Group Modification\",\n      \"User Account: User Account Modification\",\n      \"Active Directory: Active Directory Object Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2777}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4042}}', 'T1098', 'Adversaries may manipulate accounts to maintain access to victim systems. Account manipulation may consist of any action that preserves adversary access to a compromised account, such as modifying credentials or permission groups. These actions could also include account activity designed to subvert security policies, such as performing iterative password updates to bypass password duration policies and preserve the life of compromised credentials. In order to create or manipulate accounts, the adversary must already have sufficient permissions on systems or the domain.', 'Account Manipulation', 0);
INSERT INTO public.attack_patterns VALUES (13, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.588866+00', NULL, 'attack-pattern--2dbbdcd5-92cf-44c0-aea2-fe24783a6bc3', '[{"url": "https://attack.mitre.org/techniques/T1098/003", "external_id": "T1098.003", "source_name": "mitre-attack"}, {"url": "https://support.office.com/en-us/article/add-another-admin-f693489f-9f55-4bd0-a637-a81ce93de22d", "description": "Microsoft. (n.d.). Add Another Admin. Retrieved October 18, 2019.", "source_name": "Microsoft Support O365 Add Another Admin, October 2019"}, {"url": "https://docs.microsoft.com/en-us/office365/admin/add-users/about-admin-roles?view=o365-worldwide", "description": "Ako-Adjei, K., Dickhaus, M., Baumgartner, P., Faigel, D., et. al.. (2019, October 8). About admin roles. Retrieved October 18, 2019.", "source_name": "Microsoft O365 Admin Roles"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2713}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect usage logs from cloud administrator accounts to identify unusual activity in the assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins. \"", "Str": "Collect usage logs from cloud administrator accounts to identify unusual activity in the assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins. ", "Type": 3, "Index": 2306}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 2662}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 2134}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2224}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2564}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2606}}', 'T1098.003', 'An adversary may add the Global Administrator role to an adversary-controlled account to maintain persistent access to an Office 365 tenant.(Citation: Microsoft Support O365 Add Another Admin, October 2019)(Citation: Microsoft O365 Admin Roles) With sufficient permissions, a compromised account can gain almost unlimited access to data and settings (including the ability to reset the passwords of other admins) via the global admin role.(Citation: Microsoft O365 Admin Roles) 
INSERT INTO public.attack_patterns VALUES (14, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.631883+00', NULL, 'attack-pattern--34f1d81d-fe88-4f97-bd3b-a3164536255d', '[{"url": "https://attack.mitre.org/techniques/T1137/006", "external_id": "T1137.006", "source_name": "mitre-attack"}, {"url": "https://support.office.com/article/Add-or-remove-add-ins-0af570c4-5cf3-4fa9-9b88-403625a0b460", "description": "Microsoft. (n.d.). Add or remove add-ins. Retrieved July 3, 2017.", "source_name": "Microsoft Office Add-ins"}, {"url": "https://labs.mwrinfosecurity.com/blog/add-in-opportunities-for-office-persistence/", "description": "Knowles, W. (2017, April 21). Add-In Opportunities for Office Persistence. Retrieved July 3, 2017.", "source_name": "MRWLabs Office Persistence Add-ins"}, {"url": "https://summit.fireeye.com/content/dam/fireeye-www/summit/cds-2018/presentations/cds18-technical-s03-youve-got-mail.pdf", "description": "Caban, D. and Hirani, M. (2018, October 3). You’ve Got Mail! Enterprise Email Compromise. Retrieved April 22, 2019.", "source_name": "FireEye Mail CDS 2018"}, {"url": "https://www.221bluestreet.com/post/office-templates-and-globaldotname-a-stealthy-office-persistence-technique", "description": "Shukrun, S. (2019, June 2). Office Templates and GlobalDotName - A Stealthy Office Persistence Technique. Retrieved August 26, 2019.", "source_name": "GlobalDotName Jun 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3768}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and validate the Office trusted locations on the file system and audit the Registry entries relevant for enabling add-ins.(Citation: GlobalDotName Jun 2019)(Citation: MRWLabs Office Persistence Add-ins)\\n\\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior\"", "Str": "Monitor and validate the Office trusted locations on the file system and audit the Registry entries relevant for enabling add-ins.(Citation: GlobalDotName Jun 2019)(Citation: MRWLabs Office Persistence Add-ins)\n\nCollect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior", "Type": 3, "Index": 3082}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 3700}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2798}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3588}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3630}}', 'T1137.006', 'Adversaries may abuse Microsoft Office add-ins to obtain persistence on a compromised system. Office add-ins can be used to add functionality to Office programs. (Citation: Microsoft Office Add-ins) There are different types of add-ins that can be used by the various Office products; including Word/Excel add-in Libraries (WLL/XLL), VBA add-ins, Office Component Object Model (COM) add-ins, automation add-ins, VBA Editor (VBE), Visual Studio Tools for Office (VSTO) add-ins, and Outlook add-ins. (Citation: MRWLabs Office Persistence Add-ins)(Citation: FireEye Mail CDS 2018)
INSERT INTO public.attack_patterns VALUES (16, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.738615+00', NULL, 'attack-pattern--7d57b371-10c2-45e5-b3cc-83a8fb380e4c', '[{"url": "https://attack.mitre.org/techniques/T1546/009", "external_id": "T1546.009", "source_name": "mitre-attack"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}, {"url": "https://forum.sysinternals.com/appcertdlls_topic12546.html", "description": "Microsoft. (2007, October 24). Windows Sysinternals - AppCertDlls. Retrieved December 18, 2017.", "source_name": "Sysinternals AppCertDlls Oct 2007"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4326}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Monitor the AppCertDLLs Registry value for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as RegCreateKeyEx and RegSetValueEx. (Citation: Elastic Process Injection July 2017) \\n\\nTools such as Sysinternals Autoruns may overlook AppCert DLLs as an auto-starting location. (Citation: TechNet Autoruns) (Citation: Sysinternals AppCertDlls Oct 2007)\\n\\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement.\"", "Str": "Monitor DLL loads by processes, specifically looking for DLLs that are not recognized or not normally loaded into a process. Monitor the AppCertDLLs Registry value for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as RegCreateKeyEx and RegSetValueEx. (Citation: Elastic Process Injection July 2017) \n\nTools such as Sysinternals Autoruns may overlook AppCert DLLs as an auto-starting location. (Citation: TechNet Autoruns) (Citation: Sysinternals AppCertDlls Oct 2007)\n\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement.", "Type": 3, "Index": 3071}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4278}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2877}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4164}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4206}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4086}}', 'T1546.009', 'Adversaries may establish persistence and/or elevate privileges by executing malicious content triggered by AppCert DLLs loaded into processes. Dynamic-link libraries (DLLs) that are specified in the <code>AppCertDLLs</code> Registry key under <code>HKEY_LOCAL_MACHINE\System\CurrentControlSet\Control\Session Manager\</code> are loaded into every process that calls the ubiquitously used application programming interface (API) functions <code>CreateProcess</code>, <code>CreateProcessAsUser</code>, <code>CreateProcessWithLoginW</code>, <code>CreateProcessWithTokenW</code>, or <code>WinExec</code>. (Citation: Elastic Process Injection July 2017)
INSERT INTO public.attack_patterns VALUES (17, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.80711+00', NULL, 'attack-pattern--cc89ecbd-3d33-4a41-bcca-001e702d18fd', '[{"url": "https://attack.mitre.org/techniques/T1546/010", "external_id": "T1546.010", "source_name": "mitre-attack"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "https://support.microsoft.com/en-us/kb/197571", "description": "Microsoft. (2006, October). Working with the AppInit_DLLs registry value. Retrieved July 15, 2015.", "source_name": "AppInit Registry"}, {"url": "https://msdn.microsoft.com/en-us/library/dn280412", "description": "Microsoft. (n.d.). AppInit DLLs and Secure Boot. Retrieved July 15, 2015.", "source_name": "AppInit Secure Boot"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4798}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor DLL loads by processes that load user32.dll and look for DLLs that are not recognized or not normally loaded into a process. Monitor the AppInit_DLLs Registry values for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code>. (Citation: Elastic Process Injection July 2017)\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current AppInit DLLs. (Citation: TechNet Autoruns) \\n\\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement.\"", "Str": "Monitor DLL loads by processes that load user32.dll and look for DLLs that are not recognized or not normally loaded into a process. Monitor the AppInit_DLLs Registry values for modifications that do not correlate with known software, patch cycles, etc. Monitor and analyze application programming interface (API) calls that are indicative of Registry edits such as <code>RegCreateKeyEx</code> and <code>RegSetValueEx</code>. (Citation: Elastic Process Injection July 2017)\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current AppInit DLLs. (Citation: TechNet Autoruns) \n\nLook for abnormal process behavior that may be due to a process loading a malicious DLL. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as making network connections for Command and Control, learning details about the environment through Discovery, and conducting Lateral Movement.", "Type": 3, "Index": 3392}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4638}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 3198}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4540}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Secure boot disabled on systems running Windows 8 and later\"\n    ]", "Str": "", "Type": 5, "Index": 4698}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4582}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4462}}', 'T1546.010', 'Adversaries may establish persistence and/or elevate privileges by executing malicious content triggered by AppInit DLLs loaded into processes. Dynamic-link libraries (DLLs) that are specified in the <code>AppInit_DLLs</code> value in the Registry keys <code>HKEY_LOCAL_MACHINE\Software\Microsoft\Windows NT\CurrentVersion\Windows</code> or <code>HKEY_LOCAL_MACHINE\Software\Wow6432Node\Microsoft\Windows NT\CurrentVersion\Windows</code> are loaded by user32.dll into every process that loads user32.dll. In practice this is nearly every program, since user32.dll is a very common library. (Citation: Elastic Process Injection July 2017)
INSERT INTO public.attack_patterns VALUES (19, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:26.897325+00', NULL, 'attack-pattern--f005e783-57d4-4837-88ad-dbe7faee1c51', '[{"url": "https://attack.mitre.org/techniques/T1550/001", "external_id": "T1550.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/593.html", "external_id": "CAPEC-593", "source_name": "capec"}, {"url": "https://auth0.com/blog/why-should-use-accesstokens-to-secure-an-api/", "description": "Auth0. (n.d.). Why You Should Always Use Access Tokens to Secure APIs. Retrieved September 12, 2019.", "source_name": "Auth0 - Why You Should Always Use Access Tokens to Secure APIs Sept 2019"}, {"url": "https://developer.okta.com/blog/2018/06/20/what-happens-if-your-jwt-is-stolen", "description": "okta. (n.d.). What Happens If Your JWT Is Stolen?. Retrieved September 12, 2019.", "source_name": "okta"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/access-tokens", "description": "Cai, S., Flores, J., de Guzman, C., et. al.. (2019, August 27). Microsoft identity platform access tokens. Retrieved October 4, 2019.", "source_name": "Microsoft Identity Platform Access 2019"}, {"url": "https://staaldraad.github.io/2017/08/02/o356-phishing-with-oauth/", "description": "Stalmans, E.. (2017, August 2). Phishing with OAuth and o365/Azure. Retrieved October 4, 2019.", "source_name": "Staaldraad Phishing with OAuth 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 5184}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor access token activity for abnormal use and permissions granted to unusual or suspicious applications and APIs.\"", "Str": "Monitor access token activity for abnormal use and permissions granted to unusual or suspicious applications and APIs.", "Type": 3, "Index": 4909}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Office 365\",\n      \"SaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 5093}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Shailesh Tiwary (Indian Army)\",\n      \"Saisha Agrawal, Microsoft Threat Intelligent Center (MSTIC)\",\n      \"Jeff Sakowicz, Microsoft Identity Developer Platform Services (IDPM Services)\",\n      \"Mark Wee\"\n    ]", "Str": "", "Type": 5, "Index": 4459}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Web Credential: Web Credential Usage\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 4708}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5062}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"System Access Controls\"\n    ]", "Str": "", "Type": 5, "Index": 4844}}', 'T1550.001', 'Adversaries may use stolen application access tokens to bypass the typical authentication process and access restricted accounts, information, or services on remote systems. These tokens are typically stolen from users and used in lieu of login credentials.
INSERT INTO public.attack_patterns VALUES (22, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.049714+00', NULL, 'attack-pattern--42fe883a-21ea-4cfb-b94a-78b6476dcc83', '[{"url": "https://attack.mitre.org/techniques/T1546/011", "external_id": "T1546.011", "source_name": "mitre-attack"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}, {"url": "http://files.brucon.org/2015/Tomczak_and_Ballenthin_Shims_for_the_Win.pdf", "description": "Ballenthin, W., Tomczak, J.. (2015). The Real Shim Shary. Retrieved May 4, 2020.", "source_name": "FireEye Application Shimming"}, {"url": "https://www.blackhat.com/docs/eu-15/materials/eu-15-Pierce-Defending-Against-Malicious-Application-Compatibility-Shims-wp.pdf", "description": "Pierce, Sean. (2015, November). Defending Against Malicious Application Compatibility Shims. Retrieved June 22, 2017.", "source_name": "Black Hat 2015 App Shim"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5284}, "x_mitre_detection": {"Num": 0, "Raw": "\"There are several public tools available that will detect shims that are currently available (Citation: Black Hat 2015 App Shim):\\n\\n* Shim-Process-Scanner - checks memory of every running process for any shim flags\\n* Shim-Detector-Lite - detects installation of custom shim databases\\n* Shim-Guard - monitors registry for any shim installations\\n* ShimScanner - forensic tool to find active shims in memory\\n* ShimCacheMem - Volatility plug-in that pulls shim cache from memory (note: shims are only cached after reboot)\\n\\nMonitor process execution for sdbinst.exe and command-line arguments for potential indications of application shim abuse.\"", "Str": "There are several public tools available that will detect shims that are currently available (Citation: Black Hat 2015 App Shim):\n\n* Shim-Process-Scanner - checks memory of every running process for any shim flags\n* Shim-Detector-Lite - detects installation of custom shim databases\n* Shim-Guard - monitors registry for any shim installations\n* ShimScanner - forensic tool to find active shims in memory\n* ShimCacheMem - Volatility plug-in that pulls shim cache from memory (note: shims are only cached after reboot)\n\nMonitor process execution for sdbinst.exe and command-line arguments for potential indications of application shim abuse.", "Type": 3, "Index": 4456}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5236}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4229}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5138}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5180}}', 'T1546.011', 'Adversaries may establish persistence and/or elevate privileges by executing malicious content triggered by application shims. The Microsoft Windows Application Compatibility Infrastructure/Framework (Application Shim) was created to allow for backward compatibility of software as the operating system codebase changes over time. For example, the application shimming feature allows developers to apply fixes to applications (without rewriting code) that were created for Windows XP so that it will work with Windows 10. (Citation: Elastic Process Injection July 2017)
INSERT INTO public.attack_patterns VALUES (26, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.201618+00', NULL, 'attack-pattern--143c0cbb-a297-4142-9624-87ffc778980b', '[{"url": "https://attack.mitre.org/techniques/T1560/003", "external_id": "T1560.003", "source_name": "mitre-attack"}, {"url": "http://www.welivesecurity.com/wp-content/uploads/2016/10/eset-sednit-part-2.pdf", "description": "ESET. (2016, October). En Route with Sednit - Part 2: Observing the Comings and Goings. Retrieved November 21, 2016.", "source_name": "ESET Sednit Part 2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1910}, "x_mitre_detection": {"Num": 0, "Raw": "\"Custom archival methods can be very difficult to detect, since many of them use standard programming language concepts, such as bitwise operations.\"", "Str": "Custom archival methods can be very difficult to detect, since many of them use standard programming language concepts, such as bitwise operations.", "Type": 3, "Index": 1619}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1832}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1523}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1801}}', 'T1560.003', 'An adversary may compress or encrypt data that is collected prior to exfiltration using a custom method. Adversaries may choose to use custom archival methods, such as encryption with XOR or stream ciphers implemented with no external library or utility references. Custom implementations of well-known compression algorithms have also been used.(Citation: ESET Sednit Part 2)', 'Archive via Custom Method', 25);
INSERT INTO public.attack_patterns VALUES (27, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.260073+00', NULL, 'attack-pattern--41868330-6ee2-4d0f-b743-9f2294c3c9b6', '[{"url": "https://attack.mitre.org/techniques/T1560/002", "external_id": "T1560.002", "source_name": "mitre-attack"}, {"url": "https://pypi.org/project/rarfile/", "description": "mkz. (2020). rarfile 3.1. Retrieved February 20, 2020.", "source_name": "PyPI RAR"}, {"url": "https://libzip.org/", "description": "D. Baron, T. Klausner. (2020). libzip. Retrieved February 20, 2020.", "source_name": "libzip"}, {"url": "https://github.com/madler/zlib", "description": "madler. (2017). zlib. Retrieved February 20, 2020.", "source_name": "Zlib Github"}, {"url": "https://en.wikipedia.org/wiki/List_of_file_signatures", "description": "Wikipedia. (2016, March 31). List of file signatures. Retrieved April 22, 2016.", "source_name": "Wikipedia File Header Signatures"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3119}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for accesses to known archival libraries. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\\n\\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)\"", "Str": "Monitor processes for accesses to known archival libraries. This may yield a significant number of benign events, depending on how systems in the environment are typically used.\n\nConsider detecting writing of files with extensions and/or headers associated with compressed or encrypted file types. Detection efforts may focus on follow-on exfiltration activity, where compressed or encrypted files can be detected in transit with a network intrusion detection or data loss prevention system analyzing file headers.(Citation: Wikipedia File Header Signatures)", "Type": 3, "Index": 2415}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3041}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2319}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3010}}', 'T1560.002', 'An adversary may compress or encrypt data that is collected prior to exfiltration using 3rd party libraries. Many libraries exist that can archive data, including [Python](https://attack.mitre.org/techniques/T1059/006) rarfile (Citation: PyPI RAR), libzip (Citation: libzip), and zlib (Citation: Zlib Github). Most libraries include functionality to encrypt and/or compress data.
INSERT INTO public.attack_patterns VALUES (29, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.321926+00', NULL, 'attack-pattern--bf176076-b789-408e-8cba-7275e81c0ada', '[{"url": "https://attack.mitre.org/techniques/T1573/002", "external_id": "T1573.002", "source_name": "mitre-attack"}, {"url": "http://www.sans.org/reading-room/whitepapers/analyst/finding-hidden-threats-decrypting-ssl-34840", "description": "Butler, M. (2013, November). Finding Hidden Threats by Decrypting SSL. Retrieved April 5, 2016.", "source_name": "SANS Decrypting SSL"}, {"url": "https://insights.sei.cmu.edu/cert/2015/03/the-risks-of-ssl-inspection.html", "description": "Dormann, W. (2015, March 13). The Risks of SSL Inspection. Retrieved April 5, 2016.", "source_name": "SEI SSL Inspection Risks"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3673}, "x_mitre_detection": {"Num": 0, "Raw": "\"SSL/TLS inspection is one way of detecting command and control traffic within some encrypted communication channels.(Citation: SANS Decrypting SSL) SSL/TLS inspection does come with certain risks that should be considered before implementing to avoid potential security issues such as incomplete certificate validation.(Citation: SEI SSL Inspection Risks)\\n\\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "SSL/TLS inspection is one way of detecting command and control traffic within some encrypted communication channels.(Citation: SANS Decrypting SSL) SSL/TLS inspection does come with certain risks that should be considered before implementing to avoid potential security issues such as incomplete certificate validation.(Citation: SEI SSL Inspection Risks)\n\nIn general, analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2735}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3595}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2652}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3564}}', 'T1573.002', 'Adversaries may employ a known asymmetric encryption algorithm to conceal command and control traffic rather than relying on any inherent protections provided by a communication protocol. Asymmetric cryptography, also known as public key cryptography, uses a keypair per party: one public that can be freely distributed, and one private. Due to how the keys are generated, the sender encrypts data with the receiver’s public key and the receiver decrypts the data with their private key. This ensures that only the intended recipient can read the encrypted data. Common public key encryption algorithms include RSA and ElGamal.
INSERT INTO public.attack_patterns VALUES (37, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.43113+00', NULL, 'attack-pattern--c8e87b83-edbb-48d4-9295-4974897525b7', '[{"url": "https://attack.mitre.org/techniques/T1197", "external_id": "T1197", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms680573.aspx", "description": "Microsoft. (n.d.). Component Object Model (COM). Retrieved November 22, 2017.", "source_name": "Microsoft COM"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/bb968799.aspx", "description": "Microsoft. (n.d.). Background Intelligent Transfer Service. Retrieved January 12, 2018.", "source_name": "Microsoft BITS"}, {"url": "https://msdn.microsoft.com/library/aa362813.aspx", "description": "Microsoft. (n.d.). BITSAdmin Tool. Retrieved January 12, 2018.", "source_name": "Microsoft BITSAdmin"}, {"url": "https://www.secureworks.com/blog/malware-lingers-with-bits", "description": "Counter Threat Unit Research Team. (2016, June 6). Malware Lingers with BITS. Retrieved January 12, 2018.", "source_name": "CTU BITS Malware June 2016"}, {"url": "https://arstechnica.com/information-technology/2007/05/malware-piggybacks-on-windows-background-intelligent-transfer-service/", "description": "Mondok, M. (2007, May 11). Malware piggybacks on Windows’ Background Intelligent Transfer Service. Retrieved January 12, 2018.", "source_name": "Mondok Windows PiggyBack BITS May 2007"}, {"url": "https://www.symantec.com/connect/blogs/malware-update-windows-update", "description": "Florio, E. (2007, May 9). Malware Update with Windows Update. Retrieved January 12, 2018.", "source_name": "Symantec BITS May 2007"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/11/unit42-uboatrat-navigates-east-asia/", "description": "Hayashi, K. (2017, November 28). UBoatRAT Navigates East Asia. Retrieved January 12, 2018.", "source_name": "PaloAlto UBoatRAT Nov 2017"}, {"url": "https://technet.microsoft.com/library/dd939934.aspx", "description": "Microsoft. (2011, July 19). Issues with BITS. Retrieved January 12, 2018.", "source_name": "Microsoft Issues with BITS July 2011"}, {"url": "https://www.elastic.co/blog/hunting-for-persistence-using-elastic-security-part-1", "description": "French, D., Murphy, B. (2020, March 24). Adversary tradecraft 101: Hunting for persistence using Elastic Security (Part 1). Retrieved December 21, 2020.", "source_name": "Elastic - Hunting for Persistence Part 1"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 7043}, "x_mitre_detection": {"Num": 0, "Raw": "\"BITS runs as a service and its status can be checked with the Sc query utility (<code>sc query bits</code>).(Citation: Microsoft Issues with BITS July 2011) Active BITS tasks can be enumerated using the [BITSAdmin](https://attack.mitre.org/software/S0190) tool (<code>bitsadmin /list /allusers /verbose</code>).(Citation: Microsoft BITS)\\n\\nMonitor usage of the [BITSAdmin](https://attack.mitre.org/software/S0190) tool (especially the ‘Transfer’, ''Create'', ''AddFile'', ''SetNotifyFlags'', ''SetNotifyCmdLine'', ''SetMinRetryDelay'', ''SetCustomHeaders'', and ''Resume'' command options)(Citation: Microsoft BITS) Admin logs, PowerShell logs, and the Windows Event log for BITS activity.(Citation: Elastic - Hunting for Persistence Part 1) Also consider investigating more detailed information about jobs by parsing the BITS job database.(Citation: CTU BITS Malware June 2016)\\n\\nMonitor and analyze network activity generated by BITS. BITS jobs use HTTP(S) and SMB for remote connections and are tethered to the creating user and will only function when that user is logged on (this rule applies even if a user attaches the job to a service account).(Citation: Microsoft BITS)\"", "Str": "BITS runs as a service and its status can be checked with the Sc query utility (<code>sc query bits</code>).(Citation: Microsoft Issues with BITS July 2011) Active BITS tasks can be enumerated using the [BITSAdmin](https://attack.mitre.org/software/S0190) tool (<code>bitsadmin /list /allusers /verbose</code>).(Citation: Microsoft BITS)\n\nMonitor usage of the [BITSAdmin](https://attack.mitre.org/software/S0190) tool (especially the ‘Transfer’, ''Create'', ''AddFile'', ''SetNotifyFlags'', ''SetNotifyCmdLine'', ''SetMinRetryDelay'', ''SetCustomHeaders'', and ''Resume'' command options)(Citation: Microsoft BITS) Admin logs, PowerShell logs, and the Windows Event log for BITS activity.(Citation: Elastic - Hunting for Persistence Part 1) Also consider investigating more detailed information about jobs by parsing the BITS job database.(Citation: CTU BITS Malware June 2016)\n\nMonitor and analyze network activity generated by BITS. BITS jobs use HTTP(S) and SMB for remote connections and are tethered to the creating user and will only function when that user is logged on (this rule applies even if a user attaches the job to a service account).(Citation: Microsoft BITS)", "Type": 3, "Index": 5661}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6995}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brent Murphy, Elastic\",\n      \"David French, Elastic\",\n      \"Ricardo Dias\",\n      \"Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 5238}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Service: Service Metadata\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 5378}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6866}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Firewall\",\n      \"Host forensic analysis\"\n    ]", "Str": "", "Type": 5, "Index": 5578}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 6909}}', 'T1197', 'Adversaries may abuse BITS jobs to persistently execute or clean up after malicious payloads. Windows Background Intelligent Transfer Service (BITS) is a low-bandwidth, asynchronous file transfer mechanism exposed through [Component Object Model](https://attack.mitre.org/techniques/T1559/001) (COM).(Citation: Microsoft COM)(Citation: Microsoft BITS) BITS is commonly used by updaters, messengers, and other applications preferred to operate in the background (using available idle bandwidth) without interrupting other networked applications. File transfer tasks are implemented as BITS jobs, which contain a queue of one or more file operations.
INSERT INTO public.attack_patterns VALUES (47, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.204425+00', NULL, 'attack-pattern--389735f1-f21c-4208-b8f0-f8031e7169b8', '[{"url": "https://attack.mitre.org/techniques/T1176", "external_id": "T1176", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Browser_extension", "description": "Wikipedia. (2017, October 8). Browser Extension. Retrieved January 11, 2018.", "source_name": "Wikipedia Browser Extension"}, {"url": "https://developer.chrome.com/extensions", "description": "Chrome. (n.d.). What are Extensions?. Retrieved November 16, 2017.", "source_name": "Chrome Extensions Definition"}, {"url": "https://static.googleusercontent.com/media/research.google.com/en//pubs/archive/43824.pdf", "description": "Jagpal, N., et al. (2015, August). Trends and Lessons from Three Years Fighting Malicious Extensions. Retrieved November 17, 2017.", "source_name": "Malicious Chrome Extension Numbers"}, {"url": "https://www.xorrior.com/No-Place-Like-Chrome/", "description": "Chris Ross. (2019, February 8). No Place Like Chrome. Retrieved April 27, 2021.", "source_name": "xorrior chrome extensions macOS"}, {"url": "https://www.ghacks.net/2017/09/19/first-chrome-extension-with-javascript-crypto-miner-detected/", "description": "Brinkmann, M. (2017, September 19). First Chrome extension with JavaScript Crypto Miner detected. Retrieved November 16, 2017.", "source_name": "Chrome Extension Crypto Miner"}, {"url": "https://www.icebrg.io/blog/malicious-chrome-extensions-enable-criminals-to-impact-over-half-a-million-users-and-global-businesses", "description": "De Tore, M., Warner, J. (2018, January 15). MALICIOUS CHROME EXTENSIONS ENABLE CRIMINALS TO IMPACT OVER HALF A MILLION USERS AND GLOBAL BUSINESSES. Retrieved January 17, 2018.", "source_name": "ICEBRG Chrome Extensions"}, {"url": "https://isc.sans.edu/forums/diary/BankerGoogleChromeExtensiontargetingBrazil/22722/", "description": "Marinho, R. (n.d.). (Banker(GoogleChromeExtension)).targeting. Retrieved November 18, 2017.", "source_name": "Banker Google Chrome Extension Steals Creds"}, {"url": "https://isc.sans.edu/forums/diary/CatchAll+Google+Chrome+Malicious+Extension+Steals+All+Posted+Data/22976/https:/threatpost.com/malicious-chrome-extension-steals-data-posted-to-any-website/128680/)", "description": "Marinho, R. (n.d.). \"Catch-All\" Google Chrome Malicious Extension Steals All Posted Data. Retrieved November 16, 2017.", "source_name": "Catch All Chrome Extension"}, {"url": "https://www.welivesecurity.com/2017/07/20/stantinko-massive-adware-campaign-operating-covertly-since-2012/", "description": "Vachon, F., Faou, M. (2017, July 20). Stantinko: A massive adware campaign operating covertly since 2012. Retrieved November 16, 2017.", "source_name": "Stantinko Botnet"}, {"url": "https://kjaer.io/extension-malware/", "description": "Kjaer, M. (2016, July 18). Malware in the browser: how you might get hacked by a Chrome extension. Retrieved November 22, 2017.", "source_name": "Chrome Extension C2 Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 7561}, "x_mitre_detection": {"Num": 0, "Raw": "\"Inventory and monitor browser extension installations that deviate from normal, expected, and benign extensions. Process and network monitoring can be used to detect browsers communicating with a C2 server. However, this may prove to be a difficult way of initially detecting a malicious extension depending on the nature and volume of the traffic it generates.\\n\\nMonitor for any new items written to the Registry or PE files written to disk. That may correlate with browser extension installation.\\n\\nOn macOS, monitor the command line for usage of the profiles tool, such as <code>profiles install -type=configuration</code>. Additionally, all installed extensions maintain a <code>plist</code> file in the <code>/Library/Managed Preferences/username/</code> directory. Ensure all listed files are in alignment with approved extensions.(Citation: xorrior chrome extensions macOS)\"", "Str": "Inventory and monitor browser extension installations that deviate from normal, expected, and benign extensions. Process and network monitoring can be used to detect browsers communicating with a C2 server. However, this may prove to be a difficult way of initially detecting a malicious extension depending on the nature and volume of the traffic it generates.\n\nMonitor for any new items written to the Registry or PE files written to disk. That may correlate with browser extension installation.\n\nOn macOS, monitor the command line for usage of the profiles tool, such as <code>profiles install -type=configuration</code>. Additionally, all installed extensions maintain a <code>plist</code> file in the <code>/Library/Managed Preferences/username/</code> directory. Ensure all listed files are in alignment with approved extensions.(Citation: xorrior chrome extensions macOS)", "Type": 3, "Index": 6476}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7483}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Chris Ross @xorrior\",\n      \"Justin Warner, ICEBRG\"\n    ]", "Str": "", "Type": 5, "Index": 6136}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 6232}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7393}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7436}}', 'T1176', 'Adversaries may abuse Internet browser extensions to establish persistent access to victim systems. Browser extensions or plugins are small programs that can add functionality and customize aspects of Internet browsers. They can be installed directly or through a browser''s app store and generally have access and permissions to everything that the browser can access.(Citation: Wikipedia Browser Extension)(Citation: Chrome Extensions Definition)
INSERT INTO public.attack_patterns VALUES (32, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.465138+00', NULL, 'attack-pattern--f3d95a1f-bba2-44ce-9af7-37866cd63fd0', '[{"url": "https://attack.mitre.org/techniques/T1053/002", "external_id": "T1053.002", "source_name": "mitre-attack"}, {"url": "https://twitter.com/leoloobeek/status/939248813465853953", "description": "Loobeek, L. (2017, December 8). leoloobeek Status. Retrieved December 12, 2017.", "source_name": "Twitter Leoloobeek Scheduled Task"}, {"url": "https://social.technet.microsoft.com/Forums/en-US/e5bca729-52e7-4fcb-ba12-3225c564674c/scheduled-tasks-history-retention-settings?forum=winserver8gen", "description": "Satyajit321. (2015, November 3). Scheduled Tasks History Retention settings. Retrieved December 12, 2017.", "source_name": "TechNet Forum Scheduled Task Operational Setting"}, {"url": "https://technet.microsoft.com/library/dd315590.aspx", "description": "Microsoft. (n.d.). General Task Registration. Retrieved December 12, 2017.", "source_name": "TechNet Scheduled Task Events"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/audit-other-object-access-events", "description": "Microsoft. (2017, May 28). Audit Other Object Access Events. Retrieved June 27, 2019.", "source_name": "Microsoft Scheduled Task Events Win10"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5899}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process execution from the svchost.exe in Windows 10 and the Windows Task Scheduler taskeng.exe for older versions of Windows. (Citation: Twitter Leoloobeek Scheduled Task) If scheduled tasks are not used for persistence, then the adversary is likely to remove the task when the action is complete. Monitor Windows Task Scheduler stores in %systemroot%\\\\System32\\\\Tasks for change entries related to scheduled tasks that do not correlate with known software, patch cycles, etc.\\n\\nConfigure event logging for scheduled task creation and changes by enabling the \\\"Microsoft-Windows-TaskScheduler/Operational\\\" setting within the event logging service. (Citation: TechNet Forum Scheduled Task Operational Setting) Several events will then be logged on scheduled task activity, including: (Citation: TechNet Scheduled Task Events)(Citation: Microsoft Scheduled Task Events Win10)\\n\\n* Event ID 106 on Windows 7, Server 2008 R2 - Scheduled task registered\\n* Event ID 140 on Windows 7, Server 2008 R2 / 4702 on Windows 10, Server 2016 - Scheduled task updated\\n* Event ID 141 on Windows 7, Server 2008 R2 / 4699 on Windows 10, Server 2016 - Scheduled task deleted\\n* Event ID 4698 on Windows 10, Server 2016 - Scheduled task created\\n* Event ID 4700 on Windows 10, Server 2016 - Scheduled task enabled\\n* Event ID 4701 on Windows 10, Server 2016 - Scheduled task disabled\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current scheduled tasks. (Citation: TechNet Autoruns)\\n\\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Tasks may also be created through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), so additional logging may need to be configured to gather the appropriate data.\"", "Str": "Monitor process execution from the svchost.exe in Windows 10 and the Windows Task Scheduler taskeng.exe for older versions of Windows. (Citation: Twitter Leoloobeek Scheduled Task) If scheduled tasks are not used for persistence, then the adversary is likely to remove the task when the action is complete. Monitor Windows Task Scheduler stores in %systemroot%\\System32\\Tasks for change entries related to scheduled tasks that do not correlate with known software, patch cycles, etc.\n\nConfigure event logging for scheduled task creation and changes by enabling the \"Microsoft-Windows-TaskScheduler/Operational\" setting within the event logging service. (Citation: TechNet Forum Scheduled Task Operational Setting) Several events will then be logged on scheduled task activity, including: (Citation: TechNet Scheduled Task Events)(Citation: Microsoft Scheduled Task Events Win10)\n\n* Event ID 106 on Windows 7, Server 2008 R2 - Scheduled task registered\n* Event ID 140 on Windows 7, Server 2008 R2 / 4702 on Windows 10, Server 2016 - Scheduled task updated\n* Event ID 141 on Windows 7, Server 2008 R2 / 4699 on Windows 10, Server 2016 - Scheduled task deleted\n* Event ID 4698 on Windows 10, Server 2016 - Scheduled task created\n* Event ID 4700 on Windows 10, Server 2016 - Scheduled task enabled\n* Event ID 4701 on Windows 10, Server 2016 - Scheduled task disabled\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current scheduled tasks. (Citation: TechNet Autoruns)\n\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Tasks may also be created through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001), so additional logging may need to be configured to gather the appropriate data.", "Type": 3, "Index": 3663}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5815}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3479}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5870}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5717}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5759}}', 'T1053.002', 'Adversaries may abuse the <code>at.exe</code> utility to perform task scheduling for initial or recurring execution of malicious code. The [at](https://attack.mitre.org/software/S0110) utility exists as an executable within Windows for scheduling tasks at a specified time and date. Using [at](https://attack.mitre.org/software/S0110) requires that the Task Scheduler service be running, and the user to be logged on as a member of the local Administrators group. 
INSERT INTO public.attack_patterns VALUES (34, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.5407+00', NULL, 'attack-pattern--b8cfed42-6a8a-4989-ad72-541af74475ec', '[{"url": "https://attack.mitre.org/techniques/T1547/002", "external_id": "T1547.002", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/aa374733.aspx", "description": "Microsoft. (n.d.). Authentication Packages. Retrieved March 1, 2017.", "source_name": "MSDN Authentication Packages"}, {"url": "http://docplayer.net/20839173-Analysis-of-malicious-security-support-provider-dlls.html", "description": "Graeber, M. (2014, October). Analysis of Malicious Security Support Provider DLLs. Retrieved March 1, 2017.", "source_name": "Graeber 2014"}, {"url": "https://technet.microsoft.com/en-us/library/dn408187.aspx", "description": "Microsoft. (2013, July 31). Configuring Additional LSA Protection. Retrieved June 24, 2015.", "source_name": "Microsoft Configure LSA"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3278}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the Registry for changes to the LSA Registry keys. Monitor the LSA process for DLL loads. Windows 8.1 and Windows Server 2012 R2 may generate events when unsigned DLLs try to load into the LSA by setting the Registry key <code>HKLM\\\\SOFTWARE\\\\Microsoft\\\\Windows NT\\\\CurrentVersion\\\\Image File Execution Options\\\\LSASS.exe</code> with AuditLevel = 8. (Citation: Graeber 2014) (Citation: Microsoft Configure LSA)\"", "Str": "Monitor the Registry for changes to the LSA Registry keys. Monitor the LSA process for DLL loads. Windows 8.1 and Windows Server 2012 R2 may generate events when unsigned DLLs try to load into the LSA by setting the Registry key <code>HKLM\\SOFTWARE\\Microsoft\\Windows NT\\CurrentVersion\\Image File Execution Options\\LSASS.exe</code> with AuditLevel = 8. (Citation: Graeber 2014) (Citation: Microsoft Configure LSA)", "Type": 3, "Index": 2679}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3230}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2520}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3132}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3174}}', 'T1547.002', 'Adversaries may abuse authentication packages to execute DLLs when the system boots. Windows authentication package DLLs are loaded by the Local Security Authority (LSA) process at system start. They provide support for multiple logon processes and multiple security protocols to the operating system. (Citation: MSDN Authentication Packages)
INSERT INTO public.attack_patterns VALUES (38, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.673835+00', NULL, 'attack-pattern--8187bd2a-866f-4457-9009-86b0ddedffa3', '[{"url": "https://attack.mitre.org/techniques/T1552/003", "external_id": "T1552.003", "source_name": "mitre-attack"}, {"url": "http://www.slideshare.net/StephanBorosh/external-to-da-the-os-x-way", "description": "Alex Rymdeko-Harvey, Steve Borosh. (2016, May 14). External to DA, the OS X Way. Retrieved July 3, 2017.", "source_name": "External to DA, the OS X Way"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2407}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring when the user''s <code>.bash_history</code> is read can help alert to suspicious activity. While users do typically rely on their history of commands, they often access this history through other utilities like \\\"history\\\" instead of commands like <code>cat ~/.bash_history</code>.\"", "Str": "Monitoring when the user''s <code>.bash_history</code> is read can help alert to suspicious activity. While users do typically rely on their history of commands, they often access this history through other utilities like \"history\" instead of commands like <code>cat ~/.bash_history</code>.", "Type": 3, "Index": 1931}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2346}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1835}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2257}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2299}}', 'T1552.003', 'Adversaries may search the bash command history on compromised systems for insecurely stored credentials. Bash keeps track of the commands users type on the command-line with the "history" utility. Once a user logs out, the history is flushed to the user’s <code>.bash_history</code> file. For each user, this file resides at the same location: <code>~/.bash_history</code>. Typically, this file keeps track of the user’s last 500 commands. Users often type usernames and passwords on the command-line as parameters to programs, which then get saved to this file when they log out. Attackers can abuse this by looking through the file for potential credentials. (Citation: External to DA, the OS X Way)', 'Bash History', 514);
INSERT INTO public.attack_patterns VALUES (42, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:27.95555+00', NULL, 'attack-pattern--03259939-0b57-482f-8eb5-87c0e0d54334', '[{"url": "https://attack.mitre.org/techniques/T1037", "external_id": "T1037", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/564.html", "external_id": "CAPEC-564", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 2554}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.\"", "Str": "Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.", "Type": 3, "Index": 2118}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2476}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1831}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2444}}', 'T1037', 'Adversaries may use scripts automatically executed at boot or logon initialization to establish persistence. Initialization scripts can be used to perform administrative functions, which may often execute other programs or send information to an internal logging server. These scripts can vary based on operating system and whether applied locally or remotely.  
INSERT INTO public.attack_patterns VALUES (494, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.159069+00', NULL, 'attack-pattern--dc31fe1e-d722-49da-8f5f-92c7b5aff534', '[{"url": "https://attack.mitre.org/techniques/T1221", "external_id": "T1221", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/previous-versions/office/developer/office-2007/aa338205(v=office.12)", "description": "Microsoft. (2014, July 9). Introducing the Office (2007) Open XML File Formats. Retrieved July 20, 2018.", "source_name": "Microsoft Open XML July 2017"}, {"url": "https://www.sans.org/reading-room/whitepapers/testing/template-injection-attacks-bypassing-security-controls-living-land-38780", "description": "Wiltse, B.. (2018, November 7). Template Injection Attacks - Bypassing Security Controls by Living off the Land. Retrieved April 10, 2019.", "source_name": "SANS Brian Wiltse Template Injection"}, {"url": "http://blog.redxorblue.com/2018/07/executing-macros-from-docx-with-remote.html", "description": "Hawkins, J. (2018, July 18). Executing Macros From a DOCX With Remote Template Injection. Retrieved October 12, 2018.", "source_name": "Redxorblue Remote Template Injection"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/10/decoy-microsoft-word-document-delivers-malware-through-rat/", "description": "Segura, J. (2017, October 13). Decoy Microsoft Word document delivers malware through a RAT. Retrieved July 21, 2018.", "source_name": "MalwareBytes Template Injection OCT 2017"}, {"url": "https://forum.anomali.com/t/credential-harvesting-and-malicious-file-delivery-using-microsoft-office-template-injection/2104", "description": "Intel_Acquisition_Team. (2018, March 1). Credential Harvesting and Malicious File Delivery using Microsoft Office Template Injection. Retrieved July 20, 2018.", "source_name": "Anomali Template Injection MAR 2018"}, {"url": "https://blog.talosintelligence.com/2017/07/template-injection.html", "description": "Baird, S. et al.. (2017, July 7). Attack on Critical Infrastructure Leverages Template Injection. Retrieved July 21, 2018.", "source_name": "Talos Template Injection July 2017"}, {"url": "https://github.com/ryhanson/phishery", "description": "Hanson, R. (2016, September 24). phishery. Retrieved July 21, 2018.", "source_name": "ryhanson phishery SEPT 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 5923}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze process behavior to determine if an Office application is performing actions, such as opening network connections, reading files, spawning abnormal child processes (ex: [PowerShell](https://attack.mitre.org/techniques/T1059/001)), or other suspicious actions that could relate to post-compromise behavior.\"", "Str": "Analyze process behavior to determine if an Office application is performing actions, such as opening network connections, reading files, spawning abnormal child processes (ex: [PowerShell](https://attack.mitre.org/techniques/T1059/001)), or other suspicious actions that could relate to post-compromise behavior.", "Type": 3, "Index": 5437}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5875}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brian Wiltse @evalstrings\",\n      \"Patrick Campbell, @pjcampbe11\"\n    ]", "Str": "", "Type": 5, "Index": 5085}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 5195}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5785}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Static File Analysis\"\n    ]", "Str": "", "Type": 5, "Index": 5374}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5828}}', 'T1221', 'Adversaries may create or modify references in Office document templates to conceal malicious code or force authentication attempts. Microsoft’s Office Open XML (OOXML) specification defines an XML-based format for Office documents (.docx, xlsx, .pptx) to replace older binary formats (.doc, .xls, .ppt). OOXML files are packed together ZIP archives compromised of various XML files, referred to as parts, containing properties that collectively define how a document is rendered. (Citation: Microsoft Open XML July 2017)
INSERT INTO public.attack_patterns VALUES (48, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.259431+00', NULL, 'attack-pattern--a93494bb-4b80-4ea1-8695-3236a49916fd', '[{"url": "https://attack.mitre.org/techniques/T1110", "external_id": "T1110", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/49.html", "external_id": "CAPEC-49", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 2699}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials. Also monitor for many failed authentication attempts across various accounts that may result from password spraying attempts. It is difficult to detect when hashes are cracked, since this is generally done outside the scope of the target network.\"", "Str": "Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials. Also monitor for many failed authentication attempts across various accounts that may result from password spraying attempts. It is difficult to detect when hashes are cracked, since this is generally done outside the scope of the target network.", "Type": 3, "Index": 1870}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2509}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Alfredo Oliveira, Trend Micro\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 1474}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 1736}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2419}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2462}}', 'T1110', 'Adversaries may use brute force techniques to gain access to accounts when passwords are unknown or when password hashes are obtained. Without knowledge of the password for an account or set of accounts, an adversary may systematically guess the password using a repetitive or iterative mechanism. Brute forcing passwords can take place via interaction with a service that will check the validity of those credentials or offline against previously acquired credential data, such as password hashes.', 'Brute Force', 0);
INSERT INTO public.attack_patterns VALUES (51, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.379145+00', NULL, 'attack-pattern--120d5519-3098-4e1c-9191-2aa61232f073', '[{"url": "https://attack.mitre.org/techniques/T1548/002", "external_id": "T1548.002", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/itpro/windows/keep-secure/how-user-account-control-works", "description": "Lich, B. (2016, May 31). How User Account Control Works. Retrieved June 3, 2016.", "source_name": "TechNet How UAC Works"}, {"url": "https://technet.microsoft.com/en-US/magazine/2009.07.uac.aspx", "description": "Russinovich, M. (2009, July). User Account Control: Inside Windows 7 User Account Control. Retrieved July 26, 2016.", "source_name": "TechNet Inside UAC"}, {"url": "https://msdn.microsoft.com/en-us/library/ms679687.aspx", "description": "Microsoft. (n.d.). The COM Elevation Moniker. Retrieved July 26, 2016.", "source_name": "MSDN COM Elevation"}, {"url": "http://www.pretentiousname.com/misc/win7_uac_whitelist2.html", "description": "Davidson, L. (n.d.). Windows 7 UAC whitelist. Retrieved November 12, 2014.", "source_name": "Davidson Windows"}, {"url": "https://github.com/hfiref0x/UACME", "description": "UACME Project. (2016, June 16). UACMe. Retrieved July 26, 2016.", "source_name": "Github UACMe"}, {"url": "https://enigma0x3.net/2016/08/15/fileless-uac-bypass-using-eventvwr-exe-and-registry-hijacking/", "description": "Nelson, M. (2016, August 15). \"Fileless\" UAC Bypass using eventvwr.exe and Registry Hijacking. Retrieved December 27, 2016.", "source_name": "enigma0x3 Fileless UAC Bypass"}, {"url": "https://blog.fortinet.com/2016/12/16/malicious-macro-bypasses-uac-to-elevate-privilege-for-fareit-malware", "description": "Salvio, J., Joven, R. (2016, December 16). Malicious Macro Bypasses UAC to Elevate Privilege for Fareit Malware. Retrieved December 27, 2016.", "source_name": "Fortinet Fareit"}, {"url": "http://pen-testing.sans.org/blog/pen-testing/2013/08/08/psexec-uac-bypass", "description": "Medin, T. (2013, August 8). PsExec UAC Bypass. Retrieved June 3, 2016.", "source_name": "SANS UAC Bypass"}, {"url": "https://enigma0x3.net/2017/03/14/bypassing-uac-using-app-paths/", "description": "Nelson, M. (2017, March 14). Bypassing UAC using App Paths. Retrieved May 25, 2017.", "source_name": "enigma0x3 sdclt app paths"}, {"url": "https://enigma0x3.net/2017/03/17/fileless-uac-bypass-using-sdclt-exe/", "description": "Nelson, M. (2017, March 17). \"Fileless\" UAC Bypass Using sdclt.exe. Retrieved May 25, 2017.", "source_name": "enigma0x3 sdclt bypass"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 7958}, "x_mitre_detection": {"Num": 0, "Raw": "\"There are many ways to perform UAC bypasses when a user is in the local administrator group on a system, so it may be difficult to target detection on all variations. Efforts should likely be placed on mitigation and collecting enough information on process launches and actions that could be performed before and after a UAC bypass is performed. Monitor process API calls for behavior that may be indicative of [Process Injection](https://attack.mitre.org/techniques/T1055) and unusual loaded DLLs through [DLL Search Order Hijacking](https://attack.mitre.org/techniques/T1574/001), which indicate attempts to gain access to higher privileged processes.\\n\\nSome UAC bypass methods rely on modifying specific, user-accessible Registry settings. For example:\\n\\n* The <code>eventvwr.exe</code> bypass uses the <code>[HKEY_CURRENT_USER]\\\\Software\\\\Classes\\\\mscfile\\\\shell\\\\open\\\\command</code> Registry key.(Citation: enigma0x3 Fileless UAC Bypass)\\n\\n* The <code>sdclt.exe</code> bypass uses the <code>[HKEY_CURRENT_USER]\\\\Software\\\\Microsoft\\\\Windows\\\\CurrentVersion\\\\App Paths\\\\control.exe</code> and <code>[HKEY_CURRENT_USER]\\\\Software\\\\Classes\\\\exefile\\\\shell\\\\runas\\\\command\\\\isolatedCommand</code> Registry keys.(Citation: enigma0x3 sdclt app paths)(Citation: enigma0x3 sdclt bypass)\\n\\nAnalysts should monitor these Registry settings for unauthorized changes.\"", "Str": "There are many ways to perform UAC bypasses when a user is in the local administrator group on a system, so it may be difficult to target detection on all variations. Efforts should likely be placed on mitigation and collecting enough information on process launches and actions that could be performed before and after a UAC bypass is performed. Monitor process API calls for behavior that may be indicative of [Process Injection](https://attack.mitre.org/techniques/T1055) and unusual loaded DLLs through [DLL Search Order Hijacking](https://attack.mitre.org/techniques/T1574/001), which indicate attempts to gain access to higher privileged processes.\n\nSome UAC bypass methods rely on modifying specific, user-accessible Registry settings. For example:\n\n* The <code>eventvwr.exe</code> bypass uses the <code>[HKEY_CURRENT_USER]\\Software\\Classes\\mscfile\\shell\\open\\command</code> Registry key.(Citation: enigma0x3 Fileless UAC Bypass)\n\n* The <code>sdclt.exe</code> bypass uses the <code>[HKEY_CURRENT_USER]\\Software\\Microsoft\\Windows\\CurrentVersion\\App Paths\\control.exe</code> and <code>[HKEY_CURRENT_USER]\\Software\\Classes\\exefile\\shell\\runas\\command\\isolatedCommand</code> Registry keys.(Citation: enigma0x3 sdclt app paths)(Citation: enigma0x3 sdclt bypass)\n\nAnalysts should monitor these Registry settings for unauthorized changes.", "Type": 3, "Index": 6330}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7910}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\",\n      \"Casey Smith\"\n    ]", "Str": "", "Type": 5, "Index": 5971}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Metadata\",\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 6052}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7798}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\"\n    ]", "Str": "", "Type": 5, "Index": 6259}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7840}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 7736}}', 'T1548.002', 'Adversaries may bypass UAC mechanisms to elevate process privileges on system. Windows User Account Control (UAC) allows a program to elevate its privileges (tracked as integrity levels ranging from low to high) to perform a task under administrator-level permissions, possibly by prompting the user for confirmation. The impact to the user ranges from denying the operation under high enforcement to allowing the user to perform the action if they are in the local administrators group and click through the prompt or allowing them to enter an administrator password to complete the action. (Citation: TechNet How UAC Works)
INSERT INTO public.attack_patterns VALUES (53, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.424726+00', NULL, 'attack-pattern--4cbc6a62-9e34-4f94-8a19-5c1a11392a49', '[{"url": "https://attack.mitre.org/techniques/T1218/003", "external_id": "T1218.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/previous-versions/windows/it-pro/windows-server-2003/cc786431(v=ws.10)", "description": "Microsoft. (2009, October 8). How Connection Manager Works. Retrieved April 11, 2018.", "source_name": "Microsoft Connection Manager Oct 2009"}, {"url": "https://twitter.com/ItsReallyNick/status/958789644165894146", "description": "Carr, N. (2018, January 31). Here is some early bad cmstp.exe... Retrieved April 11, 2018.", "source_name": "Twitter CMSTP Usage Jan 2018"}, {"url": "https://msitpros.com/?p=3960", "description": "Moe, O. (2017, August 15). Research on CMSTP.exe. Retrieved April 11, 2018.", "source_name": "MSitPros CMSTP Aug 2017"}, {"url": "https://twitter.com/NickTyrer/status/958450014111633408", "description": "Tyrer, N. (2018, January 30). CMSTP.exe - remote .sct execution applocker bypass. Retrieved April 11, 2018.", "source_name": "Twitter CMSTP Jan 2018"}, {"url": "https://github.com/api0cradle/UltimateAppLockerByPassList", "description": "Moe, O. (2018, March 1). Ultimate AppLocker Bypass List. Retrieved April 10, 2018.", "source_name": "GitHub Ultimate AppLocker Bypass List"}, {"url": "http://www.endurant.io/cmstp/detecting-cmstp-enabled-code-execution-and-uac-bypass-with-sysmon/", "description": "Seetharaman, N. (2018, July 7). Detecting CMSTP-Enabled Code Execution and UAC Bypass With Sysmon.. Retrieved August 6, 2018.", "source_name": "Endurant CMSTP July 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5572}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to detect and analyze the execution and arguments of CMSTP.exe. Compare recent invocations of CMSTP.exe with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity.\\n\\nSysmon events can also be used to identify potential abuses of CMSTP.exe. Detection strategy may depend on the specific adversary procedure, but potential rules include: (Citation: Endurant CMSTP July 2018)\\n\\n* To detect loading and execution of local/remote payloads - Event 1 (Process creation) where ParentImage contains CMSTP.exe and/or Event 3 (Network connection) where Image contains CMSTP.exe and DestinationIP is external.\\n* To detect [Bypass User Account Control](https://attack.mitre.org/techniques/T1548/002) via an auto-elevated COM interface - Event 10 (ProcessAccess) where CallTrace contains CMLUA.dll and/or Event 12 or 13 (RegistryEvent) where TargetObject contains CMMGR32.exe. Also monitor for events, such as the creation of processes (Sysmon Event 1), that involve auto-elevated CMSTP COM interfaces such as CMSTPLUA (3E5FC7F9-9A51-4367-9063-A120244FBEC7) and CMLUAUTIL (3E000D72-A845-4CD9-BD83-80C07C3B881F).\"", "Str": "Use process monitoring to detect and analyze the execution and arguments of CMSTP.exe. Compare recent invocations of CMSTP.exe with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity.\n\nSysmon events can also be used to identify potential abuses of CMSTP.exe. Detection strategy may depend on the specific adversary procedure, but potential rules include: (Citation: Endurant CMSTP July 2018)\n\n* To detect loading and execution of local/remote payloads - Event 1 (Process creation) where ParentImage contains CMSTP.exe and/or Event 3 (Network connection) where Image contains CMSTP.exe and DestinationIP is external.\n* To detect [Bypass User Account Control](https://attack.mitre.org/techniques/T1548/002) via an auto-elevated COM interface - Event 10 (ProcessAccess) where CallTrace contains CMLUA.dll and/or Event 12 or 13 (RegistryEvent) where TargetObject contains CMMGR32.exe. Also monitor for events, such as the creation of processes (Sysmon Event 1), that involve auto-elevated CMSTP COM interfaces such as CMSTPLUA (3E5FC7F9-9A51-4367-9063-A120244FBEC7) and CMLUAUTIL (3E000D72-A845-4CD9-BD83-80C07C3B881F).", "Type": 3, "Index": 4216}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5524}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Nik Seetharaman, Palantir\",\n      \"Ye Yint Min Thu Htut, Offensive Security Team, DBS Bank\"\n    ]", "Str": "", "Type": 5, "Index": 3833}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3969}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5435}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 4134}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5477}}', 'T1218.003', 'Adversaries may abuse CMSTP to proxy execution of malicious code. The Microsoft Connection Manager Profile Installer (CMSTP.exe) is a command-line program used to install Connection Manager service profiles. (Citation: Microsoft Connection Manager Oct 2009) CMSTP.exe accepts an installation information file (INF) as a parameter and installs a service profile leveraged for remote access connections.
INSERT INTO public.attack_patterns VALUES (55, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.504249+00', NULL, 'attack-pattern--6add2ab5-2711-4e9d-87c8-7a0be8531530', '[{"url": "https://attack.mitre.org/techniques/T1003/005", "external_id": "T1003.005", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/it-pro/windows-server-2012-r2-and-2012/hh994565(v%3Dws.11)", "description": "Microsfot. (2016, August 21). Cached and Stored Credentials Technical Overview. Retrieved February 21, 2020.", "source_name": "Microsoft - Cached Creds"}, {"url": "https://passlib.readthedocs.io/en/stable/lib/passlib.hash.msdcc2.html", "description": "Eli Collins. (2016, November 25). Windows'' Domain Cached Credentials v2. Retrieved February 21, 2020.", "source_name": "PassLib mscache"}, {"url": "https://ired.team/offensive-security/credential-access-and-credential-dumping/dumping-and-cracking-mscash-cached-domain-credentials", "description": "Mantvydas Baranauskas. (2019, November 16). Dumping and Cracking mscash - Cached Domain Credentials. Retrieved February 21, 2020.", "source_name": "ired mscache"}, {"url": "https://github.com/mattifestation/PowerSploit", "description": "PowerSploit. (n.d.). Retrieved December 4, 2014.", "source_name": "Powersploit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3893}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\\n\\nDetection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well.\"", "Str": "Monitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\n\nDetection of compromised [Valid Accounts](https://attack.mitre.org/techniques/T1078) in-use by adversaries may help as well.", "Type": 3, "Index": 3104}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3845}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 2955}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3035}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3754}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3796}}', 'T1003.005', 'Adversaries may attempt to access cached domain credentials used to allow authentication to occur in the event a domain controller is unavailable.(Citation: Microsoft - Cached Creds)
INSERT INTO public.attack_patterns VALUES (57, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.623041+00', NULL, 'attack-pattern--3aef9463-9a7a-43ba-8957-a867e07c1e6a', '[{"url": "https://attack.mitre.org/techniques/T1070/003", "external_id": "T1070.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/microsoft.powershell.core/about/about_history?view=powershell-7", "description": "Microsoft. (2020, May 13). About History. Retrieved September 4, 2020.", "source_name": "Microsoft PowerShell Command History"}, {"url": "https://community.sophos.com/products/intercept/early-access-program/f/live-discover-response-queries/121529/live-discover---powershell-command-audit", "description": "jak. (2020, June 27). Live Discover - PowerShell command audit. Retrieved August 21, 2020.", "source_name": "Sophos PowerShell command audit"}, {"url": "https://community.sophos.com/products/malware/b/blog/posts/powershell-command-history-forensics", "description": "Vikas, S. (2020, August 26). PowerShell Command History Forensics. Retrieved September 4, 2020.", "source_name": "Sophos PowerShell Command History Forensics"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4911}, "x_mitre_detection": {"Num": 0, "Raw": "\"User authentication, especially via remote terminal services like SSH, without new entries in that user''s <code>~/.bash_history</code> is suspicious. Additionally, the removal/clearing of the <code>~/.bash_history</code> file can be an indicator of suspicious activity.\\n\\nMonitor for suspicious modifications or deletion of <code>ConsoleHost_history.txt</code> and use of the <code>Clear-History</code> command.\"", "Str": "User authentication, especially via remote terminal services like SSH, without new entries in that user''s <code>~/.bash_history</code> is suspicious. Additionally, the removal/clearing of the <code>~/.bash_history</code> file can be an indicator of suspicious activity.\n\nMonitor for suspicious modifications or deletion of <code>ConsoleHost_history.txt</code> and use of the <code>Clear-History</code> command.", "Type": 3, "Index": 4297}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4833}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vikas Singh, Sophos\",\n      \"Emile Kenning, Sophos\"\n    ]", "Str": "", "Type": 5, "Index": 3976}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"File: File Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 4072}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4744}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\",\n      \"Log analysis\"\n    ]", "Str": "", "Type": 5, "Index": 4210}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4786}}', 'T1070.003', 'In addition to clearing system logs, an adversary may clear the command history of a compromised account to conceal the actions undertaken during an intrusion. Various command interpreters keep track of the commands users type in their terminal so that users can retrace what they''ve done.
INSERT INTO public.attack_patterns VALUES (62, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.848592+00', NULL, 'attack-pattern--a009cb25-4801-4116-9105-80a91cf15c1b', '[{"url": "https://attack.mitre.org/techniques/T1136/003", "external_id": "T1136.003", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/office365/admin/add-users/about-admin-roles?view=o365-worldwide", "description": "Ako-Adjei, K., Dickhaus, M., Baumgartner, P., Faigel, D., et. al.. (2019, October 8). About admin roles. Retrieved October 18, 2019.", "source_name": "Microsoft O365 Admin Roles"}, {"url": "https://support.office.com/en-us/article/add-another-admin-f693489f-9f55-4bd0-a637-a81ce93de22d", "description": "Microsoft. (n.d.). Add Another Admin. Retrieved October 18, 2019.", "source_name": "Microsoft Support O365 Add Another Admin, October 2019"}, {"url": "https://docs.aws.amazon.com/IAM/latest/UserGuide/id_users_create.html", "description": "AWS. (n.d.). Creating an IAM User in Your AWS Account. Retrieved January 29, 2020.", "source_name": "AWS Create IAM User"}, {"url": "https://support.google.com/cloudidentity/answer/7332836?hl=en&ref_topic=7558554", "description": "Google. (n.d.). Create Cloud Identity user accounts. Retrieved January 29, 2020.", "source_name": "GCP Create Cloud Identity Users"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/fundamentals/add-users-azure-active-directory", "description": "Microsoft. (2019, November 11). Add or delete users using Azure Active Directory. Retrieved January 30, 2020.", "source_name": "Microsoft Azure AD Users"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3635}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect usage logs from cloud user and administrator accounts to identify unusual activity in the creation of new accounts and assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins.\"", "Str": "Collect usage logs from cloud user and administrator accounts to identify unusual activity in the creation of new accounts and assignment of roles to those accounts. Monitor for accounts assigned to admin roles that go over a certain threshold of known admins.", "Type": 3, "Index": 3133}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3526}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\",\n      \"Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 2945}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3055}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3428}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3470}}', 'T1136.003', 'Adversaries may create a cloud account to maintain access to victim systems. With a sufficient level of access, such accounts may be used to establish secondary credentialed access that does not require persistent remote access tools to be deployed on the system.(Citation: Microsoft O365 Admin Roles)(Citation: Microsoft Support O365 Add Another Admin, October 2019)(Citation: AWS Create IAM User)(Citation: GCP Create Cloud Identity Users)(Citation: Microsoft Azure AD Users)
INSERT INTO public.attack_patterns VALUES (63, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:28.892105+00', NULL, 'attack-pattern--8f104855-e5b7-4077-b1f5-bc3103b41abe', '[{"url": "https://attack.mitre.org/techniques/T1087/004", "external_id": "T1087.004", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/msonline/get-msolrolemember?view=azureadps-1.0", "description": "Microsoft. (n.d.). Get-MsolRoleMember. Retrieved October 6, 2019.", "source_name": "Microsoft msolrolemember"}, {"url": "https://github.com/True-Demon/raindance", "description": "Stringer, M.. (2018, November 21). RainDance. Retrieved October 6, 2019.", "source_name": "GitHub Raindance"}, {"url": "https://docs.microsoft.com/en-us/cli/azure/ad/user?view=azure-cli-latest", "description": "Microsoft. (n.d.). az ad user. Retrieved October 6, 2019.", "source_name": "Microsoft AZ CLI"}, {"url": "https://www.blackhillsinfosec.com/red-teaming-microsoft-part-1-active-directory-leaks-via-azure/", "description": "Felch, M.. (2018, August 31). Red Teaming Microsoft Part 1 Active Directory Leaks via Azure. Retrieved October 6, 2019.", "source_name": "Black Hills Red Teaming MS AD Azure, 2018"}, {"url": "https://docs.aws.amazon.com/cli/latest/reference/iam/list-roles.html", "description": "Amazon. (n.d.). List Roles. Retrieved August 11, 2020.", "source_name": "AWS List Roles"}, {"url": "https://docs.aws.amazon.com/cli/latest/reference/iam/list-users.html", "description": "Amazon. (n.d.). List Users. Retrieved August 11, 2020.", "source_name": "AWS List Users"}, {"url": "https://cloud.google.com/sdk/gcloud/reference/iam/service-accounts/list", "description": "Google. (2020, June 23). gcloud iam service-accounts list. Retrieved August 4, 2020.", "source_name": "Google Cloud - IAM Servie Accounts List API"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4947}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes, command-line arguments, and logs for actions that could be taken to gather information about cloud accounts, including the use of calls to cloud APIs that perform account discovery.\\n\\nSystem and network discovery techniques normally occur throughout an operation as an adversary learns the environment, and also to an extent in normal network operations. Therefore discovery data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\"", "Str": "Monitor processes, command-line arguments, and logs for actions that could be taken to gather information about cloud accounts, including the use of calls to cloud APIs that perform account discovery.\n\nSystem and network discovery techniques normally occur throughout an operation as an adversary learns the environment, and also to an extent in normal network operations. Therefore discovery data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.", "Type": 3, "Index": 4122}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4824}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 3952}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Metadata\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4008}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4735}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4777}}', 'T1087.004', 'Adversaries may attempt to get a listing of cloud accounts. Cloud accounts are those created and configured by an organization for use by users, remote support, services, or for administration of resources within a cloud service provider or SaaS application.
INSERT INTO public.attack_patterns VALUES (66, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.049053+00', NULL, 'attack-pattern--57a3d31a-d04f-4663-b2da-7df8ec3f8c9d', '[{"url": "https://attack.mitre.org/techniques/T1580", "external_id": "T1580", "source_name": "mitre-attack"}, {"url": "https://docs.aws.amazon.com/cli/latest/reference/ssm/describe-instance-information.html", "description": "Amazon. (n.d.). describe-instance-information. Retrieved March 3, 2020.", "source_name": "Amazon Describe Instance"}, {"url": "https://docs.aws.amazon.com/AWSEC2/latest/APIReference/API_DescribeInstances.html", "description": "Amazon. (n.d.). DescribeInstances. Retrieved May 26, 2020.", "source_name": "Amazon Describe Instances API"}, {"url": "https://cloud.google.com/sdk/gcloud/reference/compute/instances/list", "description": "Google. (n.d.). gcloud compute instances list. Retrieved May 26, 2020.", "source_name": "Google Compute Instances"}, {"url": "https://docs.microsoft.com/en-us/cli/azure/ad/user?view=azure-cli-latest", "description": "Microsoft. (n.d.). az ad user. Retrieved October 6, 2019.", "source_name": "Microsoft AZ CLI"}, {"url": "https://expel.io/blog/finding-evil-in-aws/", "description": "A. Randazzo, B. Manahan and S. Lipton. (2020, April 28). Finding Evil in AWS. Retrieved June 25, 2020.", "source_name": "Expel IO Evil in AWS"}, {"url": "https://content.fireeye.com/m-trends/rpt-m-trends-2020", "description": "Mandiant. (2020, February). M-Trends 2020. Retrieved April 24, 2020.", "source_name": "Mandiant M-Trends 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5041}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish centralized logging for the activity of cloud infrastructure components. Monitor logs for actions that could be taken to gather information about cloud infrastructure, including the use of discovery API calls by new or unexpected users. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.\"", "Str": "Establish centralized logging for the activity of cloud infrastructure components. Monitor logs for actions that could be taken to gather information about cloud infrastructure, including the use of discovery API calls by new or unexpected users. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.", "Type": 3, "Index": 4374}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 4996}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 3965}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Metadata\",\n      \"Instance: Instance Enumeration\",\n      \"Snapshot: Snapshot Metadata\",\n      \"Snapshot: Snapshot Enumeration\",\n      \"Cloud Storage: Cloud Storage Metadata\",\n      \"Cloud Storage: Cloud Storage Enumeration\",\n      \"Volume: Volume Metadata\",\n      \"Volume: Volume Enumeration\"\n    ]", "Str": "", "Type": 5, "Index": 4021}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4906}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4949}}', 'T1580', 'An adversary may attempt to discover resources that are available within an infrastructure-as-a-service (IaaS) environment. This includes compute service resources such as instances, virtual machines, and snapshots as well as resources of other services including the storage and database services.
INSERT INTO public.attack_patterns VALUES (71, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.249077+00', NULL, 'attack-pattern--34b3f738-bd64-40e5-a112-29b0542bc8bf', '[{"url": "https://attack.mitre.org/techniques/T1587/002", "external_id": "T1587.002", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Code_signing", "description": "Wikipedia. (2015, November 10). Code Signing. Retrieved March 31, 2016.", "source_name": "Wikipedia Code Signing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2297}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related follow-on behavior, such as [Code Signing](https://attack.mitre.org/techniques/T1553/002) or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related follow-on behavior, such as [Code Signing](https://attack.mitre.org/techniques/T1553/002) or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).", "Type": 3, "Index": 1842}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2253}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2222}}', 'T1587.002', 'Adversaries may create self-signed code signing certificates that can be used during targeting. Code signing is the process of digitally signing executables and scripts to confirm the software author and guarantee that the code has not been altered or corrupted. Code signing provides a level of authenticity for a program from the developer and a guarantee that the program has not been tampered with.(Citation: Wikipedia Code Signing) Users and/or security tools may trust a signed piece of code more than an unsigned piece of code even if they don''t know who issued the certificate or who the author is.
INSERT INTO public.attack_patterns VALUES (74, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.392924+00', NULL, 'attack-pattern--7385dfaf-6886-4229-9ecd-6fd678040830', '[{"url": "https://attack.mitre.org/techniques/T1059", "external_id": "T1059", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 3767}, "x_mitre_detection": {"Num": 0, "Raw": "\"Command-line and scripting activities can be captured through proper logging of process execution with command-line arguments. This information can be useful in gaining additional insight to adversaries'' actions through how they use native processes or custom tools. Also monitor for loading of modules associated with specific languages.\\n\\nIf scripting is restricted for normal users, then any attempt to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\\n\\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information discovery, collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.\"", "Str": "Command-line and scripting activities can be captured through proper logging of process execution with command-line arguments. This information can be useful in gaining additional insight to adversaries'' actions through how they use native processes or custom tools. Also monitor for loading of modules associated with specific languages.\n\nIf scripting is restricted for normal users, then any attempt to enable scripts running on a system would be considered suspicious. If scripts are not commonly used on a system, but enabled, scripts running out of cycle from patching or other administrator functions are suspicious. Scripts should be captured from the file system when possible to determine their actions and intent.\n\nScripts are likely to perform actions with various effects on a system that may generate events, depending on the types of monitoring used. Monitor processes and command-line arguments for script execution and subsequent behavior. Actions may be related to network and system information discovery, collection, or other scriptable post-compromise behaviors and could be used as indicators of detection leading back to the source script.", "Type": 3, "Index": 2345}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3635}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2178}, "x_mitre_remote_support": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3737}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3545}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3588}}', 'T1059', 'Adversaries may abuse command and script interpreters to execute commands, scripts, or binaries. These interfaces and languages provide ways of interacting with computer systems and are a common feature across many different platforms. Most systems come with some built-in command-line interface and scripting capabilities, for example, macOS and Linux distributions include some flavor of [Unix Shell](https://attack.mitre.org/techniques/T1059/004) while Windows installations include the [Windows Command Shell](https://attack.mitre.org/techniques/T1059/003) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).
INSERT INTO public.attack_patterns VALUES (76, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.475597+00', NULL, 'attack-pattern--c726e0a2-a57a-4b7b-a973-d0f013246617', '[{"url": "https://attack.mitre.org/techniques/T1027/004", "external_id": "T1027.004", "source_name": "mitre-attack"}, {"url": "https://www.clearskysec.com/wp-content/uploads/2018/11/MuddyWater-Operations-in-Lebanon-and-Oman.pdf", "description": "ClearSky Cyber Security. (2018, November). MuddyWater Operations in Lebanon and Oman: Using an Israeli compromised domain for a two-stage campaign. Retrieved November 29, 2018.", "source_name": "ClearSky MuddyWater Nov 2018"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/windows-app-runs-on-mac-downloads-info-stealer-and-adware/", "description": "Trend Micro. (2019, February 11). Windows App Runs on Mac, Downloads Info Stealer and Adware. Retrieved April 25, 2019.", "source_name": "TrendMicro WindowsAppMac"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3850}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the execution file paths and command-line arguments for common compilers, such as csc.exe and GCC/MinGW, and correlate with other suspicious behavior to reduce false positives from normal user and administrator behavior. The compilation of payloads may also generate file creation and/or file write events. Look for non-native binary formats and cross-platform compiler and execution frameworks like Mono and determine if they have a legitimate purpose on the system.(Citation: TrendMicro WindowsAppMac) Typically these should only be used in specific and limited cases, like for software development.\"", "Str": "Monitor the execution file paths and command-line arguments for common compilers, such as csc.exe and GCC/MinGW, and correlate with other suspicious behavior to reduce false positives from normal user and administrator behavior. The compilation of payloads may also generate file creation and/or file write events. Look for non-native binary formats and cross-platform compiler and execution frameworks like Mono and determine if they have a legitimate purpose on the system.(Citation: TrendMicro WindowsAppMac) Typically these should only be used in specific and limited cases, like for software development.", "Type": 3, "Index": 2909}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3642}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\",\n      \"Ye Yint Min Thu Htut, Offensive Security Team, DBS Bank\"\n    ]", "Str": "", "Type": 5, "Index": 2433}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\",\n      \"File: File Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2554}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3553}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Signature-based detection\",\n      \"Host intrusion prevention systems\",\n      \"Anti-virus\",\n      \"Binary Analysis\",\n      \"Static File Analysis\"\n    ]", "Str": "", "Type": 5, "Index": 2723}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Compiler software (either native to the system or delivered by the adversary)\"\n    ]", "Str": "", "Type": 5, "Index": 3732}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3595}}', 'T1027.004', 'Adversaries may attempt to make payloads difficult to discover and analyze by delivering files to victims as uncompiled code. Text-based source code files may subvert analysis and scrutiny from protections targeting executables/binaries. These payloads will need to be compiled before execution; typically via native utilities such as csc.exe or GCC/MinGW.(Citation: ClearSky MuddyWater Nov 2018)
INSERT INTO public.attack_patterns VALUES (79, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.562411+00', NULL, 'attack-pattern--2f6b4ed7-fef1-44ba-bcb8-1b4beb610b64', '[{"url": "https://attack.mitre.org/techniques/T1559/001", "external_id": "T1559.001", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/06/hunting-com-objects.html", "description": "Hamilton, C. (2019, June 4). Hunting COM Objects. Retrieved June 10, 2019.", "source_name": "Fireeye Hunting COM June 2019"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms680573.aspx", "description": "Microsoft. (n.d.). Component Object Model (COM). Retrieved November 22, 2017.", "source_name": "Microsoft COM"}, {"url": "https://googleprojectzero.blogspot.com/2018/04/windows-exploitation-tricks-exploiting.html", "description": "Forshaw, J. (2018, April 18). Windows Exploitation Tricks: Exploiting Arbitrary File Writes for Local Elevation of Privilege. Retrieved May 3, 2018.", "source_name": "ProjectZero File Write EoP Apr 2018"}, {"url": "https://enigma0x3.net/2017/11/16/lateral-movement-using-outlooks-createobject-method-and-dotnettojscript/", "description": "Nelson, M. (2017, November 16). Lateral Movement using Outlook''s CreateObject Method and DotNetToJScript. Retrieved November 21, 2017.", "source_name": "Enigma Outlook DCOM Lateral Movement Nov 2017"}, {"url": "https://enigma0x3.net/2017/01/05/lateral-movement-using-the-mmc20-application-com-object/", "description": "Nelson, M. (2017, January 5). Lateral Movement using the MMC20 Application COM Object. Retrieved November 21, 2017.", "source_name": "Enigma MMC20 COM Jan 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4347}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for COM objects loading DLLs and other modules not typically associated with the application.(Citation: Enigma Outlook DCOM Lateral Movement Nov 2017) Enumeration of COM objects, via [Query Registry](https://attack.mitre.org/techniques/T1012) or [PowerShell](https://attack.mitre.org/techniques/T1059/001), may also proceed malicious use.(Citation: Fireeye Hunting COM June 2019)(Citation: Enigma MMC20 COM Jan 2017)\\n\\nMonitor for spawning of processes associated with COM objects, especially those invoked by a user different than the one currently logged on. \"", "Str": "Monitor for COM objects loading DLLs and other modules not typically associated with the application.(Citation: Enigma Outlook DCOM Lateral Movement Nov 2017) Enumeration of COM objects, via [Query Registry](https://attack.mitre.org/techniques/T1012) or [PowerShell](https://attack.mitre.org/techniques/T1059/001), may also proceed malicious use.(Citation: Fireeye Hunting COM June 2019)(Citation: Enigma MMC20 COM Jan 2017)\n\nMonitor for spawning of processes associated with COM objects, especially those invoked by a user different than the one currently logged on. ", "Type": 3, "Index": 3663}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4299}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3532}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4268}}', 'T1559.001', 'Adversaries may use the Windows Component Object Model (COM) for local code execution. COM is an inter-process communication (IPC) component of the native Windows application programming interface (API) that enables interaction between software objects, or executable code that implements one or more interfaces.(Citation: Fireeye Hunting COM June 2019) Through COM, a client object can call methods of server objects, which are typically binary Dynamic Link Libraries (DLL) or executables (EXE).(Citation: Microsoft COM)
INSERT INTO public.attack_patterns VALUES (515, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.934303+00', NULL, 'attack-pattern--59bd0dec-f8b2-4b9a-9141-37a1e6899761', '[{"url": "https://attack.mitre.org/techniques/T1535", "external_id": "T1535", "source_name": "mitre-attack"}, {"url": "https://blog.cloudsploit.com/the-danger-of-unused-aws-regions-af0bf1b878fc", "description": "CloudSploit. (2019, June 8). The Danger of Unused AWS Regions. Retrieved October 8, 2019.", "source_name": "CloudSploit - Unused AWS Regions"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2837}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor system logs to review activities occurring across all cloud environments and regions. Configure alerting to notify of activity in normally unused regions or if the number of instances active in a region goes above a certain threshold.(Citation: CloudSploit - Unused AWS Regions)\"", "Str": "Monitor system logs to review activities occurring across all cloud environments and regions. Configure alerting to notify of activity in normally unused regions or if the number of instances active in a region goes above a certain threshold.(Citation: CloudSploit - Unused AWS Regions)", "Type": 3, "Index": 2381}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 2792}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Netskope\"\n    ]", "Str": "", "Type": 5, "Index": 2257}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2311}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2702}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2745}}', 'T1535', 'Adversaries may create cloud instances in unused geographic service regions in order to evade detection. Access is usually obtained through compromising accounts used to manage cloud infrastructure.
INSERT INTO public.attack_patterns VALUES (80, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.595752+00', NULL, 'attack-pattern--bc0f5e80-91c0-4e04-9fbb-e4e332c85dae', '[{"url": "https://attack.mitre.org/techniques/T1546/015", "external_id": "T1546.015", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/ms694363.aspx", "description": "Microsoft. (n.d.). The Component Object Model. Retrieved August 18, 2016.", "source_name": "Microsoft Component Object Model"}, {"url": "https://blog.gdatasoftware.com/2014/10/23941-com-object-hijacking-the-discreet-way-of-persistence", "description": "G DATA. (2014, October). COM Object hijacking: the discreet way of persistence. Retrieved August 13, 2016.", "source_name": "GDATA COM Hijacking"}, {"url": "https://www.elastic.co/blog/how-hunt-detecting-persistence-evasion-com", "description": "Ewing, P. Strom, B. (2016, September 15). How to Hunt: Detecting Persistence & Evasion with the COM. Retrieved September 15, 2016.", "source_name": "Elastic COM Hijacking"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4464}, "x_mitre_detection": {"Num": 0, "Raw": "\"There are opportunities to detect COM hijacking by searching for Registry references that have been replaced and through Registry operations (ex: [Reg](https://attack.mitre.org/software/S0075)) replacing known binary paths with unknown paths or otherwise malicious content. Even though some third-party applications define user COM objects, the presence of objects within HKEY_CURRENT_USER\\\\Software\\\\Classes\\\\CLSID\\\\ may be anomalous and should be investigated since user objects will be loaded prior to machine objects in HKEY_LOCAL_MACHINE\\\\SOFTWARE\\\\Classes\\\\CLSID\\\\.(Citation: Elastic COM Hijacking) Registry entries for existing COM objects may change infrequently. When an entry with a known good path and binary is replaced or changed to an unusual value to point to an unknown binary in a new location, then it may indicate suspicious behavior and should be investigated.  \\n\\nLikewise, if software DLL loads are collected and analyzed, any unusual DLL load that can be correlated with a COM object Registry modification may indicate COM hijacking has been performed. \"", "Str": "There are opportunities to detect COM hijacking by searching for Registry references that have been replaced and through Registry operations (ex: [Reg](https://attack.mitre.org/software/S0075)) replacing known binary paths with unknown paths or otherwise malicious content. Even though some third-party applications define user COM objects, the presence of objects within HKEY_CURRENT_USER\\Software\\Classes\\CLSID\\ may be anomalous and should be investigated since user objects will be loaded prior to machine objects in HKEY_LOCAL_MACHINE\\SOFTWARE\\Classes\\CLSID\\.(Citation: Elastic COM Hijacking) Registry entries for existing COM objects may change infrequently. When an entry with a known good path and binary is replaced or changed to an unusual value to point to an unknown binary in a new location, then it may indicate suspicious behavior and should be investigated.  \n\nLikewise, if software DLL loads are collected and analyzed, any unusual DLL load that can be correlated with a COM object Registry modification may indicate COM hijacking has been performed. ", "Type": 3, "Index": 3215}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4416}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Elastic\"\n    ]", "Str": "", "Type": 5, "Index": 2968}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 3021}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4327}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4369}}', 'T1546.015', 'Adversaries may establish persistence by executing malicious content triggered by hijacked references to Component Object Model (COM) objects. COM is a system within Windows to enable interaction between software components through the operating system.(Citation: Microsoft Component Object Model)  References to various COM objects are stored in the Registry. 
INSERT INTO public.attack_patterns VALUES (85, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.791887+00', NULL, 'attack-pattern--191cc6af-1bb2-4344-ab5f-28e496638720', '[{"url": "https://attack.mitre.org/techniques/T1195/001", "external_id": "T1195.001", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.com/vinfo/dk/security/news/cybercrime-and-digital-threats/hacker-infects-node-js-package-to-steal-from-bitcoin-wallets", "description": "Trendmicro. (2018, November 29). Hacker Infects Node.js Package to Steal from Bitcoin Wallets. Retrieved April 10, 2019.", "source_name": "Trendmicro NPM Compromise"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2233}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use verification of distributed binaries through hash checking or other integrity checking mechanisms. Scan downloads for malicious signatures and attempt to test software and updates prior to deployment while taking note of potential suspicious activity. \"", "Str": "Use verification of distributed binaries through hash checking or other integrity checking mechanisms. Scan downloads for malicious signatures and attempt to test software and updates prior to deployment while taking note of potential suspicious activity. ", "Type": 3, "Index": 1833}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2155}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2124}}', 'T1195.001', 'Adversaries may manipulate software dependencies and development tools prior to receipt by a final consumer for the purpose of data or system compromise. Applications often depend on external software to function properly. Popular open source projects that are used as dependencies in many applications may be targeted as a means to add malicious code to users of the dependency. (Citation: Trendmicro NPM Compromise)  
INSERT INTO public.attack_patterns VALUES (88, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:29.896552+00', NULL, 'attack-pattern--f8ef3a62-3f44-40a4-abca-761ab235c436', '[{"url": "https://attack.mitre.org/techniques/T1552/007", "external_id": "T1552.007", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/engine/api/v1.41/", "description": "Docker. (n.d.). Docker Engine API v1.41 Reference. Retrieved March 31, 2021.", "source_name": "Docker API"}, {"url": "https://kubernetes.io/docs/concepts/overview/kubernetes-api/", "description": "The Kubernetes Authors. (n.d.). The Kubernetes API. Retrieved March 29, 2021.", "source_name": "Kubernetes API"}, {"url": "https://unit42.paloaltonetworks.com/attackers-tactics-and-techniques-in-unsecured-docker-daemons-revealed/", "description": "Chen, J.. (2020, January 29). Attacker''s Tactics and Techniques in Unsecured Docker Daemons Revealed. Retrieved March 31, 2021.", "source_name": "Unit 42 Unsecured Docker Daemons"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3389}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish centralized logging for the activity of container and Kubernetes cluster components. Monitor logs for actions that could be taken to gather credentials to container and cloud infrastructure, including the use of discovery API calls by new or unexpected users and APIs that access Docker logs.\\n\\nIt may be possible to detect adversary use of credentials they have obtained such as in [Valid Accounts](https://attack.mitre.org/techniques/T1078).\"", "Str": "Establish centralized logging for the activity of container and Kubernetes cluster components. Monitor logs for actions that could be taken to gather credentials to container and cloud infrastructure, including the use of discovery API calls by new or unexpected users and APIs that access Docker logs.\n\nIt may be possible to detect adversary use of credentials they have obtained such as in [Valid Accounts](https://attack.mitre.org/techniques/T1078).", "Type": 3, "Index": 2737}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3338}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Jay Chen, Palo Alto Networks\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 2412}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Access\",\n      \"User Account: User Account Authentication\"\n    ]", "Str": "", "Type": 5, "Index": 2590}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3226}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3268}}', 'T1552.007', 'Adversaries may gather credentials via APIs within a containers environment. APIs in these environments, such as the Docker API and Kubernetes APIs, allow a user to remotely manage their container resources and cluster components.(Citation: Docker API)(Citation: Kubernetes API)
INSERT INTO public.attack_patterns VALUES (90, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.016128+00', NULL, 'attack-pattern--1126cab1-c700-412f-a510-61f4937bb096', '[{"url": "https://attack.mitre.org/techniques/T1053/007", "external_id": "T1053.007", "source_name": "mitre-attack"}, {"url": "https://kubernetes.io/docs/concepts/workloads/controllers/job/", "description": "The Kubernetes Authors. (n.d.). Kubernetes Jobs. Retrieved March 30, 2021.", "source_name": "Kubernetes Jobs"}, {"url": "https://kubernetes.io/docs/concepts/workloads/controllers/cron-jobs/", "description": "The Kubernetes Authors. (n.d.). Kubernetes CronJob. Retrieved March 29, 2021.", "source_name": "Kubernetes CronJob"}, {"url": "https://www.microsoft.com/security/blog/2020/04/02/attack-matrix-kubernetes/", "description": "Weizman, Y. (2020, April 2). Threat Matrix for Kubernetes. Retrieved March 30, 2021.", "source_name": "Threat Matrix for Kubernetes"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3423}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for the anomalous creation of scheduled jobs in container orchestration environments. Use logging agents on Kubernetes nodes and retrieve logs from sidecar proxies for application and resource pods to monitor malicious container orchestration job deployments. \"", "Str": "Monitor for the anomalous creation of scheduled jobs in container orchestration environments. Use logging agents on Kubernetes nodes and retrieve logs from sidecar proxies for application and resource pods to monitor malicious container orchestration job deployments. ", "Type": 3, "Index": 2980}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3372}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Vishwas Manral, McAfee\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 2660}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Container: Container Creation\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2832}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3283}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3325}}', 'T1053.007', 'Adversaries may abuse task scheduling functionality provided by container orchestration tools such as Kubernetes to schedule deployment of containers configured to execute malicious code. Container orchestration jobs run these automated tasks at a specific date and time, similar to cron jobs on a Linux system. Deployments of this type can also be configured to maintain a quantity of containers over time, automating the process of maintaining persistence within a cluster.
INSERT INTO public.attack_patterns VALUES (92, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.086998+00', NULL, 'attack-pattern--4ff5d6a8-c062-4c68-a778-36fc5edd564f', '[{"url": "https://attack.mitre.org/techniques/T1218/002", "external_id": "T1218.002", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/cc144185.aspx", "description": "M. (n.d.). Implementing Control Panel Items. Retrieved January 18, 2018.", "source_name": "Microsoft Implementing CPL"}, {"url": "https://www.trendmicro.de/cloud-content/us/pdfs/security-intelligence/white-papers/wp-cpl-malware.pdf", "description": "Mercês, F. (2014, January 27). CPL Malware - Malicious Control Panel Items. Retrieved January 18, 2018.", "source_name": "TrendMicro CPL Malware Jan 2014"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/control-panel-files-used-as-malicious-attachments/", "description": "Bernardino, J. (2013, December 17). Control Panel Files Used As Malicious Attachments. Retrieved January 18, 2018.", "source_name": "TrendMicro CPL Malware Dec 2013"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/11/unit42-new-malware-with-ties-to-sunorcal-discovered/", "description": "Grunzweig, J. and Miller-Osborn, J. (2017, November 10). New Malware with Ties to SunOrcal Discovered. Retrieved November 16, 2017.", "source_name": "Palo Alto Reaver Nov 2017"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2020/06/ESET_InvisiMole.pdf", "description": "Hromcova, Z. and Cherpanov, A. (2020, June). INVISIMOLE: THE HIDDEN PART OF THE STORY. Retrieved July 16, 2020.", "source_name": "ESET InvisiMole June 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 7681}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and analyze activity related to items associated with CPL files, such as the control.exe and the <code>Control_RunDLL</code> and <code>ControlRunDLLAsUser</code> API functions in shell32.dll. When executed from the command line or clicked, control.exe will execute the CPL file (ex: <code>control.exe file.cpl</code>) before [Rundll32](https://attack.mitre.org/techniques/T1218/011) is used to call the CPL''s API functions (ex: <code>rundll32.exe shell32.dll,Control_RunDLL file.cpl</code>). CPL files can be executed directly via the CPL API function with just the latter [Rundll32](https://attack.mitre.org/techniques/T1218/011) command, which may bypass detections and/or execution filters for control.exe.(Citation: TrendMicro CPL Malware Jan 2014)\\n\\nInventory Control Panel items to locate unregistered and potentially malicious files present on systems:\\n\\n* Executable format registered Control Panel items will have a globally unique identifier (GUID) and registration Registry entries in <code>HKEY_LOCAL_MACHINE\\\\SOFTWARE\\\\Microsoft\\\\Windows\\\\CurrentVersion\\\\Explorer\\\\ControlPanel\\\\NameSpace</code> and <code>HKEY_CLASSES_ROOT\\\\CLSID\\\\{GUID}</code>. These entries may contain information about the Control Panel item such as its display name, path to the local file, and the command executed when opened in the Control Panel. (Citation: Microsoft Implementing CPL)\\n* CPL format registered Control Panel items stored in the System32 directory are automatically shown in the Control Panel. Other Control Panel items will have registration entries in the <code>CPLs</code> and <code>Extended Properties</code> Registry keys of <code>HKEY_LOCAL_MACHINE or HKEY_CURRENT_USER\\\\Software\\\\Microsoft\\\\Windows\\\\CurrentVersion\\\\Control Panel</code>. These entries may include information such as a GUID, path to the local file, and a canonical name used to launch the file programmatically (<code> WinExec(\\\"c:\\\\windows\\\\system32\\\\control.exe {Canonical_Name}\\\", SW_NORMAL);</code>) or from a command line (<code>control.exe /name {Canonical_Name}</code>).(Citation: Microsoft Implementing CPL)\\n* Some Control Panel items are extensible via Shell extensions registered in <code>HKEY_LOCAL_MACHINE\\\\Software\\\\Microsoft\\\\Windows\\\\CurrentVersion\\\\Controls Folder\\\\{name}\\\\Shellex\\\\PropertySheetHandlers</code> where {name} is the predefined name of the system item.(Citation: Microsoft Implementing CPL)\\n\\nAnalyze new Control Panel items as well as those present on disk for malicious content. Both executable and CPL formats are compliant Portable Executable (PE) images and can be examined using traditional tools and methods, pending anti-reverse-engineering techniques.(Citation: TrendMicro CPL Malware Jan 2014)\"", "Str": "Monitor and analyze activity related to items associated with CPL files, such as the control.exe and the <code>Control_RunDLL</code> and <code>ControlRunDLLAsUser</code> API functions in shell32.dll. When executed from the command line or clicked, control.exe will execute the CPL file (ex: <code>control.exe file.cpl</code>) before [Rundll32](https://attack.mitre.org/techniques/T1218/011) is used to call the CPL''s API functions (ex: <code>rundll32.exe shell32.dll,Control_RunDLL file.cpl</code>). CPL files can be executed directly via the CPL API function with just the latter [Rundll32](https://attack.mitre.org/techniques/T1218/011) command, which may bypass detections and/or execution filters for control.exe.(Citation: TrendMicro CPL Malware Jan 2014)\n\nInventory Control Panel items to locate unregistered and potentially malicious files present on systems:\n\n* Executable format registered Control Panel items will have a globally unique identifier (GUID) and registration Registry entries in <code>HKEY_LOCAL_MACHINE\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Explorer\\ControlPanel\\NameSpace</code> and <code>HKEY_CLASSES_ROOT\\CLSID\\{GUID}</code>. These entries may contain information about the Control Panel item such as its display name, path to the local file, and the command executed when opened in the Control Panel. (Citation: Microsoft Implementing CPL)\n* CPL format registered Control Panel items stored in the System32 directory are automatically shown in the Control Panel. Other Control Panel items will have registration entries in the <code>CPLs</code> and <code>Extended Properties</code> Registry keys of <code>HKEY_LOCAL_MACHINE or HKEY_CURRENT_USER\\Software\\Microsoft\\Windows\\CurrentVersion\\Control Panel</code>. These entries may include information such as a GUID, path to the local file, and a canonical name used to launch the file programmatically (<code> WinExec(\"c:\\windows\\system32\\control.exe {Canonical_Name}\", SW_NORMAL);</code>) or from a command line (<code>control.exe /name {Canonical_Name}</code>).(Citation: Microsoft Implementing CPL)\n* Some Control Panel items are extensible via Shell extensions registered in <code>HKEY_LOCAL_MACHINE\\Software\\Microsoft\\Windows\\CurrentVersion\\Controls Folder\\{name}\\Shellex\\PropertySheetHandlers</code> where {name} is the predefined name of the system item.(Citation: Microsoft Implementing CPL)\n\nAnalyze new Control Panel items as well as those present on disk for malicious content. Both executable and CPL formats are compliant Portable Executable (PE) images and can be examined using traditional tools and methods, pending anti-reverse-engineering techniques.(Citation: TrendMicro CPL Malware Jan 2014)", "Type": 3, "Index": 4745}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 7633}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ESET\"\n    ]", "Str": "", "Type": 5, "Index": 4368}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Module: Module Load\",\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 4418}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 7505}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 4683}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 7547}}', 'T1218.002', 'Adversaries may abuse control.exe to proxy execution of malicious payloads. The Windows Control Panel process binary (control.exe) handles execution of Control Panel items, which are utilities that allow users to view and adjust computer settings.
INSERT INTO public.attack_patterns VALUES (95, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.237281+00', NULL, 'attack-pattern--677569f9-a8b0-459e-ab24-7f18091fa7bf', '[{"url": "https://attack.mitre.org/techniques/T1134/002", "external_id": "T1134.002", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/windows-server-docs/identity/ad-ds/manage/component-updates/command-line-process-auditing", "description": "Mathers, B. (2017, March 7). Command line process auditing. Retrieved April 21, 2017.", "source_name": "Microsoft Command-line Logging"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2957}, "x_mitre_detection": {"Num": 0, "Raw": "\"If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\\n\\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior.\\n\\nAnalysts can also monitor for use of Windows APIs such as <code>DuplicateToken(Ex)</code> and <code>CreateProcessWithTokenW</code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.\"", "Str": "If an adversary is using a standard command-line shell, analysts can detect token manipulation by auditing command-line activity. Specifically, analysts should look for use of the <code>runas</code> command. Detailed command-line logging is not enabled by default in Windows.(Citation: Microsoft Command-line Logging)\n\nIf an adversary is using a payload that calls the Windows token APIs directly, analysts can detect token manipulation only through careful analysis of user network activity, examination of running processes, and correlation with other endpoint and network behavior.\n\nAnalysts can also monitor for use of Windows APIs such as <code>DuplicateToken(Ex)</code> and <code>CreateProcessWithTokenW</code> and correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.", "Type": 3, "Index": 1977}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2909}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1726}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2878}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Windows User Account Control\",\n      \"System access controls\",\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 1837}}', 'T1134.002', 'Adversaries may create a new process with a duplicated token to escalate privileges and bypass access controls. An adversary can duplicate a desired access token with <code>DuplicateToken(Ex)</code> and use it with <code>CreateProcessWithTokenW</code> to create a new process running under the security context of the impersonated user. This is useful for creating a new process under the security context of a different user.', 'Create Process with Token', 5);
INSERT INTO public.attack_patterns VALUES (99, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.388172+00', NULL, 'attack-pattern--b2d03cea-aec1-45ca-9744-9ee583c1e1cc', '[{"url": "https://attack.mitre.org/techniques/T1110/004", "external_id": "T1110.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/600.html", "external_id": "CAPEC-600", "source_name": "capec"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-086A", "description": "US-CERT. (2018, March 27). TA18-068A Brute Force Attacks Conducted by Cyber Actors. Retrieved October 2, 2019.", "source_name": "US-CERT TA18-068A 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3453}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials.\"", "Str": "Monitor authentication logs for system and application login failures of [Valid Accounts](https://attack.mitre.org/techniques/T1078). If authentication failures are high, then there may be a brute force attempt to gain access to a system using legitimate credentials.", "Type": 3, "Index": 2872}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3263}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Diogo Fernandes\",\n      \"Anastasios Pingios\"\n    ]", "Str": "", "Type": 5, "Index": 2649}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2738}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3174}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3216}}', 'T1110.004', 'Adversaries may use credentials obtained from breach dumps of unrelated accounts to gain access to target accounts through credential overlap. Occasionally, large numbers of username and password pairs are dumped online when a website or service is compromised and the user account credentials accessed. The information may be useful to an adversary attempting to compromise accounts by taking advantage of the tendency for users to use the same passwords across personal and business accounts.
INSERT INTO public.attack_patterns VALUES (100, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.425457+00', NULL, 'attack-pattern--bc76d0a4-db11-4551-9ac4-01a469cfb161', '[{"url": "https://attack.mitre.org/techniques/T1589/001", "external_id": "T1589.001", "source_name": "mitre-attack"}, {"url": "https://cybersecurity.att.com/blogs/labs-research/scanbox-a-reconnaissance-framework-used-on-watering-hole-attacks", "description": "Blasco, J. (2014, August 28). Scanbox: A Reconnaissance Framework Used with Watering Hole Attacks. Retrieved October 19, 2020.", "source_name": "ATT ScanBox"}, {"url": "https://www.theregister.com/2017/09/26/deloitte_leak_github_and_google/", "description": "Thomson, I. (2017, September 26). Deloitte is a sitting duck: Key systems with RDP open, VPN and proxy ''login details leaked''. Retrieved October 19, 2020.", "source_name": "Register Deloitte"}, {"url": "https://www.theregister.com/2015/02/28/uber_subpoenas_github_for_hacker_details/", "description": "McCarthy, K. (2015, February 28). FORK ME! Uber hauls GitHub into court to find who hacked database of 50,000 drivers. Retrieved October 19, 2020.", "source_name": "Register Uber"}, {"url": "https://labs.detectify.com/2016/04/28/slack-bot-token-leakage-exposing-business-critical-information/", "description": "Detectify. (2016, April 28). Slack bot token leakage exposing business critical information. Retrieved October 19, 2020.", "source_name": "Detectify Slack Tokens"}, {"url": "https://www.forbes.com/sites/runasandvik/2014/01/14/attackers-scrape-github-for-cloud-service-credentials-hijack-account-to-mine-virtual-currency/#242c479d3196", "description": "Sandvik, R. (2014, January 14). Attackers Scrape GitHub For Cloud Service Credentials, Hijack Account To Mine Virtual Currency. Retrieved October 19, 2020.", "source_name": "Forbes GitHub Creds"}, {"url": "https://github.com/dxa4481/truffleHog", "description": "Dylan Ayrey. (2016, December 31). truffleHog. Retrieved October 19, 2020.", "source_name": "GitHub truffleHog"}, {"url": "https://github.com/michenriksen/gitrob", "description": "Michael Henriksen. (2018, June 9). Gitrob: Putting the Open Source in OSINT. Retrieved October 19, 2020.", "source_name": "GitHub Gitrob"}, {"url": "https://www.cnet.com/news/massive-breach-leaks-773-million-emails-21-million-passwords/", "description": "Ng, A. (2019, January 17). Massive breach leaks 773 million email addresses, 21 million passwords. Retrieved October 20, 2020.", "source_name": "CNET Leaks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5572}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 5131}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 5528}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vinayak Wadhwa, Lucideus\",\n      \"Lee Christensen, SpecterOps\",\n      \"Toby Kohlenberg\"\n    ]", "Str": "", "Type": 5, "Index": 5002}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5497}}', 'T1589.001', 'Adversaries may gather credentials that can be used during targeting. Account credentials gathered by adversaries may be those directly associated with the target victim organization or attempt to take advantage of the tendency for users to use the same passwords across personal and business accounts.
INSERT INTO public.attack_patterns VALUES (102, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.507269+00', NULL, 'attack-pattern--3fc9b85a-2862-4363-a64d-d692e3ffbee0', '[{"url": "https://attack.mitre.org/techniques/T1555", "external_id": "T1555", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2053}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor system calls, file read events, and processes for suspicious activity that could indicate searching for a password  or other activity related to performing keyword searches (e.g. password, pwd, login, store, secure, credentials, etc.) in process memory for credentials. File read events should be monitored surrounding known password storage applications.\"", "Str": "Monitor system calls, file read events, and processes for suspicious activity that could indicate searching for a password  or other activity related to performing keyword searches (e.g. password, pwd, login, store, secure, credentials, etc.) in process memory for credentials. File read events should be monitored surrounding known password storage applications.", "Type": 3, "Index": 1478}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1975}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Access\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 1279}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 1876}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 1919}}', 'T1555', 'Adversaries may search for common password storage locations to obtain user credentials. Passwords are stored in several places on a system, depending on the operating system or application holding the credentials. There are also specific applications that store passwords to make it easier for users manage and maintain. Once credentials are obtained, they can be used to perform lateral movement and access restricted information.', 'Credentials from Password Stores', 0);
INSERT INTO public.attack_patterns VALUES (104, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.591485+00', NULL, 'attack-pattern--341e222a-a6e3-4f6f-b69c-831d792b1580', '[{"url": "https://attack.mitre.org/techniques/T1552/002", "external_id": "T1552.002", "source_name": "mitre-attack"}, {"url": "https://pentestlab.blog/2017/04/19/stored-credentials/", "description": "netbiosX. (2017, April 19). Stored Credentials. Retrieved April 6, 2018.", "source_name": "Pentestlab Stored Credentials"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2728}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for applications that can be used to query the Registry, such as [Reg](https://attack.mitre.org/software/S0075), and collect command parameters that may indicate credentials are being searched. Correlate activity with related suspicious behavior that may indicate an active intrusion to reduce false positives.\"", "Str": "Monitor processes for applications that can be used to query the Registry, such as [Reg](https://attack.mitre.org/software/S0075), and collect command parameters that may indicate credentials are being searched. Correlate activity with related suspicious behavior that may indicate an active intrusion to reduce false positives.", "Type": 3, "Index": 1990}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2465}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Sudhanshu Chauhan, @Sudhanshu_C\"\n    ]", "Str": "", "Type": 5, "Index": 1754}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Access\"\n    ]", "Str": "", "Type": 5, "Index": 1831}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2353}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Ability to query some Registry locations depends on the adversary''s level of access. User permissions are usually limited to access of user-related Registry keys.\"\n    ]", "Str": "", "Type": 5, "Index": 2525}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2395}}', 'T1552.002', 'Adversaries may search the Registry on compromised systems for insecurely stored credentials. The Windows Registry stores configuration information that can be used by the system or other programs. Adversaries may query the Registry looking for credentials and passwords that have been stored for use by other programs or services. Sometimes these credentials are used for automatic logons.
INSERT INTO public.attack_patterns VALUES (105, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.623076+00', NULL, 'attack-pattern--2acf44aa-542f-4366-b4eb-55ef5747759c', '[{"url": "https://attack.mitre.org/techniques/T1053/003", "external_id": "T1053.003", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2916}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc.  \\n\\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement. \"", "Str": "Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc.  \n\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement. ", "Type": 3, "Index": 1977}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2818}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1793}, "x_mitre_remote_support": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2886}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2729}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2771}}', 'T1053.003', 'Adversaries may abuse the <code>cron</code> utility to perform task scheduling for initial or recurring execution of malicious code. The <code>cron</code> utility is a time-based job scheduler for Unix-like operating systems.  The <code> crontab</code> file contains the schedule of cron entries to be run and the specified times for execution. Any <code>crontab</code> files are stored in operating system-specific file paths.
INSERT INTO public.attack_patterns VALUES (108, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.736625+00', NULL, 'attack-pattern--e64c62cf-9cd7-4a14-94ec-cdaac43ab44b', '[{"url": "https://attack.mitre.org/techniques/T1574/002", "external_id": "T1574.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/641.html", "external_id": "CAPEC-641", "source_name": "capec"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/rpt-dll-sideloading.pdf", "description": "Amanda Steward. (2014). FireEye DLL Side-Loading: A Thorn in the Side of the Anti-Virus Industry. Retrieved March 13, 2020.", "source_name": "FireEye DLL Side-Loading"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3342}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes for unusual activity (e.g., a process that does not use the network begins to do so) as well as the introduction of new files/programs. Track DLL metadata, such as a hash, and compare DLLs that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.\"", "Str": "Monitor processes for unusual activity (e.g., a process that does not use the network begins to do so) as well as the introduction of new files/programs. Track DLL metadata, such as a hash, and compare DLLs that are loaded at process execution time against previous executions to detect differences that do not correlate with patching or updates.", "Type": 3, "Index": 2882}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3294}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2634}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3263}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2800}}', 'T1574.002', 'Adversaries may execute their own malicious payloads by side-loading DLLs. Similar to [DLL Search Order Hijacking](https://attack.mitre.org/techniques/T1574/001), side-loading involves hijacking which DLL a program loads. But rather than just planting the DLL within the search order of a program then waiting for the victim application to be invoked, adversaries may directly side-load their payloads by planting then invoking a legitimate application that executes their payload(s).
INSERT INTO public.attack_patterns VALUES (109, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.813896+00', NULL, 'attack-pattern--1996eef1-ced3-4d7f-bf94-33298cabbf72', '[{"url": "https://attack.mitre.org/techniques/T1071/004", "external_id": "T1071.004", "source_name": "mitre-attack"}, {"url": "https://www.paloaltonetworks.com/cyberpedia/what-is-dns-tunneling", "description": "Palo Alto Networks. (n.d.). What Is DNS Tunneling?. Retrieved March 15, 2020.", "source_name": "PAN DNS Tunneling"}, {"url": "https://medium.com/@galolbardes/learn-how-easy-is-to-bypass-firewalls-using-dns-tunneling-and-also-how-to-block-it-3ed652f4a000", "description": "Galobardes, R. (2018, October 30). Learn how easy is to bypass firewalls using DNS tunneling (and also how to block it). Retrieved March 15, 2020.", "source_name": "Medium DnsTunneling"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3498}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\\n\\nMonitor for DNS traffic to/from known-bad or suspicious domains.\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\n\nMonitor for DNS traffic to/from known-bad or suspicious domains.", "Type": 3, "Index": 2787}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3420}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jan Petrov, Citi\"\n    ]", "Str": "", "Type": 5, "Index": 2595}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2657}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3389}}', 'T1071.004', 'Adversaries may communicate using the Domain Name System (DNS) application layer protocol to avoid detection/network filtering by blending in with existing traffic. Commands to the remote system, and often the results of those commands, will be embedded within the protocol traffic between the client and server. 
INSERT INTO public.attack_patterns VALUES (111, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.895036+00', NULL, 'attack-pattern--83a766f8-1501-4b3a-a2de-2e2849e8dfc1', '[{"url": "https://attack.mitre.org/techniques/T1568/003", "external_id": "T1568.003", "source_name": "mitre-attack"}, {"url": "http://www.crowdstrike.com/blog/whois-numbered-panda/", "description": "Meyers, A. (2013, March 29). Whois Numbered Panda. Retrieved January 14, 2016.", "source_name": "Meyers Numbered Panda"}, {"url": "https://www.fireeye.com/blog/threat-research/2014/09/darwins-favorite-apt-group-2.html", "description": "Moran, N., Oppenheim, M., Engle, S., & Wartell, R.. (2014, September 3). Darwin’s Favorite APT Group &#91;Blog&#93;. Retrieved November 12, 2014.", "source_name": "Moran 2014"}, {"url": "https://blog.rapid7.com/2013/08/26/upcoming-g20-summit-fuels-espionage-operations/", "description": "Rapid7. (2013, August 26). Upcoming G20 Summit Fuels Espionage Operations. Retrieved March 6, 2017.", "source_name": "Rapid7G20Espionage"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2827}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection for this technique is difficult because it would require knowledge of the specific implementation of the port calculation algorithm. Detection may be possible by analyzing DNS records if the algorithm is known.\"", "Str": "Detection for this technique is difficult because it would require knowledge of the specific implementation of the port calculation algorithm. Detection may be possible by analyzing DNS records if the algorithm is known.", "Type": 3, "Index": 2463}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2749}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2380}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2718}}', 'T1568.003', 'Adversaries may perform calculations on addresses returned in DNS results to determine which port and IP address to use for command and control, rather than relying on a predetermined port number or the actual returned IP address. A IP and/or port number calculation can be used to bypass egress filtering on a C2 channel.(Citation: Meyers Numbered Panda)
INSERT INTO public.attack_patterns VALUES (114, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:30.963825+00', NULL, 'attack-pattern--17fd695c-b88c-455a-a3d1-43b6cb728532', '[{"url": "https://attack.mitre.org/techniques/T1596/001", "external_id": "T1596.001", "source_name": "mitre-attack"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2991}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2550}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2947}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2916}}', 'T1596.001', 'Adversaries may search DNS data for information about victims that can be used during targeting. DNS information may include a variety of details, including registered name servers as well as records that outline addressing for a target’s subdomains, mail servers, and other hosts.
INSERT INTO public.attack_patterns VALUES (118, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.16251+00', NULL, 'attack-pattern--ac9e6b22-11bf-45d7-9181-c1cb08360931', '[{"url": "https://attack.mitre.org/techniques/T1565", "external_id": "T1565", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2323}, "x_mitre_detection": {"Num": 0, "Raw": "\"Where applicable, inspect important file hashes, locations, and modifications for suspicious/unexpected values. With some critical processes involving transmission of data, manual or out-of-band integrity checking may be useful for identifying manipulated data.\"", "Str": "Where applicable, inspect important file hashes, locations, and modifications for suspicious/unexpected values. With some critical processes involving transmission of data, manual or out-of-band integrity checking may be useful for identifying manipulated data.", "Type": 3, "Index": 1752}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2245}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 2044}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\",\n      \"Process: OS API Execution\",\n      \"File: File Creation\",\n      \"File: File Deletion\",\n      \"File: File Modification\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 1467}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2102}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2145}}', 'T1565', 'Adversaries may insert, delete, or manipulate data in order to manipulate external outcomes or hide activity. By manipulating data, adversaries may attempt to affect a business process, organizational understanding, or decision making.
INSERT INTO public.attack_patterns VALUES (122, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.358783+00', NULL, 'attack-pattern--3298ce88-1628-43b1-87d9-0b5336b193d7', '[{"url": "https://attack.mitre.org/techniques/T1530", "external_id": "T1530", "source_name": "mitre-attack"}, {"url": "https://aws.amazon.com/premiumsupport/knowledge-center/secure-s3-resources/", "description": "Amazon. (2019, May 17). How can I secure the files in my Amazon S3 bucket?. Retrieved October 4, 2019.", "source_name": "Amazon S3 Security, 2019"}, {"url": "https://docs.microsoft.com/en-us/azure/storage/common/storage-security-guide", "description": "Amlekar, M., Brooks, C., Claman, L., et. al.. (2019, March 20). Azure Storage security guide. Retrieved October 4, 2019.", "source_name": "Microsoft Azure Storage Security, 2019"}, {"url": "https://cloud.google.com/storage/docs/best-practices", "description": "Google. (2019, September 16). Best practices for Cloud Storage. Retrieved October 4, 2019.", "source_name": "Google Cloud Storage Best Practices, 2019"}, {"url": "https://www.trendmicro.com/vinfo/us/security/news/virtualization-and-cloud/a-misconfigured-amazon-s3-exposed-almost-50-thousand-pii-in-australia", "description": "Trend Micro. (2017, November 6). A Misconfigured Amazon S3 Exposed Almost 50 Thousand PII in Australia. Retrieved October 4, 2019.", "source_name": "Trend Micro S3 Exposed PII, 2017"}, {"url": "https://www.wired.com/story/magecart-amazon-cloud-hacks/", "description": "Barrett, B.. (2019, July 11). Hack Brief: A Card-Skimming Hacker Group Hit 17K Domains—and Counting. Retrieved October 4, 2019.", "source_name": "Wired Magecart S3 Buckets, 2019"}, {"url": "https://www.hipaajournal.com/47gb-medical-records-unsecured-amazon-s3-bucket/", "description": "HIPAA Journal. (2017, October 11). 47GB of Medical Records and Test Results Found in Unsecured Amazon S3 Bucket. Retrieved October 4, 2019.", "source_name": "HIPAA Journal S3 Breach, 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4800}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unusual queries to the cloud provider''s storage service. Activity originating from unexpected sources may indicate improper permissions are set that is allowing access to data. Additionally, detecting failed attempts by a user for a certain object, followed by escalation of privileges by the same user, and access to the same object may be an indication of suspicious activity.\"", "Str": "Monitor for unusual queries to the cloud provider''s storage service. Activity originating from unexpected sources may indicate improper permissions are set that is allowing access to data. Additionally, detecting failed attempts by a user for a certain object, followed by escalation of privileges by the same user, and access to the same object may be an indication of suspicious activity.", "Type": 3, "Index": 4240}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 4755}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Netskope\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 4088}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Cloud Storage: Cloud Storage Access\"\n    ]", "Str": "", "Type": 5, "Index": 4162}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4665}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4708}}', 'T1530', 'Adversaries may access data objects from improperly secured cloud storage.
INSERT INTO public.attack_patterns VALUES (123, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.394366+00', NULL, 'attack-pattern--0ad7bc5c-235a-4048-944b-3b286676cb74', '[{"url": "https://attack.mitre.org/techniques/T1602", "external_id": "T1602", "source_name": "mitre-attack"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-106A", "description": "US-CERT. (2018, April 20). Alert (TA18-106A) Russian State-Sponsored Cyber Actors Targeting Network Infrastructure Devices. Retrieved October 19, 2020.", "source_name": "US-CERT-TA18-106A"}, {"url": "https://us-cert.cisa.gov/ncas/alerts/TA17-156A", "description": "US-CERT. (2017, June 5). Reducing the Risk of SNMP Abuse. Retrieved October 19, 2020.", "source_name": "US-CERT TA17-156A SNMP Abuse 2017"}, {"url": "https://tools.cisco.com/security/center/content/CiscoAppliedMitigationBulletin/cisco-amb-20080610-SNMPv3", "description": "Cisco. (2008, June 10). Identifying and Mitigating Exploitation of the SNMP Version 3 Authentication Vulnerabilities. Retrieved October 19, 2020.", "source_name": "Cisco Advisory SNMP v3 Authentication Vulnerabilities"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3010}, "x_mitre_detection": {"Num": 0, "Raw": "\"Identify network traffic sent or received by untrusted hosts or networks that solicits and obtains the configuration information of the queried device.(Citation: Cisco Advisory SNMP v3 Authentication Vulnerabilities)\"", "Str": "Identify network traffic sent or received by untrusted hosts or networks that solicits and obtains the configuration information of the queried device.(Citation: Cisco Advisory SNMP v3 Authentication Vulnerabilities)", "Type": 3, "Index": 2612}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2962}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2475}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2863}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2906}}', 'T1602', 'Adversaries may collect data related to managed devices from configuration repositories. Configuration repositories are used by management systems in order to configure, manage, and control data on remote systems. Configuration repositories may also facilitate remote access and administration of devices.
INSERT INTO public.attack_patterns VALUES (126, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.571526+00', NULL, 'attack-pattern--ae676644-d2d2-41b7-af7e-9bed1b55898c', '[{"url": "https://attack.mitre.org/techniques/T1039", "external_id": "T1039", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/639.html", "external_id": "CAPEC-639", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2257}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for actions that could be taken to collect files from a network share. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "Monitor processes and command-line arguments for actions that could be taken to collect files from a network share. Remote access tools with built-in features may interact directly with the Windows API to gather data. Data may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1582}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2085}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Network Share: Network Share Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1441}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2053}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Privileges to access network shared drive\"\n    ]", "Str": "", "Type": 5, "Index": 2175}}', 'T1039', 'Adversaries may search network shares on computers they have compromised to find files of interest. Sensitive data can be collected from remote systems via shared network drives (host shared directory, network file server, etc.) that are accessible from the current system prior to Exfiltration. Interactive command shells may be in use, and common functionality within [cmd](https://attack.mitre.org/software/S0106) may be used to gather information.', 'Data from Network Shared Drive', 0);
INSERT INTO public.attack_patterns VALUES (133, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.783326+00', NULL, 'attack-pattern--56e0d8b8-3e25-49dd-9050-3aa252f5aa92', '[{"url": "https://attack.mitre.org/techniques/T1610", "external_id": "T1610", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/engine/api/v1.41/#tag/Container", "description": "Docker. (n.d.). Docker Engine API v1.41 Reference - Container. Retrieved March 29, 2021.", "source_name": "Docker Containers API"}, {"url": "https://kubernetes.io/docs/tasks/access-application-cluster/web-ui-dashboard/", "description": "The Kubernetes Authors. (n.d.). Kubernetes Web UI (Dashboard). Retrieved March 29, 2021.", "source_name": "Kubernetes Dashboard"}, {"url": "https://www.kubeflow.org/docs/components/pipelines/overview/pipelines-overview/", "description": "The Kubeflow Authors. (n.d.). Overview of Kubeflow Pipelines. Retrieved March 29, 2021.", "source_name": "Kubeflow Pipelines"}, {"url": "https://blog.aquasec.com/malicious-container-image-docker-container-host", "description": "Assaf Morag. (2020, July 15). Threat Alert: Attackers Building Malicious Images on Your Hosts. Retrieved March 29, 2021.", "source_name": "Aqua Build Images on Hosts"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4248}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious or unknown container images and pods in your environment. Deploy logging agents on Kubernetes nodes and retrieve logs from sidecar proxies for application pods to detect malicious activity at the cluster level. In Docker, the daemon log provides insight into remote API calls, including those that deploy containers. Logs for management services or applications used to deploy containers other than the native technologies themselves should also be monitored.\"", "Str": "Monitor for suspicious or unknown container images and pods in your environment. Deploy logging agents on Kubernetes nodes and retrieve logs from sidecar proxies for application pods to detect malicious activity at the cluster level. In Docker, the daemon log provides insight into remote API calls, including those that deploy containers. Logs for management services or applications used to deploy containers other than the native technologies themselves should also be monitored.", "Type": 3, "Index": 3540}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4161}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Pawan Kinger, @kingerpawan, Trend Micro\",\n      \"Alfredo Oliveira, Trend Micro\",\n      \"Idan Frimark, Cisco\",\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Ariel Shuper, Cisco\",\n      \"Vishwas Manral, McAfee\",\n      \"Yossi Weizman, Azure Defender Research Team\"\n    ]", "Str": "", "Type": 5, "Index": 2959}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Container: Container Creation\",\n      \"Container: Container Start\",\n      \"Pod: Pod Creation\",\n      \"Pod: Pod Modification\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3324}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4219}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4057}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4100}}', 'T1610', 'Adversaries may deploy a container into an environment to facilitate execution or evade defenses. In some cases, adversaries may deploy a new container to execute processes associated with a particular image or deployment, such as processes that execute or download malware. In others, an adversary may deploy a new container configured without network rules, user limitations, etc. to bypass existing defenses within the environment.
INSERT INTO public.attack_patterns VALUES (135, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.893607+00', NULL, 'attack-pattern--edadea33-549c-4ed1-9783-8f5a5853cbdf', '[{"url": "https://attack.mitre.org/techniques/T1587", "external_id": "T1587", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}, {"url": "https://securelist.com/sofacy-apt-hits-high-profile-targets-with-updated-toolset/72924/", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, December 4). Sofacy APT hits high profile targets with updated toolset. Retrieved December 10, 2015.", "source_name": "Kaspersky Sofacy"}, {"url": "https://www.bitdefender.com/files/News/CaseStudies/study/353/Bitdefender-Whitepaper-StrongPity-APT.pdf", "description": "Tudorica, R. et al. (2020, June 30). StrongPity APT - Revealing Trojanized Tools, Working Hours and Infrastructure. Retrieved July 20, 2020.", "source_name": "Bitdefender StrongPity June 2020"}, {"url": "https://blog.talosintelligence.com/2020/06/promethium-extends-with-strongpity3.html", "description": "Mercer, W. et al. (2020, June 29). PROMETHIUM extends global reach with StrongPity3 APT. Retrieved July 20, 2020.", "source_name": "Talos Promethium June 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3512}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Defense Evasion or Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Defense Evasion or Command and Control.", "Type": 3, "Index": 3134}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3468}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3436}}', 'T1587', 'Adversaries may build capabilities that can be used during targeting. Rather than purchasing, freely downloading, or stealing capabilities, adversaries may develop their own capabilities in-house. This is the process of identifying development requirements and building solutions such as malware, exploits, and self-signed certificates. Adversaries may develop capabilities to support their operations throughout numerous phases of the adversary lifecycle.(Citation: Mandiant APT1)(Citation: Kaspersky Sofacy)(Citation: Bitdefender StrongPity June 2020)(Citation: Talos Promethium June 2020)
INSERT INTO public.attack_patterns VALUES (192, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.307369+00', NULL, 'attack-pattern--613d08bc-e8f4-4791-80b0-c8b974340dfd', '[{"url": "https://attack.mitre.org/techniques/T1011/001", "external_id": "T1011.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2188}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for processes utilizing the network that do not normally have network communication or have never been seen before. Processes that normally require user-driven events to access the network (for example, a web browser opening with a mouse click or key press) but access the network without such may be malicious.\\n\\nMonitor for and investigate changes to host adapter settings, such as addition and/or replication of communication interfaces.\"", "Str": "Monitor for processes utilizing the network that do not normally have network communication or have never been seen before. Processes that normally require user-driven events to access the network (for example, a web browser opening with a mouse click or key press) but access the network without such may be malicious.\n\nMonitor for and investigate changes to host adapter settings, such as addition and/or replication of communication interfaces.", "Type": 3, "Index": 1595}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2110}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1348}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2079}}', 'T1011.001', 'Adversaries may attempt to exfiltrate data over Bluetooth rather than the command and control channel. If the command and control network is a wired Internet connection, an attacker may opt to exfiltrate data using a Bluetooth communication channel.
INSERT INTO public.attack_patterns VALUES (137, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:31.973024+00', NULL, 'attack-pattern--19401639-28d0-4c3c-adcc-bc2ba22f6421', '[{"url": "https://attack.mitre.org/techniques/T1588/004", "external_id": "T1588.004", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/final-report-diginotar-hack-shows-total-compromise-ca-servers-103112/77170/", "description": "Fisher, D. (2012, October 31). Final Report on DigiNotar Hack Shows Total Compromise of CA Servers. Retrieved March 6, 2017.", "source_name": "DiginotarCompromise"}, {"url": "https://letsencrypt.org/docs/faq/", "description": "Let''s Encrypt. (2020, April 23). Let''s Encrypt FAQ. Retrieved October 15, 2020.", "source_name": "Let''s Encrypt FAQ"}, {"url": "https://www.splunk.com/en_us/blog/security/tall-tales-of-hunting-with-tls-ssl-certificates.html", "description": "Kovar, R. (2017, December 11). Tall Tales of Hunting with TLS/SSL Certificates. Retrieved October 16, 2020.", "source_name": "Splunk Kovar Certificates 2017"}, {"url": "https://www.recordedfuture.com/cobalt-strike-servers/", "description": "Insikt Group. (2019, June 18). A Multi-Method Approach to Identifying Rogue Cobalt Strike Servers. Retrieved October 16, 2020.", "source_name": "Recorded Future Beacon Certificates"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4702}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider use of services that may aid in the tracking of newly issued certificates and/or certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017) Some server-side components of adversary tools may have default values set for SSL/TLS certificates.(Citation: Recorded Future Beacon Certificates)\\n\\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001), [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002), and/or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).\"", "Str": "Consider use of services that may aid in the tracking of newly issued certificates and/or certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017) Some server-side components of adversary tools may have default values set for SSL/TLS certificates.(Citation: Recorded Future Beacon Certificates)\n\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001), [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002), and/or [Install Root Certificate](https://attack.mitre.org/techniques/T1553/004).", "Type": 3, "Index": 3849}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4658}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4627}}', 'T1588.004', 'Adversaries may buy and/or steal SSL/TLS certificates that can be used during targeting. SSL/TLS certificates are designed to instill trust. They include information about the key, information about its owner''s identity, and the digital signature of an entity that has verified the certificate''s contents are correct. If the signature is valid, and the person examining the certificate trusts the signer, then they know they can use that key to communicate with its owner.
INSERT INTO public.attack_patterns VALUES (138, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.011248+00', NULL, 'attack-pattern--0979abf9-4e26-43ec-9b6e-54efc4e70fca', '[{"url": "https://attack.mitre.org/techniques/T1596/003", "external_id": "T1596.003", "source_name": "mitre-attack"}, {"url": "https://www.sslshopper.com/ssl-checker.html", "description": "SSL Shopper. (n.d.). SSL Checker. Retrieved October 20, 2020.", "source_name": "SSLShopper Lookup"}, {"url": "https://medium.com/@menakajain/export-download-ssl-certificate-from-server-site-url-bcfc41ea46a2", "description": "Jain, M. (2019, September 16). Export & Download — SSL Certificate from Server (Site URL). Retrieved October 20, 2020.", "source_name": "Medium SSL Cert"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3220}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2779}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3176}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3145}}', 'T1596.003', 'Adversaries may search public digital certificate data for information about victims that can be used during targeting. Digital certificates are issued by a certificate authority (CA) in order to cryptographically verify the origin of signed content. These certificates, such as those used for encrypted web traffic (HTTPS SSL/TLS communications), contain information about the registered organization such as name and location.
INSERT INTO public.attack_patterns VALUES (143, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.202064+00', NULL, 'attack-pattern--4eb28bed-d11a-4641-9863-c2ac017d910a', '[{"url": "https://attack.mitre.org/techniques/T1562/002", "external_id": "T1562.002", "source_name": "mitre-attack"}, {"url": "https://www.ultimatewindowssecurity.com/securitylog/encyclopedia/", "description": "Franklin Smith. (n.d.). Windows Security Log Events. Retrieved February 21, 2020.", "source_name": "Windows Log Events"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2109}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for commands that can be used to disable logging. Lack of event logs may be suspicious.\"", "Str": "Monitor processes and command-line arguments for commands that can be used to disable logging. Lack of event logs may be suspicious.", "Type": 3, "Index": 1796}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2061}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1629}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1963}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Log analysis\"\n    ]", "Str": "", "Type": 5, "Index": 1741}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2005}}', 'T1562.002', 'Adversaries may disable Windows event logging to limit data that can be leveraged for detections and audits. Windows event logs record user and system activity such as login attempts, process creation, and much more.(Citation: Windows Log Events) This data is used by security tools and analysts to generate detections.
INSERT INTO public.attack_patterns VALUES (144, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.240151+00', NULL, 'attack-pattern--77532a55-c283-4cd2-bc5d-2d0b65e9d88c', '[{"url": "https://attack.mitre.org/techniques/T1562/007", "external_id": "T1562.007", "source_name": "mitre-attack"}, {"url": "https://expel.io/blog/finding-evil-in-aws/", "description": "A. Randazzo, B. Manahan and S. Lipton. (2020, April 28). Finding Evil in AWS. Retrieved June 25, 2020.", "source_name": "Expel IO Evil in AWS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2449}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor cloud logs for modification or creation of new security groups or firewall rules.\"", "Str": "Monitor cloud logs for modification or creation of new security groups or firewall rules.", "Type": 3, "Index": 2191}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 2404}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Expel\"\n    ]", "Str": "", "Type": 5, "Index": 2025}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Firewall: Firewall Rule Modification\",\n      \"Firewall: Firewall Disable\"\n    ]", "Str": "", "Type": 5, "Index": 2076}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2315}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2357}}', 'T1562.007', 'Adversaries may disable or modify a firewall within a cloud environment to bypass controls that limit access to cloud resources. Cloud firewalls are separate from system firewalls that are described in [Disable or Modify System Firewall](https://attack.mitre.org/techniques/T1562/004). 
INSERT INTO public.attack_patterns VALUES (147, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.416654+00', NULL, 'attack-pattern--fb640c43-aa6b-431e-a961-a279010424ac', '[{"url": "https://attack.mitre.org/techniques/T1561/001", "external_id": "T1561.001", "source_name": "mitre-attack"}, {"url": "https://www.operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Unraveling the Long Thread of the Sony Attack. Retrieved February 25, 2016.", "source_name": "Novetta Blockbuster"}, {"url": "https://operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Destructive-Malware-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Destructive Malware Report. Retrieved March 2, 2016.", "source_name": "Novetta Blockbuster Destructive Malware"}, {"url": "https://www.justice.gov/opa/press-release/file/1092091/download", "description": "Department of Justice. (2018, September 6). Criminal Complaint - United States of America v. PARK JIN HYOK. Retrieved March 29, 2019.", "source_name": "DOJ Lazarus Sony 2018"}, {"url": "https://docs.microsoft.com/sysinternals/downloads/sysmon", "description": "Russinovich, M. & Garnier, T. (2017, May 22). Sysmon v6.20. Retrieved December 13, 2017.", "source_name": "Microsoft Sysmon v6 May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4620}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for attempts to read/write to sensitive locations like the partition boot sector or BIOS parameter block/superblock. Monitor for direct access read/write attempts using the <code>\\\\\\\\\\\\\\\\.\\\\\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.\"", "Str": "Look for attempts to read/write to sensitive locations like the partition boot sector or BIOS parameter block/superblock. Monitor for direct access read/write attempts using the <code>\\\\\\\\.\\\\</code> notation.(Citation: Microsoft Sysmon v6 May 2017) Monitor for unusual kernel driver installation activity.", "Type": 3, "Index": 3997}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4542}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4339}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Driver: Driver Load\",\n      \"Drive: Drive Access\",\n      \"Drive: Drive Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3800}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4400}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"root\",\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4442}}', 'T1561.001', 'Adversaries may erase the contents of storage devices on specific systems or in large numbers in a network to interrupt availability to system and network resources.
INSERT INTO public.attack_patterns VALUES (150, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.542375+00', NULL, 'attack-pattern--68a0c5ed-bee2-4513-830d-5b0d650139bd', '[{"url": "https://attack.mitre.org/techniques/T1021/003", "external_id": "T1021.003", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/06/hunting-com-objects.html", "description": "Hamilton, C. (2019, June 4). Hunting COM Objects. Retrieved June 10, 2019.", "source_name": "Fireeye Hunting COM June 2019"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/ms680573.aspx", "description": "Microsoft. (n.d.). Component Object Model (COM). Retrieved November 22, 2017.", "source_name": "Microsoft COM"}, {"url": "https://msdn.microsoft.com/en-us/library/windows/desktop/ms687317(v=vs.85).aspx", "description": "Microsoft. (n.d.). Setting Process-Wide Security Through the Registry. Retrieved November 21, 2017.", "source_name": "Microsoft Process Wide Com Keys"}, {"url": "https://docs.microsoft.com/en-us/windows/desktop/com/dcom-security-enhancements-in-windows-xp-service-pack-2-and-windows-server-2003-service-pack-1", "description": "Microsoft. (n.d.). DCOM Security Enhancements in Windows XP Service Pack 2 and Windows Server 2003 Service Pack 1. Retrieved November 22, 2017.", "source_name": "Microsoft COM ACL"}, {"url": "https://enigma0x3.net/2017/11/16/lateral-movement-using-outlooks-createobject-method-and-dotnettojscript/", "description": "Nelson, M. (2017, November 16). Lateral Movement using Outlook''s CreateObject Method and DotNetToJScript. Retrieved November 21, 2017.", "source_name": "Enigma Outlook DCOM Lateral Movement Nov 2017"}, {"url": "https://enigma0x3.net/2017/01/05/lateral-movement-using-the-mmc20-application-com-object/", "description": "Nelson, M. (2017, January 5). Lateral Movement using the MMC20 Application COM Object. Retrieved November 21, 2017.", "source_name": "Enigma MMC20 COM Jan 2017"}, {"url": "https://enigma0x3.net/2017/01/23/lateral-movement-via-dcom-round-2/", "description": "Nelson, M. (2017, January 23). Lateral Movement via DCOM: Round 2. Retrieved November 21, 2017.", "source_name": "Enigma DCOM Lateral Movement Jan 2017"}, {"url": "https://enigma0x3.net/2017/09/11/lateral-movement-using-excel-application-and-dcom/", "description": "Nelson, M. (2017, September 11). Lateral Movement using Excel.Application and DCOM. Retrieved November 21, 2017.", "source_name": "Enigma Excel DCOM Sept 2017"}, {"url": "https://www.cybereason.com/blog/leveraging-excel-dde-for-lateral-movement-via-dcom", "description": "Tsukerman, P. (2017, November 8). Leveraging Excel DDE for lateral movement via DCOM. Retrieved November 21, 2017.", "source_name": "Cyberreason DCOM DDE Lateral Movement Nov 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6568}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for COM objects loading DLLs and other modules not typically associated with the application.(Citation: Enigma Outlook DCOM Lateral Movement Nov 2017) Enumeration of COM objects, via [Query Registry](https://attack.mitre.org/techniques/T1012) or [PowerShell](https://attack.mitre.org/techniques/T1059/001), may also proceed malicious use.(Citation: Fireeye Hunting COM June 2019)(Citation: Enigma MMC20 COM Jan 2017) Monitor for spawning of processes associated with COM objects, especially those invoked by a user different than the one currently logged on.\\n\\nMonitor for any influxes or abnormal increases in Distributed Computing Environment/Remote Procedure Call (DCE/RPC) traffic.\"", "Str": "Monitor for COM objects loading DLLs and other modules not typically associated with the application.(Citation: Enigma Outlook DCOM Lateral Movement Nov 2017) Enumeration of COM objects, via [Query Registry](https://attack.mitre.org/techniques/T1012) or [PowerShell](https://attack.mitre.org/techniques/T1059/001), may also proceed malicious use.(Citation: Fireeye Hunting COM June 2019)(Citation: Enigma MMC20 COM Jan 2017) Monitor for spawning of processes associated with COM objects, especially those invoked by a user different than the one currently logged on.\n\nMonitor for any influxes or abnormal increases in Distributed Computing Environment/Remote Procedure Call (DCE/RPC) traffic.", "Type": 3, "Index": 5677}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 6520}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 5526}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 6406}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 6448}}', 'T1021.003', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to interact with remote machines by taking advantage of Distributed Component Object Model (DCOM). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (151, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.621971+00', NULL, 'attack-pattern--7610cada-1499-41a4-b3dd-46467b68d177', '[{"url": "https://attack.mitre.org/techniques/T1136/002", "external_id": "T1136.002", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/event-4720", "description": "Lich, B., Miroshnikov, A. (2017, April 5). 4720(S): A user account was created. Retrieved June 30, 2017.", "source_name": "Microsoft User Creation Event"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2530}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for processes and command-line parameters associated with domain account creation, such as <code>net user /add /domain</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows domain controller. (Citation: Microsoft User Creation Event) Perform regular audits of domain accounts to detect suspicious accounts that may have been created by an adversary.\"", "Str": "Monitor for processes and command-line parameters associated with domain account creation, such as <code>net user /add /domain</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows domain controller. (Citation: Microsoft User Creation Event) Perform regular audits of domain accounts to detect suspicious accounts that may have been created by an adversary.", "Type": 3, "Index": 1886}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2452}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1737}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2354}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2396}}', 'T1136.002', 'Adversaries may create a domain account to maintain access to victim systems. Domain accounts are those managed by Active Directory Domain Services where access and permissions are configured across systems and services that are part of that domain. Domain accounts can cover user, administrator, and service accounts. With a sufficient level of access, the <code>net user /add /domain</code> command can be used to create a domain account.
INSERT INTO public.attack_patterns VALUES (157, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.827507+00', NULL, 'attack-pattern--2aed01ad-3df3-4410-a8cb-11ea4ded587c', '[{"url": "https://attack.mitre.org/techniques/T1069/002", "external_id": "T1069.002", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2463}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1502}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2385}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1398}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2296}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2338}}', 'T1069.002', 'Adversaries may attempt to find domain-level groups and permission settings. The knowledge of domain-level permission groups can help adversaries determine which groups exist and which users belong to a particular group. Adversaries may use this information to determine which users have elevated permissions, such as domain administrators.
INSERT INTO public.attack_patterns VALUES (548, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:46.998916+00', NULL, 'attack-pattern--60d0c01d-e2bf-49dd-a453-f8a9c9fa6f65', '[{"url": "https://attack.mitre.org/techniques/T1021/006", "external_id": "T1021.006", "source_name": "mitre-attack"}, {"url": "http://msdn.microsoft.com/en-us/library/aa384426", "description": "Microsoft. (n.d.). Windows Remote Management. Retrieved November 12, 2014.", "source_name": "Microsoft WinRM"}, {"url": "https://www.slideshare.net/kieranjacobsen/lateral-movement-with-power-shell-2", "description": "Jacobsen, K. (2014, May 16). Lateral Movement with PowerShell&#91;slides&#93;. Retrieved November 12, 2014.", "source_name": "Jacobsen 2014"}, {"url": "https://medium.com/threatpunter/detecting-lateral-movement-using-sysmon-and-splunk-318d3be141bc", "description": "French, D. (2018, September 30). Detecting Lateral Movement Using Sysmon and Splunk. Retrieved October 11, 2019.", "source_name": "Medium Detecting Lateral Movement"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2977}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor use of WinRM within an environment by tracking service execution. If it is not normally used or is disabled, then this may be an indicator of suspicious behavior. Monitor processes created and actions taken by the WinRM process or a WinRM invoked script to correlate it with other related events.(Citation: Medium Detecting Lateral Movement)\"", "Str": "Monitor use of WinRM within an environment by tracking service execution. If it is not normally used or is disabled, then this may be an indicator of suspicious behavior. Monitor processes created and actions taken by the WinRM process or a WinRM invoked script to correlate it with other related events.(Citation: Medium Detecting Lateral Movement)", "Type": 3, "Index": 2433}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2929}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2228}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2817}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2859}}', 'T1021.006', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to interact with remote systems using Windows Remote Management (WinRM). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (160, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:32.969706+00', NULL, 'attack-pattern--767dbf9e-df3f-45cb-8998-4903ab5f80c0', '[{"url": "https://attack.mitre.org/techniques/T1482", "external_id": "T1482", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/it-pro/windows-server-2003/cc759554(v=ws.10)", "description": "Microsoft. (2009, October 7). Trust Technologies. Retrieved February 14, 2019.", "source_name": "Microsoft Trusts"}, {"url": "https://adsecurity.org/?p=1588", "description": "Metcalf, S. (2015, July 15). It’s All About Trust – Forging Kerberos Trust Tickets to Spoof Access across Active Directory Trusts. Retrieved February 14, 2019.", "source_name": "AdSecurity Forging Trust Tickets"}, {"url": "http://www.harmj0y.net/blog/redteaming/a-guide-to-attacking-domain-trusts/", "description": "Schroeder, W. (2017, October 30). A Guide to Attacking Domain Trusts. Retrieved February 14, 2019.", "source_name": "Harmj0y Domain Trusts"}, {"url": "https://www.microsoft.com/security/blog/2017/05/04/windows-defender-atp-thwarts-operation-wilysupply-software-supply-chain-cyberattack/", "description": "Florio, E.. (2017, May 4). Windows Defender ATP thwarts Operation WilySupply software supply chain cyberattack. Retrieved February 14, 2019.", "source_name": "Microsoft Operation Wilysupply"}, {"url": "https://docs.microsoft.com/en-us/dotnet/api/system.directoryservices.activedirectory.domain.getalltrustrelationships?redirectedfrom=MSDN&view=netframework-4.7.2#System_DirectoryServices_ActiveDirectory_Domain_GetAllTrustRelationships", "description": "Microsoft. (n.d.). Domain.GetAllTrustRelationships Method. Retrieved February 14, 2019.", "source_name": "Microsoft GetAllTrustRelationships"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 5191}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation but as part of a chain of behavior that could lead to other activities based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information, such as `nltest /domain_trusts`. Remote access tools with built-in features may interact directly with the Windows API to gather information. Look for the `DSEnumerateDomainTrusts()` Win32 API call to spot activity associated with [Domain Trust Discovery](https://attack.mitre.org/techniques/T1482).(Citation: Harmj0y Domain Trusts) Information may also be acquired through Windows system management tools such as [PowerShell](https://attack.mitre.org/techniques/T1059/001). The .NET method `GetAllTrustRelationships()` can be an indicator of [Domain Trust Discovery](https://attack.mitre.org/techniques/T1482).(Citation: Microsoft GetAllTrustRelationships)\\n\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation but as part of a chain of behavior that could lead to other activities based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information, such as `nltest /domain_trusts`. Remote access tools with built-in features may interact directly with the Windows API to gather information. Look for the `DSEnumerateDomainTrusts()` Win32 API call to spot activity associated with [Domain Trust Discovery](https://attack.mitre.org/techniques/T1482).(Citation: Harmj0y Domain Trusts) Information may also be acquired through Windows system management tools such as [PowerShell](https://attack.mitre.org/techniques/T1059/001). The .NET method `GetAllTrustRelationships()` can be an indicator of [Domain Trust Discovery](https://attack.mitre.org/techniques/T1482).(Citation: Microsoft GetAllTrustRelationships)\n", "Type": 3, "Index": 3962}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5143}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Dave Westgard\",\n      \"Elia Florio, Microsoft\",\n      \"Mnemonic\",\n      \"RedHuntLabs, @redhuntlabs\"\n    ]", "Str": "", "Type": 5, "Index": 3645}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3789}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5053}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5096}}', 'T1482', 'Adversaries may attempt to gather information on domain trust relationships that may be used to identify lateral movement opportunities in Windows multi-domain/forest environments. Domain trusts provide a mechanism for a domain to allow access to resources based on the authentication procedures of another domain.(Citation: Microsoft Trusts) Domain trusts allow the users of the trusted domain to access resources in the trusting domain. The information discovered may help the adversary conduct [SID-History Injection](https://attack.mitre.org/techniques/T1134/005), [Pass the Ticket](https://attack.mitre.org/techniques/T1550/003), and [Kerberoasting](https://attack.mitre.org/techniques/T1558/003).(Citation: AdSecurity Forging Trust Tickets)(Citation: Harmj0y Domain Trusts) Domain trusts can be enumerated using the `DSEnumerateDomainTrusts()` Win32 API call, .NET methods, and LDAP.(Citation: Harmj0y Domain Trusts) The Windows utility [Nltest](https://attack.mitre.org/software/S0359) is known to be used by adversaries to enumerate domain trusts.(Citation: Microsoft Operation Wilysupply)', 'Domain Trust Discovery', 0);
INSERT INTO public.attack_patterns VALUES (163, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.063139+00', NULL, 'attack-pattern--f9cc4d06-775f-4ee1-b401-4e2cc0da30ba', '[{"url": "https://attack.mitre.org/techniques/T1584/001", "external_id": "T1584.001", "source_name": "mitre-attack"}, {"url": "https://www.icann.org/groups/ssac/documents/sac-007-en", "description": "ICANN Security and Stability Advisory Committee. (2005, July 12). Domain Name Hijacking: Incidents, Threats, Risks and Remediation. Retrieved March 6, 2017.", "source_name": "ICANNDomainNameHijacking"}, {"url": "https://docs.microsoft.com/en-us/azure/security/fundamentals/subdomain-takeover", "description": "Microsoft. (2020, September 29). Prevent dangling DNS entries and avoid subdomain takeover. Retrieved October 12, 2020.", "source_name": "Microsoft Sub Takeover 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2756}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 2398}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2712}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2681}}', 'T1584.001', 'Adversaries may hijack domains and/or subdomains that can be used during targeting. Domain registration hijacking is the act of changing the registration of a domain name without the permission of the original registrant.(Citation: ICANNDomainNameHijacking) An adversary may gain access to an email account for the person listed as the owner of the domain. The adversary can then claim that they forgot their password in order to make changes to the domain registration. Other possibilities include social engineering a domain registration help desk to gain access to an account or taking advantage of renewal process gaps.
INSERT INTO public.attack_patterns VALUES (170, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.247705+00', NULL, 'attack-pattern--7bd9c723-2f78-4309-82c5-47cad406572b', '[{"url": "https://attack.mitre.org/techniques/T1568", "external_id": "T1568", "source_name": "mitre-attack"}, {"url": "http://blog.talosintelligence.com/2017/09/avast-distributes-malware.html", "description": "Brumaghin, E. et al. (2017, September 18). CCleanup: A Vast Number of Machines at Risk. Retrieved March 9, 2018.", "source_name": "Talos CCleanup 2017"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/03/dissecting_one_ofap.html", "description": "Dunwoody, M.. (2017, April 3). Dissecting One of APT29’s Fileless WMI and PowerShell Backdoors (POSHSPY). Retrieved April 5, 2017.", "source_name": "FireEye POSHSPY April 2017"}, {"url": "https://www.welivesecurity.com/2017/12/21/sednit-update-fancy-bear-spent-year/", "description": "ESET. (2017, December 21). Sednit update: How Fancy Bear Spent the Year. Retrieved February 18, 2019.", "source_name": "ESET Sednit 2017 Activity"}, {"url": "https://datadrivensecurity.info/blog/posts/2014/Oct/dga-part2/", "description": "Jacobs, J. (2014, October 2). Building a DGA Classifier: Part 2, Feature Engineering. Retrieved February 18, 2019.", "source_name": "Data Driven Security DGA"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4038}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting dynamically generated C2 can be challenging due to the number of different algorithms, constantly evolving malware families, and the increasing complexity of the algorithms. There are multiple approaches to detecting a pseudo-randomly generated domain name, including using frequency analysis, Markov chains, entropy, proportion of dictionary words, ratio of vowels to other characters, and more (Citation: Data Driven Security DGA). CDN domains may trigger these detections due to the format of their domain names. In addition to detecting algorithm generated domains based on the name, another more general approach for detecting a suspicious domain is to check for recently registered names or for rarely visited domains.\"", "Str": "Detecting dynamically generated C2 can be challenging due to the number of different algorithms, constantly evolving malware families, and the increasing complexity of the algorithms. There are multiple approaches to detecting a pseudo-randomly generated domain name, including using frequency analysis, Markov chains, entropy, proportion of dictionary words, ratio of vowels to other characters, and more (Citation: Data Driven Security DGA). CDN domains may trigger these detections due to the format of their domain names. In addition to detecting algorithm generated domains based on the name, another more general approach for detecting a suspicious domain is to check for recently registered names or for rarely visited domains.", "Type": 3, "Index": 3101}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3960}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Chris Roffe\"\n    ]", "Str": "", "Type": 5, "Index": 2860}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2917}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3870}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3913}}', 'T1568', 'Adversaries may dynamically establish connections to command and control infrastructure to evade common detections and remediations. This may be achieved by using malware that shares a common algorithm with the infrastructure the adversary uses to receive the malware''s communications. These calculations can be used to dynamically adjust parameters such as the domain name, IP address, or port number the malware uses for command and control.
INSERT INTO public.attack_patterns VALUES (174, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.519837+00', NULL, 'attack-pattern--65013dd2-bc61-43e3-afb5-a14c4fa7437a', '[{"url": "https://attack.mitre.org/techniques/T1585/002", "external_id": "T1585.002", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/r980-ransomware-disposable-email-service/", "description": "Antazo, F. and Yambao, M. (2016, August 10). R980 Ransomware Found Abusing Disposable Email Address Service. Retrieved October 13, 2020.", "source_name": "Trend Micro R980 2016"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2797}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Phishing](https://attack.mitre.org/techniques/T1566)).", "Type": 3, "Index": 2384}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2753}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2722}}', 'T1585.002', 'Adversaries may create email accounts that can be used during targeting. Adversaries can use accounts created with email providers to further their operations, such as leveraging them to conduct [Phishing for Information](https://attack.mitre.org/techniques/T1598) or [Phishing](https://attack.mitre.org/techniques/T1566).(Citation: Mandiant APT1) Adversaries may also take steps to cultivate a persona around the email account, such as through use of [Social Media Accounts](https://attack.mitre.org/techniques/T1585/001), to increase the chance of success of follow-on behaviors. Created email accounts can also be used in the acquisition of infrastructure (ex: [Domains](https://attack.mitre.org/techniques/T1583/001)).(Citation: Mandiant APT1)
INSERT INTO public.attack_patterns VALUES (178, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.736873+00', NULL, 'attack-pattern--7d77a07d-02fe-4e88-8bd9-e9c008c01bf0', '[{"url": "https://attack.mitre.org/techniques/T1114/003", "external_id": "T1114.003", "source_name": "mitre-attack"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-086A", "description": "US-CERT. (2018, March 27). TA18-068A Brute Force Attacks Conducted by Cyber Actors. Retrieved October 2, 2019.", "source_name": "US-CERT TA18-068A 2018"}, {"url": "https://blogs.technet.microsoft.com/timmcmic/2015/06/08/exchange-and-office-365-mail-forwarding-2/", "description": "McMichael, T.. (2015, June 8). Exchange and Office 365 Mail Forwarding. Retrieved October 8, 2019.", "source_name": "Microsoft Tim McMichael Exchange Mail Forwarding 2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4424}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection is challenging because all messages forwarded because of an auto-forwarding rule have the same presentation as a manually forwarded message. It is also possible for the user to not be aware of the addition of such an auto-forwarding rule and not suspect that their account has been compromised; email-forwarding rules alone will not affect the normal usage patterns or operations of the email account.\\n\\nAuto-forwarded messages generally contain specific detectable artifacts that may be present in the header; such artifacts would be platform-specific. Examples include `X-MS-Exchange-Organization-AutoForwarded` set to true, `X-MailFwdBy` and `X-Forwarded-To`. The `forwardingSMTPAddress` parameter used in a forwarding process that is managed by administrators and not by user actions. All messages for the mailbox are forwarded to the specified SMTP address. However, unlike typical client-side rules, the message does not appear as forwarded in the mailbox; it appears as if it were sent directly to the specified destination mailbox.(Citation: Microsoft Tim McMichael Exchange Mail Forwarding 2) High volumes of emails that bear the `X-MS-Exchange-Organization-AutoForwarded` header (indicating auto-forwarding) without a corresponding number of emails that match the appearance of a forwarded message may indicate that further investigation is needed at the administrator level rather than user-level.\"", "Str": "Detection is challenging because all messages forwarded because of an auto-forwarding rule have the same presentation as a manually forwarded message. It is also possible for the user to not be aware of the addition of such an auto-forwarding rule and not suspect that their account has been compromised; email-forwarding rules alone will not affect the normal usage patterns or operations of the email account.\n\nAuto-forwarded messages generally contain specific detectable artifacts that may be present in the header; such artifacts would be platform-specific. Examples include `X-MS-Exchange-Organization-AutoForwarded` set to true, `X-MailFwdBy` and `X-Forwarded-To`. The `forwardingSMTPAddress` parameter used in a forwarding process that is managed by administrators and not by user actions. All messages for the mailbox are forwarded to the specified SMTP address. However, unlike typical client-side rules, the message does not appear as forwarded in the mailbox; it appears as if it were sent directly to the specified destination mailbox.(Citation: Microsoft Tim McMichael Exchange Mail Forwarding 2) High volumes of emails that bear the `X-MS-Exchange-Organization-AutoForwarded` header (indicating auto-forwarding) without a corresponding number of emails that match the appearance of a forwarded message may indicate that further investigation is needed at the administrator level rather than user-level.", "Type": 3, "Index": 2787}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Office 365\",\n      \"Windows\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4330}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Swetha Prabakaran, Microsoft Threat Intelligence Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 2595}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2704}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4241}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4283}}', 'T1114.003', 'Adversaries may setup email forwarding rules to collect sensitive information. Adversaries may abuse email-forwarding rules to monitor the activities of a victim, steal information, and further gain intelligence on the victim or the victim’s organization to use as part of further exploits or operations.(Citation: US-CERT TA18-068A 2018) Outlook and Outlook Web App (OWA) allow users to create inbox rules for various email functions, including forwarding to a different recipient. Similarly, Google Workspace users or administrators can set up mail forwarding rules via the Google Workspace web interface. Messages can be forwarded to internal or external recipients, and there are no restrictions limiting the extent of this rule. Administrators may also create forwarding rules for user accounts with the same considerations and outcomes.(Citation: Microsoft Tim McMichael Exchange Mail Forwarding 2) 
INSERT INTO public.attack_patterns VALUES (184, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:33.933685+00', NULL, 'attack-pattern--4a5b7ade-8bb5-4853-84ed-23f262002665', '[{"url": "https://attack.mitre.org/techniques/T1611", "external_id": "T1611", "source_name": "mitre-attack"}, {"url": "https://docs.docker.com/get-started/overview/", "description": "Docker. (n.d.). Docker Overview. Retrieved March 30, 2021.", "source_name": "Docker Overview"}, {"url": "https://docs.docker.com/storage/bind-mounts/", "description": "Docker. (n.d.). Use Bind Mounts. Retrieved March 30, 2021.", "source_name": "Docker Bind Mounts"}, {"url": "https://www.trendmicro.com/en_us/research/19/l/why-running-a-privileged-container-in-docker-is-a-bad-idea.html", "description": "Fiser, D., Oliveira, A.. (2019, December 20). Why a Privileged Container in Docker is a Bad Idea. Retrieved March 30, 2021.", "source_name": "Trend Micro Privileged Container"}, {"url": "https://www.intezer.com/blog/cloud-security/watch-your-containers-doki-infecting-docker-servers-in-the-cloud/", "description": "Fishbein, N., Kajiloti, M.. (2020, July 28). Watch Your Containers: Doki Infecting Docker Servers in the Cloud. Retrieved March 30, 2021.", "source_name": "Intezer Doki July 20"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4218}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for the deployment of suspicious or unknown container images and pods in your environment, particularly containers running as root. Additionally, monitor for unexpected usage of syscalls such as <code>mount</code> (as well as resulting process activity) that may indicate an attempt to escape from a privileged container to host. In Kubernetes, monitor for cluster-level events associated with changing containers'' volume configurations.\"", "Str": "Monitor for the deployment of suspicious or unknown container images and pods in your environment, particularly containers running as root. Additionally, monitor for unexpected usage of syscalls such as <code>mount</code> (as well as resulting process activity) that may indicate an attempt to escape from a privileged container to host. In Kubernetes, monitor for cluster-level events associated with changing containers'' volume configurations.", "Type": 3, "Index": 3528}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4135}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Joas Antonio dos Santos, @C0d3Cr4zy, Inmetrics\",\n      \"Alfredo Oliveira, Trend Micro\",\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Idan Frimark, Cisco\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Ariel Shuper, Cisco\",\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\"\n    ]", "Str": "", "Type": 5, "Index": 3022}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Container: Container Creation\",\n      \"Process: OS API Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3386}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4008}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4051}}', 'T1611', 'Adversaries may break out of a container to gain access to the underlying host. This can allow an adversary access to other containerized resources from the host level or to the host itself. In principle, containerized resources should provide a clear separation of application functionality and be isolated from the host environment.(Citation: Docker Overview)
INSERT INTO public.attack_patterns VALUES (188, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.138854+00', NULL, 'attack-pattern--70d81154-b187-45f9-8ec5-295d01255979', '[{"url": "https://attack.mitre.org/techniques/T1574/005", "external_id": "T1574.005", "source_name": "mitre-attack"}, {"url": "https://www.mozilla.org/en-US/security/advisories/mfsa2012-98/", "description": "Robert Kugler. (2012, November 20). Mozilla Foundation Security Advisory 2012-98. Retrieved March 10, 2017.", "source_name": "mozilla_sec_adv_2012"}, {"url": "https://seclists.org/fulldisclosure/2015/Dec/34", "description": "Stefan Kanthak. (2015, December 8). Executable installers are vulnerable^WEVIL (case 7): 7z*.exe allows remote code execution with escalation of privilege. Retrieved December 4, 2014.", "source_name": "Executable Installers are Vulnerable"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4975}, "x_mitre_detection": {"Num": 0, "Raw": "\"Look for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\\n\\nLook for abnormal process call trees from typical processes and services and for execution of other commands that could relate to Discovery or other adversary techniques.\"", "Str": "Look for changes to binaries and service executables that may normally occur during software updates. If an executable is written, renamed, and/or moved to match an existing service executable, it could be detected and correlated with other suspicious behavior. Hashing of binaries and service executables could be used to detect replacement against historical data.\n\nLook for abnormal process call trees from typical processes and services and for execution of other commands that could relate to Discovery or other adversary techniques.", "Type": 3, "Index": 4142}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4927}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Travis Smith, Tripwire\",\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 3856}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Service: Service Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3948}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4815}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4857}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4723}}', 'T1574.005', 'Adversaries may execute their own malicious payloads by hijacking the binaries used by an installer. These processes may automatically execute specific binaries as part of their functionality or to perform other actions. If the permissions on the file system directory containing a target binary, or permissions on the binary itself, are improperly set, then the target binary may be overwritten with another binary using user-level permissions and executed by the original process. If the original process and thread are running under a higher permissions level, then the replaced binary will also execute under higher-level permissions, which could include SYSTEM.
INSERT INTO public.attack_patterns VALUES (190, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.215803+00', NULL, 'attack-pattern--a19e86f8-1c0a-4fea-8407-23b73d615776', '[{"url": "https://attack.mitre.org/techniques/T1048", "external_id": "T1048", "source_name": "mitre-attack"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/10/unit42-oilrig-malware-campaign-updates-toolset-and-expands-targets/", "description": "Grunzweig, J. and Falcone, R.. (2016, October 4). OilRig Malware Campaign Updates Toolset and Expands Targets. Retrieved May 3, 2017.", "source_name": "Palo Alto OilRig Oct 2016"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3227}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)", "Type": 3, "Index": 2616}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3149}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Alfredo Abarca\"\n    ]", "Str": "", "Type": 5, "Index": 2309}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2369}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3075}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3118}}', 'T1048', 'Adversaries may steal data by exfiltrating it over a different protocol than that of the existing command and control channel. The data may also be sent to an alternate network location from the main command and control server.  
INSERT INTO public.attack_patterns VALUES (197, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.50663+00', NULL, 'attack-pattern--fb8d023d-45be-47e9-bc51-f56bcae6435b', '[{"url": "https://attack.mitre.org/techniques/T1048/003", "external_id": "T1048.003", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2608}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2) \"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2) ", "Type": 3, "Index": 1997}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2530}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1750}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2457}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2499}}', 'T1048.003', 'Adversaries may steal data by exfiltrating it over an un-encrypted network protocol other than that of the existing command and control channel. The data may also be sent to an alternate network location from the main command and control server. 
INSERT INTO public.attack_patterns VALUES (201, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.726146+00', NULL, 'attack-pattern--86a96bf6-cf8b-411c-aaeb-8959944d64f7', '[{"url": "https://attack.mitre.org/techniques/T1567/001", "external_id": "T1567.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2047}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server) to code repositories. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server) to code repositories. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. User behavior monitoring may help to detect abnormal patterns of activity.", "Type": 3, "Index": 1513}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1969}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1320}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1896}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1938}}', 'T1567.001', 'Adversaries may exfiltrate data to a code repository rather than over their primary command and control channel. Code repositories are often accessible via an API (ex: https://api.github.com). Access to these APIs are often over HTTPS, which gives the adversary an additional level of protection.
INSERT INTO public.attack_patterns VALUES (204, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.848264+00', NULL, 'attack-pattern--9c306d8d-cde7-4b4c-b6e8-d0bb16caca36', '[{"url": "https://attack.mitre.org/techniques/T1212", "external_id": "T1212", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/en-us/library/security/ms14-068.aspx", "description": "Microsoft. (2014, November 18). Vulnerability in Kerberos Could Allow Elevation of Privilege (3011780). Retrieved December 23, 2015.", "source_name": "Technet MS14-068"}, {"url": "https://adsecurity.org/?p=1515", "description": "Metcalf, S. (2015, May 03). Detecting Forged Kerberos Ticket (Golden Ticket & Silver Ticket) Use in Active Directory. Retrieved December 23, 2015.", "source_name": "ADSecurity Detecting Forged Tickets"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3011}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the system that might indicate successful compromise, such as abnormal behavior of processes. Credential resources obtained through exploitation may be detectable in use if they are not normally used or seen.\"", "Str": "Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the system that might indicate successful compromise, such as abnormal behavior of processes. Credential resources obtained through exploitation may be detectable in use if they are not normally used or seen.", "Type": 3, "Index": 2386}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2933}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"John Lambert, Microsoft Threat Intelligence Center\"\n    ]", "Str": "", "Type": 5, "Index": 2293}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2843}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2886}}', 'T1212', 'Adversaries may exploit software vulnerabilities in an attempt to collect credentials. Exploitation of a software vulnerability occurs when an adversary takes advantage of a programming error in a program, service, or within the operating system software or kernel itself to execute adversary-controlled code. Credentialing and authentication mechanisms may be targeted for exploitation by adversaries as a means to gain access to useful credentials or circumvent the process to gain access to systems. One example of this is MS14-068, which targets Kerberos and can be used to forge Kerberos tickets using domain user permissions.(Citation: Technet MS14-068)(Citation: ADSecurity Detecting Forged Tickets) Exploitation for credential access may also result in Privilege Escalation depending on the process targeted or credentials obtained.', 'Exploitation for Credential Access', 0);
INSERT INTO public.attack_patterns VALUES (207, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:34.940663+00', NULL, 'attack-pattern--9db0cf3a-a3c9-4012-8268-123b9db6fd82', '[{"url": "https://attack.mitre.org/techniques/T1210", "external_id": "T1210", "source_name": "mitre-attack"}, {"url": "https://www.cisecurity.org/advisory/multiple-vulnerabilities-in-microsoft-windows-smb-server-could-allow-for-remote-code-execution/", "description": "CIS. (2017, May 15). Multiple Vulnerabilities in Microsoft Windows SMB Server Could Allow for Remote Code Execution. Retrieved April 3, 2018.", "source_name": "CIS Multiple SMB Vulnerabilities"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2017-0176", "description": "National Vulnerability Database. (2017, June 22). CVE-2017-0176 Detail. Retrieved April 3, 2018.", "source_name": "NVD CVE-2017-0176"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2016-6662", "description": "National Vulnerability Database. (2017, February 2). CVE-2016-6662 Detail. Retrieved April 3, 2018.", "source_name": "NVD CVE-2016-6662"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2014-7169", "description": "National Vulnerability Database. (2017, September 24). CVE-2014-7169 Detail. Retrieved April 3, 2018.", "source_name": "NVD CVE-2014-7169"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4855}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.\"", "Str": "Detecting software exploitation may be difficult depending on the tools available. Software exploits may not always succeed or may cause the exploited process to become unstable or crash. Also look for behavior on the endpoint system that might indicate successful compromise, such as abnormal behavior of the processes. This could include suspicious files written to disk, evidence of [Process Injection](https://attack.mitre.org/techniques/T1055) for attempts to hide execution, evidence of Discovery, or other unusual network traffic that may indicate additional tools transferred to the system.", "Type": 3, "Index": 3820}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4543}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ExtraHop\"\n    ]", "Str": "", "Type": 5, "Index": 3633}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3687}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4453}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Unpatched software or otherwise vulnerable target. Depending on the target and goal, the system and exploitable service may need to be remotely accessible from the internal network.\"\n    ]", "Str": "", "Type": 5, "Index": 4633}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4496}}', 'T1210', 'Adversaries may exploit remote services to gain unauthorized access to internal systems once inside of a network. Exploitation of a software vulnerability occurs when an adversary takes advantage of a programming error in a program, service, or within the operating system software or kernel itself to execute adversary-controlled code. A common goal for post-compromise exploitation of remote services is for lateral movement to enable access to a remote system.
INSERT INTO public.attack_patterns VALUES (209, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.020028+00', NULL, 'attack-pattern--f4b843c1-7e92-4701-8fed-ce82f8be2636', '[{"url": "https://attack.mitre.org/techniques/T1588/005", "external_id": "T1588.005", "source_name": "mitre-attack"}, {"url": "https://www.exploit-db.com/", "description": "Offensive Security. (n.d.). Exploit Database. Retrieved October 15, 2020.", "source_name": "Exploit Database"}, {"url": "https://www.wired.co.uk/article/darkhotel-hacking-team-cyber-espionage", "description": "Temperton, J. (2015, August 10). Hacking Team zero-day used in new Darkhotel attacks. Retrieved March 9, 2017.", "source_name": "TempertonDarkHotel"}, {"url": "https://www.nytimes.com/2013/07/14/world/europe/nations-buying-as-hackers-sell-computer-flaws.html", "description": "Nicole Perlroth and David E. Sanger. (2013, July 12). Nations Buying as Hackers Sell Flaws in Computer Code. Retrieved March 9, 2017.", "source_name": "NationsBuying"}, {"url": "https://citizenlab.ca/2016/08/million-dollar-dissident-iphone-zero-day-nso-group-uae/", "description": "Bill Marczak and John Scott-Railton. (2016, August 24). The Million Dollar Dissident: NSO Group’s iPhone Zero-Days used against a UAE Human Rights Defender. Retrieved December 12, 2016.", "source_name": "PegasusCitizenLab"}, {"url": "https://www.vice.com/en/article/3kx5y3/uzbekistan-hacking-operations-uncovered-due-to-spectacularly-bad-opsec", "description": "Zetter, K. (2019, October 3). Researchers Say They Uncovered Uzbekistan Hacking Operations Due to Spectacularly Bad OPSEC. Retrieved October 15, 2020.", "source_name": "Wired SandCat Oct 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5320}, "x_mitre_detection": {"Num": 0, "Raw": "\"\\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on behaviors relating to the use of exploits (i.e. [Exploit Public-Facing Application](https://attack.mitre.org/techniques/T1190), [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203), [Exploitation for Privilege Escalation](https://attack.mitre.org/techniques/T1068), [Exploitation for Defense Evasion](https://attack.mitre.org/techniques/T1211), [Exploitation for Credential Access](https://attack.mitre.org/techniques/T1212), [Exploitation of Remote Services](https://attack.mitre.org/techniques/T1210), and [Application or System Exploitation](https://attack.mitre.org/techniques/T1499/004)).\"", "Str": "\nMuch of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on behaviors relating to the use of exploits (i.e. [Exploit Public-Facing Application](https://attack.mitre.org/techniques/T1190), [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203), [Exploitation for Privilege Escalation](https://attack.mitre.org/techniques/T1068), [Exploitation for Defense Evasion](https://attack.mitre.org/techniques/T1211), [Exploitation for Credential Access](https://attack.mitre.org/techniques/T1212), [Exploitation of Remote Services](https://attack.mitre.org/techniques/T1210), and [Application or System Exploitation](https://attack.mitre.org/techniques/T1499/004)).", "Type": 3, "Index": 4419}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 5276}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5245}}', 'T1588.005', 'Adversaries may buy, steal, or download exploits that can be used during targeting. An exploit takes advantage of a bug or vulnerability in order to cause unintended or unanticipated behavior to occur on computer hardware or software. Rather than developing their own exploits, an adversary may find/modify exploits from online or purchase them from exploit vendors.(Citation: Exploit Database)(Citation: TempertonDarkHotel)(Citation: NationsBuying)
INSERT INTO public.attack_patterns VALUES (211, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.083496+00', NULL, 'attack-pattern--69b8fd78-40e8-4600-ae4d-662c9d7afdb3', '[{"url": "https://attack.mitre.org/techniques/T1090/002", "external_id": "T1090.002", "source_name": "mitre-attack"}, {"url": "http://blog.trendmicro.com/trendlabs-security-intelligence/in-depth-look-apt-attack-tools-of-the-trade/", "description": "Wilhoit, K. (2013, March 4). In-Depth Look: APT Attack Tools of the Trade. Retrieved December 2, 2015.", "source_name": "Trend Micro APT Attack Tools"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3416}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows, such as a client sending significantly more data than it receives from an external server. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows, such as a client sending significantly more data than it receives from an external server. Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2838}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3338}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2654}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3307}}', 'T1090.002', 'Adversaries may use an external proxy to act as an intermediary for network communications to a command and control server to avoid direct connections to their infrastructure. Many tools exist that enable traffic redirection through proxies or port redirection, including [HTRAN](https://attack.mitre.org/software/S0040), ZXProxy, and ZXPortMap. (Citation: Trend Micro APT Attack Tools) Adversaries use these types of proxies to manage command and control communications, to provide resiliency in the face of connection loss, or to ride over existing trusted communications paths to avoid suspicion.
INSERT INTO public.attack_patterns VALUES (212, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.115348+00', NULL, 'attack-pattern--10d51417-ee35-4589-b1ff-b6df1c334e8d', '[{"url": "https://attack.mitre.org/techniques/T1133", "external_id": "T1133", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/555.html", "external_id": "CAPEC-555", "source_name": "capec"}, {"url": "https://www.volexity.com/blog/2015/10/07/virtual-private-keylogging-cisco-web-vpns-leveraged-for-access-and-persistence/", "description": "Adair, S. (2015, October 7). Virtual Private Keylogging: Cisco Web VPNs Leveraged for Access and Persistence. Retrieved March 20, 2017.", "source_name": "Volexity Virtual Private Keylogging"}, {"url": "https://www.trendmicro.com/en_us/research/20/f/xorddos-kaiji-botnet-malware-variants-target-exposed-docker-servers.html", "description": "Remillano II, A., et al. (2020, June 20). XORDDoS, Kaiji Variants Target Exposed Docker Servers. Retrieved April 5, 2021.", "source_name": "Trend Micro Exposed Docker Server"}, {"url": "https://unit42.paloaltonetworks.com/hildegard-malware-teamtnt/", "description": "Chen, J. et al. (2021, February 3). Hildegard: New TeamTNT Cryptojacking Malware Targeting Kubernetes. Retrieved April 5, 2021.", "source_name": "Unit 42 Hildegard Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 4765}, "x_mitre_detection": {"Num": 0, "Raw": "\"Follow best practices for detecting adversary use of [Valid Accounts](https://attack.mitre.org/techniques/T1078) for authenticating to remote services. Collect authentication logs and analyze for unusual access patterns, windows of activity, and access outside of normal business hours.\\n\\nWhen authentication is not required to access an exposed remote service, monitor for follow-on activities such as anomalous external use of the exposed API or application.\"", "Str": "Follow best practices for detecting adversary use of [Valid Accounts](https://attack.mitre.org/techniques/T1078) for authenticating to remote services. Collect authentication logs and analyze for unusual access patterns, windows of activity, and access outside of normal business hours.\n\nWhen authentication is not required to access an exposed remote service, monitor for follow-on activities such as anomalous external use of the exposed API or application.", "Type": 3, "Index": 4096}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4682}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ExtraHop\",\n      \"David Fiser, @anu4is, Trend Micro\",\n      \"Alfredo Oliveira, Trend Micro\",\n      \"Idan Frimark, Cisco\",\n      \"Rory McCune, Aqua Security\",\n      \"Yuval Avrahami, Palo Alto Networks\",\n      \"Jay Chen, Palo Alto Networks\",\n      \"Brad Geesaman, @bradgeesaman\",\n      \"Magno Logan, @magnologan, Trend Micro\",\n      \"Ariel Shuper, Cisco\",\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\",\n      \"Daniel Oakley\",\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 3382}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Logon Session: Logon Session Metadata\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3919}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4592}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4635}}', 'T1133', 'Adversaries may leverage external-facing remote services to initially access and/or persist within a network. Remote services such as VPNs, Citrix, and other access mechanisms allow users to connect to internal enterprise network resources from external locations. There are often remote service gateways that manage connections and credential authentication for these services. Services such as [Windows Remote Management](https://attack.mitre.org/techniques/T1021/006) can also be used externally.
INSERT INTO public.attack_patterns VALUES (215, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.297901+00', NULL, 'attack-pattern--29ba5a15-3b7b-4732-b817-65ea8f6468e6', '[{"url": "https://attack.mitre.org/techniques/T1568/001", "external_id": "T1568.001", "source_name": "mitre-attack"}, {"url": "https://resources.infosecinstitute.com/fast-flux-networks-working-detection-part-1/#gref", "description": "Mehta, L. (2014, December 17). Fast Flux Networks Working and Detection, Part 1. Retrieved March 6, 2017.", "source_name": "MehtaFastFluxPt1"}, {"url": "https://resources.infosecinstitute.com/fast-flux-networks-working-detection-part-2/#gref", "description": "Mehta, L. (2014, December 23). Fast Flux Networks Working and Detection, Part 2. Retrieved March 6, 2017.", "source_name": "MehtaFastFluxPt2"}, {"url": "https://www.welivesecurity.com/2017/01/12/fast-flux-networks-work/", "description": "Albors, Josep. (2017, January 12). Fast Flux networks: What are they and how do they work?. Retrieved March 11, 2020.", "source_name": "Fast Flux - Welivesecurity"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3480}, "x_mitre_detection": {"Num": 0, "Raw": "\"In general, detecting usage of fast flux DNS is difficult due to web traffic load balancing that services client requests quickly. In single flux cases only IP addresses change for static domain names. In double flux cases, nothing is static. Defenders such as domain registrars and service providers are likely in the best position for detection.\"", "Str": "In general, detecting usage of fast flux DNS is difficult due to web traffic load balancing that services client requests quickly. In single flux cases only IP addresses change for static domain names. In double flux cases, nothing is static. Defenders such as domain registrars and service providers are likely in the best position for detection.", "Type": 3, "Index": 2989}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3402}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2855}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3371}}', 'T1568.001', 'Adversaries may use Fast Flux DNS to hide a command and control channel behind an array of rapidly changing IP addresses linked to a single domain resolution. This technique uses a fully qualified domain name, with multiple IP addresses assigned to it which are swapped with high frequency, using a combination of round robin IP addressing and short Time-To-Live (TTL) for a DNS resource record.(Citation: MehtaFastFluxPt1)(Citation: MehtaFastFluxPt2)(Citation: Fast Flux - Welivesecurity)
INSERT INTO public.attack_patterns VALUES (219, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.445116+00', NULL, 'attack-pattern--65917ae0-b854-4139-83fe-bf2441cf0196', '[{"url": "https://attack.mitre.org/techniques/T1222", "external_id": "T1222", "source_name": "mitre-attack"}, {"url": "https://www.hybrid-analysis.com/sample/ef0d2628823e8e0a0de3b08b8eacaf41cf284c086a948bdfd67f4e4373c14e4d?environmentId=100", "description": "Hybrid Analysis. (2018, June 12). c9b65b764985dfd7a11d3faf599c56b8.exe. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls1 June 2018"}, {"url": "https://www.hybrid-analysis.com/sample/22dab012c3e20e3d9291bce14a2bfc448036d3b966c6e78167f4626f5f9e38d6?environmentId=110", "description": "Hybrid Analysis. (2018, May 30). 2a8efbfadd798f6111340f7c1c956bee.dll. Retrieved August 19, 2018.", "source_name": "Hybrid Analysis Icacls2 May 2018"}, {"url": "https://www.eventtracker.com/tech-articles/monitoring-file-permission-changes-windows-security-log/", "description": "Netsurion. (2014, February 19). Monitoring File Permission Changes with the Windows Security Log. Retrieved August 19, 2018.", "source_name": "EventTracker File Permissions Feb 2014"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 4537}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and investigate attempts to modify ACLs and file/directory ownership. Many of the commands used to modify ACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\\n\\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files. For example, Windows Security Log events (Event ID 4670) are created when DACLs are modified.(Citation: EventTracker File Permissions Feb 2014)\"", "Str": "Monitor and investigate attempts to modify ACLs and file/directory ownership. Many of the commands used to modify ACLs and file/directory ownership are built-in system utilities and may generate a high false positive alert rate, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\n\nConsider enabling file/directory permission change auditing on folders containing key binary/configuration files. For example, Windows Security Log events (Event ID 4670) are created when DACLs are modified.(Citation: EventTracker File Permissions Feb 2014)", "Type": 3, "Index": 3624}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4459}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"CrowdStrike Falcon OverWatch\",\n      \"Jan Miller, CrowdStrike\"\n    ]", "Str": "", "Type": 5, "Index": 3243}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3350}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4316}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"File system access controls\"\n    ]", "Str": "", "Type": 5, "Index": 3554}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\",\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4359}}', 'T1222', 'Adversaries may modify file or directory permissions/attributes to evade access control lists (ACLs) and access protected files.(Citation: Hybrid Analysis Icacls1 June 2018)(Citation: Hybrid Analysis Icacls2 May 2018) File and directory permissions are commonly managed by ACLs configured by the file or directory owner, or users with the appropriate permissions. File and directory ACL implementations vary by platform, but generally explicitly designate which users or groups can perform which actions (read, write, execute, etc.).
INSERT INTO public.attack_patterns VALUES (221, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.518861+00', NULL, 'attack-pattern--f5bb433e-bdf6-4781-84bc-35e97e43be89', '[{"url": "https://attack.mitre.org/techniques/T1495", "external_id": "T1495", "source_name": "mitre-attack"}, {"url": "https://www.symantec.com/security-center/writeup/2000-122010-2655-99", "description": "Yamamura, M. (2002, April 25). W95.CIH. Retrieved April 12, 2019.", "source_name": "Symantec Chernobyl W95.CIH"}, {"url": "http://www.mitre.org/publications/project-stories/going-deep-into-the-bios-with-mitre-firmware-security-research", "description": "Upham, K. (2014, March). Going Deep into the BIOS with MITRE Firmware Security Research. Retrieved January 5, 2016.", "source_name": "MITRE Trustworthy Firmware Measurement"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2415}, "x_mitre_detection": {"Num": 0, "Raw": "\"System firmware manipulation may be detected.(Citation: MITRE Trustworthy Firmware Measurement) Log attempts to read/write to BIOS and compare against known patching behavior.\"", "Str": "System firmware manipulation may be detected.(Citation: MITRE Trustworthy Firmware Measurement) Log attempts to read/write to BIOS and compare against known patching behavior.", "Type": 3, "Index": 1941}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2337}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 2147}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Firmware: Firmware Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1867}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2208}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2251}}', 'T1495', 'Adversaries may overwrite or corrupt the flash memory contents of system BIOS or other firmware in devices attached to a system in order to render them inoperable or unable to boot.(Citation: Symantec Chernobyl W95.CIH) Firmware is software that is loaded and executed from non-volatile memory on hardware devices in order to initialize and manage device functionality. These devices could include the motherboard, hard drive, or video cards.', 'Firmware Corruption', 0);
INSERT INTO public.attack_patterns VALUES (225, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.719592+00', NULL, 'attack-pattern--31a0a2ac-c67c-4a7e-b9ed-6a96477d4e8e', '[{"url": "https://attack.mitre.org/techniques/T1553/001", "external_id": "T1553.001", "source_name": "mitre-attack"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}, {"url": "https://derflounder.wordpress.com/2012/11/20/clearing-the-quarantine-extended-attribute-from-downloaded-applications/", "description": "Rich Trouton. (2012, November 20). Clearing the quarantine extended attribute from downloaded applications. Retrieved July 5, 2017.", "source_name": "Clearing quarantine attribute"}, {"url": "https://www.alienvault.com/blogs/labs-research/oceanlotus-for-os-x-an-application-bundle-pretending-to-be-an-adobe-flash-update", "description": "Eddie Lee. (2016, February 17). OceanLotus for OS X - an Application Bundle Pretending to be an Adobe Flash Update. Retrieved July 5, 2017.", "source_name": "OceanLotus for OS X"}, {"url": "https://blog.malwarebytes.com/cybercrime/2015/10/bypassing-apples-gatekeeper/", "description": "Thomas Reed. (2016, March 31). Bypassing Apple''s Gatekeeper. Retrieved July 5, 2017.", "source_name": "Bypassing Gatekeeper"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4882}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring for the removal of the <code>com.apple.quarantine</code> flag by a user instead of the operating system is a suspicious action and should be examined further. Monitor and investigate attempts to modify extended file attributes with utilities such as <code>xattr</code>. Built-in system utilities may generate high false positive alerts, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.\"", "Str": "Monitoring for the removal of the <code>com.apple.quarantine</code> flag by a user instead of the operating system is a suspicious action and should be examined further. Monitor and investigate attempts to modify extended file attributes with utilities such as <code>xattr</code>. Built-in system utilities may generate high false positive alerts, so compare against baseline knowledge for how systems are typically used and correlate modification events with other indications of malicious activity where possible.", "Type": 3, "Index": 4174}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4836}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3919}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4724}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 4092}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4766}}', 'T1553.001', 'Adversaries may modify file attributes that signify programs are from untrusted sources to subvert Gatekeeper controls. In macOS and OS X, when applications or programs are downloaded from the internet, there is a special attribute set on the file called <code>com.apple.quarantine</code>. This attribute is read by Apple''s Gatekeeper defense program at execution time and provides a prompt to the user to allow or deny execution. 
INSERT INTO public.attack_patterns VALUES (227, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.804723+00', NULL, 'attack-pattern--5282dd9a-d26d-4e16-88b7-7c0f4553daf4', '[{"url": "https://attack.mitre.org/techniques/T1589", "external_id": "T1589", "source_name": "mitre-attack"}, {"url": "https://www.opm.gov/cybersecurity/cybersecurity-incidents/", "description": "Cybersecurity Resource Center. (n.d.). CYBERSECURITY INCIDENTS. Retrieved October 20, 2020.", "source_name": "OPM Leak"}, {"url": "https://www.theregister.com/2017/09/26/deloitte_leak_github_and_google/", "description": "Thomson, I. (2017, September 26). Deloitte is a sitting duck: Key systems with RDP open, VPN and proxy ''login details leaked''. Retrieved October 19, 2020.", "source_name": "Register Deloitte"}, {"url": "https://www.theregister.com/2015/02/28/uber_subpoenas_github_for_hacker_details/", "description": "McCarthy, K. (2015, February 28). FORK ME! Uber hauls GitHub into court to find who hacked database of 50,000 drivers. Retrieved October 19, 2020.", "source_name": "Register Uber"}, {"url": "https://labs.detectify.com/2016/04/28/slack-bot-token-leakage-exposing-business-critical-information/", "description": "Detectify. (2016, April 28). Slack bot token leakage exposing business critical information. Retrieved October 19, 2020.", "source_name": "Detectify Slack Tokens"}, {"url": "https://www.forbes.com/sites/runasandvik/2014/01/14/attackers-scrape-github-for-cloud-service-credentials-hijack-account-to-mine-virtual-currency/#242c479d3196", "description": "Sandvik, R. (2014, January 14). Attackers Scrape GitHub For Cloud Service Credentials, Hijack Account To Mine Virtual Currency. Retrieved October 19, 2020.", "source_name": "Forbes GitHub Creds"}, {"url": "https://github.com/dxa4481/truffleHog", "description": "Dylan Ayrey. (2016, December 31). truffleHog. Retrieved October 19, 2020.", "source_name": "GitHub truffleHog"}, {"url": "https://github.com/michenriksen/gitrob", "description": "Michael Henriksen. (2018, June 9). Gitrob: Putting the Open Source in OSINT. Retrieved October 19, 2020.", "source_name": "GitHub Gitrob"}, {"url": "https://www.cnet.com/news/massive-breach-leaks-773-million-emails-21-million-passwords/", "description": "Ng, A. (2019, January 17). Massive breach leaks 773 million email addresses, 21 million passwords. Retrieved October 20, 2020.", "source_name": "CNET Leaks"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5112}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 4670}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 5068}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5036}}', 'T1589', 'Adversaries may gather information about the victim''s identity that can be used during targeting. Information about identities may include a variety of details, including personal data (ex: employee names, email addresses, etc.) as well as sensitive details such as credentials.
INSERT INTO public.attack_patterns VALUES (229, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:35.968622+00', NULL, 'attack-pattern--937e4772-8441-4e4a-8bf0-8d447d667e23', '[{"url": "https://attack.mitre.org/techniques/T1591", "external_id": "T1591", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/broadvoice-leaks-350m-records-voicemail-transcripts/160158/", "description": "Seals, T. (2020, October 15). Broadvoice Leak Exposes 350M Records, Personal Voicemail Transcripts. Retrieved October 20, 2020.", "source_name": "ThreatPost Broadvoice Leak"}, {"url": "https://www.dobsearch.com/business-lookup/", "description": "Concert Technologies . (n.d.). Business Lookup - Company Name Search. Retrieved October 20, 2020.", "source_name": "DOB Business Lookup"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3213}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2771}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3169}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3137}}', 'T1591', 'Adversaries may gather information about the victim''s organization that can be used during targeting. Information about an organization may include a variety of details, including the names of divisions/departments, specifics of business operations, as well as the roles and responsibilities of key employees.
INSERT INTO public.attack_patterns VALUES (232, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.102138+00', NULL, 'attack-pattern--8d7bd4f5-3a89-4453-9c82-2c8894d5655e', '[{"url": "https://attack.mitre.org/techniques/T1552/006", "external_id": "T1552.006", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/previous-versions/windows/it-pro/windows-server-2012-r2-and-2012/dn581922(v%3Dws.11)", "description": "Microsoft. (2016, August 31). Group Policy Preferences. Retrieved March 9, 2020.", "source_name": "Microsoft GPP 2016"}, {"url": "https://msdn.microsoft.com/library/cc422924.aspx", "description": "Microsoft. (n.d.). 2.2.1.1.4 Password Encryption. Retrieved April 11, 2018.", "source_name": "Microsoft GPP Key"}, {"url": "https://obscuresecurity.blogspot.co.uk/2012/05/gpp-password-retrieval-with-powershell.html", "description": "Campbell, C. (2012, May 24). GPP Password Retrieval with PowerShell. Retrieved April 11, 2018.", "source_name": "Obscuresecurity Get-GPPPassword"}, {"url": "https://adsecurity.org/?p=2288", "description": "Sean Metcalf. (2015, December 28). Finding Passwords in SYSVOL & Exploiting Group Policy Preferences. Retrieved February 17, 2020.", "source_name": "ADSecurity Finding Passwords in SYSVOL"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3405}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for attempts to access SYSVOL that involve searching for XML files. \\n\\nDeploy a new XML file with permissions set to Everyone:Deny and monitor for Access Denied errors.(Citation: ADSecurity Finding Passwords in SYSVOL)\"", "Str": "Monitor for attempts to access SYSVOL that involve searching for XML files. \n\nDeploy a new XML file with permissions set to Everyone:Deny and monitor for Access Denied errors.(Citation: ADSecurity Finding Passwords in SYSVOL)", "Type": 3, "Index": 3006}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3357}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2910}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3268}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3310}}', 'T1552.006', 'Adversaries may attempt to find unsecured credentials in Group Policy Preferences (GPP). GPP are tools that allow administrators to create domain policies with embedded credentials. These policies allow administrators to set local accounts.(Citation: Microsoft GPP 2016)
INSERT INTO public.attack_patterns VALUES (235, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.168414+00', NULL, 'attack-pattern--dfebc3b7-d19d-450b-81c7-6dafe4184c04', '[{"url": "https://attack.mitre.org/techniques/T1564/005", "external_id": "T1564.005", "source_name": "mitre-attack"}, {"url": "https://www.malwaretech.com/2014/11/virtual-file-systems-for-beginners.html", "description": "Hutchins, M. (2014, November 28). Virtual File Systems for Beginners. Retrieved June 22, 2020.", "source_name": "MalwareTech VFS Nov 2014"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/12/fin1-targets-boot-record.html", "description": "Andonov, D., et al. (2015, December 7). Thriving Beyond The Operating System: Financial Threat Group Targets Volume Boot Record. Retrieved May 13, 2016.", "source_name": "FireEye Bootkits"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2020/05/ESET_Turla_ComRAT.pdf", "description": "Faou, M. (2020, May). From Agent.btz to ComRAT v4: A ten-year journey. Retrieved June 15, 2020.", "source_name": "ESET ComRAT May 2020"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/08064459/Equation_group_questions_and_answers.pdf", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, February). Equation Group: Questions and Answers. Retrieved December 21, 2015.", "source_name": "Kaspersky Equation QA"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4507}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the use of a hidden file system may be exceptionally difficult depending on the implementation. Emphasis may be placed on detecting related aspects of the adversary lifecycle, such as how malware interacts with the hidden file system or how a hidden file system is loaded. Consider looking for anomalous interactions with the Registry or with a particular file on disk. Likewise, if the hidden file system is loaded on boot from reserved disk space, consider shifting focus to detecting [Bootkit](https://attack.mitre.org/techniques/T1542/003) activity.\"", "Str": "Detecting the use of a hidden file system may be exceptionally difficult depending on the implementation. Emphasis may be placed on detecting related aspects of the adversary lifecycle, such as how malware interacts with the hidden file system or how a hidden file system is loaded. Consider looking for anomalous interactions with the Registry or with a particular file on disk. Likewise, if the hidden file system is loaded on boot from reserved disk space, consider shifting focus to detecting [Bootkit](https://attack.mitre.org/techniques/T1542/003) activity.", "Type": 3, "Index": 3719}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4429}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Modification\",\n      \"Firmware: Firmware Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3551}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4317}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4359}}', 'T1564.005', 'Adversaries may use a hidden file system to conceal malicious activity from users and security tools. File systems provide a structure to store and access data from physical storage. Typically, a user engages with a file system through applications that allow them to access files and directories, which are an abstraction from their physical location (ex: disk sector). Standard file systems include FAT, NTFS, ext4, and APFS. File systems can also contain other structures, such as the Volume Boot Record (VBR) and Master File Table (MFT) in NTFS.(Citation: MalwareTech VFS Nov 2014)
INSERT INTO public.attack_patterns VALUES (237, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.248963+00', NULL, 'attack-pattern--8c4aef43-48d5-49aa-b2af-c0cd58d30c3d', '[{"url": "https://attack.mitre.org/techniques/T1564/002", "external_id": "T1564.002", "source_name": "mitre-attack"}, {"url": "http://go.cybereason.com/rs/996-YZT-709/images/Cybereason-Lab-Analysis-OSX-Pirrit-4-6-16.pdf", "description": "Amit Serper. (2016). Cybereason Lab Analysis OSX.Pirrit. Retrieved July 31, 2020.", "source_name": "Cybereason OSX Pirrit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2393}, "x_mitre_detection": {"Num": 0, "Raw": "\"This technique prevents the new user from showing up at the log in screen, but all of the other signs of a new user still exist. The user still gets a home directory and will appear in the authentication logs.\"", "Str": "This technique prevents the new user from showing up at the log in screen, but all of the other signs of a new user still exist. The user still gets a home directory and will appear in the authentication logs.", "Type": 3, "Index": 1991}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2347}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Creation\",\n      \"User Account: User Account Metadata\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1835}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2235}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2277}}', 'T1564.002', 'Adversaries may use hidden users to mask the presence of user accounts they create. Every user account in macOS has a userID associated with it. When creating a user, you can specify the userID for that account.
INSERT INTO public.attack_patterns VALUES (239, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.330193+00', NULL, 'attack-pattern--22905430-4901-4c2a-84f6-98243cb173f8', '[{"url": "https://attack.mitre.org/techniques/T1564", "external_id": "T1564", "source_name": "mitre-attack"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}, {"url": "http://go.cybereason.com/rs/996-YZT-709/images/Cybereason-Lab-Analysis-OSX-Pirrit-4-6-16.pdf", "description": "Amit Serper. (2016). Cybereason Lab Analysis OSX.Pirrit. Retrieved July 31, 2020.", "source_name": "Cybereason OSX Pirrit"}, {"url": "https://blog.malwarebytes.com/101/2015/07/introduction-to-alternate-data-streams/", "description": "Arntz, P. (2015, July 22). Introduction to Alternate Data Streams. Retrieved March 21, 2018.", "source_name": "MalwareBytes ADS July 2015"}, {"url": "https://news.sophos.com/en-us/2020/05/21/ragnar-locker-ransomware-deploys-virtual-machine-to-dodge-security/", "description": "SophosLabs. (2020, May 21). Ragnar Locker ransomware deploys virtual machine to dodge security. Retrieved June 29, 2020.", "source_name": "Sophos Ragnar May 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3867}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor files, processes, and command-line arguments for actions indicative of hidden artifacts. Monitor event and authentication logs for records of hidden artifacts being used. Monitor the file system and shell commands for hidden attribute usage.\"", "Str": "Monitor files, processes, and command-line arguments for actions indicative of hidden artifacts. Monitor event and authentication logs for records of hidden artifacts being used. Monitor the file system and shell commands for hidden attribute usage.", "Type": 3, "Index": 3473}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3789}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\",\n      \"User Account: User Account Creation\",\n      \"User Account: User Account Metadata\",\n      \"File: File Modification\",\n      \"Script: Script Execution\",\n      \"Process: OS API Execution\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Firmware: Firmware Modification\",\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Service: Service Creation\",\n      \"File: File Content\"\n    ]", "Str": "", "Type": 5, "Index": 2890}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3757}}', 'T1564', 'Adversaries may attempt to hide artifacts associated with their behaviors to evade detection. Operating systems may have features to hide various artifacts, such as important system files and administrative task execution, to avoid disrupting user work environments and prevent users from changing files or features on the system. Adversaries may abuse these features to hide artifacts such as files, directories, user accounts, or other system activity to evade detection.(Citation: Sofacy Komplex Trojan)(Citation: Cybereason OSX Pirrit)(Citation: MalwareBytes ADS July 2015)
INSERT INTO public.attack_patterns VALUES (241, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.477775+00', NULL, 'attack-pattern--0dda99f0-4701-48ca-9774-8504922e92d3', '[{"url": "https://attack.mitre.org/techniques/T1590/005", "external_id": "T1590.005", "source_name": "mitre-attack"}, {"url": "https://www.whois.net/", "description": "NTT America. (n.d.). Whois Lookup. Retrieved October 20, 2020.", "source_name": "WHOIS"}, {"url": "https://dnsdumpster.com/", "description": "Hacker Target. (n.d.). DNS Dumpster. Retrieved October 20, 2020.", "source_name": "DNS Dumpster"}, {"url": "https://www.circl.lu/services/passive-dns/", "description": "CIRCL Computer Incident Response Center. (n.d.). Passive DNS. Retrieved October 20, 2020.", "source_name": "Circl Passive DNS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3416}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2975}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3372}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3341}}', 'T1590.005', 'Adversaries may gather the victim''s IP addresses that can be used during targeting. Public IP addresses may be allocated to organizations by block, or a range of sequential addresses. Information about assigned IP addresses may include a variety of details, such as which IP addresses are in use. IP addresses may also enable an adversary to derive other details about a victim, such as organizational size, physical location(s), Internet service provider, and or where/how their publicly-facing infrastructure is hosted.
INSERT INTO public.attack_patterns VALUES (243, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.541363+00', NULL, 'attack-pattern--cc723aff-ec88-40e3-a224-5af9fd983cc4', '[{"url": "https://attack.mitre.org/techniques/T1591/004", "external_id": "T1591.004", "source_name": "mitre-attack"}, {"url": "https://threatpost.com/broadvoice-leaks-350m-records-voicemail-transcripts/160158/", "description": "Seals, T. (2020, October 15). Broadvoice Leak Exposes 350M Records, Personal Voicemail Transcripts. Retrieved October 20, 2020.", "source_name": "ThreatPost Broadvoice Leak"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2861}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2420}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2817}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2786}}', 'T1591.004', 'Adversaries may gather information about identities and roles within the victim organization that can be used during targeting. Information about business roles may reveal a variety of targetable details, including identifiable information for key personnel as well as what data/resources they have access to.
INSERT INTO public.attack_patterns VALUES (245, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.575179+00', NULL, 'attack-pattern--8f504411-cb96-4dac-a537-8d2bb7679c59', '[{"url": "https://attack.mitre.org/techniques/T1562/003", "external_id": "T1562.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/13.html", "external_id": "CAPEC-13", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/microsoft.powershell.core/about/about_history?view=powershell-7", "description": "Microsoft. (2020, May 13). About History. Retrieved September 4, 2020.", "source_name": "Microsoft PowerShell Command History"}, {"url": "https://community.sophos.com/products/intercept/early-access-program/f/live-discover-response-queries/121529/live-discover---powershell-command-audit", "description": "jak. (2020, June 27). Live Discover - PowerShell command audit. Retrieved August 21, 2020.", "source_name": "Sophos PowerShell command audit"}, {"url": "https://community.sophos.com/products/malware/b/blog/posts/powershell-command-history-forensics", "description": "Vikas, S. (2020, August 26). PowerShell Command History Forensics. Retrieved September 4, 2020.", "source_name": "Sophos PowerShell Command History Forensics"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 5430}, "x_mitre_detection": {"Num": 0, "Raw": "\"Correlating a user session with a distinct lack of new commands in their <code>.bash_history</code> can be a clue to suspicious behavior. Additionally, users checking or changing their <code>HISTCONTROL</code>, <code>HISTFILE</code>, or <code>HISTFILESIZE</code> environment variables may be suspicious.\\n\\nMonitor for modification of PowerShell command history settings through processes being created with <code>-HistorySaveStyle SaveNothing</code> command-line arguments and use of the PowerShell commands <code>Set-PSReadlineOption -HistorySaveStyle SaveNothing</code> and <code>Set-PSReadLineOption -HistorySavePath {File Path}</code>. \"", "Str": "Correlating a user session with a distinct lack of new commands in their <code>.bash_history</code> can be a clue to suspicious behavior. Additionally, users checking or changing their <code>HISTCONTROL</code>, <code>HISTFILE</code>, or <code>HISTFILESIZE</code> environment variables may be suspicious.\n\nMonitor for modification of PowerShell command history settings through processes being created with <code>-HistorySaveStyle SaveNothing</code> command-line arguments and use of the PowerShell commands <code>Set-PSReadlineOption -HistorySaveStyle SaveNothing</code> and <code>Set-PSReadLineOption -HistorySavePath {File Path}</code>. ", "Type": 3, "Index": 4587}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5352}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vikas Singh, Sophos\",\n      \"Emile Kenning, Sophos\"\n    ]", "Str": "", "Type": 5, "Index": 4292}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4388}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5263}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Host forensic analysis\",\n      \"Log analysis\"\n    ]", "Str": "", "Type": 5, "Index": 4500}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5305}}', 'T1562.003', 'Adversaries may impair command history logging to hide commands they run on a compromised system. Various command interpreters keep track of the commands users type in their terminal so that users can retrace what they''ve done. 
INSERT INTO public.attack_patterns VALUES (247, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.660306+00', NULL, 'attack-pattern--4fd8a28b-4b3a-4cd6-a8cf-85ba5f824a7f', '[{"url": "https://attack.mitre.org/techniques/T1525", "external_id": "T1525", "source_name": "mitre-attack"}, {"url": "https://rhinosecuritylabs.com/aws/cloud-container-attack-tool/", "description": "Rhino Labs. (2019, August). Exploiting AWS ECR and ECS with the Cloud Container Attack Tool (CCAT). Retrieved September 12, 2019.", "source_name": "Rhino Labs Cloud Image Backdoor Technique Sept 2019"}, {"url": "https://github.com/RhinoSecurityLabs/ccat", "description": "Rhino Labs. (2019, September). Cloud Container Attack Tool (CCAT). Retrieved September 12, 2019.", "source_name": "Rhino Labs Cloud Backdoor September 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3315}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor interactions with images and containers by users to identify ones that are added or modified anomalously.\\n\\nIn containerized environments, changes may be detectable by monitoring the Docker daemon logs or setting up and monitoring Kubernetes audit logs depending on registry configuration. \"", "Str": "Monitor interactions with images and containers by users to identify ones that are added or modified anomalously.\n\nIn containerized environments, changes may be detectable by monitoring the Docker daemon logs or setting up and monitoring Kubernetes audit logs depending on registry configuration. ", "Type": 3, "Index": 2826}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 3250}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yossi Weizman, Azure Defender Research Team\",\n      \"Vishwas Manral, McAfee\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2586}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Image: Image Creation\",\n      \"Image: Image Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2727}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3160}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3203}}', 'T1525', 'Adversaries may implant cloud or container images with malicious code to establish persistence after gaining access to an environment. Amazon Web Services (AWS) Amazon Machine Images (AMIs), Google Cloud Platform (GCP) Images, and Azure Images as well as popular container runtimes such as Docker can be implanted or backdoored. Unlike [Upload Malware](https://attack.mitre.org/techniques/T1608/001), this technique focuses on adversaries implanting an image in a registry within a victim’s environment. Depending on how the infrastructure is provisioned, this could provide persistent access if the infrastructure provisioning tool is instructed to always use the latest image.(Citation: Rhino Labs Cloud Image Backdoor Technique Sept 2019)
INSERT INTO public.attack_patterns VALUES (249, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.73175+00', NULL, 'attack-pattern--b0533c6e-8fea-4788-874f-b799cacc4b92', '[{"url": "https://attack.mitre.org/techniques/T1027/005", "external_id": "T1027.005", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2422}, "x_mitre_detection": {"Num": 0, "Raw": "\"The first detection of a malicious tool may trigger an anti-virus or other security tool alert. Similar events may also occur at the boundary through network IDS, email scanning appliance, etc. The initial detection should be treated as an indication of a potentially more invasive intrusion. The alerting system should be thoroughly investigated beyond that initial alert for activity that was not detected. Adversaries may continue with an operation, assuming that individual events like an anti-virus detect will not be investigated or that an analyst will not be able to conclusively link that event to other activity occurring on the network.\"", "Str": "The first detection of a malicious tool may trigger an anti-virus or other security tool alert. Similar events may also occur at the boundary through network IDS, email scanning appliance, etc. The initial detection should be treated as an indication of a potentially more invasive intrusion. The alerting system should be thoroughly investigated beyond that initial alert for activity that was not detected. Adversaries may continue with an operation, assuming that individual events like an anti-virus detect will not be investigated or that an analyst will not be able to conclusively link that event to other activity occurring on the network.", "Type": 3, "Index": 1631}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2344}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2313}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Host intrusion prevention systems\",\n      \"Log analysis\",\n      \"Signature-based detection\"\n    ]", "Str": "", "Type": 5, "Index": 1478}}', 'T1027.005', 'Adversaries may remove indicators from tools if they believe their malicious tool was detected, quarantined, or otherwise curtailed. They can modify the tool by removing the indicator and using the updated version that is no longer detected by the target''s defensive systems or subsequent targets that may use similar systems.
INSERT INTO public.attack_patterns VALUES (252, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.831953+00', NULL, 'attack-pattern--e6919abc-99f9-4c6c-95a5-14761e7b2add', '[{"url": "https://attack.mitre.org/techniques/T1105", "external_id": "T1105", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2631}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for file creation and files transferred into the network. Unusual processes with external network connections creating files on-system may be suspicious. Use of utilities, such as FTP, that does not normally occur may also be suspicious.\\n\\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Monitor for file creation and files transferred into the network. Unusual processes with external network connections creating files on-system may be suspicious. Use of utilities, such as FTP, that does not normally occur may also be suspicious.\n\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1756}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2553}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1543}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2463}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2506}}', 'T1105', 'Adversaries may transfer tools or other files from an external system into a compromised environment. Files may be copied from an external adversary controlled system through the command and control channel to bring tools into the victim network or through alternate protocols with another tool such as FTP. Files can also be copied over on Mac and Linux with native tools like scp, rsync, and sftp.', 'Ingress Tool Transfer', 0);
INSERT INTO public.attack_patterns VALUES (253, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.865451+00', NULL, 'attack-pattern--f5d8eed6-48a9-4cdf-a3d7-d1ffa99c3d2a', '[{"url": "https://attack.mitre.org/techniques/T1490", "external_id": "T1490", "source_name": "mitre-attack"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/05/wannacry-malware-profile.html", "description": "Berry, A., Homan, J., and Eitzman, R. (2017, May 23). WannaCry Malware Profile. Retrieved March 15, 2019.", "source_name": "FireEye WannaCry 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4093}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and command line parameters of binaries involved in inhibiting system recovery, such as vssadmin, wbadmin, and bcdedit. The Windows event logs, ex. Event ID 524 indicating a system catalog was deleted, may contain entries associated with suspicious activity.\\n\\nMonitor the status of services involved in system recovery. Monitor the registry for changes associated with system recovery features (ex: the creation of <code>HKEY_CURRENT_USER\\\\Software\\\\Policies\\\\Microsoft\\\\PreviousVersions\\\\DisableLocalPage</code>).\"", "Str": "Use process monitoring to monitor the execution and command line parameters of binaries involved in inhibiting system recovery, such as vssadmin, wbadmin, and bcdedit. The Windows event logs, ex. Event ID 524 indicating a system catalog was deleted, may contain entries associated with suspicious activity.\n\nMonitor the status of services involved in system recovery. Monitor the registry for changes associated with system recovery features (ex: the creation of <code>HKEY_CURRENT_USER\\Software\\Policies\\Microsoft\\PreviousVersions\\DisableLocalPage</code>).", "Type": 3, "Index": 3216}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 4015}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 3811}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Yonatan Gotlib, Deep Instinct\"\n    ]", "Str": "", "Type": 5, "Index": 2912}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Deletion\"\n    ]", "Str": "", "Type": 5, "Index": 2987}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3872}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"root\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3915}}', 'T1490', 'Adversaries may delete or remove built-in operating system data and turn off services designed to aid in the recovery of a corrupted system to prevent recovery.(Citation: Talos Olympic Destroyer 2018)(Citation: FireEye WannaCry 2017) Operating systems may contain features that can help fix corrupted systems, such as a backup catalog, volume shadow copies, and automatic repair features. Adversaries may disable or delete system recovery features to augment the effects of [Data Destruction](https://attack.mitre.org/techniques/T1485) and [Data Encrypted for Impact](https://attack.mitre.org/techniques/T1486).(Citation: Talos Olympic Destroyer 2018)(Citation: FireEye WannaCry 2017)
INSERT INTO public.attack_patterns VALUES (255, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:36.939241+00', NULL, 'attack-pattern--c071d8c1-3b3a-4f22-9407-ca4e96921069', '[{"url": "https://attack.mitre.org/techniques/T1608/003", "external_id": "T1608.003", "source_name": "mitre-attack"}, {"url": "https://www.digicert.com/kb/ssl-certificate-installation.htm", "description": "DigiCert. (n.d.). How to Install an SSL Certificate. Retrieved April 19, 2021.", "source_name": "DigiCert Install SSL Cert"}, {"url": "https://www.splunk.com/en_us/blog/security/tall-tales-of-hunting-with-tls-ssl-certificates.html", "description": "Kovar, R. (2017, December 11). Tall Tales of Hunting with TLS/SSL Certificates. Retrieved October 16, 2020.", "source_name": "Splunk Kovar Certificates 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3713}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider use of services that may aid in the tracking of certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017)\\n\\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001) or [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002).\"", "Str": "Consider use of services that may aid in the tracking of certificates in use on sites across the Internet. In some cases it may be possible to pivot on known pieces of certificate information to uncover other adversary infrastructure.(Citation: Splunk Kovar Certificates 2017)\n\nDetection efforts may be focused on related behaviors, such as [Web Protocols](https://attack.mitre.org/techniques/T1071/001) or [Asymmetric Cryptography](https://attack.mitre.org/techniques/T1573/002).", "Type": 3, "Index": 3121}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3669}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3638}}', 'T1608.003', 'Adversaries may install SSL/TLS certificates that can be used during targeting. SSL/TLS certificates are files that can be installed on servers to enable secure communications between systems. Digital certificates include information about the key, information about its owner''s identity, and the digital signature of an entity that has verified the certificate''s contents are correct. If the signature is valid, and the person examining the certificate trusts the signer, then they know they can use that key to communicate securely with its owner. Certificates can be uploaded to a server, then the server can be configured to use the certificate to enable encrypted communication with it.(Citation: DigiCert Install SSL Cert)
INSERT INTO public.attack_patterns VALUES (258, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.040237+00', NULL, 'attack-pattern--acd0ba37-7ba9-4cc5-ac61-796586cd856d', '[{"url": "https://attack.mitre.org/techniques/T1559", "external_id": "T1559", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2202}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for strings in files/commands, loaded DLLs/libraries, or spawned processes that are associated with abuse of IPC mechanisms.\"", "Str": "Monitor for strings in files/commands, loaded DLLs/libraries, or spawned processes that are associated with abuse of IPC mechanisms.", "Type": 3, "Index": 1858}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2154}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Module: Module Load\",\n      \"Process: Process Creation\",\n      \"Script: Script Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1727}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2025}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 2068}}', 'T1559', 'Adversaries may abuse inter-process communication (IPC) mechanisms for local code or command execution. IPC is typically used by processes to share data, communicate with each other, or synchronize execution. IPC is also commonly used to avoid situations such as deadlocks, which occurs when processes are stuck in a cyclic waiting pattern. 
INSERT INTO public.attack_patterns VALUES (259, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.077273+00', NULL, 'attack-pattern--8c41090b-aa47-4331-986b-8c9a51a91103', '[{"url": "https://attack.mitre.org/techniques/T1491/001", "external_id": "T1491.001", "source_name": "mitre-attack"}, {"url": "https://www.operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Unraveling the Long Thread of the Sony Attack. Retrieved February 25, 2016.", "source_name": "Novetta Blockbuster"}, {"url": "https://operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Destructive-Malware-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Destructive Malware Report. Retrieved March 2, 2016.", "source_name": "Novetta Blockbuster Destructive Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2967}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor internal and websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.\"", "Str": "Monitor internal and websites for unplanned content changes. Monitor application logs for abnormal behavior that may indicate attempted or successful exploitation. Use deep packet inspection to look for artifacts of common exploit traffic, such as SQL injection. Web Application Firewalls may detect improper inputs attempting exploitation.", "Type": 3, "Index": 2429}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2889}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Integrity\"\n    ]", "Str": "", "Type": 5, "Index": 2800}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"File: File Creation\",\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2234}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2858}}', 'T1491.001', 'An adversary may deface systems internal to an organization in an attempt to intimidate or mislead users. This may take the form of modifications to internal websites, or directly to user systems with the replacement of the desktop wallpaper.(Citation: Novetta Blockbuster) Disturbing or offensive images may be used as a part of [Internal Defacement](https://attack.mitre.org/techniques/T1491/001) in order to cause user discomfort, or to pressure compliance with accompanying messages. Since internally defacing systems exposes an adversary''s presence, it often takes place after other intrusion goals have been accomplished.(Citation: Novetta Blockbuster Destructive Malware)', 'Internal Defacement', 129);
INSERT INTO public.attack_patterns VALUES (262, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.211742+00', NULL, 'attack-pattern--132d5b37-aac5-4378-a8dc-3127b18a73dc', '[{"url": "https://attack.mitre.org/techniques/T1016/001", "external_id": "T1016.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2112}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Command and Control, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to check Internet connectivity.\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Command and Control, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to check Internet connectivity.", "Type": 3, "Index": 1493}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2034}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1389}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1945}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 1987}}', 'T1016.001', 'Adversaries may check for Internet connectivity on compromised systems. This may be performed during automated discovery and can be accomplished in numerous ways such as using [Ping](https://attack.mitre.org/software/S0097), <code>tracert</code>, and GET requests to websites.
INSERT INTO public.attack_patterns VALUES (267, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.379498+00', NULL, 'attack-pattern--a1b52199-c8c5-438a-9ded-656f1d0888c6', '[{"url": "https://attack.mitre.org/techniques/T1547/006", "external_id": "T1547.006", "source_name": "mitre-attack"}, {"url": "https://www.tldp.org/LDP/lkmpg/2.4/lkmpg.pdf", "description": "Pomerantz, O., Salzman, P.. (2003, April 4). The Linux Kernel Module Programming Guide. Retrieved April 6, 2018.", "source_name": "Linux Kernel Programming"}, {"url": "http://www.tldp.org/LDP/lkmpg/2.4/html/x437.html", "description": "Pomerantz, O., Salzman, P. (2003, April 4). Modules vs Programs. Retrieved April 6, 2018.", "source_name": "Linux Kernel Module Programming Guide"}, {"url": "http://www.megasecurity.org/papers/Rootkits.pdf", "description": "Chuvakin, A. (2003, February). An Overview of Rootkits. Retrieved April 6, 2018.", "source_name": "iDefense Rootkit Overview"}, {"url": "https://developer.apple.com/support/kernel-extensions/", "description": "Apple. (n.d.). Deprecated Kernel Extensions and System Extension Alternatives. Retrieved November 4, 2020.", "source_name": "Apple Kernel Extension Deprecation"}, {"url": "https://volatility-labs.blogspot.com/2012/10/phalanx-2-revealed-using-volatility-to.html", "description": "Case, A. (2012, October 10). Phalanx 2 Revealed: Using Volatility to Analyze an Advanced Linux Rootkit. Retrieved April 9, 2018.", "source_name": "Volatility Phalanx2"}, {"url": "https://www.crowdstrike.com/blog/http-iframe-injecting-linux-rootkit/", "description": "Kurtz, G. (2012, November 19). HTTP iframe Injecting Linux Rootkit. Retrieved December 21, 2017.", "source_name": "CrowdStrike Linux Rootkit"}, {"url": "https://github.com/f0rb1dd3n/Reptile", "description": "Augusto, I. (2018, March 8). Reptile - LMK Linux rootkit. Retrieved April 9, 2018.", "source_name": "GitHub Reptile"}, {"url": "https://github.com/m0nad/Diamorphine", "description": "Mello, V. (2018, March 8). Diamorphine - LMK rootkit for Linux Kernels 2.6.x/3.x/4.x (x86 and x86_64). Retrieved April 9, 2018.", "source_name": "GitHub Diamorphine"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Wardle, P. (2015, April). Malware Persistence on OS X Yosemite. Retrieved April 6, 2018.", "source_name": "RSAC 2015 San Francisco Patrick Wardle"}, {"url": "https://www.synack.com/2017/09/08/high-sierras-secure-kernel-extension-loading-is-broken/", "description": "Wardle, P. (2017, September 8). High Sierra’s ‘Secure Kernel Extension Loading’ is Broken. Retrieved April 6, 2018.", "source_name": "Synack Secure Kernel Extension Broken"}, {"url": "https://securelist.com/the-ventir-trojan-assemble-your-macos-spy/67267/", "description": "Mikhail, K. (2014, October 16). The Ventir Trojan: assemble your MacOS spy. Retrieved April 6, 2018.", "source_name": "Securelist Ventir"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/skidmap-linux-malware-uses-rootkit-capabilities-to-hide-cryptocurrency-mining-payload/", "description": "Remillano, A., Urbanec, J. (2019, September 19). Skidmap Linux Malware Uses Rootkit Capabilities to Hide Cryptocurrency-Mining Payload. Retrieved June 4, 2020.", "source_name": "Trend Micro Skidmap"}, {"url": "http://tldp.org/HOWTO/Module-HOWTO/x197.html", "description": "Henderson, B. (2006, September 24). How To Insert And Remove LKMs. Retrieved April 9, 2018.", "source_name": "Linux Loadable Kernel Module Insert and Remove LKMs"}, {"url": "https://en.wikipedia.org/wiki/Loadable_kernel_module#Linux", "description": "Wikipedia. (2018, March 17). Loadable kernel module. Retrieved April 9, 2018.", "source_name": "Wikipedia Loadable Kernel Module"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 8673}, "x_mitre_detection": {"Num": 0, "Raw": "\"Loading, unloading, and manipulating modules on Linux systems can be detected by monitoring for the following commands:<code>modprobe</code>, <code>insmod</code>, <code>lsmod</code>, <code>rmmod</code>, or <code>modinfo</code> (Citation: Linux Loadable Kernel Module Insert and Remove LKMs) LKMs are typically loaded into <code>/lib/modules</code> and have had the extension .ko (\\\"kernel object\\\") since version 2.6 of the Linux kernel. (Citation: Wikipedia Loadable Kernel Module)\\n\\nFor macOS, monitor for execution of <code>kextload</code> commands and correlate with other unknown or suspicious activity.\\n\\nAdversaries may run commands on the target system before loading a malicious module in order to ensure that it is properly compiled. (Citation: iDefense Rootkit Overview) Adversaries may also execute commands to identify the exact version of the running Linux kernel and/or download multiple versions of the same .ko (kernel object) files to use the one appropriate for the running system.(Citation: Trend Micro Skidmap) Many LKMs require Linux headers (specific to the target kernel) in order to compile properly. These are typically obtained through the operating systems package manager and installed like a normal package. On Ubuntu and Debian based systems this can be accomplished by running: <code>apt-get install linux-headers-$(uname -r)</code> On RHEL and CentOS based systems this can be accomplished by running: <code>yum install kernel-devel-$(uname -r)</code>\"", "Str": "Loading, unloading, and manipulating modules on Linux systems can be detected by monitoring for the following commands:<code>modprobe</code>, <code>insmod</code>, <code>lsmod</code>, <code>rmmod</code>, or <code>modinfo</code> (Citation: Linux Loadable Kernel Module Insert and Remove LKMs) LKMs are typically loaded into <code>/lib/modules</code> and have had the extension .ko (\"kernel object\") since version 2.6 of the Linux kernel. (Citation: Wikipedia Loadable Kernel Module)\n\nFor macOS, monitor for execution of <code>kextload</code> commands and correlate with other unknown or suspicious activity.\n\nAdversaries may run commands on the target system before loading a malicious module in order to ensure that it is properly compiled. (Citation: iDefense Rootkit Overview) Adversaries may also execute commands to identify the exact version of the running Linux kernel and/or download multiple versions of the same .ko (kernel object) files to use the one appropriate for the running system.(Citation: Trend Micro Skidmap) Many LKMs require Linux headers (specific to the target kernel) in order to compile properly. These are typically obtained through the operating systems package manager and installed like a normal package. On Ubuntu and Debian based systems this can be accomplished by running: <code>apt-get install linux-headers-$(uname -r)</code> On RHEL and CentOS based systems this can be accomplished by running: <code>yum install kernel-devel-$(uname -r)</code>", "Type": 3, "Index": 7001}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 8612}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Wayne Silva, F-Secure Countercept\",\n      \"Anastasios Pingios\",\n      \"Jeremy Galloway\",\n      \"Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 6715}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Creation\",\n      \"Kernel: Kernel Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 6867}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 8523}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 8565}}', 'T1547.006', 'Adversaries may modify the kernel to automatically execute programs on system boot. Loadable Kernel Modules (LKMs) are pieces of code that can be loaded and unloaded into the kernel upon demand. They extend the functionality of the kernel without the need to reboot the system. For example, one type of module is the device driver, which allows the kernel to access hardware connected to the system. (Citation: Linux Kernel Programming) 
INSERT INTO public.attack_patterns VALUES (272, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.513497+00', NULL, 'attack-pattern--1ecfdab8-7d59-4c98-95d4-dc41970f57fc', '[{"url": "https://attack.mitre.org/techniques/T1003/004", "external_id": "T1003.004", "source_name": "mitre-attack"}, {"url": "https://www.passcape.com/index.php?section=docsys&cmd=details&id=23", "description": "Passcape. (n.d.). Windows LSA secrets. Retrieved February 21, 2020.", "source_name": "Passcape LSA Secrets"}, {"url": "https://docs.microsoft.com/en-us/windows-server/identity/securing-privileged-access/securing-privileged-access-reference-material?redirectedfrom=MSDN", "description": "Microsoft. (2019, February 14). Active Directory administrative tier model. Retrieved February 21, 2020.", "source_name": "Microsoft AD Admin Tier Model"}, {"url": "https://www.first.org/resources/papers/conf2017/Windows-Credentials-Attacks-and-Mitigation-Techniques.pdf", "description": "Chad Tilbury. (2017, August 8). 1Windows Credentials: Attack, Mitigation, Defense. Retrieved February 21, 2020.", "source_name": "Tilbury Windows Credentials"}, {"url": "https://ired.team/offensive-security/credential-access-and-credential-dumping/dumping-lsa-secrets", "description": "Mantvydas Baranauskas. (2019, November 16). Dumping LSA Secrets. Retrieved February 21, 2020.", "source_name": "ired Dumping LSA Secrets"}, {"url": "https://github.com/mattifestation/PowerSploit", "description": "PowerSploit. (n.d.). Retrieved December 4, 2014.", "source_name": "Powersploit"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3870}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.\"", "Str": "Monitor processes and command-line arguments for program execution that may be indicative of credential dumping. Remote access tools may contain built-in features or incorporate existing tools like Mimikatz. PowerShell scripts also exist that contain credential dumping functionality, such as PowerSploit''s Invoke-Mimikatz module,(Citation: Powersploit) which may require additional logging features to be configured in the operating system to collect necessary information for analysis.", "Type": 3, "Index": 3209}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3822}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 3005}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Windows Registry: Windows Registry Key Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3085}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3731}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3773}}', 'T1003.004', 'Adversaries with SYSTEM access to a host may attempt to access Local Security Authority (LSA) secrets, which can contain a variety of different credential materials, such as credentials for service accounts.(Citation: Passcape LSA Secrets)(Citation: Microsoft AD Admin Tier Model)(Citation: Tilbury Windows Credentials) LSA secrets are stored in the registry at <code>HKEY_LOCAL_MACHINE\SECURITY\Policy\Secrets</code>. LSA secrets can also be dumped from memory.(Citation: ired Dumping LSA Secrets)
INSERT INTO public.attack_patterns VALUES (275, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.617563+00', NULL, 'attack-pattern--bf90d72c-c00b-45e3-b3aa-68560560d4c5', '[{"url": "https://attack.mitre.org/techniques/T1570", "external_id": "T1570", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2423}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for file creation and files transferred within a network using protocols such as SMB. Unusual processes with internal network connections creating files on-system may be suspicious. Consider monitoring for abnormal usage of utilities and command-line arguments that may be used in support of remote transfer of files. Considering monitoring for alike file hashes or characteristics (ex: filename) that are created on multiple hosts.\"", "Str": "Monitor for file creation and files transferred within a network using protocols such as SMB. Unusual processes with internal network connections creating files on-system may be suspicious. Consider monitoring for abnormal usage of utilities and command-line arguments that may be used in support of remote transfer of files. Considering monitoring for alike file hashes or characteristics (ex: filename) that are created on multiple hosts.", "Type": 3, "Index": 1780}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2345}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 1521}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2255}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2298}}', 'T1570', 'Adversaries may transfer tools or other files between systems in a compromised environment. Files may be copied from one system to another to stage adversary tools or other files over the course of an operation. Adversaries may copy files laterally between internal victim systems to support lateral movement using inherent file sharing protocols such as file sharing over SMB to connected network shares or with authenticated connections with [SMB/Windows Admin Shares](https://attack.mitre.org/techniques/T1021/002) or [Remote Desktop Protocol](https://attack.mitre.org/techniques/T1021/001). Files can also be copied over on Mac and Linux with native tools like scp, rsync, and sftp.', 'Lateral Tool Transfer', 0);
INSERT INTO public.attack_patterns VALUES (277, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.690978+00', NULL, 'attack-pattern--573ad264-1371-4ae0-8482-d2673b719dba', '[{"url": "https://attack.mitre.org/techniques/T1543/004", "external_id": "T1543.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/550.html", "external_id": "CAPEC-550", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/551.html", "external_id": "CAPEC-551", "source_name": "capec"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLaunchdJobs.html", "description": "Apple. (n.d.). Creating Launch Daemons and Agents. Retrieved July 10, 2017.", "source_name": "AppleDocs Launch Agent Daemons"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}, {"url": "https://www.synack.com/wp-content/uploads/2016/03/RSA_OSX_Malware.pdf", "description": "Patrick Wardle. (2016, February 29). Let''s Play Doctor: Practical OS X Malware Detection & Analysis. Retrieved July 10, 2017.", "source_name": "OSX Malware Detection"}, {"url": "https://www.paloaltonetworks.com/content/dam/pan/en_US/assets/pdf/reports/Unit_42/unit42-wirelurker.pdf", "description": "Claud Xiao. (n.d.). WireLurker: A New Era in iOS and OS X Malware. Retrieved July 10, 2017.", "source_name": "WireLurker"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4519}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for launch daemon creation or modification through plist files and utilities such as Objective-See''s KnockKnock application. \"", "Str": "Monitor for launch daemon creation or modification through plist files and utilities such as Objective-See''s KnockKnock application. ", "Type": 3, "Index": 4148}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4473}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Service: Service Creation\",\n      \"Service: Service Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3908}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4375}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4417}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4322}}', 'T1543.004', 'Adversaries may create or modify launch daemons to repeatedly execute malicious payloads as part of persistence. Per Apple’s developer documentation, when macOS and OS X boot up, launchd is run to finish system initialization. This process loads the parameters for each launch-on-demand system-level daemon from the property list (plist) files found in <code>/System/Library/LaunchDaemons</code> and <code>/Library/LaunchDaemons</code> (Citation: AppleDocs Launch Agent Daemons). These LaunchDaemons have property list files which point to the executables that will be launched (Citation: Methods of Mac Malware Persistence). 
INSERT INTO public.attack_patterns VALUES (279, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.759154+00', NULL, 'attack-pattern--8faedf87-dceb-4c35-b2a2-7286f59a3bc3', '[{"url": "https://attack.mitre.org/techniques/T1053/004", "external_id": "T1053.004", "source_name": "mitre-attack"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLaunchdJobs.html", "description": "Apple. (n.d.). Creating Launch Daemons and Agents. Retrieved July 10, 2017.", "source_name": "AppleDocs Launch Agent Daemons"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3875}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \\n\\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor scheduled task creation from common utilities using command-line invocation. Legitimate scheduled tasks may be created during installation of new software or through system administration functions. Look for changes to tasks that do not correlate with known software, patch cycles, etc. \n\nSuspicious program execution through scheduled tasks may show up as outlier processes that have not been seen before when compared against historical data. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 2953}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 3792}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2769}, "x_mitre_remote_support": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3845}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3703}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 3745}}', 'T1053.004', 'Adversaries may abuse the <code>Launchd</code> daemon to perform task scheduling for initial or recurring execution of malicious code. The <code>launchd</code> daemon, native to macOS, is responsible for loading and maintaining services within the operating system. This process loads the parameters for each launch-on-demand system-level daemon from the property list (plist) files found in <code>/System/Library/LaunchDaemons</code> and <code>/Library/LaunchDaemons</code> (Citation: AppleDocs Launch Agent Daemons). These LaunchDaemons have property list files which point to the executables that will be launched (Citation: Methods of Mac Malware Persistence).
INSERT INTO public.attack_patterns VALUES (282, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.86545+00', NULL, 'attack-pattern--635cbe30-392d-4e27-978e-66774357c762', '[{"url": "https://attack.mitre.org/techniques/T1136/001", "external_id": "T1136.001", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/event-4720", "description": "Lich, B., Miroshnikov, A. (2017, April 5). 4720(S): A user account was created. Retrieved June 30, 2017.", "source_name": "Microsoft User Creation Event"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2440}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for processes and command-line parameters associated with local account creation, such as <code>net user /add</code> or <code>useradd</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows system. (Citation: Microsoft User Creation Event) Perform regular audits of local system accounts to detect suspicious accounts that may have been created by an adversary.\"", "Str": "Monitor for processes and command-line parameters associated with local account creation, such as <code>net user /add</code> or <code>useradd</code>. Collect data on account creation within a network. Event ID 4720 is generated when a user account is created on a Windows system. (Citation: Microsoft User Creation Event) Perform regular audits of local system accounts to detect suspicious accounts that may have been created by an adversary.", "Type": 3, "Index": 1786}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2362}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Creation\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1637}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2264}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2306}}', 'T1136.001', 'Adversaries may create a local account to maintain access to victim systems. Local accounts are those configured by an organization for use by users, remote support, services, or for administration on a single system or service. With a sufficient level of access, the <code>net user /add</code> command can be used to create a local account.
INSERT INTO public.attack_patterns VALUES (284, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:37.940355+00', NULL, 'attack-pattern--fdc47f44-dd32-4b99-af5f-209f556f63c2', '[{"url": "https://attack.mitre.org/techniques/T1078/003", "external_id": "T1078.003", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2419}, "x_mitre_detection": {"Num": 0, "Raw": "\"Perform regular audits of local system accounts to detect accounts that may have been created by an adversary for persistence. Look for suspicious account behavior, such as accounts logged in at odd times or outside of business hours.\"", "Str": "Perform regular audits of local system accounts to detect accounts that may have been created by an adversary for persistence. Look for suspicious account behavior, such as accounts logged in at odd times or outside of business hours.", "Type": 3, "Index": 1940}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 2321}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1809}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2209}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2251}}', 'T1078.003', 'Adversaries may obtain and abuse credentials of a local account as a means of gaining Initial Access, Persistence, Privilege Escalation, or Defense Evasion. Local accounts are those configured by an organization for use by users, remote support, services, or for administration on a single system or service.
INSERT INTO public.attack_patterns VALUES (287, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.046778+00', NULL, 'attack-pattern--a01bf75f-00b2-4568-a58f-565ff9bf202b', '[{"url": "https://attack.mitre.org/techniques/T1069/001", "external_id": "T1069.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2479}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 1518}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2401}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1414}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2312}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2354}}', 'T1069.001', 'Adversaries may attempt to find local system groups and permission settings. The knowledge of local system permission groups can help adversaries determine which groups exist and which users belong to a particular group. Adversaries may use this information to determine which users have elevated permissions, such as the users found within the local administrators group.
INSERT INTO public.attack_patterns VALUES (291, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.205298+00', NULL, 'attack-pattern--54b4c251-1f0e-4eba-ba6b-dbc7a6f6f06b', '[{"url": "https://attack.mitre.org/techniques/T1071/003", "external_id": "T1071.003", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2687}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2044}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2609}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1914}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2578}}', 'T1071.003', 'Adversaries may communicate using application layer protocols associated with electronic mail delivery to avoid detection/network filtering by blending in with existing traffic. Commands to the remote system, and often the results of those commands, will be embedded within the protocol traffic between the client and server. 
INSERT INTO public.attack_patterns VALUES (302, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.593875+00', NULL, 'attack-pattern--42e8de7b-37b2-4258-905a-6897815e58e0', '[{"url": "https://attack.mitre.org/techniques/T1036", "external_id": "T1036", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/177.html", "external_id": "CAPEC-177", "source_name": "capec"}, {"url": "https://lolbas-project.github.io/", "description": "LOLBAS. (n.d.). Living Off The Land Binaries and Scripts (and also Libraries). Retrieved February 10, 2020.", "source_name": "LOLBAS Main Site"}, {"url": "http://pages.endgame.com/rs/627-YBU-612/images/EndgameJournal_The%20Masquerade%20Ball_Pages_R2.pdf", "description": "Ewing, P. (2016, October 31). How to Hunt: The Masquerade Ball. Retrieved October 31, 2016.", "source_name": "Elastic Masquerade Ball"}, {"url": "https://twitter.com/ItsReallyNick/status/1055321652777619457", "description": "Carr, N.. (2018, October 25). Nick Carr Status Update Masquerading. Retrieved April 22, 2019.", "source_name": "Twitter ItsReallyNick Masquerading Update"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.4\"", "Str": "1.4", "Type": 3, "Index": 4453}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect file hashes; file names that do not match their expected hash are suspect. Perform file monitoring; files with known names but in unusual locations are suspect. Likewise, files that are modified outside of an update or patch are suspect.\\n\\nIf file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)\\n\\nLook for indications of common characters that may indicate an attempt to trick users into misidentifying the file type, such as a space as the last character of a file name or the right-to-left override characters\\\"\\\\u202E\\\", \\\"[U+202E]\\\", and \\\"%E2%80%AE”.\"", "Str": "Collect file hashes; file names that do not match their expected hash are suspect. Perform file monitoring; files with known names but in unusual locations are suspect. Likewise, files that are modified outside of an update or patch are suspect.\n\nIf file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)\n\nLook for indications of common characters that may indicate an attempt to trick users into misidentifying the file type, such as a space as the last character of a file name or the right-to-left override characters\"\\u202E\", \"[U+202E]\", and \"%E2%80%AE”.", "Type": 3, "Index": 3077}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Containers\"\n    ]", "Str": "", "Type": 5, "Index": 4355}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Oleg Kolesnikov, Securonix\",\n      \"Nick Carr, FireEye\",\n      \"David Lu, Tripwire\",\n      \"Felipe Espósito, @Pr0teus\",\n      \"Elastic\",\n      \"Bartosz Jerzman\"\n    ]", "Str": "", "Type": 5, "Index": 2416}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Image: Image Metadata\",\n      \"Command: Command Execution\",\n      \"Service: Service Metadata\",\n      \"Service: Service Creation\",\n      \"Scheduled Job: Scheduled Job Metadata\",\n      \"Scheduled Job: Scheduled Job Modification\",\n      \"File: File Metadata\",\n      \"Process: Process Metadata\",\n      \"File: File Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2622}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4323}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control by file name or path\"\n    ]", "Str": "", "Type": 5, "Index": 2994}}', 'T1036', 'Adversaries may attempt to manipulate features of their artifacts to make them appear legitimate or benign to users and/or security tools. Masquerading occurs when the name or location of an object, legitimate or malicious, is manipulated or abused for the sake of evading defenses and observation. This may include manipulating file metadata, tricking users into misidentifying the file type, and giving legitimate task or service names.
INSERT INTO public.attack_patterns VALUES (293, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.244495+00', NULL, 'attack-pattern--232b7f21-adf9-4b42-b936-b9d6f7df856e', '[{"url": "https://attack.mitre.org/techniques/T1204/002", "external_id": "T1204.002", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2852}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor the execution of and command-line arguments for applications that may be used by an adversary to gain initial access that require user interaction. This includes compression applications, such as those for zip files, that can be used to [Deobfuscate/Decode Files or Information](https://attack.mitre.org/techniques/T1140) in payloads.\\n\\nAnti-virus can potentially detect malicious documents and files that are downloaded and executed on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning powershell.exe).\"", "Str": "Monitor the execution of and command-line arguments for applications that may be used by an adversary to gain initial access that require user interaction. This includes compression applications, such as those for zip files, that can be used to [Deobfuscate/Decode Files or Information](https://attack.mitre.org/techniques/T1140) in payloads.\n\nAnti-virus can potentially detect malicious documents and files that are downloaded and executed on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning powershell.exe).", "Type": 3, "Index": 1984}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2774}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1887}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2685}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2727}}', 'T1204.002', 'An adversary may rely upon a user opening a malicious file in order to gain execution. Users may be subjected to social engineering to get them to open a file that will lead to code execution. This user action will typically be observed as follow-on behavior from [Spearphishing Attachment](https://attack.mitre.org/techniques/T1566/001). Adversaries may use several types of files that require a user to execute them, including .doc, .pdf, .xls, .rtf, .scr, .exe, .lnk, .pif, and .cpl.
INSERT INTO public.attack_patterns VALUES (297, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.425335+00', NULL, 'attack-pattern--7807d3a4-a885-4639-a786-c1ed41484970', '[{"url": "https://attack.mitre.org/techniques/T1588/001", "external_id": "T1588.001", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1989}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on post-compromise phases of the adversary lifecycle.", "Type": 3, "Index": 1659}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 1945}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1914}}', 'T1588.001', 'Adversaries may buy, steal, or download malware that can be used during targeting. Malicious software can include payloads, droppers, post-compromise tools, backdoors, packers, and C2 protocols. Adversaries may acquire malware to support their operations, obtaining a means for maintaining control of remote machines, evading defenses, and executing post-compromise behaviors.
INSERT INTO public.attack_patterns VALUES (298, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.456991+00', NULL, 'attack-pattern--544b0346-29ad-41e1-a808-501bb4193f47', '[{"url": "https://attack.mitre.org/techniques/T1185", "external_id": "T1185", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Man-in-the-browser", "description": "Wikipedia. (2017, October 28). Man-in-the-browser. Retrieved January 10, 2018.", "source_name": "Wikipedia Man in the Browser"}, {"url": "https://www.cobaltstrike.com/help-browser-pivoting", "description": "Mudge, R. (n.d.). Browser Pivoting. Retrieved January 10, 2018.", "source_name": "Cobalt Strike Browser Pivot"}, {"url": "https://www.icebrg.io/blog/malicious-chrome-extensions-enable-criminals-to-impact-over-half-a-million-users-and-global-businesses", "description": "De Tore, M., Warner, J. (2018, January 15). MALICIOUS CHROME EXTENSIONS ENABLE CRIMINALS TO IMPACT OVER HALF A MILLION USERS AND GLOBAL BUSINESSES. Retrieved January 17, 2018.", "source_name": "ICEBRG Chrome Extensions"}, {"url": "https://cobaltstrike.com/downloads/csmanual38.pdf", "description": "Strategic Cyber LLC. (2017, March 14). Cobalt Strike Manual. Retrieved May 24, 2017.", "source_name": "cobaltstrike manual"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4259}, "x_mitre_detection": {"Num": 0, "Raw": "\"This is a difficult technique to detect because adversary traffic would be masked by normal user traffic. No new processes are created and no additional software touches disk. Authentication logs can be used to audit logins to specific web applications, but determining malicious logins versus benign logins may be difficult if activity matches typical user behavior. Monitor for process injection against browser applications.\"", "Str": "This is a difficult technique to detect because adversary traffic would be masked by normal user traffic. No new processes are created and no additional software touches disk. Authentication logs can be used to audit logins to specific web applications, but determining malicious logins versus benign logins may be difficult if activity matches typical user behavior. Monitor for process injection against browser applications.", "Type": 3, "Index": 3634}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4211}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Justin Warner, ICEBRG\"\n    ]", "Str": "", "Type": 5, "Index": 3454}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Access\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3521}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4096}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4139}}', 'T1185', 'Adversaries can take advantage of security vulnerabilities and inherent functionality in browser software to change content, modify behavior, and intercept information as part of various man in the browser techniques. (Citation: Wikipedia Man in the Browser)
INSERT INTO public.attack_patterns VALUES (300, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.533859+00', NULL, 'attack-pattern--7e7c2fba-7cca-486c-9582-4c1bb2851961', '[{"url": "https://attack.mitre.org/techniques/T1553/005", "external_id": "T1553.005", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/openspecs/windows_protocols/ms-fscc/6e3f7352-d11c-4d76-8c39-2516a9df36e8", "description": "Microsoft. (2020, August 31). Zone.Identifier Stream Name. Retrieved February 22, 2021.", "source_name": "Microsoft Zone.Identifier 2020"}, {"url": "https://medium.com/swlh/investigating-the-use-of-vhd-files-by-cybercriminals-3f1f08304316", "description": "Beek, C. (2020, December 3). Investigating the Use of VHD Files By Cybercriminals. Retrieved February 22, 2021.", "source_name": "Beek Use of VHD Dec 2020"}, {"url": "https://outflank.nl/blog/2020/03/30/mark-of-the-web-from-a-red-teams-perspective/", "description": "Hegt, S. (2020, March 30). Mark-of-the-Web from a red team’s perspective. Retrieved February 22, 2021.", "source_name": "Outflank MotW 2020"}, {"url": "https://www.intezer.com/blog/research/russian-apt-uses-covid-19-lures-to-deliver-zebrocy/", "description": "Kennedy, J. (2020, December 9). A Zebra in Gopher''s Clothing: Russian APT Uses COVID-19 Lures to Deliver Zebrocy. Retrieved February 22, 2021.", "source_name": "Intezer Russian APT Dec 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4321}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor compressed/archive and image files downloaded from the Internet as the contents may not be tagged with the MOTW. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities.\"", "Str": "Monitor compressed/archive and image files downloaded from the Internet as the contents may not be tagged with the MOTW. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities.", "Type": 3, "Index": 3906}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4273}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Christiaan Beek, @ChristiaanBeek\"\n    ]", "Str": "", "Type": 5, "Index": 3656}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 3734}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4184}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus, Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3832}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4226}}', 'T1553.005', 'Adversaries may abuse specific file formats to subvert Mark-of-the-Web (MOTW) controls. In Windows, when files are downloaded from the Internet, they are tagged with a hidden NTFS Alternate Data Stream (ADS) named <code>Zone.Identifier</code> with a specific value known as the MOTW.(Citation: Microsoft Zone.Identifier 2020) Files that are tagged with MOTW are protected and cannot perform certain actions. For example, starting in MS Office 10, if a MS Office file has the MOTW, it will open in Protected View. Executables tagged with the MOTW will be processed by Windows Defender SmartScreen that compares files with an allowlist of well-known executables. If the file in not known/trusted, SmartScreen will prevent the execution and warn the user not to run it.(Citation: Beek Use of VHD Dec 2020)(Citation: Outflank MotW 2020)(Citation: Intezer Russian APT Dec 2020)
INSERT INTO public.attack_patterns VALUES (304, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.659626+00', NULL, 'attack-pattern--f4c1826f-a322-41cd-9557-562100848c84', '[{"url": "https://attack.mitre.org/techniques/T1556", "external_id": "T1556", "source_name": "mitre-attack"}, {"url": "https://clymb3r.wordpress.com/2013/09/15/intercepting-password-changes-with-function-hooking/", "description": "Bialek, J. (2013, September 15). Intercepting Password Changes With Function Hooking. Retrieved November 21, 2017.", "source_name": "Clymb3r Function Hook Passwords Sept 2013"}, {"url": "https://www.secureworks.com/research/skeleton-key-malware-analysis", "description": "Dell SecureWorks. (2015, January 12). Skeleton Key Malware Analysis. Retrieved April 8, 2019.", "source_name": "Dell Skeleton"}, {"url": "https://xorrior.com/persistent-credential-theft/", "description": "Chris Ross. (2018, October 17). Persistent Credential Theft with Authorization Plugins. Retrieved April 22, 2021.", "source_name": "Xorrior Authorization Plugins"}, {"url": "https://technet.microsoft.com/en-us/library/dn487457.aspx", "description": "Microsoft. (2016, April 15). Audit Policy Recommendations. Retrieved June 3, 2016.", "source_name": "TechNet Audit Policy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 5641}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for new, unfamiliar DLL files written to a domain controller and/or local computer. Monitor for changes to Registry entries for password filters (ex: <code>HKEY_LOCAL_MACHINE\\\\SYSTEM\\\\CurrentControlSet\\\\Control\\\\Lsa\\\\Notification Packages</code>) and correlate then investigate the DLL files these files reference. \\n\\nPassword filters will also show up as an autorun and loaded DLL in lsass.exe.(Citation: Clymb3r Function Hook Passwords Sept 2013)\\n\\nMonitor for calls to <code>OpenProcess</code> that can be used to manipulate lsass.exe running on a domain controller as well as for malicious modifications to functions exported from authentication-related system DLLs (such as cryptdll.dll and samsrv.dll).(Citation: Dell Skeleton) \\n\\nMonitor PAM configuration and module paths (ex: <code>/etc/pam.d/</code>) for changes. Use system-integrity tools such as AIDE and monitoring tools such as auditd to monitor PAM files.\\n\\nMonitor for suspicious additions to the /Library/Security/SecurityAgentPlugins directory.(Citation: Xorrior Authorization Plugins)\\n\\nConfigure robust, consistent account activity audit policies across the enterprise and with externally accessible services. (Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).\"", "Str": "Monitor for new, unfamiliar DLL files written to a domain controller and/or local computer. Monitor for changes to Registry entries for password filters (ex: <code>HKEY_LOCAL_MACHINE\\SYSTEM\\CurrentControlSet\\Control\\Lsa\\Notification Packages</code>) and correlate then investigate the DLL files these files reference. \n\nPassword filters will also show up as an autorun and loaded DLL in lsass.exe.(Citation: Clymb3r Function Hook Passwords Sept 2013)\n\nMonitor for calls to <code>OpenProcess</code> that can be used to manipulate lsass.exe running on a domain controller as well as for malicious modifications to functions exported from authentication-related system DLLs (such as cryptdll.dll and samsrv.dll).(Citation: Dell Skeleton) \n\nMonitor PAM configuration and module paths (ex: <code>/etc/pam.d/</code>) for changes. Use system-integrity tools such as AIDE and monitoring tools such as auditd to monitor PAM files.\n\nMonitor for suspicious additions to the /Library/Security/SecurityAgentPlugins directory.(Citation: Xorrior Authorization Plugins)\n\nConfigure robust, consistent account activity audit policies across the enterprise and with externally accessible services. (Citation: TechNet Audit Policy) Look for suspicious account behavior across systems that share accounts, either user, admin, or service accounts. Examples: one account logged into multiple systems simultaneously; multiple accounts logged into the same machine simultaneously; accounts logged in at odd times or outside of business hours. Activity may be from interactive login sessions or process ownership from accounts being used to execute binaries on a remote system as a particular account. Correlate other security systems with login information (e.g., a user has an active login session but has not entered the building or does not have VPN access).", "Type": 3, "Index": 3628}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 5546}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Chris Ross @xorrior\"\n    ]", "Str": "", "Type": 5, "Index": 3263}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Logon Session: Logon Session Creation\",\n      \"Process: OS API Execution\",\n      \"Process: Process Access\",\n      \"File: File Modification\",\n      \"File: File Creation\",\n      \"Module: Module Load\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3328}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5514}}', 'T1556', 'Adversaries may modify authentication mechanisms and processes to access user credentials or enable otherwise unwarranted access to accounts. The authentication process is handled by mechanisms, such as the Local Security Authentication Server (LSASS) process and the Security Accounts Manager (SAM) on Windows, pluggable authentication modules (PAM) on Unix-based systems, and authorization plugins on MacOS systems, responsible for gathering, storing, and validating credentials. By modifying an authentication process, an adversary may be able to authenticate to a service or system without using [Valid Accounts](https://attack.mitre.org/techniques/T1078).
INSERT INTO public.attack_patterns VALUES (309, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.80304+00', NULL, 'attack-pattern--365be77f-fc0e-42ee-bac8-4faf806d9336', '[{"url": "https://attack.mitre.org/techniques/T1218/007", "external_id": "T1218.007", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows-server/administration/windows-commands/msiexec", "description": "Microsoft. (2017, October 15). msiexec. Retrieved January 24, 2020.", "source_name": "Microsoft msiexec"}, {"url": "https://lolbas-project.github.io/lolbas/Binaries/Msiexec/", "description": "LOLBAS. (n.d.). Msiexec.exe. Retrieved April 18, 2019.", "source_name": "LOLBAS Msiexec"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/attack-using-windows-installer-msiexec-exe-leads-lokibot/", "description": "Co, M. and Sison, G. (2018, February 8). Attack Using Windows Installer msiexec.exe leads to LokiBot. Retrieved April 18, 2019.", "source_name": "TrendMicro Msiexec Feb 2018"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/msi/alwaysinstallelevated", "description": "Microsoft. (2018, May 31). AlwaysInstallElevated. Retrieved December 14, 2020.", "source_name": "Microsoft AlwaysInstallElevated 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3705}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of msiexec.exe. Compare recent invocations of msiexec.exe with prior history of known good arguments and executed MSI files or DLLs to determine anomalous and potentially adversarial activity. Command arguments used before and after the invocation of msiexec.exe may also be useful in determining the origin and purpose of the MSI files or DLLs being executed.\"", "Str": "Use process monitoring to monitor the execution and arguments of msiexec.exe. Compare recent invocations of msiexec.exe with prior history of known good arguments and executed MSI files or DLLs to determine anomalous and potentially adversarial activity. Command arguments used before and after the invocation of msiexec.exe may also be useful in determining the origin and purpose of the MSI files or DLLs being executed.", "Type": 3, "Index": 3111}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3657}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Alexandros Pappas\"\n    ]", "Str": "", "Type": 5, "Index": 2752}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Module: Module Load\",\n      \"Command: Command Execution\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2815}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3568}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 3009}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3610}}', 'T1218.007', 'Adversaries may abuse msiexec.exe to proxy execution of malicious payloads. Msiexec.exe is the command-line utility for the Windows Installer and is thus commonly associated with executing installation packages (.msi).(Citation: Microsoft msiexec) Msiexec.exe is digitally signed by Microsoft.
INSERT INTO public.attack_patterns VALUES (312, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.901956+00', NULL, 'attack-pattern--edf91964-b26e-4b4a-9600-ccacd7d7df24', '[{"url": "https://attack.mitre.org/techniques/T1003/003", "external_id": "T1003.003", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Active_Directory", "description": "Wikipedia. (2018, March 10). Active Directory. Retrieved April 11, 2018.", "source_name": "Wikipedia Active Directory"}, {"url": "http://adsecurity.org/?p=1275", "description": "Metcalf, S. (2015, January 19). Attackers Can Now Use Mimikatz to Implant Skeleton Key on Domain Controllers & BackDoor Your Active Directory Forest. Retrieved February 3, 2015.", "source_name": "Metcalf 2015"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2789}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments for program execution that may be indicative of credential dumping, especially attempts to access or copy the NTDS.dit.\"", "Str": "Monitor processes and command-line arguments for program execution that may be indicative of credential dumping, especially attempts to access or copy the NTDS.dit.", "Type": 3, "Index": 2354}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2651}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ed Williams, Trustwave, SpiderLabs\"\n    ]", "Str": "", "Type": 5, "Index": 2178}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2258}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2553}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Access to Domain Controller or backup\"\n    ]", "Str": "", "Type": 5, "Index": 2711}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2595}}', 'T1003.003', 'Adversaries may attempt to access or create a copy of the Active Directory domain database in order to steal credential information, as well as obtain other information about domain members such as devices, users, and access rights. By default, the NTDS file (NTDS.dit) is located in <code>%SystemRoot%\NTDS\Ntds.dit</code> of a domain controller.(Citation: Wikipedia Active Directory)
INSERT INTO public.attack_patterns VALUES (314, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:38.966085+00', NULL, 'attack-pattern--391d824f-0ef1-47a0-b0ee-c59a75e27670', '[{"url": "https://attack.mitre.org/techniques/T1106", "external_id": "T1106", "source_name": "mitre-attack"}, {"url": "https://undocumented.ntinternals.net/", "description": "The NTinterlnals.net team. (n.d.). Nowak, T. Retrieved June 25, 2020.", "source_name": "NT API Windows"}, {"url": "https://www.kernel.org/doc/html/v4.12/core-api/kernel-api.html", "description": "Linux Kernel Organization, Inc. (n.d.). The Linux Kernel API. Retrieved June 25, 2020.", "source_name": "Linux Kernel API"}, {"url": "http://msdn.microsoft.com/en-us/library/ms682425", "description": "Microsoft. (n.d.). CreateProcess function. Retrieved December 5, 2014.", "source_name": "Microsoft CreateProcess"}, {"url": "https://www.gnu.org/software/libc/manual/html_node/Creating-a-Process.html", "description": "Free Software Foundation, Inc.. (2020, June 18). Creating a Process. Retrieved June 25, 2020.", "source_name": "GNU Fork"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/api/", "description": "Microsoft. (n.d.). Programming reference for the Win32 API. Retrieved March 15, 2020.", "source_name": "Microsoft Win32"}, {"url": "https://man7.org/linux/man-pages//man7/libc.7.html", "description": "Kerrisk, M. (2016, December 12). libc(7) — Linux manual page. Retrieved June 25, 2020.", "source_name": "LIBC"}, {"url": "https://www.gnu.org/software/libc/", "description": "glibc developer community. (2020, February 1). The GNU C Library (glibc). Retrieved June 25, 2020.", "source_name": "GLIBC"}, {"url": "https://dotnet.microsoft.com/learn/dotnet/what-is-dotnet-framework", "description": "Microsoft. (n.d.). What is .NET Framework?. Retrieved March 15, 2020.", "source_name": "Microsoft NET"}, {"url": "https://developer.apple.com/documentation/coreservices", "description": "Apple. (n.d.). Core Services. Retrieved June 25, 2020.", "source_name": "Apple Core Services"}, {"url": "https://developer.apple.com/library/archive/documentation/MacOSX/Conceptual/OSX_Technology_Overview/CocoaApplicationLayer/CocoaApplicationLayer.html#//apple_ref/doc/uid/TP40001067-CH274-SW1", "description": "Apple. (2015, September 16). Cocoa Application Layer. Retrieved June 25, 2020.", "source_name": "MACOS Cocoa"}, {"url": "https://developer.apple.com/documentation/foundation", "description": "Apple. (n.d.). Foundation. Retrieved July 1, 2020.", "source_name": "macOS Foundation"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 6595}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring API calls may generate a significant amount of data and may not be useful for defense unless collected under specific circumstances, since benign use of API functions are common and difficult to distinguish from malicious behavior. Correlation of other events with behavior surrounding API function calls using API monitoring will provide additional context to an event that may assist in determining if it is due to malicious behavior. Correlation of activity by process lineage by process ID may be sufficient. \\n\\nUtilization of the Windows API may involve processes loading/accessing system DLLs associated with providing called functions (ex: kernel32.dll, advapi32.dll, user32.dll, and gdi32.dll). Monitoring for DLL loads, especially to abnormal/unusual or potentially malicious processes, may indicate abuse of the Windows API. Though noisy, this data can be combined with other indicators to identify adversary activity. \"", "Str": "Monitoring API calls may generate a significant amount of data and may not be useful for defense unless collected under specific circumstances, since benign use of API functions are common and difficult to distinguish from malicious behavior. Correlation of other events with behavior surrounding API function calls using API monitoring will provide additional context to an event that may assist in determining if it is due to malicious behavior. Correlation of activity by process lineage by process ID may be sufficient. \n\nUtilization of the Windows API may involve processes loading/accessing system DLLs associated with providing called functions (ex: kernel32.dll, advapi32.dll, user32.dll, and gdi32.dll). Monitoring for DLL loads, especially to abnormal/unusual or potentially malicious processes, may indicate abuse of the Windows API. Though noisy, this data can be combined with other indicators to identify adversary activity. ", "Type": 3, "Index": 5414}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 6480}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 5257}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 5317}, "x_mitre_remote_support": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6565}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 6390}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 6433}}', 'T1106', 'Adversaries may directly interact with the native OS application programming interface (API) to execute behaviors. Native APIs provide a controlled means of calling low-level OS services within the kernel, such as those involving hardware/devices, memory, and processes.(Citation: NT API Windows)(Citation: Linux Kernel API) These native APIs are leveraged by the OS during system boot (when other system components are not yet initialized) as well as carrying out tasks and requests during routine operations.
INSERT INTO public.attack_patterns VALUES (315, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.007115+00', NULL, 'attack-pattern--f63fe421-b1d1-45c0-b8a7-02cd16ff2bed', '[{"url": "https://attack.mitre.org/techniques/T1546/007", "external_id": "T1546.007", "source_name": "mitre-attack"}, {"url": "https://technet.microsoft.com/library/bb490939.aspx", "description": "Microsoft. (n.d.). Using Netsh. Retrieved February 13, 2017.", "source_name": "TechNet Netsh"}, {"url": "https://github.com/outflankbv/NetshHelperBeacon", "description": "Smeets, M. (2016, September 26). NetshHelperBeacon. Retrieved February 13, 2017.", "source_name": "Github Netsh Helper CS Beacon"}, {"url": "https://htmlpreview.github.io/?https://github.com/MatthewDemaske/blogbackup/blob/master/netshell.html", "description": "Demaske, M. (2016, September 23). USING NETSHELL TO EXECUTE EVIL DLLS AND PERSIST ON A HOST. Retrieved April 8, 2017.", "source_name": "Demaske Netsh Persistence"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3539}, "x_mitre_detection": {"Num": 0, "Raw": "\"It is likely unusual for netsh.exe to have any child processes in most environments. Monitor process executions and investigate any child processes spawned by netsh.exe for malicious behavior. Monitor the <code>HKLM\\\\SOFTWARE\\\\Microsoft\\\\Netsh</code> registry key for any new or suspicious entries that do not correlate with known system files or benign software. (Citation: Demaske Netsh Persistence)\"", "Str": "It is likely unusual for netsh.exe to have any child processes in most environments. Monitor process executions and investigate any child processes spawned by netsh.exe for malicious behavior. Monitor the <code>HKLM\\SOFTWARE\\Microsoft\\Netsh</code> registry key for any new or suspicious entries that do not correlate with known system files or benign software. (Citation: Demaske Netsh Persistence)", "Type": 3, "Index": 2941}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3491}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 2672}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2747}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3377}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3419}}', 'T1546.007', 'Adversaries may establish persistence by executing malicious content triggered by Netsh Helper DLLs. Netsh.exe (also referred to as Netshell) is a command-line scripting utility used to interact with the network configuration of a system. It contains functionality to add helper DLLs for extending functionality of the utility. (Citation: TechNet Netsh) The paths to registered netsh.exe helper DLLs are entered into the Windows Registry at <code>HKLM\SOFTWARE\Microsoft\Netsh</code>.
INSERT INTO public.attack_patterns VALUES (320, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.184677+00', NULL, 'attack-pattern--818302b2-d640-477b-bf88-873120ce85c4', '[{"url": "https://attack.mitre.org/techniques/T1059/008", "external_id": "T1059.008", "source_name": "mitre-attack"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "https://tools.cisco.com/security/center/resources/integrity_assurance.html#23", "description": "Cisco. (n.d.). Cisco IOS Software Integrity Assurance - Command History. Retrieved October 21, 2020.", "source_name": "Cisco IOS Software Integrity Assurance - Command History"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3320}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider reviewing command history in either the console or as part of the running memory to determine if unauthorized or suspicious commands were used to modify device configuration.(Citation: Cisco IOS Software Integrity Assurance - Command History)\\n\\nConsider comparing a copy of the network device configuration against a known-good version to discover unauthorized changes to the command interpreter. The same process can be accomplished through a comparison of the run-time memory, though this is non-trivial and may require assistance from the vendor.\"", "Str": "Consider reviewing command history in either the console or as part of the running memory to determine if unauthorized or suspicious commands were used to modify device configuration.(Citation: Cisco IOS Software Integrity Assurance - Command History)\n\nConsider comparing a copy of the network device configuration against a known-good version to discover unauthorized changes to the command interpreter. The same process can be accomplished through a comparison of the run-time memory, though this is non-trivial and may require assistance from the vendor.", "Type": 3, "Index": 2566}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3272}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2497}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3160}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3202}}', 'T1059.008', 'Adversaries may abuse scripting or built-in command line interpreters (CLI) on network devices to execute malicious command and payloads. The CLI is the primary means through which users and administrators interact with the device in order to view system information, modify device operations, or perform diagnostic and administrative functions. CLIs typically contain various permission levels required for different commands. 
INSERT INTO public.attack_patterns VALUES (322, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.251825+00', NULL, 'attack-pattern--c63a348e-ffc2-486a-b9d9-d7f11ec54d99', '[{"url": "https://attack.mitre.org/techniques/T1037/003", "external_id": "T1037.003", "source_name": "mitre-attack"}, {"url": "https://www.petri.com/setting-up-logon-script-through-active-directory-users-computers-windows-server-2008", "description": "Daniel Petri. (2009, January 8). Setting up a Logon Script through Active Directory Users and Computers in Windows Server 2008. Retrieved November 15, 2019.", "source_name": "Petri Logon Script AD"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2625}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.\"", "Str": "Monitor logon scripts for unusual access by abnormal users or at abnormal times. Look for files added or modified by unusual accounts outside of normal administration duties. Monitor running process for actions that could be indicative of abnormal programs or executables running upon logon.", "Type": 3, "Index": 2220}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2577}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Active Directory: Active Directory Object Modification\"\n    ]", "Str": "", "Type": 5, "Index": 1990}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2546}}', 'T1037.003', 'Adversaries may use network logon scripts automatically executed at logon initialization to establish persistence. Network logon scripts can be assigned using Active Directory or Group Policy Objects.(Citation: Petri Logon Script AD) These logon scripts run with the privileges of the user they are assigned to. Depending on the systems within the network, initializing one of these scripts could apply to more than one or potentially all systems.  
INSERT INTO public.attack_patterns VALUES (326, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.352424+00', NULL, 'attack-pattern--3489cfc5-640f-4bb3-a103-9137b97de79f', '[{"url": "https://attack.mitre.org/techniques/T1135", "external_id": "T1135", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/643.html", "external_id": "CAPEC-643", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Shared_resource", "description": "Wikipedia. (2017, April 15). Shared resource. Retrieved June 30, 2017.", "source_name": "Wikipedia Shared Resource"}, {"url": "https://technet.microsoft.com/library/cc770880.aspx", "description": "Microsoft. (n.d.). Share a Folder or Drive. Retrieved June 30, 2017.", "source_name": "TechNet Shared Folder"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"3.0\"", "Str": "3.0", "Type": 3, "Index": 3526}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nNormal, benign system and network events related to legitimate remote system discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nNormal, benign system and network events related to legitimate remote system discovery may be uncommon, depending on the environment and how they are used. Monitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2408}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 3448}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 2213}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2269}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3358}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3401}}', 'T1135', 'Adversaries may look for folders and drives shared on remote systems as a means of identifying sources of information to gather as a precursor for Collection and to identify potential systems of interest for Lateral Movement. Networks often contain shared network drives and folders that enable users to access file directories on various systems across a network. 
INSERT INTO public.attack_patterns VALUES (327, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.386932+00', NULL, 'attack-pattern--3257eb21-f9a7-4430-8de1-d8b6e288f529', '[{"url": "https://attack.mitre.org/techniques/T1040", "external_id": "T1040", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/158.html", "external_id": "CAPEC-158", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3325}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detecting the events leading up to sniffing network traffic may be the best method of detection. From the host level, an adversary would likely need to perform a [Man-in-the-Middle](https://attack.mitre.org/techniques/T1557) attack against other devices on a wired network in order to capture traffic that was not to or from the current compromised system. This change in the flow of information is detectable at the enclave network level. Monitor for ARP spoofing and gratuitous ARP broadcasts. Detecting compromised network devices is a bit more challenging. Auditing administrator logins, configuration changes, and device images is required to detect malicious changes.\"", "Str": "Detecting the events leading up to sniffing network traffic may be the best method of detection. From the host level, an adversary would likely need to perform a [Man-in-the-Middle](https://attack.mitre.org/techniques/T1557) attack against other devices on a wired network in order to capture traffic that was not to or from the current compromised system. This change in the flow of information is detectable at the enclave network level. Monitor for ARP spoofing and gratuitous ARP broadcasts. Detecting compromised network devices is a bit more challenging. Auditing administrator logins, configuration changes, and device images is required to detect malicious changes.", "Type": 3, "Index": 2304}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3127}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2200}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3012}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"Network interface access and packet capture driver\"\n    ]", "Str": "", "Type": 5, "Index": 3234}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3055}}', 'T1040', 'Adversaries may sniff network traffic to capture information about an environment, including authentication material passed over the network. Network sniffing refers to using the network interface on a system to monitor or capture information sent over a wired or wireless connection. An adversary may place a network interface into promiscuous mode to passively access data in transit over the network, or use span ports to capture a larger amount of data.
INSERT INTO public.attack_patterns VALUES (365, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.756875+00', NULL, 'attack-pattern--43881e51-ac74-445b-b4c6-f9f9e9bf23fe', '[{"url": "https://attack.mitre.org/techniques/T1547/010", "external_id": "T1547.010", "source_name": "mitre-attack"}, {"url": "http://msdn.microsoft.com/en-us/library/dd183341", "description": "Microsoft. (n.d.). AddMonitor function. Retrieved November 12, 2014.", "source_name": "AddMonitor"}, {"url": "https://www.defcon.org/images/defcon-22/dc-22-presentations/Bloxham/DEFCON-22-Brady-Bloxham-Windows-API-Abuse-UPDATED.pdf", "description": "Bloxham, B. (n.d.). Getting Windows to Play with Itself &#91;PowerPoint slides&#93;. Retrieved November 12, 2014.", "source_name": "Bloxham"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3694}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process API calls to <code>AddMonitor</code>.(Citation: AddMonitor) Monitor DLLs that are loaded by spoolsv.exe for DLLs that are abnormal. New DLLs written to the System32 directory that do not correlate with known good software or patching may be suspicious. \\n\\nMonitor Registry writes to <code>HKLM\\\\SYSTEM\\\\CurrentControlSet\\\\Control\\\\Print\\\\Monitors</code>. Run the Autoruns utility, which checks for this Registry key as a persistence mechanism (Citation: TechNet Autoruns)\"", "Str": "Monitor process API calls to <code>AddMonitor</code>.(Citation: AddMonitor) Monitor DLLs that are loaded by spoolsv.exe for DLLs that are abnormal. New DLLs written to the System32 directory that do not correlate with known good software or patching may be suspicious. \n\nMonitor Registry writes to <code>HKLM\\SYSTEM\\CurrentControlSet\\Control\\Print\\Monitors</code>. Run the Autoruns utility, which checks for this Registry key as a persistence mechanism (Citation: TechNet Autoruns)", "Type": 3, "Index": 2948}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3646}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\",\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 2669}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Process: OS API Execution\",\n      \"Module: Module Load\",\n      \"Windows Registry: Windows Registry Key Modification\"\n    ]", "Str": "", "Type": 5, "Index": 2761}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3532}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"SYSTEM\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3574}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3477}}', 'T1547.010', 'Adversaries may use port monitors to run an attacker supplied DLL during system boot for persistence or privilege escalation. A port monitor can be set through the <code>AddMonitor</code> API call to set a DLL to be loaded at startup. (Citation: AddMonitor) This DLL can be located in <code>C:\Windows\System32</code> and will be loaded by the print spooler service, spoolsv.exe, on boot. The spoolsv.exe process also runs under SYSTEM level permissions. (Citation: Bloxham) Alternatively, an arbitrary DLL can be loaded if permissions allow writing a fully-qualified pathname for that DLL to <code>HKLM\SYSTEM\CurrentControlSet\Control\Print\Monitors</code>. 
INSERT INTO public.attack_patterns VALUES (329, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.458192+00', NULL, 'attack-pattern--36aa137f-5166-41f8-b2f0-a4cfa1b4133e', '[{"url": "https://attack.mitre.org/techniques/T1590/003", "external_id": "T1590.003", "source_name": "mitre-attack"}, {"url": "https://www.slideshare.net/rootedcon/carlos-garca-pentesting-active-directory-forests-rooted2019", "description": "García, C. (2019, April 3). Pentesting Active Directory Forests. Retrieved October 20, 2020.", "source_name": "Pentesting AD Forests"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2835}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2394}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2791}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2760}}', 'T1590.003', 'Adversaries may gather information about the victim''s network trust dependencies that can be used during targeting. Information about network trusts may include a variety of details, including second or third-party organizations/domains (ex: managed service providers, contractors, etc.) that have connected (and potentially elevated) network access.
INSERT INTO public.attack_patterns VALUES (330, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.531681+00', NULL, 'attack-pattern--c21d5a77-d422-4a69-acd7-2c53c1faa34b', '[{"url": "https://attack.mitre.org/techniques/T1095", "external_id": "T1095", "source_name": "mitre-attack"}, {"url": "http://en.wikipedia.org/wiki/List_of_network_protocols_%28OSI_model%29", "description": "Wikipedia. (n.d.). List of network protocols (OSI model). Retrieved December 4, 2014.", "source_name": "Wikipedia OSI"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}, {"url": "http://support.microsoft.com/KB/170292", "description": "Microsoft. (n.d.). Internet Control Message Protocol (ICMP) Basics. Retrieved December 1, 2014.", "source_name": "Microsoft ICMP"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 4300}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network traffic for ICMP messages or other protocols that contain abnormal data or are not normally seen within or exiting the network.(Citation: Cisco Blog Legacy Device Attacks)\\n\\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2) \\n\\nMonitor and investigate API calls to functions associated with enabling and/or utilizing alternative communication channels.\"", "Str": "Analyze network traffic for ICMP messages or other protocols that contain abnormal data or are not normally seen within or exiting the network.(Citation: Cisco Blog Legacy Device Attacks)\n\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2) \n\nMonitor and investigate API calls to functions associated with enabling and/or utilizing alternative communication channels.", "Type": 3, "Index": 3353}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 4205}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ryan Becwar\"\n    ]", "Str": "", "Type": 5, "Index": 3166}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3223}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4131}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4174}}', 'T1095', 'Adversaries may use a non-application layer protocol for communication between host and C2 server or among infected hosts within a network. The list of possible protocols is extensive.(Citation: Wikipedia OSI) Specific examples include use of network layer protocols, such as the Internet Control Message Protocol (ICMP), transport layer protocols, such as the User Datagram Protocol (UDP), session layer protocols, such as Socket Secure (SOCKS), as well as redirected/tunneled protocols, such as Serial over LAN (SOL).
INSERT INTO public.attack_patterns VALUES (331, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.568818+00', NULL, 'attack-pattern--d467bc38-284b-4a00-96ac-125f447799fc', '[{"url": "https://attack.mitre.org/techniques/T1132/002", "external_id": "T1132.002", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Binary-to-text_encoding", "description": "Wikipedia. (2016, December 26). Binary-to-text encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Binary-to-text Encoding"}, {"url": "https://en.wikipedia.org/wiki/Character_encoding", "description": "Wikipedia. (2017, February 19). Character Encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Character Encoding"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2913}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2288}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2835}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2205}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2746}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2788}}', 'T1132.002', 'Adversaries may encode data with a non-standard data encoding system to make the content of command and control traffic more difficult to detect. Command and control (C2) information can be encoded using a non-standard data encoding system that diverges from existing protocol specifications. Non-standard data encoding schemes may be based on or related to standard data encoding schemes, such as a modified Base64 encoding for the message body of an HTTP request.(Citation: Wikipedia Binary-to-text Encoding) (Citation: Wikipedia Character Encoding) ', 'Non-Standard Encoding', 116);
INSERT INTO public.attack_patterns VALUES (336, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.708506+00', NULL, 'attack-pattern--ce0687a0-e692-4b77-964a-0784a8e54ff1', '[{"url": "https://attack.mitre.org/techniques/T1588", "external_id": "T1588", "source_name": "mitre-attack"}, {"url": "https://www.nytimes.com/2013/07/14/world/europe/nations-buying-as-hackers-sell-computer-flaws.html", "description": "Nicole Perlroth and David E. Sanger. (2013, July 12). Nations Buying as Hackers Sell Flaws in Computer Code. Retrieved March 9, 2017.", "source_name": "NationsBuying"}, {"url": "https://citizenlab.ca/2016/08/million-dollar-dissident-iphone-zero-day-nso-group-uae/", "description": "Bill Marczak and John Scott-Railton. (2016, August 24). The Million Dollar Dissident: NSO Group’s iPhone Zero-Days used against a UAE Human Rights Defender. Retrieved December 12, 2016.", "source_name": "PegasusCitizenLab"}, {"url": "https://threatpost.com/final-report-diginotar-hack-shows-total-compromise-ca-servers-103112/77170/", "description": "Fisher, D. (2012, October 31). Final Report on DigiNotar Hack Shows Total Compromise of CA Servers. Retrieved March 6, 2017.", "source_name": "DiginotarCompromise"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3386}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Defense Evasion or Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Defense Evasion or Command and Control.", "Type": 3, "Index": 3008}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3342}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3310}}', 'T1588', 'Adversaries may buy and/or steal capabilities that can be used during targeting. Rather than developing their own capabilities in-house, adversaries may purchase, freely download, or steal them. Activities may include the acquisition of malware, software (including licenses), exploits, certificates, and information relating to vulnerabilities. Adversaries may obtain capabilities to support their operations throughout numerous phases of the adversary lifecycle.
INSERT INTO public.attack_patterns VALUES (338, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.775887+00', NULL, 'attack-pattern--2c4d4e92-0ccf-4a97-b54c-86d662988a53', '[{"url": "https://attack.mitre.org/techniques/T1137", "external_id": "T1137", "source_name": "mitre-attack"}, {"url": "https://github.com/sensepost/ruler", "description": "SensePost. (2016, August 18). Ruler: A tool to abuse Exchange services. Retrieved February 4, 2019.", "source_name": "SensePost Ruler GitHub"}, {"url": "https://blogs.technet.microsoft.com/office365security/defending-against-rules-and-forms-injection/", "description": "Koeller, B.. (2018, February 21). Defending Against Rules and Forms Injection. Retrieved November 5, 2019.", "source_name": "TechNet O365 Outlook Rules"}, {"url": "https://malware.news/t/using-outlook-forms-for-lateral-movement-and-persistence/13746", "description": "Parisi, T., et al. (2017, July). Using Outlook Forms for Lateral Movement and Persistence. Retrieved February 5, 2019.", "source_name": "CrowdStrike Outlook Forms"}, {"url": "https://medium.com/@bwtech789/outlook-today-homepage-persistence-33ea9b505943", "description": "Soutcast. (2018, September 14). Outlook Today Homepage Persistence. Retrieved February 5, 2019.", "source_name": "Outlook Today Home Page"}, {"url": "https://docs.microsoft.com/en-us/office365/securitycompliance/detect-and-remediate-outlook-rules-forms-attack", "description": "Fox, C., Vangel, D. (2018, April 22). Detect and Remediate Outlook Rules and Custom Forms Injections Attacks in Office 365. Retrieved February 4, 2019.", "source_name": "Microsoft Detect Outlook Forms"}, {"url": "https://github.com/sensepost/notruler", "description": "SensePost. (2017, September 21). NotRuler - The opposite of Ruler, provides blue teams with the ability to detect Ruler usage against Exchange. Retrieved February 4, 2019.", "source_name": "SensePost NotRuler"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 5431}, "x_mitre_detection": {"Num": 0, "Raw": "\"Collect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior. If winword.exe is the parent process for suspicious processes and activity relating to other adversarial techniques, then it could indicate that the application was used maliciously.\\n\\nMany Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence.(Citation: CrowdStrike Outlook Forms)(Citation: Outlook Today Home Page)\\n\\nMicrosoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)\"", "Str": "Collect process execution information including process IDs (PID) and parent process IDs (PPID) and look for abnormal chains of activity resulting from Office processes. Non-standard process execution trees may also indicate suspicious or malicious behavior. If winword.exe is the parent process for suspicious processes and activity relating to other adversarial techniques, then it could indicate that the application was used maliciously.\n\nMany Office-related persistence mechanisms require changes to the Registry and for binaries, files, or scripts to be written to disk or existing files modified to include malicious scripts. Collect events related to Registry key creation and modification for keys that could be used for Office-based persistence.(Citation: CrowdStrike Outlook Forms)(Citation: Outlook Today Home Page)\n\nMicrosoft has released a PowerShell script to safely gather mail forwarding rules and custom forms in your mail environment as well as steps to interpret the output.(Citation: Microsoft Detect Outlook Forms) SensePost, whose tool [Ruler](https://attack.mitre.org/software/S0358) can be used to carry out malicious rules, forms, and Home Page attacks, has released a tool to detect Ruler usage.(Citation: SensePost NotRuler)", "Type": 3, "Index": 3959}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Office 365\"\n    ]", "Str": "", "Type": 5, "Index": 5363}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Nick Carr, FireEye\",\n      \"Microsoft Threat Intelligence Center (MSTIC)\",\n      \"Sahar Shukrun\",\n      \"Praetorian\",\n      \"Loic Jaquemet\",\n      \"Ricardo Dias\"\n    ]", "Str": "", "Type": 5, "Index": 3390}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Creation\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Module: Module Load\",\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 3596}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5250}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5293}}', 'T1137', 'Adversaries may leverage Microsoft Office-based applications for persistence between startups. Microsoft Office is a fairly common application suite on Windows-based operating systems within an enterprise network. There are multiple mechanisms that can be used with Office for persistence when an Office-based application is started; this can include the use of Office Template Macros and add-ins.
INSERT INTO public.attack_patterns VALUES (341, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:39.880223+00', NULL, 'attack-pattern--9c99724c-a483-4d60-ad9d-7f004e42e8e8', '[{"url": "https://attack.mitre.org/techniques/T1102/003", "external_id": "T1102.003", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3083}, "x_mitre_detection": {"Num": 0, "Raw": "\"Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. Analyze network data for uncommon data flows. User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)\"", "Str": "Host data that can relate unknown or suspicious process activity using a network connection is important to supplement any existing indicators of compromise based on malware command and control signatures and infrastructure or the presence of strong encryption. Packet capture analysis will require SSL/TLS inspection if data is encrypted. Analyze network data for uncommon data flows. User behavior monitoring may help to detect abnormal patterns of activity.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2382}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3005}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2198}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2916}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2958}}', 'T1102.003', 'Adversaries may use an existing, legitimate external Web service as a means for sending commands to a compromised system without receiving return output over the Web service channel. Compromised systems may leverage popular websites and social media to host command and control (C2) instructions. Those infected systems may opt to send the output from those commands back over a different C2 channel, including to another distinct Web service. Alternatively, compromised systems may return no output at all in cases where adversaries want to send instructions to systems and do not want a response.
INSERT INTO public.attack_patterns VALUES (346, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.087024+00', NULL, 'attack-pattern--e624264c-033a-424d-9fd7-fc9c3bbdb03e', '[{"url": "https://attack.mitre.org/techniques/T1550/002", "external_id": "T1550.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/644.html", "external_id": "CAPEC-644", "source_name": "capec"}, {"url": "https://stealthbits.com/blog/how-to-detect-overpass-the-hash-attacks/", "description": "Warren, J. (2019, February 26). How to Detect Overpass-the-Hash Attacks. Retrieved February 4, 2021.", "source_name": "Stealthbits Overpass-the-Hash"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3623}, "x_mitre_detection": {"Num": 0, "Raw": "\"Audit all logon and credential use events and review for discrepancies. Unusual remote logins that correlate with other suspicious activity (such as writing and executing binaries) may indicate malicious activity. NTLM LogonType 3 authentications that are not associated to a domain login and are not anonymous logins are suspicious.\\n\\nEvent ID 4768 and 4769 will also be generated on the Domain Controller when a user requests a new ticket granting ticket or service ticket. These events combined with the above activity may be indicative of an overpass the hash attempt.(Citation: Stealthbits Overpass-the-Hash)\"", "Str": "Audit all logon and credential use events and review for discrepancies. Unusual remote logins that correlate with other suspicious activity (such as writing and executing binaries) may indicate malicious activity. NTLM LogonType 3 authentications that are not associated to a domain login and are not anonymous logins are suspicious.\n\nEvent ID 4768 and 4769 will also be generated on the Domain Controller when a user requests a new ticket granting ticket or service ticket. These events combined with the above activity may be indicative of an overpass the hash attempt.(Citation: Stealthbits Overpass-the-Hash)", "Type": 3, "Index": 2895}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3575}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Blake Strom, Microsoft 365 Defender\",\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 2516}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Authentication\",\n      \"Logon Session: Logon Session Creation\",\n      \"Active Directory: Active Directory Credential Request\"\n    ]", "Str": "", "Type": 5, "Index": 2629}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3544}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"System Access Controls\"\n    ]", "Str": "", "Type": 5, "Index": 2830}}', 'T1550.002', 'Adversaries may “pass the hash” using stolen password hashes to move laterally within an environment, bypassing normal system access controls. Pass the hash (PtH) is a method of authenticating as a user without having access to the user''s cleartext password. This method bypasses standard authentication steps that require a cleartext password, moving directly into the portion of the authentication that uses the password hash.
INSERT INTO public.attack_patterns VALUES (351, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.285333+00', NULL, 'attack-pattern--315f51f0-6b03-4c1e-bfb2-84740afb8e21', '[{"url": "https://attack.mitre.org/techniques/T1555/005", "external_id": "T1555.005", "source_name": "mitre-attack"}, {"url": "https://www.ise.io/casestudies/password-manager-hacking/", "description": "ise. (2019, February 19). Password Managers: Under the Hood of Secrets Management. Retrieved January 22, 2021.", "source_name": "ise Password Manager February 2019"}, {"url": "https://resources.fox-it.com/rs/170-CAK-271/images/201912_Report_Operation_Wocao.pdf", "description": "Dantzig, M. v., Schamper, E. (2019, December 19). Operation Wocao: Shining a light on one of China’s hidden hacking groups. Retrieved October 8, 2020.", "source_name": "FoxIT Wocao December 2019"}, {"url": "https://github.com/GhostPack/KeeThief", "description": "Lee, C., Schoreder, W. (n.d.). KeeThief. Retrieved February 8, 2021.", "source_name": "Github KeeThief"}, {"url": "https://nvd.nist.gov/vuln/detail/CVE-2019-3610", "description": "National Vulnerability Database. (2019, October 9). CVE-2019-3610 Detail. Retrieved April 14, 2021.", "source_name": "NVD CVE-2019-3610"}, {"url": "https://www.cybereason.com/blog/dropping-anchor-from-a-trickbot-infection-to-the-discovery-of-the-anchor-malware", "description": "Dahan, A. et al. (2019, December 11). DROPPING ANCHOR: FROM A TRICKBOT INFECTION TO THE DISCOVERY OF THE ANCHOR MALWARE. Retrieved September 10, 2020.", "source_name": "Cyberreason Anchor December 2019"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4097}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring API calls, file read events, and processes for suspicious activity that could indicate searching in process memory of password managers. \\n\\nConsider monitoring file reads surrounding known password manager applications.\"", "Str": "Consider monitoring API calls, file read events, and processes for suspicious activity that could indicate searching in process memory of password managers. \n\nConsider monitoring file reads surrounding known password manager applications.", "Type": 3, "Index": 3655}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4019}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matt Burrough, @mattburrough, Microsoft\"\n    ]", "Str": "", "Type": 5, "Index": 3406}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"File: File Access\",\n      \"Process: Process Access\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 3491}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3930}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3972}}', 'T1555.005', 'Adversaries may acquire user credentials from third-party password managers.(Citation: ise Password Manager February 2019) Password managers are applications designed to store user credentials, normally in an encrypted database. Credentials are typically accessible after a user provides a master password that unlocks the database. After the database is unlocked, these credentials may be copied to memory. These databases can be stored as files on disk.(Citation: ise Password Manager February 2019)
INSERT INTO public.attack_patterns VALUES (355, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.43305+00', NULL, 'attack-pattern--0c2d00da-7742-49e7-9928-4514e5075d32', '[{"url": "https://attack.mitre.org/techniques/T1574/007", "external_id": "T1574.007", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/13.html", "external_id": "CAPEC-13", "source_name": "capec"}, {"url": "https://capec.mitre.org/data/definitions/38.html", "external_id": "CAPEC-38", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4028}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \\\"findstr,\\\" \\\"net,\\\" and \\\"python\\\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\\n\\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\"", "Str": "Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \"findstr,\" \"net,\" and \"python\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\n\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.", "Type": 3, "Index": 3050}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3980}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 2791}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2851}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3949}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2988}}', 'T1574.007', 'Adversaries may execute their own malicious payloads by hijacking environment variables used to load libraries. Adversaries may place a program in an earlier entry in the list of directories stored in the PATH environment variable, which Windows will then execute when it searches sequentially through that PATH listing in search of the binary that was called from a script or the command line.
INSERT INTO public.attack_patterns VALUES (356, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.464869+00', NULL, 'attack-pattern--58af3705-8740-4c68-9329-ec015a7013c2', '[{"url": "https://attack.mitre.org/techniques/T1574/008", "external_id": "T1574.008", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/159.html", "external_id": "CAPEC-159", "source_name": "capec"}, {"url": "http://msdn.microsoft.com/en-us/library/ms682425", "description": "Microsoft. (n.d.). CreateProcess function. Retrieved December 5, 2014.", "source_name": "Microsoft CreateProcess"}, {"url": "https://docs.microsoft.com/en-us/previous-versions//cc723564(v=technet.10)?redirectedfrom=MSDN#XSLTsection127121120120", "description": "Tim Hill. (2014, February 2). The Windows NT Command Shell. Retrieved December 5, 2014.", "source_name": "Windows NT Command Shell"}, {"url": "http://msdn.microsoft.com/en-us/library/ms687393", "description": "Microsoft. (n.d.). WinExec function. Retrieved December 5, 2014.", "source_name": "Microsoft WinExec"}, {"url": "https://docs.microsoft.com/en-us/previous-versions//fd7hxfdd(v=vs.85)?redirectedfrom=MSDN", "description": "Microsoft. (2011, October 24). Environment Property. Retrieved July 27, 2016.", "source_name": "Microsoft Environment Property"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5628}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \\\"findstr,\\\" \\\"net,\\\" and \\\"python\\\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\\n\\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\\n\"", "Str": "Monitor file creation for files named after partial directories and in locations that may be searched for common processes through the environment variable, or otherwise should not be user writable. Monitor the executing process for process executable paths that are named for partial directories. Monitor file creation for programs that are named after Windows system programs or programs commonly executed without a path (such as \"findstr,\" \"net,\" and \"python\"). If this activity occurs outside of known administration activity, upgrades, installations, or patches, then it may be suspicious.\n\nData and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as network connections made for Command and Control, learning details about the environment through Discovery, and Lateral Movement.\n", "Type": 3, "Index": 4453}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5580}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Stefan Kanthak\"\n    ]", "Str": "", "Type": 5, "Index": 4263}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 4323}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5452}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 5494}, "x_mitre_effective_permissions": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 5360}}', 'T1574.008', 'Adversaries may execute their own malicious payloads by hijacking the search order used to load other programs. Because some programs do not call other programs using the full path, adversaries may place their own file in the directory where the calling program is located, causing the operating system to launch their malicious software at the request of the calling program.
INSERT INTO public.attack_patterns VALUES (360, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.600474+00', NULL, 'attack-pattern--a62a8db3-f23a-4d8f-afd6-9dbc77e7813b', '[{"url": "https://attack.mitre.org/techniques/T1566", "external_id": "T1566", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/98.html", "external_id": "CAPEC-98", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 4150}, "x_mitre_detection": {"Num": 0, "Raw": "\"Network intrusion detection systems and email gateways can be used to detect phishing with malicious attachments in transit. Detonation chambers may also be used to identify malicious attachments. Solutions can be signature and behavior based, but adversaries may construct attachments in a way to avoid these systems.\\n\\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\\n\\nURL inspection within email (including expanding shortened links) can help detect links leading to known malicious sites. Detonation chambers can be used to detect these links and either automatically go to these sites to determine if they''re potentially malicious, or wait and capture the content if a user visits the link.\\n\\nBecause most common third-party services used for phishing via service leverage TLS encryption, SSL/TLS inspection is generally required to detect the initial communication/delivery. With SSL/TLS inspection intrusion detection signatures or other security gateway appliances may be able to detect malware.\\n\\nAnti-virus can potentially detect malicious documents and files that are downloaded on the user''s computer. Many possible detections of follow-on behavior may take place once [User Execution](https://attack.mitre.org/techniques/T1204) occurs.\"", "Str": "Network intrusion detection systems and email gateways can be used to detect phishing with malicious attachments in transit. Detonation chambers may also be used to identify malicious attachments. Solutions can be signature and behavior based, but adversaries may construct attachments in a way to avoid these systems.\n\nFiltering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\n\nURL inspection within email (including expanding shortened links) can help detect links leading to known malicious sites. Detonation chambers can be used to detect these links and either automatically go to these sites to determine if they''re potentially malicious, or wait and capture the content if a user visits the link.\n\nBecause most common third-party services used for phishing via service leverage TLS encryption, SSL/TLS inspection is generally required to detect the initial communication/delivery. With SSL/TLS inspection intrusion detection signatures or other security gateway appliances may be able to detect malware.\n\nAnti-virus can potentially detect malicious documents and files that are downloaded on the user''s computer. Many possible detections of follow-on behavior may take place once [User Execution](https://attack.mitre.org/techniques/T1204) occurs.", "Type": 3, "Index": 2578}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"SaaS\",\n      \"Office 365\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 4012}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\"\n    ]", "Str": "", "Type": 5, "Index": 2338}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2398}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3980}}', 'T1566', 'Adversaries may send phishing messages to gain access to victim systems. All forms of phishing are electronically delivered social engineering. Phishing can be targeted, known as spearphishing. In spearphishing, a specific individual, company, or industry will be targeted by the adversary. More generally, adversaries can conduct non-targeted phishing, such as in mass malware spam campaigns.
INSERT INTO public.attack_patterns VALUES (362, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.673049+00', NULL, 'attack-pattern--6747daa2-3533-4e78-8fb8-446ebb86448a', '[{"url": "https://attack.mitre.org/techniques/T1547/011", "external_id": "T1547.011", "source_name": "mitre-attack"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLoginItems.html", "description": "Apple. (2016, September 13). Adding Login Items. Retrieved July 11, 2017.", "source_name": "Adding Login Items"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2015). Malware Persistence on OS X Yosemite. Retrieved July 10, 2017.", "source_name": "Malware Persistence on OS X"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/04/new-osx-dok-malware-intercepts-web-traffic/", "description": "Thomas Reed. (2017, July 7). New OSX.Dok malware intercepts web traffic. Retrieved July 10, 2017.", "source_name": "OSX.Dok Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 5911}, "x_mitre_detection": {"Num": 0, "Raw": "\"File system monitoring can determine if plist files are being modified. Users should not have permission to modify these in most cases. Some software tools like \\\"Knock Knock\\\" can detect persistence mechanisms and point to the specific files that are being referenced. This can be helpful to see what is actually being executed.\\n\\nAll the login items created via shared file lists are viewable by going to the Apple menu -> System Preferences -> Users & Groups -> Login items. This area (and the corresponding file locations) should be monitored and allowed for known good applications. Otherwise, Login Items are located in <code> Contents/Library/LoginItems </code> within an application bundle, so these paths should be monitored as well.(Citation: Adding Login Items)\\n\\nMonitor process execution for abnormal process execution resulting from modified plist files. Monitor utilities used to modify plist files or that take a plist file as an argument, which may indicate suspicious activity.\"", "Str": "File system monitoring can determine if plist files are being modified. Users should not have permission to modify these in most cases. Some software tools like \"Knock Knock\" can detect persistence mechanisms and point to the specific files that are being referenced. This can be helpful to see what is actually being executed.\n\nAll the login items created via shared file lists are viewable by going to the Apple menu -> System Preferences -> Users & Groups -> Login items. This area (and the corresponding file locations) should be monitored and allowed for known good applications. Otherwise, Login Items are located in <code> Contents/Library/LoginItems </code> within an application bundle, so these paths should be monitored as well.(Citation: Adding Login Items)\n\nMonitor process execution for abnormal process execution resulting from modified plist files. Monitor utilities used to modify plist files or that take a plist file as an argument, which may indicate suspicious activity.", "Type": 3, "Index": 4721}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 5865}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 4591}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5753}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5795}}', 'T1547.011', 'Adversaries may modify plist files to run a program during system boot or user login. Property list (plist) files contain all of the information that macOS and OS X uses to configure applications and services. These files are UTF-8 encoded and formatted like XML documents via a series of keys surrounded by < >. They detail when programs should execute, file paths to the executables, program arguments, required OS permissions, and many others. plists are located in certain locations depending on their purpose such as <code>/Library/Preferences</code> (which execute with elevated privileges) and <code>~/Library/Preferences</code> (which execute with a user''s privileges). 
INSERT INTO public.attack_patterns VALUES (364, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.729545+00', NULL, 'attack-pattern--8868cb5b-d575-4a60-acb2-07d37389a2fd', '[{"url": "https://attack.mitre.org/techniques/T1205/001", "external_id": "T1205.001", "source_name": "mitre-attack"}, {"url": "https://www.giac.org/paper/gcih/342/handle-cd00r-invisible-backdoor/103631", "description": "Hartrell, Greg. (2002, August). Get a handle on cd00r: The invisible backdoor. Retrieved October 13, 2018.", "source_name": "Hartrell cd00r 2002"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2753}, "x_mitre_detection": {"Num": 0, "Raw": "\"Record network packets sent to and from the system, looking for extraneous packets that do not belong to established flows.\"", "Str": "Record network packets sent to and from the system, looking for extraneous packets that do not belong to established flows.", "Type": 3, "Index": 2411}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 2658}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2277}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2569}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2611}}', 'T1205.001', 'Adversaries may use port knocking to hide open ports used for persistence or command and control. To enable a port, an adversary sends a series of attempted connections to a predefined sequence of closed ports. After the sequence is completed, opening a port is often accomplished by the host based firewall, but could also be implemented by custom software.
INSERT INTO public.attack_patterns VALUES (391, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.686349+00', NULL, 'attack-pattern--36b2a1d7-e09e-49bf-b45e-477076c2ec01', '[{"url": "https://attack.mitre.org/techniques/T1498/002", "external_id": "T1498.002", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/490.html", "external_id": "CAPEC-490", "source_name": "capec"}, {"url": "https://blog.cloudflare.com/reflections-on-reflections/", "description": "Marek Majkowsk, Cloudflare. (2017, May 24). Reflections on reflection (attacks). Retrieved April 23, 2019.", "source_name": "Cloudflare ReflectionDoS May 2017"}, {"url": "https://www.cloudflare.com/learning/ddos/dns-amplification-ddos-attack/", "description": "Cloudflare. (n.d.). What is a DNS amplification attack?. Retrieved April 23, 2019.", "source_name": "Cloudflare DNSamplficationDoS"}, {"url": "https://www.cloudflare.com/learning/ddos/ntp-amplification-ddos-attack/", "description": "Cloudflare. (n.d.). What is a NTP amplificaiton attack?. Retrieved April 23, 2019.", "source_name": "Cloudflare NTPamplifciationDoS"}, {"url": "https://pages.arbornetworks.com/rs/082-KNA-087/images/13th_Worldwide_Infrastructure_Security_Report.pdf", "description": "Philippe Alcoy, Steinthor Bjarnason, Paul Bowen, C.F. Chui, Kirill Kasavchnko, and Gary Sockrider of Netscout Arbor. (2018, January). Insight into the Global Threat Landscape - Netscout Arbor''s 13th Annual Worldwide Infrastructure Security Report. Retrieved April 22, 2019.", "source_name": "Arbor AnnualDoSreport Jan 2018"}, {"url": "https://blog.cloudflare.com/memcrashed-major-amplification-attacks-from-port-11211/", "description": "Marek Majkowski of Cloudflare. (2018, February 27). Memcrashed - Major amplification attacks from UDP port 11211. Retrieved April 18, 2019.", "source_name": "Cloudflare Memcrashed Feb 2018"}, {"url": "https://www.cisco.com/c/en/us/td/docs/ios-xml/ios/netflow/configuration/15-mt/nf-15-mt-book/nf-detct-analy-thrts.pdf", "description": "Cisco. (n.d.). Detecting and Analyzing Network Threats With NetFlow. Retrieved April 25, 2019.", "source_name": "Cisco DoSdetectNetflow"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 5989}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of reflection amplification can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect a reflection amplification DoS event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.\"", "Str": "Detection of reflection amplification can sometimes be achieved before the traffic volume is sufficient to cause impact to the availability of the service, but such response time typically requires very aggressive monitoring and responsiveness or services provided by an upstream network service provider. Typical network throughput monitoring tools such as netflow(Citation: Cisco DoSdetectNetflow), SNMP, and custom scripts can be used to detect sudden increases in network or service utilization. Real-time, automated, and qualitative study of the network traffic can identify a sudden surge in one type of protocol can be used to detect a reflection amplification DoS event as it starts. Often, the lead time may be small and the indicator of an event availability of the network or service drops. The analysis tools mentioned can then be used to determine the type of DoS causing the outage and help with remediation.", "Type": 3, "Index": 4774}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 5819}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 5727}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Sensor Health: Host Status\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 4658}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5788}}', 'T1498.002', 'Adversaries may attempt to cause a denial of service by reflecting a high-volume of network traffic to a target. This type of Network DoS takes advantage of a third-party server intermediary that hosts and will respond to a given spoofed source IP address. This third-party server is commonly termed a reflector. An adversary accomplishes a reflection attack by sending packets to reflectors with the spoofed address of the victim. Similar to Direct Network Floods, more than one system may be used to conduct the attack, or a botnet may be used. Likewise, one or more reflector may be used to focus traffic on the target.(Citation: Cloudflare ReflectionDoS May 2017)
INSERT INTO public.attack_patterns VALUES (368, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.845022+00', NULL, 'attack-pattern--0f2c410d-d740-4ed9-abb1-b8f4a7faf6c3', '[{"url": "https://attack.mitre.org/techniques/T1546/013", "external_id": "T1546.013", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/powershell/module/microsoft.powershell.core/about/about_profiles?view=powershell-6", "description": "Microsoft. (2017, November 29). About Profiles. Retrieved June 14, 2019.", "source_name": "Microsoft About Profiles"}, {"url": "https://www.welivesecurity.com/2019/05/29/turla-powershell-usage/", "description": "Faou, M. and Dumont R.. (2019, May 29). A dive into Turla PowerShell usage. Retrieved June 14, 2019.", "source_name": "ESET Turla PowerShell May 2019"}, {"url": "https://witsendandshady.blogspot.com/2019/06/lab-notes-persistence-and-privilege.html", "description": "DeRyke, A.. (2019, June 7). Lab Notes: Persistence and Privilege Elevation using the Powershell Profile. Retrieved July 8, 2019.", "source_name": "Wits End and Shady PowerShell Profiles"}, {"url": "http://www.malwarearchaeology.com/s/Windows-PowerShell-Logging-Cheat-Sheet-ver-June-2016-v2.pdf", "description": "Malware Archaeology. (2016, June). WINDOWS POWERSHELL LOGGING CHEAT SHEET - Win 7/Win 2008 or later. Retrieved June 24, 2016.", "source_name": "Malware Archaeology PowerShell Cheat Sheet"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4700}, "x_mitre_detection": {"Num": 0, "Raw": "\"Locations where <code>profile.ps1</code> can be stored should be monitored for new profiles or modifications. (Citation: Malware Archaeology PowerShell Cheat Sheet) Example profile locations include:\\n\\n* <code>$PsHome\\\\Profile.ps1</code>\\n* <code>$PsHome\\\\Microsoft.{HostProgram}_profile.ps1</code>\\n* <code>$Home\\\\My Documents\\\\PowerShell\\\\Profile.ps1</code>\\n* <code>$Home\\\\My Documents\\\\PowerShell\\\\Microsoft.{HostProgram}_profile.ps1</code>\\n\\nMonitor abnormal PowerShell commands, unusual loading of PowerShell drives or modules, and/or execution of unknown programs.\"", "Str": "Locations where <code>profile.ps1</code> can be stored should be monitored for new profiles or modifications. (Citation: Malware Archaeology PowerShell Cheat Sheet) Example profile locations include:\n\n* <code>$PsHome\\Profile.ps1</code>\n* <code>$PsHome\\Microsoft.{HostProgram}_profile.ps1</code>\n* <code>$Home\\My Documents\\PowerShell\\Profile.ps1</code>\n* <code>$Home\\My Documents\\PowerShell\\Microsoft.{HostProgram}_profile.ps1</code>\n\nMonitor abnormal PowerShell commands, unusual loading of PowerShell drives or modules, and/or execution of unknown programs.", "Type": 3, "Index": 3932}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4652}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Allen DeRyke, ICE\"\n    ]", "Str": "", "Type": 5, "Index": 3703}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"File: File Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3766}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4540}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 4582}}', 'T1546.013', 'Adversaries may gain persistence and elevate privileges by executing malicious content triggered by PowerShell profiles. A PowerShell profile  (<code>profile.ps1</code>) is a script that runs when [PowerShell](https://attack.mitre.org/techniques/T1059/001) starts and can be used as a logon script to customize user environments.
INSERT INTO public.attack_patterns VALUES (370, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.920243+00', NULL, 'attack-pattern--2de47683-f398-448f-b947-9abcc3e32fad', '[{"url": "https://attack.mitre.org/techniques/T1547/012", "external_id": "T1547.012", "source_name": "mitre-attack"}, {"url": "https://docs.microsoft.com/en-us/windows/win32/printdocs/addprintprocessor", "description": "Microsoft. (2018, May 31). AddPrintProcessor function. Retrieved October 5, 2020.", "source_name": "Microsoft AddPrintProcessor May 2018"}, {"url": "https://www.welivesecurity.com/2020/05/21/no-game-over-winnti-group/", "description": "Tartare, M. et al. (2020, May 21). No “Game over” for the Winnti Group. Retrieved August 24, 2020.", "source_name": "ESET PipeMon May 2020"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4070}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process API calls to <code>AddPrintProcessor</code> and <code>GetPrintProcessorDirectory</code>. New print processor DLLs are written to the print processor directory. Also monitor Registry writes to <code>HKLM\\\\SYSTEM\\\\ControlSet001\\\\Control\\\\Print\\\\Environments\\\\\\\\[Windows architecture]\\\\Print Processors\\\\\\\\[user defined]\\\\\\\\Driver</code> or <code>HKLM\\\\SYSTEM\\\\CurrentControlSet\\\\Control\\\\Print\\\\Environments\\\\\\\\[Windows architecture]\\\\Print Processors\\\\\\\\[user defined]\\\\Driver</code> as they pertain to print processor installations.\\n\\nMonitor for abnormal DLLs that are loaded by spoolsv.exe. Print processors that do not correlate with known good software or patching may be suspicious.\"", "Str": "Monitor process API calls to <code>AddPrintProcessor</code> and <code>GetPrintProcessorDirectory</code>. New print processor DLLs are written to the print processor directory. Also monitor Registry writes to <code>HKLM\\SYSTEM\\ControlSet001\\Control\\Print\\Environments\\\\[Windows architecture]\\Print Processors\\\\[user defined]\\\\Driver</code> or <code>HKLM\\SYSTEM\\CurrentControlSet\\Control\\Print\\Environments\\\\[Windows architecture]\\Print Processors\\\\[user defined]\\Driver</code> as they pertain to print processor installations.\n\nMonitor for abnormal DLLs that are loaded by spoolsv.exe. Print processors that do not correlate with known good software or patching may be suspicious.", "Type": 3, "Index": 3169}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4022}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Mathieu Tartare, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2886}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"Process: OS API Execution\",\n      \"Module: Module Load\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Driver: Driver Load\"\n    ]", "Str": "", "Type": 5, "Index": 2953}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3908}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3950}}', 'T1547.012', 'Adversaries may abuse print processors to run malicious DLLs during system boot for persistence and/or privilege escalation. Print processors are DLLs that are loaded by the print spooler service, spoolsv.exe, during boot. 
INSERT INTO public.attack_patterns VALUES (372, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:40.979398+00', NULL, 'attack-pattern--3120b9fa-23b8-4500-ae73-09494f607b7d', '[{"url": "https://attack.mitre.org/techniques/T1003/007", "external_id": "T1003.007", "source_name": "mitre-attack"}, {"url": "https://github.com/huntergregal/mimipenguin", "description": "Gregal, H. (2017, May 12). MimiPenguin. Retrieved December 5, 2017.", "source_name": "MimiPenguin GitHub May 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2768}, "x_mitre_detection": {"Num": 0, "Raw": "\"To obtain the passwords and hashes stored in memory, processes must open a maps file in the /proc filesystem for the process being analyzed. This file is stored under the path <code>/proc/\\\\*/maps</code>, where the <code>\\\\*</code> directory is the unique pid of the program being interrogated for such authentication data. The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes opening this file in the proc file system, alerting on the pid, process name, and arguments of such programs.\"", "Str": "To obtain the passwords and hashes stored in memory, processes must open a maps file in the /proc filesystem for the process being analyzed. This file is stored under the path <code>/proc/\\*/maps</code>, where the <code>\\*</code> directory is the unique pid of the program being interrogated for such authentication data. The AuditD monitoring tool, which ships stock in many Linux distributions, can be used to watch for hostile processes opening this file in the proc file system, alerting on the pid, process name, and arguments of such programs.", "Type": 3, "Index": 2047}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 2722}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Command: Command Execution\",\n      \"File: File Access\"\n    ]", "Str": "", "Type": 5, "Index": 1951}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2633}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 2675}}', 'T1003.007', 'Adversaries may gather credentials from information stored in the Proc filesystem or <code>/proc</code>. The Proc filesystem on Linux contains a great deal of information regarding the state of the running operating system. Processes running with root privileges can use this facility to scrape live memory of other running programs. If any of these programs store passwords in clear text or password hashes in memory, these values can then be harvested for either usage or brute force attacks, respectively.
INSERT INTO public.attack_patterns VALUES (376, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.077712+00', NULL, 'attack-pattern--b200542e-e877-4395-875b-cf1a44537ca4', '[{"url": "https://attack.mitre.org/techniques/T1055/012", "external_id": "T1055.012", "source_name": "mitre-attack"}, {"url": "http://www.autosectools.com/process-hollowing.pdf", "description": "Leitch, J. (n.d.). Process Hollowing. Retrieved November 12, 2014.", "source_name": "Leitch Hollowing"}, {"url": "https://www.endgame.com/blog/technical-blog/ten-process-injection-techniques-technical-survey-common-and-trending-process", "description": "Hosseini, A. (2017, July 18). Ten Process Injection Techniques: A Technical Survey Of Common And Trending Process Injection Techniques. Retrieved December 7, 2017.", "source_name": "Elastic Process Injection July 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4350}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\\n\\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. \"", "Str": "Monitoring Windows API calls indicative of the various types of code injection may generate a significant amount of data and may not be directly useful for defense unless collected under specific circumstances for known bad sequences of calls, since benign use of API functions may be common and difficult to distinguish from malicious behavior. Windows API calls such as <code>CreateRemoteThread</code>, <code>SuspendThread</code>/<code>SetThreadContext</code>/<code>ResumeThread</code>, and those that can be used to modify memory within another process, such as <code>VirtualAllocEx</code>/<code>WriteProcessMemory</code>, may be used for this technique.(Citation: Elastic Process Injection July 2017)\n\nAnalyze process behavior to determine if a process is performing actions it usually does not, such as opening network connections, reading files, or other suspicious actions that could relate to post-compromise behavior. ", "Type": 3, "Index": 3249}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4302}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Process: Process Access\"\n    ]", "Str": "", "Type": 5, "Index": 3059}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4213}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 3167}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4255}}', 'T1055.012', 'Adversaries may inject malicious code into suspended and hollowed processes in order to evade process-based defenses. Process hollowing is a method of executing arbitrary code in the address space of a separate live process.  
INSERT INTO public.attack_patterns VALUES (418, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.645748+00', NULL, 'attack-pattern--2db31dcd-54da-405d-acef-b9129b816ed6', '[{"url": "https://attack.mitre.org/techniques/T1021/004", "external_id": "T1021.004", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/555.html", "external_id": "CAPEC-555", "source_name": "capec"}, {"url": "https://www.ssh.com/ssh", "description": "SSH.COM. (n.d.). SSH (Secure Shell). Retrieved March 23, 2020.", "source_name": "SSH Secure Shell"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2692}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use of SSH may be legitimate depending on the environment and how it’s used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with SSH. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.\"", "Str": "Use of SSH may be legitimate depending on the environment and how it’s used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with SSH. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.", "Type": 3, "Index": 2096}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2538}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1927}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2507}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"An SSH server is configured and running.\"\n    ]", "Str": "", "Type": 5, "Index": 2611}}', 'T1021.004', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to log into remote machines using Secure Shell (SSH). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (379, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.218353+00', NULL, 'attack-pattern--4fe28b27-b13c-453e-a386-c2ef362a573b', '[{"url": "https://attack.mitre.org/techniques/T1572", "external_id": "T1572", "source_name": "mitre-attack"}, {"url": "https://www.ssh.com/ssh/tunneling", "description": "SSH.COM. (n.d.). SSH tunnel. Retrieved March 15, 2020.", "source_name": "SSH Tunneling"}, {"url": "https://www.bleepingcomputer.com/news/security/new-godlua-malware-evades-traffic-monitoring-via-dns-over-https/", "description": "Gatlan, S. (2019, July 3). New Godlua Malware Evades Traffic Monitoring via DNS over HTTPS. Retrieved March 15, 2020.", "source_name": "BleepingComp Godlua JUL19"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4295}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring for systems listening and/or establishing external connections using ports/protocols commonly associated with tunneling, such as SSH (port 22). Also monitor for processes commonly associated with tunneling, such as Plink and the OpenSSH client. \\n\\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)\"", "Str": "Monitoring for systems listening and/or establishing external connections using ports/protocols commonly associated with tunneling, such as SSH (port 22). Also monitor for processes commonly associated with tunneling, such as Plink and the OpenSSH client. \n\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol standards regarding syntax, structure, or any other variable adversaries could leverage to conceal data.(Citation: University of Birmingham C2)", "Type": 3, "Index": 3391}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4217}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3207}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4185}}', 'T1572', 'Adversaries may tunnel network communications to and from a victim system within a separate protocol to avoid detection/network filtering and/or enable access to otherwise unreachable systems. Tunneling involves explicitly encapsulating a protocol within another. This behavior may conceal malicious traffic by blending in with existing traffic and/or provide an outer layer of encryption (similar to a VPN). Tunneling could also enable routing of network packets that would otherwise not reach their intended destination, such as SMB, RDP, or other traffic that would be filtered by network appliances or not routed over the Internet. 
INSERT INTO public.attack_patterns VALUES (380, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.285297+00', NULL, 'attack-pattern--731f4f55-b6d0-41d1-a7a9-072a66389aea', '[{"url": "https://attack.mitre.org/techniques/T1090", "external_id": "T1090", "source_name": "mitre-attack"}, {"url": "http://blog.trendmicro.com/trendlabs-security-intelligence/in-depth-look-apt-attack-tools-of-the-trade/", "description": "Wilhoit, K. (2013, March 4). In-Depth Look: APT Attack Tools of the Trade. Retrieved December 2, 2015.", "source_name": "Trend Micro APT Attack Tools"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"3.1\"", "Str": "3.1", "Type": 3, "Index": 3477}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server or between clients that should not or often do not communicate with one another). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\\n\\nConsider monitoring for traffic to known anonymity networks (such as [Tor](https://attack.mitre.org/software/S0183)).\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server or between clients that should not or often do not communicate with one another). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used. (Citation: University of Birmingham C2)\n\nConsider monitoring for traffic to known anonymity networks (such as [Tor](https://attack.mitre.org/software/S0183)).", "Type": 3, "Index": 2690}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\",\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3382}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Brian Prange\",\n      \"Heather Linn\",\n      \"Walker Johnson\"\n    ]", "Str": "", "Type": 5, "Index": 2402}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2506}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3350}}', 'T1090', 'Adversaries may use a connection proxy to direct network traffic between systems or act as an intermediary for network communications to a command and control server to avoid direct connections to their infrastructure. Many tools exist that enable traffic redirection through proxies or port redirection, including [HTRAN](https://attack.mitre.org/software/S0040), ZXProxy, and ZXPortMap. (Citation: Trend Micro APT Attack Tools) Adversaries use these types of proxies to manage command and control communications, reduce the number of simultaneous outbound network connections, provide resiliency in the face of connection loss, or to ride over existing trusted communications paths between victims to avoid suspicion. Adversaries may chain together multiple proxies to further disguise the source of malicious traffic.
INSERT INTO public.attack_patterns VALUES (385, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.472771+00', NULL, 'attack-pattern--c32f7008-9fea-41f7-8366-5eb9b74bd896', '[{"url": "https://attack.mitre.org/techniques/T1012", "external_id": "T1012", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/647.html", "external_id": "CAPEC-647", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Windows_Registry", "description": "Wikipedia. (n.d.). Windows Registry. Retrieved February 2, 2015.", "source_name": "Wikipedia Windows Registry"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3378}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\\n\\nInteraction with the Windows Registry may come from the command line using utilities such as [Reg](https://attack.mitre.org/software/S0075) or through running malware that may interact with the Registry through an API. Command-line invocation of utilities used to query the Registry may be detected through process and command-line monitoring. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as Lateral Movement, based on the information obtained.\n\nInteraction with the Windows Registry may come from the command line using utilities such as [Reg](https://attack.mitre.org/software/S0075) or through running malware that may interact with the Registry through an API. Command-line invocation of utilities used to query the Registry may be detected through process and command-line monitoring. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).", "Type": 3, "Index": 2182}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3330}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Access\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1988}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3201}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 3244}}', 'T1012', 'Adversaries may interact with the Windows Registry to gather information about the system, configuration, and installed software.
INSERT INTO public.attack_patterns VALUES (386, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.509552+00', NULL, 'attack-pattern--dca670cf-eeec-438f-8185-fd959d9ef211', '[{"url": "https://attack.mitre.org/techniques/T1037/004", "external_id": "T1037.004", "source_name": "mitre-attack"}, {"url": "https://iranthreats.github.io/resources/attribution-flying-rocket-kitten/", "description": "Iran Threats . (2017, December 5). Flying Kitten to Rocket Kitten, A Case of Ambiguity and Shared Code. Retrieved May 28, 2020.", "source_name": "IranThreats Kittens Dec 2017"}, {"url": "https://www.intezer.com/blog-hiddenwasp-malware-targeting-linux-systems/", "description": "Sanmillan, I. (2019, May 29). HiddenWasp Malware Stings Targeted Linux Systems. Retrieved June 24, 2019.", "source_name": "Intezer HiddenWasp Map 2019"}, {"url": "https://www.intezer.com/blog/research/kaiji-new-chinese-linux-malware-turning-to-golang/", "description": "Paul Litvak. (2020, May 4). Kaiji: New Chinese Linux malware turning to Golang. Retrieved December 17, 2020.", "source_name": "intezer-kaiji-malware"}, {"url": "https://developer.apple.com/library/archive/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/CreatingLaunchdJobs.html", "description": "Apple. (2016, September 13). Daemons and Services Programming Guide - Creating Launch Daemons and Agents. Retrieved February 24, 2021.", "source_name": "Apple Developer Doco Archive Launchd"}, {"url": "https://developer.apple.com/library/content/documentation/MacOSX/Conceptual/BPSystemStartup/Chapters/StartupItems.html", "description": "Apple. (2016, September 13). Startup Items. Retrieved July 11, 2017.", "source_name": "Startup Items"}, {"url": "https://www.virusbulletin.com/uploads/pdf/conference/vb2014/VB2014-Wardle.pdf", "description": "Patrick Wardle. (2014, September). Methods of Malware Persistence on Mac OS X. Retrieved July 5, 2017.", "source_name": "Methods of Mac Malware Persistence"}, {"url": "http://manpages.ubuntu.com/manpages/bionic/man8/systemd-rc-local-generator.8.html", "description": "Canonical Ltd.. (n.d.). systemd-rc-local-generator - Compatibility generator for starting /etc/rc.local and        /usr/sbin/halt.local during boot and shutdown. Retrieved February 23, 2021.", "source_name": "Ubuntu Manpage systemd rc"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 5395}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unexpected changes to RC scripts in the <code>/etc/</code> directory. Monitor process execution resulting from RC scripts for unusual or unknown applications or behavior.\\n\\nMonitor for <code>/etc/rc.local</code> file creation. Although types of RC scripts vary for each Unix-like distribution, several execute <code>/etc/rc.local</code> if present. \"", "Str": "Monitor for unexpected changes to RC scripts in the <code>/etc/</code> directory. Monitor process execution resulting from RC scripts for unusual or unknown applications or behavior.\n\nMonitor for <code>/etc/rc.local</code> file creation. Although types of RC scripts vary for each Unix-like distribution, several execute <code>/etc/rc.local</code> if present. ", "Type": 3, "Index": 4848}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 5334}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 4682}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5245}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 5287}}', 'T1037.004', 'Adversaries may establish persistence by modifying RC scripts which are executed during a Unix-like system’s startup. These files allow system administrators to map and start custom services at startup for different run levels. RC scripts require root privileges to modify.
INSERT INTO public.attack_patterns VALUES (395, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.826662+00', NULL, 'attack-pattern--4061e78c-1284-44b4-9116-73e4ac3912f7', '[{"url": "https://attack.mitre.org/techniques/T1219", "external_id": "T1219", "source_name": "mitre-attack"}, {"url": "https://www.symantec.com/content/dam/symantec/docs/security-center/white-papers/istr-living-off-the-land-and-fileless-attack-techniques-en.pdf", "description": "Wueest, C., Anand, H. (2017, July). Living off the land and fileless attack techniques. Retrieved April 10, 2018.", "source_name": "Symantec Living off the Land"}, {"url": "https://go.crowdstrike.com/rs/281-OBQ-266/images/15GlobalThreatReport.pdf", "description": "CrowdStrike Intelligence. (2016). 2015 Global Threat Report. Retrieved April 11, 2018.", "source_name": "CrowdStrike 2015 Global Threat Report"}, {"url": "https://blog.crysys.hu/2013/03/teamspy/", "description": "CrySyS Lab. (2013, March 20). TeamSpy – Obshie manevri. Ispolzovat’ tolko s razreshenija S-a. Retrieved April 11, 2018.", "source_name": "CrySyS Blog TeamSpy"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 4371}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for applications and processes related to remote admin tools. Correlate activity with other suspicious behavior that may reduce false positives if these tools are used by legitimate users and administrators.\\n\\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol for the port that is being used.\\n\\n[Domain Fronting](https://attack.mitre.org/techniques/T1090/004) may be used in conjunction to avoid defenses. Adversaries will likely need to deploy and/or install these remote tools to compromised systems. It may be possible to detect or prevent the installation of these tools with host-based solutions.\"", "Str": "Monitor for applications and processes related to remote admin tools. Correlate activity with other suspicious behavior that may reduce false positives if these tools are used by legitimate users and administrators.\n\nAnalyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect application layer protocols that do not follow the expected protocol for the port that is being used.\n\n[Domain Fronting](https://attack.mitre.org/techniques/T1090/004) may be used in conjunction to avoid defenses. Adversaries will likely need to deploy and/or install these remote tools to compromised systems. It may be possible to detect or prevent the installation of these tools with host-based solutions.", "Type": 3, "Index": 3209}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4293}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matt Kelly, @breakersall\"\n    ]", "Str": "", "Type": 5, "Index": 2920}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2990}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4161}, "x_mitre_network_requirements": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4204}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4246}}', 'T1219', 'An adversary may use legitimate desktop support and remote access software, such as Team Viewer, Go2Assist, LogMein, AmmyyAdmin, etc, to establish an interactive command and control channel to target systems within networks. These services are commonly used as legitimate technical support software, and may be allowed by application control within a target environment. Remote access tools like VNC, Ammyy, and Teamviewer are used frequently when compared with other legitimate software commonly used by adversaries. (Citation: Symantec Living off the Land)
INSERT INTO public.attack_patterns VALUES (397, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.914364+00', NULL, 'attack-pattern--eb062747-2193-45de-8fa2-e62549c37ddf', '[{"url": "https://attack.mitre.org/techniques/T1021/001", "external_id": "T1021.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/555.html", "external_id": "CAPEC-555", "source_name": "capec"}, {"url": "https://technet.microsoft.com/en-us/windowsserver/ee236407.aspx", "description": "Microsoft. (n.d.). Remote Desktop Services. Retrieved June 1, 2016.", "source_name": "TechNet Remote Desktop Services"}, {"url": "http://blog.crowdstrike.com/adversary-tricks-crowdstrike-treats/", "description": "Alperovitch, D. (2014, October 31). Malware-Free Intrusions. Retrieved November 4, 2014.", "source_name": "Alperovitch Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3477}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use of RDP may be legitimate, depending on the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with RDP. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.\"", "Str": "Use of RDP may be legitimate, depending on the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with RDP. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time.", "Type": 3, "Index": 2776}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3314}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 2485}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Flow\",\n      \"Logon Session: Logon Session Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2560}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3195}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"RDP service enabled, account in the Remote Desktop Users group\"\n    ]", "Str": "", "Type": 5, "Index": 3374}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Remote Desktop Users\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3237}}', 'T1021.001', 'Adversaries may use [Valid Accounts](https://attack.mitre.org/techniques/T1078) to log into a computer using the Remote Desktop Protocol (RDP). The adversary may then perform actions as the logged-on user.
INSERT INTO public.attack_patterns VALUES (398, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:41.951945+00', NULL, 'attack-pattern--b4694861-542c-48ea-9eb1-10d356e7140a', '[{"url": "https://attack.mitre.org/techniques/T1114/002", "external_id": "T1114.002", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1836}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for unusual login activity from unknown or abnormal locations, especially for privileged accounts (ex: Exchange administrator account).\"", "Str": "Monitor for unusual login activity from unknown or abnormal locations, especially for privileged accounts (ex: Exchange administrator account).", "Type": 3, "Index": 1533}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Office 365\",\n      \"Windows\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 1742}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 1363}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 1711}}', 'T1114.002', 'Adversaries may target an Exchange server, Office 365, or Google Workspace to collect sensitive information. Adversaries may leverage a user''s credentials and interact directly with the Exchange server to acquire information from within a network. Adversaries may also access externally facing Exchange services, Office 365, or Google Workspace to access email using credentials or access tokens. Tools such as [MailSniper](https://attack.mitre.org/software/S0413) can be used to automate searches for specific keywords.', 'Remote Email Collection', 177);
INSERT INTO public.attack_patterns VALUES (402, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.14628+00', NULL, 'attack-pattern--bd5b58a4-a52d-4a29-bc0d-3f1d3968eb6b', '[{"url": "https://attack.mitre.org/techniques/T1036/003", "external_id": "T1036.003", "source_name": "mitre-attack"}, {"url": "https://lolbas-project.github.io/", "description": "LOLBAS. (n.d.). Living Off The Land Binaries and Scripts (and also Libraries). Retrieved February 10, 2020.", "source_name": "LOLBAS Main Site"}, {"url": "http://pages.endgame.com/rs/627-YBU-612/images/EndgameJournal_The%20Masquerade%20Ball_Pages_R2.pdf", "description": "Ewing, P. (2016, October 31). How to Hunt: The Masquerade Ball. Retrieved October 31, 2016.", "source_name": "Elastic Masquerade Ball"}, {"url": "https://blog-assets.f-secure.com/wp-content/uploads/2019/10/15163418/CozyDuke.pdf", "description": "F-Secure Labs. (2015, April 22). CozyDuke: Malware Analysis. Retrieved December 10, 2015.", "source_name": "F-Secure CozyDuke"}, {"url": "https://twitter.com/ItsReallyNick/status/1055321652777619457", "description": "Carr, N.. (2018, October 25). Nick Carr Status Update Masquerading. Retrieved April 22, 2019.", "source_name": "Twitter ItsReallyNick Masquerading Update"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3629}, "x_mitre_detection": {"Num": 0, "Raw": "\"If file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)\"", "Str": "If file names are mismatched between the file name on disk and that of the binary''s PE metadata, this is a likely indicator that a binary was renamed after it was compiled. Collecting and comparing disk and resource filenames for binaries by looking to see if the InternalName, OriginalFilename, and/or ProductName match what is expected could provide useful leads, but may not always be indicative of malicious activity. (Citation: Elastic Masquerade Ball) Do not focus on the possible names a file could have, but instead on the command-line arguments that are known to be used and are distinct because it will have a better rate of detection.(Citation: Twitter ItsReallyNick Masquerading Update)", "Type": 3, "Index": 2787}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3551}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Modification\",\n      \"Process: Process Metadata\",\n      \"Command: Command Execution\",\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2621}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3520}}', 'T1036.003', 'Adversaries may rename legitimate system utilities to try to evade security mechanisms concerning the usage of those utilities. Security monitoring and control mechanisms may be in place for system utilities adversaries are capable of abusing. (Citation: LOLBAS Main Site) It may be possible to bypass those security mechanisms by renaming the utility prior to utilization (ex: rename <code>rundll32.exe</code>). (Citation: Elastic Masquerade Ball) An alternative case occurs when a legitimate utility is copied or moved to a different directory and renamed to avoid detections based on system utilities executing from non-standard paths. (Citation: F-Secure CozyDuke)', 'Rename System Utilities', 302);
INSERT INTO public.attack_patterns VALUES (405, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.254966+00', NULL, 'attack-pattern--0708ae90-d0eb-4938-9a76-d0fc94f6eec1', '[{"url": "https://attack.mitre.org/techniques/T1578/004", "external_id": "T1578.004", "source_name": "mitre-attack"}, {"url": "https://www.techrepublic.com/blog/the-enterprise-cloud/backing-up-and-restoring-snapshots-on-amazon-ec2-machines/", "description": "Hardiman, N.. (2012, March 20). Backing up and restoring snapshots on Amazon EC2 machines. Retrieved October 8, 2019.", "source_name": "Tech Republic - Restore AWS Snapshots"}, {"url": "https://cloud.google.com/compute/docs/disks/restore-and-delete-snapshots", "description": "Google. (2019, October 7). Restoring and deleting persistent disk snapshots. Retrieved October 8, 2019.", "source_name": "Google - Restore Cloud Snapshot"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3207}, "x_mitre_detection": {"Num": 0, "Raw": "\"Establish centralized logging of instance activity, which can be used to monitor and review system events even after reverting to a snapshot, rolling back changes, or changing persistence/type of storage. Monitor specifically for events related to snapshots and rollbacks and VM configuration changes, that are occurring outside of normal activity. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.\"", "Str": "Establish centralized logging of instance activity, which can be used to monitor and review system events even after reverting to a snapshot, rolling back changes, or changing persistence/type of storage. Monitor specifically for events related to snapshots and rollbacks and VM configuration changes, that are occurring outside of normal activity. To reduce false positives, valid change management procedures could introduce a known identifier that is logged with the change (e.g., tag or header) if supported by the cloud provider, to help distinguish valid, expected actions from malicious ones.", "Type": 3, "Index": 2439}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 3162}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Netskope\"\n    ]", "Str": "", "Type": 5, "Index": 2244}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Instance: Instance Modification\",\n      \"Instance: Instance Start\",\n      \"Instance: Instance Stop\"\n    ]", "Str": "", "Type": 5, "Index": 2298}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3073}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3115}}', 'T1578.004', 'An adversary may revert changes made to a cloud instance after they have performed malicious activities in attempt to evade detection and remove evidence of their presence. In highly virtualized environments, such as cloud-based infrastructure, this may be accomplished by restoring virtual machine (VM) or data storage snapshots through the cloud management dashboard or cloud APIs.
INSERT INTO public.attack_patterns VALUES (407, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.324329+00', NULL, 'attack-pattern--564998d8-ab3e-4123-93fb-eccaa6b9714a', '[{"url": "https://attack.mitre.org/techniques/T1207", "external_id": "T1207", "source_name": "mitre-attack"}, {"url": "https://www.dcshadow.com/", "description": "Delpy, B. & LE TOUX, V. (n.d.). DCShadow. Retrieved March 20, 2018.", "source_name": "DCShadow Blog"}, {"url": "https://adsecurity.org/?page_id=1821", "description": "Metcalf, S. (2015, November 13). Unofficial Guide to Mimikatz & Command Reference. Retrieved December 23, 2015.", "source_name": "Adsecurity Mimikatz Guide"}, {"url": "https://github.com/shellster/DCSYNCMonitor", "description": "Spencer S. (2018, February 22). DCSYNCMonitor. Retrieved March 30, 2018.", "source_name": "GitHub DCSYNCMonitor"}, {"url": "https://msdn.microsoft.com/en-us/library/ms677626.aspx", "description": "Microsoft. (n.d.). Polling for Changes Using the DirSync Control. Retrieved March 30, 2018.", "source_name": "Microsoft DirSync"}, {"url": "https://adds-security.blogspot.fr/2018/02/detecter-dcshadow-impossible.html", "description": "Lucand,G. (2018, February 18). Detect DCShadow, impossible?. Retrieved March 30, 2018.", "source_name": "ADDSecurity DCShadow Feb 2018"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 5436}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor and analyze network traffic associated with data replication (such as calls to DrsAddEntry, DrsReplicaAdd, and especially GetNCChanges) between DCs as well as to/from non DC hosts. (Citation: GitHub DCSYNCMonitor) (Citation: DCShadow Blog) DC replication will naturally take place every 15 minutes but can be triggered by an attacker or by legitimate urgent changes (ex: passwords). Also consider monitoring and alerting on the replication of AD objects (Audit Detailed Directory Service Replication Events 4928 and 4929). (Citation: DCShadow Blog)\\n\\nLeverage AD directory synchronization (DirSync) to monitor changes to directory state using AD replication cookies. (Citation: Microsoft DirSync) (Citation: ADDSecurity DCShadow Feb 2018)\\n\\nBaseline and periodically analyze the Configuration partition of the AD schema and alert on creation of nTDSDSA objects. (Citation: DCShadow Blog)\\n\\nInvestigate usage of Kerberos Service Principal Names (SPNs), especially those associated with services (beginning with “GC/”) by computers not present in the DC organizational unit (OU). The SPN associated with the Directory Replication Service (DRS) Remote Protocol interface (GUID E3514235–4B06–11D1-AB04–00C04FC2DCD2) can be set without logging. (Citation: ADDSecurity DCShadow Feb 2018) A rogue DC must authenticate as a service using these two SPNs for the replication process to successfully complete.\"", "Str": "Monitor and analyze network traffic associated with data replication (such as calls to DrsAddEntry, DrsReplicaAdd, and especially GetNCChanges) between DCs as well as to/from non DC hosts. (Citation: GitHub DCSYNCMonitor) (Citation: DCShadow Blog) DC replication will naturally take place every 15 minutes but can be triggered by an attacker or by legitimate urgent changes (ex: passwords). Also consider monitoring and alerting on the replication of AD objects (Audit Detailed Directory Service Replication Events 4928 and 4929). (Citation: DCShadow Blog)\n\nLeverage AD directory synchronization (DirSync) to monitor changes to directory state using AD replication cookies. (Citation: Microsoft DirSync) (Citation: ADDSecurity DCShadow Feb 2018)\n\nBaseline and periodically analyze the Configuration partition of the AD schema and alert on creation of nTDSDSA objects. (Citation: DCShadow Blog)\n\nInvestigate usage of Kerberos Service Principal Names (SPNs), especially those associated with services (beginning with “GC/”) by computers not present in the DC organizational unit (OU). The SPN associated with the Directory Replication Service (DRS) Remote Protocol interface (GUID E3514235–4B06–11D1-AB04–00C04FC2DCD2) can be set without logging. (Citation: ADDSecurity DCShadow Feb 2018) A rogue DC must authenticate as a service using these two SPNs for the replication process to successfully complete.", "Type": 3, "Index": 3835}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5388}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Vincent Le Toux\"\n    ]", "Str": "", "Type": 5, "Index": 3454}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Active Directory: Active Directory Object Creation\",\n      \"Active Directory: Active Directory Object Modification\",\n      \"Network Traffic: Network Traffic Content\",\n      \"User Account: User Account Authentication\"\n    ]", "Str": "", "Type": 5, "Index": 3515}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 5289}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Log analysis\"\n    ]", "Str": "", "Type": 5, "Index": 3780}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5332}}', 'T1207', 'Adversaries may register a rogue Domain Controller to enable manipulation of Active Directory data. DCShadow may be used to create a rogue Domain Controller (DC). DCShadow is a method of manipulating Active Directory (AD) data, including objects and schemas, by registering (or reusing an inactive registration) and simulating the behavior of a DC. (Citation: DCShadow Blog) Once registered, a rogue DC may be able to inject and replicate changes into AD infrastructure for any domain object, including credentials and keys.
INSERT INTO public.attack_patterns VALUES (410, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.431622+00', NULL, 'attack-pattern--045d0922-2310-4e60-b5e4-3302302cb3c5', '[{"url": "https://attack.mitre.org/techniques/T1218/011", "external_id": "T1218.011", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.de/cloud-content/us/pdfs/security-intelligence/white-papers/wp-cpl-malware.pdf", "description": "Merces, F. (2014). CPL Malware Malicious Control Panel Items. Retrieved November 1, 2017.", "source_name": "Trend Micro CPL"}, {"url": "https://thisissecurity.stormshield.com/2014/08/20/poweliks-command-line-confusion/", "description": "B. Ancel. (2014, August 20). Poweliks – Command Line Confusion. Retrieved March 5, 2018.", "source_name": "This is Security Command Line Confusion"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3451}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use process monitoring to monitor the execution and arguments of rundll32.exe. Compare recent invocations of rundll32.exe with prior history of known good arguments and loaded DLLs to determine anomalous and potentially adversarial activity. Command arguments used with the rundll32.exe invocation may also be useful in determining the origin and purpose of the DLL being loaded.\"", "Str": "Use process monitoring to monitor the execution and arguments of rundll32.exe. Compare recent invocations of rundll32.exe with prior history of known good arguments and loaded DLLs to determine anomalous and potentially adversarial activity. Command arguments used with the rundll32.exe invocation may also be useful in determining the origin and purpose of the DLL being loaded.", "Type": 3, "Index": 2900}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3403}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Casey Smith\",\n      \"Ricardo Dias\"\n    ]", "Str": "", "Type": 5, "Index": 2559}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Module: Module Load\"\n    ]", "Str": "", "Type": 5, "Index": 2638}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3314}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Digital Certificate Validation\",\n      \"Application control\",\n      \"Anti-virus\"\n    ]", "Str": "", "Type": 5, "Index": 2778}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3356}}', 'T1218.011', 'Adversaries may abuse rundll32.exe to proxy execution of malicious code. Using rundll32.exe, vice executing directly (i.e. [Shared Modules](https://attack.mitre.org/techniques/T1129)), may avoid triggering security tools that may not monitor execution of the rundll32.exe process because of allowlists or false positives from normal operations. Rundll32.exe is commonly associated with executing DLL payloads.
INSERT INTO public.attack_patterns VALUES (413, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.492519+00', NULL, 'attack-pattern--b7dc639b-24cd-482d-a7f1-8897eda21023', '[{"url": "https://attack.mitre.org/techniques/T1134/005", "external_id": "T1134.005", "source_name": "mitre-attack"}, {"url": "https://msdn.microsoft.com/library/windows/desktop/aa379571.aspx", "description": "Microsoft. (n.d.). Security Identifiers. Retrieved November 30, 2017.", "source_name": "Microsoft SID"}, {"url": "https://msdn.microsoft.com/library/ms679833.aspx", "description": "Microsoft. (n.d.). Active Directory Schema - SID-History attribute. Retrieved November 30, 2017.", "source_name": "Microsoft SID-History Attribute"}, {"url": "https://support.microsoft.com/help/243330/well-known-security-identifiers-in-windows-operating-systems", "description": "Microsoft. (2017, June 23). Well-known security identifiers in Windows operating systems. Retrieved November 30, 2017.", "source_name": "Microsoft Well Known SIDs Jun 2017"}, {"url": "https://technet.microsoft.com/library/ee617241.aspx", "description": "Microsoft. (n.d.). Active Directory Cmdlets - Get-ADUser. Retrieved November 30, 2017.", "source_name": "Microsoft Get-ADUser"}, {"url": "https://adsecurity.org/?p=1772", "description": "Metcalf, S. (2015, September 19). Sneaky Active Directory Persistence #14: SID History. Retrieved November 30, 2017.", "source_name": "AdSecurity SID History Sept 2015"}, {"url": "https://msdn.microsoft.com/library/ms677982.aspx", "description": "Microsoft. (n.d.). Using DsAddSidHistory. Retrieved November 30, 2017.", "source_name": "Microsoft DsAddSidHistory"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4759}, "x_mitre_detection": {"Num": 0, "Raw": "\"Examine data in user’s SID-History attributes using the PowerShell <code>Get-ADUser</code> cmdlet (Citation: Microsoft Get-ADUser), especially users who have SID-History values from the same domain. (Citation: AdSecurity SID History Sept 2015) Also monitor account management events on Domain Controllers for successful and failed changes to SID-History. (Citation: AdSecurity SID History Sept 2015) (Citation: Microsoft DsAddSidHistory)\\n\\nMonitor for Windows API calls to the <code>DsAddSidHistory</code> function. (Citation: Microsoft DsAddSidHistory)\"", "Str": "Examine data in user’s SID-History attributes using the PowerShell <code>Get-ADUser</code> cmdlet (Citation: Microsoft Get-ADUser), especially users who have SID-History values from the same domain. (Citation: AdSecurity SID History Sept 2015) Also monitor account management events on Domain Controllers for successful and failed changes to SID-History. (Citation: AdSecurity SID History Sept 2015) (Citation: Microsoft DsAddSidHistory)\n\nMonitor for Windows API calls to the <code>DsAddSidHistory</code> function. (Citation: Microsoft DsAddSidHistory)", "Type": 3, "Index": 4006}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4711}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Alain Homewood, Insomnia Security\",\n      \"Vincent Le Toux\"\n    ]", "Str": "", "Type": 5, "Index": 3725}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"User Account: User Account Metadata\",\n      \"Active Directory: Active Directory Object Modification\"\n    ]", "Str": "", "Type": 5, "Index": 3829}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4597}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\"\n    ]", "Str": "", "Type": 5, "Index": 4639}}', 'T1134.005', 'Adversaries may use SID-History Injection to escalate privileges and bypass access controls. The Windows security identifier (SID) is a unique value that identifies a user or group account. SIDs are used by Windows security in both security descriptors and access tokens. (Citation: Microsoft SID) An account can hold additional SIDs in the SID-History Active Directory attribute (Citation: Microsoft SID-History Attribute), allowing inter-operable account migration between domains (e.g., all values in SID-History are included in access tokens).
INSERT INTO public.attack_patterns VALUES (416, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.584846+00', NULL, 'attack-pattern--ee7ff928-801c-4f34-8a99-3df965e581a5', '[{"url": "https://attack.mitre.org/techniques/T1602/001", "external_id": "T1602.001", "source_name": "mitre-attack"}, {"url": "https://www.sans.org/reading-room/whitepapers/networkdevs/securing-snmp-net-snmp-snmpv3-1051", "description": "Michael Stump. (2003). Information Security Reading Room Securing SNMP: A Look atNet-SNMP (SNMPv3). Retrieved October 19, 2020.", "source_name": "SANS Information Security Reading Room Securing SNMP Securing SNMP"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA18-106A", "description": "US-CERT. (2018, April 20). Alert (TA18-106A) Russian State-Sponsored Cyber Actors Targeting Network Infrastructure Devices. Retrieved October 19, 2020.", "source_name": "US-CERT-TA18-106A"}, {"url": "https://community.cisco.com/t5/security-blogs/attackers-continue-to-target-legacy-devices/ba-p/4169954", "description": "Omar Santos. (2020, October 19). Attackers Continue to Target Legacy Devices. Retrieved October 20, 2020.", "source_name": "Cisco Blog Legacy Device Attacks"}, {"url": "https://tools.cisco.com/security/center/content/CiscoAppliedMitigationBulletin/cisco-amb-20080610-SNMPv3", "description": "Cisco. (2008, June 10). Identifying and Mitigating Exploitation of the SNMP Version 3 Authentication Vulnerabilities. Retrieved October 19, 2020.", "source_name": "Cisco Advisory SNMP v3 Authentication Vulnerabilities"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3884}, "x_mitre_detection": {"Num": 0, "Raw": "\"Identify network traffic sent or received by untrusted hosts or networks that expose MIB content or use unauthorized protocols.(Citation: Cisco Advisory SNMP v3 Authentication Vulnerabilities)\"", "Str": "Identify network traffic sent or received by untrusted hosts or networks that expose MIB content or use unauthorized protocols.(Citation: Cisco Advisory SNMP v3 Authentication Vulnerabilities)", "Type": 3, "Index": 3511}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 3836}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Connection Creation\",\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 3374}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3738}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 3780}}', 'T1602.001', 'Adversaries may target the Management Information Base (MIB) to collect and/or mine valuable information in a network managed using Simple Network Management Protocol (SNMP).
INSERT INTO public.attack_patterns VALUES (420, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.702739+00', NULL, 'attack-pattern--4d2a5b3e-340d-4600-9123-309dd63c9bf8', '[{"url": "https://attack.mitre.org/techniques/T1563/001", "external_id": "T1563.001", "source_name": "mitre-attack"}, {"url": "https://www.slideshare.net/morisson/mistrusting-and-abusing-ssh-13526219", "description": "Duarte, H., Morrison, B. (2012). (Mis)trusting and (ab)using ssh. Retrieved January 8, 2018.", "source_name": "Slideshare Abusing SSH"}, {"url": "https://www.blackhat.com/presentations/bh-usa-05/bh-us-05-boileau.pdf", "description": "Adam Boileau. (2005, August 5). Trust Transience:  Post Intrusion SSH Hijacking. Retrieved December 19, 2017.", "source_name": "SSHjack Blackhat"}, {"url": "https://www.clockwork.com/news/2012/09/28/602/ssh_agent_hijacking", "description": "Beuchler, B. (2012, September 28). SSH Agent Hijacking. Retrieved December 20, 2017.", "source_name": "Clockwork SSH Agent Hijacking"}, {"url": "https://matrix.org/blog/2019/05/08/post-mortem-and-remediations-for-apr-11-security-incident", "description": "Hodgson, M. (2019, May 8). Post-mortem and remediations for Apr 11 security incident. Retrieved February 17, 2020.", "source_name": "Breach Post-mortem SSH Hijack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4297}, "x_mitre_detection": {"Num": 0, "Raw": "\"Use of SSH may be legitimate, depending upon the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with SSH. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time. Also monitor user SSH-agent socket files being used by different users.\"", "Str": "Use of SSH may be legitimate, depending upon the network environment and how it is used. Other factors, such as access patterns and activity that occurs after a remote login, may indicate suspicious or malicious behavior with SSH. Monitor for user accounts logged into systems they would not normally access or access patterns to multiple systems over a relatively short period of time. Also monitor user SSH-agent socket files being used by different users.", "Type": 3, "Index": 3525}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4107}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Anastasios Pingios\"\n    ]", "Str": "", "Type": 5, "Index": 3213}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Flow\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Logon Session: Logon Session Creation\",\n      \"Command: Command Execution\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3277}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4018}, "x_mitre_system_requirements": {"Num": 0, "Raw": "[\n      \"SSH service enabled, trust relationships configured, established connections\"\n    ]", "Str": "", "Type": 5, "Index": 4180}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"root\"\n    ]", "Str": "", "Type": 5, "Index": 4060}}', 'T1563.001', 'Adversaries may hijack a legitimate user''s SSH session to move laterally within an environment. Secure Shell (SSH) is a standard means of remote access on Linux and macOS systems. It allows a user to connect to another system via an encrypted tunnel, commonly authenticating through a password, certificate or the use of an asymmetric encryption key pair.
INSERT INTO public.attack_patterns VALUES (423, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.793316+00', NULL, 'attack-pattern--005a06c6-14bf-4118-afa0-ebcd8aebb0c9', '[{"url": "https://attack.mitre.org/techniques/T1053/005", "external_id": "T1053.005", "source_name": "mitre-attack"}, {"url": "https://twitter.com/leoloobeek/status/939248813465853953", "description": "Loobeek, L. (2017, December 8). leoloobeek Status. Retrieved December 12, 2017.", "source_name": "Twitter Leoloobeek Scheduled Task"}, {"url": "https://social.technet.microsoft.com/Forums/en-US/e5bca729-52e7-4fcb-ba12-3225c564674c/scheduled-tasks-history-retention-settings?forum=winserver8gen", "description": "Satyajit321. (2015, November 3). Scheduled Tasks History Retention settings. Retrieved December 12, 2017.", "source_name": "TechNet Forum Scheduled Task Operational Setting"}, {"url": "https://technet.microsoft.com/library/dd315590.aspx", "description": "Microsoft. (n.d.). General Task Registration. Retrieved December 12, 2017.", "source_name": "TechNet Scheduled Task Events"}, {"url": "https://docs.microsoft.com/en-us/windows/security/threat-protection/auditing/audit-other-object-access-events", "description": "Microsoft. (2017, May 28). Audit Other Object Access Events. Retrieved June 27, 2019.", "source_name": "Microsoft Scheduled Task Events Win10"}, {"url": "https://technet.microsoft.com/en-us/sysinternals/bb963902", "description": "Russinovich, M. (2016, January 4). Autoruns for Windows v13.51. Retrieved June 6, 2016.", "source_name": "TechNet Autoruns"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 6027}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor process execution from the <code>svchost.exe</code> in Windows 10 and the Windows Task Scheduler <code>taskeng.exe</code> for older versions of Windows. (Citation: Twitter Leoloobeek Scheduled Task) If scheduled tasks are not used for persistence, then the adversary is likely to remove the task when the action is complete. Monitor Windows Task Scheduler stores in %systemroot%\\\\System32\\\\Tasks for change entries related to scheduled tasks that do not correlate with known software, patch cycles, etc.\\n\\nConfigure event logging for scheduled task creation and changes by enabling the \\\"Microsoft-Windows-TaskScheduler/Operational\\\" setting within the event logging service. (Citation: TechNet Forum Scheduled Task Operational Setting) Several events will then be logged on scheduled task activity, including: (Citation: TechNet Scheduled Task Events)(Citation: Microsoft Scheduled Task Events Win10)\\n\\n* Event ID 106 on Windows 7, Server 2008 R2 - Scheduled task registered\\n* Event ID 140 on Windows 7, Server 2008 R2 / 4702 on Windows 10, Server 2016 - Scheduled task updated\\n* Event ID 141 on Windows 7, Server 2008 R2 / 4699 on Windows 10, Server 2016 - Scheduled task deleted\\n* Event ID 4698 on Windows 10, Server 2016 - Scheduled task created\\n* Event ID 4700 on Windows 10, Server 2016 - Scheduled task enabled\\n* Event ID 4701 on Windows 10, Server 2016 - Scheduled task disabled\\n\\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current scheduled tasks. (Citation: TechNet Autoruns)\\n\\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Tasks may also be created through Windows system management tools such as Windows Management Instrumentation and PowerShell, so additional logging may need to be configured to gather the appropriate data.\"", "Str": "Monitor process execution from the <code>svchost.exe</code> in Windows 10 and the Windows Task Scheduler <code>taskeng.exe</code> for older versions of Windows. (Citation: Twitter Leoloobeek Scheduled Task) If scheduled tasks are not used for persistence, then the adversary is likely to remove the task when the action is complete. Monitor Windows Task Scheduler stores in %systemroot%\\System32\\Tasks for change entries related to scheduled tasks that do not correlate with known software, patch cycles, etc.\n\nConfigure event logging for scheduled task creation and changes by enabling the \"Microsoft-Windows-TaskScheduler/Operational\" setting within the event logging service. (Citation: TechNet Forum Scheduled Task Operational Setting) Several events will then be logged on scheduled task activity, including: (Citation: TechNet Scheduled Task Events)(Citation: Microsoft Scheduled Task Events Win10)\n\n* Event ID 106 on Windows 7, Server 2008 R2 - Scheduled task registered\n* Event ID 140 on Windows 7, Server 2008 R2 / 4702 on Windows 10, Server 2016 - Scheduled task updated\n* Event ID 141 on Windows 7, Server 2008 R2 / 4699 on Windows 10, Server 2016 - Scheduled task deleted\n* Event ID 4698 on Windows 10, Server 2016 - Scheduled task created\n* Event ID 4700 on Windows 10, Server 2016 - Scheduled task enabled\n* Event ID 4701 on Windows 10, Server 2016 - Scheduled task disabled\n\nTools such as Sysinternals Autoruns may also be used to detect system changes that could be attempts at persistence, including listing current scheduled tasks. (Citation: TechNet Autoruns)\n\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. Tasks may also be created through Windows system management tools such as Windows Management Instrumentation and PowerShell, so additional logging may need to be configured to gather the appropriate data.", "Type": 3, "Index": 3859}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 5943}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Scheduled Job: Scheduled Job Creation\",\n      \"Command: Command Execution\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 3675}, "x_mitre_remote_support": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5998}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 5845}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 5887}}', 'T1053.005', 'Adversaries may abuse the Windows Task Scheduler to perform task scheduling for initial or recurring execution of malicious code. There are multiple ways to access the Task Scheduler in Windows. The <code>schtasks</code> can be run directly on the command line, or the Task Scheduler can be opened through the GUI within the Administrator Tools section of the Control Panel. In some cases, adversaries have used a .NET wrapper for the Windows Task Scheduler, and alternatively, adversaries have used the Windows netapi32 library to create a scheduled task.
INSERT INTO public.attack_patterns VALUES (426, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.901428+00', NULL, 'attack-pattern--0259baeb-9f63-4c69-bf10-eb038c390688', '[{"url": "https://attack.mitre.org/techniques/T1113", "external_id": "T1113", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/648.html", "external_id": "CAPEC-648", "source_name": "capec"}, {"url": "https://docs.microsoft.com/en-us/dotnet/api/system.drawing.graphics.copyfromscreen?view=netframework-4.8", "description": "Microsoft. (n.d.). Graphics.CopyFromScreen Method. Retrieved March 24, 2020.", "source_name": "CopyFromScreen .NET"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2017/01/new-mac-backdoor-using-antiquated-code/", "description": "Thomas Reed. (2017, January 18). New Mac backdoor using antiquated code. Retrieved July 5, 2017.", "source_name": "Antiquated Mac Malware"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2763}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitoring for screen capture behavior will depend on the method used to obtain data from the operating system and write output files. Detection methods could include collecting information from unusual processes using API calls used to obtain image data, and monitoring for image files written to disk. The sensor data may need to be correlated with other events to identify malicious activity, depending on the legitimacy of this behavior within a given network environment.\"", "Str": "Monitoring for screen capture behavior will depend on the method used to obtain data from the operating system and write output files. Detection methods could include collecting information from unusual processes using API calls used to obtain image data, and monitoring for image files written to disk. The sensor data may need to be correlated with other events to identify malicious activity, depending on the legitimacy of this behavior within a given network environment.", "Type": 3, "Index": 2142}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2685}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2038}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2653}}', 'T1113', 'Adversaries may attempt to take screen captures of the desktop to gather information over the course of an operation. Screen capturing functionality may be included as a feature of a remote access tool used in post-compromise operations. Taking a screenshot is also typically possible through native utilities or API calls, such as <code>CopyFromScreen</code>, <code>xwd</code>, or <code>screencapture</code>.(Citation: CopyFromScreen .NET)(Citation: Antiquated Mac Malware)
INSERT INTO public.attack_patterns VALUES (428, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:42.963344+00', NULL, 'attack-pattern--a51eb150-93b1-484b-a503-e51453b127a4', '[{"url": "https://attack.mitre.org/techniques/T1597", "external_id": "T1597", "source_name": "mitre-attack"}, {"url": "https://d3security.com/blog/10-of-the-best-open-source-threat-intelligence-feeds/", "description": "Banerd, W. (2019, April 30). 10 of the Best Open Source Threat Intelligence Feeds. Retrieved October 20, 2020.", "source_name": "D3Secutrity CTI Feeds"}, {"url": "https://www.zdnet.com/article/a-hacker-group-is-selling-more-than-73-million-user-records-on-the-dark-web/", "description": "Cimpanu, C. (2020, May 9). A hacker group is selling more than 73 million user records on the dark web. Retrieved October 20, 2020.", "source_name": "ZDNET Selling Data"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3075}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2633}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3031}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2999}}', 'T1597', 'Adversaries may search and gather information about victims from closed sources that can be used during targeting. Information about victims may be available for purchase from reputable private sources and databases, such as paid subscriptions to feeds of technical/threat intelligence data.(Citation: D3Secutrity CTI Feeds) Adversaries may also purchase information from less-reputable sources such as dark web or cybercrime blackmarkets.(Citation: ZDNET Selling Data)
INSERT INTO public.attack_patterns VALUES (509, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:45.717301+00', NULL, 'attack-pattern--ff25900d-76d5-449b-a351-8824e62fc81b', '[{"url": "https://attack.mitre.org/techniques/T1127", "external_id": "T1127", "source_name": "mitre-attack"}, {"url": "https://enigma0x3.net/2016/11/17/bypassing-application-whitelisting-by-using-dnx-exe/", "description": "Nelson, M. (2017, November 17). Bypassing Application Whitelisting By Using dnx.exe. Retrieved May 25, 2017.", "source_name": "engima0x3 DNX Bypass"}, {"url": "https://enigma0x3.net/2016/11/21/bypassing-application-whitelisting-by-using-rcsi-exe/", "description": "Nelson, M. (2016, November 21). Bypassing Application Whitelisting By Using rcsi.exe. Retrieved May 26, 2017.", "source_name": "engima0x3 RCSI Bypass"}, {"url": "http://www.exploit-monday.com/2016/08/windbg-cdb-shellcode-runner.html", "description": "Graeber, M. (2016, August 15). Bypassing Application Whitelisting by using WinDbg/CDB as a Shellcode Runner. Retrieved May 26, 2017.", "source_name": "Exploit Monday WinDbg"}, {"url": "https://lolbas-project.github.io/lolbas/OtherMSBinaries/Tracker/", "description": "LOLBAS. (n.d.). Tracker.exe. Retrieved July 31, 2019.", "source_name": "LOLBAS Tracker"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3943}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for abnormal presence of these or other utilities that enable proxy execution that are typically used for development, debugging, and reverse engineering on a system that is not used for these purposes may be suspicious.\\n\\nUse process monitoring to monitor the execution and arguments of from developer utilities that may be abused. Compare recent invocations of those binaries with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. It is likely that these utilities will be used by software developers or for other software development related tasks, so if it exists and is used outside of that context, then the event may be suspicious. Command arguments used before and after invocation of the utilities may also be useful in determining the origin and purpose of the binary being executed.\"", "Str": "Monitor for abnormal presence of these or other utilities that enable proxy execution that are typically used for development, debugging, and reverse engineering on a system that is not used for these purposes may be suspicious.\n\nUse process monitoring to monitor the execution and arguments of from developer utilities that may be abused. Compare recent invocations of those binaries with prior history of known good arguments and executed binaries to determine anomalous and potentially adversarial activity. It is likely that these utilities will be used by software developers or for other software development related tasks, so if it exists and is used outside of that context, then the event may be suspicious. Command arguments used before and after invocation of the utilities may also be useful in determining the origin and purpose of the binary being executed.", "Type": 3, "Index": 2897}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3895}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Casey Smith\",\n      \"Matthew Demaske, Adaptforward\"\n    ]", "Str": "", "Type": 5, "Index": 2628}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2724}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3805}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Application control\"\n    ]", "Str": "", "Type": 5, "Index": 2835}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3848}}', 'T1127', 'Adversaries may take advantage of trusted developer utilities to proxy execution of malicious payloads. There are many utilities used for software development related tasks that can be used to execute code in various forms to assist in development, debugging, and reverse engineering.(Citation: engima0x3 DNX Bypass)(Citation: engima0x3 RCSI Bypass)(Citation: Exploit Monday WinDbg)(Citation: LOLBAS Tracker) These utilities may often be signed with legitimate certificates that allow them to execute on a system and proxy execution of malicious code through a trusted process that effectively bypasses application control solutions.', 'Trusted Developer Utilities Proxy Execution', 0);
INSERT INTO public.attack_patterns VALUES (431, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.087933+00', NULL, 'attack-pattern--a0e6614a-7740-4b24-bd65-f1bde09fc365', '[{"url": "https://attack.mitre.org/techniques/T1593", "external_id": "T1593", "source_name": "mitre-attack"}, {"url": "https://cyware.com/news/how-hackers-exploit-social-media-to-break-into-your-company-88e8da8e", "description": "Cyware Hacker News. (2019, October 2). How Hackers Exploit Social Media To Break Into Your Company. Retrieved October 20, 2020.", "source_name": "Cyware Social Media"}, {"url": "https://securitytrails.com/blog/google-hacking-techniques", "description": "Borges, E. (2019, March 5). Exploring Google Hacking Techniques. Retrieved October 20, 2020.", "source_name": "SecurityTrails Google Hacking"}, {"url": "https://www.exploit-db.com/google-hacking-database", "description": "Offensive Security. (n.d.). Google Hacking Database. Retrieved October 23, 2020.", "source_name": "ExploitDB GoogleHacking"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3234}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2792}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 3190}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 3158}}', 'T1593', 'Adversaries may search freely available websites and/or domains for information about victims that can be used during targeting. Information about victims may be available in various online sites, such as social media, new sites, or those hosting information about business operations such as hiring or requested/rewarded contracts.(Citation: Cyware Social Media)(Citation: SecurityTrails Google Hacking)(Citation: ExploitDB GoogleHacking)
INSERT INTO public.attack_patterns VALUES (432, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.126333+00', NULL, 'attack-pattern--16cdd21f-da65-4e4f-bc04-dd7d198c7b26', '[{"url": "https://attack.mitre.org/techniques/T1594", "external_id": "T1594", "source_name": "mitre-attack"}, {"url": "https://www.comparitech.com/blog/vpn-privacy/350-million-customer-records-exposed-online/", "description": "Bischoff, P. (2020, October 15). Broadvoice database of more than 350 million customer records exposed online. Retrieved October 20, 2020.", "source_name": "Comparitech Leak"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2952}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious network traffic that could be indicative of adversary reconnaissance, such as rapid successions of requests indicative of web crawling and/or large quantities of requests originating from a single source (especially if the source is known to be associated with an adversary). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.\"", "Str": "Monitor for suspicious network traffic that could be indicative of adversary reconnaissance, such as rapid successions of requests indicative of web crawling and/or large quantities of requests originating from a single source (especially if the source is known to be associated with an adversary). Analyzing web metadata may also reveal artifacts that can be attributed to potentially malicious activity, such as referer or user-agent string HTTP/S fields.", "Type": 3, "Index": 2384}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2908}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\"\n    ]", "Str": "", "Type": 5, "Index": 2301}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2876}}', 'T1594', 'Adversaries may search websites owned by the victim for information that can be used during targeting. Victim-owned websites may contain a variety of details, including names of departments/divisions, physical locations, and data about key employees such as names, roles, and contact info (ex: [Email Addresses](https://attack.mitre.org/techniques/T1589/002)). These sites may also have details highlighting business operations and relationships.(Citation: Comparitech Leak)
INSERT INTO public.attack_patterns VALUES (434, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.193129+00', NULL, 'attack-pattern--cba37adb-d6fb-4610-b069-dd04c0643384', '[{"url": "https://attack.mitre.org/techniques/T1518/001", "external_id": "T1518.001", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/581.html", "external_id": "CAPEC-581", "source_name": "capec"}, {"url": "https://expel.io/blog/finding-evil-in-aws/", "description": "A. Randazzo, B. Manahan and S. Lipton. (2020, April 28). Finding Evil in AWS. Retrieved June 25, 2020.", "source_name": "Expel IO Evil in AWS"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 3920}, "x_mitre_detection": {"Num": 0, "Raw": "\"System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained.\\n\\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\\n\\nIn cloud environments, additionally monitor logs for the usage of APIs that may be used to gather information about security software configurations within the environment.\"", "Str": "System and network discovery techniques normally occur throughout an operation as an adversary learns the environment. Data and events should not be viewed in isolation, but as part of a chain of behavior that could lead to other activities, such as lateral movement, based on the information obtained.\n\nMonitor processes and command-line arguments for actions that could be taken to gather system and network information. Remote access tools with built-in features may interact directly with the Windows API to gather information. Information may also be acquired through Windows system management tools such as [Windows Management Instrumentation](https://attack.mitre.org/techniques/T1047) and [PowerShell](https://attack.mitre.org/techniques/T1059/001).\n\nIn cloud environments, additionally monitor logs for the usage of APIs that may be used to gather information about security software configurations within the environment.", "Type": 3, "Index": 2691}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Azure AD\",\n      \"Office 365\",\n      \"SaaS\",\n      \"IaaS\",\n      \"Linux\",\n      \"macOS\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 3750}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Firewall: Firewall Metadata\",\n      \"Firewall: Firewall Enumeration\",\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\"\n    ]", "Str": "", "Type": 5, "Index": 2475}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3661}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 3703}}', 'T1518.001', 'Adversaries may attempt to get a listing of security software, configurations, defensive tools, and sensors that are installed on a system or in a cloud environment. This may include things such as firewall rules and anti-virus. Adversaries may use the information from [Security Software Discovery](https://attack.mitre.org/techniques/T1518/001) during automated discovery to shape follow-on behaviors, including whether or not the adversary fully infects the target and/or attempts specific actions.
INSERT INTO public.attack_patterns VALUES (437, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.347231+00', NULL, 'attack-pattern--60c4b628-4807-4b0b-bbf5-fdac8643c337', '[{"url": "https://attack.mitre.org/techniques/T1583/004", "external_id": "T1583.004", "source_name": "mitre-attack"}, {"url": "https://www.nytimes.com/2011/01/16/world/middleeast/16stuxnet.html", "description": "William J. Broad, John Markoff, and David E. Sanger. (2011, January 15). Israeli Test on Worm Called Crucial in Iran Nuclear Delay. Retrieved March 1, 2017.", "source_name": "NYTStuxnet"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2362}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.\"", "Str": "Much of this activity will take place outside the visibility of the target organization, making detection of this behavior difficult. Detection efforts may be focused on related stages of the adversary lifecycle, such as during Command and Control.", "Type": 3, "Index": 2004}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2318}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2287}}', 'T1583.004', 'Adversaries may buy, lease, or rent physical servers that can be used during targeting. Use of servers allows an adversary to stage, launch, and execute an operation. During post-compromise activity, adversaries may utilize servers for various tasks, including for Command and Control. Instead of compromising a third-party [Server](https://attack.mitre.org/techniques/T1584/004) or renting a [Virtual Private Server](https://attack.mitre.org/techniques/T1583/003), adversaries may opt to configure and run their own servers in support of operations.
INSERT INTO public.attack_patterns VALUES (442, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.5299+00', NULL, 'attack-pattern--20fb2507-d71c-455d-9b6d-6104461cf26b', '[{"url": "https://attack.mitre.org/techniques/T1489", "external_id": "T1489", "source_name": "mitre-attack"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}, {"url": "https://www.operationblockbuster.com/wp-content/uploads/2016/02/Operation-Blockbuster-Report.pdf", "description": "Novetta Threat Research Group. (2016, February 24). Operation Blockbuster: Unraveling the Long Thread of the Sony Attack. Retrieved February 25, 2016.", "source_name": "Novetta Blockbuster"}, {"url": "https://www.secureworks.com/research/wcry-ransomware-analysis", "description": "Counter Threat Unit Research Team. (2017, May 18). WCry Ransomware Analysis. Retrieved March 26, 2019.", "source_name": "SecureWorks WannaCry Analysis"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 4477}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line arguments to see if critical processes are terminated or stop running.\\n\\nMonitor for edits for modifications to services and startup programs that correspond to services of high importance. Look for changes to services that do not correlate with known software, patch cycles, etc. Windows service information is stored in the Registry at <code>HKLM\\\\SYSTEM\\\\CurrentControlSet\\\\Services</code>. Systemd service unit files are stored within the /etc/systemd/system, /usr/lib/systemd/system/, and /home/.config/systemd/user/ directories, as well as associated symbolic links.\\n\\nAlterations to the service binary path or the service startup type changed to disabled may be suspicious.\\n\\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. For example, <code>ChangeServiceConfigW</code> may be used by an adversary to prevent services from starting.(Citation: Talos Olympic Destroyer 2018)\"", "Str": "Monitor processes and command-line arguments to see if critical processes are terminated or stop running.\n\nMonitor for edits for modifications to services and startup programs that correspond to services of high importance. Look for changes to services that do not correlate with known software, patch cycles, etc. Windows service information is stored in the Registry at <code>HKLM\\SYSTEM\\CurrentControlSet\\Services</code>. Systemd service unit files are stored within the /etc/systemd/system, /usr/lib/systemd/system/, and /home/.config/systemd/user/ directories, as well as associated symbolic links.\n\nAlterations to the service binary path or the service startup type changed to disabled may be suspicious.\n\nRemote access tools with built-in features may interact directly with the Windows API to perform these functions outside of typical system utilities. For example, <code>ChangeServiceConfigW</code> may be used by an adversary to prevent services from starting.(Citation: Talos Olympic Destroyer 2018)", "Type": 3, "Index": 3158}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 4399}, "x_mitre_impact_type": {"Num": 0, "Raw": "[\n      \"Availability\"\n    ]", "Str": "", "Type": 5, "Index": 4209}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"Command: Command Execution\",\n      \"Process: OS API Execution\",\n      \"Service: Service Metadata\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"File: File Modification\",\n      \"Process: Process Termination\"\n    ]", "Str": "", "Type": 5, "Index": 2852}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 4270}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"SYSTEM\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 4313}}', 'T1489', 'Adversaries may stop or disable services on a system to render those services unavailable to legitimate users. Stopping critical services or processes can inhibit or stop response to an incident or aid in the adversary''s overall objectives to cause damage to the environment.(Citation: Talos Olympic Destroyer 2018)(Citation: Novetta Blockbuster) 
INSERT INTO public.attack_patterns VALUES (448, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.736418+00', NULL, 'attack-pattern--4ab929c6-ee2d-4fb5-aab4-b14be2ed7179', '[{"url": "https://attack.mitre.org/techniques/T1547/009", "external_id": "T1547.009", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/132.html", "external_id": "CAPEC-132", "source_name": "capec"}, {"url": "https://www.youtube.com/watch?v=nJ0UsyiUEqQ", "description": "French, D., Filar, B.. (2020, March 21). A Chain Is No Stronger Than Its Weakest LNK. Retrieved November 30, 2020.", "source_name": "BSidesSLC 2020 - LNK Elastic"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3113}, "x_mitre_detection": {"Num": 0, "Raw": "\"Since a shortcut''s target path likely will not change, modifications to shortcut files that do not correlate with known software changes, patches, removal, etc., may be suspicious. Analysis should attempt to relate shortcut file change or creation events to other potentially suspicious events based on known adversary behavior such as process launches of unknown executables that make network connections.\\n\\nMonitor for LNK files created with a Zone Identifier value greater than 1, which may indicate that the LNK file originated from outside of the network.(Citation: BSidesSLC 2020 - LNK Elastic)\"", "Str": "Since a shortcut''s target path likely will not change, modifications to shortcut files that do not correlate with known software changes, patches, removal, etc., may be suspicious. Analysis should attempt to relate shortcut file change or creation events to other potentially suspicious events based on known adversary behavior such as process launches of unknown executables that make network connections.\n\nMonitor for LNK files created with a Zone Identifier value greater than 1, which may indicate that the LNK file originated from outside of the network.(Citation: BSidesSLC 2020 - LNK Elastic)", "Type": 3, "Index": 2317}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3065}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"David French, Elastic\",\n      \"Bobby, Filar, Elastic\",\n      \"Travis Smith, Tripwire\"\n    ]", "Str": "", "Type": 5, "Index": 2057}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Creation\",\n      \"File: File Modification\",\n      \"Process: Process Creation\"\n    ]", "Str": "", "Type": 5, "Index": 2187}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2953}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"Administrator\",\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2995}}', 'T1547.009', 'Adversaries may create or edit shortcuts to run a program during system boot or user login. Shortcuts or symbolic links are ways of referencing other files or programs that will be opened or executed when the shortcut is clicked or executed by a system startup process.
INSERT INTO public.attack_patterns VALUES (449, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.764827+00', NULL, 'attack-pattern--457c7820-d331-465a-915e-42f85500ccc4', '[{"url": "https://attack.mitre.org/techniques/T1218", "external_id": "T1218", "source_name": "mitre-attack"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 2774}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor processes and command-line parameters for signed binaries that may be used to proxy execution of malicious files. Compare recent invocations of signed binaries that may be used to proxy execution with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity. Legitimate programs used in suspicious ways, like msiexec.exe downloading an MSI file from the Internet, may be indicative of an intrusion. Correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.\\n\\nMonitor for file activity (creations, downloads, modifications, etc.), especially for file types that are not typical within an environment and may be indicative of adversary activity.\"", "Str": "Monitor processes and command-line parameters for signed binaries that may be used to proxy execution of malicious files. Compare recent invocations of signed binaries that may be used to proxy execution with prior history of known good arguments and loaded files to determine anomalous and potentially adversarial activity. Legitimate programs used in suspicious ways, like msiexec.exe downloading an MSI file from the Internet, may be indicative of an intrusion. Correlate activity with other suspicious behavior to reduce false positives that may be due to normal benign use by users and administrators.\n\nMonitor for file activity (creations, downloads, modifications, etc.), especially for file types that are not typical within an environment and may be indicative of adversary activity.", "Type": 3, "Index": 1784}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2726}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Nishan Maharjan, @loki248\",\n      \"Hans Christoffer Gaardløs\",\n      \"Praetorian\"\n    ]", "Str": "", "Type": 5, "Index": 1216}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Process: Process Creation\",\n      \"File: File Creation\",\n      \"Module: Module Load\",\n      \"Process: OS API Execution\",\n      \"Command: Command Execution\",\n      \"Windows Registry: Windows Registry Key Modification\",\n      \"Network Traffic: Network Connection Creation\"\n    ]", "Str": "", "Type": 5, "Index": 1343}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 2613}, "x_mitre_defense_bypassed": {"Num": 0, "Raw": "[\n      \"Anti-virus\",\n      \"Application control\",\n      \"Digital Certificate Validation\"\n    ]", "Str": "", "Type": 5, "Index": 1662}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\",\n      \"Administrator\"\n    ]", "Str": "", "Type": 5, "Index": 2656}}', 'T1218', 'Adversaries may bypass process and/or signature-based defenses by proxying execution of malicious content with signed binaries. Binaries signed with trusted digital certificates can execute on Windows systems protected by digital signature validation. Several Microsoft signed binaries that are default on Windows installations can be used to proxy execution of other files.', 'Signed Binary Proxy Execution', 0);
INSERT INTO public.attack_patterns VALUES (452, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.896997+00', NULL, 'attack-pattern--bbe5b322-e2af-4a5e-9625-a4e62bf84ed3', '[{"url": "https://attack.mitre.org/techniques/T1593/001", "external_id": "T1593.001", "source_name": "mitre-attack"}, {"url": "https://cyware.com/news/how-hackers-exploit-social-media-to-break-into-your-company-88e8da8e", "description": "Cyware Hacker News. (2019, October 2). How Hackers Exploit Social Media To Break Into Your Company. Retrieved October 20, 2020.", "source_name": "Cyware Social Media"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2814}, "x_mitre_detection": {"Num": 0, "Raw": "\"Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.\"", "Str": "Much of this activity may have a very high occurrence and associated false positive rate, as well as potentially taking place outside the visibility of the target organization, making detection difficult for defenders.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access.", "Type": 3, "Index": 2373}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 2770}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2739}}', 'T1593.001', 'Adversaries may search social media for information about victims that can be used during targeting. Social media sites may contain various information about a victim organization, such as business announcements as well as information about the roles, locations, and interests of staff.
INSERT INTO public.attack_patterns VALUES (454, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:43.990196+00', NULL, 'attack-pattern--274770e0-2612-4ccf-a678-ef8e7bad365d', '[{"url": "https://attack.mitre.org/techniques/T1586/001", "external_id": "T1586.001", "source_name": "mitre-attack"}, {"url": "https://arstechnica.com/tech-policy/2011/02/anonymous-speaks-the-inside-story-of-the-hbgary-hack/", "description": "Bright, P. (2011, February 15). Anonymous speaks: the inside story of the HBGary hack. Retrieved March 9, 2017.", "source_name": "AnonHBGary"}, {"url": "https://www.securityweek.com/iranian-hackers-targeted-us-officials-elaborate-social-media-attack-operation", "description": "Lennon, M. (2014, May 29). Iranian Hackers Targeted US Officials in Elaborate Social Media Attack Operation. Retrieved March 1, 2017.", "source_name": "NEWSCASTER2014"}, {"url": "http://media.blackhat.com/bh-us-10/whitepapers/Ryan/BlackHat-USA-2010-Ryan-Getting-In-Bed-With-Robin-Sage-v1.0.pdf", "description": "Ryan, T. (2010). “Getting In Bed with Robin Sage.”. Retrieved March 6, 2017.", "source_name": "BlackHatRobinSage"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 4161}, "x_mitre_detection": {"Num": 0, "Raw": "\"Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently modified accounts making numerous connection requests to accounts affiliated with your organization.\\n\\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Spearphishing via Service](https://attack.mitre.org/techniques/T1566/003)).\"", "Str": "Consider monitoring social media activity related to your organization. Suspicious activity may include personas claiming to work for your organization or recently modified accounts making numerous connection requests to accounts affiliated with your organization.\n\nDetection efforts may be focused on related stages of the adversary lifecycle, such as during Initial Access (ex: [Spearphishing via Service](https://attack.mitre.org/techniques/T1566/003)).", "Type": 3, "Index": 3593}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4117}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4086}}', 'T1586.001', 'Adversaries may compromise social media accounts that can be used during targeting. For operations incorporating social engineering, the utilization of an online persona may be important. Rather than creating and cultivating social media profiles (i.e. [Social Media Accounts](https://attack.mitre.org/techniques/T1585/001)), adversaries may compromise existing social media accounts. Utilizing an existing persona may engender a level of trust in a potential victim if they have a relationship, or knowledge of, the compromised persona. 
INSERT INTO public.attack_patterns VALUES (459, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.074007+00', NULL, 'attack-pattern--e51137a5-1cdc-499e-911a-abaedaa5ac86', '[{"url": "https://attack.mitre.org/techniques/T1036/006", "external_id": "T1036.006", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/649.html", "external_id": "CAPEC-649", "source_name": "capec"}, {"url": "https://arstechnica.com/security/2016/07/after-hiatus-in-the-wild-mac-backdoors-are-suddenly-back/", "description": "Dan Goodin. (2016, July 6). After hiatus, in-the-wild Mac backdoors are suddenly back. Retrieved July 8, 2017.", "source_name": "Mac Backdoors are back"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 3015}, "x_mitre_detection": {"Num": 0, "Raw": "\"It''s not common for spaces to be at the end of filenames, so this is something that can easily be checked with file monitoring. From the user''s perspective though, this is very hard to notice from within the Finder.app or on the command-line in Terminal.app. Processes executed from binaries containing non-standard extensions in the filename are suspicious.\"", "Str": "It''s not common for spaces to be at the end of filenames, so this is something that can easily be checked with file monitoring. From the user''s perspective though, this is very hard to notice from within the Finder.app or on the command-line in Terminal.app. Processes executed from binaries containing non-standard extensions in the filename are suspicious.", "Type": 3, "Index": 2472}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2954}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Erye Hernandez, Palo Alto Networks\"\n    ]", "Str": "", "Type": 5, "Index": 2330}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Metadata\"\n    ]", "Str": "", "Type": 5, "Index": 2410}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2865}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2907}}', 'T1036.006', 'Adversaries can hide a program''s true filetype by changing the extension of a file. With certain file types (specifically this does not work with .app extensions), appending a space to the end of a filename will change how the file is processed by the operating system.
INSERT INTO public.attack_patterns VALUES (472, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.20349+00', NULL, 'attack-pattern--c2e147a9-d1a8-4074-811a-d8789202d916', '[{"url": "https://attack.mitre.org/techniques/T1027/003", "external_id": "T1027.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/636.html", "external_id": "CAPEC-636", "source_name": "capec"}, {"url": "https://en.wikipedia.org/wiki/Duqu", "description": "Wikipedia. (2017, December 29). Duqu. Retrieved April 10, 2018.", "source_name": "Wikipedia Duqu"}, {"url": "https://securingtomorrow.mcafee.com/mcafee-labs/malicious-document-targets-pyeongchang-olympics/", "description": "Saavedra-Morales, J., Sherstobitoff, R. (2018, January 6). Malicious Document Targets Pyeongchang Olympics. Retrieved April 10, 2018.", "source_name": "McAfee Malicious Doc Targets Pyeongchang Olympics"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3002}, "x_mitre_detection": {"Num": 0, "Raw": "\"Detection of steganography is difficult unless artifacts are left behind by the obfuscation process that are detectable with a known signature. Look for strings are other signatures left in system artifacts related to decoding steganography.\"", "Str": "Detection of steganography is difficult unless artifacts are left behind by the obfuscation process that are detectable with a known signature. Look for strings are other signatures left in system artifacts related to decoding steganography.", "Type": 3, "Index": 2617}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2924}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"File: File Content\"\n    ]", "Str": "", "Type": 5, "Index": 2556}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2893}}', 'T1027.003', 'Adversaries may use steganography techniques in order to prevent the detection of hidden information. Steganographic techniques can be used to hide data in digital media such as images, audio tracks, video clips, or text files.
INSERT INTO public.attack_patterns VALUES (463, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.235923+00', NULL, 'attack-pattern--2d3f5b3c-54ca-4f4d-bb1f-849346d31230', '[{"url": "https://attack.mitre.org/techniques/T1598/003", "external_id": "T1598.003", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.com/en_us/research/20/i/tricky-forms-of-phishing.html", "description": "Babon, P. (2020, September 3). Tricky ''Forms'' of Phishing. Retrieved October 20, 2020.", "source_name": "TrendMictro Phishing"}, {"url": "https://www.pcmag.com/news/hackers-try-to-phish-united-nations-staffers-with-fake-login-pages", "description": "Kan, M. (2019, October 24). Hackers Try to Phish United Nations Staffers With Fake Login Pages. Retrieved October 20, 2020.", "source_name": "PCMag FakeLogin"}, {"url": "https://docs.microsoft.com/en-us/microsoft-365/security/office-365-security/anti-spoofing-protection?view=o365-worldwide", "description": "Microsoft. (2020, October 13). Anti-spoofing protection in EOP. Retrieved October 19, 2020.", "source_name": "Microsoft Anti Spoofing"}, {"url": "https://www.cyber.gov.au/sites/default/files/2019-03/spoof_email_sender_policy_framework.pdf", "description": "Australian Cyber Security Centre. (2012, December). Mitigating Spoofed Emails Using Sender Policy Framework. Retrieved October 19, 2020.", "source_name": "ACSC Email Spoofing"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 4458}, "x_mitre_detection": {"Num": 0, "Raw": "\"Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\\n\\nMonitor for references to uncategorized or known-bad sites. URL inspection within email (including expanding shortened links) can also help detect links leading to known malicious sites.\"", "Str": "Monitor for suspicious email activity, such as numerous accounts receiving messages from a single unusual/unknown sender. Filtering based on DKIM+SPF or header analysis can help detect when the email sender is spoofed.(Citation: Microsoft Anti Spoofing)(Citation: ACSC Email Spoofing)\n\nMonitor for references to uncategorized or known-bad sites. URL inspection within email (including expanding shortened links) can also help detect links leading to known malicious sites.", "Type": 3, "Index": 3874}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"PRE\"\n    ]", "Str": "", "Type": 5, "Index": 4414}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Philip Winther\",\n      \"Sebastian Salla, McAfee\",\n      \"Robert Simmons, @MalwareUtkonos\"\n    ]", "Str": "", "Type": 5, "Index": 3560}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 3694}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 4383}}', 'T1598.003', 'Adversaries may send spearphishing messages with a malicious link to elicit sensitive information that can be used during targeting. Spearphishing for information is an attempt to trick targets into divulging information, frequently credentials or other actionable information. Spearphishing for information frequently involves social engineering techniques, such as posing as a source with a reason to collect information (ex: [Establish Accounts](https://attack.mitre.org/techniques/T1585) or [Compromise Accounts](https://attack.mitre.org/techniques/T1586)) and/or sending multiple, seemingly urgent messages.
INSERT INTO public.attack_patterns VALUES (465, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.318003+00', NULL, 'attack-pattern--f6ad61ee-65f3-4bd0-a3f5-2f0accb36317', '[{"url": "https://attack.mitre.org/techniques/T1566/003", "external_id": "T1566.003", "source_name": "mitre-attack"}, {"url": "https://capec.mitre.org/data/definitions/163.html", "external_id": "CAPEC-163", "source_name": "capec"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3704}, "x_mitre_detection": {"Num": 0, "Raw": "\"Because most common third-party services used for spearphishing via service leverage TLS encryption, SSL/TLS inspection is generally required to detect the initial communication/delivery. With SSL/TLS inspection intrusion detection signatures or other security gateway appliances may be able to detect malware. \\n\\nAnti-virus can potentially detect malicious documents and files that are downloaded on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning Powershell.exe) for techniques such as [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203) or usage of malicious scripts.\"", "Str": "Because most common third-party services used for spearphishing via service leverage TLS encryption, SSL/TLS inspection is generally required to detect the initial communication/delivery. With SSL/TLS inspection intrusion detection signatures or other security gateway appliances may be able to detect malware. \n\nAnti-virus can potentially detect malicious documents and files that are downloaded on the user''s computer. Endpoint sensing or network sensing can potentially detect malicious events once the file is opened (such as a Microsoft Word document or PDF reaching out to the internet or spawning Powershell.exe) for techniques such as [Exploitation for Client Execution](https://attack.mitre.org/techniques/T1203) or usage of malicious scripts.", "Type": 3, "Index": 2806}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3626}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Application Log: Application Log Content\",\n      \"Network Traffic: Network Traffic Content\",\n      \"Network Traffic: Network Traffic Flow\"\n    ]", "Str": "", "Type": 5, "Index": 2626}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 3595}}', 'T1566.003', 'Adversaries may send spearphishing messages via third-party services in an attempt to gain access to victim systems. Spearphishing via service is a specific variant of spearphishing. It is different from other forms of spearphishing in that it employs the use of third party services rather than directly via enterprise email channels. 
INSERT INTO public.attack_patterns VALUES (467, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.447825+00', NULL, 'attack-pattern--04fd5427-79c7-44ea-ae13-11b24778ff1c', '[{"url": "https://attack.mitre.org/techniques/T1132/001", "external_id": "T1132.001", "source_name": "mitre-attack"}, {"url": "https://en.wikipedia.org/wiki/Binary-to-text_encoding", "description": "Wikipedia. (2016, December 26). Binary-to-text encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Binary-to-text Encoding"}, {"url": "https://en.wikipedia.org/wiki/Character_encoding", "description": "Wikipedia. (2017, February 19). Character Encoding. Retrieved March 1, 2017.", "source_name": "Wikipedia Character Encoding"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2886}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 2261}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2808}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 2178}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2719}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 2761}}', 'T1132.001', 'Adversaries may encode data with a standard data encoding system to make the content of command and control traffic more difficult to detect. Command and control (C2) information can be encoded using a standard data encoding system that adheres to existing protocol specifications. Common data encoding schemes include ASCII, Unicode, hexadecimal, Base64, and MIME.(Citation: Wikipedia Binary-to-text Encoding) (Citation: Wikipedia Character Encoding) Some data encoding systems may also result in data compression, such as gzip.', 'Standard Encoding', 116);
INSERT INTO public.attack_patterns VALUES (469, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.528352+00', NULL, 'attack-pattern--890c9858-598c-401d-a4d5-c67ebcdd703a', '[{"url": "https://attack.mitre.org/techniques/T1528", "external_id": "T1528", "source_name": "mitre-attack"}, {"url": "https://auth0.com/blog/why-should-use-accesstokens-to-secure-an-api/", "description": "Auth0. (n.d.). Why You Should Always Use Access Tokens to Secure APIs. Retrieved September 12, 2019.", "source_name": "Auth0 - Why You Should Always Use Access Tokens to Secure APIs Sept 2019"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/active-directory-v2-protocols", "description": "Microsoft. (n.d.). Retrieved September 12, 2019.", "source_name": "Microsoft Identity Platform Protocols May 2019"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/v2-oauth2-auth-code-flow", "description": "Microsoft. (n.d.). Microsoft identity platform and OAuth 2.0 authorization code flow. Retrieved September 12, 2019.", "source_name": "Microsoft - OAuth Code Authorization flow - June 2019"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/quickstart-register-app", "description": "Microsoft. (2019, May 8). Quickstart: Register an application with the Microsoft identity platform. Retrieved September 12, 2019.", "source_name": "Microsoft - Azure AD App Registration - May 2019"}, {"url": "https://docs.microsoft.com/en-us/azure/active-directory/develop/access-tokens", "description": "Microsoft. (2019, August 29). Microsoft identity platform access tokens. Retrieved September 12, 2019.", "source_name": "Microsoft - Azure AD Identity Tokens - Aug 2019"}, {"url": "https://www.amnesty.org/en/latest/research/2019/08/evolving-phishing-attacks-targeting-journalists-and-human-rights-defenders-from-the-middle-east-and-north-africa/", "description": "Amnesty International. (2019, August 16). Evolving Phishing Attacks Targeting Journalists and Human Rights Defenders from the Middle-East and North Africa. Retrieved October 8, 2019.", "source_name": "Amnesty OAuth Phishing Attacks, August 2019"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/pawn-storm-abuses-open-authentication-advanced-social-engineering-attacks", "description": "Hacquebord, F.. (2017, April 25). Pawn Storm Abuses Open Authentication in Advanced Social Engineering Attacks. Retrieved October 4, 2019.", "source_name": "Trend Micro Pawn Storm OAuth 2017"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 7430}, "x_mitre_detection": {"Num": 0, "Raw": "\"Administrators should set up monitoring to trigger automatic alerts when policy criteria are met. For example, using a Cloud Access Security Broker (CASB), admins can create a “High severity app permissions” policy that generates alerts if apps request high severity permissions or send permissions requests for too many users.\\n\\nSecurity analysts can hunt for malicious apps using the tools available in their CASB, identity provider, or resource provider (depending on platform.) For example, they can filter for apps that are authorized by a small number of users, apps requesting high risk permissions, permissions incongruous with the app’s purpose, or apps with old “Last authorized” fields. A specific app can be investigated using an activity log displaying activities the app has performed, although some activities may be mis-logged as being performed by the user. App stores can be useful resources to further investigate suspicious apps.\\n\\nAdministrators can set up a variety of logs and leverage audit tools to monitor actions that can be conducted as a result of OAuth 2.0 access. For instance, audit reports enable admins to identify privilege escalation actions such as role creations or policy modifications, which could be actions performed after initial access.\"", "Str": "Administrators should set up monitoring to trigger automatic alerts when policy criteria are met. For example, using a Cloud Access Security Broker (CASB), admins can create a “High severity app permissions” policy that generates alerts if apps request high severity permissions or send permissions requests for too many users.\n\nSecurity analysts can hunt for malicious apps using the tools available in their CASB, identity provider, or resource provider (depending on platform.) For example, they can filter for apps that are authorized by a small number of users, apps requesting high risk permissions, permissions incongruous with the app’s purpose, or apps with old “Last authorized” fields. A specific app can be investigated using an activity log displaying activities the app has performed, although some activities may be mis-logged as being performed by the user. App stores can be useful resources to further investigate suspicious apps.\n\nAdministrators can set up a variety of logs and leverage audit tools to monitor actions that can be conducted as a result of OAuth 2.0 access. For instance, audit reports enable admins to identify privilege escalation actions such as role creations or policy modifications, which could be actions performed after initial access.", "Type": 3, "Index": 5904}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"SaaS\",\n      \"Office 365\",\n      \"Azure AD\",\n      \"Google Workspace\"\n    ]", "Str": "", "Type": 5, "Index": 7321}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Shailesh Tiwary (Indian Army)\",\n      \"Mark Wee\",\n      \"Jeff Sakowicz, Microsoft Identity Developer Platform Services (IDPM Services)\",\n      \"Saisha Agrawal, Microsoft Threat Intelligent Center (MSTIC)\"\n    ]", "Str": "", "Type": 5, "Index": 5573}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"User Account: User Account Modification\"\n    ]", "Str": "", "Type": 5, "Index": 5822}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "false", "Str": "", "Type": 1, "Index": 7231}, "x_mitre_permissions_required": {"Num": 0, "Raw": "[\n      \"User\"\n    ]", "Str": "", "Type": 5, "Index": 7274}}', 'T1528', 'Adversaries can steal user application access tokens as a means of acquiring credentials to access remote systems and resources. This can occur through social engineering and typically requires user action to grant access.
INSERT INTO public.attack_patterns VALUES (473, '2021-07-16 00:44:23.835672+00', '2021-07-16 00:44:44.648692+00', NULL, 'attack-pattern--eec23884-3fa1-4d8a-ac50-6f104d51e235', '[{"url": "https://attack.mitre.org/techniques/T1001/002", "external_id": "T1001.002", "source_name": "mitre-attack"}, {"url": "https://arxiv.org/ftp/arxiv/papers/1408/1408.1136.pdf", "description": "Gardiner, J.,  Cova, M., Nagaraja, S. (2014, February). Command & Control Understanding, Denying and Detecting. Retrieved April 20, 2016.", "source_name": "University of Birmingham C2"}]', '{"x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2251}, "x_mitre_detection": {"Num": 0, "Raw": "\"Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)\"", "Str": "Analyze network data for uncommon data flows (e.g., a client sending significantly more data than it receives from a server). Processes utilizing the network that do not normally have network communication or have never been seen before are suspicious. Analyze packet contents to detect communications that do not follow the expected protocol behavior for the port that is being used.(Citation: University of Birmingham C2)", "Type": 3, "Index": 1684}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2173}, "x_mitre_data_sources": {"Num": 0, "Raw": "[\n      \"Network Traffic: Network Traffic Content\"\n    ]", "Str": "", "Type": 5, "Index": 1601}, "x_mitre_is_subtechnique": {"Num": 0, "Raw": "true", "Str": "", "Type": 4, "Index": 2142}}', 'T1001.002', 'Adversaries may use steganographic techniques to hide command and control traffic to make detection efforts more difficult. Steganographic techniques can be used to hide data in digital messages that are transferred between systems. This hidden information can be used for command and control of compromised systems. In some cases, the passing of files embedded using steganography, such as image or document files, can be used for command and control. ', 'Steganography', 119);
