INSERT INTO public.attack_softwares VALUES (29, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--123bd7b3-675c-4b1a-8482-c55782b20e2b', '[{"url": "https://attack.mitre.org/software/S0043", "external_id": "S0043", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/11/china-based-threat.html", "description": "FireEye Threat Intelligence. (2015, December 1). China-based Cyber Threat Group Uses Dropbox for Malware Communications and Targets Hong Kong Media Outlets. Retrieved December 4, 2015.", "source_name": "FireEye admin@338"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BUBBLEWRAP\",\n      \"Backdoor.APT.FakeWinHTTPHelper\"\n    ]", "Str": "", "Type": 5, "Index": 1372}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1513}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1465}}', 'S0043', '[BUBBLEWRAP](https://attack.mitre.org/software/S0043) is a full-featured, second-stage backdoor used by the [admin@338](https://attack.mitre.org/groups/G0018) group. It is set to run when the system boots and includes functionality to check, upload, and register plug-ins that can further enhance its capabilities. (Citation: FireEye admin@338)', 'BUBBLEWRAP');
INSERT INTO public.attack_softwares VALUES (30, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d1b7830a-fced-4be3-a99c-f495af9d9e1b', '[{"url": "https://attack.mitre.org/software/S0414", "external_id": "S0414", "source_name": "mitre-attack"}, {"description": "(Citation: Unit42 BabyShark Feb 2019)(Citation: Unit42 BabyShark Apr 2019)", "source_name": "BabyShark"}, {"url": "https://unit42.paloaltonetworks.com/new-babyshark-malware-targets-u-s-national-security-think-tanks/", "description": "Unit 42. (2019, February 22). New BabyShark Malware Targets U.S. National Security Think Tanks. Retrieved October 7, 2019.", "source_name": "Unit42 BabyShark Feb 2019"}, {"url": "https://unit42.paloaltonetworks.com/babyshark-malware-part-two-attacks-continue-using-kimjongrat-and-pcrat/", "description": "Lim, M.. (2019, April 26). BabyShark Malware Part Two – Attacks Continue Using KimJongRAT and PCRat . Retrieved October 7, 2019.", "source_name": "Unit42 BabyShark Apr 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BabyShark\"\n    ]", "Str": "", "Type": 5, "Index": 1723}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1823}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1775}}', 'S0414', '[BabyShark](https://attack.mitre.org/software/S0414) is a Microsoft Visual Basic (VB) script-based malware family that is believed to be associated with several North Korean campaigns. (Citation: Unit42 BabyShark Feb 2019)', 'BabyShark');
INSERT INTO public.attack_softwares VALUES (31, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--c13d9621-aca7-436b-ab3d-3a95badb3d00', '[{"url": "https://attack.mitre.org/software/S0475", "external_id": "S0475", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/updated-backconfig-malware-targeting-government-and-military-organizations/", "description": "Hinchliffe, A. and Falcone, R. (2020, May 11). Updated BackConfig Malware Targeting Government and Military Organizations in South Asia. Retrieved June 17, 2020.", "source_name": "Unit 42 BackConfig May 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BackConfig\"\n    ]", "Str": "", "Type": 5, "Index": 1271}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1372}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1324}}', 'S0475', '[BackConfig](https://attack.mitre.org/software/S0475) is a custom Trojan with a flexible plugin architecture that has been used by [Patchwork](https://attack.mitre.org/groups/G0040).(Citation: Unit 42 BackConfig May 2020)', 'BackConfig');
INSERT INTO public.attack_softwares VALUES (33, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--9af05de0-bc09-4511-a350-5eb8b06185c1', '[{"url": "https://attack.mitre.org/software/S0337", "external_id": "S0337", "source_name": "mitre-attack"}, {"description": "(Citation: Unit 42 BadPatch Oct 2017)", "source_name": "BadPatch"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/10/unit42-badpatch/", "description": "Bar, T., Conant, S. (2017, October 20). BadPatch. Retrieved November 13, 2018.", "source_name": "Unit 42 BadPatch Oct 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BadPatch\"\n    ]", "Str": "", "Type": 5, "Index": 1192}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1291}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1243}}', 'S0337', '[BadPatch](https://attack.mitre.org/software/S0337) is a Windows Trojan that was used in a Gaza Hackers-linked campaign.(Citation: Unit 42 BadPatch Oct 2017)', 'BadPatch');
INSERT INTO public.attack_softwares VALUES (34, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--835a79f1-842d-472d-b8f4-d54b545c341b', '[{"url": "https://attack.mitre.org/software/S0234", "external_id": "S0234", "source_name": "mitre-attack"}, {"url": "https://www.eff.org/files/2016/08/03/i-got-a-letter-from-the-government.pdf", "description": "Galperin, E., Et al.. (2016, August). I Got a Letter From the Government the Other Day.... Retrieved April 25, 2018.", "source_name": "EFF Manul Aug 2016"}, {"url": "https://info.lookout.com/rs/051-ESQ-475/images/Lookout_Dark-Caracal_srr_20180118_us_v.1.0.pdf", "description": "Blaich, A., et al. (2018, January 18). Dark Caracal: Cyber-espionage at a Global Scale. Retrieved April 11, 2018.", "source_name": "Lookout Dark Caracal Jan 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bandook\"\n    ]", "Str": "", "Type": 5, "Index": 1502}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1600}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1552}}', 'S0234', '[Bandook](https://attack.mitre.org/software/S0234) is a commercially available RAT, written in Delphi, which has been available since roughly 2007  (Citation: EFF Manul Aug 2016) (Citation: Lookout Dark Caracal Jan 2018).', 'Bandook');
INSERT INTO public.attack_softwares VALUES (35, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--1f6e3702-7ca1-4582-b2e7-4591297d05a8', '[{"url": "https://attack.mitre.org/software/S0239", "external_id": "S0239", "source_name": "mitre-attack"}, {"description": "(Citation: McAfee Bankshot)", "source_name": "Bankshot"}, {"description": "(Citation: McAfee Bankshot)", "source_name": "Trojan Manuscript"}, {"url": "https://securingtomorrow.mcafee.com/mcafee-labs/hidden-cobra-targets-turkish-financial-sector-new-bankshot-implant/", "description": "Sherstobitoff, R. (2018, March 08). Hidden Cobra Targets Turkish Financial Sector With New Bankshot Implant. Retrieved May 18, 2018.", "source_name": "McAfee Bankshot"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bankshot\",\n      \"Trojan Manuscript\"\n    ]", "Str": "", "Type": 5, "Index": 1605}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1731}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1683}}', 'S0239', '[Bankshot](https://attack.mitre.org/software/S0239) is a remote access tool (RAT) that was first reported by the Department of Homeland Security in December of 2017. In 2018, [Lazarus Group](https://attack.mitre.org/groups/G0032) used the [Bankshot](https://attack.mitre.org/software/S0239) implant in attacks against the Turkish financial sector. (Citation: McAfee Bankshot)', 'Bankshot');
INSERT INTO public.attack_softwares VALUES (36, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--99fdf3b4-96ef-4ab9-b191-fc683441cad0', '[{"url": "https://attack.mitre.org/software/S0534", "external_id": "S0534", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye KEGTAP SINGLEMALT October 2020)", "source_name": "KEGTAP"}, {"description": "(Citation: Cybereason Bazar July 2020)(Citation: NCC Group Team9 June 2020)", "source_name": "Team9"}, {"url": "https://www.cybereason.com/blog/a-bazar-of-tricks-following-team9s-development-cycles", "description": "Cybereason Nocturnus. (2020, July 16). A BAZAR OF TRICKS: FOLLOWING TEAM9’S DEVELOPMENT CYCLES. Retrieved November 18, 2020.", "source_name": "Cybereason Bazar July 2020"}, {"url": "https://www.fireeye.com/blog/threat-research/2020/10/kegtap-and-singlemalt-with-a-ransomware-chaser.html", "description": "Kimberly Goody, Jeremy Kennelly, Joshua Shilko, Steve Elovitz, Douglas Bienstock. (2020, October 28). Unhappy Hour Special: KEGTAP and SINGLEMALT With a Ransomware Chaser. Retrieved October 28, 2020.", "source_name": "FireEye KEGTAP SINGLEMALT October 2020"}, {"url": "https://research.nccgroup.com/2020/06/02/in-depth-analysis-of-the-new-team9-malware-family/", "description": "Pantazopoulos, N. (2020, June 2). In-depth analysis of the new Team9 malware family. Retrieved December 1, 2020.", "source_name": "NCC Group Team9 June 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bazar\",\n      \"KEGTAP\",\n      \"Team9\"\n    ]", "Str": "", "Type": 5, "Index": 2549}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2754}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2706}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Cybereason Nocturnus, @nocturnus\"\n    ]", "Str": "", "Type": 5, "Index": 2631}}', 'S0534', '[Bazar](https://attack.mitre.org/software/S0534) is a downloader and backdoor malware in use since at least April 2020, with infections mainly targeting professional services, healthcare, manufacturing, IT, logistics and travel companies across the US and Europe. [Bazar](https://attack.mitre.org/software/S0534) has been reported to have ties to [TrickBot](https://attack.mitre.org/software/S0266) campaigns and can be used to deploy additional malware, including ransomware, and to steal sensitive data.(Citation: Cybereason Bazar July 2020)', 'Bazar');
INSERT INTO public.attack_softwares VALUES (37, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--805480f1-6caa-4a67-8ca9-b2b39650d986', '[{"url": "https://attack.mitre.org/software/S0574", "external_id": "S0574", "source_name": "mitre-attack"}, {"description": "(Citation: Unit42 BendyBear Feb 2021)", "source_name": "BendyBear"}, {"url": "https://unit42.paloaltonetworks.com/bendybear-shellcode-blacktech/", "description": "Harbison, M. (2021, February 9). BendyBear: Novel Chinese Shellcode Linked With Cyber Espionage Group BlackTech. Retrieved February 16, 2021.", "source_name": "Unit42 BendyBear Feb 2021"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BendyBear\"\n    ]", "Str": "", "Type": 5, "Index": 1572}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1672}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1624}}', 'S0574', '[BendyBear](https://attack.mitre.org/software/S0574) is an x64 shellcode for a stage-zero implant designed to download malware from a C2 server. First discovered in August 2020, [BendyBear](https://attack.mitre.org/software/S0574) shares a variety of features with [Waterbear](https://attack.mitre.org/software/S0579), malware previously attributed to the Chinese cyber espionage group [BlackTech](https://attack.mitre.org/groups/G0098).(Citation: Unit42 BendyBear Feb 2021)', 'BendyBear');
INSERT INTO public.attack_softwares VALUES (38, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--65ffc206-d7c1-45b3-b543-f6b726e7840d', '[{"url": "https://attack.mitre.org/software/S0268", "external_id": "S0268", "source_name": "mitre-attack"}, {"description": "(Citation: Unit 42 Bisonal July 2018)", "source_name": "Bisonal"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/07/unit42-bisonal-malware-used-attacks-russia-south-korea/", "description": "Hayashi, K., Ray, V. (2018, July 31). Bisonal Malware Used in Attacks Against Russia and South Korea. Retrieved August 7, 2018.", "source_name": "Unit 42 Bisonal July 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bisonal\"\n    ]", "Str": "", "Type": 5, "Index": 1346}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1444}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1396}}', 'S0268', '[Bisonal](https://attack.mitre.org/software/S0268) is malware that has been used in attacks against targets in Russia, South Korea, and Japan. It has been observed in the wild since 2014. (Citation: Unit 42 Bisonal July 2018)', 'Bisonal');
INSERT INTO public.attack_softwares VALUES (39, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--fa766a65-5136-4ff3-8429-36d08eaa0100', '[{"url": "https://attack.mitre.org/software/S0570", "external_id": "S0570", "source_name": "mitre-attack"}, {"description": "(Citation: Crowdstrike Indrik November 2018)", "source_name": "BitPaymer"}, {"description": "(Citation: Crowdstrike Indrik November 2018)", "source_name": "wp_encrypt"}, {"description": "(Citation: Crowdstrike Indrik November 2018)", "source_name": "FriedEx"}, {"url": "https://www.crowdstrike.com/blog/big-game-hunting-the-evolution-of-indrik-spider-from-dridex-wire-fraud-to-bitpaymer-targeted-ransomware/", "description": "Frankoff, S., Hartley, B. (2018, November 14). Big Game Hunting: The Evolution of INDRIK SPIDER From Dridex Wire Fraud to BitPaymer Targeted Ransomware. Retrieved January 6, 2021.", "source_name": "Crowdstrike Indrik November 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BitPaymer\",\n      \"wp_encrypt\",\n      \"FriedEx\"\n    ]", "Str": "", "Type": 5, "Index": 2028}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2165}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2117}}', 'S0570', '[BitPaymer](https://attack.mitre.org/software/S0570) is a ransomware variant first observed in August 2017 targeting hospitals in the U.K. [BitPaymer](https://attack.mitre.org/software/S0570) uses a unique encryption key, ransom note, and contact information for each operation. [BitPaymer](https://attack.mitre.org/software/S0570) has several indicators suggesting overlap with the [Dridex](https://attack.mitre.org/software/S0384) malware and is often delivered via [Dridex](https://attack.mitre.org/software/S0384).(Citation: Crowdstrike Indrik November 2018)', 'BitPaymer');
INSERT INTO public.attack_softwares VALUES (40, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--54cc1d4f-5c53-4f0e-9ef5-11b4998e82e4', '[{"url": "https://attack.mitre.org/software/S0089", "external_id": "S0089", "source_name": "mitre-attack"}, {"url": "https://blog-assets.f-secure.com/wp-content/uploads/2019/10/15163408/BlackEnergy_Quedagh.pdf", "description": "F-Secure Labs. (2014). BlackEnergy & Quedagh: The convergence of crimeware and APT attacks. Retrieved March 24, 2016.", "source_name": "F-Secure BlackEnergy 2014"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BlackEnergy\",\n      \"Black Energy\"\n    ]", "Str": "", "Type": 5, "Index": 1540}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 1664}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1616}}', 'S0089', '[BlackEnergy](https://attack.mitre.org/software/S0089) is a malware toolkit that has been used by both criminal and APT actors. It dates back to at least 2007 and was originally designed to create botnets for use in conducting Distributed Denial of Service (DDoS) attacks, but its use has evolved to support various plug-ins. It is well known for being used during the confrontation between Georgia and Russia in 2008, as well as in targeting Ukrainian institutions. Variants include BlackEnergy 2 and BlackEnergy 3. (Citation: F-Secure BlackEnergy 2014)', 'BlackEnergy');
INSERT INTO public.attack_softwares VALUES (41, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--63c4511b-2d6e-4bb2-b582-e2e99a8a467d', '[{"url": "https://attack.mitre.org/software/S0564", "external_id": "S0564", "source_name": "mitre-attack"}, {"url": "https://www.microsoft.com/security/blog/2019/12/12/gallium-targeting-global-telecom/", "description": "MSTIC. (2019, December 12). GALLIUM: Targeting global telecom. Retrieved January 13, 2021.", "source_name": "Microsoft GALLIUM December 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"BlackMould\"\n    ]", "Str": "", "Type": 5, "Index": 1330}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1431}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1383}}', 'S0564', '[BlackMould](https://attack.mitre.org/software/S0564) is a web shell based on [China Chopper](https://attack.mitre.org/software/S0020) for servers running Microsoft IIS. First reported in December 2019, it has been used in malicious campaigns by [GALLIUM](https://attack.mitre.org/groups/G0093) against telecommunication providers.(Citation: Microsoft GALLIUM December 2019)', 'BlackMould');
INSERT INTO public.attack_softwares VALUES (42, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4c6d62c2-89f5-4159-8fab-0190b1f9d328', '[{"url": "https://attack.mitre.org/software/S0486", "external_id": "S0486", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2018/12/ESET-The_Dark_Side_of_the_ForSSHe.pdf", "description": "Dumont, R., M.Léveillé, M., Porcher, H. (2018, December 1). THE DARK SIDE OF THE FORSSHE A landscape of OpenSSH backdoors. Retrieved July 16, 2020.", "source_name": "ESET ForSSHe December 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bonadan\"\n    ]", "Str": "", "Type": 5, "Index": 1390}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1486}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1440}}', 'S0486', '[Bonadan](https://attack.mitre.org/software/S0486) is a malicious version of OpenSSH which acts as a custom backdoor. [Bonadan](https://attack.mitre.org/software/S0486) has been active since at least 2018 and combines a new cryptocurrency-mining module with the same credential-stealing module used by the Onderon family of backdoors.(Citation: ESET ForSSHe December 2018)', 'Bonadan');
INSERT INTO public.attack_softwares VALUES (43, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--28b97733-ef07-4414-aaa5-df50b2d30cc5', '[{"url": "https://attack.mitre.org/software/S0252", "external_id": "S0252", "source_name": "mitre-attack"}, {"description": "(Citation: McAfee Gold Dragon)", "source_name": "Brave Prince"}, {"url": "https://www.mcafee.com/blogs/other-blogs/mcafee-labs/gold-dragon-widens-olympics-malware-attacks-gains-permanent-presence-on-victims-systems/", "description": "Sherstobitoff, R., Saavedra-Morales, J. (2018, February 02). Gold Dragon Widens Olympics Malware Attacks, Gains Permanent Presence on Victims’ Systems. Retrieved June 6, 2018.", "source_name": "McAfee Gold Dragon"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Brave Prince\"\n    ]", "Str": "", "Type": 5, "Index": 1668}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1771}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1723}}', 'S0252', '[Brave Prince](https://attack.mitre.org/software/S0252) is a Korean-language implant that was first observed in the wild in December 2017. It contains similar code and behavior to [Gold Dragon](https://attack.mitre.org/software/S0249), and was seen along with [Gold Dragon](https://attack.mitre.org/software/S0249) and [RunningRAT](https://attack.mitre.org/software/S0253) in operations surrounding the 2018 Pyeongchang Winter Olympics. (Citation: McAfee Gold Dragon)', 'Brave Prince');
INSERT INTO public.attack_softwares VALUES (44, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--79499993-a8d6-45eb-b343-bf58dea5bdde', '[{"url": "https://attack.mitre.org/software/S0204", "external_id": "S0204", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Briba May 2012)", "source_name": "Briba"}, {"url": "https://web.archive.org/web/20190717233006/http://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-elderwood-project.pdf", "description": "O''Gorman, G., and McDonald, G.. (2012, September 6). The Elderwood Project. Retrieved February 15, 2018.", "source_name": "Symantec Elderwood Sept 2012"}, {"url": "https://www.symantec.com/security_response/writeup.jsp?docid=2012-051515-2843-99", "description": "Ladley, F. (2012, May 15). Backdoor.Briba. Retrieved February 21, 2018.", "source_name": "Symantec Briba May 2012"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Briba\"\n    ]", "Str": "", "Type": 5, "Index": 1663}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1759}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1711}}', 'S0204', '[Briba](https://attack.mitre.org/software/S0204) is a trojan used by [Elderwood](https://attack.mitre.org/groups/G0066) to open a backdoor and download files on to compromised hosts. (Citation: Symantec Elderwood Sept 2012) (Citation: Symantec Briba May 2012)', 'Briba');
INSERT INTO public.attack_softwares VALUES (45, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--7bef1b56-4870-4e74-b32a-7dd88c390c44', '[{"url": "https://attack.mitre.org/software/S0482", "external_id": "S0482", "source_name": "mitre-attack"}, {"description": "(Citation: MacKeeper Bundlore Apr 2019)", "source_name": "OSX.Bundlore"}, {"url": "https://mackeeper.com/blog/post/610-macos-bundlore-adware-analysis/", "description": "Sushko, O. (2019, April 17). macOS Bundlore: Mac Virus Bypassing macOS Security Features. Retrieved June 30, 2020.", "source_name": "MacKeeper Bundlore Apr 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Bundlore\",\n      \"OSX.Bundlore\"\n    ]", "Str": "", "Type": 5, "Index": 1382}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1501}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1455}}', 'S0482', '[Bundlore](https://attack.mitre.org/software/S0482) is adware written for macOS that has been in use since at least 2015. Though categorized as adware, [Bundlore](https://attack.mitre.org/software/S0482) has many features associated with more traditional backdoors.(Citation: MacKeeper Bundlore Apr 2019)', 'Bundlore');
INSERT INTO public.attack_softwares VALUES (46, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5a84dc36-df0d-4053-9b7c-f0c388a57283', '[{"url": "https://attack.mitre.org/software/S0025", "external_id": "S0025", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report.pdf", "description": "Mandiant. (n.d.). APT1 Exposing One of China’s Cyber Espionage Units. Retrieved July 18, 2016.", "source_name": "Mandiant APT1"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CALENDAR\"\n    ]", "Str": "", "Type": 5, "Index": 1133}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1232}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1184}}', 'S0025', '[CALENDAR](https://attack.mitre.org/software/S0025) is malware used by [APT1](https://attack.mitre.org/groups/G0006) that mimics legitimate Gmail Calendar traffic. (Citation: Mandiant APT1)', 'CALENDAR');
