INSERT INTO public.attack_softwares VALUES (48, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b0f13390-cec7-4814-b37c-ccec01887faa', '[{"url": "https://attack.mitre.org/software/S0222", "external_id": "S0222", "source_name": "mitre-attack"}, {"url": "http://blog.talosintelligence.com/2017/09/avast-distributes-malware.html", "description": "Brumaghin, E. et al. (2017, September 18). CCleanup: A Vast Number of Machines at Risk. Retrieved March 9, 2018.", "source_name": "Talos CCleanup 2017"}, {"url": "http://www.intezer.com/evidence-aurora-operation-still-active-supply-chain-attack-through-ccleaner/", "description": "Rosenberg, J. (2017, September 20). Evidence Aurora Operation Still Active: Supply Chain Attack Through CCleaner. Retrieved February 13, 2018.", "source_name": "Intezer Aurora Sept 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CCBkdr\"\n    ]", "Str": "", "Type": 5, "Index": 1539}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1636}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1588}}', 'S0222', '[CCBkdr](https://attack.mitre.org/software/S0222) is malware that was injected into a signed version of CCleaner and distributed from CCleaner''s distribution website. (Citation: Talos CCleanup 2017) (Citation: Intezer Aurora Sept 2017)', 'CCBkdr');
INSERT INTO public.attack_softwares VALUES (49, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--ccd61dfc-b03f-4689-8c18-7c97eab08472', '[{"url": "https://attack.mitre.org/software/S0023", "external_id": "S0023", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT28) (Citation: ESET Sednit Part 2) (Citation: FireEye APT28 January 2017)", "source_name": "CHOPSTICK"}, {"description": "(Citation: Symantec APT28 Oct 2018)", "source_name": "Backdoor.SofacyX"}, {"description": "(Citation: ESET Sednit Part 2) (Citation: FireEye APT28 January 2017)", "source_name": "SPLM"}, {"description": "(Citation: ESET Sednit Part 2) (Citation: FireEye APT28 January 2017)", "source_name": "Xagent"}, {"description": "(Citation: ESET Sednit Part 2) (Citation: FireEye APT28 January 2017)", "source_name": "X-Agent"}, {"description": "(Citation: FireEye APT28 January 2017)", "source_name": "webhp"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/global/en/current-threats/pdfs/rpt-apt28.pdf", "description": "FireEye. (2015). APT28: A WINDOW INTO RUSSIA’S CYBER ESPIONAGE OPERATIONS?. Retrieved August 19, 2015.", "source_name": "FireEye APT28"}, {"url": "http://www.welivesecurity.com/wp-content/uploads/2016/10/eset-sednit-part-2.pdf", "description": "ESET. (2016, October). En Route with Sednit - Part 2: Observing the Comings and Goings. Retrieved November 21, 2016.", "source_name": "ESET Sednit Part 2"}, {"url": "https://www2.fireeye.com/rs/848-DID-242/images/APT28-Center-of-Storm-2017.pdf", "description": "FireEye iSIGHT Intelligence. (2017, January 11). APT28: At the Center of the Storm. Retrieved January 11, 2017.", "source_name": "FireEye APT28 January 2017"}, {"url": "https://www.justice.gov/file/1080281/download", "description": "Mueller, R. (2018, July 13). Indictment - United States of America vs. VIKTOR BORISOVICH NETYKSHO, et al. Retrieved September 13, 2018.", "source_name": "DOJ GRU Indictment Jul 2018"}, {"url": "https://www.symantec.com/blogs/election-security/apt28-espionage-military-government", "description": "Symantec Security Response. (2018, October 04). APT28: New Espionage Operations Target Military and Government Organizations. Retrieved November 14, 2018.", "source_name": "Symantec APT28 Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CHOPSTICK\",\n      \"Backdoor.SofacyX\",\n      \"SPLM\",\n      \"Xagent\",\n      \"X-Agent\",\n      \"webhp\"\n    ]", "Str": "", "Type": 5, "Index": 3636}, "x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 3914}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 3851}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Richard Gold, Digital Shadows\"\n    ]", "Str": "", "Type": 5, "Index": 3779}}', 'S0023', '[CHOPSTICK](https://attack.mitre.org/software/S0023) is a malware family of modular backdoors used by [APT28](https://attack.mitre.org/groups/G0007). It has been used since at least 2012 and is usually dropped on victims as second-stage malware, though it has been used as first-stage malware in several cases. It has both Windows and Linux variants. (Citation: FireEye APT28) (Citation: ESET Sednit Part 2) (Citation: FireEye APT28 January 2017) (Citation: DOJ GRU Indictment Jul 2018) It is tracked separately from the [X-Agent for Android](https://attack.mitre.org/software/S0314).', 'CHOPSTICK');
