INSERT INTO public.attack_softwares VALUES (54, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--cb7bcf6f-085f-41db-81ee-4b68481661b5', '[{"url": "https://attack.mitre.org/software/S0077", "external_id": "S0077", "source_name": "mitre-attack"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/01/scarlet-mimic-years-long-espionage-targets-minority-activists/", "description": "Falcone, R. and Miller-Osborn, J.. (2016, January 24). Scarlet Mimic: Years-Long Espionage Campaign Targets Minority Activists. Retrieved February 10, 2016.", "source_name": "Scarlet Mimic Jan 2016"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CallMe\"\n    ]", "Str": "", "Type": 5, "Index": 1224}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1319}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1273}}', 'S0077', '[CallMe](https://attack.mitre.org/software/S0077) is a Trojan designed to run on Apple OSX. It is based on a publicly available tool called Tiny SHell. (Citation: Scarlet Mimic Jan 2016)', 'CallMe');
INSERT INTO public.attack_softwares VALUES (55, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d20b397a-ea47-48a9-b503-2e2a3551e11d', '[{"url": "https://attack.mitre.org/software/S0351", "external_id": "S0351", "source_name": "mitre-attack"}, {"description": "(Citation: Unit42 Cannon Nov 2018)", "source_name": "Cannon"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/11/unit42-sofacy-continues-global-attacks-wheels-new-cannon-trojan/", "description": "Falcone, R., Lee, B. (2018, November 20). Sofacy Continues Global Attacks and Wheels Out New ‘Cannon’ Trojan. Retrieved November 26, 2018.", "source_name": "Unit42 Cannon Nov 2018"}, {"url": "https://unit42.paloaltonetworks.com/dear-joohn-sofacy-groups-global-campaign/", "description": "Lee, B., Falcone, R. (2018, December 12). Dear Joohn: The Sofacy Group’s Global Campaign. Retrieved April 19, 2019.", "source_name": "Unit42 Sofacy Dec 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Cannon\"\n    ]", "Str": "", "Type": 5, "Index": 1649}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1746}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1698}}', 'S0351', '[Cannon](https://attack.mitre.org/software/S0351) is a Trojan with variants written in C# and Delphi. It was first observed in April 2018. (Citation: Unit42 Cannon Nov 2018)(Citation: Unit42 Sofacy Dec 2018)', 'Cannon');
INSERT INTO public.attack_softwares VALUES (56, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--72f54d66-675d-4587-9bd3-4ed09f9522e4', '[{"url": "https://attack.mitre.org/software/S0030", "external_id": "S0030", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye CARBANAK June 2017)", "source_name": "Carbanak"}, {"description": "(Citation: Fox-It Anunak Feb 2015) (Citation: FireEye CARBANAK June 2017)", "source_name": "Anunak"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/08064518/Carbanak_APT_eng.pdf", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, February). CARBANAK APT THE GREAT BANK ROBBERY. Retrieved August 23, 2018.", "source_name": "Kaspersky Carbanak"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/06/behind-the-carbanak-backdoor.html", "description": "Bennett, J., Vengerik, B. (2017, June 12). Behind the CARBANAK Backdoor. Retrieved June 11, 2018.", "source_name": "FireEye CARBANAK June 2017"}, {"url": "https://www.fox-it.com/en/news/blog/anunak-aka-carbanak-update/", "description": "Prins, R. (2015, February 16). Anunak (aka Carbanak) Update. Retrieved January 20, 2017.", "source_name": "Fox-It Anunak Feb 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Carbanak\",\n      \"Anunak\"\n    ]", "Str": "", "Type": 5, "Index": 2169}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2284}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2236}}', 'S0030', '[Carbanak](https://attack.mitre.org/software/S0030) is a full-featured, remote backdoor used by a group of the same name ([Carbanak](https://attack.mitre.org/groups/G0008)). It is intended for espionage, data exfiltration, and providing remote access to infected machines. (Citation: Kaspersky Carbanak) (Citation: FireEye CARBANAK June 2017)', 'Carbanak');
INSERT INTO public.attack_softwares VALUES (57, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--bbcd7a02-ef24-4171-ac94-a93540173b94', '[{"url": "https://attack.mitre.org/software/S0484", "external_id": "S0484", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.com/vinfo/us/threat-encyclopedia/malware/carberp", "description": "Trend Micro. (2014, February 27). CARBERP. Retrieved July 29, 2020.", "source_name": "Trend Micro Carberp February 2014"}, {"url": "https://securelist.com/the-great-bank-robbery-the-carbanak-apt/68732/", "description": "Kaspersky Lab''s Global Research & Analysis Team. (2015, February). CARBANAK APT THE GREAT BANK ROBBERY. Retrieved March 27, 2017.", "source_name": "KasperskyCarbanak"}, {"url": "https://www.rsa.com/content/dam/en/white-paper/the-carbanak-fin7-syndicate.pdf", "description": "RSA. (2017, November 21). THE CARBANAK/FIN7 SYNDICATE A HISTORICAL OVERVIEW OF AN EVOLVING THREAT. Retrieved July 29, 2020.", "source_name": "RSA Carbanak November 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Carberp\"\n    ]", "Str": "", "Type": 5, "Index": 1994}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2092}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2044}}', 'S0484', '[Carberp](https://attack.mitre.org/software/S0484) is a credential and information stealing malware that has been active since at least 2009. [Carberp](https://attack.mitre.org/software/S0484)''s source code was leaked online in 2013, and subsequently used as the foundation for the [Carbanak](https://attack.mitre.org/software/S0030) backdoor.(Citation: Trend Micro Carberp February 2014)(Citation: KasperskyCarbanak)(Citation: RSA Carbanak November 2017)', 'Carberp');
INSERT INTO public.attack_softwares VALUES (58, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b7e9880a-7a7c-4162-bddb-e28e8ef2bf1f', '[{"url": "https://attack.mitre.org/software/S0335", "external_id": "S0335", "source_name": "mitre-attack"}, {"description": "(Citation: ESET Carbon Mar 2017)(Citation: Securelist Turla Oct 2018)", "source_name": "Carbon"}, {"url": "https://www.welivesecurity.com/2017/03/30/carbon-paper-peering-turlas-second-stage-backdoor/", "description": "ESET. (2017, March 30). Carbon Paper: Peering into Turla’s second stage backdoor. Retrieved November 7, 2018.", "source_name": "ESET Carbon Mar 2017"}, {"url": "https://securelist.com/shedding-skin-turlas-fresh-faces/88069/", "description": "Kaspersky Lab''s Global Research & Analysis Team. (2018, October 04). Shedding Skin – Turla’s Fresh Faces. Retrieved November 7, 2018.", "source_name": "Securelist Turla Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Carbon\"\n    ]", "Str": "", "Type": 5, "Index": 1870}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1967}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1919}}', 'S0335', '[Carbon](https://attack.mitre.org/software/S0335) is a sophisticated, second-stage backdoor and framework that can be used to steal sensitive information from victims. [Carbon](https://attack.mitre.org/software/S0335) has been selectively used by [Turla](https://attack.mitre.org/groups/G0010) to target government and foreign affairs-related organizations in Central Asia.(Citation: ESET Carbon Mar 2017)(Citation: Securelist Turla Oct 2018)', 'Carbon');
INSERT INTO public.attack_softwares VALUES (59, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b879758f-bbc4-4cab-b5ba-177ac9b009b4', '[{"url": "https://attack.mitre.org/software/S0348", "external_id": "S0348", "source_name": "mitre-attack"}, {"description": "(Citation: PaloAlto CardinalRat Apr 2017)", "source_name": "Cardinal RAT"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/04/unit42-cardinal-rat-active-two-years/", "description": "Grunzweig, J.. (2017, April 20). Cardinal RAT Active for Over Two Years. Retrieved December 8, 2018.", "source_name": "PaloAlto CardinalRat Apr 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Cardinal RAT\"\n    ]", "Str": "", "Type": 5, "Index": 1451}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1554}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1506}}', 'S0348', '[Cardinal RAT](https://attack.mitre.org/software/S0348) is a potentially low volume remote access trojan (RAT) observed since December 2015. [Cardinal RAT](https://attack.mitre.org/software/S0348) is notable for its unique utilization of uncompiled C# source code and the Microsoft Windows built-in csc.exe compiler.(Citation: PaloAlto CardinalRat Apr 2017)', 'Cardinal RAT');
INSERT INTO public.attack_softwares VALUES (60, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8d9e758b-735f-4cbc-ba7c-32cd15138b2a', '[{"url": "https://attack.mitre.org/software/S0261", "external_id": "S0261", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Catchamas April 2018)", "source_name": "Catchamas"}, {"url": "https://www-west.symantec.com/content/symantec/english/en/security-center/writeup.html/2018-040209-1742-99", "description": "Balanza, M. (2018, April 02). Infostealer.Catchamas. Retrieved July 10, 2018.", "source_name": "Symantec Catchamas April 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Catchamas\"\n    ]", "Str": "", "Type": 5, "Index": 1246}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1346}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1298}}', 'S0261', '[Catchamas](https://attack.mitre.org/software/S0261) is a Windows Trojan that steals information from compromised systems. (Citation: Symantec Catchamas April 2018)', 'Catchamas');
INSERT INTO public.attack_softwares VALUES (61, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--751b77e6-af1f-483b-93fe-eddf17f92a64', '[{"url": "https://attack.mitre.org/software/S0572", "external_id": "S0572", "source_name": "mitre-attack"}, {"description": "(Citation: ClearSky Lebanese Cedar Jan 2021)(Citation: CheckPoint Volatile Cedar March 2015)", "source_name": "Caterpillar WebShell"}, {"url": "https://www.clearskysec.com/wp-content/uploads/2021/01/Lebanese-Cedar-APT.pdf", "description": "ClearSky Cyber Security. (2021, January). “Lebanese Cedar” APT Global Lebanese Espionage Campaign Leveraging Web Servers. Retrieved February 10, 2021.", "source_name": "ClearSky Lebanese Cedar Jan 2021"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2015/03/20082004/volatile-cedar-technical-report.pdf", "description": "Threat Intelligence and Research. (2015, March 30). VOLATILE CEDAR. Retrieved February 8, 2021.", "source_name": "CheckPoint Volatile Cedar March 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Caterpillar WebShell\"\n    ]", "Str": "", "Type": 5, "Index": 1765}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1876}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1828}}', 'S0572', '[Caterpillar WebShell](https://attack.mitre.org/software/S0572) is a self-developed Web Shell tool created by the group [Volatile Cedar](https://attack.mitre.org/groups/G0123).(Citation: ClearSky Lebanese Cedar Jan 2021) ', 'Caterpillar WebShell');
INSERT INTO public.attack_softwares VALUES (62, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--dc5d1a33-62aa-4a0c-aa8c-589b87beb11e', '[{"url": "https://attack.mitre.org/software/S0144", "external_id": "S0144", "source_name": "mitre-attack"}, {"description": "(Citation: Palo Alto menuPass Feb 2017) (Citation: JPCERT ChChes Feb 2017) (Citation: PWC Cloud Hopper Technical Annex April 2017)", "source_name": "ChChes"}, {"description": "(Citation: PWC Cloud Hopper Technical Annex April 2017)", "source_name": "Scorpion"}, {"description": "Based on similarities in reported malware behavior and open source reporting, it is assessed that the malware named HAYMAKER by FireEye is likely the same as the malware ChChes. (Citation: FireEye APT10 April 2017) (Citation: Twitter Nick Carr APT10)", "source_name": "HAYMAKER"}, {"url": "http://researchcenter.paloaltonetworks.com/2017/02/unit42-menupass-returns-new-malware-new-attacks-japanese-academics-organizations/", "description": "Miller-Osborn, J. and Grunzweig, J.. (2017, February 16). menuPass Returns with New Malware and New Attacks Against Japanese Academics and Organizations. Retrieved March 1, 2017.", "source_name": "Palo Alto menuPass Feb 2017"}, {"url": "http://blog.jpcert.or.jp/2017/02/chches-malware--93d6.html", "description": "Nakamura, Y.. (2017, February 17). ChChes - Malware that Communicates with C&C Servers Using Cookie Headers. Retrieved March 1, 2017.", "source_name": "JPCERT ChChes Feb 2017"}, {"url": "https://www.pwc.co.uk/cyber-security/pdf/cloud-hopper-annex-b-final.pdf", "description": "PwC and BAE Systems. (2017, April). Operation Cloud Hopper: Technical Annex. Retrieved April 13, 2017.", "source_name": "PWC Cloud Hopper Technical Annex April 2017"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/04/apt10_menupass_grou.html", "description": "FireEye iSIGHT Intelligence. (2017, April 6). APT10 (MenuPass Group): New Tools, Global Campaign Latest Manifestation of Longstanding Threat. Retrieved June 29, 2017.", "source_name": "FireEye APT10 April 2017"}, {"url": "https://twitter.com/ItsReallyNick/status/850105140589633536", "description": "Carr, N.. (2017, April 6). Retrieved June 29, 2017.", "source_name": "Twitter Nick Carr APT10"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ChChes\",\n      \"Scorpion\",\n      \"HAYMAKER\"\n    ]", "Str": "", "Type": 5, "Index": 3355}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3488}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3440}}', 'S0144', '[ChChes](https://attack.mitre.org/software/S0144) is a Trojan that appears to be used exclusively by [menuPass](https://attack.mitre.org/groups/G0045). It was used to target Japanese organizations in 2016. Its lack of persistence methods suggests it may be intended as a first-stage tool. (Citation: Palo Alto menuPass Feb 2017) (Citation: JPCERT ChChes Feb 2017) (Citation: PWC Cloud Hopper Technical Annex April 2017)', 'ChChes');
INSERT INTO public.attack_softwares VALUES (63, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5bcd5511-6756-4824-a692-e8bb109364af', '[{"url": "https://attack.mitre.org/software/S0220", "external_id": "S0220", "source_name": "mitre-attack"}, {"description": "(Citation: Chaos Stolen Backdoor)", "source_name": "Chaos"}, {"url": "http://gosecure.net/2018/02/14/chaos-stolen-backdoor-rising/", "description": "Sebastian Feldmann. (2018, February 14). Chaos: a Stolen Backdoor Rising Again. Retrieved March 5, 2018.", "source_name": "Chaos Stolen Backdoor"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Chaos\"\n    ]", "Str": "", "Type": 5, "Index": 1306}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1400}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1354}}', 'S0220', '[Chaos](https://attack.mitre.org/software/S0220) is Linux malware that compromises systems by brute force attacks against SSH services. Once installed, it provides a reverse shell to its controllers, triggered by unsolicited packets. (Citation: Chaos Stolen Backdoor)', 'Chaos');
INSERT INTO public.attack_softwares VALUES (65, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5a3a31fe-5a8f-48e1-bff0-a753e5b1be70', '[{"url": "https://attack.mitre.org/software/S0020", "external_id": "S0020", "source_name": "mitre-attack"}, {"description": "(Citation: Dell TG-3390) (Citation: FireEye Periscope March 2018)", "source_name": "China Chopper"}, {"url": "https://www.fireeye.com/blog/threat-research/2013/08/breaking-down-the-china-chopper-web-shell-part-i.html", "description": "Lee, T., Hanzlik, D., Ahl, I. (2013, August 7). Breaking Down the China Chopper Web Shell - Part I. Retrieved March 27, 2015.", "source_name": "Lee 2013"}, {"url": "https://www.secureworks.com/research/threat-group-3390-targets-organizations-for-cyberespionage", "description": "Dell SecureWorks Counter Threat Unit Threat Intelligence. (2015, August 5). Threat Group-3390 Targets Organizations for Cyberespionage. Retrieved August 18, 2018.", "source_name": "Dell TG-3390"}, {"url": "https://www.fireeye.com/blog/threat-research/2018/03/suspected-chinese-espionage-group-targeting-maritime-and-engineering-industries.html", "description": "FireEye. (2018, March 16). Suspected Chinese Cyber Espionage Group (TEMP.Periscope) Targeting U.S. Engineering and Maritime Industries. Retrieved April 11, 2018.", "source_name": "FireEye Periscope March 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"China Chopper\"\n    ]", "Str": "", "Type": 5, "Index": 2330}, "x_mitre_version": {"Num": 0, "Raw": "\"2.2\"", "Str": "2.2", "Type": 3, "Index": 2434}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2386}}', 'S0020', '[China Chopper](https://attack.mitre.org/software/S0020) is a [Web Shell](https://attack.mitre.org/techniques/T1505/003) hosted on Web servers to provide access back into an enterprise network that does not rely on an infected system calling back to a remote command and control server. (Citation: Lee 2013) It has been used by several threat groups. (Citation: Dell TG-3390) (Citation: FireEye Periscope March 2018)', 'China Chopper');
INSERT INTO public.attack_softwares VALUES (66, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--cbf646f1-7db5-4dc6-808b-0094313949df', '[{"url": "https://attack.mitre.org/software/S0054", "external_id": "S0054", "source_name": "mitre-attack"}, {"url": "https://www.f-secure.com/documents/996508/1030745/dukes_whitepaper.pdf", "description": "F-Secure Labs. (2015, September 17). The Dukes: 7 years of Russian cyberespionage. Retrieved December 10, 2015.", "source_name": "F-Secure The Dukes"}, {"url": "https://securelist.com/minidionis-one-more-apt-with-a-usage-of-cloud-drives/71443/", "description": "Lozhkin, S.. (2015, July 16). Minidionis – one more APT with a usage of cloud drives. Retrieved April 5, 2017.", "source_name": "Securelist Minidionis July 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CloudDuke\",\n      \"MiniDionis\",\n      \"CloudLook\"\n    ]", "Str": "", "Type": 5, "Index": 1474}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1613}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1565}}', 'S0054', '[CloudDuke](https://attack.mitre.org/software/S0054) is malware that was used by [APT29](https://attack.mitre.org/groups/G0016) in 2015. (Citation: F-Secure The Dukes) (Citation: Securelist Minidionis July 2015)', 'CloudDuke');
INSERT INTO public.attack_softwares VALUES (67, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a7881f21-e978-4fe4-af56-92c9416a2616', '[{"url": "https://attack.mitre.org/software/S0154", "external_id": "S0154", "source_name": "mitre-attack"}, {"url": "https://cobaltstrike.com/downloads/csmanual38.pdf", "description": "Strategic Cyber LLC. (2017, March 14). Cobalt Strike Manual. Retrieved May 24, 2017.", "source_name": "cobaltstrike manual"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Cobalt Strike\"\n    ]", "Str": "", "Type": 5, "Index": 1609}, "x_mitre_version": {"Num": 0, "Raw": "\"1.6\"", "Str": "1.6", "Type": 3, "Index": 1814}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1766}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Martin Sohn Christensen, Improsec\",\n      \"Josh Abraham\"\n    ]", "Str": "", "Type": 5, "Index": 1668}}', 'S0154', '[Cobalt Strike](https://attack.mitre.org/software/S0154) is a commercial, full-featured, remote access tool that bills itself as “adversary simulation software designed to execute targeted attacks and emulate the post-exploitation actions of advanced threat actors”. Cobalt Strike’s interactive post-exploit capabilities cover the full range of ATT&CK tactics, all executed within a single, integrated system.(Citation: cobaltstrike manual)
INSERT INTO public.attack_softwares VALUES (68, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--aa1462a1-d065-416c-b354-bedd04998c7f', '[{"url": "https://attack.mitre.org/software/S0338", "external_id": "S0338", "source_name": "mitre-attack"}, {"description": "(Citation: Zscaler Cobain Aug 2017)", "source_name": "Cobian RAT"}, {"url": "https://www.zscaler.com/blogs/research/cobian-rat-backdoored-rat", "description": "Yadav, A., et al. (2017, August 31). Cobian RAT – A backdoored RAT. Retrieved November 13, 2018.", "source_name": "Zscaler Cobian Aug 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Cobian RAT\"\n    ]", "Str": "", "Type": 5, "Index": 1208}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1309}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1261}}', 'S0338', '[Cobian RAT](https://attack.mitre.org/software/S0338) is a backdoor, remote access tool that has been observed since 2016.(Citation: Zscaler Cobian Aug 2017)', 'Cobian RAT');
INSERT INTO public.attack_softwares VALUES (69, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d1531eaa-9e17-473e-a680-3298469662c3', '[{"url": "https://attack.mitre.org/software/S0369", "external_id": "S0369", "source_name": "mitre-attack"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2018/10/mac-cryptocurrency-ticker-app-installs-backdoors/", "description": "Thomas Reed. (2018, October 29). Mac cryptocurrency ticker app installs backdoors. Retrieved April 23, 2019.", "source_name": "CoinTicker 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CoinTicker\"\n    ]", "Str": "", "Type": 5, "Index": 1203}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1372}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1326}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Richie Cyrus, SpecterOps\"\n    ]", "Str": "", "Type": 5, "Index": 1259}}', 'S0369', '[CoinTicker](https://attack.mitre.org/software/S0369) is a malicious application that poses as a cryptocurrency price ticker and installs components of the open source backdoors EvilOSX and EggShell.(Citation: CoinTicker 2019)', 'CoinTicker');
INSERT INTO public.attack_softwares VALUES (70, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--da5880b4-f7da-4869-85f2-e0aba84b8565', '[{"url": "https://attack.mitre.org/software/S0126", "external_id": "S0126", "source_name": "mitre-attack"}, {"url": "https://www.threatminer.org/report.php?q=waterbug-attack-group.pdf&y=2015#gsc.tab=0&gsc.q=waterbug-attack-group.pdf&gsc.page=1", "description": "Symantec. (2015, January 26). The Waterbug attack group. Retrieved April 10, 2015.", "source_name": "Symantec Waterbug"}, {"url": "https://docplayer.net/101655589-Tools-used-by-the-uroburos-actors.html", "description": "Rascagneres, P. (2015, May). Tools used by the Uroburos actors. Retrieved August 18, 2016.", "source_name": "NorthSec 2015 GData Uroburos Tools"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2020/05/ESET_Turla_ComRAT.pdf", "description": "Faou, M. (2020, May). From Agent.btz to ComRAT v4: A ten-year journey. Retrieved June 15, 2020.", "source_name": "ESET ComRAT May 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ComRAT\"\n    ]", "Str": "", "Type": 5, "Index": 2036}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2198}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2150}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matthieu Faou, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2088}}', 'S0126', '[ComRAT](https://attack.mitre.org/software/S0126) is a second stage implant suspected of being a descendant of [Agent.btz](https://attack.mitre.org/software/S0092) and used by [Turla](https://attack.mitre.org/groups/G0010). The first version of [ComRAT](https://attack.mitre.org/software/S0126) was identified in 2007, but the tool has undergone substantial development for many years since.(Citation: Symantec Waterbug)(Citation: NorthSec 2015 GData Uroburos Tools)(Citation: ESET ComRAT May 2020)', 'ComRAT');
INSERT INTO public.attack_softwares VALUES (71, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f4c80d39-ce10-4f74-9b50-a7e3f5df1f2e', '[{"url": "https://attack.mitre.org/software/S0244", "external_id": "S0244", "source_name": "mitre-attack"}, {"description": "(Citation: Palo Alto Comnie)", "source_name": "Comnie"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/01/unit42-comnie-continues-target-organizations-east-asia/", "description": "Grunzweig, J. (2018, January 31). Comnie Continues to Target Organizations in East Asia. Retrieved June 7, 2018.", "source_name": "Palo Alto Comnie"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Comnie\"\n    ]", "Str": "", "Type": 5, "Index": 1230}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1327}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1279}}', 'S0244', '[Comnie](https://attack.mitre.org/software/S0244) is a remote backdoor which has been used in attacks in East Asia. (Citation: Palo Alto Comnie)', 'Comnie');
INSERT INTO public.attack_softwares VALUES (72, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4dea7d8e-af94-4bfb-afe4-7ff54f59308b', '[{"url": "https://attack.mitre.org/software/S0575", "external_id": "S0575", "source_name": "mitre-attack"}, {"description": "(Citation: CarbonBlack Conti July 2020)(Citation: Cybereason Conti Jan 2021)", "source_name": "Conti"}, {"url": "https://www.cybereason.com/blog/cybereason-vs.-conti-ransomware", "description": "Rochberger, L. (2021, January 12). Cybereason vs. Conti Ransomware. Retrieved February 17, 2021.", "source_name": "Cybereason Conti Jan 2021"}, {"url": "https://www.carbonblack.com/blog/tau-threat-discovery-conti-ransomware/", "description": "Baskin, B. (2020, July 8). TAU Threat Discovery: Conti Ransomware. Retrieved February 17, 2021.", "source_name": "CarbonBlack Conti July 2020"}, {"url": "https://cybleinc.com/2021/01/21/conti-ransomware-resurfaces-targeting-government-large-organizations/", "description": "Cybleinc. (2021, January 21). Conti Ransomware Resurfaces, Targeting Government & Large Organizations. Retrieved April 13, 2021.", "source_name": "Cybleinc Conti January 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Conti\"\n    ]", "Str": "", "Type": 5, "Index": 2354}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2522}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2474}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniyal Naeem, BT Security\"\n    ]", "Str": "", "Type": 5, "Index": 2405}}', 'S0575', '[Conti](https://attack.mitre.org/software/S0575) is a Ransomware-as-a-Service that was first observed in December 2019, and has being distributed via [TrickBot](https://attack.mitre.org/software/S0266). It has been used against major corporations and government agencies, particularly those in North America. As with other ransomware families, actors using [Conti](https://attack.mitre.org/software/S0575) steal sensitive files and information from compromised networks, and threaten to publish this data unless the ransom is paid.(Citation: Cybereason Conti Jan 2021)(Citation: CarbonBlack Conti July 2020)(Citation: Cybleinc Conti January 2020)', 'Conti');
INSERT INTO public.attack_softwares VALUES (73, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--eedc01d5-95e6-4d21-bcd4-1121b1df4586', '[{"url": "https://attack.mitre.org/software/S0492", "external_id": "S0492", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/mac-malware-steals-cryptocurrency-exchanges-cookies/", "description": "Chen, y., et al. (2019, January 31). Mac Malware Steals Cryptocurrency Exchanges’ Cookies. Retrieved July 22, 2020.", "source_name": "Unit42 CookieMiner Jan 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CookieMiner\"\n    ]", "Str": "", "Type": 5, "Index": 1280}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1380}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1334}}', 'S0492', '[CookieMiner](https://attack.mitre.org/software/S0492) is mac-based malware that targets information associated with cryptocurrency exchanges as well as enabling cryptocurrency mining on the victim system itself. It was first discovered in the wild in 2019.(Citation: Unit42 CookieMiner Jan 2019)', 'CookieMiner');
INSERT INTO public.attack_softwares VALUES (74, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--2eb9b131-d333-4a48-9eb4-d8dec46c19ee', '[{"url": "https://attack.mitre.org/software/S0050", "external_id": "S0050", "source_name": "mitre-attack"}, {"url": "https://www.f-secure.com/documents/996508/1030745/dukes_whitepaper.pdf", "description": "F-Secure Labs. (2015, September 17). The Dukes: 7 years of Russian cyberespionage. Retrieved December 10, 2015.", "source_name": "F-Secure The Dukes"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CosmicDuke\",\n      \"TinyBaron\",\n      \"BotgenStudios\",\n      \"NemesisGemina\"\n    ]", "Str": "", "Type": 5, "Index": 1128}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1294}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1246}}', 'S0050', '[CosmicDuke](https://attack.mitre.org/software/S0050) is malware that was used by [APT29](https://attack.mitre.org/groups/G0016) from 2010 to 2015. (Citation: F-Secure The Dukes)', 'CosmicDuke');
INSERT INTO public.attack_softwares VALUES (75, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--e6ef745b-077f-42e1-a37d-29eecff9c754', '[{"url": "https://attack.mitre.org/software/S0046", "external_id": "S0046", "source_name": "mitre-attack"}, {"url": "https://www.f-secure.com/documents/996508/1030745/dukes_whitepaper.pdf", "description": "F-Secure Labs. (2015, September 17). The Dukes: 7 years of Russian cyberespionage. Retrieved December 10, 2015.", "source_name": "F-Secure The Dukes"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"CozyCar\",\n      \"CozyDuke\",\n      \"CozyBear\",\n      \"Cozer\",\n      \"EuroAPT\"\n    ]", "Str": "", "Type": 5, "Index": 1276}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1442}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1394}}', 'S0046', '[CozyCar](https://attack.mitre.org/software/S0046) is malware that was used by [APT29](https://attack.mitre.org/groups/G0016) from 2010 to 2015. It is a modular malware platform, and its backdoor component can be instructed to download and execute a variety of modules with different functionality. (Citation: F-Secure The Dukes)', 'CozyCar');
