INSERT INTO public.attack_softwares VALUES (78, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--925a6c52-5cf0-4fec-99de-b0d6917d8593', '[{"url": "https://attack.mitre.org/software/S0538", "external_id": "S0538", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/2020/12/02/turla-crutch-keeping-back-door-open/", "description": "Faou, M. (2020, December 2). Turla Crutch: Keeping the “back door” open. Retrieved December 4, 2020.", "source_name": "ESET Crutch December 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Crutch\"\n    ]", "Str": "", "Type": 5, "Index": 1172}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1269}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1221}}', 'S0538', '[Crutch](https://attack.mitre.org/software/S0538) is a backdoor designed for document theft that has been used by [Turla](https://attack.mitre.org/groups/G0010) since at least 2015.(Citation: ESET Crutch December 2020)', 'Crutch');
INSERT INTO public.attack_softwares VALUES (79, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a04d9a4c-bb52-40bf-98ec-e350c2d6a862', '[{"url": "https://attack.mitre.org/software/S0498", "external_id": "S0498", "source_name": "mitre-attack"}, {"url": "https://www.sentinelone.com/blog/four-distinct-families-of-lazarus-malware-target-apples-macos-platform/", "description": "Stokes, P. (2020, July 27). Four Distinct Families of Lazarus Malware Target Apple’s macOS Platform. Retrieved August 7, 2020.", "source_name": "SentinelOne Lazarus macOS July 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Cryptoistic\"\n    ]", "Str": "", "Type": 5, "Index": 1231}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1331}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1285}}', 'S0498', '[Cryptoistic](https://attack.mitre.org/software/S0498) is a backdoor, written in Swift, that has been used by [Lazarus Group](https://attack.mitre.org/groups/G0032).(Citation: SentinelOne Lazarus macOS July 2020)', 'Cryptoistic');
