INSERT INTO public.attack_softwares VALUES (131, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--76abb3ef-dafd-4762-97cb-a35379429db4', '[{"url": "https://attack.mitre.org/software/S0168", "external_id": "S0168", "source_name": "mitre-attack"}, {"description": "(Citation: ESET Gazer Aug 2017)", "source_name": "Gazer"}, {"description": "The term WhiteBear is used both for the activity group (a subset of G0010) as well as the malware observed. Based on similarities in behavior and C2, WhiteBear is assessed to be the same as S0168. (Citation: Securelist WhiteBear Aug 2017)(Citation: ESET Crutch December 2020)", "source_name": "WhiteBear"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2017/08/eset-gazer.pdf", "description": "ESET. (2017, August). Gazing at Gazer: Turla’s new second stage backdoor. Retrieved September 14, 2017.", "source_name": "ESET Gazer Aug 2017"}, {"url": "https://securelist.com/introducing-whitebear/81638/", "description": "Kaspersky Lab''s Global Research & Analysis Team. (2017, August 30). Introducing WhiteBear. Retrieved September 21, 2017.", "source_name": "Securelist WhiteBear Aug 2017"}, {"url": "https://www.welivesecurity.com/2020/12/02/turla-crutch-keeping-back-door-open/", "description": "Faou, M. (2020, December 2). Turla Crutch: Keeping the “back door” open. Retrieved December 4, 2020.", "source_name": "ESET Crutch December 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Gazer\",\n      \"WhiteBear\"\n    ]", "Str": "", "Type": 5, "Index": 2157}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2333}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2285}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bartosz Jerzman\"\n    ]", "Str": "", "Type": 5, "Index": 2227}}', 'S0168', '[Gazer](https://attack.mitre.org/software/S0168) is a backdoor used by [Turla](https://attack.mitre.org/groups/G0010) since at least 2016. (Citation: ESET Gazer Aug 2017)', 'Gazer');
INSERT INTO public.attack_softwares VALUES (132, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--199463de-d9be-46d6-bb41-07234c1dd5a6', '[{"url": "https://attack.mitre.org/software/S0049", "external_id": "S0049", "source_name": "mitre-attack"}, {"url": "https://www.f-secure.com/documents/996508/1030745/dukes_whitepaper.pdf", "description": "F-Secure Labs. (2015, September 17). The Dukes: 7 years of Russian cyberespionage. Retrieved December 10, 2015.", "source_name": "F-Secure The Dukes"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GeminiDuke\"\n    ]", "Str": "", "Type": 5, "Index": 1128}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1229}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1181}}', 'S0049', '[GeminiDuke](https://attack.mitre.org/software/S0049) is malware that was used by [APT29](https://attack.mitre.org/groups/G0016) from 2009 to 2012. (Citation: F-Secure The Dukes)', 'GeminiDuke');
INSERT INTO public.attack_softwares VALUES (133, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--099ecff2-41b8-436d-843c-038a9aa9aa69', '[{"url": "https://attack.mitre.org/software/S0460", "external_id": "S0460", "source_name": "mitre-attack"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/ta505-distributes-new-sdbbot-remote-access-trojan-get2-downloader", "description": "Schwarz, D. et al. (2019, October 16). TA505 Distributes New SDBbot Remote Access Trojan with Get2 Downloader. Retrieved May 29, 2020.", "source_name": "Proofpoint TA505 October 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Get2\"\n    ]", "Str": "", "Type": 5, "Index": 1396}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1491}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1443}}', 'S0460', '[Get2](https://attack.mitre.org/software/S0460) is a downloader written in C++ that has been used by [TA505](https://attack.mitre.org/groups/G0092) to deliver [FlawedGrace](https://attack.mitre.org/software/S0383), [FlawedAmmyy](https://attack.mitre.org/software/S0381), Snatch and [SDBbot](https://attack.mitre.org/software/S0461).(Citation: Proofpoint TA505 October 2019)', 'Get2');
INSERT INTO public.attack_softwares VALUES (134, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b9799466-9dd7-4098-b2d6-f999ce50b9a8', '[{"url": "https://attack.mitre.org/software/S0249", "external_id": "S0249", "source_name": "mitre-attack"}, {"description": "(Citation: McAfee Gold Dragon)", "source_name": "Gold Dragon"}, {"url": "https://www.mcafee.com/blogs/other-blogs/mcafee-labs/gold-dragon-widens-olympics-malware-attacks-gains-permanent-presence-on-victims-systems/", "description": "Sherstobitoff, R., Saavedra-Morales, J. (2018, February 02). Gold Dragon Widens Olympics Malware Attacks, Gains Permanent Presence on Victims’ Systems. Retrieved June 6, 2018.", "source_name": "McAfee Gold Dragon"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Gold Dragon\"\n    ]", "Str": "", "Type": 5, "Index": 1675}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1777}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1729}}', 'S0249', '[Gold Dragon](https://attack.mitre.org/software/S0249) is a Korean-language, data gathering implant that was first observed in the wild in South Korea in July 2017. [Gold Dragon](https://attack.mitre.org/software/S0249) was used along with [Brave Prince](https://attack.mitre.org/software/S0252) and [RunningRAT](https://attack.mitre.org/software/S0253) in operations targeting organizations associated with the 2018 Pyeongchang Winter Olympics. (Citation: McAfee Gold Dragon)', 'Gold Dragon');
INSERT INTO public.attack_softwares VALUES (135, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b7010785-699f-412f-ba49-524da6033c76', '[{"url": "https://attack.mitre.org/software/S0597", "external_id": "S0597", "source_name": "mitre-attack"}, {"description": "(Citation: MSTIC NOBELIUM Mar 2021)", "source_name": "GoldFinder"}, {"url": "https://www.microsoft.com/security/blog/2021/03/04/goldmax-goldfinder-sibot-analyzing-nobelium-malware/", "description": "Nafisi, R., Lelli, A. (2021, March 4). GoldMax, GoldFinder, and Sibot: Analyzing NOBELIUM’s layered persistence. Retrieved March 8, 2021.", "source_name": "MSTIC NOBELIUM Mar 2021"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GoldFinder\"\n    ]", "Str": "", "Type": 5, "Index": 1679}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1780}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1732}}', 'S0597', '[GoldFinder](https://attack.mitre.org/software/S0597) is a custom HTTP tracer tool written in Go that logs the route a packet takes between a compromised network and a C2 server. It can be used to inform  threat actors of potential points of discovery or logging of their actions, including C2 related to other malware. [GoldFinder](https://attack.mitre.org/software/S0597) was discovered in early 2021 during an investigation into the SolarWinds cyber intrusion by [APT29](https://attack.mitre.org/groups/G0016).(Citation: MSTIC NOBELIUM Mar 2021)', 'GoldFinder');
INSERT INTO public.attack_softwares VALUES (136, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5c747acd-47f0-4c5a-b9e5-213541fc01e0', '[{"url": "https://attack.mitre.org/software/S0588", "external_id": "S0588", "source_name": "mitre-attack"}, {"description": "(Citation: MSTIC NOBELIUM Mar 2021)", "source_name": "GoldMax"}, {"description": "(Citation: FireEye SUNSHUTTLE Mar 2021)", "source_name": "SUNSHUTTLE"}, {"url": "https://www.microsoft.com/security/blog/2021/03/04/goldmax-goldfinder-sibot-analyzing-nobelium-malware/", "description": "Nafisi, R., Lelli, A. (2021, March 4). GoldMax, GoldFinder, and Sibot: Analyzing NOBELIUM’s layered persistence. Retrieved March 8, 2021.", "source_name": "MSTIC NOBELIUM Mar 2021"}, {"url": "https://www.fireeye.com/blog/threat-research/2021/03/sunshuttle-second-stage-backdoor-targeting-us-based-entity.html", "description": "Smith, L., Leathery, J., Read, B. (2021, March 4). New SUNSHUTTLE Second-Stage Backdoor Uncovered Targeting U.S.-Based Entity; Possible Connection to UNC2452. Retrieved March 12, 2021.", "source_name": "FireEye SUNSHUTTLE Mar 2021"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GoldMax\",\n      \"SUNSHUTTLE\"\n    ]", "Str": "", "Type": 5, "Index": 2165}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2283}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2235}}', 'S0588', '[GoldMax](https://attack.mitre.org/software/S0588) is a second-stage C2 backdoor written in Go that was used by [APT29](https://attack.mitre.org/groups/G0016) and discovered in early 2021 during the investigation into breaches related to the SolarWinds intrusion. [GoldMax](https://attack.mitre.org/software/S0588) uses multiple defense evasion techniques, including avoiding virtualization execution and masking malicious traffic.(Citation: MSTIC NOBELIUM Mar 2021)(Citation: FireEye SUNSHUTTLE Mar 2021)', 'GoldMax');
INSERT INTO public.attack_softwares VALUES (137, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b9704a7d-feef-4af9-8898-5280f1686326', '[{"url": "https://attack.mitre.org/software/S0493", "external_id": "S0493", "source_name": "mitre-attack"}, {"url": "https://www.trustwave.com/en-us/resources/library/documents/the-golden-tax-department-and-the-emergence-of-goldenspy-malware/", "description": "Trustwave SpiderLabs. (2020, June 25). The Golden Tax Department and Emergence of GoldenSpy Malware. Retrieved July 23, 2020.", "source_name": "Trustwave GoldenSpy June 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GoldenSpy\"\n    ]", "Str": "", "Type": 5, "Index": 1486}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1586}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1538}}', 'S0493', '[GoldenSpy](https://attack.mitre.org/software/S0493) is a backdoor malware which has been packaged with legitimate tax preparation software. [GoldenSpy](https://attack.mitre.org/software/S0493) was discovered targeting organizations in China, being delivered with the "Intelligent Tax" software suite which is produced by the Golden Tax Department of Aisino Credit Information Co. and required to pay local taxes.(Citation: Trustwave GoldenSpy June 2020) ', 'GoldenSpy');
INSERT INTO public.attack_softwares VALUES (138, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--eac3d77f-2b7b-4599-ba74-948dc16633ad', '[{"url": "https://attack.mitre.org/software/S0477", "external_id": "S0477", "source_name": "mitre-attack"}, {"url": "https://cdn2.hubspot.net/hubfs/3354902/Cybereason%20Labs%20Analysis%20Operation%20Cobalt%20Kitty.pdf", "description": "Dahan, A. (2017). Operation Cobalt Kitty. Retrieved December 27, 2018.", "source_name": "Cybereason Cobalt Kitty 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Goopy\"\n    ]", "Str": "", "Type": 5, "Index": 1367}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1463}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1415}}', 'S0477', '[Goopy](https://attack.mitre.org/software/S0477) is a Windows backdoor and Trojan used by [APT32](https://attack.mitre.org/groups/G0050) and shares several similarities to another backdoor used by the group ([Denis](https://attack.mitre.org/software/S0354)). [Goopy](https://attack.mitre.org/software/S0477) is named for its impersonation of the legitimate Google Updater executable.(Citation: Cybereason Cobalt Kitty 2017)', 'Goopy');
INSERT INTO public.attack_softwares VALUES (139, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--958b5d06-8bb0-4c5b-a2e7-0130fe654ac7', '[{"url": "https://attack.mitre.org/software/S0531", "external_id": "S0531", "source_name": "mitre-attack"}, {"url": "https://securelist.com/the-tetrade-brazilian-banking-malware/97779/", "description": "GReAT. (2020, July 14). The Tetrade: Brazilian banking malware goes global. Retrieved November 9, 2020.", "source_name": "Securelist Brazilian Banking Malware July 2020"}, {"url": "https://www.welivesecurity.com/2020/04/28/grandoreiro-how-engorged-can-exe-get/", "description": "ESET. (2020, April 28). Grandoreiro: How engorged can an EXE get?. Retrieved November 13, 2020.", "source_name": "ESET Grandoreiro April 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Grandoreiro\"\n    ]", "Str": "", "Type": 5, "Index": 1651}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1753}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1705}}', 'S0531', '[Grandoreiro](https://attack.mitre.org/software/S0531) is a banking trojan written in Delphi that was first observed in 2016 and uses a Malware-as-a-Service (MaaS) business model. [Grandoreiro](https://attack.mitre.org/software/S0531) has confirmed victims in Brazil, Mexico, Portugal, and Spain.(Citation: Securelist Brazilian Banking Malware July 2020)(Citation: ESET Grandoreiro April 2020)', 'Grandoreiro');
INSERT INTO public.attack_softwares VALUES (140, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--1d1fce2f-0db5-402b-9843-4278a0694637', '[{"url": "https://attack.mitre.org/software/S0237", "external_id": "S0237", "source_name": "mitre-attack"}, {"description": "(Citation: Talos GravityRAT)", "source_name": "GravityRAT"}, {"url": "https://blog.talosintelligence.com/2018/04/gravityrat-two-year-evolution-of-apt.html", "description": "Mercer, W., Rascagneres, P. (2018, April 26). GravityRAT - The Two-Year Evolution Of An APT Targeting India. Retrieved May 16, 2018.", "source_name": "Talos GravityRAT"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GravityRAT\"\n    ]", "Str": "", "Type": 5, "Index": 1569}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1670}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1622}}', 'S0237', '[GravityRAT](https://attack.mitre.org/software/S0237) is a remote access tool (RAT) and has been in ongoing development since 2016. The actor behind the tool remains unknown, but two usernames have been recovered that link to the author, which are "TheMartian" and "The Invincible." According to the National Computer Emergency Response Team (CERT) of India, the malware has been identified in attacks against organization and entities in India. (Citation: Talos GravityRAT)', 'GravityRAT');
INSERT INTO public.attack_softwares VALUES (141, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--308b3d68-a084-4dfb-885a-3125e1a9c1e8', '[{"url": "https://attack.mitre.org/software/S0342", "external_id": "S0342", "source_name": "mitre-attack"}, {"description": "(Citation: ESET GreyEnergy Oct 2018)", "source_name": "GreyEnergy"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2018/10/ESET_GreyEnergy.pdf", "description": "Cherepanov, A. (2018, October). GREYENERGY A successor to BlackEnergy. Retrieved November 15, 2018.", "source_name": "ESET GreyEnergy Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GreyEnergy\"\n    ]", "Str": "", "Type": 5, "Index": 1402}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1503}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1455}}', 'S0342', '[GreyEnergy](https://attack.mitre.org/software/S0342) is a backdoor written in C and compiled in Visual Studio. [GreyEnergy](https://attack.mitre.org/software/S0342) shares similarities with the [BlackEnergy](https://attack.mitre.org/software/S0089) malware and is thought to be the successor of it.(Citation: ESET GreyEnergy Oct 2018)', 'GreyEnergy');
INSERT INTO public.attack_softwares VALUES (142, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--45c759ac-b490-48bb-80d4-c8eee3431027', '[{"url": "https://attack.mitre.org/software/S0561", "external_id": "S0561", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/guloader-installing-netwire-rat/", "description": "Duncan, B. (2020, April 3). GuLoader: Malspam Campaign Installing NetWire RAT. Retrieved January 7, 2021.", "source_name": "Unit 42 NETWIRE April 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"GuLoader\"\n    ]", "Str": "", "Type": 5, "Index": 1237}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1336}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1288}}', 'S0561', '[GuLoader](https://attack.mitre.org/software/S0561) is a file downloader that has been used since at least December 2019 to distribute a variety of remote administration tool (RAT) malware, including [NETWIRE](https://attack.mitre.org/software/S0198).(Citation: Unit 42 NETWIRE April 2020)', 'GuLoader');
INSERT INTO public.attack_softwares VALUES (143, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f8dfbc54-b070-4224-b560-79aaa5f835bd', '[{"url": "https://attack.mitre.org/software/S0132", "external_id": "S0132", "source_name": "mitre-attack"}, {"url": "http://blogs.cisco.com/security/h1n1-technical-analysis-reveals-new-capabilities", "description": "Reynolds, J.. (2016, September 13). H1N1: Technical analysis reveals new capabilities. Retrieved September 26, 2016.", "source_name": "Cisco H1N1 Part 1"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"H1N1\"\n    ]", "Str": "", "Type": 5, "Index": 1247}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1342}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1294}}', 'S0132', '[H1N1](https://attack.mitre.org/software/S0132) is a malware variant that has been distributed via a campaign using VBA macros to infect victims. Although it initially had only loader capabilities, it has evolved to include information-stealing functionality. (Citation: Cisco H1N1 Part 1)', 'H1N1');
