INSERT INTO public.attack_softwares VALUES (160, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--40a1b8ec-7295-416c-a6b1-68181d86f120', '[{"url": "https://attack.mitre.org/software/S0601", "external_id": "S0601", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/hildegard-malware-teamtnt/", "description": "Chen, J. et al. (2021, February 3). Hildegard: New TeamTNT Cryptojacking Malware Targeting Kubernetes. Retrieved April 5, 2021.", "source_name": "Unit 42 Hildegard Malware"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Hildegard\"\n    ]", "Str": "", "Type": 5, "Index": 1316}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1535}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Containers\",\n      \"IaaS\"\n    ]", "Str": "", "Type": 5, "Index": 1455}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\"\n    ]", "Str": "", "Type": 5, "Index": 1371}}', 'S0601', '[Hildegard](https://attack.mitre.org/software/S0601) is malware that targets misconfigured kubelets for initial access and runs cryptocurrency miner operations. The malware was first observed in January 2021. The TeamTNT activity group is believed to be behind [Hildegard](https://attack.mitre.org/software/S0601). (Citation: Unit 42 Hildegard Malware)', 'Hildegard');
INSERT INTO public.attack_softwares VALUES (161, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--aad11e34-02ca-4220-91cd-2ed420af4db3', '[{"url": "https://attack.mitre.org/software/S0431", "external_id": "S0431", "source_name": "mitre-attack"}, {"url": "https://www.us-cert.gov/ncas/analysis-reports/ar20-045d", "description": "US-CERT. (2020, February 20). MAR-10271944-1.v1 – North Korean Trojan: HOTCROISSANT. Retrieved May 1, 2020.", "source_name": "US-CERT HOTCROISSANT February 2020"}, {"url": "https://www.carbonblack.com/2020/04/16/vmware-carbon-black-tau-threat-analysis-the-evolution-of-lazarus/", "description": "Knight, S.. (2020, April 16). VMware Carbon Black TAU Threat Analysis: The Evolution of Lazarus. Retrieved May 1, 2020.", "source_name": "Carbon Black HotCroissant April 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"HotCroissant\"\n    ]", "Str": "", "Type": 5, "Index": 1757}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1860}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1812}}', 'S0431', '[HotCroissant](https://attack.mitre.org/software/S0431) is a remote access trojan (RAT) attributed by U.S. government entities to malicious North Korean government cyber activity, tracked collectively as HIDDEN COBRA.(Citation: US-CERT HOTCROISSANT February 2020) [HotCroissant](https://attack.mitre.org/software/S0431) shares numerous code similarities with [Rifdoor](https://attack.mitre.org/software/S0433).(Citation: Carbon Black HotCroissant April 2020)', 'HotCroissant');
INSERT INTO public.attack_softwares VALUES (162, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--73a4793a-ce55-4159-b2a6-208ef29b326f', '[{"url": "https://attack.mitre.org/software/S0203", "external_id": "S0203", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Elderwood Sept 2012) (Citation: Symantec Trojan.Hydraq Jan 2010)", "source_name": "Hydraq"}, {"description": "(Citation: Symantec Elderwood Sept 2012) (Citation: Symantec Trojan.Hydraq Jan 2010)", "source_name": "Aurora"}, {"description": "(Citation: MicroFocus 9002 Aug 2016)", "source_name": "9002 RAT"}, {"url": "https://community.softwaregrp.com/t5/Security-Research/9002-RAT-a-second-building-on-the-left/ba-p/228686#.WosBVKjwZPZ", "description": "Petrovsky, O. (2016, August 30). “9002 RAT” -- a second building on the left. Retrieved February 20, 2018.", "source_name": "MicroFocus 9002 Aug 2016"}, {"url": "https://web.archive.org/web/20190717233006/http://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-elderwood-project.pdf", "description": "O''Gorman, G., and McDonald, G.. (2012, September 6). The Elderwood Project. Retrieved February 15, 2018.", "source_name": "Symantec Elderwood Sept 2012"}, {"url": "https://www.symantec.com/connect/blogs/trojanhydraq-incident", "description": "Symantec Security Response. (2010, January 18). The Trojan.Hydraq Incident. Retrieved February 20, 2018.", "source_name": "Symantec Trojan.Hydraq Jan 2010"}, {"url": "https://www.arbornetworks.com/blog/asert/wp-content/uploads/2016/01/ASERT-Threat-Intelligence-Brief-2015-08-Uncovering-the-Seven-Point-Dagger.pdf", "description": "ASERT. (2015, August). ASERT Threat Intelligence Report – Uncovering the Seven Pointed Dagger. Retrieved March 19, 2018.", "source_name": "ASERT Seven Pointed Dagger Aug 2015"}, {"url": "https://www.fireeye.com/blog/threat-research/2013/11/operation-ephemeral-hydra-ie-zero-day-linked-to-deputydog-uses-diskless-method.htm