INSERT INTO public.attack_softwares VALUES (166, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5147ef15-1cae-4707-8ea1-bee8d98b7f1d', '[{"url": "https://attack.mitre.org/software/S0483", "external_id": "S0483", "source_name": "mitre-attack"}, {"url": "https://securityintelligence.com/new-banking-trojan-icedid-discovered-by-ibm-x-force-research/", "description": "Kessem, L., et al. (2017, November 13). New Banking Trojan IcedID Discovered by IBM X-Force Research. Retrieved July 14, 2020.", "source_name": "IBM IcedID November 2017"}, {"url": "https://blogs.juniper.net/en-us/threat-research/covid-19-and-fmla-campaigns-used-to-install-new-icedid-banking-malware", "description": "Kimayong, P. (2020, June 18). COVID-19 and FMLA Campaigns used to install new IcedID banking malware. Retrieved July 14, 2020.", "source_name": "Juniper IcedID June 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"IcedID\"\n    ]", "Str": "", "Type": 5, "Index": 1742}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1839}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1791}}', 'S0483', '[IcedID](https://attack.mitre.org/software/S0483) is a modular banking malware designed to steal financial information that has been observed in the wild since at least 2017.  [IcedID](https://attack.mitre.org/software/S0483)  has been downloaded by [Emotet](https://attack.mitre.org/software/S0367) in multiple campaigns.(Citation: IBM IcedID November 2017)(Citation: Juniper IcedID June 2020)', 'IcedID');
INSERT INTO public.attack_softwares VALUES (167, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--c8b6cc43-ce61-42ae-87f3-a5f10526f952', '[{"url": "https://attack.mitre.org/software/S0259", "external_id": "S0259", "source_name": "mitre-attack"}, {"description": "(Citation: ASERT InnaputRAT April 2018)", "source_name": "InnaputRAT"}, {"url": "https://asert.arbornetworks.com/innaput-actors-utilize-remote-access-trojan-since-2016-presumably-targeting-victim-files/", "description": "ASERT Team. (2018, April 04). Innaput Actors Utilize Remote Access Trojan Since 2016, Presumably Targeting Victim Files. Retrieved July 9, 2018.", "source_name": "ASERT InnaputRAT April 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"InnaputRAT\"\n    ]", "Str": "", "Type": 5, "Index": 1428}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1529}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1481}}', 'S0259', '[InnaputRAT](https://attack.mitre.org/software/S0259) is a remote access tool that can exfiltrate files from a victim’s machine. [InnaputRAT](https://attack.mitre.org/software/S0259) has been seen out in the wild since 2016. (Citation: ASERT InnaputRAT April 2018)', 'InnaputRAT');
INSERT INTO public.attack_softwares VALUES (168, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--47afe41c-4c08-485e-b062-c3bd209a1cce', '[{"url": "https://attack.mitre.org/software/S0260", "external_id": "S0260", "source_name": "mitre-attack"}, {"description": "(Citation: ESET InvisiMole June 2018)", "source_name": "InvisiMole"}, {"url": "https://www.welivesecurity.com/2018/06/07/invisimole-equipped-spyware-undercover/", "description": "Hromcová, Z. (2018, June 07). InvisiMole: Surprisingly equipped spyware, undercover since 2013. Retrieved July 10, 2018.", "source_name": "ESET InvisiMole June 2018"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2020/06/ESET_InvisiMole.pdf", "description": "Hromcova, Z. and Cherpanov, A. (2020, June). INVISIMOLE: THE HIDDEN PART OF THE STORY. Retrieved July 16, 2020.", "source_name": "ESET InvisiMole June 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"InvisiMole\"\n    ]", "Str": "", "Type": 5, "Index": 2046}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2197}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2149}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2102}}', 'S0260', '[InvisiMole](https://attack.mitre.org/software/S0260) is a modular spyware program that has been used by the InvisiMole Group since at least 2013. [InvisiMole](https://attack.mitre.org/software/S0260) has two backdoor modules called RC2FM and RC2CL that are used to perform post-exploitation activities. It has been discovered on compromised victims in the Ukraine and Russia. [Gamaredon Group](https://attack.mitre.org/groups/G0047) infrastructure has been used to download and execute [InvisiMole](https://attack.mitre.org/software/S0260) against a small number of victims.(Citation: ESET InvisiMole June 2018)(Citation: ESET InvisiMole June 2020)', 'InvisiMole');
