INSERT INTO public.attack_softwares VALUES (171, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8ae43c46-57ef-47d5-a77a-eebb35628db2', '[{"url": "https://attack.mitre.org/software/S0044", "external_id": "S0044", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT28 January 2017)", "source_name": "JHUHUGIT"}, {"description": "This designation has been used in reporting both to refer to the threat group (Skeleton Key) and its associated malware.(Citation: Symantec APT28 Oct 2018)", "source_name": "Trojan.Sofacy"}, {"description": "(Citation: FireEye APT28 January 2017)(Citation: Talos Seduploader Oct 2017)", "source_name": "Seduploader"}, {"description": "(Citation: FireEye APT28 January 2017)", "source_name": "JKEYSKW"}, {"description": "This designation has been used in reporting both to refer to the threat group (APT28) and its associated malware.(Citation: FireEye APT28 January 2017)", "source_name": "Sednit"}, {"description": "(Citation: FireEye APT28 January 2017)", "source_name": "GAMEFISH"}, {"description": "(Citation: Unit 42 Sofacy Feb 2018)", "source_name": "SofacyCarberp"}, {"url": "https://securelist.com/sofacy-apt-hits-high-profile-targets-with-updated-toolset/72924/", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, December 4). Sofacy APT hits high profile targets with updated toolset. Retrieved December 10, 2015.", "source_name": "Kaspersky Sofacy"}, {"url": "https://labsblog.f-secure.com/2015/09/08/sofacy-recycles-carberp-and-metasploit-code/", "description": "F-Secure. (2015, September 8). Sofacy Recycles Carberp and Metasploit Code. Retrieved August 3, 2016.", "source_name": "F-Secure Sofacy 2015"}, {"url": "http://www.welivesecurity.com/wp-content/uploads/2016/10/eset-sednit-part1.pdf", "description": "ESET. (2016, October). En Route with Sednit - Part 1: Approaching the Target. Retrieved November 8, 2016.", "source_name": "ESET Sednit Part 1"}, {"url": "https://www2.fireeye.com/rs/848-DID-242/images/APT28-Center-of-Storm-2017.pdf", "description": "FireEye iSIGHT Intelligence. (2017, January 11). APT28: At the Center of the Storm. Retrieved January 11, 2017.", "source_name": "FireEye APT28 January 2017"}, {"url": "https://www.symantec.com/blogs/election-security/apt28-espionage-military-government", "description": "Symantec Security Response. (2018, October 04). APT28: New Espionage Operations Target Military and Government Organizations. Retrieved November 14, 2018.", "source_name": "Symantec APT28 Oct 2018"}, {"url": "https://blog.talosintelligence.com/2017/10/cyber-conflict-decoy-document.html", "description": "Mercer, W., et al. (2017, October 22). \"Cyber Conflict\" Decoy Document Used in Real Cyber Conflict. Retrieved November 2, 2018.", "source_name": "Talos Seduploader Oct 2017"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/02/unit42-sofacy-attacks-multiple-government-entities/", "description": "Lee, B, et al. (2018, February 28). Sofacy Attacks Multiple Government Entities. Retrieved March 15, 2018.", "source_name": "Unit 42 Sofacy Feb 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"JHUHUGIT\",\n      \"Trojan.Sofacy\",\n      \"Seduploader\",\n      \"JKEYSKW\",\n      \"Sednit\",\n      \"GAMEFISH\",\n      \"SofacyCarberp\"\n    ]", "Str": "", "Type": 5, "Index": 4300}, "x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 4517}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 4469}}', 'S0044', '[JHUHUGIT](https://attack.mitre.org/software/S0044) is malware used by [APT28](https://attack.mitre.org/groups/G0007). It is based on Carberp source code and serves as reconnaissance malware. (Citation: Kaspersky Sofacy) (Citation: F-Secure Sofacy 2015) (Citation: ESET Sednit Part 1) (Citation: FireEye APT28 January 2017)', 'JHUHUGIT');
INSERT INTO public.attack_softwares VALUES (172, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--de6cb631-52f6-4169-a73b-7965390b0c30', '[{"url": "https://attack.mitre.org/software/S0201", "external_id": "S0201", "source_name": "mitre-attack"}, {"description": "(Citation: Microsoft PLATINUM April 2016)", "source_name": "JPIN"}, {"url": "https://download.microsoft.com/download/2/2/5/225BFE3E-E1DE-4F5B-A77B-71200928D209/Platinum%20feature%20article%20-%20Targeted%20attacks%20in%20South%20and%20Southeast%20Asia%20April%202016.pdf", "description": "Windows Defender Advanced Threat Hunting Team. (2016, April 29). PLATINUM: Targeted attacks in South and Southeast Asia. Retrieved February 15, 2018.", "source_name": "Microsoft PLATINUM April 2016"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"JPIN\"\n    ]", "Str": "", "Type": 5, "Index": 1585}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1737}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1689}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Ryan Becwar\"\n    ]", "Str": "", "Type": 5, "Index": 1635}}', 'S0201', '[JPIN](https://attack.mitre.org/software/S0201) is a custom-built backdoor family used by [PLATINUM](https://attack.mitre.org/groups/G0068). Evidence suggests developers of [JPIN](https://attack.mitre.org/software/S0201) and [Dipsind](https://attack.mitre.org/software/S0200) code bases were related in some way. (Citation: Microsoft PLATINUM April 2016)', 'JPIN');
