INSERT INTO public.attack_softwares VALUES (176, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--11e36d5b-6a92-4bf9-8eb7-85eb24f59e22', '[{"url": "https://attack.mitre.org/software/S0271", "external_id": "S0271", "source_name": "mitre-attack"}, {"description": "(Citation: US-CERT KEYMARBLE Aug 2018)", "source_name": "KEYMARBLE"}, {"url": "https://www.us-cert.gov/ncas/analysis-reports/AR18-221A", "description": "US-CERT. (2018, August 09). MAR-10135536-17 – North Korean Trojan: KEYMARBLE. Retrieved August 16, 2018.", "source_name": "US-CERT KEYMARBLE Aug 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"KEYMARBLE\"\n    ]", "Str": "", "Type": 5, "Index": 1219}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1319}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1271}}', 'S0271', '[KEYMARBLE](https://attack.mitre.org/software/S0271) is a Trojan that has reportedly been used by the North Korean government. (Citation: US-CERT KEYMARBLE Aug 2018)', 'KEYMARBLE');
INSERT INTO public.attack_softwares VALUES (177, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8bdfe255-e658-4ddd-a11c-b854762e451d', '[{"url": "https://attack.mitre.org/software/S0526", "external_id": "S0526", "source_name": "mitre-attack"}, {"url": "https://www.cybereason.com/blog/back-to-the-future-inside-the-kimsuky-kgh-spyware-suite", "description": "Dahan, A. et al. (2020, November 2). Back to the Future: Inside the Kimsuky KGH Spyware Suite. Retrieved November 6, 2020.", "source_name": "Cybereason Kimsuky November 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"KGH_SPY\",\n      \"KGH_SPY \"\n    ]", "Str": "", "Type": 5, "Index": 1376}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1492}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1444}}', 'S0526', '[KGH_SPY](https://attack.mitre.org/software/S0526) is a modular suite of tools used by [Kimsuky](https://attack.mitre.org/groups/G0094) for reconnaissance, information stealing, and backdoor capabilities. [KGH_SPY](https://attack.mitre.org/software/S0526) derived its name from PDB paths and internal names found in samples containing "KGH".(Citation: Cybereason Kimsuky November 2020)', 'KGH_SPY');
INSERT INTO public.attack_softwares VALUES (178, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--7dbb67c7-270a-40ad-836e-c45f8948aa5a', '[{"url": "https://attack.mitre.org/software/S0156", "external_id": "S0156", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT32 May 2017)", "source_name": "KOMPROGO"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/05/cyber-espionage-apt32.html", "description": "Carr, N.. (2017, May 14). Cyber Espionage is Alive and Well: APT32 and the Threat to Global Corporations. Retrieved June 18, 2017.", "source_name": "FireEye APT32 May 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"KOMPROGO\"\n    ]", "Str": "", "Type": 5, "Index": 1316}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1415}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1367}}', 'S0156', '[KOMPROGO](https://attack.mitre.org/software/S0156) is a signature backdoor used by [APT32](https://attack.mitre.org/groups/G0050) that is capable of process, file, and registry management. (Citation: FireEye APT32 May 2017)', 'KOMPROGO');
INSERT INTO public.attack_softwares VALUES (179, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--86b92f6c-9c05-4c51-b361-4c7bb13e21a1', '[{"url": "https://attack.mitre.org/software/S0356", "external_id": "S0356", "source_name": "mitre-attack"}, {"description": "(Citation: Talos Konni May 2017)", "source_name": "KONNI"}, {"url": "https://blog.talosintelligence.com/2017/05/konni-malware-under-radar-for-years.html", "description": "Rascagneres, P. (2017, May 03). KONNI: A Malware Under The Radar For Years. Retrieved November 5, 2018.", "source_name": "Talos Konni May 2017"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/09/unit42-new-konni-malware-attacking-eurasia-southeast-asia/", "description": "Grunzweig, J., Lee, B. (2018, September 27). New KONNI Malware attacking Eurasia and Southeast Asia. Retrieved November 5, 2018.", "source_name": "Unit 42 NOKKI Sept 2018"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/10/unit42-nokki-almost-ties-the-knot-with-dogcall-reaper-group-uses-new-malware-to-deploy-rat/", "description": "Grunzweig, J. (2018, October 01). NOKKI Almost Ties the Knot with DOGCALL: Reaper Group Uses New Malware to Deploy RAT. Retrieved November 5, 2018.", "source_name": "Unit 42 Nokki Oct 2018"}, {"url": "https://medium.com/d-hunter/a-look-into-konni-2019-campaign-b45a0f321e9b", "description": "Karmi, D. (2020, January 4). A Look Into Konni 2019 Campaign. Retrieved April 28, 2020.", "source_name": "Medium KONNI Jan 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"KONNI\"\n    ]", "Str": "", "Type": 5, "Index": 2804}, "x_mitre_version": {"Num": 0, "Raw": "\"1.4\"", "Str": "1.4", "Type": 3, "Index": 2970}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2922}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Doron Karmi, @DoronKarmi\"\n    ]", "Str": "", "Type": 5, "Index": 2855}}', 'S0356', '[KONNI](https://attack.mitre.org/software/S0356) is a Windows remote administration too that has been seen in use since 2014 and evolved in its capabilities through at least 2017. [KONNI](https://attack.mitre.org/software/S0356) has been linked to several campaigns involving North Korean themes.(Citation: Talos Konni May 2017) [KONNI](https://attack.mitre.org/software/S0356) has significant code overlap with the [NOKKI](https://attack.mitre.org/software/S0353) malware family. There is some evidence potentially linking [KONNI](https://attack.mitre.org/software/S0356) to [APT37](https://attack.mitre.org/groups/G0067).(Citation: Unit 42 NOKKI Sept 2018)(Citation: Unit 42 Nokki Oct 2018)(Citation: Medium KONNI Jan 2020)', 'KONNI');
INSERT INTO public.attack_softwares VALUES (180, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--26fed817-e7bf-41f9-829a-9075ffac45c2', '[{"url": "https://attack.mitre.org/software/S0088", "external_id": "S0088", "source_name": "mitre-attack"}, {"url": "http://research.zscaler.com/2016/01/malicious-office-files-dropping-kasidet.html", "description": "Yadav, A., et al. (2016, January 29). Malicious Office files dropping Kasidet and Dridex. Retrieved March 24, 2016.", "source_name": "Zscaler Kasidet"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kasidet\"\n    ]", "Str": "", "Type": 5, "Index": 1103}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1201}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1153}}', 'S0088', '[Kasidet](https://attack.mitre.org/software/S0088) is a backdoor that has been dropped by using malicious VBA macros. (Citation: Zscaler Kasidet)', 'Kasidet');
INSERT INTO public.attack_softwares VALUES (181, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--536be338-e2ef-4a6b-afb6-8d5568b91eb2', '[{"url": "https://attack.mitre.org/software/S0265", "external_id": "S0265", "source_name": "mitre-attack"}, {"description": "(Citation: Unit 42 Kazuar May 2017)", "source_name": "Kazuar"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/05/unit42-kazuar-multiplatform-espionage-backdoor-api-access/", "description": "Levene, B, et al. (2017, May 03). Kazuar: Multiplatform Espionage Backdoor with API Access. Retrieved July 17, 2018.", "source_name": "Unit 42 Kazuar May 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kazuar\"\n    ]", "Str": "", "Type": 5, "Index": 1288}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 1400}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1337}}', 'S0265', '[Kazuar](https://attack.mitre.org/software/S0265) is a fully featured, multi-platform backdoor Trojan written using the Microsoft .NET framework. (Citation: Unit 42 Kazuar May 2017)', 'Kazuar');
INSERT INTO public.attack_softwares VALUES (182, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8c1d01ff-fdc0-4586-99bd-c248e0761af5', '[{"url": "https://attack.mitre.org/software/S0585", "external_id": "S0585", "source_name": "mitre-attack"}, {"url": "https://www.amnesty.org/en/latest/news/2021/02/viet-nam-hacking-group-targets-activist/", "description": "Amnesty International. (2021, February 24). Vietnamese activists targeted by notorious hacking group. Retrieved March 1, 2021.", "source_name": "Amnesty Intl. Ocean Lotus February 2021"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kerrdown\"\n    ]", "Str": "", "Type": 5, "Index": 1232}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1331}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1283}}', 'S0585', '[Kerrdown](https://attack.mitre.org/software/S0585) is a downloader used by [APT32](https://attack.mitre.org/groups/G0050) to install spyware from a server on the victim''s network.(Citation: Amnesty Intl. Ocean Lotus February 2021)', 'Kerrdown');
INSERT INTO public.attack_softwares VALUES (183, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--c984b414-b766-44c5-814a-2fe96c913c12', '[{"url": "https://attack.mitre.org/software/S0487", "external_id": "S0487", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2018/12/ESET-The_Dark_Side_of_the_ForSSHe.pdf", "description": "Dumont, R., M.Léveillé, M., Porcher, H. (2018, December 1). THE DARK SIDE OF THE FORSSHE A landscape of OpenSSH backdoors. Retrieved July 16, 2020.", "source_name": "ESET ForSSHe December 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kessel\"\n    ]", "Str": "", "Type": 5, "Index": 1347}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1442}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1396}}', 'S0487', '[Kessel](https://attack.mitre.org/software/S0487) is an advanced version of OpenSSH which acts as a custom backdoor, mainly acting to steal credentials and function as a bot. [Kessel](https://attack.mitre.org/software/S0487) has been active since its C2 domain began resolving in August 2018.(Citation: ESET ForSSHe December 2018)', 'Kessel');
INSERT INTO public.attack_softwares VALUES (184, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5dd649c0-bca4-488b-bd85-b180474ec62e', '[{"url": "https://attack.mitre.org/software/S0387", "external_id": "S0387", "source_name": "mitre-attack"}, {"description": "(Citation: PWC KeyBoys Feb 2017)(Citation: CitizenLab KeyBoy Nov 2016)(Citation: Rapid7 KeyBoy Jun 2013)", "source_name": "KeyBoy"}, {"url": "https://citizenlab.ca/2016/11/parliament-keyboy/", "description": "Hulcoop, A., et al. (2016, November 17). It’s Parliamentary KeyBoy and the targeting of the Tibetan Community. Retrieved June 13, 2019.", "source_name": "CitizenLab KeyBoy Nov 2016"}, {"url": "https://www.pwc.co.uk/issues/cyber-security-data-privacy/research/the-keyboys-are-back-in-town.html", "description": "Parys, B. (2017, February 11). The KeyBoys are back in town. Retrieved June 13, 2019.", "source_name": "PWC KeyBoys Feb 2017"}, {"url": "https://blog.rapid7.com/2013/06/07/keyboy-targeted-attacks-against-vietnam-and-india/", "description": "Guarnieri, C., Schloesser M. (2013, June 7). KeyBoy, Targeted Attacks against Vietnam and India. Retrieved June 14, 2019.", "source_name": "Rapid7 KeyBoy Jun 2013"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"KeyBoy\"\n    ]", "Str": "", "Type": 5, "Index": 1970}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 2123}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2075}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bart Parys\"\n    ]", "Str": "", "Type": 5, "Index": 2022}}', 'S0387', '[KeyBoy](https://attack.mitre.org/software/S0387) is malware that has been used in targeted campaigns against members of the Tibetan Parliament in 2016.(Citation: CitizenLab KeyBoy Nov 2016)(Citation: PWC KeyBoys Feb 2017)', 'KeyBoy');
INSERT INTO public.attack_softwares VALUES (190, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--2a6f4c7b-e690-4cc7-ab6b-1f821fb6b80b', '[{"url": "https://attack.mitre.org/software/S0042", "external_id": "S0042", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/11/china-based-threat.html", "description": "FireEye Threat Intelligence. (2015, December 1). China-based Cyber Threat Group Uses Dropbox for Malware Communications and Targets Hong Kong Media Outlets. Retrieved December 4, 2015.", "source_name": "FireEye admin@338"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LOWBALL\"\n    ]", "Str": "", "Type": 5, "Index": 1268}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1366}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1318}}', 'S0042', '[LOWBALL](https://attack.mitre.org/software/S0042) is malware used by [admin@338](https://attack.mitre.org/groups/G0018). It was used in August 2015 in email messages targeting Hong Kong-based media organizations. (Citation: FireEye admin@338)', 'LOWBALL');
INSERT INTO public.attack_softwares VALUES (185, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4b072c90-bc7a-432b-940e-016fc1c01761', '[{"url": "https://attack.mitre.org/software/S0276", "external_id": "S0276", "source_name": "mitre-attack"}, {"description": "(Citation: synack 2016 review)", "source_name": "Keydnap"}, {"description": "(Citation: OSX Keydnap malware)", "source_name": "OSX/Keydnap"}, {"url": "https://www.welivesecurity.com/2016/07/06/new-osxkeydnap-malware-hungry-credentials/", "description": "Marc-Etienne M.Leveille. (2016, July 6). New OSX/Keydnap malware is hungry for credentials. Retrieved July 3, 2017.", "source_name": "OSX Keydnap malware"}, {"url": "https://www.synack.com/2017/01/01/mac-malware-2016/", "description": "Patrick Wardle. (2017, January 1). Mac Malware of 2016. Retrieved September 21, 2018.", "source_name": "synack 2016 review"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Keydnap\",\n      \"OSX/Keydnap\"\n    ]", "Str": "", "Type": 5, "Index": 1564}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1681}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1635}}', 'S0276', 'This piece of malware steals the content of the user''s keychain while maintaining a permanent backdoor  (Citation: OSX Keydnap malware).', 'Keydnap');
INSERT INTO public.attack_softwares VALUES (186, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d6e55656-e43f-411f-a7af-45df650471c5', '[{"url": "https://attack.mitre.org/software/S0599", "external_id": "S0599", "source_name": "mitre-attack"}, {"url": "https://blog.aquasec.com/threat-alert-kinsing-malware-container-vulnerability", "description": "Singer, G. (2020, April 3). Threat Alert: Kinsing Malware Attacks Targeting Container Environments. Retrieved April 1, 2021.", "source_name": "Aqua Kinsing April 2020"}, {"url": "https://sysdig.com/blog/zoom-into-kinsing-kdevtmpfsi/", "description": "Huang, K. (2020, November 23). Zoom into Kinsing. Retrieved April 1, 2021.", "source_name": "Sysdig Kinsing November 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kinsing\"\n    ]", "Str": "", "Type": 5, "Index": 1471}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1674}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Containers\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1608}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\"\n    ]", "Str": "", "Type": 5, "Index": 1524}}', 'S0599', '[Kinsing](https://attack.mitre.org/software/S0599) is Golang-based malware that runs a cryptocurrency miner and attempts to spread itself to other hosts in the victim environment. (Citation: Aqua Kinsing April 2020)(Citation: Sysdig Kinsing November 2020)', 'Kinsing');
INSERT INTO public.attack_softwares VALUES (187, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b2d134a1-7bd5-4293-94d4-8fc978cb1cd7', '[{"url": "https://attack.mitre.org/software/S0437", "external_id": "S0437", "source_name": "mitre-attack"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/following-trail-blacktech-cyber-espionage-campaigns/", "description": "Bermejo, L., et al. (2017, June 22). Following the Trail of BlackTech’s Cyber Espionage Campaigns. Retrieved May 5, 2020.", "source_name": "TrendMicro BlackTech June 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kivars\"\n    ]", "Str": "", "Type": 5, "Index": 1260}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1357}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1309}}', 'S0437', '[Kivars](https://attack.mitre.org/software/S0437) is a modular remote access tool (RAT), derived from the Bifrost RAT, that was used by [BlackTech](https://attack.mitre.org/groups/G0098) in a 2010 campaign.(Citation: TrendMicro BlackTech June 2017)', 'Kivars');
INSERT INTO public.attack_softwares VALUES (188, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f108215f-3487-489d-be8b-80e346d32518', '[{"url": "https://attack.mitre.org/software/S0162", "external_id": "S0162", "source_name": "mitre-attack"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/02/unit42-xagentosx-sofacys-xagent-macos-tool/", "description": "Robert Falcone. (2017, February 14). XAgentOSX: Sofacy''s Xagent macOS Tool. Retrieved July 12, 2017.", "source_name": "XAgentOSX 2017"}, {"url": "https://researchcenter.paloaltonetworks.com/2016/09/unit42-sofacys-komplex-os-x-trojan/", "description": "Dani Creus, Tyler Halfpop, Robert Falcone. (2016, September 26). Sofacy''s ''Komplex'' OS X Trojan. Retrieved July 8, 2017.", "source_name": "Sofacy Komplex Trojan"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Komplex\"\n    ]", "Str": "", "Type": 5, "Index": 1581}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1677}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1631}}', 'S0162', '[Komplex](https://attack.mitre.org/software/S0162) is a backdoor that has been used by [APT28](https://attack.mitre.org/groups/G0007) on OS X and appears to be developed in a similar manner to [XAgentOSX](https://attack.mitre.org/software/S0161) (Citation: XAgentOSX 2017) (Citation: Sofacy Komplex Trojan).', 'Komplex');
INSERT INTO public.attack_softwares VALUES (189, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--c2417bab-3189-4d4d-9d60-96de2cdaf0ab', '[{"url": "https://attack.mitre.org/software/S0236", "external_id": "S0236", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Orangeworm April 2018)", "source_name": "Kwampirs"}, {"url": "https://www.symantec.com/blogs/threat-intelligence/orangeworm-targets-healthcare-us-europe-asia", "description": "Symantec Security Response Attack Investigation Team. (2018, April 23). New Orangeworm attack group targets the healthcare sector in the U.S., Europe, and Asia. Retrieved May 8, 2018.", "source_name": "Symantec Orangeworm April 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Kwampirs\"\n    ]", "Str": "", "Type": 5, "Index": 1497}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1700}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1652}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Elger Vinicius S. Rodrigues, @elgervinicius, CYBINT Centre\"\n    ]", "Str": "", "Type": 5, "Index": 1551}}', 'S0236', '[Kwampirs](https://attack.mitre.org/software/S0236) is a backdoor Trojan used by [Orangeworm](https://attack.mitre.org/groups/G0071). It has been found on machines which had software installed for the use and control of high-tech imaging devices such as X-Ray and MRI machines. (Citation: Symantec Orangeworm April 2018)', 'Kwampirs');
INSERT INTO public.attack_softwares VALUES (191, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--6ba1d7ae-d60b-43e6-9f08-a8b787e9d9cb', '[{"url": "https://attack.mitre.org/software/S0395", "external_id": "S0395", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2019/05/ESET-LightNeuron.pdf", "description": "Faou, M. (2019, May). Turla LightNeuron: One email away from remote code execution. Retrieved June 24, 2019.", "source_name": "ESET LightNeuron May 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LightNeuron\"\n    ]", "Str": "", "Type": 5, "Index": 1469}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1586}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1523}}', 'S0395', '[LightNeuron](https://attack.mitre.org/software/S0395) is a sophisticated backdoor that has targeted Microsoft Exchange servers since at least 2014. [LightNeuron](https://attack.mitre.org/software/S0395) has been used by [Turla](https://attack.mitre.org/groups/G0010) to target diplomatic and foreign affairs-related organizations. The presence of certain strings in the malware suggests a Linux variant of [LightNeuron](https://attack.mitre.org/software/S0395) exists.(Citation: ESET LightNeuron May 2019)', 'LightNeuron');
INSERT INTO public.attack_softwares VALUES (192, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--e9e9bfe2-76f4-4870-a2a1-b7af89808613', '[{"url": "https://attack.mitre.org/software/S0211", "external_id": "S0211", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Linfo May 2012)", "source_name": "Linfo"}, {"url": "https://web.archive.org/web/20190717233006/http://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-elderwood-project.pdf", "description": "O''Gorman, G., and McDonald, G.. (2012, September 6). The Elderwood Project. Retrieved February 15, 2018.", "source_name": "Symantec Elderwood Sept 2012"}, {"url": "https://www.symantec.com/security_response/writeup.jsp?docid=2012-051605-2535-99", "description": "Zhou, R. (2012, May 15). Backdoor.Linfo. Retrieved February 23, 2018.", "source_name": "Symantec Linfo May 2012"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Linfo\"\n    ]", "Str": "", "Type": 5, "Index": 1647}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1743}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1695}}', 'S0211', '[Linfo](https://attack.mitre.org/software/S0211) is a rootkit trojan used by [Elderwood](https://attack.mitre.org/groups/G0066) to open a backdoor on compromised hosts. (Citation: Symantec Elderwood Sept 2012) (Citation: Symantec Linfo May 2012)', 'Linfo');
INSERT INTO public.attack_softwares VALUES (193, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--0efefea5-78da-4022-92bc-d726139e8883', '[{"url": "https://attack.mitre.org/software/S0362", "external_id": "S0362", "source_name": "mitre-attack"}, {"url": "https://www.anomali.com/blog/pulling-linux-rabbit-rabbot-malware-out-of-a-hat", "description": "Anomali Threat Research. (2018, December 6). Pulling Linux Rabbit/Rabbot Malware Out of a Hat. Retrieved December 17, 2020.", "source_name": "anomali-linux-rabbit"}, {"url": "https://www.anomali.com/blog/pulling-linux-rabbit-rabbot-malware-out-of-a-hat", "description": "Anomali Labs. (2018, December 6). Pulling Linux Rabbit/Rabbot Malware Out of a Hat. Retrieved March 4, 2019.", "source_name": "Anomali Linux Rabbit 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Linux Rabbit\"\n    ]", "Str": "", "Type": 5, "Index": 1636}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1737}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1691}}', 'S0362', '[Linux Rabbit](https://attack.mitre.org/software/S0362) is malware that targeted Linux servers and IoT devices in a campaign lasting from August to October 2018. It shares code with another strain of malware known as Rabbot. The goal of the campaign was to install cryptocurrency miners onto the targeted servers and devices.(Citation: Anomali Linux Rabbit 2018)
INSERT INTO public.attack_softwares VALUES (194, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b865dded-0553-4962-a44b-6fe7863effed', '[{"url": "https://attack.mitre.org/software/S0397", "external_id": "S0397", "source_name": "mitre-attack"}, {"description": "(Citation: ESET LoJax Sept 2018)", "source_name": "LoJax"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2018/09/ESET-LoJax.pdf", "description": "ESET. (2018, September). LOJAX First UEFI rootkit found in the wild, courtesy of the Sednit group. Retrieved July 2, 2019.", "source_name": "ESET LoJax Sept 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LoJax\"\n    ]", "Str": "", "Type": 5, "Index": 1275}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1438}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1390}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jean-Ian Boutin, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 1326}}', 'S0397', '[LoJax](https://attack.mitre.org/software/S0397) is a UEFI rootkit used by [APT28](https://attack.mitre.org/groups/G0007) to persist remote access software on targeted systems.(Citation: ESET LoJax Sept 2018)', 'LoJax');
INSERT INTO public.attack_softwares VALUES (195, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5af7a825-2d9f-400d-931a-e00eb9e27f48', '[{"url": "https://attack.mitre.org/software/S0372", "external_id": "S0372", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/born-this-way-origins-of-lockergoga/", "description": "Harbison, M.. (2019, March 26). Born This Way? Origins of LockerGoga. Retrieved April 16, 2019.", "source_name": "Unit42 LockerGoga 2019"}, {"url": "https://www.carbonblack.com/2019/03/22/tau-threat-intelligence-notification-lockergoga-ransomware/", "description": "CarbonBlack Threat Analysis Unit. (2019, March 22). TAU Threat Intelligence Notification – LockerGoga Ransomware. Retrieved April 16, 2019.", "source_name": "CarbonBlack LockerGoga 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LockerGoga\"\n    ]", "Str": "", "Type": 5, "Index": 1540}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 1641}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1593}}', 'S0372', '[LockerGoga](https://attack.mitre.org/software/S0372) is ransomware that has been tied to various attacks on European companies. It was first reported upon in January 2019.(Citation: Unit42 LockerGoga 2019)(Citation: CarbonBlack LockerGoga 2019)', 'LockerGoga');
INSERT INTO public.attack_softwares VALUES (196, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--cb741463-f0fe-42e0-8d45-bc7e8335f5ae', '[{"url": "https://attack.mitre.org/software/S0447", "external_id": "S0447", "source_name": "mitre-attack"}, {"url": "https://insights.infoblox.com/threat-intelligence-reports/threat-intelligence--22", "description": "Hoang, M. (2019, January 31). Malicious Activity Report: Elements of  Lokibot Infostealer. Retrieved May 15, 2020.", "source_name": "Infoblox Lokibot January 2019"}, {"url": "https://blog.morphisec.com/lokibot-with-autoit-obfuscator-frenchy-shellcode", "description": "Cheruku, H. (2020, April 15). LOKIBOT WITH AUTOIT OBFUSCATOR + FRENCHY SHELLCODE. Retrieved May 14, 2020.", "source_name": "Morphisec Lokibot April 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Lokibot\"\n    ]", "Str": "", "Type": 5, "Index": 1612}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1710}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1662}}', 'S0447', '[Lokibot](https://attack.mitre.org/software/S0447) is a malware designed to collect credentials and security tokens from an infected machine. [Lokibot](https://attack.mitre.org/software/S0447) has also been used to establish backdoors in enterprise environments.(Citation: Infoblox Lokibot January 2019)(Citation: Morphisec Lokibot April 2020)', 'Lokibot');
INSERT INTO public.attack_softwares VALUES (197, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--c9ccc4df-1f56-49e7-ad57-b383e1451688', '[{"url": "https://attack.mitre.org/software/S0582", "external_id": "S0582", "source_name": "mitre-attack"}, {"description": "(Citation: Proofpoint LookBack Malware Aug 2019)", "source_name": "LookBack"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/lookback-malware-targets-united-states-utilities-sector-phishing-attacks", "description": "Raggi, M. Schwarz, D.. (2019, August 1). LookBack Malware Targets the United States Utilities Sector with Phishing Attacks Impersonating Engineering Licensing Boards. Retrieved February 25, 2021.", "source_name": "Proofpoint LookBack Malware Aug 2019"}, {"url": "https://www.dragos.com/threat/talonite/", "description": "Dragos. (null). TALONITE. Retrieved February 25, 2021.", "source_name": "Dragos TALONITE"}, {"url": "https://hub.dragos.com/hubfs/Year-in-Review/Dragos_2020_ICS_Cybersecurity_Year_In_Review.pdf?hsCtaTracking=159c0fc3-92d8-425d-aeb8-12824f2297e8%7Cf163726d-579b-4996-9a04-44e5a124d770", "description": "Dragos. (n.d.). ICS Cybersecurity Year in Review 2020. Retrieved February 25, 2021.", "source_name": "Dragos Threat Report 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LookBack\"\n    ]", "Str": "", "Type": 5, "Index": 2185}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2284}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2236}}', 'S0582', '[LookBack](https://attack.mitre.org/software/S0582) is a remote access trojan written in C++ that was used against at least three US utility companies in July 2019. The TALONITE activity group has been observed using [LookBack](https://attack.mitre.org/software/S0582).(Citation: Proofpoint LookBack Malware Aug 2019)(Citation: Dragos TALONITE)(Citation: Dragos Threat Report 2020)', 'LookBack');
INSERT INTO public.attack_softwares VALUES (198, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f99f3dcc-683f-4936-8791-075ac5e58f10', '[{"url": "https://attack.mitre.org/software/S0451", "external_id": "S0451", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/2019/06/20/loudminer-mining-cracked-vst-software/", "description": "Malik, M. (2019, June 20). LoudMiner: Cross-platform mining in cracked VST software. Retrieved May 18, 2020.", "source_name": "ESET LoudMiner June 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"LoudMiner\"\n    ]", "Str": "", "Type": 5, "Index": 1244}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1359}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1296}}', 'S0451', '[LoudMiner](https://attack.mitre.org/software/S0451) is a cryptocurrency miner which uses virtualization software to siphon system resources. The miner has been bundled with pirated copies of Virtual Studio Technology (VST) for Windows and macOS.(Citation: ESET LoudMiner June 2019)', 'LoudMiner');
