INSERT INTO public.attack_softwares VALUES (200, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--251fbae2-78f6-4de7-84f6-194c727a64ad', '[{"url": "https://attack.mitre.org/software/S0010", "external_id": "S0010", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2014/07/spy-of-the-tiger.html", "description": "Villeneuve, N., Homan, J. (2014, July 31). Spy of the Tiger. Retrieved September 29, 2015.", "source_name": "Villeneuve 2014"}, {"url": "http://www.trendmicro.com/cloud-content/us/pdfs/security-intelligence/white-papers/wp_dissecting-lurid-apt.pdf", "description": "Villeneuve, N., Sancho, D. (2011). THE “LURID” DOWNLOADER. Retrieved November 12, 2014.", "source_name": "Villeneuve 2011"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Lurid\",\n      \"Enfal\"\n    ]", "Str": "", "Type": 5, "Index": 1496}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1607}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1559}}', 'S0010', '[Lurid](https://attack.mitre.org/software/S0010) is a malware family that has been used by several groups, including [PittyTiger](https://attack.mitre.org/groups/G0011), in targeted attacks as far back as 2006. (Citation: Villeneuve 2014) (Citation: Villeneuve 2011)', 'Lurid');
INSERT INTO public.attack_softwares VALUES (201, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--9b19d6b4-cfcb-492f-8ca8-8449e7331573', '[{"url": "https://attack.mitre.org/software/S0443", "external_id": "S0443", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/10/messagetap-who-is-reading-your-text-messages.html", "description": "Leong, R., Perez, D., Dean, T. (2019, October 31). MESSAGETAP: Who’s Reading Your Text Messages?. Retrieved May 11, 2020.", "source_name": "FireEye MESSAGETAP October 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"MESSAGETAP\"\n    ]", "Str": "", "Type": 5, "Index": 1338}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1437}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1391}}', 'S0443', '[MESSAGETAP](https://attack.mitre.org/software/S0443) is a data mining malware family deployed by [APT41](https://attack.mitre.org/groups/G0096) into telecommunications networks to monitor and save SMS traffic from specific phone numbers, IMSI numbers, or that contain specific keywords. (Citation: FireEye MESSAGETAP October 2019)', 'MESSAGETAP');
