INSERT INTO public.attack_softwares VALUES (204, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--35cd1d01-1ede-44d2-b073-a264d727bc04', '[{"url": "https://attack.mitre.org/software/S0409", "external_id": "S0409", "source_name": "mitre-attack"}, {"description": "(Citation: Securelist Machete Aug 2014)", "source_name": "Machete"}, {"description": "(Citation: 360 Machete Sep 2020)", "source_name": "Pyark"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2019/08/ESET_Machete.pdf", "description": "ESET. (2019, July). MACHETE JUST GOT SHARPER Venezuelan government institutions under attack. Retrieved September 13, 2019.", "source_name": "ESET Machete July 2019"}, {"url": "https://securelist.com/el-machete/66108/", "description": "Kaspersky Global Research and Analysis Team. (2014, August 20). El Machete. Retrieved September 13, 2019.", "source_name": "Securelist Machete Aug 2014"}, {"url": "https://blog.360totalsecurity.com/en/apt-c-43-steals-venezuelan-military-secrets-to-provide-intelligence-support-for-the-reactionaries-hpreact-campaign/", "description": "kate. (2020, September 25). APT-C-43 steals Venezuelan military secrets to provide intelligence support for the reactionaries — HpReact campaign. Retrieved November 20, 2020.", "source_name": "360 Machete Sep 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Machete\",\n      \"Pyark\"\n    ]", "Str": "", "Type": 5, "Index": 2220}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2407}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2359}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Matias Nicolas Porolli, ESET\"\n    ]", "Str": "", "Type": 5, "Index": 2288}}', 'S0409', '[Machete](https://attack.mitre.org/software/S0409) is a cyber espionage toolset used by [Machete](https://attack.mitre.org/groups/G0095). It is a Python-based backdoor targeting Windows machines that was first observed in 2010.(Citation: ESET Machete July 2019)(Citation: Securelist Machete Aug 2014)(Citation: 360 Machete Sep 2020)', 'Machete');
INSERT INTO public.attack_softwares VALUES (205, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--1cc934e4-b01d-4543-a011-b988dfc1a458', '[{"url": "https://attack.mitre.org/software/S0167", "external_id": "S0167", "source_name": "mitre-attack"}, {"description": "(Citation: ClearSky Wilted Tulip July 2017)", "source_name": "Matryoshka"}, {"url": "http://www.clearskysec.com/wp-content/uploads/2017/07/Operation_Wilted_Tulip.pdf", "description": "ClearSky Cyber Security and Trend Micro. (2017, July). Operation Wilted Tulip: Exposing a cyber espionage apparatus. Retrieved August 21, 2017.", "source_name": "ClearSky Wilted Tulip July 2017"}, {"url": "https://s3-eu-west-1.amazonaws.com/minervaresearchpublic/CopyKittens/CopyKittens.pdf", "description": "Minerva Labs LTD and ClearSky Cyber Security. (2015, November 23). CopyKittens Attack Group. Retrieved September 11, 2017.", "source_name": "CopyKittens Nov 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Matryoshka\"\n    ]", "Str": "", "Type": 5, "Index": 1838}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 1939}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1891}}', 'S0167', '[Matryoshka](https://attack.mitre.org/software/S0167) is a malware framework used by [CopyKittens](https://attack.mitre.org/groups/G0052) that consists of a dropper, loader, and RAT. It has multiple versions; v1 was seen in the wild from July 2016 until January 2017. v2 has fewer commands and other minor differences. (Citation: ClearSky Wilted Tulip July 2017) (Citation: CopyKittens Nov 2015)', 'Matryoshka');
INSERT INTO public.attack_softwares VALUES (206, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d9f7383c-95ec-4080-bbce-121c9384457b', '[{"url": "https://attack.mitre.org/software/S0449", "external_id": "S0449", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2020/05/tactics-techniques-procedures-associated-with-maze-ransomware-incidents.html", "description": "Kennelly, J., Goody, K., Shilko, J. (2020, May 7). Navigating the MAZE: Tactics, Techniques and Procedures Associated With MAZE Ransomware Incidents. Retrieved May 18, 2020.", "source_name": "FireEye Maze May 2020"}, {"url": "https://www.mcafee.com/blogs/other-blogs/mcafee-labs/ransomware-maze/", "description": "Mundo, A. (2020, March 26). Ransomware Maze. Retrieved May 18, 2020.", "source_name": "McAfee Maze March 2020"}, {"url": "https://news.sophos.com/en-us/2020/09/17/maze-attackers-adopt-ragnar-locker-virtual-machine-technique/", "description": "Brandt, A., Mackenzie, P.. (2020, September 17). Maze Attackers Adopt Ragnar Locker Virtual Machine Technique. Retrieved October 9, 2020.", "source_name": "Sophos Maze VM September 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Maze\"\n    ]", "Str": "", "Type": 5, "Index": 2142}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2368}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2320}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Center for Threat-Informed Defense (CTID)\",\n      \"SarathKumar Rajendran, Trimble Inc\"\n    ]", "Str": "", "Type": 5, "Index": 2192}}', 'S0449', '[Maze](https://attack.mitre.org/software/S0449) ransomware, previously known as "ChaCha", was discovered in May 2019. In addition to encrypting files on victim machines for impact, [Maze](https://attack.mitre.org/software/S0449) operators conduct information stealing campaigns prior to encryption and post the