INSERT INTO public.attack_softwares VALUES (207, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--dfa03c7d-79ed-4ce2-b9d1-ddc9dbf56ad2', '[{"url": "https://attack.mitre.org/software/S0459", "external_id": "S0459", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/new-python-based-payload-mechaflounder-used-by-chafer/", "description": "Falcone, R. (2019, March 4). New Python-Based Payload MechaFlounder Used by Chafer. Retrieved May 27, 2020.", "source_name": "Unit 42 MechaFlounder March 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"MechaFlounder\"\n    ]", "Str": "", "Type": 5, "Index": 1321}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1425}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1377}}', 'S0459', '[MechaFlounder](https://attack.mitre.org/software/S0459) is a python-based remote access tool (RAT) that has been used by [APT39](https://attack.mitre.org/groups/G0087). The payload uses a combination of actor developed code and code snippets freely available online in development communities.(Citation: Unit 42 MechaFlounder March 2019)', 'MechaFlounder');
