INSERT INTO public.attack_softwares VALUES (225, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--2a70812b-f1ef-44db-8578-a496a227aef2', '[{"url": "https://attack.mitre.org/software/S0198", "external_id": "S0198", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT33 Sept 2017) (Citation: FireEye APT33 Webinar Sept 2017) (Citation: McAfee Netwire Mar 2015)", "source_name": "NETWIRE"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/09/apt33-insights-into-iranian-cyber-espionage.html", "description": "O''Leary, J., et al. (2017, September 20). Insights into Iranian Cyber Espionage: APT33 Targets Aerospace and Energy Sectors and has Ties to Destructive Malware. Retrieved February 15, 2018.", "source_name": "FireEye APT33 Sept 2017"}, {"url": "https://securingtomorrow.mcafee.com/mcafee-labs/netwire-rat-behind-recent-targeted-attacks/", "description": "McAfee. (2015, March 2). Netwire RAT Behind Recent Targeted Attacks. Retrieved February 15, 2018.", "source_name": "McAfee Netwire Mar 2015"}, {"url": "https://www.brighttalk.com/webcast/10703/275683", "description": "Davis, S. and Carr, N. (2017, September 21). APT33: New Insights into Iranian Cyber Espionage Group. Retrieved February 15, 2018.", "source_name": "FireEye APT33 Webinar Sept 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"NETWIRE\"\n    ]", "Str": "", "Type": 5, "Index": 2153}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 2351}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\",\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 2273}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tony Lambert, Red Canary\"\n    ]", "Str": "", "Type": 5, "Index": 2206}}', 'S0198', '[NETWIRE](https://attack.mitre.org/software/S0198) is a publicly available, multiplatform remote administration tool (RAT) that has been used by criminal and APT groups since at least 2012.(Citation: FireEye APT33 Sept 2017) (Citation: McAfee Netwire Mar 2015) (Citation: FireEye APT33 Webinar Sept 2017)', 'NETWIRE');
INSERT INTO public.attack_softwares VALUES (226, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--071d5d65-83ec-4a55-acfa-be7d5f28ba9a', '[{"url": "https://attack.mitre.org/software/S0353", "external_id": "S0353", "source_name": "mitre-attack"}, {"description": "(Citation: Unit 42 NOKKI Sept 2018)", "source_name": "NOKKI"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/09/unit42-new-konni-malware-attacking-eurasia-southeast-asia/", "description": "Grunzweig, J., Lee, B. (2018, September 27). New KONNI Malware attacking Eurasia and Southeast Asia. Retrieved November 5, 2018.", "source_name": "Unit 42 NOKKI Sept 2018"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/10/unit42-nokki-almost-ties-the-knot-with-dogcall-reaper-group-uses-new-malware-to-deploy-rat/", "description": "Grunzweig, J. (2018, October 01). NOKKI Almost Ties the Knot with DOGCALL: Reaper Group Uses New Malware to Deploy RAT. Retrieved November 5, 2018.", "source_name": "Unit 42 Nokki Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"NOKKI\"\n    ]", "Str": "", "Type": 5, "Index": 2068}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2164}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2116}}', 'S0353', '[NOKKI](https://attack.mitre.org/software/S0353) is a modular remote access tool. The earliest observed attack using [NOKKI](https://attack.mitre.org/software/S0353) was in January 2018. [NOKKI](https://attack.mitre.org/software/S0353) has significant code overlap with the [KONNI](https://attack.mitre.org/software/S0356) malware family. There is some evidence potentially linking [NOKKI](https://attack.mitre.org/software/S0353) to [APT37](https://attack.mitre.org/groups/G0067).(Citation: Unit 42 NOKKI Sept 2018)(Citation: Unit 42 Nokki Oct 2018)', 'NOKKI');
