INSERT INTO public.attack_softwares VALUES (232, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--fde50aaa-f5de-4cb8-989a-babb57d6a704', '[{"url": "https://attack.mitre.org/software/S0056", "external_id": "S0056", "source_name": "mitre-attack"}, {"url": "https://web.archive.org/web/20200302085133/https://www.cylance.com/content/dam/cylance/pages/operation-cleaver/Cylance_Operation_Cleaver_Report.pdf", "description": "Cylance. (2014, December). Operation Cleaver. Retrieved September 14, 2017.", "source_name": "Cylance Cleaver"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Net Crawler\",\n      \"NetC\"\n    ]", "Str": "", "Type": 5, "Index": 1385}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1501}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1453}}', 'S0056', '[Net Crawler](https://attack.mitre.org/software/S0056) is an intranet worm capable of extracting credentials using credential dumpers and spreading to systems on a network over SMB by brute forcing accounts with recovered passwords and using [PsExec](https://attack.mitre.org/software/S0029) to execute a copy of [Net Crawler](https://attack.mitre.org/software/S0056). (Citation: Cylance Cleaver)', 'Net Crawler');
INSERT INTO public.attack_softwares VALUES (233, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--cafd0bf8-2b9c-46c7-ae3c-3e0f42c5062e', '[{"url": "https://attack.mitre.org/software/S0033", "external_id": "S0033", "source_name": "mitre-attack"}, {"url": "http://www.securelist.com/en/downloads/vlpdfs/kaspersky-the-net-traveler-part1-final.pdf", "description": "Kaspersky Lab''s Global Research and Analysis Team. (n.d.). The NetTraveler (aka ‘Travnet’). Retrieved November 12, 2014.", "source_name": "Kaspersky NetTraveler"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"NetTraveler\"\n    ]", "Str": "", "Type": 5, "Index": 1312}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1414}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1366}}', 'S0033', '[NetTraveler](https://attack.mitre.org/software/S0033) is malware that has been used in multiple cyber espionage campaigns for basic surveillance of victims. The earliest known samples have timestamps back to 2005, and the largest number of observed samples were created between 2010 and 2013. (Citation: Kaspersky NetTraveler)', 'NetTraveler');
