INSERT INTO public.attack_softwares VALUES (235, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--911fe4c3-444d-4e92-83b8-cc761ac5fd3b', '[{"url": "https://attack.mitre.org/software/S0508", "external_id": "S0508", "source_name": "mitre-attack"}, {"url": "https://www.zdnet.com/article/sly-malware-author-hides-cryptomining-botnet-behind-ever-shifting-proxy-service/", "description": "Cimpanu, C. (2018, September 13). Sly malware author hides cryptomining botnet behind ever-shifting proxy service. Retrieved September 15, 2020.", "source_name": "Zdnet Ngrok September 2018"}, {"url": "https://www.fireeye.com/blog/threat-research/2020/05/tactics-techniques-procedures-associated-with-maze-ransomware-incidents.html", "description": "Kennelly, J., Goody, K., Shilko, J. (2020, May 7). Navigating the MAZE: Tactics, Techniques and Procedures Associated With MAZE Ransomware Incidents. Retrieved May 18, 2020.", "source_name": "FireEye Maze May 2020"}, {"url": "https://cyware.com/news/cyber-attackers-leverage-tunneling-service-to-drop-lokibot-onto-victims-systems-6f610e44", "description": "Cyware. (2019, May 29). Cyber attackers leverage tunneling service to drop Lokibot onto victims’ systems. Retrieved September 15, 2020.", "source_name": "Cyware Ngrok May 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Ngrok\"\n    ]", "Str": "", "Type": 5, "Index": 2267}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2428}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2380}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Janantha Marasinghe\"\n    ]", "Str": "", "Type": 5, "Index": 2318}}', 'S0508', '[Ngrok](https://attack.mitre.org/software/S0508) is a legitimate reverse proxy tool that can create a secure tunnel to servers located behind firewalls or on local machines that do not have a public IP. [Ngrok](https://attack.mitre.org/software/S0508) has been leveraged by threat actors in several campaigns including use for lateral movement and data exfiltration.(Citation: Zdnet Ngrok September 2018)(Citation: FireEye Maze May 2020)(Citation: Cyware Ngrok May 2019)', 'Ngrok');
