INSERT INTO public.attack_softwares VALUES (237, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5719af9d-6b16-46f9-9b28-fb019541ddbb', '[{"url": "https://attack.mitre.org/software/S0368", "external_id": "S0368", "source_name": "mitre-attack"}, {"description": "(Citation: ESET Telebots June 2017)", "source_name": "ExPetr"}, {"description": "(Citation: ESET Telebots June 2017)", "source_name": "Diskcoder.C"}, {"description": "(Citation: Talos Nyetya June 2017)", "source_name": "GoldenEye"}, {"description": "(Citation: Talos Nyetya June 2017)(Citation: ESET Telebots June 2017)", "source_name": "Petrwrap"}, {"description": "(Citation: Talos Nyetya June 2017)", "source_name": "Nyetya"}, {"url": "https://blog.talosintelligence.com/2017/06/worldwide-ransomware-variant.html", "description": "Chiu, A. (2016, June 27). New Ransomware Variant \"Nyetya\" Compromises Systems Worldwide. Retrieved March 26, 2019.", "source_name": "Talos Nyetya June 2017"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA17-181A", "description": "US-CERT. (2017, July 1). Alert (TA17-181A): Petya Ransomware. Retrieved March 15, 2019.", "source_name": "US-CERT NotPetya 2017"}, {"url": "https://www.welivesecurity.com/2017/06/30/telebots-back-supply-chain-attacks-against-ukraine/", "description": "Cherepanov, A.. (2017, June 30). TeleBots are back: Supply chain attacks against Ukraine. Retrieved June 11, 2020.", "source_name": "ESET Telebots June 2017"}, {"url": "https://www.justice.gov/opa/press-release/file/1328521/download", "description": "Scott W. Brady. (2020, October 15). United States vs. Yuriy Sergeyevich Andrienko et al.. Retrieved November 25, 2020.", "source_name": "US District Court Indictment GRU Unit 74455 October 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"NotPetya\",\n      \"ExPetr\",\n      \"Diskcoder.C\",\n      \"GoldenEye\",\n      \"Petrwrap\",\n      \"Nyetya\"\n    ]", "Str": "", "Type": 5, "Index": 3347}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 3536}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3488}}', 'S0368', '[NotPetya](https://attack.mitre.org/software/S0368) is malware that was used by [Sandworm Team](https://attack.mitre.org/groups/G0034) in a worldwide attack starting on June 27, 2017. While [NotPetya](https://attack.mitre.org/software/S0368) appears as a form of ransomware, its main purpose was to destroy data and disk structures on compromised systems; the attackers never intended to make the encrypted data recoverable. As such, [NotPetya](https://attack.mitre.org/software/S0368) may be more appropriately thought of as a form of wiper malware. [NotPetya](https://attack.mitre.org/software/S0368) contains worm-like features to spread itself across a computer network using the SMBv1 exploits EternalBlue and EternalRomance.(Citation: Talos Nyetya June 2017)(Citation: US-CERT NotPetya 2017)(Citation: ESET Telebots June 2017)(Citation: US District Court Indictment GRU Unit 74455 October 2020)', 'NotPetya');
