INSERT INTO public.attack_softwares VALUES (244, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4b6ec280-7bbb-48ff-ae59-b189520ebe83', '[{"url": "https://attack.mitre.org/software/S0439", "external_id": "S0439", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2019/07/ESET_Okrum_and_Ketrican.pdf", "description": "Hromcova, Z. (2019, July). OKRUM AND KETRICAN: AN OVERVIEW OF RECENT KE3CHANG GROUP ACTIVITY. Retrieved May 6, 2020.", "source_name": "ESET Okrum July 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Okrum\"\n    ]", "Str": "", "Type": 5, "Index": 1187}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1333}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1285}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"ESET\"\n    ]", "Str": "", "Type": 5, "Index": 1238}}', 'S0439', '[Okrum](https://attack.mitre.org/software/S0439) is a Windows backdoor that has been seen in use since December 2016 with strong links to [Ke3chang](https://attack.mitre.org/groups/G0004).(Citation: ESET Okrum July 2019)', 'Okrum');
INSERT INTO public.attack_softwares VALUES (245, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--3249e92a-870b-426d-8790-ba311c1abfb4', '[{"url": "https://attack.mitre.org/software/S0365", "external_id": "S0365", "source_name": "mitre-attack"}, {"url": "https://blog.talosintelligence.com/2018/02/olympic-destroyer.html", "description": "Mercer, W. and Rascagneres, P. (2018, February 12). Olympic Destroyer Takes Aim At Winter Olympics. Retrieved March 14, 2019.", "source_name": "Talos Olympic Destroyer 2018"}, {"url": "https://www.justice.gov/opa/press-release/file/1328521/download", "description": "Scott W. Brady. (2020, October 15). United States vs. Yuriy Sergeyevich Andrienko et al.. Retrieved November 25, 2020.", "source_name": "US District Court Indictment GRU Unit 74455 October 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Olympic Destroyer\"\n    ]", "Str": "", "Type": 5, "Index": 1983}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2091}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2043}}', 'S0365', '[Olympic Destroyer](https://attack.mitre.org/software/S0365) is malware that was used by [Sandworm Team](https://attack.mitre.org/groups/G0034) against the 2018 Winter Olympics, held in Pyeongchang, South Korea. The main purpose of the malware was to render infected computer systems inoperable. The malware leverages various native Windows utilities and API calls to carry out its destructive tasks. [Olympic Destroyer](https://attack.mitre.org/software/S0365) has worm-like features to spread itself across a computer network in order to maximize its destructive impact.(Citation: Talos Olympic Destroyer 2018)(Citation: US District Court Indictment GRU Unit 74455 October 2020) ', 'Olympic Destroyer');
