INSERT INTO public.attack_softwares VALUES (248, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--06d735e7-1db1-4dbe-ab4b-acbe419f902b', '[{"url": "https://attack.mitre.org/software/S0229", "external_id": "S0229", "source_name": "mitre-attack"}, {"description": "(Citation: Proofpoint Leviathan Oct 2017)", "source_name": "Orz"}, {"description": "(Citation: FireEye Periscope March 2018)", "source_name": "AIRBREAK"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/leviathan-espionage-actor-spearphishes-maritime-and-defense-targets", "description": "Axel F, Pierre T. (2017, October 16). Leviathan: Espionage actor spearphishes maritime and defense targets. Retrieved February 15, 2018.", "source_name": "Proofpoint Leviathan Oct 2017"}, {"url": "https://www.fireeye.com/blog/threat-research/2018/03/suspected-chinese-espionage-group-targeting-maritime-and-engineering-industries.html", "description": "FireEye. (2018, March 16). Suspected Chinese Cyber Espionage Group (TEMP.Periscope) Targeting U.S. Engineering and Maritime Industries. Retrieved April 11, 2018.", "source_name": "FireEye Periscope March 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Orz\",\n      \"AIRBREAK\"\n    ]", "Str": "", "Type": 5, "Index": 2005}, "x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 2117}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2069}}', 'S0229', '[Orz](https://attack.mitre.org/software/S0229) is a custom JavaScript backdoor used by [Leviathan](https://attack.mitre.org/groups/G0065). It was observed being used in 2014 as well as in August 2017 when it was dropped by Microsoft Publisher files. (Citation: Proofpoint Leviathan Oct 2017) (Citation: FireEye Periscope March 2018)', 'Orz');
INSERT INTO public.attack_softwares VALUES (249, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a60657fa-e2e7-4f8f-8128-a882534ae8c5', '[{"url": "https://attack.mitre.org/software/S0072", "external_id": "S0072", "source_name": "mitre-attack"}, {"url": "https://www.secureworks.com/research/threat-group-3390-targets-organizations-for-cyberespionage", "description": "Dell SecureWorks Counter Threat Unit Threat Intelligence. (2015, August 5). Threat Group-3390 Targets Organizations for Cyberespionage. Retrieved August 18, 2018.", "source_name": "Dell TG-3390"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"OwaAuth\"\n    ]", "Str": "", "Type": 5, "Index": 1268}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1366}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1318}}', 'S0072', '[OwaAuth](https://attack.mitre.org/software/S0072) is a Web shell and credential stealer deployed to Microsoft Exchange servers that appears to be exclusively used by [Threat Group-3390](https://attack.mitre.org/groups/G0027). (Citation: Dell TG-3390)', 'OwaAuth');
INSERT INTO public.attack_softwares VALUES (250, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4800d0f9-00aa-47cd-a4d2-92198585b8fd', '[{"url": "https://attack.mitre.org/software/S0598", "external_id": "S0598", "source_name": "mitre-attack"}, {"description": "(Citation: NCCIC AR-17-20045 February 2017)", "source_name": "Fobushell"}, {"url": "https://www.cert.ssi.gouv.fr/uploads/CERTFR-2021-CTI-005.pdf", "description": "ANSSI. (2021, January 27). SANDWORM INTRUSION SET CAMPAIGN TARGETING CENTREON SYSTEMS. Retrieved March 30, 2021.", "source_name": "ANSSI Sandworm January 2021"}, {"url": "https://us-cert.cisa.gov/sites/default/files/publications/AR-17-20045_Enhanced_Analysis_of_GRIZZLY_STEPPE_Activity.pdf", "description": "NCCIC. (2017, February 10). Enhanced Analysis of GRIZZLY STEPPE Activity. Retrieved April 12, 2021.", "source_name": "NCCIC AR-17-20045 February 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"P.A.S. Webshell\",\n      \"Fobushell\"\n    ]", "Str": "", "Type": 5, "Index": 1656}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1796}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\",\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1733}}', 'S0598', '[P.A.S. Webshell](https://attack.mitre.org/software/S0598) is a publicly available multifunctional PHP webshell in use since at least 2016 that provides remote access and execution on target web servers.(Citation: ANSSI Sandworm January 2021)', 'P.A.S. Webshell');
INSERT INTO public.attack_softwares VALUES (251, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b2c5d3ca-b43a-4888-ad8d-e2d43497bf85', '[{"url": "https://attack.mitre.org/software/S0016", "external_id": "S0016", "source_name": "mitre-attack"}, {"url": "http://www.secureworks.com/cyber-threat-intelligence/threats/The_Lifecycle_of_Peer_to_Peer_Gameover_ZeuS/", "description": "SecureWorks. (2013). The Lifecycle of Peer-to-Peer (Gameover) ZeuS. Retrieved August 19, 2015.", "source_name": "Dell P2P ZeuS"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"P2P ZeuS\",\n      \"Peer-to-Peer ZeuS\",\n      \"Gameover ZeuS\"\n    ]", "Str": "", "Type": 5, "Index": 1193}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1342}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1294}}', 'S0016', '[P2P ZeuS](https://attack.mitre.org/software/S0016) is a closed-source fork of the leaked version of the ZeuS botnet. It presents improvements over the leaked version, including a peer-to-peer architecture. (Citation: Dell P2P ZeuS)', 'P2P ZeuS');
INSERT INTO public.attack_softwares VALUES (252, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f6ae7a52-f3b6-4525-9daf-640c083f006e', '[{"url": "https://attack.mitre.org/software/S0158", "external_id": "S0158", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT32 May 2017)", "source_name": "PHOREAL"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/05/cyber-espionage-apt32.html", "description": "Carr, N.. (2017, May 14). Cyber Espionage is Alive and Well: APT32 and the Threat to Global Corporations. Retrieved June 18, 2017.", "source_name": "FireEye APT32 May 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PHOREAL\"\n    ]", "Str": "", "Type": 5, "Index": 1255}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1353}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1305}}', 'S0158', '[PHOREAL](https://attack.mitre.org/software/S0158) is a signature backdoor used by [APT32](https://attack.mitre.org/groups/G0050). (Citation: FireEye APT32 May 2017)', 'PHOREAL');
INSERT INTO public.attack_softwares VALUES (253, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--21c0b55b-5ff3-4654-a05e-e3fc1ee1ce1b', '[{"url": "https://attack.mitre.org/software/S0254", "external_id": "S0254", "source_name": "mitre-attack"}, {"description": "(Citation: Rancor Unit42 June 2018)", "source_name": "PLAINTEE"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/06/unit42-rancor-targeted-attacks-south-east-asia-using-plaintee-ddkong-malware-families/", "description": "Ash, B., et al. (2018, June 26). RANCOR: Targeted Attacks in South East Asia Using PLAINTEE and DDKONG Malware Families. Retrieved July 2, 2018.", "source_name": "Rancor Unit42 June 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PLAINTEE\"\n    ]", "Str": "", "Type": 5, "Index": 1391}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1490}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1442}}', 'S0254', '[PLAINTEE](https://attack.mitre.org/software/S0254) is a malware sample that has been used by [Rancor](https://attack.mitre.org/groups/G0075) in targeted attacks in Singapore and Cambodia. (Citation: Rancor Unit42 June 2018)', 'PLAINTEE');
INSERT INTO public.attack_softwares VALUES (259, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--e85cae1a-bce3-4ac4-b36b-b00acac0567b', '[{"url": "https://attack.mitre.org/software/S0371", "external_id": "S0371", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2018/12/overruled-containing-a-potentially-destructive-adversary.html", "description": "Ackerman, G., et al. (2018, December 21). OVERRULED: Containing a Potentially Destructive Adversary. Retrieved January 17, 2019.", "source_name": "FireEye APT33 Guardrail"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"POWERTON\"\n    ]", "Str": "", "Type": 5, "Index": 1381}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1480}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1432}}', 'S0371', '[POWERTON](https://attack.mitre.org/software/S0371) is a custom PowerShell backdoor first observed in 2018. It has typically been deployed as a late-stage backdoor by [APT33](https://attack.mitre.org/groups/G0064). At least two variants of the backdoor have been identified, with the later version containing improved functionality.(Citation: FireEye APT33 Guardrail)', 'POWERTON');
INSERT INTO public.attack_softwares VALUES (254, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b57f419e-8b12-49d3-886b-145383725dcd', '[{"url": "https://attack.mitre.org/software/S0435", "external_id": "S0435", "source_name": "mitre-attack"}, {"description": "PLEAD derived its name from letters used in backdoor commands in intrusion campaigns.(Citation: Trend Micro PLEAD RTLO)", "source_name": "PLEAD"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/following-trail-blacktech-cyber-espionage-campaigns/", "description": "Bermejo, L., et al. (2017, June 22). Following the Trail of BlackTech’s Cyber Espionage Campaigns. Retrieved May 5, 2020.", "source_name": "TrendMicro BlackTech June 2017"}, {"url": "https://blogs.jpcert.or.jp/en/2018/03/malware-tscooki-7aa0.html", "description": "Tomonaga, S.. (2018, June 8). PLEAD Downloader Used by BlackTech. Retrieved May 6, 2020.", "source_name": "JPCert PLEAD Downloader June 2018"}, {"url": "https://blogs.jpcert.or.jp/en/2018/03/malware-tscooki-7aa0.html", "description": "Tomonaga, S.. (2018, March 6). Malware “TSCookie”. Retrieved May 6, 2020.", "source_name": "JPCert TSCookie March 2018"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/plead-targeted-attacks-against-taiwanese-government-agencies-2/", "description": "Alintanahin, K.. (2014, May 23). PLEAD Targeted Attacks Against Taiwanese Government Agencies. Retrieved April 22, 2019.", "source_name": "Trend Micro PLEAD RTLO"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PLEAD\"\n    ]", "Str": "", "Type": 5, "Index": 2688}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2876}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2828}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Tatsuya Daitoku, Cyber Defense Institute, Inc.\"\n    ]", "Str": "", "Type": 5, "Index": 2739}}', 'S0435', '[PLEAD](https://attack.mitre.org/software/S0435) is a remote access tool (RAT) and downloader used by [BlackTech](https://attack.mitre.org/groups/G0098) in targeted attacks in East Asia including Taiwan, Japan, and Hong Kong.(Citation: TrendMicro BlackTech June 2017)(Citation: JPCert PLEAD Downloader June 2018) [PLEAD](https://attack.mitre.org/software/S0435) has also been referred to as [TSCookie](https://attack.mitre.org/software/S0436), though more recent reporting indicates likely separation between the two.(Citation: JPCert TSCookie March 2018)(Citation: JPCert PLEAD Downloader June 2018) ', 'PLEAD');
INSERT INTO public.attack_softwares VALUES (255, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--53d47b09-09c2-4015-8d37-6633ecd53f79', '[{"url": "https://attack.mitre.org/software/S0216", "external_id": "S0216", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT37 Feb 2018)", "source_name": "POORAIM"}, {"url": "https://www2.fireeye.com/rs/848-DID-242/images/rpt_APT37.pdf", "description": "FireEye. (2018, February 20). APT37 (Reaper): The Overlooked North Korean Actor. Retrieved March 1, 2018.", "source_name": "FireEye APT37 Feb 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"POORAIM\"\n    ]", "Str": "", "Type": 5, "Index": 1234}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1332}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1284}}', 'S0216', '[POORAIM](https://attack.mitre.org/software/S0216) is a backdoor used by [APT37](https://attack.mitre.org/groups/G0067) in campaigns since at least 2014. (Citation: FireEye APT37 Feb 2018)', 'POORAIM');
INSERT INTO public.attack_softwares VALUES (256, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5e595477-2e78-4ce7-ae42-e0b059b17808', '[{"url": "https://attack.mitre.org/software/S0150", "external_id": "S0150", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye POSHSPY April 2017)", "source_name": "POSHSPY"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/03/dissecting_one_ofap.html", "description": "Dunwoody, M.. (2017, April 3). Dissecting One of APT29’s Fileless WMI and PowerShell Backdoors (POSHSPY). Retrieved April 5, 2017.", "source_name": "FireEye POSHSPY April 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"POSHSPY\"\n    ]", "Str": "", "Type": 5, "Index": 1396}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1494}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1446}}', 'S0150', '[POSHSPY](https://attack.mitre.org/software/S0150) is a backdoor that has been used by [APT29](https://attack.mitre.org/groups/G0016) since at least 2015. It appears to be used as a secondary backdoor used if the actors lost access to their primary backdoors. (Citation: FireEye POSHSPY April 2017)', 'POSHSPY');
INSERT INTO public.attack_softwares VALUES (257, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--17e919aa-4a49-445c-b103-dbb8df9e7351', '[{"url": "https://attack.mitre.org/software/S0145", "external_id": "S0145", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye FIN7 March 2017)", "source_name": "POWERSOURCE"}, {"description": "Based on similar descriptions of functionality, it appears S0145, as named by FireEye, is the same as the first stages of a backdoor named DNSMessenger by Cisco''s Talos Intelligence Group. However, FireEye appears to break DNSMessenger into two parts: S0145 and S0146. (Citation: Cisco DNSMessenger March 2017) (Citation: FireEye FIN7 March 2017)", "source_name": "DNSMessenger"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/03/fin7_spear_phishing.html", "description": "Miller, S., et al. (2017, March 7). FIN7 Spear Phishing Campaign Targets Personnel Involved in SEC Filings. Retrieved March 8, 2017.", "source_name": "FireEye FIN7 March 2017"}, {"url": "http://blog.talosintelligence.com/2017/03/dnsmessenger.html", "description": "Brumaghin, E. and Grady, C.. (2017, March 2). Covert Channels and Poor Decisions: The Tale of DNSMessenger. Retrieved March 8, 2017.", "source_name": "Cisco DNSMessenger March 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"POWERSOURCE\",\n      \"DNSMessenger\"\n    ]", "Str": "", "Type": 5, "Index": 2367}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2491}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2443}}', 'S0145', '[POWERSOURCE](https://attack.mitre.org/software/S0145) is a PowerShell backdoor that is a heavily obfuscated and modified version of the publicly available tool DNS_TXT_Pwnage. It was observed in February 2017 in spearphishing campaigns against personnel involved with United States Securities and Exchange Commission (SEC) filings at various organizations. The malware was delivered when macros were enabled by the victim and a VBS script was dropped. (Citation: FireEye FIN7 March 2017) (Citation: Cisco DNSMessenger March 2017)', 'POWERSOURCE');
