INSERT INTO public.attack_softwares VALUES (273, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--3d57dcc4-be99-4613-9482-d5218f5ec13e', '[{"url": "https://attack.mitre.org/software/S0518", "external_id": "S0518", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/wp-content/uploads/2019/10/ESET_Operation_Ghost_Dukes.pdf", "description": "Faou, M., Tartare, M., Dupuy, T. (2019, October). OPERATION GHOST. Retrieved September 23, 2020.", "source_name": "ESET Dukes October 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PolyglotDuke\"\n    ]", "Str": "", "Type": 5, "Index": 1287}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1390}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1342}}', 'S0518', '[PolyglotDuke](https://attack.mitre.org/software/S0518) is a downloader that has been used by [APT29](https://attack.mitre.org/groups/G0016) since at least 2013. [PolyglotDuke](https://attack.mitre.org/software/S0518) has been used to drop [MiniDuke](https://attack.mitre.org/software/S0051).(Citation: ESET Dukes October 2019)', 'PolyglotDuke');
