INSERT INTO public.attack_softwares VALUES (274, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--222ba512-32d9-49ac-aefd-50ce981ce2ce', '[{"url": "https://attack.mitre.org/software/S0453", "external_id": "S0453", "source_name": "mitre-attack"}, {"url": "https://blog.malwarebytes.com/threat-analysis/2015/11/no-money-but-pony-from-a-mail-to-a-trojan-horse/", "description": "hasherezade. (2016, April 11). No money, but Pony! From a mail to a trojan horse. Retrieved May 21, 2020.", "source_name": "Malwarebytes Pony April 2016"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Pony\"\n    ]", "Str": "", "Type": 5, "Index": 1289}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1494}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1446}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Arie Olshtein, Check Point\",\n      \"Kobi Eisenkraft, Check Point\"\n    ]", "Str": "", "Type": 5, "Index": 1339}}', 'S0453', '[Pony](https://attack.mitre.org/software/S0453) is a credential stealing malware, though has also been used among adversaries for its downloader capabilities. The source code for Pony Loader 1.0 and 2.0 were leaked online, leading to their use by various threat actors.(Citation: Malwarebytes Pony April 2016)', 'Pony');
INSERT INTO public.attack_softwares VALUES (275, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--0a9c51e0-825d-4b9b-969d-ce86ed8ce3c3', '[{"url": "https://attack.mitre.org/software/S0177", "external_id": "S0177", "source_name": "mitre-attack"}, {"url": "https://www.malwaretech.com/2013/08/powerloader-injection-something-truly.html", "description": "MalwareTech. (2013, August 13). PowerLoader Injection – Something truly amazing. Retrieved December 16, 2017.", "source_name": "MalwareTech Power Loader Aug 2013"}, {"url": "https://www.welivesecurity.com/2013/03/19/gapz-and-redyms-droppers-based-on-power-loader-code/", "description": "Matrosov, A. (2013, March 19). Gapz and Redyms droppers based on Power Loader code. Retrieved December 16, 2017.", "source_name": "WeLiveSecurity Gapz and Redyms Mar 2013"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Power Loader\",\n      \"Win32/Agent.UAW\"\n    ]", "Str": "", "Type": 5, "Index": 1583}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1711}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1663}}', 'S0177', '[Power Loader](https://attack.mitre.org/software/S0177) is modular code sold in the cybercrime market used as a downloader in malware families such as Carberp, Redyms and Gapz. (Citation: MalwareTech Power Loader Aug 2013) (Citation: WeLiveSecurity Gapz and Redyms Mar 2013)', 'Power Loader');
INSERT INTO public.attack_softwares VALUES (276, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--00c3bfcb-99bd-4767-8c03-b08f585f5c8a', '[{"url": "https://attack.mitre.org/software/S0139", "external_id": "S0139", "source_name": "mitre-attack"}, {"description": "(Citation: Volexity PowerDuke November 2016)", "source_name": "PowerDuke"}, {"url": "https://www.volexity.com/blog/2016/11/09/powerduke-post-election-spear-phishing-campaigns-targeting-think-tanks-and-ngos/", "description": "Adair, S.. (2016, November 9). PowerDuke: Widespread Post-Election Spear Phishing Campaigns Targeting Think Tanks and NGOs. Retrieved January 11, 2017.", "source_name": "Volexity PowerDuke November 2016"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PowerDuke\"\n    ]", "Str": "", "Type": 5, "Index": 1466}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1566}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1518}}', 'S0139', '[PowerDuke](https://attack.mitre.org/software/S0139) is a backdoor that was used by [APT29](https://attack.mitre.org/groups/G0016) in 2016. It has primarily been delivered through Microsoft Word or Excel attachments containing malicious macros. (Citation: Volexity PowerDuke November 2016)', 'PowerDuke');
INSERT INTO public.attack_softwares VALUES (277, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--53486bc7-7748-4716-8190-e4f1fde04c53', '[{"url": "https://attack.mitre.org/software/S0441", "external_id": "S0441", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/unit42-inception-attackers-target-europe-year-old-office-vulnerability/", "description": "Lancaster, T. (2018, November 5). Inception Attackers Target Europe with Year-old Office Vulnerability. Retrieved May 8, 2020.", "source_name": "Unit 42 Inception November 2018"}, {"url": "https://securelist.com/recent-cloud-atlas-activity/92016/", "description": "GReAT. (2019, August 12). Recent Cloud Atlas activity. Retrieved May 8, 2020.", "source_name": "Kaspersky Cloud Atlas August 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PowerShower\"\n    ]", "Str": "", "Type": 5, "Index": 1576}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1678}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1630}}', 'S0441', '[PowerShower](https://attack.mitre.org/software/S0441) is a PowerShell backdoor used by [Inception](https://attack.mitre.org/groups/G0100) for initial reconnaissance and to download and execute second stage payloads.(Citation: Unit 42 Inception November 2018)(Citation: Kaspersky Cloud Atlas August 2019)', 'PowerShower');
INSERT INTO public.attack_softwares VALUES (278, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--dcac85c1-6485-4790-84f6-de5e6f6b91dd', '[{"url": "https://attack.mitre.org/software/S0393", "external_id": "S0393", "source_name": "mitre-attack"}, {"url": "https://www.welivesecurity.com/2019/05/29/turla-powershell-usage/", "description": "Faou, M. and Dumont R.. (2019, May 29). A dive into Turla PowerShell usage. Retrieved June 14, 2019.", "source_name": "ESET Turla PowerShell May 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"PowerStallion\"\n    ]", "Str": "", "Type": 5, "Index": 1252}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1356}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1308}}', 'S0393', '[PowerStallion](https://attack.mitre.org/software/S0393) is a lightweight [PowerShell](https://attack.mitre.org/techniques/T1059/001) backdoor used by [Turla](https://attack.mitre.org/groups/G0010), possibly as a recovery access tool to install other backdoors.(Citation: ESET Turla PowerShell May 2019)', 'PowerStallion');
INSERT INTO public.attack_softwares VALUES (281, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--069af411-9b24-4e85-b26c-623d035bbe84', '[{"url": "https://attack.mitre.org/software/S0238", "external_id": "S0238", "source_name": "mitre-attack"}, {"description": "(Citation: McAfee GhostSecret)", "source_name": "Proxysvc"}, {"url": "https://securingtomorrow.mcafee.com/mcafee-labs/analyzing-operation-ghostsecret-attack-seeks-to-steal-data-worldwide/", "description": "Sherstobitoff, R., Malhotra, A. (2018, April 24). Analyzing Operation GhostSecret: Attack Seeks to Steal Data Worldwide. Retrieved May 16, 2018.", "source_name": "McAfee GhostSecret"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Proxysvc\"\n    ]", "Str": "", "Type": 5, "Index": 1691}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1853}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1805}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Edward Millington\"\n    ]", "Str": "", "Type": 5, "Index": 1745}}', 'S0238', '[Proxysvc](https://attack.mitre.org/software/S0238) is a malicious DLL used by [Lazarus Group](https://attack.mitre.org/groups/G0032) in a campaign known as Operation GhostSecret. It has appeared to be operating undetected since 2017 and was mostly observed in higher education organizations. The goal of [Proxysvc](https://attack.mitre.org/software/S0238) is to deliver additional payloads to the target and to maintain control for the attacker. It is in the form of a DLL that can also be executed as a standalone process. (Citation: McAfee GhostSecret)', 'Proxysvc');
