INSERT INTO public.attack_softwares VALUES (285, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--7e6c2a9d-9dc1-4eb0-b27c-91e8076a9d77', '[{"url": "https://attack.mitre.org/software/S0269", "external_id": "S0269", "source_name": "mitre-attack"}, {"description": "(Citation: Unit 42 QUADAGENT July 2018)", "source_name": "QUADAGENT"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/07/unit42-oilrig-targets-technology-service-provider-government-agency-quadagent/", "description": "Lee, B., Falcone, R. (2018, July 25). OilRig Targets Technology Service Provider and Government Agency with QUADAGENT. Retrieved August 9, 2018.", "source_name": "Unit 42 QUADAGENT July 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"QUADAGENT\"\n    ]", "Str": "", "Type": 5, "Index": 1343}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1443}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1395}}', 'S0269', '[QUADAGENT](https://attack.mitre.org/software/S0269) is a PowerShell backdoor used by [OilRig](https://attack.mitre.org/groups/G0049). (Citation: Unit 42 QUADAGENT July 2018)', 'QUADAGENT');
INSERT INTO public.attack_softwares VALUES (286, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8c553311-0baa-4146-997a-f79acef3d831', '[{"url": "https://attack.mitre.org/software/S0055", "external_id": "S0055", "source_name": "mitre-attack"}, {"url": "http://blog.trendmicro.com/trendlabs-security-intelligence/rarstone-found-in-targeted-attacks/", "description": "Aquino, M. (2013, June 13). RARSTONE Found In Targeted Attacks. Retrieved December 17, 2015.", "source_name": "Aquino RARSTONE"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"RARSTONE\"\n    ]", "Str": "", "Type": 5, "Index": 1197}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1296}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1248}}', 'S0055', '[RARSTONE](https://attack.mitre.org/software/S0055) is malware used by the [Naikon](https://attack.mitre.org/groups/G0019) group that has some characteristics similar to [PlugX](https://attack.mitre.org/software/S0013). (Citation: Aquino RARSTONE)', 'RARSTONE');
INSERT INTO public.attack_softwares VALUES (287, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--9b325b06-35a1-457d-be46-a4ecc0b7ff0c', '[{"url": "https://attack.mitre.org/software/S0241", "external_id": "S0241", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.com/en_us/research/17/b/ratankba-watering-holes-against-enterprises.html", "description": "Trend Micro. (2017, February 27). RATANKBA: Delving into Large-scale Watering Holes against Enterprises. Retrieved May 22, 2018.", "source_name": "RATANKBA"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/lazarus-campaign-targeting-cryptocurrencies-reveals-remote-controller-tool-evolved-ratankba/", "description": "Lei, C., et al. (2018, January 24). Lazarus Campaign Targeting Cryptocurrencies Reveals Remote Controller Tool, an Evolved RATANKBA, and More. Retrieved May 22, 2018.", "source_name": "Lazarus RATANKBA"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"RATANKBA\"\n    ]", "Str": "", "Type": 5, "Index": 2079}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2178}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2130}}', 'S0241', '[RATANKBA](https://attack.mitre.org/software/S0241) is a remote controller tool used by [Lazarus Group](https://attack.mitre.org/groups/G0032). [RATANKBA](https://attack.mitre.org/software/S0241) has been used in attacks targeting financial institutions in Poland, Mexico, Uruguay, the United Kingdom, and Chile. It was also seen used against organizations related to telecommunications, management consulting, information technology, insurance, aviation, and education. [RATANKBA](https://attack.mitre.org/software/S0241) has a graphical user interface to allow the attacker to issue jobs to perform on the infected machines. (Citation: Lazarus RATANKBA) (Citation: RATANKBA)', 'RATANKBA');
INSERT INTO public.attack_softwares VALUES (288, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4b346d12-7f91-48d2-8f06-b26ffa0d825b', '[{"url": "https://attack.mitre.org/software/S0495", "external_id": "S0495", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/oilrig-novel-c2-channel-steganography/", "description": "Falcone, R. (2020, July 22). OilRig Targets Middle Eastern Telecommunications Organization and Adds Novel C2 Channel with Steganography to Its Inventory. Retrieved July 28, 2020.", "source_name": "Unit42 RDAT July 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"RDAT\",\n      \"RDAT \"\n    ]", "Str": "", "Type": 5, "Index": 1343}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1453}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1405}}', 'S0495', '[RDAT](https://attack.mitre.org/software/S0495) is a backdoor used by the suspected Iranian threat group [OilRig](https://attack.mitre.org/groups/G0049). [RDAT](https://attack.mitre.org/software/S0495) was originally identified in 2017 and targeted companies in the telecommunications sector.(Citation: Unit42 RDAT July 2020)', 'RDAT');
INSERT INTO public.attack_softwares VALUES (289, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--065196de-d7e8-4888-acfb-b2134022ba1b', '[{"url": "https://attack.mitre.org/software/S0416", "external_id": "S0416", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2019/10/mahalo-fin7-responding-to-new-tools-and-techniques.html", "description": "Carr, N, et all. (2019, October 10). Mahalo FIN7: Responding to the Criminal Operators’ New Tools and Techniques. Retrieved October 11, 2019.", "source_name": "FireEye FIN7 Oct 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"RDFSNIFFER\"\n    ]", "Str": "", "Type": 5, "Index": 1370}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1471}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1423}}', 'S0416', '[RDFSNIFFER](https://attack.mitre.org/software/S0416) is a module loaded by [BOOSTWRITE](https://attack.mitre.org/software/S0415) which allows an attacker to monitor and tamper with legitimate connections made via an application designed to provide visibility and system management capabilities to remote IT techs.(Citation: FireEye FIN7 Oct 2019)', 'RDFSNIFFER');
