INSERT INTO public.attack_softwares VALUES (323, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--414dc555-c79e-4b24-a2da-9b607f7eaf16', '[{"url": "https://attack.mitre.org/software/S0218", "external_id": "S0218", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT37 Feb 2018)", "source_name": "SLOWDRIFT"}, {"url": "https://www2.fireeye.com/rs/848-DID-242/images/rpt_APT37.pdf", "description": "FireEye. (2018, February 20). APT37 (Reaper): The Overlooked North Korean Actor. Retrieved March 1, 2018.", "source_name": "FireEye APT37 Feb 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SLOWDRIFT\"\n    ]", "Str": "", "Type": 5, "Index": 1261}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1361}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1313}}', 'S0218', '[SLOWDRIFT](https://attack.mitre.org/software/S0218) is a backdoor used by [APT37](https://attack.mitre.org/groups/G0067) against academic and strategic victims in South Korea. (Citation: FireEye APT37 Feb 2018)', 'SLOWDRIFT');
INSERT INTO public.attack_softwares VALUES (306, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--69d6f4a9-fcf0-4f51-bca7-597c51ad0bb8', '[{"url": "https://attack.mitre.org/software/S0125", "external_id": "S0125", "source_name": "mitre-attack"}, {"description": "ProjectSauron is used to refer both to the threat group also known as G0041 as well as the malware platform also known as S0125. (Citation: Kaspersky ProjectSauron Blog)", "source_name": "ProjectSauron"}, {"url": "http://www.symantec.com/connect/blogs/strider-cyberespionage-group-turns-eye-sauron-targets", "description": "Symantec Security Response. (2016, August 7). Strider: Cyberespionage group turns eye of Sauron on targets. Retrieved August 17, 2016.", "source_name": "Symantec Strider Blog"}, {"url": "https://securelist.com/faq-the-projectsauron-apt/75533/", "description": "Kaspersky Lab''s Global Research & Analysis Team. (2016, August 8). ProjectSauron: top level cyber-espionage platform covertly extracts encrypted government comms. Retrieved August 17, 2016.", "source_name": "Kaspersky ProjectSauron Blog"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Remsec\",\n      \"Backdoor.Remsec\",\n      \"ProjectSauron\"\n    ]", "Str": "", "Type": 5, "Index": 1890}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2035}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1987}}', 'S0125', '[Remsec](https://attack.mitre.org/software/S0125) is a modular backdoor that has been used by [Strider](https://attack.mitre.org/groups/G0041) and appears to have been designed primarily for espionage purposes. Many of its modules are written in Lua. (Citation: Symantec Strider Blog)', 'Remsec');
INSERT INTO public.attack_softwares VALUES (307, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--bdb27a1d-1844-42f1-a0c0-826027ae0326', '[{"url": "https://attack.mitre.org/software/S0379", "external_id": "S0379", "source_name": "mitre-attack"}, {"url": "https://www.cylance.com/content/dam/cylance-web/en-us/resources/knowledge-center/resource-library/reports/WhiteCompanyOperationShaheenReport.pdf?_ga=2.161661948.1943296560.1555683782-1066572390.1555511517", "description": "Livelli, K, et al. (2018, November 12). Operation Shaheen. Retrieved May 1, 2019.", "source_name": "Cylance Shaheen Nov 2018"}, {"url": "https://cofense.com/upgrades-delivery-support-infrastructure-revenge-rat-malware-bigger-threat/", "description": "Gannon, M. (2019, February 11). With Upgrades in Delivery and Support Infrastructure, Revenge RAT Malware is a Bigger Threat. Retrieved May 1, 2019.", "source_name": "Cofense RevengeRAT Feb 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Revenge RAT\"\n    ]", "Str": "", "Type": 5, "Index": 1612}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1714}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1666}}', 'S0379', '[Revenge RAT](https://attack.mitre.org/software/S0379) is a freely available remote access tool written in .NET (C#).(Citation: Cylance Shaheen Nov 2018)(Citation: Cofense RevengeRAT Feb 2019)', 'Revenge RAT');
INSERT INTO public.attack_softwares VALUES (308, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--44c75271-0e4d-496f-ae0a-a6d883a42a65', '[{"url": "https://attack.mitre.org/software/S0433", "external_id": "S0433", "source_name": "mitre-attack"}, {"url": "https://www.carbonblack.com/2020/04/16/vmware-carbon-black-tau-threat-analysis-the-evolution-of-lazarus/", "description": "Knight, S.. (2020, April 16). VMware Carbon Black TAU Threat Analysis: The Evolution of Lazarus. Retrieved May 1, 2020.", "source_name": "Carbon Black HotCroissant April 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Rifdoor\"\n    ]", "Str": "", "Type": 5, "Index": 1238}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1336}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1288}}', 'S0433', '[Rifdoor](https://attack.mitre.org/software/S0433) is a remote access trojan (RAT) that shares numerous code similarities with [HotCroissant](https://attack.mitre.org/software/S0431).(Citation: Carbon Black HotCroissant April 2020)', 'Rifdoor');
INSERT INTO public.attack_softwares VALUES (309, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--56e6b6c2-e573-4969-8bab-783205cebbbf', '[{"url": "https://attack.mitre.org/software/S0448", "external_id": "S0448", "source_name": "mitre-attack"}, {"url": "https://www.mcafee.com/enterprise/en-us/assets/reports/rp-operation-sharpshooter.pdf", "description": "Sherstobitoff, R., Malhotra, A., et. al.. (2018, December 18). Operation Sharpshooter Campaign Targets Global Defense, Critical Infrastructure. Retrieved May 14, 2020.", "source_name": "McAfee Sharpshooter December 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Rising Sun\"\n    ]", "Str": "", "Type": 5, "Index": 1513}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1614}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1566}}', 'S0448', '[Rising Sun](https://attack.mitre.org/software/S0448) is a modular backdoor malware used extensively in Operation [Sharpshooter](https://attack.mitre.org/groups/G0104). The malware has been observed targeting nuclear, defense, energy, and financial services companies across the world. [Rising Sun](https://attack.mitre.org/software/S0448) uses source code from [Lazarus Group](https://attack.mitre.org/groups/G0032)''s Trojan Duuzer.(Citation: McAfee Sharpshooter December 2018)', 'Rising Sun');
