INSERT INTO public.attack_softwares VALUES (312, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--6b616fc1-1505-48e3-8b2c-0d19337bff38', '[{"url": "https://attack.mitre.org/software/S0090", "external_id": "S0090", "source_name": "mitre-attack"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/02/new-malware-rover-targets-indian-ambassador-to-afghanistan/", "description": "Ray, V., Hayashi, K. (2016, February 29). New Malware ‘Rover’ Targets Indian Ambassador to Afghanistan. Retrieved February 29, 2016.", "source_name": "Palo Alto Rover"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Rover\"\n    ]", "Str": "", "Type": 5, "Index": 1227}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1323}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1275}}', 'S0090', '[Rover](https://attack.mitre.org/software/S0090) is malware suspected of being used for espionage purposes. It was used in 2015 in a targeted email sent to an Indian Ambassador to Afghanistan. (Citation: Palo Alto Rover)', 'Rover');
