INSERT INTO public.attack_softwares VALUES (330, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b2b0b946-be0a-4a7f-9c32-a2e5211d1cd9', '[{"url": "https://attack.mitre.org/software/S0578", "external_id": "S0578", "source_name": "mitre-attack"}, {"url": "https://www.guidepointsecurity.com/supernova-solarwinds-net-webshell-analysis/", "description": "Riley, W. (2020, December 1). SUPERNOVA SolarWinds .NET Webshell Analysis. Retrieved February 18, 2021.", "source_name": "Guidepoint SUPERNOVA Dec 2020"}, {"url": "https://unit42.paloaltonetworks.com/solarstorm-supernova/", "description": "Tennis, M. (2020, December 17). SUPERNOVA: A Novel .NET Webshell. Retrieved February 22, 2021.", "source_name": "Unit42 SUPERNOVA Dec 2020"}, {"url": "https://www.solarwinds.com/sa-overview/securityadvisory", "description": "SolarWinds. (2020, December 24). SolarWinds Security Advisory. Retrieved February 22, 2021.", "source_name": "SolarWinds Advisory Dec 2020"}, {"url": "https://us-cert.cisa.gov/ncas/analysis-reports/ar21-027a", "description": "CISA. (2021, January 27). Malware Analysis Report (AR21-027A). Retrieved February 22, 2021.", "source_name": "CISA Supernova Jan 2021"}, {"url": "https://www.microsoft.com/security/blog/2020/12/18/analyzing-solorigate-the-compromised-dll-file-that-started-a-sophisticated-cyberattack-and-how-microsoft-defender-helps-protect/", "description": "MSTIC. (2020, December 18). Analyzing Solorigate, the compromised DLL file that started a sophisticated cyberattack, and how Microsoft Defender helps protect customers . Retrieved January 5, 2021.", "source_name": "Microsoft Analyzing Solorigate Dec 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SUPERNOVA\"\n    ]", "Str": "", "Type": 5, "Index": 2864}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2964}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2916}}', 'S0578', '[SUPERNOVA](https://attack.mitre.org/software/S0578) is an in-memory web shell written in .NET C#. It was discovered in November 2020 during the investigation of [APT29](https://attack.mitre.org/groups/G0016)''s SolarWinds cyber operation but determined to be unrelated. Subsequent analysis suggests [SUPERNOVA](https://attack.mitre.org/software/S0578) may have been used by the China-based threat group SPIRAL.(Citation: Guidepoint SUPERNOVA Dec 2020)(Citation: Unit42 SUPERNOVA Dec 2020)(Citation: SolarWinds Advisory Dec 2020)(Citation: CISA Supernova Jan 2021)(Citation: Microsoft Analyzing Solorigate Dec 2020)', 'SUPERNOVA');
INSERT INTO public.attack_softwares VALUES (331, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--84c1ecc6-e5a2-4e8a-bf4b-651a618e0053', '[{"url": "https://attack.mitre.org/software/S0519", "external_id": "S0519", "source_name": "mitre-attack"}, {"url": "https://www.fireeye.com/blog/threat-research/2015/09/synful_knock_-_acis.html", "description": "Bill Hau, Tony Lee, Josh Homan. (2015, September 15). SYNful Knock - A Cisco router implant - Part I. Retrieved October 19, 2020.", "source_name": "FireEye - Synful Knock"}, {"url": "https://blogs.cisco.com/security/evolution-of-attacks-on-cisco-ios-devices", "description": "Graham Holmes. (2015, October 8). Evolution of attacks on Cisco IOS devices. Retrieved October 19, 2020.", "source_name": "Cisco Synful Knock Evolution"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SYNful Knock\"\n    ]", "Str": "", "Type": 5, "Index": 1589}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1692}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Network\"\n    ]", "Str": "", "Type": 5, "Index": 1644}}', 'S0519', '[SYNful Knock](https://attack.mitre.org/software/S0519) is a stealthy modification of the operating system of network devices that can be used to maintain persistence within a victim''s network and provide new capabilities to the adversary.(Citation: FireEye - Synful Knock)(Citation: Cisco Synful Knock Evolution)', 'SYNful Knock');
INSERT INTO public.attack_softwares VALUES (332, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--edf5aee2-9b1c-4252-8e64-25b12f14c8b3', '[{"url": "https://attack.mitre.org/software/S0464", "external_id": "S0464", "source_name": "mitre-attack"}, {"url": "https://unit42.paloaltonetworks.com/unit42-the-fractured-block-campaign-carrotbat-malware-used-to-deliver-malware-targeting-southeast-asia/", "description": "Grunzweig, J. and Wilhoit, K. (2018, November 29). The Fractured Block Campaign: CARROTBAT Used to Deliver Malware Targeting Southeast Asia. Retrieved June 2, 2020.", "source_name": "Unit 42 CARROTBAT November 2018"}, {"url": "https://unit42.paloaltonetworks.com/the-fractured-statue-campaign-u-s-government-targeted-in-spear-phishing-attacks/", "description": "McCabe, A. (2020, January 23). The Fractured Statue Campaign: U.S. Government Agency Targeted in Spear-Phishing Attacks. Retrieved June 2, 2020.", "source_name": "Unit 42 CARROTBAT January 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SYSCON\"\n    ]", "Str": "", "Type": 5, "Index": 1916}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2013}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1965}}', 'S0464', '[SYSCON](https://attack.mitre.org/software/S0464) is a backdoor that has been in use since at least 2017 and has been associated with campaigns involving North Korean themes. [SYSCON](https://attack.mitre.org/software/S0464) has been delivered by the [CARROTBALL](https://attack.mitre.org/software/S0465) and [CARROTBAT](https://attack.mitre.org/software/S0462) droppers.(Citation: Unit 42 CARROTBAT November 2018)(Citation: Unit 42 CARROTBAT January 2020)', 'SYSCON');
INSERT INTO public.attack_softwares VALUES (333, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--96b08451-b27a-4ff6-893f-790e26393a8e', '[{"url": "https://attack.mitre.org/software/S0074", "external_id": "S0074", "source_name": "mitre-attack"}, {"url": "http://www.secureworks.com/cyber-threat-intelligence/threats/sakula-malware-family/", "description": "Dell SecureWorks Counter Threat Unit Threat Intelligence. (2015, July 30). Sakula Malware Family. Retrieved January 26, 2016.", "source_name": "Dell Sakula"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Sakula\",\n      \"Sakurel\",\n      \"VIPER\"\n    ]", "Str": "", "Type": 5, "Index": 1141}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1270}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1222}}', 'S0074', '[Sakula](https://attack.mitre.org/software/S0074) is a remote access tool (RAT) that first surfaced in 2012 and was used in intrusions throughout 2015. (Citation: Dell Sakula)', 'Sakula');
INSERT INTO public.attack_softwares VALUES (334, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4d56e6e9-1a6d-46e3-896c-dfdf3cc96e62', '[{"url": "https://attack.mitre.org/software/S0370", "external_id": "S0370", "source_name": "mitre-attack"}, {"description": "(Citation: US-CERT SamSam 2018)", "source_name": "Samas"}, {"url": "https://www.us-cert.gov/ncas/alerts/AA18-337A", "description": "US-CERT. (2018, December 3). Alert (AA18-337A): SamSam Ransomware. Retrieved March 15, 2019.", "source_name": "US-CERT SamSam 2018"}, {"url": "https://blog.talosintelligence.com/2018/01/samsam-evolution-continues-netting-over.html", "description": "Ventura, V. (2018, January 22). SamSam - The Evolution Continues Netting Over $325,000 in 4 Weeks. Retrieved April 16, 2019.", "source_name": "Talos SamSam Jan 2018"}, {"url": "https://www.sophos.com/en-us/medialibrary/PDFs/technical-papers/SamSam-ransomware-chooses-Its-targets-carefully-wpna.pdf", "description": " Palotay, D. and Mackenzie, P. (2018, April). SamSam Ransomware Chooses Its Targets Carefully. Retrieved April 15, 2019.", "source_name": "Sophos SamSam Apr 2018"}, {"url": "https://www.symantec.com/blogs/threat-intelligence/samsam-targeted-ransomware-attacks", "description": "Symantec Security Response Attack Investigation Team. (2018, October 30). SamSam: Targeted Ransomware Attacks Continue. Retrieved April 16, 2019.", "source_name": "Symantec SamSam Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SamSam\",\n      \"Samas\"\n    ]", "Str": "", "Type": 5, "Index": 2389}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2501}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2453}}', 'S0370', '[SamSam](https://attack.mitre.org/software/S0370) is ransomware that appeared in early 2016. Unlike some ransomware, its variants have required operators to manually interact with the malware to execute some of its core components.(Citation: US-CERT SamSam 2018)(Citation: Talos SamSam Jan 2018)(Citation: Sophos SamSam Apr 2018)(Citation: Symantec SamSam Oct 2018)', 'SamSam');
INSERT INTO public.attack_softwares VALUES (335, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--67e6d66b-1b82-4699-b47a-e2efb6268d14', '[{"url": "https://attack.mitre.org/software/S0053", "external_id": "S0053", "source_name": "mitre-attack"}, {"url": "https://www.f-secure.com/documents/996508/1030745/dukes_whitepaper.pdf", "description": "F-Secure Labs. (2015, September 17). The Dukes: 7 years of Russian cyberespionage. Retrieved December 10, 2015.", "source_name": "F-Secure The Dukes"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SeaDuke\",\n      \"SeaDaddy\",\n      \"SeaDesk\"\n    ]", "Str": "", "Type": 5, "Index": 1267}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1400}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1352}}', 'S0053', '[SeaDuke](https://attack.mitre.org/software/S0053) is malware that was used by [APT29](https://attack.mitre.org/groups/G0016) from 2014 to 2015. It was used primarily as a secondary backdoor for victims that were already compromised with [CozyCar](https://attack.mitre.org/software/S0046). (Citation: F-Secure The Dukes)', 'SeaDuke');
INSERT INTO public.attack_softwares VALUES (336, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--b45747dc-87ca-4597-a245-7e16a61bc491', '[{"url": "https://attack.mitre.org/software/S0345", "external_id": "S0345", "source_name": "mitre-attack"}, {"description": "(Citation: Mandiant APT1 Appendix)(Citation: McAfee Oceansalt Oct 2018)", "source_name": "Seasalt"}, {"url": "https://www.fireeye.com/content/dam/fireeye-www/services/pdfs/mandiant-apt1-report-appendix.zip", "description": "Mandiant. (n.d.). Appendix C (Digital) - The Malware Arsenal. Retrieved July 18, 2016.", "source_name": "Mandiant APT1 Appendix"}, {"url": "https://www.mcafee.com/enterprise/en-us/assets/reports/rp-operation-oceansalt.pdf", "description": "Sherstobitoff, R., Malhotra, A. (2018, October 18). ‘Operation Oceansalt’ Attacks South Korea, U.S., and Canada With Source Code From Chinese Hacker Group. Retrieved November 30, 2018.", "source_name": "McAfee Oceansalt Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Seasalt\"\n    ]", "Str": "", "Type": 5, "Index": 1795}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1893}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1845}}', 'S0345', '[Seasalt](https://attack.mitre.org/software/S0345) is malware that has been linked to [APT1](https://attack.mitre.org/groups/G0006)''s 2010 operations. It shares some code similarities with [OceanSalt](https://attack.mitre.org/software/S0346).(Citation: Mandiant APT1 Appendix)(Citation: McAfee Oceansalt Oct 2018)', 'Seasalt');
INSERT INTO public.attack_softwares VALUES (337, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--aae22730-e571-4d17-b037-65f2a3e26213', '[{"url": "https://attack.mitre.org/software/S0382", "external_id": "S0382", "source_name": "mitre-attack"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/servhelper-and-flawedgrace-new-malware-introduced-ta505", "description": "Schwarz, D. and Proofpoint Staff. (2019, January 9). ServHelper and FlawedGrace - New malware introduced by TA505. Retrieved May 28, 2019.", "source_name": "Proofpoint TA505 Jan 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ServHelper\"\n    ]", "Str": "", "Type": 5, "Index": 1228}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1329}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1281}}', 'S0382', '[ServHelper](https://attack.mitre.org/software/S0382) is a backdoor first observed in late 2018. The backdoor is written in Delphi and is typically delivered as a DLL file.(Citation: Proofpoint TA505 Jan 2019)', 'ServHelper');
INSERT INTO public.attack_softwares VALUES (338, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--ec9e00dd-0313-4d5b-8105-c20aa47abffc', '[{"url": "https://attack.mitre.org/software/S0596", "external_id": "S0596", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye APT41 Aug 2019)", "source_name": "POISONPLUG.SHADOW"}, {"url": "https://go.recordedfuture.com/hubfs/reports/cta-2021-0228.pdf", "description": "Insikt Group. (2021, February 28). China-Linked Group RedEcho Targets the Indian Power Sector Amid Heightened Border Tensions. Retrieved March 22, 2021.", "source_name": "Recorded Future RedEcho Feb 2021"}, {"url": "https://securelist.com/shadowpad-in-corporate-networks/81432/", "description": "GReAT. (2017, August 15). ShadowPad in corporate networks. Retrieved March 22, 2021.", "source_name": "Securelist ShadowPad Aug 2017"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2017/08/07172148/ShadowPad_technical_description_PDF.pdf", "description": "Kaspersky Lab. (2017, August). ShadowPad: popular server management software hit in supply chain attack. Retrieved March 22, 2021.", "source_name": "Kaspersky ShadowPad Aug 2017"}, {"url": "https://content.fireeye.com/apt-41/rpt-apt41", "description": "Fraser, N., et al. (2019, August 7). Double DragonAPT41, a dual espionage and cyber crime operation APT41. Retrieved September 23, 2019.", "source_name": "FireEye APT41 Aug 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ShadowPad\",\n      \"POISONPLUG.SHADOW\"\n    ]", "Str": "", "Type": 5, "Index": 2530}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2657}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2609}}', 'S0596', '[ShadowPad](https://attack.mitre.org/software/S0596) is a modular backdoor that was first identified in a supply chain compromise of the NetSarang software in mid-July 2017. The malware was originally thought to be exclusively used by [APT41](https://attack.mitre.org/groups/G0096), but has since been observed to be used by various Chinese threat activity groups. (Citation: Recorded Future RedEcho Feb 2021)(Citation: Securelist ShadowPad Aug 2017)(Citation: Kaspersky ShadowPad Aug 2017) ', 'ShadowPad');
INSERT INTO public.attack_softwares VALUES (339, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8901ac23-6b50-410c-b0dd-d8174a86f9b3', '[{"url": "https://attack.mitre.org/software/S0140", "external_id": "S0140", "source_name": "mitre-attack"}, {"description": "(Citation: Palo Alto Shamoon Nov 2016)", "source_name": "Disttrack"}, {"url": "http://researchcenter.paloaltonetworks.com/2016/11/unit42-shamoon-2-return-disttrack-wiper/", "description": "Falcone, R.. (2016, November 30). Shamoon 2: Return of the Disttrack Wiper. Retrieved January 11, 2017.", "source_name": "Palo Alto Shamoon Nov 2016"}, {"url": "https://unit42.paloaltonetworks.com/shamoon-3-targets-oil-gas-organization/", "description": "Falcone, R. (2018, December 13). Shamoon 3 Targets Oil and Gas Organization. Retrieved March 14, 2019.", "source_name": "Unit 42 Shamoon3 2018"}, {"url": "https://www.symantec.com/connect/blogs/shamoon-attacks", "description": "Symantec. (2012, August 16). The Shamoon Attacks. Retrieved March 14, 2019.", "source_name": "Symantec Shamoon 2012"}, {"url": "https://www.fireeye.com/blog/threat-research/2016/11/fireeye_respondsto.html", "description": "FireEye. (2016, November 30). FireEye Responds to Wave of Destructive Cyber Attacks in Gulf Region. Retrieved January 11, 2017.", "source_name": "FireEye Shamoon Nov 2016"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Shamoon\",\n      \"Disttrack\"\n    ]", "Str": "", "Type": 5, "Index": 2588}, "x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 2705}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2657}}', 'S0140', '[Shamoon](https://attack.mitre.org/software/S0140) is wiper malware that was first used by an Iranian group known as the "Cutting Sword of Justice" in 2012. Other versions known as Shamoon 2 and Shamoon 3 were observed in 2016 and 2018. [Shamoon](https://attack.mitre.org/software/S0140) has also been seen leveraging [RawDisk](https://attack.mitre.org/software/S0364) and Filerase to carry out data wiping tasks. The term Shamoon is sometimes used to refer to the group using the malware as well as the malware itself.(Citation: Palo Alto Shamoon Nov 2016)(Citation: Unit 42 Shamoon3 2018)(Citation: Symantec Shamoon 2012)(Citation: FireEye Shamoon Nov 2016)', 'Shamoon');
INSERT INTO public.attack_softwares VALUES (340, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--0ba9281c-93fa-4b29-8e9e-7ef918c7b13a', '[{"url": "https://attack.mitre.org/software/S0546", "external_id": "S0546", "source_name": "mitre-attack"}, {"description": "(Citation: Cybereason Molerats Dec 2020)(Citation: BleepingComputer Molerats Dec 2020)", "source_name": "SharpStage"}, {"url": "https://www.cybereason.com/hubfs/dam/collateral/reports/Molerats-in-the-Cloud-New-Malware-Arsenal-Abuses-Cloud-Platforms-in-Middle-East-Espionage-Campaign.pdf", "description": "Cybereason Nocturnus Team. (2020, December 9). MOLERATS IN THE CLOUD: New Malware Arsenal Abuses Cloud Platforms in Middle East Espionage Campaign. Retrieved December 22, 2020.", "source_name": "Cybereason Molerats Dec 2020"}, {"url": "https://www.bleepingcomputer.com/news/security/hacking-group-s-new-malware-abuses-google-and-facebook-services/", "description": "Ilascu, I. (2020, December 14). Hacking group’s new malware abuses Google and Facebook services. Retrieved December 28, 2020.", "source_name": "BleepingComputer Molerats Dec 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"SharpStage\"\n    ]", "Str": "", "Type": 5, "Index": 1825}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1926}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1878}}', 'S0546', '[SharpStage](https://attack.mitre.org/software/S0546) is a .NET malware with backdoor capabilities.(Citation: Cybereason Molerats Dec 2020)(Citation: BleepingComputer Molerats Dec 2020)', 'SharpStage');
INSERT INTO public.attack_softwares VALUES (341, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--5763217a-05b6-4edd-9bca-057e47b5e403', '[{"url": "https://attack.mitre.org/software/S0444", "external_id": "S0444", "source_name": "mitre-attack"}, {"url": "https://foxitsecurity.files.wordpress.com/2016/06/fox-it_mofang_threatreport_tlp-white.pdf", "description": "Yonathan Klijnsma. (2016, May 17). Mofang: A politically motivated information stealing adversary. Retrieved May 12, 2020.", "source_name": "FOX-IT May 2016 Mofang"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ShimRat\"\n    ]", "Str": "", "Type": 5, "Index": 1476}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1574}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1526}}', 'S0444', '[ShimRat](https://attack.mitre.org/software/S0444) has been used by the suspected China-based adversary [Mofang](https://attack.mitre.org/groups/G0103) in campaigns targeting multiple countries and sectors including government, military, critical infrastructure, automobile, and weapons development. The name "[ShimRat](https://attack.mitre.org/software/S0444)" comes from the malware''s extensive use of Windows Application Shimming to maintain persistence. (Citation: FOX-IT May 2016 Mofang)', 'ShimRat');
INSERT INTO public.attack_softwares VALUES (342, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--979adb5a-dc30-48f0-9e3d-9a26d866928c', '[{"url": "https://attack.mitre.org/software/S0589", "external_id": "S0589", "source_name": "mitre-attack"}, {"url": "https://www.microsoft.com/security/blog/2021/03/04/goldmax-goldfinder-sibot-analyzing-nobelium-malware/", "description": "Nafisi, R., Lelli, A. (2021, March 4). GoldMax, GoldFinder, and Sibot: Analyzing NOBELIUM’s layered persistence. Retrieved March 8, 2021.", "source_name": "MSTIC NOBELIUM Mar 2021"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Sibot\"\n    ]", "Str": "", "Type": 5, "Index": 1466}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1562}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1514}}', 'S0589', '[Sibot](https://attack.mitre.org/software/S0589) is dual-purpose malware written in VBScript designed to achieve persistence on a compromised system as well as download and execute additional payloads. Microsoft discovered three [Sibot](https://attack.mitre.org/software/S0589) variants in early 2021 during its investigation of [APT29](https://attack.mitre.org/groups/G0016) and the SolarWinds cyber intrusion campaign.(Citation: MSTIC NOBELIUM Mar 2021)', 'Sibot');
