INSERT INTO public.attack_softwares VALUES (362, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4f6aa78c-c3d4-4883-9840-96ca2f5d6d47', '[{"url": "https://attack.mitre.org/software/S0146", "external_id": "S0146", "source_name": "mitre-attack"}, {"description": "(Citation: FireEye FIN7 March 2017)", "source_name": "TEXTMATE"}, {"description": "Based on similar descriptions of functionality, it appears S0146, as named by FireEye, is the same as Stage 4 of a backdoor named DNSMessenger by Cisco''s Talos Intelligence Group. However, FireEye appears to break DNSMessenger into two parts: S0145 and S0146. (Citation: Cisco DNSMessenger March 2017) (Citation: FireEye FIN7 March 2017)", "source_name": "DNSMessenger"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/03/fin7_spear_phishing.html", "description": "Miller, S., et al. (2017, March 7). FIN7 Spear Phishing Campaign Targets Personnel Involved in SEC Filings. Retrieved March 8, 2017.", "source_name": "FireEye FIN7 March 2017"}, {"url": "http://blog.talosintelligence.com/2017/03/dnsmessenger.html", "description": "Brumaghin, E. and Grady, C.. (2017, March 2). Covert Channels and Poor Decisions: The Tale of DNSMessenger. Retrieved March 8, 2017.", "source_name": "Cisco DNSMessenger March 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"TEXTMATE\",\n      \"DNSMessenger\"\n    ]", "Str": "", "Type": 5, "Index": 2083}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2204}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2156}}', 'S0146', '[TEXTMATE](https://attack.mitre.org/software/S0146) is a second-stage PowerShell backdoor that is memory-resident. It was observed being used along with [POWERSOURCE](https://attack.mitre.org/software/S0145) in February 2017. (Citation: FireEye FIN7 March 2017)', 'TEXTMATE');
INSERT INTO public.attack_softwares VALUES (363, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--85b39628-204a-48d2-b377-ec368cbcb7ca', '[{"url": "https://attack.mitre.org/software/S0131", "external_id": "S0131", "source_name": "mitre-attack"}, {"url": "https://www.forcepoint.com/sites/default/files/resources/files/forcepoint-security-labs-monsoon-analysis-report.pdf", "description": "Settle, A., et al. (2016, August 8). MONSOON - Analysis Of An APT Campaign. Retrieved September 22, 2016.", "source_name": "Forcepoint Monsoon"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"TINYTYPHON\"\n    ]", "Str": "", "Type": 5, "Index": 1227}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1328}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1280}}', 'S0131', '[TINYTYPHON](https://attack.mitre.org/software/S0131) is a backdoor  that has been used by the actors responsible for the MONSOON campaign. The majority of its code was reportedly taken from the MyDoom worm. (Citation: Forcepoint Monsoon)', 'TINYTYPHON');
