INSERT INTO public.attack_softwares VALUES (377, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--af2ad3b7-ab6a-4807-91fd-51bcaff9acbb', '[{"url": "https://attack.mitre.org/software/S0136", "external_id": "S0136", "source_name": "mitre-attack"}, {"url": "http://www.welivesecurity.com/2014/11/11/sednit-espionage-group-attacking-air-gapped-networks/", "description": "Calvet, J. (2014, November 11). Sednit Espionage Group Attacking Air-Gapped Networks. Retrieved January 4, 2017.", "source_name": "ESET Sednit USBStealer 2014"}, {"url": "https://securelist.com/sofacy-apt-hits-high-profile-targets-with-updated-toolset/72924/", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2015, December 4). Sofacy APT hits high profile targets with updated toolset. Retrieved December 10, 2015.", "source_name": "Kaspersky Sofacy"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"USBStealer\",\n      \"USB Stealer\",\n      \"Win32/USBStealer\"\n    ]", "Str": "", "Type": 5, "Index": 1761}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1909}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1861}}', 'S0136', '[USBStealer](https://attack.mitre.org/software/S0136) is malware that has used by [APT28](https://attack.mitre.org/groups/G0007) since at least 2005 to extract information from air-gapped networks. It does not have the capability to communicate over the Internet and has been used in conjunction with [ADVSTORESHELL](https://attack.mitre.org/software/S0045). (Citation: ESET Sednit USBStealer 2014) (Citation: Kaspersky Sofacy)', 'USBStealer');
INSERT INTO public.attack_softwares VALUES (378, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--75bba379-4ba1-467e-8c60-ec2b269ee984', '[{"url": "https://attack.mitre.org/software/S0452", "external_id": "S0452", "source_name": "mitre-attack"}, {"url": "https://documents.trendmicro.com/assets/Tech-Brief-Tropic-Trooper-s-Back-USBferry-Attack-Targets-Air-gapped-Environments.pdf", "description": "Chen, J.. (2020, May 12). Tropic Trooper’s Back: USBferry Attack Targets Air gapped Environments. Retrieved May 20, 2020.", "source_name": "TrendMicro Tropic Trooper May 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"USBferry\"\n    ]", "Str": "", "Type": 5, "Index": 1541}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1640}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1592}}', 'S0452', '[USBferry](https://attack.mitre.org/software/S0452) is an information stealing malware and has been used by [Tropic Trooper](https://attack.mitre.org/groups/G0081) in targeted attacks against Taiwanese and Philippine air-gapped military environments. [USBferry](https://attack.mitre.org/software/S0452) shares an overlapping codebase with [YAHOYAH](https://attack.mitre.org/software/S0388), though it has several features which makes it a distinct piece of malware.(Citation: TrendMicro Tropic Trooper May 2020)', 'USBferry');
