INSERT INTO public.attack_softwares VALUES (382, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--1492d0f8-7e14-4af3-9239-bc3fe10d3407', '[{"url": "https://attack.mitre.org/software/S0386", "external_id": "S0386", "source_name": "mitre-attack"}, {"description": "(Citation: NJCCIC Ursnif Sept 2016)", "source_name": "Ursnif"}, {"description": "(Citation: FireEye Ursnif Nov 2017)(Citation: ProofPoint Ursnif Aug 2016)", "source_name": "Gozi-ISFB"}, {"description": "(Citation: TrendMicro Ursnif Mar 2015)", "source_name": "PE_URSNIF"}, {"description": "(Citation: NJCCIC Ursnif Sept 2016)(Citation: ProofPoint Ursnif Aug 2016)", "source_name": "Dreambot"}, {"url": "https://www.cyber.nj.gov/threat-profiles/trojan-variants/ursnif", "description": "NJCCIC. (2016, September 27). Ursnif. Retrieved June 4, 2019.", "source_name": "NJCCIC Ursnif Sept 2016"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/ursnif-variant-dreambot-adds-tor-functionality", "description": "Proofpoint Staff. (2016, August 25). Nightmare on Tor Street: Ursnif variant Dreambot adds Tor functionality. Retrieved June 5, 2019.", "source_name": "ProofPoint Ursnif Aug 2016"}, {"url": "https://blog.trendmicro.com/trendlabs-security-intelligence/ursnif-the-multifaceted-malware/?_ga=2.165628854.808042651.1508120821-744063452.1505819992", "description": "Caragay, R. (2015, March 26). URSNIF: The Multifaceted Malware. Retrieved June 5, 2019.", "source_name": "TrendMicro Ursnif Mar 2015"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/11/ursnif-variant-malicious-tls-callback-technique.html", "description": "Vaish, A. & Nemes, S. (2017, November 28). Newly Observed Ursnif Variant Employs Malicious TLS Callback Technique to Achieve Process Injection. Retrieved June 5, 2019.", "source_name": "FireEye Ursnif Nov 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Ursnif\",\n      \"Gozi-ISFB\",\n      \"PE_URSNIF\",\n      \"Dreambot\"\n    ]", "Str": "", "Type": 5, "Index": 3087}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 3240}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3192}}', 'S0386', '[Ursnif](https://attack.mitre.org/software/S0386) is a banking trojan and variant of the Gozi malware observed being spread through various automated exploit kits, [Spearphishing Attachment](https://attack.mitre.org/techniques/T1566/001)s, and malicious links.(Citation: NJCCIC Ursnif Sept 2016)(Citation: ProofPoint Ursnif Aug 2016) [Ursnif](https://attack.mitre.org/software/S0386) is associated primarily with data theft, but variants also include components (backdoors, spyware, file injectors, etc.) capable of a wide variety of behaviors.(Citation: TrendMicro Ursnif Mar 2015)', 'Ursnif');
