INSERT INTO public.attack_softwares VALUES (387, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--495b6cdb-7b5a-4fbc-8d33-e7ef68806d08', '[{"url": "https://attack.mitre.org/software/S0180", "external_id": "S0180", "source_name": "mitre-attack"}, {"description": "(Citation: US-CERT Volgmer Nov 2017) (Citation: US-CERT Volgmer 2 Nov 2017) (Citation: Symantec Volgmer Aug 2014)", "source_name": "Volgmer"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA17-318B", "description": "US-CERT. (2017, November 22). Alert (TA17-318B): HIDDEN COBRA – North Korean Trojan: Volgmer. Retrieved December 7, 2017.", "source_name": "US-CERT Volgmer Nov 2017"}, {"url": "https://www.us-cert.gov/sites/default/files/publications/MAR-10135536-D_WHITE_S508C.PDF", "description": "US-CERT. (2017, November 01). Malware Analysis Report (MAR) - 10135536-D. Retrieved July 16, 2018.", "source_name": "US-CERT Volgmer 2 Nov 2017"}, {"url": "https://www.symantec.com/security-center/writeup/2014-081811-3237-99?tabid=2", "description": "Yagi, J. (2014, August 24). Trojan.Volgmer. Retrieved July 16, 2018.", "source_name": "Symantec Volgmer Aug 2014"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Volgmer\"\n    ]", "Str": "", "Type": 5, "Index": 2028}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2126}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2078}}', 'S0180', '[Volgmer](https://attack.mitre.org/software/S0180) is a backdoor Trojan designed to provide covert access to a compromised system. It has been used since at least 2013 to target the government, financial, automotive, and media industries. Its primary delivery mechanism is suspected to be spearphishing. (Citation: US-CERT Volgmer Nov 2017)', 'Volgmer');
