INSERT INTO public.attack_softwares VALUES (391, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--75ecdbf1-c2bb-4afc-a3f9-c8da4de8c661', '[{"url": "https://attack.mitre.org/software/S0366", "external_id": "S0366", "source_name": "mitre-attack"}, {"description": "(Citation: SecureWorks WannaCry Analysis)", "source_name": "WanaCry"}, {"description": "(Citation: SecureWorks WannaCry Analysis)", "source_name": "WanaCrypt"}, {"description": "(Citation: LogRhythm WannaCry)", "source_name": "WanaCrypt0r"}, {"description": "(Citation: LogRhythm WannaCry)(Citation: SecureWorks WannaCry Analysis)", "source_name": "WCry"}, {"url": "https://logrhythm.com/blog/a-technical-analysis-of-wannacry-ransomware/", "description": "Noerenberg, E., Costis, A., and Quist, N. (2017, May 16). A Technical Analysis of WannaCry Ransomware. Retrieved March 25, 2019.", "source_name": "LogRhythm WannaCry"}, {"url": "https://www.us-cert.gov/ncas/alerts/TA17-132A", "description": "US-CERT. (2017, May 12). Alert (TA17-132A): Indicators Associated With WannaCry Ransomware. Retrieved March 25, 2019.", "source_name": "US-CERT WannaCry 2017"}, {"url": "https://www.washingtonpost.com/business/economy/more-than-150-countries-affected-by-massive-cyberattack-europol-says/2017/05/14/5091465e-3899-11e7-9e48-c4f199710b69_story.html?utm_term=.7fa16b41cad4", "description": "Dwoskin, E. and Adam, K. (2017, May 14). More than 150 countries affected by massive cyberattack, Europol says. Retrieved March 25, 2019.", "source_name": "Washington Post WannaCry 2017"}, {"url": "https://www.fireeye.com/blog/threat-research/2017/05/wannacry-malware-profile.html", "description": "Berry, A., Homan, J., and Eitzman, R. (2017, May 23). WannaCry Malware Profile. Retrieved March 15, 2019.", "source_name": "FireEye WannaCry 2017"}, {"url": "https://www.secureworks.com/research/wcry-ransomware-analysis", "description": "Counter Threat Unit Research Team. (2017, May 18). WCry Ransomware Analysis. Retrieved March 26, 2019.", "source_name": "SecureWorks WannaCry Analysis"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"WannaCry\",\n      \"WanaCry\",\n      \"WanaCrypt\",\n      \"WanaCrypt0r\",\n      \"WCry\"\n    ]", "Str": "", "Type": 5, "Index": 3172}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 3411}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3363}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Jan Miller, CrowdStrike\"\n    ]", "Str": "", "Type": 5, "Index": 3297}}', 'S0366', '[WannaCry](https://attack.mitre.org/software/S0366) is ransomware that was first seen in a global attack during May 2017, which affected more than 150 countries. It contains worm-like features to spread itself across a computer network using the SMBv1 exploit EternalBlue.(Citation: LogRhythm WannaCry)(Citation: US-CERT WannaCry 2017)(Citation: Washington Post WannaCry 2017)(Citation: FireEye WannaCry 2017)', 'WannaCry');
INSERT INTO public.attack_softwares VALUES (392, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--f3f1fbed-7e29-49cb-8579-4a378f858deb', '[{"url": "https://attack.mitre.org/software/S0579", "external_id": "S0579", "source_name": "mitre-attack"}, {"description": "(Citation: Trend Micro Waterbear December 2019)", "source_name": "Waterbear"}, {"url": "https://www.trendmicro.com/en_us/research/19/l/waterbear-is-back-uses-api-hooking-to-evade-security-product-detection.html", "description": "Su, V. et al. (2019, December 11). Waterbear Returns, Uses API Hooking to Evade Security. Retrieved February 22, 2021.", "source_name": "Trend Micro Waterbear December 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Waterbear\"\n    ]", "Str": "", "Type": 5, "Index": 1465}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1565}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1517}}', 'S0579', '[Waterbear](https://attack.mitre.org/software/S0579) is modular malware attributed to [BlackTech](https://attack.mitre.org/groups/G0098) that has been used primarily for lateral movement, decrypting, and triggering payloads and is capable of hiding network behaviors.(Citation: Trend Micro Waterbear December 2019)', 'Waterbear');
INSERT INTO public.attack_softwares VALUES (393, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--959f3b19-2dc8-48d5-8942-c66813a5101a', '[{"url": "https://attack.mitre.org/software/S0515", "external_id": "S0515", "source_name": "mitre-attack"}, {"url": "https://us-cert.cisa.gov/ncas/analysis-reports/ar20-198c", "description": "CISA. (2020, July 16). MAR-10296782-3.v1 – WELLMAIL. Retrieved September 29, 2020.", "source_name": "CISA WellMail July 2020"}, {"url": "https://www.ncsc.gov.uk/files/Advisory-APT29-targets-COVID-19-vaccine-development-V1-1.pdf", "description": "National Cyber Security Centre. (2020, July 16). Advisory: APT29 targets COVID-19 vaccine development. Retrieved September 29, 2020.", "source_name": "NCSC APT29 July 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"WellMail\"\n    ]", "Str": "", "Type": 5, "Index": 1548}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1740}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1692}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Josh Campbell, Cyborg Security, @cyb0rgsecur1ty\"\n    ]", "Str": "", "Type": 5, "Index": 1602}}', 'S0515', '[WellMail](https://attack.mitre.org/software/S0515) is a lightweight malware written in Golang used by [APT29](https://attack.mitre.org/groups/G0016), similar in design and structure to [WellMess](https://attack.mitre.org/software/S0514).(Citation: CISA WellMail July 2020)(Citation: NCSC APT29 July 2020)', 'WellMail');
INSERT INTO public.attack_softwares VALUES (394, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--3a4197ae-ec63-4162-907b-9a073d1157e4', '[{"url": "https://attack.mitre.org/software/S0514", "external_id": "S0514", "source_name": "mitre-attack"}, {"url": "https://us-cert.cisa.gov/ncas/analysis-reports/ar20-198b", "description": "CISA. (2020, July 16). MAR-10296782-2.v1 – WELLMESS. Retrieved September 24, 2020.", "source_name": "CISA WellMess July 2020"}, {"url": "https://www.pwc.co.uk/issues/cyber-security-services/insights/cleaning-up-after-wellmess.html", "description": "PWC. (2020, July 16). How WellMess malware has been used to target COVID-19 vaccines. Retrieved September 24, 2020.", "source_name": "PWC WellMess July 2020"}, {"url": "https://www.ncsc.gov.uk/files/Advisory-APT29-targets-COVID-19-vaccine-development-V1-1.pdf", "description": "National Cyber Security Centre. (2020, July 16). Advisory: APT29 targets COVID-19 vaccine development. Retrieved September 29, 2020.", "source_name": "NCSC APT29 July 2020"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"WellMess\"\n    ]", "Str": "", "Type": 5, "Index": 1877}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2048}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2000}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Daniyal Naeem, BT Security\"\n    ]", "Str": "", "Type": 5, "Index": 1931}}', 'S0514', '[WellMess](https://attack.mitre.org/software/S0514) is lightweight malware family with variants written in .NET and Golang that has been in use since at least 2018 by [APT29](https://attack.mitre.org/groups/G0016).(Citation: CISA WellMess July 2020)(Citation: PWC WellMess July 2020)(Citation: NCSC APT29 July 2020)', 'WellMess');
INSERT INTO public.attack_softwares VALUES (395, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--039814a0-88de-46c5-a4fb-b293db21880a', '[{"url": "https://attack.mitre.org/software/S0206", "external_id": "S0206", "source_name": "mitre-attack"}, {"description": "(Citation: Symantec Wiarp May 2012)", "source_name": "Wiarp"}, {"url": "https://web.archive.org/web/20190717233006/http://www.symantec.com/content/en/us/enterprise/media/security_response/whitepapers/the-elderwood-project.pdf", "description": "O''Gorman, G., and McDonald, G.. (2012, September 6). The Elderwood Project. Retrieved February 15, 2018.", "source_name": "Symantec Elderwood Sept 2012"}, {"url": "https://www.symantec.com/security_response/writeup.jsp?docid=2012-051606-1005-99", "description": "Zhou, R. (2012, May 15). Backdoor.Wiarp. Retrieved February 22, 2018.", "source_name": "Symantec Wiarp May 2012"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Wiarp\"\n    ]", "Str": "", "Type": 5, "Index": 1639}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1735}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1687}}', 'S0206', '[Wiarp](https://attack.mitre.org/software/S0206) is a trojan used by [Elderwood](https://attack.mitre.org/groups/G0066) to open a backdoor on compromised hosts. (Citation: Symantec Elderwood Sept 2012) (Citation: Symantec Wiarp May 2012)', 'Wiarp');
INSERT INTO public.attack_softwares VALUES (396, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--22addc7b-b39f-483d-979a-1b35147da5de', '[{"url": "https://attack.mitre.org/software/S0059", "external_id": "S0059", "source_name": "mitre-attack"}, {"url": "https://media.kasperskycontenthub.com/wp-content/uploads/sites/43/2018/03/07205555/TheNaikonAPT-MsnMM1.pdf", "description": "Baumgartner, K., Golovkin, M.. (2015, May). The MsnMM Campaigns: The Earliest Naikon APT Campaigns. Retrieved April 10, 2019.", "source_name": "Baumgartner Naikon 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"WinMM\"\n    ]", "Str": "", "Type": 5, "Index": 1177}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1273}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1225}}', 'S0059', '[WinMM](https://attack.mitre.org/software/S0059) is a full-featured, simple backdoor used by [Naikon](https://attack.mitre.org/groups/G0019). (Citation: Baumgartner Naikon 2015)', 'WinMM');
INSERT INTO public.attack_softwares VALUES (397, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--0d1f9f5b-11ea-42c3-b5f4-63cce0122541', '[{"url": "https://attack.mitre.org/software/S0466", "external_id": "S0466", "source_name": "mitre-attack"}, {"url": "https://www.sans.org/cyber-security-summit/archives/file/summit-archive-1554718868.pdf", "description": "Karim, T. (2018, August). TRAILS OF WINDSHIFT. Retrieved June 25, 2020.", "source_name": "SANS Windshift August 2018"}, {"url": "https://objective-see.com/blog/blog_0x3B.html", "description": "Wardle, Patrick. (2018, December 20). Middle East Cyber-Espionage analyzing WindShift''s implant: OSX.WindTail (part 1). Retrieved October 3, 2019.", "source_name": "objective-see windtail1 dec 2018"}, {"url": "https://objective-see.com/blog/blog_0x3D.html", "description": "Wardle, Patrick. (2019, January 15). Middle East Cyber-Espionage analyzing WindShift''s implant: OSX.WindTail (part 2). Retrieved October 3, 2019.", "source_name": "objective-see windtail2 jan 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"WindTail\"\n    ]", "Str": "", "Type": 5, "Index": 1930}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 2027}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1981}}', 'S0466', '[WindTail](https://attack.mitre.org/software/S0466) is a macOS surveillance implant used by [Windshift](https://attack.mitre.org/groups/G0112). [WindTail](https://attack.mitre.org/software/S0466) shares code similarities with Hack Back aka KitM OSX.(Citation: SANS Windshift August 2018)(Citation: objective-see windtail1 dec 2018)(Citation: objective-see windtail2 jan 2019)', 'WindTail');
INSERT INTO public.attack_softwares VALUES (398, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a8d3d497-2da9-4797-8e0b-ed176be08654', '[{"url": "https://attack.mitre.org/software/S0176", "external_id": "S0176", "source_name": "mitre-attack"}, {"description": "(Citation: Microsoft SIR Vol 21) (Citation: Microsoft NEODYMIUM Dec 2016) (Citation: Microsoft Wingbird Nov 2017)", "source_name": "Wingbird"}, {"url": "http://download.microsoft.com/download/E/B/0/EB0F50CC-989C-4B66-B7F6-68CD3DC90DE3/Microsoft_Security_Intelligence_Report_Volume_21_English.pdf", "description": "Anthe, C. et al. (2016, December 14). Microsoft Security Intelligence Report Volume 21. Retrieved November 27, 2017.", "source_name": "Microsoft SIR Vol 21"}, {"url": "https://blogs.technet.microsoft.com/mmpc/2016/12/14/twin-zero-day-attacks-promethium-and-neodymium-target-individuals-in-europe/", "description": "Microsoft. (2016, December 14). Twin zero-day attacks: PROMETHIUM and NEODYMIUM target individuals in Europe. Retrieved November 27, 2017.", "source_name": "Microsoft NEODYMIUM Dec 2016"}, {"url": "https://www.microsoft.com/en-us/wdsi/threats/malware-encyclopedia-description?Name=Backdoor:Win32/Wingbird.A!dha", "description": "Microsoft. (2017, November 9). Backdoor:Win32/Wingbird.A!dha. Retrieved November 27, 2017.", "source_name": "Microsoft Wingbird Nov 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Wingbird\"\n    ]", "Str": "", "Type": 5, "Index": 2328}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 2427}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2379}}', 'S0176', '[Wingbird](https://attack.mitre.org/software/S0176) is a backdoor that appears to be a version of commercial software [FinFisher](https://attack.mitre.org/software/S0182). It is reportedly used to attack individual computers instead of networks. It was used by [NEODYMIUM](https://attack.mitre.org/groups/G0055) in a May 2016 campaign. (Citation: Microsoft SIR Vol 21) (Citation: Microsoft NEODYMIUM Dec 2016)', 'Wingbird');
INSERT INTO public.attack_softwares VALUES (399, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--8787e86d-8475-4f13-acea-d33eb83b6105', '[{"url": "https://attack.mitre.org/software/S0430", "external_id": "S0430", "source_name": "mitre-attack"}, {"url": "https://medium.com/chronicle-blog/winnti-more-than-just-windows-and-gates-e4f03436031a", "description": "Chronicle Blog. (2019, May 15). Winnti: More than just Windows and Gates. Retrieved April 29, 2020.", "source_name": "Chronicle Winnti for Linux May 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Winnti for Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1431}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1536}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1490}}', 'S0430', '[Winnti for Linux](https://attack.mitre.org/software/S0430) is a trojan, seen since at least 2015, designed specifically for targeting Linux systems. Reporting indicates the winnti malware family is shared across a number of actors including [Winnti Group](https://attack.mitre.org/groups/G0044). The Windows variant is tracked separately under [Winnti for Windows](https://attack.mitre.org/software/S0141).(Citation: Chronicle Winnti for Linux May 2019)', 'Winnti for Linux');
INSERT INTO public.attack_softwares VALUES (400, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--d3afa961-a80c-4043-9509-282cdf69ab21', '[{"url": "https://attack.mitre.org/software/S0141", "external_id": "S0141", "source_name": "mitre-attack"}, {"url": "https://securelist.com/winnti-more-than-just-a-game/37029/", "description": "Kaspersky Lab''s Global Research and Analysis Team. (2013, April 11). Winnti. More than just a game. Retrieved February 8, 2017.", "source_name": "Kaspersky Winnti April 2013"}, {"url": "https://blogs.technet.microsoft.com/mmpc/2017/01/25/detecting-threat-actors-in-recent-german-industrial-attacks-with-windows-defender-atp/", "description": "Cap, P., et al. (2017, January 25). Detecting threat actors in recent German industrial attacks with Windows Defender ATP. Retrieved February 8, 2017.", "source_name": "Microsoft Winnti Jan 2017"}, {"url": "http://www.novetta.com/wp-content/uploads/2015/04/novetta_winntianalysis.pdf", "description": "Novetta Threat Research Group. (2015, April 7). Winnti Analysis. Retrieved February 8, 2017.", "source_name": "Novetta Winnti April 2015"}, {"url": "https://medium.com/chronicle-blog/winnti-more-than-just-windows-and-gates-e4f03436031a", "description": "Chronicle Blog. (2019, May 15). Winnti: More than just Windows and Gates. Retrieved April 29, 2020.", "source_name": "Chronicle Winnti for Linux May 2019"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Winnti for Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2665}, "x_mitre_version": {"Num": 0, "Raw": "\"2.0\"", "Str": "2.0", "Type": 3, "Index": 2774}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2726}}', 'S0141', '[Winnti for Windows](https://attack.mitre.org/software/S0141) is a Trojan that has been used by multiple groups to carry out intrusions in varied regions from at least 2010 to 2016. One of the groups using this malware is referred to by the same name, [Winnti Group](https://attack.mitre.org/groups/G0044); however, reporting indicates a second distinct group, [Axiom](https://attack.mitre.org/groups/G0001), also uses the malware. (Citation: Kaspersky Winnti April 2013) (Citation: Microsoft Winnti Jan 2017) (Citation: Novetta Winnti April 2015) The Linux variant is tracked separately under [Winnti for Linux](https://attack.mitre.org/software/S0430).(Citation: Chronicle Winnti for Linux May 2019)', 'Winnti for Windows');
INSERT INTO public.attack_softwares VALUES (401, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a19c49aa-36fe-4c05-b817-23e1c7a7d085', '[{"url": "https://attack.mitre.org/software/S0041", "external_id": "S0041", "source_name": "mitre-attack"}, {"url": "http://www.secureworks.com/cyber-threat-intelligence/threats/wiper-malware-analysis-attacking-korean-financial-sector/", "description": "Dell SecureWorks. (2013, March 21). Wiper Malware Analysis Attacking Korean Financial Sector. Retrieved May 13, 2015.", "source_name": "Dell Wiper"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Wiper\"\n    ]", "Str": "", "Type": 5, "Index": 1175}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1271}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1223}}', 'S0041', '[Wiper](https://attack.mitre.org/software/S0041) is a family of destructive malware used in March 2013 during breaches of South Korean banks and media companies. (Citation: Dell Wiper)', 'Wiper');
INSERT INTO public.attack_softwares VALUES (402, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--59a97b15-8189-4d51-9404-e1ce8ea4a069', '[{"url": "https://attack.mitre.org/software/S0161", "external_id": "S0161", "source_name": "mitre-attack"}, {"description": "(Citation: XAgentOSX 2017)", "source_name": "XAgentOSX"}, {"description": "(Citation: Symantec APT28 Oct 2018)", "source_name": "OSX.Sofacy"}, {"url": "https://researchcenter.paloaltonetworks.com/2017/02/unit42-xagentosx-sofacys-xagent-macos-tool/", "description": "Robert Falcone. (2017, February 14). XAgentOSX: Sofacy''s Xagent macOS Tool. Retrieved July 12, 2017.", "source_name": "XAgentOSX 2017"}, {"url": "https://www.symantec.com/blogs/election-security/apt28-espionage-military-government", "description": "Symantec Security Response. (2018, October 04). APT28: New Espionage Operations Target Military and Government Organizations. Retrieved November 14, 2018.", "source_name": "Symantec APT28 Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"XAgentOSX\",\n      \"OSX.Sofacy\"\n    ]", "Str": "", "Type": 5, "Index": 1812}, "x_mitre_version": {"Num": 0, "Raw": "\"1.3\"", "Str": "1.3", "Type": 3, "Index": 1930}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"macOS\"\n    ]", "Str": "", "Type": 5, "Index": 1884}}', 'S0161', '[XAgentOSX](https://attack.mitre.org/software/S0161) is a trojan that has been used by [APT28](https://attack.mitre.org/groups/G0007)  on OS X and appears to be a port of their standard [CHOPSTICK](https://attack.mitre.org/software/S0023) or XAgent trojan. (Citation: XAgentOSX 2017)', 'XAgentOSX');
INSERT INTO public.attack_softwares VALUES (403, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--7343e208-7cab-45f2-a47b-41ba5e2f0fab', '[{"url": "https://attack.mitre.org/software/S0117", "external_id": "S0117", "source_name": "mitre-attack"}, {"description": "(Citation: ESET Sednit Part 2)", "source_name": "XTunnel"}, {"description": "(Citation: Symantec APT28 Oct 2018)", "source_name": "Trojan.Shunnael"}, {"description": "(Citation: Crowdstrike DNC June 2016)(Citation: Symantec APT28 Oct 2018)", "source_name": "X-Tunnel"}, {"description": "(Citation: ESET Sednit Part 2)", "source_name": "XAPS"}, {"url": "https://www.crowdstrike.com/blog/bears-midst-intrusion-democratic-national-committee/", "description": "Alperovitch, D.. (2016, June 15). Bears in the Midst: Intrusion into the Democratic National Committee. Retrieved August 3, 2016.", "source_name": "Crowdstrike DNC June 2016"}, {"url": "https://www.invincea.com/2016/07/tunnel-of-gov-dnc-hack-and-the-russian-xtunnel/", "description": "Belcher, P.. (2016, July 28). Tunnel of Gov: DNC Hack and the Russian XTunnel. Retrieved August 3, 2016.", "source_name": "Invincea XTunnel"}, {"url": "http://www.welivesecurity.com/wp-content/uploads/2016/10/eset-sednit-part-2.pdf", "description": "ESET. (2016, October). En Route with Sednit - Part 2: Observing the Comings and Goings. Retrieved November 21, 2016.", "source_name": "ESET Sednit Part 2"}, {"url": "https://www.symantec.com/blogs/election-security/apt28-espionage-military-government", "description": "Symantec Security Response. (2018, October 04). APT28: New Espionage Operations Target Military and Government Organizations. Retrieved November 14, 2018.", "source_name": "Symantec APT28 Oct 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"XTunnel\",\n      \"Trojan.Shunnael\",\n      \"X-Tunnel\",\n      \"XAPS\"\n    ]", "Str": "", "Type": 5, "Index": 2804}, "x_mitre_version": {"Num": 0, "Raw": "\"2.1\"", "Str": "2.1", "Type": 3, "Index": 2959}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 2911}}', 'S0117', '[XTunnel](https://attack.mitre.org/software/S0117) a VPN-like network proxy tool that can relay traffic between a C2 server and a victim. It was first seen in May 2013 and reportedly used by [APT28](https://attack.mitre.org/groups/G0007) during the compromise of the Democratic National Committee. (Citation: Crowdstrike DNC June 2016) (Citation: Invincea XTunnel) (Citation: ESET Sednit Part 2)', 'XTunnel');
INSERT INTO public.attack_softwares VALUES (404, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--6a92d80f-cc65-45f6-aa66-3cdea6786b3c', '[{"url": "https://attack.mitre.org/software/S0341", "external_id": "S0341", "source_name": "mitre-attack"}, {"description": "(Citation: Unit42 Xbash Sept 2018)", "source_name": "Xbash"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/09/unit42-xbash-combines-botnet-ransomware-coinmining-worm-targets-linux-windows/", "description": "Xiao, C. (2018, September 17). Xbash Combines Botnet, Ransomware, Coinmining in Worm that Targets Linux and Windows. Retrieved November 14, 2018.", "source_name": "Unit42 Xbash Sept 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Xbash\"\n    ]", "Str": "", "Type": 5, "Index": 1571}, "x_mitre_version": {"Num": 0, "Raw": "\"1.2\"", "Str": "1.2", "Type": 3, "Index": 1682}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\",\n      \"Linux\"\n    ]", "Str": "", "Type": 5, "Index": 1619}}', 'S0341', '[Xbash](https://attack.mitre.org/software/S0341) is a malware family that has targeted Linux and Microsoft Windows servers. The malware has been tied to the Iron Group, a threat actor group known for previous ransomware attacks. [Xbash](https://attack.mitre.org/software/S0341) was developed in Python and then converted into a self-contained Linux ELF executable by using PyInstaller.(Citation: Unit42 Xbash Sept 2018)', 'Xbash');
INSERT INTO public.attack_softwares VALUES (405, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--cb444a16-3ea5-4a91-88c6-f329adcb8af3', '[{"url": "https://attack.mitre.org/software/S0388", "external_id": "S0388", "source_name": "mitre-attack"}, {"url": "https://www.trendmicro.de/cloud-content/us/pdfs/security-intelligence/white-papers/wp-operation-tropic-trooper.pdf", "description": "Alintanahin, K. (2015). Operation Tropic Trooper: Relying on Tried-and-Tested Flaws to Infiltrate Secret Keepers. Retrieved June 14, 2019.", "source_name": "TrendMicro TropicTrooper 2015"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"YAHOYAH\"\n    ]", "Str": "", "Type": 5, "Index": 1224}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1378}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1330}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Bart Parys\"\n    ]", "Str": "", "Type": 5, "Index": 1277}}', 'S0388', '[YAHOYAH](https://attack.mitre.org/software/S0388) is a Trojan used by [Tropic Trooper](https://attack.mitre.org/groups/G0081) as a second-stage backdoor.(Citation: TrendMicro TropicTrooper 2015)', 'YAHOYAH');
