INSERT INTO public.attack_softwares VALUES (407, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--a4f57468-fbd5-49e4-8476-52088220b92d', '[{"url": "https://attack.mitre.org/software/S0251", "external_id": "S0251", "source_name": "mitre-attack"}, {"description": "(Citation: Palo Alto Sofacy 06-2018)(Citation: Unit42 Cannon Nov 2018)", "source_name": "Zebrocy"}, {"description": "(Citation: CyberScoop APT28 Nov 2018)(Citation: Accenture SNAKEMACKEREL Nov 2018)", "source_name": "Zekapab"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/06/unit42-sofacy-groups-parallel-attacks/", "description": "Lee, B., Falcone, R. (2018, June 06). Sofacy Group’s Parallel Attacks. Retrieved June 18, 2018.", "source_name": "Palo Alto Sofacy 06-2018"}, {"url": "https://researchcenter.paloaltonetworks.com/2018/11/unit42-sofacy-continues-global-attacks-wheels-new-cannon-trojan/", "description": "Falcone, R., Lee, B. (2018, November 20). Sofacy Continues Global Attacks and Wheels Out New ‘Cannon’ Trojan. Retrieved November 26, 2018.", "source_name": "Unit42 Cannon Nov 2018"}, {"url": "https://unit42.paloaltonetworks.com/dear-joohn-sofacy-groups-global-campaign/", "description": "Lee, B., Falcone, R. (2018, December 12). Dear Joohn: The Sofacy Group’s Global Campaign. Retrieved April 19, 2019.", "source_name": "Unit42 Sofacy Dec 2018"}, {"url": "https://us-cert.cisa.gov/ncas/analysis-reports/ar20-303b", "description": "CISA. (2020, October 29). Malware Analysis Report (AR20-303B). Retrieved December 9, 2020.", "source_name": "CISA Zebrocy Oct 2020"}, {"url": "https://www.cyberscoop.com/apt28-brexit-phishing-accenture/", "description": "Shoorbajee, Z. (2018, November 29). Accenture: Russian hackers using Brexit talks to disguise phishing lures. Retrieved July 16, 2019.", "source_name": "CyberScoop APT28 Nov 2018"}, {"url": "https://www.accenture.com/t20181129T203820Z__w__/us-en/_acnmedia/PDF-90/Accenture-snakemackerel-delivers-zekapab-malware.pdf#zoom=50", "description": "Accenture Security. (2018, November 29). SNAKEMACKEREL. Retrieved April 15, 2019.", "source_name": "Accenture SNAKEMACKEREL Nov 2018"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Zebrocy\",\n      \"Zekapab\"\n    ]", "Str": "", "Type": 5, "Index": 3250}, "x_mitre_version": {"Num": 0, "Raw": "\"3.0\"", "Str": "3.0", "Type": 3, "Index": 3429}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 3381}, "x_mitre_contributors": {"Num": 0, "Raw": "[\n      \"Emily Ratliff, IBM\"\n    ]", "Str": "", "Type": 5, "Index": 3320}}', 'S0251', '[Zebrocy](https://attack.mitre.org/software/S0251) is a Trojan that has been used by [APT28](https://attack.mitre.org/groups/G0007) since at least November 2015. The malware comes in several programming language variants, including C++, Delphi, AutoIt, C#, VB.NET, and Golang. (Citation: Palo Alto Sofacy 06-2018)(Citation: Unit42 Cannon Nov 2018)(Citation: Unit42 Sofacy Dec 2018)(Citation: CISA Zebrocy Oct 2020) ', 'Zebrocy');
INSERT INTO public.attack_softwares VALUES (408, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--4ab44516-ad75-4e43-a280-705dc0420e2f', '[{"url": "https://attack.mitre.org/software/S0230", "external_id": "S0230", "source_name": "mitre-attack"}, {"description": "(Citation: Proofpoint TA459 April 2017) (Citation: Proofpoint ZeroT Feb 2017)", "source_name": "ZeroT"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/apt-targets-financial-analysts", "description": "Axel F. (2017, April 27). APT Targets Financial Analysts with CVE-2017-0199. Retrieved February 15, 2018.", "source_name": "Proofpoint TA459 April 2017"}, {"url": "https://www.proofpoint.com/us/threat-insight/post/APT-targets-russia-belarus-zerot-plugx", "description": "Huss, D., et al. (2017, February 2). Oops, they did it again: APT Targets Russia and Belarus with ZeroT and PlugX. Retrieved April 5, 2018.", "source_name": "Proofpoint ZeroT Feb 2017"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"ZeroT\"\n    ]", "Str": "", "Type": 5, "Index": 1721}, "x_mitre_version": {"Num": 0, "Raw": "\"1.1\"", "Str": "1.1", "Type": 3, "Index": 1817}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1769}}', 'S0230', '[ZeroT](https://attack.mitre.org/software/S0230) is a Trojan used by [TA459](https://attack.mitre.org/groups/G0062), often in conjunction with [PlugX](https://attack.mitre.org/software/S0013). (Citation: Proofpoint TA459 April 2017) (Citation: Proofpoint ZeroT Feb 2017)', 'ZeroT');
INSERT INTO public.attack_softwares VALUES (409, '2021-07-16 00:44:47.246715+00', '2021-07-16 00:44:47.246715+00', NULL, 'malware--552462b9-ae79-49dd-855c-5973014e157f', '[{"url": "https://attack.mitre.org/software/S0027", "external_id": "S0027", "source_name": "mitre-attack"}, {"url": "https://sophosnews.files.wordpress.com/2012/04/zeroaccess2.pdf", "description": "Wyke, J. (2012, April). ZeroAccess. Retrieved July 18, 2016.", "source_name": "Sophos ZeroAccess"}]', '{"x_mitre_aliases": {"Num": 0, "Raw": "[\n      \"Zeroaccess\",\n      \"Trojan.Zeroaccess\"\n    ]", "Str": "", "Type": 5, "Index": 1123}, "x_mitre_version": {"Num": 0, "Raw": "\"1.0\"", "Str": "1.0", "Type": 3, "Index": 1251}, "x_mitre_platforms": {"Num": 0, "Raw": "[\n      \"Windows\"\n    ]", "Str": "", "Type": 5, "Index": 1203}}', 'S0027', '[Zeroaccess](https://attack.mitre.org/software/S0027) is a kernel-mode [Rootkit](https://attack.mitre.org/techniques/T1014) that attempts to add victims to the ZeroAccess botnet, often for monetary gain. (Citation: Sophos ZeroAccess)', 'Zeroaccess');
